INSERT INTO `question` VALUES (1733399752390844418, 1, 'f1d94', 0001, '造成交通事故后逃逸且构成犯罪的驾驶人，将吊销驾驶证且终生不得重新取得驾驶证。', NULL, '对', '错', '空', '空', '对', '【<b>假1骗3醉5逃终生</b>】《道路交通安全法》第一百零一条：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。<b>造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。</b>', '', '逃逸且构成犯罪|终生不得', 0002, 0.06);
INSERT INTO `question` VALUES (1733399755654012930, 2, 'dc736', 0002, '驾驶机动车应当依法取得哪种证件？ ', 'A、工作证 B、机动车驾驶证 C、身份证 D、职业资格证', '工作证 ', '机动车驾驶证 ', '身份证 ', '职业资格证', 'B', '《机动车驾驶证申领和使用规定》第十条，驾驶机动车，应当依法取得<b>机动车驾驶证</b>。', '', '依法取得', 0001, 0.01);
INSERT INTO `question` VALUES (1733399755750481921, 3, 'fd969', 0002, '未取得驾驶证的学员在道路上学习驾驶技能，下列哪种做法是正确的？ ', 'A、使用所学车型的教练车由教练员随车指导 B、使用所学车型的教练车单独驾驶学习 C、使用私家车由教练员随车指导 D、使用所学车型的教练车由非教练员的驾驶人随车指导', '使用所学车型的教练车由教练员随车指导 ', '使用所学车型的教练车单独驾驶学习 ', '使用私家车由教练员随车指导 ', '使用所学车型的教练车由非教练员的驾驶人随车指导', 'A', '《机动车驾驶证申领和使用规定》第四十二条，申请人在道路上学习驾驶，应当随身携带学习驾驶证明，使用教练车或者学车专用标识签注的自学用车，在教练员或者学车专用标识签注的指导人员随车指导下，按照公安机关交通管理部门指定的路线、时间进行。', '', '未取得|学习驾驶', 0003, 0.10);
INSERT INTO `question` VALUES (1733399755825979393, 4, '601d1', 0002, '机动车驾驶人初次申领驾驶证后的实习期是多长时间？ ', 'A、6个月 B、12个月 C、16个月 D、18个月', '6个月 ', '12个月 ', '16个月 ', '18个月', 'B', '《机动车驾驶证申领和使用规定》第七十六条，机动车驾驶人<b>初次取得</b>汽车类准驾车型或者<b>初次取得</b>摩托车类准驾车型后的<b>12个月为实习期</b>。', '', '初次申领|实习期', 0001, 0.03);
INSERT INTO `question` VALUES (1733399755914059778, 5, '8d481', 0002, '在实习期内驾驶机动车的，应当在车身后部粘贴或者悬挂哪种标志？ ', 'A、注意新手标志 B、注意避让标志 C、统一式样的实习标志 D、注意车距标志', '注意新手标志 ', '注意避让标志 ', '统一式样的实习标志 ', '注意车距标志', 'C', '《机动车驾驶证申领和使用规定》第七十六条 机动车驾驶人初次取得汽车类准驾车型或者初次取得摩托车类准驾车型后的12个月为实习期。<br>在实习期内驾驶机动车的，应当在车身后部粘贴或者<b>悬挂统一式样的实习标志</b>。', '', '实习期|标志|车身后部', 0001, 0.03);
INSERT INTO `question` VALUES (1733399755989557250, 6, '6a5af', 0002, '以欺骗、贿赂等不正当手段取得驾驶证被依法撤销驾驶许可的，多长时间不得重新申请驾驶许可？ ', 'A、3年内 B、终身 C、1年内 D、5年内', '3年内 ', '终身 ', '1年内 ', '5年内', 'A', '《机动车驾驶证申领和使用规定》第九十三条，<b>申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的</b>，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，处二千元以下罚款；申请人<b>在三年内不得再次申领</b>机动车驾驶证。【骗3】', '', '欺骗|不得重新', 0005, 0.29);
INSERT INTO `question` VALUES (1733399756073443329, 7, '9e4bf', 0001, '驾驶人要按照驾驶证载明的准驾车型驾驶车辆。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第十九条　驾驶机动车，应当依法取得机动车驾驶证。驾驶人应当<b>按照驾驶证载明的准驾车型驾驶机动车</b>；驾驶机动车时，应当随身携带机动车驾驶证。', '', '要按照|准驾车型', 0001, 0.01);
INSERT INTO `question` VALUES (1733399756153135105, 8, '09f75', 0001, '机动车驾驶人在实习期内驾驶机动车不得牵引挂车。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十七条，<b>机动车驾驶人在实习期内</b>不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；驾驶的机动车<b>不得牵引挂车</b>。', '', '实习期内|不得', 0002, 0.06);
INSERT INTO `question` VALUES (1733399756220243969, 9, '84f6f', 0002, '驾驶拼装机动车上路行驶的驾驶人，除按规定接受罚款外，还要受到哪种处理？ ', 'A、处10日以下拘留 B、暂扣驾驶证 C、吊销驾驶证 D、追究刑事责任', '处10日以下拘留 ', '暂扣驾驶证 ', '吊销驾驶证 ', '追究刑事责任', 'C', '根据《道路交通安全法》的规定，对<b>驾驶拼装的机动车</b>或已达到报废标准的机动车上路行驶的驾驶人，处200元以上2000元以下罚款，<b>并吊销机动车驾驶证</b>，对车辆予以收缴，强制报废。', '', '拼装机动车|接受罚款', 0004, 0.17);
INSERT INTO `question` VALUES (1733399756312518657, 10, '3c384', 0002, '驾驶报废机动车上路行驶的驾驶人，除按规定罚款外，还要受到哪种处理？ ', 'A、撤销驾驶许可 B、收缴驾驶证 C、强制恢复车况 D、吊销驾驶证', '撤销驾驶许可 ', '收缴驾驶证 ', '强制恢复车况 ', '吊销驾驶证', 'D', '根据《道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。<b>对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并<b>吊销机动车驾驶证</b>。</b>', '', '报废机动车|罚款', 0002, 0.07);
INSERT INTO `question` VALUES (1733399756375433218, 11, '2b592', 0002, '对驾驶已达到报废标准的机动车上路行驶的驾驶人，会受到下列哪种处罚？ ', 'A、处15日以下拘留 B、吊销机动车驾驶证 C、处20以上200元以下罚款 D、追究刑事责任', '处15日以下拘留 ', '吊销机动车驾驶证 ', '处20以上200元以下罚款 ', '追究刑事责任', 'B', '<b>拼装/报废/超50：200-2000，吊驾照</b>。根据《道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处<b>200元以上2000元以下</b>罚款，并<b>吊销机动车驾驶证</b>。【1罚2吊】', '', '报废', 0005, 0.29);
INSERT INTO `question` VALUES (1733399756459319297, 12, '39e8f', 0002, '对驾驶拼装机动车上路行驶的驾驶人，会受到下列哪种处罚？ ', 'A、处15日以下拘留 B、依法追究刑事责任 C、处200以上2000元以下罚款 D、吊销机动车行驶证', '处15日以下拘留 ', '依法追究刑事责任 ', '处200以上2000元以下罚款 ', '吊销机动车行驶证', 'C', '【<b>拼装/报废车上路：罚款200-2000，吊销驾驶证</b>】。根据《道路交通安全法》第一百条规定，<b>驾驶拼装</b>的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处<b>200元以上2000元以下</b>罚款，并<b>吊销机动车驾驶证</b>。请注意：<b>吊销驾驶证，不是吊销行驶证。</b>', '', '拼装机动车', 0005, 0.41);
INSERT INTO `question` VALUES (1733399756530622466, 13, 'c575e', 0001, '驾驶机动车上路前应当检查车辆安全技术性能。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第二十一条规定：驾驶人驾驶机动车上道路行驶前，应当对机动车的安全技术性能进行认真检查；不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车。', '', '上路前|安全技术', 0001, 0.01);
INSERT INTO `question` VALUES (1733399756589342721, 14, '655f7', 0001, '不得驾驶具有安全隐患的机动车上道路行驶。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第二十一条：驾驶人驾驶机动车上道路行驶前，应当对机动车的安全技术性能进行认真检查；<b>不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车。</b>', '', '不得|安全隐患', 0001, 0.03);
INSERT INTO `question` VALUES (1733399756652257282, 15, '8778a', 0001, '拼装的机动车只要认为安全就可以上路行驶。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第一百条规定：<b>驾驶拼装的机动车</b>或者已达到报废标准的机动车上道路行驶的，<b>公安机关交通管理部门应当予以收缴，强制报废</b>，处200元以上2000元以下罚款，并吊销机动车驾驶证。', '', '只要认为', 0002, 0.04);
INSERT INTO `question` VALUES (1733399756715171841, 16, 'cba5a', 0001, '已经达到报废标准的机动车经大修后可以上路行驶。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第十四条　国家实行机动车强制报废制度，根据机动车的安全技术状况和不同用途，规定不同的报废标准。应当报废的机动车必须及时办理注销登记。<b>达到报废标准的机动车不得上道路行驶</b>。因此，题干表述错误。', '', '达到报废|可以上路', 0002, 0.04);
INSERT INTO `question` VALUES (1733399756786475009, 17, 'df836', 0002, '下列哪种证件是驾驶机动车上路行驶应当随车携带？ ', 'A、机动车登记证 B、机动车保险单 C、机动车行驶证 D、出厂合格证明', '机动车登记证 ', '机动车保险单 ', '机动车行驶证 ', '出厂合格证明', 'C', '上道路行驶的机动车应当随车携带：两证、两标、一号牌。根据《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，<b>并随车携带机动车行驶证。</b>', '', '随车携带', 0001, 0.03);
INSERT INTO `question` VALUES (1733399756861972482, 18, '52913', 0002, '驾驶这种机动车上路行驶属于什么行为？ ', 'A、违规行为 B、违章行为 C、违法行为 D、犯罪行为', '违规行为 ', '违章行为 ', '违法行为 ', '犯罪行为', 'C', '《道路交通安全法》第十一条规定：机动车号牌应当按照规定悬挂并保持清晰、完整，不得故意遮挡、污损。 <br>如图所示机动车，<b>故意遮挡号牌</b>，违反了《道路交通安全法》，属于<b>违法行为</b>。', 'https://timg.mnks.cn/20230926aee9f4b8.jpg', '上路行驶', 0002, 0.06);
INSERT INTO `question` VALUES (1733399756962635778, 19, '3cdaa', 0002, '下列哪种标识是自学直考人员在道路上学习驾驶时，应当在车上放置的标志？ ', 'A、产品合格标识 B、保持车距标识 C、提醒危险标识 D、学车专用标识', '产品合格标识 ', '保持车距标识 ', '提醒危险标识 ', '学车专用标识', 'D', '《机动车驾驶证申领和使用规定》第四十二条，申请人在道路上学习驾驶，应当随身携带学习驾驶证明，使用教练车或者<b>学车专用标识</b>签注的自学用车，在教练员或者<b>学车专用标识</b>签注的指导人员随车指导下，按照公安机关交通管理部门指定的路线、时间进行。<br><b>申请人为自学直考人员的，在道路上学习驾驶时</b>，应当在自学用车上按规定放置、粘贴<b>学车专用标识</b>，自学用车不得搭载随车指导人员以外的其他人员。', '', '应当', 0002, 0.06);
INSERT INTO `question` VALUES (1733399757033938946, 20, 'bbae1', 0002, '驾驶人在下列哪种情况下不能驾驶机动车？ ', 'A、饮酒后 B、喝茶后 C、喝咖啡后 D、喝牛奶后', '饮酒后 ', '喝茶后 ', '喝咖啡后 ', '喝牛奶后', 'A', '《道路交通安全法》第二十二条，机动车驾驶人应当遵守道路交通安全法律、法规的规定，按照操作规范安全驾驶、文明驾驶。<br><b>饮酒、</b>服用国家管制的精神药品或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，<b>不得驾驶机动车</b>。', '', '不能驾驶', 0001, 0.03);
INSERT INTO `question` VALUES (1733399757109436418, 21, '41c2f', 0002, '这辆在道路上行驶的机动车有下列哪种违法行为？ ', 'A、逆向行驶 B、未按规定悬挂号牌 C、故意遮挡号牌 D、占用非机动车道', '逆向行驶 ', '未按规定悬挂号牌 ', '故意遮挡号牌 ', '占用非机动车道', 'C', '如图所示机动车的违法行为是<b>遮挡号牌</b>。根据《道路交通安全法》第十一条规定：机动车号牌应当按照规定悬挂并保持清晰、完整，<b>不得故意遮挡</b>、污损。', 'https://timg.mnks.cn/20230926f0564dce.jpg', '违法行为', 0002, 0.05);
INSERT INTO `question` VALUES (1733399757205905410, 22, '3ba35', 0001, '驾驶机动车上路行驶应当按规定悬挂号牌。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，<b>应当悬挂机动车号牌</b>，放置检验合格标志、保险标志，并随车携带机动车行驶证。【两证两标一号牌，不带扣车还罚款】', '', '按规定', 0001, 0.02);
INSERT INTO `question` VALUES (1733399757285597185, 23, '96b8e', 0001, '驾驶这种机动车上路行驶没有违法行为。', NULL, '对', '错', '空', '空', '错', '如图所示，<b>机动车号牌被遮挡</b>，属于违法行为。《道路交通安全法》第十一条，驾驶机动车<b>上道路行驶，应当悬挂机动车号牌</b>，放置检验合格标志、保险标志，并随车携带机动车行驶证。<br>机动车号牌应当按照规定悬挂并保持清晰、完整，不得故意遮挡、污损。', 'https://timg.mnks.cn/20230926c315f965.jpg', '没有', 0002, 0.07);
INSERT INTO `question` VALUES (1733399757327540226, 24, '527ca', 0001, '服用国家管制的精神药品可以短途驾驶机动车。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第二十二条规定：饮酒、<b>服用国家管制的精神药品</b>或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，<b>不得驾驶</b>机动车。本题题干表述有误，因此本题答案应为错误。', '', '服用|可以短途', 0002, 0.05);
INSERT INTO `question` VALUES (1733399757390454785, 25, '4e150', 0001, '饮酒后只要不影响驾驶操作可以短距离驾驶机动车。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第二十二条规定：<b>饮酒</b>、服用国家管制的精神药品或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，<b>不得驾驶</b>机动车。', '', '只要|可以', 0002, 0.04);
INSERT INTO `question` VALUES (1733399757495312385, 26, '1d5ca', 0002, '公安交通管理部门对驾驶人的交通违法行为除依法给予行政处罚外，实行下列哪种制度？ ', 'A、违法登记制度 B、奖励里程制度 C、累积记分制度 D、强制报废制度', '违法登记制度 ', '奖励里程制度 ', '累积记分制度 ', '强制报废制度', 'C', '《道路交通安全法》第二十四条：公安机关交通管理部门对机动车驾驶人违反道路交通安全法律、法规的行为，除依法给予行政处罚外，实行<b>累积记分制度</b>。', '', '交通违法行为|制度', 0003, 0.12);
INSERT INTO `question` VALUES (1733399757604364289, 27, '3b40b', 0002, '道路交通安全违法行为累积记分的周期是多长时间？ ', 'A、3个月 B、6个月 C、12个月 D、24个月', '3个月 ', '6个月 ', '12个月 ', '24个月', 'C', '两个12要牢记，<b>周期12个月</b>，满分12分。《机动车驾驶证申领和使用规定》第七十一条 公安机关交通管理部门对机动车驾驶人的道路交通安全违法行为，除依法给予行政处罚外，实行道路交通安全违法行为累积记分制度，<b>记分周期为12个月</b>，满分为12分。', '', '累积记分', 0002, 0.05);
INSERT INTO `question` VALUES (1733399757675667457, 28, 'f6bfb', 0002, '公安机关交通管理部门对累积记分达到规定分值的驾驶人怎样处理？ ', 'A、依法追究刑事责任 B、处15日以下拘留 C、终生禁驾 D、进行法律法规教育，重新考试', '依法追究刑事责任 ', '处15日以下拘留 ', '终生禁驾 ', '进行法律法规教育，重新考试', 'D', '《道路交通安全违法行为记分管理办法》第十七条，机动车驾驶人在一个记分周期内<b>累积记分满12分的</b>，公安机关交通管理部门应当<b>扣留其机动车驾驶证</b>，开具强制措施凭证，并送达满分教育通知书，<b>通知机动车驾驶人参加满分学习、考试</b>。', '', '达到规定分值', 0002, 0.07);
INSERT INTO `question` VALUES (1733399757734387714, 29, 'da8d6', 0002, '驾驶人出现下列哪种情况，不得驾驶机动车？ ', 'A、驾驶证丢失、损毁 B、驾驶证接近有效期 C、记分达到10分 D、记分达到6分', '驾驶证丢失', '损毁 B', '驾驶证接近有效期 C', '记分达到10分 D', 'A', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车<b>驾驶证丢失、损毁</b>、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，<b>不得驾驶</b>机动车。', '', '不得', 0001, 0.03);
INSERT INTO `question` VALUES (1733399757797302274, 30, 'e7d03', 0001, '驾驶人在驾驶证丢失后3个月内还可以驾驶机动车。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车<b>驾驶证丢失、损毁</b>、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，<b>不得驾驶</b>机动车。', '', '丢失|可以驾驶', 0002, 0.05);
INSERT INTO `question` VALUES (1733399757868605442, 31, '2a612', 0001, '驾驶人持超过有效期的驾驶证可以在1年内驾驶机动车。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车<b>驾驶证</b>丢失、损毁、<b>超过有效期</b>或者被依法扣留、暂扣期间以及记分达到12分的，<b>不得驾驶</b>机动车。', '', '超过|可以在', 0003, 0.12);
INSERT INTO `question` VALUES (1733399757939908609, 32, '372cd', 0001, '驾驶人的驾驶证损毁后不得驾驶机动车。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车<b>驾驶证</b>丢失、<b>损毁</b>、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，<b>不得驾驶</b>机动车。', '', '不得驾驶', 0002, 0.04);
INSERT INTO `question` VALUES (1733399758023794690, 33, '345bd', 0001, '记分满12分的驾驶人拒不参加学习和考试的将被公告驾驶证停止使用。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第二十九条，机动车驾驶人在一个记分周期内累积记分满12分，机动车驾驶证未被依法扣留或者收到满分教育通知书后三十日内<b>拒不参加</b>公安机关交通管理部门通知的满分学习、考试的，由公安机关交通管理部门公告<b>其机动车驾驶证停止使用</b>。', '', '满12分|拒不参加|停止使用', 0001, 0.03);
INSERT INTO `question` VALUES (1733399758090903553, 34, '6e636', 0001, '驾驶人的机动车驾驶证被依法扣留、暂扣的情况下不得驾驶机动车。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', '', '不得驾驶', 0001, 0.02);
INSERT INTO `question` VALUES (1733399758162206722, 35, 'e0378', 0002, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？ ', 'A、未悬挂机动车号牌 B、未携带身份证 C、未携带保险合同 D、未放置城市环保标志', '未悬挂机动车号牌 ', '未携带身份证 ', '未携带保险合同 ', '未放置城市环保标志', 'A', '《道路交通安全法》第九十五条：上道路行驶的机动车<b>未悬挂机动车号牌，</b>未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门<b>应当扣留机动车</b>，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。', '', '扣留车辆', 0003, 0.11);
INSERT INTO `question` VALUES (1733399758225121282, 36, '98478', 0002, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？ ', 'A、未携带身份证 B、未携带行驶证 C、未放置城市环保标志 D、未携带机动车登记证书', '未携带身份证 ', '未携带行驶证 ', '未放置城市环保标志 ', '未携带机动车登记证书', 'B', '《道路交通安全法》第九十五条 上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者<b>未随车携带行驶证</b>、驾驶证的，公安机关交通管理部门<b>应当扣留机动车</b>，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。', '', '扣留车辆', 0003, 0.10);
INSERT INTO `question` VALUES (1733399758283841537, 37, 'e1824', 0002, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？ ', 'A、未携带机动车登记证书 B、未携带保险合同 C、未放置保险标志 D、未放置城市环保标志', '未携带机动车登记证书 ', '未携带保险合同 ', '未放置保险标志 ', '未放置城市环保标志', 'C', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、<b>保险标志，</b>或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当<b>扣留机动车</b>。<br>上道路行驶的机动车，两证两标一号牌，缺一不可。', '', '扣留车辆', 0004, 0.24);
INSERT INTO `question` VALUES (1733399758371921922, 38, '921e2', 0002, '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？ ', 'A、机动车驾驶证 B、居民身份证 C、从业资格证 D、机动车通行证', '机动车驾驶证 ', '居民身份证 ', '从业资格证 ', '机动车通行证', 'A', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，<b>依法扣留车辆</b>：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者<b>未随车携带机动车行驶证、驾驶证的</b>。<br>【<b>牌检保行驾</b>】即：<b>机动车号牌、检验合格标志、保险标志、行驶证、驾驶证</b>，缺一不可。', '', '扣留车辆', 0002, 0.07);
INSERT INTO `question` VALUES (1733399758434836481, 39, '30de9', 0002, '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？ ', 'A、机动车通行证 B、居民身份证 C、从业资格证 D、机动车行驶证', '机动车通行证 ', '居民身份证 ', '从业资格证 ', '机动车行驶证', 'D', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，<b>依法扣留车辆</b>：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者<b>未随车携带机动车行驶证、驾驶证的</b>。', '', '未携带', 0002, 0.05);
INSERT INTO `question` VALUES (1733399758485168130, 40, '14d02', 0002, '有下列哪种违法行为的机动车驾驶人将被一次记9分？ ', 'A、使用伪造机动车号牌上道路行驶 B、机动车驾驶证被暂扣期间驾驶机动车的 C、驾驶故意污损号牌的机动车上道路行驶 D、驾驶机动车行经人行横道不按规定避让行人的', '使用伪造机动车号牌上道路行驶 ', '机动车驾驶证被暂扣期间驾驶机动车的 ', '驾驶故意污损号牌的机动车上道路行驶 ', '驾驶机动车行经人行横道不按规定避让行人的', 'C', '《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，<b>一次记9分</b>：<br>（四）驾驶未悬挂机动车号牌或者故意遮挡、<b>污损机动车号牌的机动车上道路行驶的</b>；<br>【拓展知识】<br>记12分：使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；<br>记6分：驾驶证被暂扣或者扣留期间驾驶机动车的；<br>记3分：行经人行横道不按规定减速、停车、避让行人的。', '', '记9分', 0004, 0.18);
INSERT INTO `question` VALUES (1733399758560665602, 41, '536f6', 0001, '驾驶机动车造成重大交通事故后逃逸，构成犯罪的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第一百零一条规定，<b>造成交通事故后逃逸的</b>，由公安机关交通管理部门<b>吊销机动车驾驶证</b>，且<b>终生不得重新取得机动车驾驶证</b>。', '', '逃逸|构成犯罪|终生', 0002, 0.04);
INSERT INTO `question` VALUES (1733399758619385858, 42, '5a0ac', 0002, '隐瞒有关情况或者提供虚假材料申请机动车驾驶证，申请人在多少年内不得再次申领机动车驾驶证？ ', 'A、1年 B、2年 C、3年 D、4年', '1年 ', '2年 ', '3年 ', '4年', 'A', '<b>【假1】</b>《机动车驾驶证申领和使用规定》第九十三条，申请人<b>隐瞒有关情况或者提供虚假材料申领机动车驾驶证的</b>，公安机关交通管理部门不予受理或者不予办理，处五百元以下罚款；申请人在<b>一年内不得再次</b>申领机动车驾驶证。', '', '虚假材料|不得再次', 0004, 0.18);
INSERT INTO `question` VALUES (1733399758728437762, 43, '8b0a0', 0002, '有下列哪种违法行为的机动车驾驶人将被一次记9分？ ', 'A、使用伪造机动车号牌上道路行驶的 B、饮酒后驾驶机动车的 C、驾驶机动车不按交通信号灯指示通行的 D、未取得校车驾驶资格驾驶校车的', '使用伪造机动车号牌上道路行驶的 ', '饮酒后驾驶机动车的 ', '驾驶机动车不按交通信号灯指示通行的 ', '未取得校车驾驶资格驾驶校车的', 'D', '《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，一次<b>记9分</b>：<br>（六）<b>未取得校车驾驶资格</b>驾驶校车的；<br>【拓展知识】<br>记12分：使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；饮酒后驾驶机动车的；<br>记6分：不按交通信号灯指示通行的', '', '记9分', 0005, 0.31);
INSERT INTO `question` VALUES (1733399758824906753, 44, '267e7', 0002, '申请人在考试过程中有贿赂、舞弊行为的，申请人在多少年内不得再次申领机动车驾驶证？ ', 'A、1年 B、2年 C、3年 D、4年', '1年 ', '2年 ', '3年 ', '4年', 'A', '《机动车驾驶证申领和使用规定》第九十三条，申请人在<b>考试过程中有贿赂、舞弊行为的</b>，取消考试资格，已经通过考试的其他科目成绩无效，公安机关交通管理部门处二千元以下罚款；申请人在<b>一年内</b>不得再次申领机动车驾驶证。', '', '贿赂|舞弊', 0005, 0.31);
INSERT INTO `question` VALUES (1733399758887821313, 45, 'a1afb', 0001, '机动车参加安全技术检验的主要目的是检查车辆各项性能系数，及时消除车辆安全隐患，减少事故发生。', NULL, '对', '错', '空', '空', '对', '机动车参加安全技术检验的主要目的，检查车辆各项性能系数，及时消除车辆的安全隐患，从而有效减少交通事故的发生。', '', '目的|及时消除', 0001, 0.01);
INSERT INTO `question` VALUES (1733399758933958658, 46, '8a7a7', 0001, '道路交通事故中，机动车无号牌、检验合格标志、保险标志时，要保护现场并立即报警。', NULL, '对', '错', '空', '空', '对', '无两标两证一号牌的机动车不得上道路行驶。《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当<b>保护现场并立即报警</b>：　　 　　<br>（三）<b>机动车无号牌、无检验合格标志、无保险标志的；</b> 　', '', '保护现场', 0001, 0.01);
INSERT INTO `question` VALUES (1733399758988484610, 47, '0fd13', 0002, '申请小型汽车准驾车型驾驶证的人年龄条件是多少？ ', 'A、16周岁以上 B、18周岁以上 C、20周岁以上 D、22周岁以上', '16周岁以上 ', '18周岁以上 ', '20周岁以上 ', '22周岁以上', 'B', '《机动车驾驶证申领和使用规定》第十四条 ，申请机动车驾驶证的人，应当符合下列规定：（一）年龄条件：<br>1. <b>申请小型汽车</b>、小型自动挡汽车、残疾人专用小型自动挡载客汽车、轻便摩托车准驾车型的，<b>在18周岁以上</b>；', '', '年龄条件', 0002, 0.08);
INSERT INTO `question` VALUES (1733399759043010561, 48, 'ecb4d', 0002, '3年内有下列哪种行为的人不得申请机动车驾驶证？ ', 'A、吸烟成瘾 B、注射毒品 C、注射胰岛素 D、酒醉经历', '吸烟成瘾 ', '注射毒品 ', '注射胰岛素 ', '酒醉经历', 'B', '《机动车驾驶证申领和使用规定》第十五条 有下列情形之一的，不得申请机动车驾驶证:（二）三年内有吸食、<b>注射毒品</b>行为或者解除强制隔离戒毒措施未满三年，以及长期服用依赖性精神药品成瘾尚未戒除的。', '', '不得申请', 0003, 0.16);
INSERT INTO `question` VALUES (1733399759110119425, 49, 'b4051', 0001, '申请小型汽车驾驶证的，年龄应在18周岁以上70周岁以下。', NULL, '对', '错', '空', '空', '错', '<b>申请小车的年龄条件：有底线（18周岁），无上限。</b>《机动车驾驶证申领和使用规定》第十四条 ，申请机动车驾驶证的人，应当符合下列规定：（一）年龄条件：<br>1. <b>申请小型汽车</b>、小型自动挡汽车、残疾人专用小型自动挡载客汽车、轻便摩托车准驾车型的，<b>在18周岁以上</b>；', '', '年龄', 0004, 0.24);
INSERT INTO `question` VALUES (1733399759173033985, 50, '2d545', 0001, '酒后驾驶发生重大交通事故被依法追究刑事责任的人不能申请机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第十五条 有下列情形之一的，不得申请机动车驾驶证:（四）<b>饮酒后或者醉酒驾驶机动车发生重大交通事故构成犯罪</b>的。', '', '依法追究', 0002, 0.06);
INSERT INTO `question` VALUES (1733399759227559937, 51, '45031', 0002, '驾驶人在驾驶证有效期满前多长时间申请换证？ ', 'A、30日内 B、60日内 C、90日内 D、6个月内', '30日内 ', '60日内 ', '90日内 ', '6个月内', 'C', '《机动车驾驶证申领和使用规定》第六十三条 机动车驾驶人应当于机动车驾驶证<b>有效期满前九十日内</b>，向机动车驾驶证核发地或者核发地以外的车辆管理所申请换证。', '', '期满前', 0005, 0.31);
INSERT INTO `question` VALUES (1733399759286280194, 52, 'b2c67', 0002, '驾驶人户籍迁出原车辆管理所需要向什么地方的车辆管理所提出申请？ ', 'A、迁出地 B、居住地 C、所在地 D、迁入地', '迁出地 ', '居住地 ', '所在地 ', '迁入地', 'D', '《机动车驾驶证申领和使用规定》第六十四条 机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向<b>迁入地车辆管理所申请换证</b>。', '', '户籍迁出', 0003, 0.14);
INSERT INTO `question` VALUES (1733399759357583361, 53, '10f52', 0002, '驾驶证记载的驾驶人信息发生变化的要在多长时间内申请换证？ ', 'A、60日 B、50日 C、40日 D、30日', '60日 ', '50日 ', '40日 ', '30日', 'D', '《机动车驾驶证申领和使用规定》第六十六条 有下列情形之一的，机动车驾驶人应当在<b>三十日内</b>到机动车驾驶证核发地或者核发地以外的车辆管理所申请换证：（一）在车辆管理所管辖区域内，<b>机动车驾驶证记载的机动车驾驶人信息发生变化</b>的；', '', '申请换证|信息发生变化', 0002, 0.08);
INSERT INTO `question` VALUES (1733399759420497921, 54, '0d295', 0001, '驾驶人在驾驶证核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第六十四条，机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。机动车驾驶人在核发地车辆管理所管辖区<b>以外居住的</b>，可以<b>向居住地车辆管理所申请换证</b>。申请时应当填写申请表，提交机动车驾驶人的身份证明和机动车驾驶证，并申报身体条件情况。【车登记，证核发，期满换证居住地】', '', '申请换证|核发地', 0003, 0.15);
INSERT INTO `question` VALUES (1733399759495995394, 55, '8a6f1', 0001, '机动车驾驶人一次有两个以上违法行为记分的，应当分别计算累加分值。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十四条，机动车驾驶人<b>有二起以上</b>交通违法行为应当予以记分的，记分分值<b>累积计算</b>。', '', '两个以上', 0003, 0.09);
INSERT INTO `question` VALUES (1733399759558909954, 56, '17805', 0001, '道路交通安全违法行为累积记分一个周期满分为12分。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十一条，公安机关交通管理部门对机动车驾驶人的道路交通安全违法行为，除依法给予行政处罚外，实行道路交通安全违法行为累积记分制度，<b>记分周期为12个月，满分为12分</b>。<br>机动车驾驶人在一个记分周期内记分达到12分的，应当按规定参加学习、考试。', '', '一个周期', 0002, 0.04);
INSERT INTO `question` VALUES (1733399759638601729, 57, '7dc86', 0001, '驾驶人记分没有达到满分，有罚款尚未缴纳的，记分转入下一记分周期。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十五条 机动车驾驶人在一个记分周期期限届满，累积记分未满12分的，该记分周期内的记分予以清除；累积记分虽未满12分，但有罚款逾期<b>未缴纳的</b>，该记分周期内尚未缴纳罚款的交通违法行为记分分值<b>转入下一记分周期</b>。', '', '尚未缴纳', 0002, 0.08);
INSERT INTO `question` VALUES (1733399759705710593, 58, 'a49d7', 0002, '持小型汽车驾驶证的驾驶人在下列哪种情况下需要接受审验？ ', 'A、有效期满换发驾驶证时 B、一个记分周期末 C、记分周期未满分 D、记分周期满12分', '有效期满换发驾驶证时 ', '一个记分周期末 ', '记分周期未满分 ', '记分周期满12分', 'A', '机动车驾驶人按照《机动车驾驶证申领和使用规定》第六十三条、第六十四条<b>换领机动车驾驶证</b>时，应当接受<b>公安机关交通管理部门的审验</b>。', '', '需要', 0004, 0.26);
INSERT INTO `question` VALUES (1733399759797985281, 59, '8dae5', 0002, '驾驶人因服兵役、出国（境）等原因无法办理审验时，延期审验期限最长不超过多长时间？ ', 'A、1年 B、2年 C、3年 D、5年', '1年 ', '2年 ', '3年 ', '5年', 'C', '《机动车驾驶证申领和使用规定》第七十五条 机动车驾驶人因服兵役、出国（境）等原因，无法在规定时间内办理驾驶证期满换证、审验、提交身体条件证明的，可以在驾驶证有效期内或者有效期届满一年内向机动车驾驶证核发地车辆管理所申请延期办理。申请时应当确认申请信息，并提交机动车驾驶人的身份证明。<b>延期期限最长不超过三年</b>。延期期间机动车驾驶人不得驾驶机动车。', '', '审验期限', 0004, 0.17);
INSERT INTO `question` VALUES (1733399759852511233, 60, '146ea', 0001, '小型汽车驾驶人发生交通事故造成人员死亡，承担同等以上责任未被吊销驾驶证的，应当在记分周期结束后30日内接受审验。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十二条：持有大型客车、重型牵引挂车、城市公交车、中型客车、大型货车以外准驾车型驾驶证的驾驶人，发生交通事故造成人员死亡承担同等以上责任未被吊销机动车驾驶证的，应当在本记分周期结束后<b>三十日内</b>到公安机关交通管理部门接受审验。', '', '未被吊销|30日', 0001, 0.04);
INSERT INTO `question` VALUES (1733399759907037186, 61, 'fb755', 0001, '驾驶人因服兵役、出国（境）等原因延期审验期间不得驾驶机动车。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十五条 机动车驾驶人因服兵役、出国（境）等原因，无法在规定时间内办理驾驶证期满换证、审验、提交身体条件证明的，可以在驾驶证有效期内或者有效期届满一年内向机动车驾驶证核发地车辆管理所申请延期办理。申请时应当确认申请信息，并提交机动车驾驶人的身份证明。延期期限最长不超过三年。<b>延期期间机动车驾驶人不得驾驶机动车</b>。', '', '不得驾驶', 0002, 0.06);
INSERT INTO `question` VALUES (1733399759953174529, 62, '530d9', 0002, '将机动车交由未取得机动车驾驶证的人驾驶的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处以下哪种处罚？ ', 'A、15日以下拘留 B、吊销驾驶证 C、扣留车辆 D、5年不得重新取得新驾驶证', '15日以下拘留 ', '吊销驾驶证 ', '扣留车辆 ', '5年不得重新取得新驾驶证', 'B', '将车借给未取得驾驶证的人驾驶的，<b>要负连带责任</b>，<b>出借人</b>会受到如下处罚：<b>罚款200元以上2000元以下</b>，<b>吊销驾驶证</b>。根据《道路交通安全法》第九十九条规定：有下列行为之一的，由<b>公安机关</b>交通管理部门处200元以上2000元以下罚款：<br><b>2、将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；</b><br>行为人<b>有前款第二项</b>、第四项情形之一的，可以<b>并处吊销机动车驾驶证</b>。', '', '交由未取得|可以并处', 0005, 0.32);
INSERT INTO `question` VALUES (1733399760007700481, 63, 'c5053', 0002, '年龄在70周岁以上的驾驶人多长时间提交一次身体条件证明？ ', 'A、每3年 B、每2年 C、每1年 D、每6个月', '每3年 ', '每2年 ', '每1年 ', '每6个月', 'C', '《机动车驾驶证申领和使用规定》第七十四条 年龄在70周岁以上的机动车驾驶人，应当<b>每年进行一次身体检查</b>，在记分周期结束后三十日内，<b>提交医疗机构出具的有关身体条件的证明</b>。', '', '身体条件证明', 0001, 0.03);
INSERT INTO `question` VALUES (1733399760079003649, 64, 'a6be1', 0001, '持有大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶证的驾驶人，应当在每个记分周期结束后三十日内到公安机关交通管理部门接受审验。但在一个记分周期内没有记分记录的，免予本记分周期审验。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十二条 持有大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶证的驾驶人，应当在每个记分周期结束后三十日内到公安机关交通管理部门接受审验。但<b>在一个记分周期内没有记分记录的，免予本记分周期审验</b>。', '', '应当|没有记分记录|免予', 0002, 0.05);
INSERT INTO `question` VALUES (1733399760175472641, 65, '02d55', 0002, '机动车驾驶人初次取得汽车类准驾车型或者初次取得摩托车类准驾车型后的多长时间为实习期？ ', 'A、6个月 B、12个月 C、3个月 D、2年', '6个月 ', '12个月 ', '3个月 ', '2年', 'B', '《机动车驾驶证申领和使用规定》第七十六条，机动车驾驶人初次取得汽车类准驾车型或者初次取得摩托车类准驾车型后的<b>12个月</b>为实习期。', '', '初次取得|实习期', 0002, 0.04);
INSERT INTO `question` VALUES (1733399760229998594, 66, 'ead7a', 0001, '初次申领驾驶证的驾驶人在实习期内可以单独驾驶机动车上高速公路行驶。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第七十七条驾驶人在<b>实习期</b>内驾驶机动车上高速公路行驶，应当<b>由持相应或者包含其准驾车型驾驶证三年以上</b>的驾驶人陪同。其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', '', '实习期内', 0002, 0.06);
INSERT INTO `question` VALUES (1733399760280330241, 67, 'b61cc', 0001, '驾驶人在实习期内驾驶机动车时，应当在车身后部粘贴或者悬挂统一式样的实习标志。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十六条，机动车驾驶人初次取得汽车类准驾车型或者初次取得摩托车类准驾车型后的12个月为实习期。<br><b>在实习期内驾驶机动车的</b>，应当<b>在车身后部粘贴或者悬挂统一式样的实习标志</b>。', '', '统一式样|应当', 0001, 0.01);
INSERT INTO `question` VALUES (1733399760339050498, 68, '0b878', 0001, '机动车驾驶人在实习期内有记满12分记录的，注销其实习的准驾车型驾驶资格。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第八十条，机动车驾驶人在实习期内发生的道路交通安全违法行为<b>被记满12分的</b>，<b>注销</b>其实习的准驾车型驾驶资格。', '', '记满12分|注销', 0002, 0.08);
INSERT INTO `question` VALUES (1733399760389382145, 69, 'bc56c', 0002, '提供虚假材料申领驾驶证的申请人会承担下列哪种法律责任？ ', 'A、处20元以上200元以下罚款 B、取消申领驾驶证资格 C、1年内不得再次申领驾驶证 D、2年内不能再次申领驾驶证', '处20元以上200元以下罚款 ', '取消申领驾驶证资格 ', '1年内不得再次申领驾驶证 ', '2年内不能再次申领驾驶证', 'C', '《机动车驾驶证申领和使用规定》第九十三条，申请人隐瞒有关情况或者<b>提供虚假材料申领机动车驾驶证的</b>，公安机关交通管理部门不予受理或者不予办理，处五百元以下罚款；申请人在<b>一年内不得再次申领</b>机动车驾驶证。【假1】', '', '虚假材料', 0004, 0.23);
INSERT INTO `question` VALUES (1733399760477462530, 70, 'd9ed7', 0001, '伪造、变造或者使用伪造、变造驾驶证的驾驶人构成犯罪的，将依法追究刑事责任。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十六条　伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', '', '构成犯罪|追究', 0001, 0.02);
INSERT INTO `question` VALUES (1733399760540377090, 71, 'f9601', 0002, '驾驶人有下列哪种违法行为一次记12分？ ', 'A、不按交通信号灯指示通行的 B、使用伪造机动车号牌 C、违反禁令标志指示 D、拨打、接听手机的', '不按交通信号灯指示通行的 ', '使用伪造机动车号牌 ', '违反禁令标志指示 ', '拨打', 'B', '《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：（三）<b>使用伪造</b>、变造的<b>机动车号牌</b>、行驶证、驾驶证、校车标牌或者使用其他<b>机动车号牌</b>、行驶证的。', '', '记12分', 0002, 0.06);
INSERT INTO `question` VALUES (1733399760636846081, 72, '92979', 0002, '驾驶与准驾车型不符的机动车一次记多少分？ ', 'A、9分 B、6分 C、3分 D、1分', '9分 ', '6分 ', '3分 ', '1分', 'A', '《道路交通安全违法行为记分管理办法》第九条 机动车驾驶人有下列交通违法行为之一，<b>一次记9分</b>：<br><b>（五）驾驶与准驾车型不符的机动车的。</b>', '', '准驾车型不符', 0005, 0.34);
INSERT INTO `question` VALUES (1733399760687177730, 73, 'af84c', 0001, '无证驾驶可构成危险驾驶罪。', NULL, '对', '错', '空', '空', '错', '<b>无证驾驶不构成危险驾驶罪</b>。危险驾驶罪是指在醉酒状态，或者以追逐竞驶、严重超载、违法运输危险化学品等方式在道路上驾驶机动车，危及公共安全所构成的罪名。', '', '无证驾驶', 0005, 0.38);
INSERT INTO `question` VALUES (1733399760758480897, 74, 'effab', 0002, '饮酒后驾驶机动车一次记几分？ ', 'A、3分 B、6分 C、9分 D、12分', '3分 ', '6分 ', '9分 ', '12分', 'D', '《道路交通安全违法行为记分管理办法》第八条机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：（一）饮酒后驾驶机动车的。', '', '饮酒后', 0003, 0.10);
INSERT INTO `question` VALUES (1733399760813006849, 75, 'c0d35', 0001, '处于实习期的驾驶人，任何情况下都不允许上高速。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第七十七条 驾驶人在实习期内驾驶机动车上高速公路行驶，<b>应当由持相应或者包含其准驾车型驾驶证三年以上的驾驶人陪同</b>。其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', '', '实习期|都不允许', 0003, 0.13);
INSERT INTO `question` VALUES (1733399760863338498, 76, 'bda94', 0002, '造成致人轻伤以上或者死亡的交通事故后逃逸，尚不构成犯罪的一次记几分？ ', 'A、12分 B、9分 C、6分 D、3分', '12分 ', '9分 ', '6分 ', '3分', 'A', '《道路交通安全违法行为记分管理办法》第八条机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：（二）<b>造成致人轻伤以上</b>或者死亡的交通事故后逃逸，<b>尚不构成犯罪的</b>。', '', '不构成犯罪', 0004, 0.17);
INSERT INTO `question` VALUES (1733399760959807490, 77, 'd95e9', 0002, '上道路行驶的机动车未悬挂机动车号牌的一次记多少分？ ', 'A、12分 B、3分 C、6分 D、9分', '12分 ', '3分 ', '6分 ', '9分', 'D', '《道路交通安全违法行为记分管理办法》第九条机动车驾驶人有下列交通违法行为之一，<b>一次记9分</b>：（四）驾驶<b>未悬挂机动车号牌</b>或者故意遮挡、污损机动车号牌的机动车上道路行驶的。', '', '未悬挂', 0005, 0.45);
INSERT INTO `question` VALUES (1733399761022722049, 78, 'abfdf', 0002, '上道路行驶的机动车故意遮挡、污损机动车号牌的一次记多少分？ ', 'A、9分 B、6分 C、3分 D、12分', '9分 ', '6分 ', '3分 ', '12分', 'A', '《道路交通安全违法行为记分管理办法》第九条机动车驾驶人有下列交通违法行为之一，<b>一次记9分</b>：（四）驾驶未悬挂机动车号牌或者<b>故意遮挡、污损机动车号牌</b>的机动车上道路行驶的。', '', '故意遮挡', 0004, 0.23);
INSERT INTO `question` VALUES (1733399761102413825, 79, '17dd0', 0002, '使用伪造、变造的机动车号牌一次记几分？ ', 'A、9分 B、3分 C、6分 D、12分', '9分 ', '3分 ', '6分 ', '12分', 'D', '《道路交通安全违法行为记分管理办法》第八条机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：（三）<b>使用伪造、变造的机动车</b>号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的。', '', '伪造|变造', 0004, 0.19);
INSERT INTO `question` VALUES (1733399761194688513, 80, '7b520', 0002, '使用伪造、变造的行驶证一次记几分？ ', 'A、12分 B、6分 C、3分 D、9分', '12分 ', '6分 ', '3分 ', '9分', 'A', '《道路交通安全违法行为记分管理办法》第八条机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：（三）使用<b>伪造、变造</b>的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的。', '', '伪造|变造', 0003, 0.11);
INSERT INTO `question` VALUES (1733399761270185986, 81, '3ee5e', 0002, '驾驶人驾驶机动车不按交通信号灯指示通行的，一次记多少分？ ', 'A、1分 B、3分 C、6分 D、12分', '1分 ', '3分 ', '6分 ', '12分', 'C', '《道路交通安全违法行为记分管理办法》第十条机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：（八）驾驶机动车不按交通信号灯指示通行的。', '', '交通信号灯', 0005, 0.35);
INSERT INTO `question` VALUES (1733399761333100545, 82, '3f4ea', 0002, '有下列哪种违法行为的机动车驾驶人将被一次记6分？ ', 'A、驾驶与准驾车型不符的机动车 B、饮酒后驾驶机动车 C、驾驶机动车不按交通信号灯指示通行 D、未取得校车驾驶资格驾驶校车', '驾驶与准驾车型不符的机动车 ', '饮酒后驾驶机动车 ', '驾驶机动车不按交通信号灯指示通行 ', '未取得校车驾驶资格驾驶校车', 'C', '《道路交通安全违法行为记分管理办法》第十条机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：（八）<b>驾驶机动车不按交通信号灯指示通行的。</b>', '', '记6分', 0004, 0.25);
INSERT INTO `question` VALUES (1733399761425375234, 83, 'af85b', 0002, '有下列哪种违法行为的机动车驾驶人将被一次记12分？ ', 'A、驾驶小型汽车在高速路行驶超速50%以上 B、驾驶故意污损号牌的机动车上道路行驶 C、机动车驾驶证被暂扣期间驾驶机动车的 D、驾驶机动车不按规定避让校车的', '驾驶小型汽车在高速路行驶超速50%以上 ', '驾驶故意污损号牌的机动车上道路行驶 ', '机动车驾驶证被暂扣期间驾驶机动车的 ', '驾驶机动车不按规定避让校车的', 'A', '<b>【50%以上】高速、快速路扣12分</b>；普通路扣6分。《道路交通安全违法行为记分管理办法》第八条机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：<br>（五）驾驶校车、中型以上载客载货汽车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速百分之二十以上，或者驾驶其他机动车在高速公路、城市快速路上行驶<b>超过规定时速百分之五十以上</b>的。<br>【拓展知识】<br>号牌未悬挂、污损、遮挡，记9分；<br>驾照暂扣期间，记6分；<br>礼让3分，不避让校车、行人，记3分。', '', '违法行为|记12分', 0004, 0.26);
INSERT INTO `question` VALUES (1733399761509261314, 84, 'ccf76', 0001, '饮酒后驾驶机动车的一次记12分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，一次<b>记12分</b>：<br>（一）饮酒后驾驶机动车的；', '', '饮酒', 0002, 0.07);
INSERT INTO `question` VALUES (1733399761567981569, 85, 'ddfe3', 0001, '使用伪造、变造的驾驶证一次记12分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》机动车驾驶人有下列违法行为之一，<b>一次记12分</b>： <br>（三）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；', '', '伪造|变造', 0002, 0.05);
INSERT INTO `question` VALUES (1733399761635090433, 86, 'a8500', 0001, '使用其他机动车号牌、行驶证的一次记3分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》机动车驾驶人有下列违法行为之一，<b>一次记12分</b>： <br>（三）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者<b>使用其他机动车号牌、行驶证的</b>；', '', '其他', 0004, 0.21);
INSERT INTO `question` VALUES (1733399761698004993, 87, 'ffc61', 0001, '驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的一次记6分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第八条机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：<br>（六）驾驶机动车在高速公路、城市快速路上<b>倒车、逆行、穿越中央分隔带掉头的</b>。<br>【高速记分】慢3占6停9<b>倒逆12</b>。', '', '高速公路', 0004, 0.19);
INSERT INTO `question` VALUES (1733399761752530946, 88, 'ccfd7', 0001, '不按交通信号灯指示通行的一次记6分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（八）驾驶机动车<b>不按交通信号灯指示通行</b>的；', '', '交通信号灯', 0004, 0.16);
INSERT INTO `question` VALUES (1733399761798668289, 89, 'f1672', 0001, '驾驶小型汽车在高速公路行驶，车速超过规定时速50%以上的一次记12分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：<br>（五）驾驶校车、中型以上载客载货汽车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速百分之二十以上，或者驾驶其他机动车<b>在高速公路、城市快速路上行驶超过规定时速百分之五十以上的。</b>', '', '高速公路|50%以上', 0004, 0.23);
INSERT INTO `question` VALUES (1733399761853194242, 90, 'a339c', 0001, '驾驶机动车在高速公路违法占用应急车道行驶的一次记6分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（十一）驾驶机动车在高速公路或者城市快速路上<b>违法占用应急车道</b>行驶的。', '', '占用应急车道|6分', 0003, 0.15);
INSERT INTO `question` VALUES (1733399761899331585, 91, 'ce80e', 0001, '驾驶机动车不按规定避让校车的，一次记3分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分：</b><br>（八）驾驶机动车<b>不按规定避让校车的</b>。', '', '避让校车|记3分', 0004, 0.17);
INSERT INTO `question` VALUES (1733399761941274626, 92, 'fc93c', 0002, '已注册登记的小型载客汽车有下列哪种情形，所有人不需要办理变更登记？ ', 'A、机动车更换发动机 B、加装前后防撞装置 C、改变车身颜色 D、更换车身或者车架', '机动车更换发动机 ', '加装前后防撞装置 ', '改变车身颜色 ', '更换车身或者车架', 'B', '<b>请注意：不需要办理</b>。《机动车登记规定》<b>第十六条</b>，已注册登记的机动车有下列情形之一的，机动车所有人<b>应当向登记地车辆管理所申请变更登记</b>：<br><b>（一）改变车身颜色的；</b><br><b>（二）更换发动机的；</b><br><b>（三）更换车身或者车架的；</b><br>（四）因质量问题更换整车的；<br>（五）机动车登记的使用性质改变的；<br>（六）机动车所有人的住所迁出、迁入车辆管理所管辖区域的。', '', '不需要', 0003, 0.12);
INSERT INTO `question` VALUES (1733399761983217666, 93, 'e93f2', 0001, '机动车达到国家规定的强制报废标准的不能办理注册登记。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》<b>第十五条，</b>有下列情形之一的，不予办理注册登记：（七）<b>机动车达到国家规定的强制报废标准的。</b>', '', '不能办理', 0002, 0.06);
INSERT INTO `question` VALUES (1733399762037743618, 94, '233d9', 0001, '机动车行驶证灭失、丢失，机动车所有人要向登记地车辆管理所申请补领、换领。', NULL, '对', '错', '空', '空', '对', '【车登记】根据《机动车登记规定》第五十二条，机动车登记证书、<b>行驶证灭失、丢失</b>或者损毁的，机动车所有人应当向<b>登记地车辆管理所</b>申请补领、换领。申请时，机动车所有人应当确认申请信息并提交身份证明。', '', '登记地', 0002, 0.05);
INSERT INTO `question` VALUES (1733399762083880961, 95, '61b7e', 0001, '机动车号牌损毁，机动车所有人要向登记地车辆管理所申请补领、换领。', NULL, '对', '错', '空', '空', '对', '【车登记】《机动车登记规定》第五十一条，<b>机动车号牌灭失、丢失或者损毁</b>的，机动车所有人应当向<b>登记地车辆管理所</b>申请补领、换领。申请时，机动车所有人应当确认申请信息并提交身份证明。<br>车辆管理所应当审查提交的证明、凭证，收回未灭失、丢失或者损毁的号牌，自受理之日起十五日内补发、换发号牌，原机动车号牌号码不变。<br>补发、换发号牌期间，申请人可以申领有效期不超过十五日的临时行驶车号牌。<br>补领、换领机动车号牌的，原机动车号牌作废，不得继续使用。', '', '登记地', 0002, 0.04);
INSERT INTO `question` VALUES (1733399762134212609, 96, 'bc058', 0002, '初次申领的机动车驾驶证的有效期为多少年？ ', 'A、3年 B、5年 C、6年 D、12年', '3年 ', '5年 ', '6年 ', '12年', 'C', '《机动车驾驶证申领和使用规定》第十三条，机动车驾驶证有效期分为<b>六年</b>、十年和长期。', '', '初次申领', 0003, 0.12);
INSERT INTO `question` VALUES (1733399762201321473, 97, 'b5ab0', 0002, '准驾车型为小型汽车的，可以驾驶下列哪种车辆？ ', 'A、低速载货汽车 B、中型客车 C、三轮摩托车 D、轮式自行机械车', '低速载货汽车 ', '中型客车 ', '三轮摩托车 ', '轮式自行机械车', 'A', '小型汽车（C1）准驾车型：小型、微型载客汽车以及轻型、微型载货汽车；轻型、微型专项作业车，小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车，<b>低速载货汽车</b>，三轮汽车。', '', '小型汽车', 0005, 0.28);
INSERT INTO `question` VALUES (1733399762264236033, 98, 'a5133', 0002, '准驾车型为小型自动挡汽车的，可以驾驶以下哪种车型？ ', 'A、低速载货汽车 B、小型汽车 C、二轮摩托车 D、轻型自动挡载货汽车', '低速载货汽车 ', '小型汽车 ', '二轮摩托车 ', '轻型自动挡载货汽车', 'D', '小型自动挡汽车（C2）准驾的车辆：小型、微型自动挡载客汽车以及<b>轻型、<b>微型自动挡载货汽车</b>；轻型、微型自动挡专项作业车；上肢残疾人专用小型自动挡载客汽车</b>', '', '小型自动挡', 0004, 0.20);
INSERT INTO `question` VALUES (1733399762322956290, 99, 'bc157', 0001, '驾驶人在机动车驾驶证的6年有效期内，每个记分周期均未达到12分的，换发10年有效期的机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第六十二条，机动车驾驶人在机动车驾驶证的<b>六年有效期内</b>，<b>每个记分周期均未记满12分的，换发十年有效期的机动车驾驶证</b>；在机动车驾驶证的十年有效期内，每个记分周期均未记满12分的，换发长期有效的机动车驾驶证。', '', '有效期内|未达到12分', 0003, 0.08);
INSERT INTO `question` VALUES (1733399762377482242, 100, '7a9a5', 0001, '机动车驾驶证有效期分为6年、10年、20年。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第十三条 机动车驾驶证有效期分为<b>六年、十年和长期</b>。', '', '有效期', 0003, 0.15);
INSERT INTO `question` VALUES (1733399762436202497, 101, '4ae6b', 0001, '准驾车型为小型汽车的，可以驾驶小型自动挡载客汽车。', NULL, '对', '错', '空', '空', '对', '小型汽车（C1）准驾车型：小型、微型载客汽车以及轻型、微型载货汽车；轻型、微型专项作业车，小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车，低速载货汽车，三轮汽车。', '', '小型自动挡', 0003, 0.15);
INSERT INTO `question` VALUES (1733399762482339842, 102, '98c5d', 0001, '准驾车型为小型自动挡汽车的，可以驾驶低速载货汽车。', NULL, '对', '错', '空', '空', '错', '小型自动挡汽车（C2）准驾车型：小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车。<b>低速载货汽车不在C2驾驶证的使用范围内</b>，因此，题干表述错误。', '', '小型自动挡|低速载货', 0005, 0.29);
INSERT INTO `question` VALUES (1733399762532671489, 103, '28121', 0001, '初次申领的机动车驾驶证的有效期为6年。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第十三条 机动车驾驶证有效期分为<b>六年、十年和长期</b>。【6、10、长】', '', '有效期', 0002, 0.06);
INSERT INTO `question` VALUES (1733399762599780353, 104, '9d00f', 0001, '初次申领的机动车驾驶证的有效期为4年。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第十三条，机动车驾驶证有效期分为<b>六年、十年和长期</b>。', '', '初次|有效期', 0002, 0.04);
INSERT INTO `question` VALUES (1733399762679472130, 105, '67bd6', 0002, '初次申领机动车驾驶证的，可以申请下列哪种准驾车型？ ', 'A、中型客车 B、大型客车 C、普通三轮摩托车 D、轻型牵引挂车', '中型客车 ', '大型客车 ', '普通三轮摩托车 ', '轻型牵引挂车', 'C', '《机动车驾驶证申领和使用规定》第十六条，<b>初次申领机动车驾驶证的</b>，<b>可以申请准驾车型</b>为城市公交车、大型货车、小型汽车、小型自动挡汽车、低速载货汽车、三轮汽车、残疾人专用小型自动挡载客汽车、<b>普通三轮摩托车</b>、普通二轮摩托车、轻便摩托车、轮式专用机械车、无轨电车、有轨电车的机动车驾驶证。', '', '初次申领', 0004, 0.24);
INSERT INTO `question` VALUES (1733399762725609474, 106, '8720f', 0002, '年满20周岁，可以初次申请下列哪种准驾车型？ ', 'A、大型货车 B、大型客车 C、中型客车 D、重型牵引挂车', '大型货车 ', '大型客车 ', '中型客车 ', '重型牵引挂车', 'A', '大型货车可以<b>初次申领</b>；大型客车、中型客车及牵引车均需增驾申领。<br>《机动车驾驶证申领和使用规定》第十六条，<b><b>初次申领</b>机动车驾驶证的</b>，<b>可以申请准驾车型</b>为城市公交车、<b>大型货车</b>、小型汽车、小型自动挡汽车、低速载货汽车、三轮汽车、残疾人专用小型自动挡载客汽车、普通三轮摩托车、普通二轮摩托车、轻便摩托车、轮式专用机械车、无轨电车、有轨电车的机动车驾驶证。', '', '初次|准驾车型', 0005, 0.35);
INSERT INTO `question` VALUES (1733399762771746818, 107, '8dd28', 0001, '小型手动挡汽车科目二考试内容包括倒车入库、坡道定点停车和起步、侧方停车、曲线行驶、直角转弯。', NULL, '对', '错', '空', '空', '对', '请注意区分<b>自动挡or手动挡</b>。《机动车驾驶证申领和使用规定》第三十三条 ，科目二考试内容包括：<br>（二）<b>小型汽车</b>、低速载货汽车考试<b>倒车入库、坡道定点停车和起步、侧方停车、曲线行驶、直角转弯</b>；<br>（三）小型自动挡汽车、残疾人专用小型自动挡载客汽车考试倒车入库、侧方停车、曲线行驶、直角转弯；', '', '手动挡汽车|包括', 0001, 0.03);
INSERT INTO `question` VALUES (1733399762822078466, 108, '8de9e', 0001, '科目三考试分为道路驾驶技能考试和安全文明驾驶常识考试两部分。', NULL, '对', '错', '空', '空', '对', '机动车驾驶人考试内容分为道路交通安全法律、法规和相关知识考试科目（以下简称“科目一”）、场地驾驶技能考试科目（以下简称“科目二”）、<b>道路驾驶技能和安全文明驾驶常识考试科目（以下简称“科目三”）。</b>', '', '科目三考试', 0002, 0.07);
INSERT INTO `question` VALUES (1733399762897575938, 109, '41388', 0001, '科目三道路驾驶技能和安全文明驾驶常识考试满分分别为100分，成绩分别达到80和90分的为合格。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第三十八条 各科目考试的合格标准为：<br>（一）科目一考试满分为100分，成绩达到90分的为合格；<br>（二）科目二考试满分为100分，考试大型客车、重型牵引挂车、城市公交车、中型客车、大型货车、轻型牵引挂车准驾车型的，成绩达到90分的为合格，其他准驾车型的成绩达到80分的为合格；<br>（三）<b>科目三道路驾驶技能和安全文明驾驶常识考试满分分别为100分，成绩分别达到90分的为合格</b>。', '', '科目三|80和90|考试', 0003, 0.13);
INSERT INTO `question` VALUES (1733399762956296194, 110, '2d2d9', 0002, '在学习驾驶证明有效期内，科目二和科目三道路驾驶技能考试预约考试的次数分别不得超过多少次？ ', 'A、3次 B、4次 C、5次 D、6次', '3次 ', '4次 ', '5次 ', '6次', 'C', '《机动车驾驶证申领和使用规定》第四十七条，在学习驾驶证明有效期内，科目二和科目三道路驾驶技能考试<b>预约考试的次数分别不得超过五次</b>。第五次考试仍不合格的，已考试合格的其他科目成绩作废。', '', '预约考试|不得超过', 0003, 0.10);
INSERT INTO `question` VALUES (1733399763002433538, 111, '44e5e', 0002, '学习驾驶证明的有效期是多久？ ', 'A、1年 B、2年 C、3年 D、4年', '1年 ', '2年 ', '3年 ', '4年', 'C', '《机动车驾驶证申领和使用规定》第四十一条，申请人在场地和道路上学习驾驶，应当按规定取得学习驾驶证明。<b>学习驾驶证明的有效期为三年</b>，但有效期截止日期不得超过申请年龄条件上限。申请人应当在有效期内完成科目二和科目三考试。未在有效期内完成考试的，已考试合格的科目成绩作废。', '', '有效期', 0004, 0.18);
INSERT INTO `question` VALUES (1733399763052765185, 112, '04032', 0001, '申请人因故不能按照预约时间参加考试的，应当提前一日申请取消预约，对申请人未按照预约考试时间参加考试的，判定该次考试不合格。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第四十三条，申请人<b>因故不能</b>按照预约时间参加考试的，应当<b>提前一日申请取消</b>预约。对申请人<b>未按照预约考试时间参加考试的</b>，判定该次考试<b>不合格</b>。', '', '应当提前|不合格|未按照', 0002, 0.04);
INSERT INTO `question` VALUES (1733399763090513921, 113, 'dfb7b', 0001, '机动车驾驶证遗失、损毁无法辨认时，机动车驾驶人可以向机动车驾驶证核发地车辆管理所申请补发。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第六十九条 机动车驾驶证遗失的，机动车驾驶人应当向机动车驾驶证核发地<b>或者</b>核发地以外的车辆管理所申请补发。申请时应当确认申请信息，并提交机动车驾驶人的身份证明。符合规定的，车辆管理所应当在一日内补发机动车驾驶证。<br><b>即驾驶证遗失或损毁无法辨认的，可以向核发地车管所，也可以向核发地以外的车管所申请补发。</b>', '', '驾驶证|可以向|核发地', 0002, 0.08);
INSERT INTO `question` VALUES (1733399763149234177, 114, '6e2a2', 0001, '有吸食、注射毒品后驾驶车辆行为的机动车驾驶人，不会被注销驾驶证。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第七十九条 机动车驾驶人有下列情形之一的，车辆管理所<b>应当注销其机动车驾驶证</b>：<br><b>（六）被查获有吸食、注射毒品后驾驶机动车行为，依法被责令社区戒毒、社区康复或者决定强制隔离戒毒，或者长期服用依赖性精神药品成瘾尚未戒除的；</b>', '', '毒品|不会', 0003, 0.08);
INSERT INTO `question` VALUES (1733399763203760130, 115, '80c67', 0001, '正在执行社区戒毒、强制隔离戒毒、社区康复措施，车辆管理所将注销其驾驶证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十九条 机动车驾驶人有下列情形之一的，车辆管理所应当<b>注销其机动车驾驶证</b>：<br>（六）被查获有吸食、注射毒品后驾驶机动车行为，<b>依法被责令社区戒毒、社区康复或者决定强制隔离戒毒</b>，或者长期服用依赖性精神药品成瘾尚未戒除的；', '', '强制|注销其驾驶证', 0002, 0.04);
INSERT INTO `question` VALUES (1733399763279257601, 116, '893a5', 0002, '机动车登记证书、号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向哪个部门申请补领、换领？ ', 'A、驻地交警支队车辆管理所 B、驾驶证核发地车辆管理所 C、登记地车辆管理所 D、当地公安局', '驻地交警支队车辆管理所 ', '驾驶证核发地车辆管理所 ', '登记地车辆管理所 ', '当地公安局', 'C', '<b>【车登记】</b>《机动车登记规定》第五十一条 <b>机动车号牌</b>灭失、丢失或者损毁的，机动车所有人应当向<b><b>登记地车辆管理所</b></b>申请补领、换领。申请时，机动车所有人应当确认申请信息并提交身份证明。<br>第五十二条 <b>机动车登记证书、行驶证</b>灭失、丢失或者损毁的，机动车所有人应当向<b>登记地车辆管理所</b>申请补领、换领。申请时，机动车所有人应当确认申请信息并提交身份证明。', '', '丢失或损毁|申请补领', 0004, 0.24);
INSERT INTO `question` VALUES (1733399763337977858, 117, '3ff6b', 0002, '机动车购买后尚未注册登记，需要临时上道路行驶的，可以凭什么临时上道路行驶？ ', 'A、合法来源凭证 B、临时行驶车号牌 C、借用的机动车号牌 D、法人单位证明', '合法来源凭证 ', '临时行驶车号牌 ', '借用的机动车号牌 ', '法人单位证明', 'B', '《道路交通安全法》第八条：国家对机动车实行登记制度。机动车经公安机关交通管理部门登记后，方可上道路行驶。尚未登记的机动车，需要临时上道路行驶的，应当取得临时通行牌证。', '', '尚未注册登记|临时', 0002, 0.04);
INSERT INTO `question` VALUES (1733399763384115202, 118, '09bed', 0001, '申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第九十三条，申请人在<b>考试过程中</b>有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效，公安机关交通管理部门处二千元以下罚款；申请人在<b>一年内</b>不得再次申领机动车驾驶证。', '', '取消|无效', 0001, 0.02);
INSERT INTO `question` VALUES (1733399763434446849, 119, 'b7c9c', 0001, '年龄在70周岁以上，在一个记分周期结束后一年内未提交身体条件证明的，其机动车驾驶证将会被车辆管理所注销。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十九条，机动车驾驶人有下列情形之一的，车辆管理所<b>应当注销</b>其机动车驾驶证：（九）<b>年龄在70周岁以上</b>，在一个记分周期结束后<b>一年内未提交身体条件证明的</b>；或者持有残疾人专用小型自动挡载客汽车准驾车型，在三个记分周期结束后一年内未提交身体条件证明的；', '', '未提交|注销', 0002, 0.04);
INSERT INTO `question` VALUES (1733399763480584193, 120, 'c6586', 0001, '持有大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶证的驾驶人从业单位等信息发生变化的，应当在信息变更后三十日内，向从业单位所在地车辆管理所备案。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第八十一条，持有大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶证的<b>驾驶人从业单位等信息发生变化</b>的，应在信息<b>变更后三十日</b>内，向从业单位所在地车辆管理所备案。', '', '从业单位|三十日内', 0001, 0.03);
INSERT INTO `question` VALUES (1733399763547693058, 121, '14c46', 0001, '持有小型汽车驾驶证的驾驶人，发生交通事故造成人员死亡承担同等以上责任未被吊销机动车驾驶证的，应当在本记分周期结束后三十日内到公安机关交通管理部门接受审验，同时应当申报身体条件情况。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十二条，小型汽车驾驶人发生交通事故造成人员死亡承担同等以上责任<b>未被吊销机动车驾驶证的</b>，应当<b>在本记分周期结束后三十日内</b>到公安机关交通管理部门接受审验。因此题干表述正确。', '', '未被吊销|应当', 0001, 0.03);
INSERT INTO `question` VALUES (1733399763598024705, 122, '072f4', 0001, '发动机号码、车辆识别代号因磨损、锈蚀、事故等原因辨认不清或者损坏的，可以向登记地车辆管理所申请备案。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》第二十三条，已注册登记的机动车有下列情形之一的，机动车所有人应当在信息或者事项变更后三十日内，<b>向登记地车辆管理所申请变更备案</b>：<br>（一）机动车所有人住所在车辆管理所管辖区域内迁移、机动车所有人姓名（单位名称）变更的；<br>（二）机动车所有人身份证明名称或者号码变更的；<br>（三）机动车所有人联系方式变更的；<br><b>（四）车辆识别代号因磨损、锈蚀、事故等原因辨认不清或者损坏的</b>；', '', '登记地', 0001, 0.03);
INSERT INTO `question` VALUES (1733399763639967745, 123, '1421f', 0001, '机动车登记证书丢失后应及时补办，避免被不法分子利用。', NULL, '对', '错', '空', '空', '对', '机动车登记证书丢失后，一旦被他人非法利用很容易造成重大损失，一定要在丢失后及时补办。', '', '及时补办', 0001, 0.02);
INSERT INTO `question` VALUES (1733399763690299393, 124, 'c8ecd', 0002, '机动车驾驶证补领后，以下说法正确的是什么？ ', 'A、原驾驶证继续使用 B、原驾驶证作废，不得继续使用 C、原驾驶证特殊情况下使用 D、替换使用', '原驾驶证继续使用 ', '原驾驶证作废，不得继续使用 ', '原驾驶证特殊情况下使用 ', '替换使用', 'B', '《机动车驾驶证申领和使用规定》第六十九条 机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。', '', '补领', 0001, 0.03);
INSERT INTO `question` VALUES (1733399763753213954, 125, 'b31b8', 0001, '机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第六十九条 机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，<b>不得继续使用。</b>', '', '不得继续', 0001, 0.02);
INSERT INTO `question` VALUES (1733399763828711425, 126, 'e4c6c', 0002, '超过机动车驾驶证有效期一年以上未换证被注销，但未超过2年的，机动车驾驶人应当如何恢复驾驶资格？ ', 'A、参加道路交通安全法律、法规和相关知识考试合格后 B、参加场地考试合格后 C、参加道路驾驶技能考试合格后 D、参加安全文明驾驶常识考试合格后', '参加道路交通安全法律', '法规和相关知识考试合格后 B', '参加场地考试合格后 C', '参加道路驾驶技能考试合格后 D', 'A', '《机动车驾驶证申领和使用规定第七十九条 机动车驾驶人有下列情形之一的，车辆管理所应当注销其机动车驾驶证：<b>（八）超过机动车驾驶证有效期一年以上未换证的；</b><br>有第一款第八项情形<b>被注销机动车驾驶证未超过二年的</b>，机动车驾驶人参加<b>道路交通安全法律、法规和相关知识考试合格后</b>，可以恢复驾驶资格。申请人可以向机动车驾驶证核发地或者核发地以外的车辆管理所申请。', '', '注销|未超过2年', 0003, 0.11);
INSERT INTO `question` VALUES (1733399763895820289, 127, '35c7b', 0001, '已注册登记的机动车达到国家规定的强制报废标准的，应当向登记地车辆管理所申请注销登记。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》第三十七条，机动车有下列情形之一的，机动车所有人应当向<b>登记地车辆管理所</b>申请注销登记：<br><b>（一）机动车已达到国家强制报废标准的；</b><br>（二）机动车未达到国家强制报废标准，机动车所有人自愿报废的；<br>（三）因自然灾害、失火、交通事故等造成机动车灭失的；<br>（四）机动车因故不在我国境内使用的；<br>（五）因质量问题退车的。', '', '强制报废|登记地', 0001, 0.03);
INSERT INTO `question` VALUES (1733399763967123457, 128, 'eca2b', 0002, '存在以下哪种行为的申请人在一年内不得再次申领机动车驾驶证？ ', 'A、在考试过程中出现身体不适 B、在考试过程中有舞弊行为 C、不能按照教学大纲认真练习驾驶技能 D、未参加理论培训', '在考试过程中出现身体不适 ', '在考试过程中有舞弊行为 ', '不能按照教学大纲认真练习驾驶技能 ', '未参加理论培训', 'B', '《机动车驾驶证申领和使用规定》第九十三条，申请人在<b>考试过程中有贿赂、舞弊行为</b>的，取消考试资格，已经通过考试的其他科目成绩无效，公安机关交通管理部门处二千元以下罚款；申请人在<b>一年内</b>不得再次申领机动车驾驶证。', '', '不得再次', 0002, 0.07);
INSERT INTO `question` VALUES (1733399764063592450, 129, 'd70cb', 0001, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，记分周期内有记分的，应当在记分周期结束后三十日内到公安机关交通管理部门接受审验，同时还应当申报身体条件情况。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十二条 持有大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶证的驾驶人，应当在每个记分周期结束后<b>三十日内</b>到公安机关交通管理部门接受审验。但在一个记分周期内没有记分记录的，免予本记分周期审验。<br>第七十三条 机动车驾驶证审验内容包括：<br>（一）道路交通安全违法行为、交通事故处理情况；<br><b>（二）身体条件情况；</b><br>（三）道路交通安全违法行为记分及记满12分后参加学习和考试情况。', '', '三十日内', 0001, 0.03);
INSERT INTO `question` VALUES (1733399764139089922, 130, '94c41', 0002, '申请人有下列哪种行为，三年内不得再次申领机动车驾驶证？ ', 'A、实习期记满12分，注销驾驶证的 B、申请人在考试过程中有舞弊行为的 C、申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的 D、申请人未能在培训过程中认真练习的', '实习期记满12分，注销驾驶证的 ', '申请人在考试过程中有舞弊行为的 ', '申请人以欺骗', '贿赂等不正当手段取得机动车驾驶证的 D', 'C', '《机动车驾驶证申领和使用规定》第九十三条，<b>申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的</b>，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，处二千元以下罚款；申请人<b>在三年内不得再次申领</b>机动车驾驶证。【骗3】', '', '三年内不得', 0003, 0.11);
INSERT INTO `question` VALUES (1733399764193615874, 131, 'a9d8e', 0001, '申请人患有精神病的，可以申领机动车驾驶证，但是在发病期间不得驾驶机动车。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第十五条，有下列情形之一的，<b>不得申请</b>机动车驾驶证:<br>（一）有器质性心脏病、癫痫病、美尼尔氏症、眩晕症、癔病、震颤麻痹、<b>精神病</b>、痴呆以及影响肢体活动的神经系统疾病<b>等妨碍安全驾驶疾病的</b>；', '', '精神病|可以申领', 0003, 0.11);
INSERT INTO `question` VALUES (1733399764235558913, 132, '98e15', 0001, '在居住地初次申领机动车驾驶证的，不能直接申领大型货车驾驶证。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第十六条，<b>初次申领</b>机动车驾驶证的，<b>可以申请</b>准驾车型为城市公交车、<b>大型货车</b>、小型汽车、小型自动挡汽车、低速载货汽车、三轮汽车、残疾人专用小型自动挡载客汽车、普通三轮摩托车、普通二轮摩托车、轻便摩托车、轮式专用机械车、无轨电车、有轨电车的机动车驾驶证。<b>因此，题干表述错误。</b><br>第二十二条 申领机动车驾驶证的人，按照下列规定向车辆管理所提出申请：（二）在户籍所在地以外居住的，可以在居住地提出申请；【车登记，证随意】', '', '初次申领|不能', 0005, 0.42);
INSERT INTO `question` VALUES (1733399764285890561, 133, '66b2b', 0001, '机动车驾驶证有效期超过一年以上未换证的，驾驶证将被注销。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十九条 机动车驾驶人有下列情形之一的，车辆管理所应当注销其机动车驾驶证：<br>（八）超过机动车驾驶证有效期一年以上未换证的。', '', '超过一年以上', 0003, 0.09);
INSERT INTO `question` VALUES (1733399764332027905, 134, '6a3e5', 0002, '驾驶证审验内容不包括以下哪一项？ ', 'A、道路交通安全违法行为、交通事故处理情况 B、身体条件情况 C、道路交通安全违法行为记分及记满12分后参加学习和考试情况 D、机动车检验情况', '道路交通安全违法行为', '交通事故处理情况 B', '身体条件情况 C', '道路交通安全违法行为记分及记满12分后参加学习和考试情况 D', 'D', '《机动车驾驶证的申领和使用规定》第七十三条，机动车驾驶证<b>审验内容包括</b>：<br>（一）道路交通安全违法行为、交通事故处理情况；<br>（二）身体条件情况；<br>（三）道路交通安全违法行为记分及记满12分后参加学习和考试情况。<br>因此，驾驶证的审验内容<b>不包括</b>机动车检验情况。', '', '不包括', 0004, 0.20);
INSERT INTO `question` VALUES (1733399764378165250, 135, 'fecee', 0002, '机动车驾驶证遗失的，机动车驾驶人应当向哪里的车辆管理所申请补发？ ', 'A、核发地 B、户籍地 C、居住地 D、以上均可', '核发地 ', '户籍地 ', '居住地 ', '以上均可', 'D', '《机动车驾驶证申领和使用规定》第六十九条 机动车驾驶证遗失的，机动车驾驶人应当向机动车驾驶证<b>核发地或者核发地以外</b>的车辆管理所申请补发。申请时应当确认申请信息，并提交机动车驾驶人的身份证明。符合规定的，车辆管理所应当在一日内补发机动车驾驶证。', '', '遗失|申请补发', 0004, 0.22);
INSERT INTO `question` VALUES (1733399764424302593, 136, 'c2132', 0002, '机动车驾驶证被依法扣押、扣留、暂扣期间能否申请补发？ ', 'A、可以申请 B、扣留期间可以临时申请 C、暂扣期间可以临时申请 D、不得申请补发', '可以申请 ', '扣留期间可以临时申请 ', '暂扣期间可以临时申请 ', '不得申请补发', 'D', '《机动车驾驶证申领和使用规定》第六十九条 机动车驾驶证<b>被依法扣押、扣留或者暂扣期间</b>，机动车驾驶人<b>不得申请</b>补发。', '', '补发', 0002, 0.05);
INSERT INTO `question` VALUES (1733399764466245633, 137, '1023c', 0001, '已注册登记的机动车，改变机动车车身颜色的，机动车所有人应到登记地车辆管理所申请变更登记。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》第十六条，已注册登记的机动车有下列情形之一的，机动车所有人应当<b>向登记地车辆管理所申请变更</b>登记：<br><b>（一）改变车身颜色的；</b><br>（二）更换发动机的；<br>（三）更换车身或者车架的；<br>（四）因质量问题更换整车的；<br>（五）机动车登记的使用性质改变的；<br>（六）机动车所有人的住所迁出、迁入车辆管理所管辖区域的。<br>属于第一款第一项至第三项规定的变更事项的，机动车所有人应当在变更后十日内向车辆管理所申请变更登记。', '', '车身颜色|登记地', 0001, 0.03);
INSERT INTO `question` VALUES (1733399764508188674, 138, 'a950e', 0002, '实习期驾驶人驾驶机动车上高速公路行驶，以下做法正确的是什么？ ', 'A、任何情况下都不允许上高速 B、不需要其他人员陪同 C、需要持有相应或者包含其准驾车型驾驶证三年以上的驾驶人陪同 D、需要持有相应或者包含其准驾车型驾驶证、同在实习期内的驾驶人陪同', '任何情况下都不允许上高速 ', '不需要其他人员陪同 ', '需要持有相应或者包含其准驾车型驾驶证三年以上的驾驶人陪同 ', '需要持有相应或者包含其准驾车型驾驶证', 'C', '《机动车驾驶证申领和使用规定》第七十七条，驾驶人在实习期内驾驶机动车上<b>高速公路</b>行驶，应当由持相应或者包含其准驾车型驾驶证<b>三年以上</b>的驾驶人陪同。其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', '', '实习期|高速公路', 0002, 0.04);
INSERT INTO `question` VALUES (1733399764558520322, 139, 'b59fa', 0002, '年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查的目的是什么？ ', 'A、体现对老年人的关心 B、例行程序仅供参考 C、检查是否患有老年常见病 D、检查是否患有妨碍安全驾驶的疾病', '体现对老年人的关心 ', '例行程序仅供参考 ', '检查是否患有老年常见病 ', '检查是否患有妨碍安全驾驶的疾病', 'D', '年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查，<b>检查是否患有妨碍安全驾驶的疾病</b>，以防因身体条件变化而导致安全事故发生。', '', '70周岁|身体检查', 0001, 0.02);
INSERT INTO `question` VALUES (1733399764604657665, 140, '36c43', 0001, '年龄在50周岁以上的机动车驾驶人，应当每年进行一次身体检查，并向公安机关交通管理部门申报身体条件情况。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第七十四条，<b>年龄在70周岁以上</b>的机动车驾驶人，应当<b>每年进行一次身体检查</b>，在记分周期结束后三十日内，提交医疗机构出具的有关身体条件的证明。', '', '50周岁|应当', 0004, 0.16);
INSERT INTO `question` VALUES (1733399764646600706, 141, 'ddd7e', 0001, '机动车驾驶人联系电话、联系地址等信息发生变化，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', NULL, '对', '错', '空', '空', '对', '在车辆管理所管辖区域内，<b>机动车驾驶证记载的机动车驾驶人信息发生变化的</b>；机动车驾驶证损毁无法辨认的，机动车驾驶人应当在30日内到<b>机动车驾驶证核发地</b>或者核发地以外的车辆管理所备案申请换证。', '', '三十日内|核发地', 0002, 0.04);
INSERT INTO `question` VALUES (1733399764688543745, 142, '6a944', 0001, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人联系电话、从业单位等信息发生变化未及时申报变更信息的，公安机关交通管理部门处二十元以上二百元以下罚款。', NULL, '对', '错', '空', '空', '对', '如实申报信息是驾驶人的法定义务。因此，为保障驾驶人相关信息的准确真实，规定驾驶人的个人信息或者从业单位信息发生变化的，应当在变更后的30天内向驾驶证核发地的车管所备案，否则将被处以20元以上200元以下的罚款处罚。<br>《机动车驾驶证申领和使用规定》第九十八条，机动车驾驶人有下列行为之一的，由公安机关交通管理部门<b>处二十元以上二百元以下罚款</b>：<br>（一）机动车驾驶人补换领机动车驾驶证后，继续使用原机动车驾驶证的；<br>（二）在实习期内驾驶机动车不符合第七十七条规定的；<br><b>（三）持有大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶证的驾驶人，未按照第八十一条规定申报变更信息的。</b>', '', '未及时申报|二十元以上二百元以下', 0002, 0.04);
INSERT INTO `question` VALUES (1733399764738875394, 143, 'd6dac', 0001, '已注册登记的机动车，机动车所有人住所在车辆管理所管辖区域内迁移或者机动车所有人姓名（单位名称）、联系方式变更的，应当向登记地车辆管理所备案。', NULL, '对', '错', '空', '空', '对', '【信息有变要备案】《机动车登记规定》第二十三条，已注册登记的机动车有下列情形之一的，机动车所有人应当在信息或者事项变更后三十日内，<b>向登记地车辆管理所</b>申请变更备案：<br>（一）机动车所有人住所在车辆管理所管辖区域内迁移、机动车所有人姓名（单位名称）变更的；', '', '迁移|变更|登记地', 0001, 0.03);
INSERT INTO `question` VALUES (1733399764789207042, 144, '66649', 0002, '下列哪种情况可以向机动车驾驶证核发地车辆管理所申请补发? ', 'A、驾驶证被扣押 B、驾驶证被扣留 C、驾驶证遗失 D、驾驶证被暂扣', '驾驶证被扣押 ', '驾驶证被扣留 ', '驾驶证遗失 ', '驾驶证被暂扣', 'C', '《机动车驾驶证申领和使用规定》第六十九条 机动车驾驶证遗失的，机动车驾驶人应当向机动车驾驶证核发地或者核发地以外的车辆管理所申请补发。申请时应当确认申请信息，并提交机动车驾驶人的身份证明。符合规定的，车辆管理所应当在一日内补发机动车驾驶证。<br>机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。<br>机动车驾驶证<b>被依法扣押、扣留或者暂</b>扣期间，机动车驾驶人<b>不得申请补发</b>。', '', '核发地|申请补发|可以向', 0001, 0.03);
INSERT INTO `question` VALUES (1733399764839538690, 145, '93fad', 0002, '机动车驾驶人补领机动车驾驶证后，使用原机动车驾驶证驾驶的，除由公安机关交通管理部门收回原机动车驾驶证外，还应当受到何种处罚？ ', 'A、吊销驾驶证 B、拘留驾驶人 C、警告 D、罚款', '吊销驾驶证 ', '拘留驾驶人 ', '警告 ', '罚款', 'D', '《机动车驾驶证申领和使用规定》第九十八条 机动车驾驶人有下列行为之一的，由公安机关交通管理部门<b>处二十元以上二百元以下罚款</b>：<br><b>（一）机动车驾驶人补换领机动车驾驶证后，继续使用原机动车驾驶证的；</b><br>（二）在实习期内驾驶机动车不符合第七十七条规定的；<br>（三）持有大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶证的驾驶人，未按照第八十一条规定申报变更信息的。<br>有第一款第一项规定情形的，由公安机关交通管理部门收回原机动车驾驶证。', '', '补领|收回', 0004, 0.20);
INSERT INTO `question` VALUES (1733399764898258946, 146, '8445a', 0001, '机动车在抵押登记、质押备案期间不可以办理注销登记。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》第四十二条 ，机动车在抵押登记、质押备案期间的，<b>不予办理</b>注销登记。', '', '不可以', 0004, 0.23);
INSERT INTO `question` VALUES (1733399764961173505, 147, '52f86', 0001, '机动车所有人申请转移登记前，应当将涉及该车的道路交通安全违法行为和交通事故处理完毕。', NULL, '对', '错', '空', '空', '对', '已注册登记的机动车所有权发生转移的，现机动车所有人应当自机动车交付之日起三十日内向登记地车辆管理所申请转移登记。机动车所有人申请转移登记前，应当将涉及该车的道路交通安全违法行为和交通事故处理完毕。', '', '转移登记|处理完毕', 0001, 0.01);
INSERT INTO `question` VALUES (1733399765007310849, 148, 'f8c13', 0001, '经购买、调拨、赠予等方式获得机动车后尚未注册登记的，向车辆管理所申领临时行驶车号牌后，方可临时上道路行驶。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》第四十六条，机动车具有下列情形之一，<b>需要临时上道路行驶</b>的，机动车所有人应当向车辆管理所<b>申领临时行驶车号牌</b>：<br>（一）未销售的；<br><b>（二）购买、调拨、赠予等方式获得机动车后尚未注册登记的；</b><br>（三）新车出口销售的；<br>（四）进行科研、定型试验的；<br>（五）因轴荷、总质量、外廓尺寸超出国家标准不予办理注册登记的特型机动车。', '', '方可', 0002, 0.08);
INSERT INTO `question` VALUES (1733399765045059585, 149, '66b87', 0002, '以下不属于机动车驾驶证审验内容的是什么？ ', 'A、道路交通安全违法行为、交通事故处理情况 B、驾驶人身体条件 C、记满12分后参加学习和考试情况 D、驾驶车辆累计行驶里程', '道路交通安全违法行为', '交通事故处理情况 B', '驾驶人身体条件 C', '记满12分后参加学习和考试情况 D', 'D', '请注意，题干要求选出<b>不属于审验内容</b>的选项。《机动车驾驶证申领和使用规定》第七十三条 机动车驾驶证<b>审验内容包括</b>：<br>（一）道路交通安全违法行为、交通事故处理情况；<br>（二）身体条件情况；<br>（三）道路交通安全违法行为记分及记满12分后参加学习和考试情况。', '', '不属于|审验', 0003, 0.14);
INSERT INTO `question` VALUES (1733399765091196929, 150, '38b2d', 0001, '已注册登记的机动车，改变车身颜色，机动车所有人不需要向登记地车辆管理所申请变更登记。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第六条，已注册登记的机动车有下列情形之一的，<b>机动车所有人应当向登记该机动车的公安机关交通管理部门申请变更登记</b>：<br>(一)<b>改变机动车车身颜色的</b>；<br>(二)更换发动机的；<br>(三)更换车身或者车架的；<br>(四)因质量有问题，制造厂更换整车的；<br>(五)营运机动车改为非营运机动车或者非营运机动车改为营运机动车的；<br>(六)机动车所有人的住所迁出或者迁入公安机关交通管理部门管辖区域的。', '', '不需要', 0004, 0.17);
INSERT INTO `question` VALUES (1733399765133139970, 151, '847fa', 0002, '以下哪种身体条件，不可以申请机动车驾驶证? ', 'A、糖尿病 B、红绿色盲 C、高血压 D、怀孕', '糖尿病 ', '红绿色盲 ', '高血压 ', '怀孕', 'B', '《机动车驾驶证申领和使用规定》第十四条 申请机动车驾驶证的人，应当符合下列规定：（二）身体条件：<b>3. 辨色力：无红绿色盲</b>；', '', '不可以', 0001, 0.02);
INSERT INTO `question` VALUES (1733399765200248833, 152, '663fa', 0001, '申请人患有癫痫病的，可以申领机动车驾驶证，但是驾驶时必须有人陪同。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第十五条 有下列情形之一的，<b>不得申请机动车驾驶证</b>:<br>（一）有器质性心脏病、<b>癫痫病</b>、美尼尔氏症、眩晕症、癔病、震颤麻痹、精神病、痴呆以及影响肢体活动的神经系统疾病等妨碍安全驾驶疾病的。', '', '癫痫病|可以申领', 0003, 0.09);
INSERT INTO `question` VALUES (1733399765258969090, 153, '9cd64', 0001, '自愿降级的驾驶人需要到车辆管理所申请换领驾驶证。', NULL, '对', '错', '空', '空', '对', '自愿降级的驾驶人，由本人带驾驶证、身份证去车管所提出申请，并换领驾驶证。', '', '自愿降级|换领', 0002, 0.04);
INSERT INTO `question` VALUES (1733399765313495041, 154, 'ebd6b', 0001, '机动车驾驶人驾驶证有效期满换领驾驶证时，只能提交县级以上医疗机构出具的身体条件证明。', NULL, '对', '错', '空', '空', '错', '根据公安部交通管理局发布的新措施，对申请机动车驾驶证、办理机动车驾驶证业务需要提交身体条件证明的，体检医疗机构由县级或者部队团级以上医疗机构<b>扩大到</b>符合健康体检资质的二级以上医院、乡镇卫生院、社区卫生服务中心等医疗机构。因此，<b>必须提交</b>县级以上医疗机构出具的身体条件证明，<b>说法错误</b>。', '', '只能', 0004, 0.23);
INSERT INTO `question` VALUES (1733399765359632385, 155, '1ca8e', 0001, '年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十四条 年龄在70周岁以上的机动车驾驶人，应当<b>每年进行一次身体检查</b>，在记分周期结束后三十日内，提交医疗机构出具的有关身体条件的证明。', '', '70周岁|每年|一次', 0001, 0.02);
INSERT INTO `question` VALUES (1733399765405769730, 156, 'df491', 0001, '驾驶人吸食或注射毒品后驾驶机动车的，一经查获，其驾驶证将被注销。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证的申领和使用》第七十九条 机动车驾驶人有下列情形之一的，车辆管理所应当<b>注销</b>其机动车驾驶证：<br>（六）被查获有吸食、注射毒品后驾驶机动车行为，依法被责令社区戒毒、社区康复或者决定强制隔离戒毒，或者长期服用依赖性精神药品成瘾尚未戒除的。', '', '毒品|注销', 0001, 0.02);
INSERT INTO `question` VALUES (1733399765447712770, 157, '1e247', 0001, '驾驶人在实习期内驾驶机动车上高速公路行驶，应由持相应或者包含其准驾车型驾驶证一年以上的驾驶人陪同。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第七十七条，驾驶人在实习期内驾驶机动车上<b>高速公路</b>行驶，应当由持相应或者包含其准驾车型驾驶证<b>三年以上</b>的驾驶人陪同。其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', '', '实习期内|一年以上', 0004, 0.16);
INSERT INTO `question` VALUES (1733399765493850114, 158, 'd62dc', 0001, '机动车驾驶人在一个记分周期内累计记分达到12分，拒不参加学习和考试的，将被公安机关交通部门公告其驾驶证停止使用。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第二十九条，机动车驾驶人在一个记分周期内累积记分满12分，机动车驾驶证未被依法扣留或者收到满分教育通知书后三十日内<b>拒不参加</b>公安机关交通管理部门通知的满分学习、考试的，由公安机关交通管理部门公告<b>其机动车驾驶证停止使用</b>。', '', '拒不参加|停止使用', 0001, 0.02);
INSERT INTO `question` VALUES (1733399765535793153, 159, 'cd273', 0002, '驾驶达到报废标准的机动车上道路行驶的，公安交通管理部门将会予以收缴，以下说法错误的是什么？ ', 'A、驾驶报废车影响驾驶人行车安全 B、报废车机械老化、容易发生交通事故 C、车辆不符合安全技术标准，需要强制报废 D、不美观，影响城市形象', '驾驶报废车影响驾驶人行车安全 ', '报废车机械老化', '容易发生交通事故 C', '车辆不符合安全技术标准，需要强制报废 D', 'D', '请注意，题干要求<font color=\"#FF0000\">选出说法错误的</font>选项。由于报废车机械老化，极其不符合安全技术标准，因此很容易发生交通事故并影响驾驶人行车安全，为了保障道路交通安全，不符合安全技术标准的机动车应当强制报废，跟美不美观，没有关系。因此<b>不美观，影响城市形象的说法是错误的</b>。', '', '说法错误', 0002, 0.07);
INSERT INTO `question` VALUES (1733399765581930497, 160, 'd5a39', 0001, '驾驶人在驾驶证核发地车辆管理所管辖区以外地方居住的，可以向政务大厅申请换证。', NULL, '对', '错', '空', '空', '错', '《机动车驾驶证申领和使用规定》第六十四条，机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。机动车驾驶人<b>在核发地车辆管理所管辖区以外居住</b>的，可以向<b>居住地车辆管理所</b>申请换证。申请时应当确认申请信息，提交机动车驾驶人的身份证明和机动车驾驶证，并申报身体条件情况。', '', '政务大厅', 0004, 0.26);
INSERT INTO `question` VALUES (1733399765644845057, 161, 'a1d98', 0001, '允许自学直考人员使用图中教练车，在学车专用标识签注的指导人员随车指导下学习驾驶。', NULL, '对', '错', '空', '空', '错', '自学直考人员，应使用粘贴学车专用标识的自学用车，<b>不得使用教练车</b>。<br>申请人为自学直考人员的，在道路上学习驾驶时，应当在自学用车上<b>按规定放置、粘贴学车专用标识</b>，自学用车不得搭载随车指导人员以外的其他人员。', 'https://timg.mnks.cn/20230926ccc83736.jpg', '自学直考|专用', 0005, 0.32);
INSERT INTO `question` VALUES (1733399765716148225, 162, 'dde16', 0001, '如图所示，黄色机动车驾驶人违法占用高速公路应急车道行驶，会被记3分。', NULL, '对', '错', '空', '空', '错', '如图所示，<b>黄色机动车驾驶人违法占用高速公路应急车道行驶</b>，根据《道路交通安全违法行为记分管理办法》第十条规定，《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（十一）驾驶机动车在高速公路或者城市快速路上<b>违法占用应急车道行驶的。</b>', 'https://timg.mnks.cn/20230926e9b45075.jpg', '占用|应急车道|3分', 0004, 0.22);
INSERT INTO `question` VALUES (1733399765762285569, 163, 'f478c', 0001, '如图所示，黄色小型客车驾驶人在高速公路逆向行驶是违法行为，会被记6分。', NULL, '对', '错', '空', '空', '错', '【高速记分】慢3占6停9<b>倒逆12</b>，黄车在高速公路上逆行，将被记12分，《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：<br>（六）驾驶机动车在<b>高速公路</b>、城市快速路上倒车、<b>逆行</b>、穿越中央分隔带掉头的；', 'https://timg.mnks.cn/20230926aebc25e0.jpg', '逆向行驶|6分', 0004, 0.16);
INSERT INTO `question` VALUES (1733399765812617217, 164, 'e8738', 0002, '如图所示，驾驶人的这种违法行为会被记多少分？ ', 'A、记12分 B、记3分 C、记6分 D、记9分', '记12分 ', '记3分 ', '记6分 ', '记9分', 'D', '图中驾驶人的违法行为是<b>遮挡机动车号牌</b>，会被<b>记9分</b>。<br>《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，<b>一次<b>记9分</b></b>：<br>（四）驾驶未悬挂机动车号牌或者<b>故意遮挡、污损机动车号牌</b>的机动车上道路行驶的。', 'https://timg.mnks.cn/20230926f0564dce.jpg', '违法行为', 0005, 0.28);
INSERT INTO `question` VALUES (1733399765858754562, 165, 'd0c10', 0002, '如图所示，驾驶人的这种违法行为会被记多少分？ ', 'A、3分 B、6分 C、12分 D、24分', '3分 ', '6分 ', '12分 ', '24分', 'C', '《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，一次<b>记12分</b>：<br>（一）<b>饮酒后驾驶</b>机动车的。', 'https://timg.mnks.cn/20230926b1029dcc.jpg', '违法行为', 0002, 0.06);
INSERT INTO `question` VALUES (1733399765904891906, 166, 'aea3c', 0002, '驾驶机动车不按规定会车的，将被一次记多少分？ ', 'A、1分 B、2分 C、3分 D、4分', '1分 ', '2分 ', '3分 ', '4分', 'A', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<br>（二）驾驶机动车<b>不按规定会车</b>，或者在高速公路、城市快速路以外的道路上不按规定倒车、掉头的；', '', '不按规定', 0005, 0.31);
INSERT INTO `question` VALUES (1733399765955223554, 167, 'a077e', 0001, '实习期内驾驶机动车牵引挂车的，由公安机关交通管理部门处20元以上200元以下罚款。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第九十八条，机动车驾驶人有下列行为之一的，由公安机关交通管理部门处<b>二十元以上二百元以下</b>罚款：<br>（二）<b>在实习期内</b>驾驶机动车<b>不符合第七十七条</b>规定的；<br><b>第七十七条</b>，机动车驾驶人<b>在实习期内</b>不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；<b>驾驶的机动车不得牵引挂车</b>。<br>因此，题干表述正确。', '', '实习期内|20元以上200元以下', 0003, 0.09);
INSERT INTO `question` VALUES (1733399766013943809, 168, 'c4f90', 0002, '驾驶人在实习期内单独驾驶机动车上高速公路行驶，会受到什么处罚？ ', 'A、二十元以上二百元以下罚款 B、记3分 C、记6分 D、吊销驾驶证', '二十元以上二百元以下罚款 ', '记3分 ', '记6分 ', '吊销驾驶证', 'A', '将处以<b>罚款20元以上200元以下</b>。根据《机动车驾驶证申领和使用规定》第九十八条，机动车驾驶人有下列行为之一的，由公安机关交通管理部门<b>处二十元以上二百元以下</b>罚款：<br>（二）在实习期内驾驶机动车<b>不符合<b>第七十七条</b></b>规定的；<br><b>第七十七条</b>，驾驶人在实习期内驾驶机动车上高速公路行驶，应当由持相应或者更高准驾车型驾驶证三年以上的驾驶人陪同。陪同的驾驶人应乘坐在副驾驶位置，对实习驾驶人进行指导。', '', '实习期|高速公路', 0003, 0.15);
INSERT INTO `question` VALUES (1733399766055886849, 169, 'f0f03', 0002, '驾驶机动车有拨打、接听手持电话等妨碍安全驾驶的行为，一次记多少分？ ', 'A、3分 B、9分 C、6分 D、1分', '3分 ', '9分 ', '6分 ', '1分', 'A', '开车接打电话，记3分。《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：<br>（六）驾驶机动车<b>有拨打、接听手持电话</b>等妨碍安全驾驶的行为的。', '', '电话', 0004, 0.26);
INSERT INTO `question` VALUES (1733399766089441281, 170, 'a8dda', 0002, '驾驶机动车在高速公路行驶，超过规定时速20%以上未达50%的，一次记多少分？ ', 'A、3分 B、6分 C、9分 D、12分', '3分 ', '6分 ', '9分 ', '12分', 'B', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（三）驾驶校车、中型以上载客载货汽车、危险物品运输车辆<b>以外的机动车</b>在高速公路、城市快速路上行驶<b>超过规定时速百分之二十以上未达到百分之五十</b>，或者在高速公路、城市快速路以外的道路上行驶超过规定时速百分之五十以上的；', '', '20%以上未达50%|高速公路', 0005, 0.35);
INSERT INTO `question` VALUES (1733399766148161537, 171, 'd7280', 0002, '小型自动挡汽车驾驶人在一个记分周期内二次累积记分满12分，应当在科目一考试合格后，在取得学习驾驶证明多长时间后预约参加科目三考试？ ', 'A、7日 B、10日 C、20日 D、30日', '7日 ', '10日 ', '20日 ', '30日', 'C', '《道路交通安全违法行为记分管理办法》第二十二条，机动车驾驶人<b>在一个记分周期内二次累积记分满12分</b>或者累积记分满24分未满36分的，应当<b>在道路交通安全法律、法规和相关知识考试合格后</b>，按照<b><b>《机动车驾驶证申领和使用规定》第四十四条</b></b>的规定<b>预约参加道路驾驶技能考试</b>。考试不合格的，十日后预约重新考试。<br><b>《机动车驾驶证申领和使用规定》第四十四条</b>，初次申请机动车驾驶证或者申请增加准驾车型的，<b>申请人预约考试科目三，应当符合下列规定</b>：<br>（一）报考<b>小型自动挡汽车</b>、残疾人专用小型自动挡载客汽车、低速载货汽车、三轮汽车准驾车型的，在取得学习驾驶证明满<b>二十日后</b>预约考试；', '', '小型自动挡汽车|二次累积记分|多长时间后', 0005, 0.39);
INSERT INTO `question` VALUES (1733399766370459649, 172, 'bb0de', 0002, '驾驶机动车遇前方机动车停车排队或者缓慢行驶时，借道超车或者占用对面车道、穿插等候车辆的，将被一次记多少分 ？ ', 'A、3分 B、6分 C、9分 D、12分', '3分 ', '6分 ', '9分 ', '12分', 'A', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：驾驶机动车遇前方机动车停车排队或者缓慢行驶时，<b>借道超车</b>或者<b>占用对面车道</b>、<b>穿插等候车辆</b>的。', '', '借道|占用', 0005, 0.31);
INSERT INTO `question` VALUES (1733399766420791297, 173, 'c6028', 0002, '机动车驾驶人逾期不参加审验仍驾驶机动车的，会受到什么处罚？ ', 'A、20元以上200元以下 B、200元以上500元以下 C、1000元以上2000元以下 D、吊销驾驶证', '20元以上200元以下 ', '200元以上500元以下 ', '1000元以上2000元以下 ', '吊销驾驶证', 'B', '《机动车驾驶证申领和使用规定》第九十九条，机动车驾驶人有下列行为之一的，由公安机关交通管理部门处<b>二百元以上五百元以下</b>罚款：<b>逾期不参加审验仍驾驶机动车的</b>。', '', '逾期不参加审验', 0005, 0.46);
INSERT INTO `question` VALUES (1733399766466928642, 174, 'aaf4d', 0002, '驾驶人有下列哪种违法行为一次记3分？ ', 'A、造成致人轻微伤后逃逸，尚不构成犯罪的 B、驾驶机动车在高速公路或者城市快速路上不按规定车道行驶的 C、驾驶机动车不按交通信号灯指示通行的 D、驾驶机动车不按规定使用灯光的', '造成致人轻微伤后逃逸，尚不构成犯罪的 ', '驾驶机动车在高速公路或者城市快速路上不按规定车道行驶的 ', '驾驶机动车不按交通信号灯指示通行的 ', '驾驶机动车不按规定使用灯光的', 'B', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：<br>（三）驾驶机动车在<b>高速公路</b>或者<b>城市快速路上</b><b>不按规定车道行驶</b>的。', '', '3分', 0005, 0.41);
INSERT INTO `question` VALUES (1733399766521454593, 175, 'b3cca', 0002, '驾驶人违法使用其他机动车号牌、行驶证的，一次记几分？ ', 'A、3分 B、6分 C、9分 D、12分', '3分 ', '6分 ', '9分 ', '12分', 'D', '《道路交通安全违法行为记分管理办法》第八条 ，机动车驾驶人有下列交通违法行为之一，一次<b>记12分</b>：<br>（三）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者<b>使用其他机动车号牌、行驶证的</b>。', '', '违法使用', 0005, 0.32);
INSERT INTO `question` VALUES (1733399766571786242, 176, 'b0fa7', 0002, '驾驶机动车在高速公路或者城市快速路上不按规定车道行驶的，将被一次记多少分？ ', 'A、6分 B、3分 C、9分 D、12分', '6分 ', '3分 ', '9分 ', '12分', 'B', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，一次<b>记3分</b>：（三）驾驶机动车在高速公路或者城市快速路上<b>不按规定车道行驶</b>的；', '', '不按规定车道', 0005, 0.40);
INSERT INTO `question` VALUES (1733399766613729281, 177, 'f6a74', 0001, '饮酒后驾驶非营运机动车的，处暂扣6个月机动车驾驶证，并处1000元以上2000元以下罚款。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十一条，<b>饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。</b><br>饮酒后驾驶营运机动车的，处十五日拘留，并处五千元罚款，吊销机动车驾驶证，五年内不得重新取得机动车驾驶证。', '', '非营运|暂扣|1000元以上2000元以下', 0003, 0.10);
INSERT INTO `question` VALUES (1733399766651478018, 178, 'd5a2b', 0002, '机动车驾驶证依法被吊销或者驾驶许可依法被撤销的，车辆管理所应当对机动车驾驶人处以何种处罚？ ', 'A、注销其行驶证 B、注销其驾驶证 C、扣留机动车 D、罚款', '注销其行驶证 ', '注销其驾驶证 ', '扣留机动车 ', '罚款', 'B', '《机动车驾驶证申领和使用规定》第七十九条，机动车驾驶人有下列情形之一的，车辆管理所<b>应当注销其机动车驾驶证</b>：（十一）机动车驾驶证依法被吊销或者驾驶许可依法被撤销的。', '', '吊销|撤销', 0005, 0.45);
INSERT INTO `question` VALUES (1733399766710198274, 179, 'a9f70', 0002, '驾驶机动车在高速公路上行驶低于规定最低时速的，一次记多少分？ ', 'A、12分 B、9分 C、6分 D、3分', '12分 ', '9分 ', '6分 ', '3分', 'D', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，一次<b>记3分</b>：<br>（十五）驾驶机动车在高速公路上行驶<b>低于规定最低时速</b>的。', '', '低于规定', 0004, 0.25);
INSERT INTO `question` VALUES (1733399766747947010, 180, 'a1e83', 0002, '符合记分减免条件的，在一个记分周期内累计最高可扣减多少分？ ', 'A、3分 B、6分 C、9分 D、12分', '3分 ', '6分 ', '9分 ', '12分', 'B', '《道路交通安全违法行为记分管理办法》第二十五条，机动车驾驶人处理完交通违法行为记录后累积记分未满12分，参加公安机关交通管理部门组织的交通安全教育并达到规定要求的，可以申请在机动车驾驶人现有累积记分分值中扣减记分。<b>在一个记分周期内累计最高扣减6分</b>。', '', '累计最高', 0005, 0.42);
INSERT INTO `question` VALUES (1733399766798278657, 181, 'e4d7a', 0002, '代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的，由公安机关交通管理部门处违法所得多少倍以下罚款，但最高不超过多少元？ ', 'A、3倍，5万 B、3倍，10万 C、5倍，5万 D、5倍，10万', '3倍，5万 ', '3倍，10万 ', '5倍，5万 ', '5倍，10万', 'A', '《道路交通安全违法行为记分管理办法》第三十条，<b>代替实际机动车驾驶人</b>接受交通违法行为处罚和记分<b>牟取经济利益</b>的，由公安机关交通管理部门处违法所得<b>三倍以下罚款</b>，但最高不超过<b>五万元</b>；同时，依法撤销原行政处罚决定。', '', '代替实际|牟取经济利益|罚款', 0004, 0.17);
INSERT INTO `question` VALUES (1733399766840221698, 182, 'ce254', 0002, '关于交通违法行为，以下说法错误的是什么？ ', 'A、造成致人轻微伤或者财产损失的交通事故后逃逸，尚不构成犯罪的，一次记9分 B、驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的，一次记6分 C、驾驶机动车在高速公路或者城市快速路上违法停车的，一次记9分 D、机动车驾驶证被暂扣或者扣留期间驾驶机动车的，一次记6分', '造成致人轻微伤或者财产损失的交通事故后逃逸，尚不构成犯罪的，一次记9分 ', '驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的，一次记6分 ', '驾驶机动车在高速公路或者城市快速路上违法停车的，一次记9分 ', '机动车驾驶证被暂扣或者扣留期间驾驶机动车的，一次记6分', 'A', '<b>请注意，题干要求选出说法错误的选项</b>，造成致人轻微伤或者财产损失的交通事故后逃逸，尚不构成犯罪的，一次记6分，记9分的说法错误。《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，一次<b>记6分</b>：<br>（九）机动车驾驶证<b>被暂扣或者扣留期间</b>驾驶机动车的；【暂扣6】<br>（十）造成致人<b>轻微伤或者财产损失</b>的交通事故后逃逸，尚不构成犯罪的；【轻伤6】<br>（十一）驾驶机动车在高速公路或者城市快速路上<b>违法占用应急车道</b>行驶的。【占6】<br>第九条，机动车驾驶人有下列交通违法行为之一，<b>一次记9分</b>：<br>（三）驾驶机动车在高速公路或者城市快速路上<b>违法停车</b>的；【违停9】', '', '说法错误', 0005, 0.41);
INSERT INTO `question` VALUES (1733399766886359041, 183, 'd7020', 0002, '驾驶小型汽车在高速公路、城市快速路以外的道路上行驶，超过规定时速20%以上未达50%的，一次记多少分？ ', 'A、12分 B、9分 C、6分 D、3分', '12分 ', '9分 ', '6分 ', '3分', 'D', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：驾驶校车、中型以上载客载货汽车、危险物品运输车辆以外的机动车在<b>高速公路</b>、<b>城市快速路以外的道路上</b>行驶超过规定时速<b>百分之二十以上未达到百分之五十</b>的。', '', '以外的|20%以上未达50%', 0005, 0.55);
INSERT INTO `question` VALUES (1733399766974439426, 184, 'f76b8', 0001, '机动车驾驶人经满分学习、考试合格且罚款已缴纳的，记分予以清除，发还机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第二十三条，机动车驾驶人经满分学习、考试<b>合格且罚款已缴纳</b>的，记分予以清除，发还机动车驾驶证。机动车驾驶人同时被处以暂扣机动车驾驶证的，在暂扣期限届满后发还机动车驾驶证。', '', '合格|予以清除', 0002, 0.06);
INSERT INTO `question` VALUES (1733399767024771074, 185, 'edd8b', 0002, '机动车驾驶人在一个记分周期内累积记分满多少分的，公安机关交通管理部门应当扣留其机动车驾驶证，开具强制措施凭证，并送达满分教育通知书，通知机动车驾驶人参加满分学习、考试？ ', 'A、12分 B、18分 C、24分 D、36分', '12分 ', '18分 ', '24分 ', '36分', 'A', '《道路交通安全违法行为记分管理办法》第十七条 机动车驾驶人在一个记分周期内累积<b>记分满12分</b>的，公安机关交通管理部门应当扣留其机动车驾驶证，开具强制措施凭证，并送达<b>满分教育通知书</b>，通知机动车驾驶人参加满分学习、考试。', '', '累积记分|参加满分学习', 0001, 0.03);
INSERT INTO `question` VALUES (1733399767083491329, 186, 'db7c1', 0001, '机动车驾驶人可以一次性处理完毕同一辆机动车的多起交通违法行为记录，记分分值累积计算。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十四条 机动车驾驶人有二起以上交通违法行为应当予以记分的，记分分值累积计算。<br><b>机动车驾驶人可以一次性处理完毕同一辆机动车的多起交通违法行为记录，记分分值累积计算</b>。累积记分未满12分的，可以处理其驾驶的其他机动车的交通违法行为记录；累积记分满12分的，不得再处理其他机动车的交通违法行为记录。', '', '分值累积|同一辆|多起', 0002, 0.07);
INSERT INTO `question` VALUES (1733399767129628674, 187, 'd9636', 0002, '申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，处多少元以下罚款，申请人在多长时间内不得再次申领机动车驾驶证？ ', 'A、500元，1年 B、500元，3年 C、2000元，1年 D、2000元，3年', '500元，1年 ', '500元，3年 ', '2000元，1年 ', '2000元，3年', 'D', '《机动车驾驶证申领和使用规定》第九十三条，申请人以欺骗、贿赂等<b>不正当手段取得</b>机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，处<b>二千元以下</b>罚款；申请人在<b>三年内</b>不得再次申领机动车驾驶证。', '', '不正当手段|撤销|多少元以下|多长时间内不得', 0005, 0.31);
INSERT INTO `question` VALUES (1733399767175766017, 188, 'd0141', 0002, '小型汽车驾驶人在一个记分周期内累积记分满12分的，应当参加为期几天的道路交通安全法律、法规和相关知识学习？ ', 'A、10天 B、7天 C、5天 D、3天', '10天 ', '7天 ', '5天 ', '3天', 'B', '《道路交通安全违法行为记分管理办法》第十八条，机动车驾驶人在一个记分周期内<b>累积记分满12分的</b>，应当参加<b>为期七天</b>的道路交通安全法律、法规和相关知识学习。其中，大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶人应当参加为期三十天的道路交通安全法律、法规和相关知识学习。', '', '小型汽车|满12分|学习', 0003, 0.14);
INSERT INTO `question` VALUES (1733399767221903361, 189, 'c4849', 0001, '驾驶机动车追逐竞驶、超员、超速构成犯罪依法被吊销机动车驾驶证未满3年的，不得申请机动车驾驶证。', NULL, '对', '错', '空', '空', '错', '危险驾驶构成犯罪吊销驾照<b>5年内不得</b>再次申领。《机动车驾驶证申领和使用规定》第十五条，有下列情形之一的，<b>不得申请机动车驾驶证</b>:<br>（七）驾驶机动车追逐竞驶、超员、超速、违反危险化学品安全管理规定运输危险化学品构成犯罪依法<b>被吊销机动车驾驶证未满五年的</b>；', '', '构成犯罪|吊销|未满3年', 0005, 0.61);
INSERT INTO `question` VALUES (1733399767280623617, 190, 'c5506', 0001, '已持有C1/C2准驾车型驾驶证申请增加C6准驾车型的，应当考试科目二和科目三道路驾驶技能。', NULL, '对', '错', '空', '空', '错', '<b>增驾C6，需考试科二和科三安全文明驾驶常识（俗称科四）</b>。根据《机动车驾驶证申领和使用规定》第三十条，机动车驾驶人考试内容分为道路交通安全法律、法规和相关知识考试科目（以下简称“科目一”）、场地驾驶技能考试科目（以下简称“科目二”）、<b>道路驾驶技能和安全文明驾驶常识考试科目（以下简称“科目三”）</b>。<br><b>已持有</b>大型客车、城市公交车、中型客车、大型货车、<b>小型汽车、小型自动挡汽车</b>准驾车型驾驶证<b>申请增加轻型牵引挂车</b>准驾车型的，应当考试<b>科目二</b>和<b>科目三安全文明驾驶常识</b>。', '', '申请增加C6|应当考试', 0005, 0.44);
INSERT INTO `question` VALUES (1733399767347732482, 191, 'b90cc', 0002, '已取得驾驶小型汽车、小型自动挡汽车准驾车型资格多长时间以上的，可以申请增加轻型牵引挂车准驾车型？ ', 'A、1年 B、2年 C、3年 D、5年', '1年 ', '2年 ', '3年 ', '5年', 'A', '《机动车驾驶证申领和使用规定》第十七条，已持有机动车驾驶证，申请增加准驾车型的，应当在本记分周期和申请前最近一个记分周期内没有记满12分记录。申请增加轻型牵引挂车、中型客车、重型牵引挂车、大型客车准驾车型的，还应当符合下列规定：<br>（一）<b>申请增加轻型牵引挂车准驾车型的</b>，已取得驾驶小型汽车、小型自动挡汽车准驾车型资格<b>一年以上</b>；<br>（二）申请增加中型客车准驾车型的，已取得驾驶城市公交车、大型货车、小型汽车、小型自动挡汽车、低速载货汽车或者三轮汽车准驾车型资格二年以上，并在申请前最近连续二个记分周期内没有记满12分记录；', '', '多长时间|申请增加', 0004, 0.20);
INSERT INTO `question` VALUES (1733399767414841346, 192, 'f1ee7', 0002, '机动车驾驶人申请学法减分时，参加道路交通安全法律、法规和相关知识现场学习满1小时且考试合格的，一次可扣减几分？ ', 'A、1分 B、2分 C、3分 D、6分', '1分 ', '2分 ', '3分 ', '6分', 'B', '《道路交通安全违法行为记分管理办法》第二十七条，参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识<b>现场学习满一小时且考试合格</b>的，一次扣减<b>2分</b>。', '', '现场学习|满1小时', 0005, 0.42);
INSERT INTO `question` VALUES (1733399767490338818, 193, 'ab2e2', 0002, '上道路行驶的机动车不按规定安装机动车号牌的一次记多少分？ ', 'A、12分 B、9分 C、6分 D、3分', '12分 ', '9分 ', '6分 ', '3分', 'D', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，一次<b>记3分</b>：<br>（十）驾驶<b>不按规定安装机动车号牌</b>的机动车上道路行驶的；', '', '不按规定安装', 0005, 0.51);
INSERT INTO `question` VALUES (1733399767553253378, 194, 'b19de', 0002, '机动车驾驶人由摩托车类准驾车型增加汽车类准驾车型后的多长时间为实习期？ ', 'A、6个月 B、12个月 C、18个月 D、24个月', '6个月 ', '12个月 ', '18个月 ', '24个月', 'B', '<b>持有摩托车增驾小型汽车的，属于初次取得汽车类准驾车型，应有12个月的实习期</b>。《机动车驾驶证申领和使用规定》第七十六条，机动车驾驶人初次取得汽车类准驾车型或者初次取得摩托车类准驾车型后的12个月为实习期。', '', '增加|实习期', 0002, 0.05);
INSERT INTO `question` VALUES (1733399767603585025, 195, 'bc172', 0001, '机动车驾驶证在实习期内，驾驶人不得通过接受交通安全教育扣减交通违法行为记分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第二十六条，机动车驾驶人<b>申请接受交通安全教育扣减交通违法行为记分的</b>，公安机关交通管理部门应当受理。但<b>有以下情形之一的，不予受理</b>：<br>（三）<b>机动车驾驶证在实习期内</b>，或者机动车驾驶证逾期未审验，或者机动车驾驶证被扣留、暂扣期间的；', '', '实习期|不得|扣减', 0003, 0.15);
INSERT INTO `question` VALUES (1733399767649722369, 196, 'f49b1', 0002, '交通违法行为累积记分未满多少分的，可以处理其驾驶的其他机动车的交通违法行为记录？ ', 'A、3分 B、12分 C、9分 D、6分', '3分 ', '12分 ', '9分 ', '6分', 'B', '《道路交通安全违法行为记分管理办法》第十四条，机动车驾驶人有二起以上交通违法行为应当予以记分的，记分分值累积计算。<br>机动车驾驶人可以一次性处理完毕同一辆机动车的多起交通违法行为记录，记分分值累积计算。<b>累积记分未满12分的，可以处理其驾驶的其他机动车的交通违法行为记录</b>；累积记分满12分的，不得再处理其他机动车的交通违法行为记录。', '', '未满|其他机动车', 0003, 0.11);
INSERT INTO `question` VALUES (1733399767691665410, 197, 'ecdbe', 0001, '行政处罚决定被依法变更或撤销的，相应记分不会变更或撤销。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十六条，行政处罚决定被<b>依法</b>变更或者撤销的，相应记分<b>应当</b>变更或者撤销。因此，题干表述错误。', '', '依法变更|不会变更', 0005, 0.40);
INSERT INTO `question` VALUES (1733399767750385665, 198, 'f9940', 0002, '轻型牵引挂车对应的车型代号是什么？ ', 'A、C1 B、C2 C、C5 D、C6', 'C1 ', 'C2 ', 'C5 ', 'C6', 'D', '<b>轻型牵引挂车对应的车型代号是C6</b>，准驾的车辆是总质量小于（不包含等于）4500kg的汽车列车。', '', '轻型牵引挂车', 0003, 0.14);
INSERT INTO `question` VALUES (1733399767792328705, 199, 'e8c50', 0002, '机动车驾驶人在参加接受交通安全教育扣减交通违法行为记分中弄虚作假的，由公安机关交通管理部门撤销相应的记分扣减记录，恢复相应记分，处多少元以下罚款？ ', 'A、五百元以下 B、一千元以下 C、二千元以下 D、三千元以下', '五百元以下 ', '一千元以下 ', '二千元以下 ', '三千元以下', 'B', '《道路交通安全违法行为记分管理办法》第三十一条，机动车驾驶人在参加<b>接受交通安全教育扣减交通违法行为记分中弄虚作假</b>的，由公安机关交通管理部门撤销相应记分扣减记录，恢复相应记分，处<b>一千元以下罚款</b>。', '', '弄虚作假|撤销|记分扣减记录|多少元以下', 0005, 0.43);
INSERT INTO `question` VALUES (1733399767838466050, 200, 'c0c1e', 0001, '驾驶非机动车、拖拉机、轮式专用机械车、铰接式客车、全挂拖斗车以及其他设计最高时速低于70公里的机动车，不得进入高速公路。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第五节，高速公路的特别规定，第六十七条，<b>行人、非机动车、拖拉机、轮式专用机械车、铰接式客车、全挂拖斗车以及其他设计最高时速低于七十公里的机动车</b>，<b>不得进入</b>高速公路。高速公路限速标志标明的最高时速不得超过一百二十公里。<br>因此，题干表述正确。', '', '低于70公里|不得进入', 0003, 0.14);
INSERT INTO `question` VALUES (1733399767872020482, 201, 'e4765', 0002, '关于机动车号牌灭失、丢失或者损毁的，以下说法错误的是什么？ ', 'A、向登记地车辆管理所申请补领、换领 B、补领、换领机动车号牌的，原机动车号牌可以继续使用 C、车辆管理所补发、换发号牌，原机动车号码不变 D、补发、换发号牌期间，可申领有效期不超过15日的临时号牌', '向登记地车辆管理所申请补领', '换领 B', '补领', '换领机动车号牌的，原机动车号牌可以继续使用 C', 'B', '<b>请注意，题干要求选出说法错误的选项。</b>《机动车登记规定》第五十一条，机动车号牌灭失、丢失或者损毁的，机动车所有人应当<b>向登记地车辆管理所申请补领、换领</b>。申请时，机动车所有人应当确认申请信息并提交身份证明。<br>车辆管理所应当审查提交的证明、凭证，收回未灭失、丢失或者损毁的号牌，自受理之日起十五日内补发、换发号牌，<b>原机动车号牌号码不变</b>。<br>补发、换发号牌期间，申请人<b>可以申领</b>有效期不超过十五日的临时行驶车号牌。<br><b>补领、换领机动车号牌的，原机动车号牌作废，不得继续使用</b>。', '', '说法错误|号牌灭失', 0004, 0.16);
INSERT INTO `question` VALUES (1733399767930740737, 202, 'b7ced', 0002, '根据交通违法行为的严重程度，一次记分的分值为？ ', 'A、12分、9分、2分、1分 B、12分、6分、2分、1分 C、12分、9分、6分、3分、1分 D、12分、9分、6分、2分、1分', '12分', '9分', '2分', '1分 B', 'C', '《道路交通安全违法行为记分管理办法》第二章 记分分值，第七条 根据交通违法行为的严重程度，一次记分的分值为<b>12分、9分、6分、3分、1分</b>。', '', '分值', 0002, 0.04);
INSERT INTO `question` VALUES (1733399767968489473, 203, 'b35cc', 0001, '代替他人或者让他人代替自己参加机动车驾驶人考试构成犯罪的，处拘役或者管制，并处或者单处罚金。', NULL, '对', '错', '空', '空', '对', '《中华人民共和国刑法》第二百八十四条之一【组织考试作弊罪】在法律规定的国家考试中，组织作弊的，处三年以下有期徒刑或者拘役，并处或者单处罚金；情节严重的，处三年以上七年以下有期徒刑，并处罚金。<br>为他人实施前款犯罪提供作弊器材或者其他帮助的，依照前款的规定处罚。<br>【非法出售、提供试题、答案罪】为实施考试作弊行为，向他人非法出售或者提供第一款规定的考试的试题、答案的，依照第一款的规定处罚。<br><b>【代替考试罪】</b><b>代替他人或者让他人代替自己参加第一款规定的考试的，处拘役或者管制，并处或者单处罚金。</b>', '', '代替|构成犯罪', 0002, 0.05);
INSERT INTO `question` VALUES (1733399768018821121, 204, 'd5906', 0001, '机动车所有权转让后，现机动车所有人未按照规定的时限办理转让登记的，由公安机关交通管理部门处警告或者一千元以下罚款。', NULL, '对', '错', '空', '空', '错', '《机动车登记规定》第七十八条，有下列情形之一的，由公安机关交通管理部门处<b>警告或者二百元以下</b>罚款：<br>（六）机动车所有权转让后，现机动车所有人未按照<b>第二十五条</b>规定的时限办理转让登记的；<br><b>第二十五条</b> 已注册登记的机动车所有权发生转让的，现机动车所有人应当自机动车交付之日起三十日内向登记地车辆管理所申请转让登记。', '', '未按照规定的时限|一千元以下', 0005, 0.64);
INSERT INTO `question` VALUES (1733399768069152769, 205, 'd8acc', 0001, '驾驶机动车载运爆炸物品，未悬挂警示标志并采取必要的安全措施的，一次记12分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（五）驾驶机动车载运爆炸物品、易燃易爆化学物品以及剧毒、放射性等危险物品，未按指定的时间、路线、速度行驶或者<b>未悬挂警示标志并采取必要的安全措施的</b>；', '', '未悬挂|12分', 0005, 0.40);
INSERT INTO `question` VALUES (1733399768119484418, 206, 'df1f5', 0002, '下列关于同一机动车所有人名下两辆机动车的号牌号码互换，以下说法正确的是什么? ', 'A、同一机动车一年内可以多次互换变更 B、两辆机动车使用性质为营运可以申请互换 C、申请前两车无未处理的道路交通安全违法行为和交通事故记录 D、两辆机动车在不同辖区车辆管理所登记可以申请互换', '同一机动车一年内可以多次互换变更 ', '两辆机动车使用性质为营运可以申请互换 ', '申请前两车无未处理的道路交通安全违法行为和交通事故记录 ', '两辆机动车在不同辖区车辆管理所登记可以申请互换', 'C', '《机动车使用规定》第二十条，<b>同一机动车所有人名下机动车的号牌号码需要互换</b>，符合以下情形的，可以向登记地车辆管理所申请变更登记：<br>（一）<b>两辆机动车在同一辖区车辆管理所登记</b>；<br>（二）两辆机动车属于同一号牌种类；<br>（三）<b>两辆机动车使用性质为非营运</b>。<br>机动车所有人应当确认申请信息，提交机动车所有人身份证明、两辆机动车的登记证书、行驶证、号牌。<b>申请前，应当将两车的道路交通安全违法行为和交通事故处理完毕</b>。<br>车辆管理所应当自受理之日起一日内，审查提交的证明、凭证，在机动车登记证书上签注变更事项，收回两车的号牌、行驶证，重新核发号牌、行驶证和检验合格标志。<br><b>同一机动车一年内可以互换变更一次机动车号牌号码。</b>', '', '号牌号码互换|说法正确', 0004, 0.16);
INSERT INTO `question` VALUES (1733399768178204673, 207, 'badba', 0002, '重型、中型载货汽车的车厢后部未按照规定喷涂放大的牌号或者放大的牌号不清晰的，由公安机关交通管理部门处警告或者多少元以下罚款？ ', 'A、二百元 B、五百元 C、一千元 D、二千元', '二百元 ', '五百元 ', '一千元 ', '二千元', 'A', '《机动车登记规定》第七十八条，有下列情形之一的，由公安机关交通管理部门处<b>警告或者二百元以下罚款</b>：<br>（一）重型、中型载货汽车、专项作业车、挂车及大型客车的车身或者车厢后部未按照规定喷涂放大的牌号或者放大的牌号不清晰的；', '', '放大的牌号|不清晰', 0005, 0.32);
INSERT INTO `question` VALUES (1733399768224342018, 208, 'be189', 0001, '驾驶机动车载货长度、宽度、高度超过规定的，一次记1分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，一次<b>记1分</b>：<br>（五）驾驶机动车<b>载货</b>长度、宽度、高度超过规定的；', '', '载货|超过规定|1分', 0003, 0.14);
INSERT INTO `question` VALUES (1733399768270479361, 209, 'a4642', 0001, '组织他人代替实际机动车驾驶人参加审验教育的，没有违法所得的，由公安机关交通管理部门处二万元以下罚款。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第一百条，机动车驾驶人参加审验教育时在签注学习记录、学习过程中弄虚作假的，相应学习记录无效，重新参加审验学习，由公安机关交通管理部门处一千元以下罚款。<br>代替实际机动车驾驶人参加审验教育的，由公安机关交通管理部门处二千元以下罚款。<br><b>组织他人实施前两款行为之一</b>，有违法所得的，由公安机关交通管理部门处违法所得三倍以下罚款，但最高不超过二万元；<b>没有违法所得的</b>，由公安机关交通管理部门处<b>二万元以下罚款。</b>', '', '没有违法所得|处二万元以下', 0003, 0.13);
INSERT INTO `question` VALUES (1733399768522137602, 210, 'b7fdb', 0002, '关于机动车驾驶人参加审验教育时在签注学习记录、学习过程中弄虚作假的会受到的处罚，以下说法错误的是什么? ', 'A、处一千元以下罚款 B、相应学习记录无效 C、暂扣机动车驾驶证 D、重新参加审验学习', '处一千元以下罚款 ', '相应学习记录无效 ', '暂扣机动车驾驶证 ', '重新参加审验学习', 'C', '<b>请注意，题干要求选出说法错误的选项。</b>《机动车驾驶证申领和使用规定》第一百条，机动车驾驶人参加<b>审验教育</b>时在签注学习记录、学习过程中<b>弄虚作假</b>的，相应学习<b>记录无效</b>，<b>重新参加审验学习</b>，由公安机关交通管理部门处<b>一千元以下罚款</b>。', '', '说法错误|弄虚作假', 0005, 0.46);
INSERT INTO `question` VALUES (1733399768585052162, 211, 'bf506', 0001, '组织、参与实施欺骗、贿赂等不正当手段取得机动车驾驶证牟取经济利益的，由公安机关交通管理部门处违法所得三倍以上五倍以下罚款，但最高不超过十万元。', NULL, '对', '错', '空', '空', '对', '根据《机动车驾驶证申领和使用规定》第九十三条：申请人隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，公安机关交通管理部门不予受理或者不予办理，处五百元以下罚款；申请人在一年内不得再次申领机动车驾驶证。<br>申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效，公安机关交通管理部门处二千元以下罚款；申请人在一年内不得再次申领机动车驾驶证。<br>申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，处二千元以下罚款；申请人在三年内不得再次申领机动车驾驶证。<br><b>组织、参与实施前三款行为之一牟取经济利益的，由公安机关交通管理部门处违法所得三倍以上五倍以下罚款，但最高不超过十万元。</b>', '', '组织、参与实施|不正当手段|牟取|三倍以上五倍以下', 0003, 0.10);
INSERT INTO `question` VALUES (1733399768631189506, 212, 'c4fa1', 0001, '年龄在70周岁以上的机动车驾驶人审验时应当按照规定进行记忆力、判断力、反应力等能力测试。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第十四条，申请机动车驾驶证的人，应当符合下列规定：<b>年龄在70周岁以上能够通过记忆力、判断力、反应力等能力测试的</b>，可以申请小型汽车、小型自动挡汽车、残疾人专用小型自动挡载客汽车、轻便摩托车准驾车型的机动车驾驶证。', '', '审验时|70周岁以上', 0001, 0.03);
INSERT INTO `question` VALUES (1733399768694104065, 213, 'b0f4d', 0001, '机动车驾驶人在驾驶证有效期满换领驾驶证时，可以到具有健康体检资质的二级以上医院、乡镇卫生院、社区卫生服务中心、健康体检中心等医疗机构体检。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第六十三条，机动车驾驶人应当于机动车<b>驾驶证有效期满</b>前九十日内，向机动车驾驶证核发地或者核发地以外的车辆管理所<b>申请换证</b>。<b>申请时应当确认申请信息，并提交以下证明、凭证：</b><br>（一）机动车驾驶人的身份证明；<br>（二）<b>医疗机构出具的有关身体条件的证明</b>。<br>第八十五条，办理残疾人专用小型自动挡载客汽车驾驶证业务时，提交的身体条件证明应当由经省级卫生健康行政部门认定的专门医疗机构出具。<b>办理其他机动车驾驶证业务时，提交的身体条件证明应当由县级、部队团级以上医疗机构，或者经地市级以上卫生健康行政部门认定的具有健康体检资质的二级以上医院、乡镇卫生院、社区卫生服务中心、健康体检中心等医疗机构出具</b>。<br>身体条件证明自出具之日起六个月内有效。', '', '健康体检资质', 0002, 0.08);
INSERT INTO `question` VALUES (1733399768740241409, 214, 'bf65d', 0002, '机动车驾驶人请他人代为接受交通违法行为处罚和记分并支付经济利益的，由公安机关交通管理部门处所支付经济利益多少倍以下罚款，但最高不超过多少万元? ', 'A、两，五 B、三，五 C、两，两 D、三，三', '两，五 ', '三，五 ', '两，两 ', '三，三', 'B', '《道路交通安全违法行为记分管理办法》第三十条，机动车驾驶人<b>请他人</b>代为接受交通违法行为处罚和记分<b>并支付经济利益</b>的，由公安机关交通管理部门处所支付经济利益<b>三倍以下</b>罚款，但最高<b>不超过五万元</b>；同时，依法对原交通违法行为作出处罚。', '', '请他人|并支付', 0004, 0.19);
INSERT INTO `question` VALUES (1733399768786378753, 215, 'bb2fd', 0002, '申请人在机动车驾驶人考试过程中组织作弊的，情节严重构成犯罪的，会受到什么处罚? ', 'A、处管制，并处罚金 B、处三年以下有期徒刑，并处罚金 C、处三年以上七年以下有期徒刑，并处罚金 D、处七年以上有期徒刑，并处罚金', '处管制，并处罚金 ', '处三年以下有期徒刑，并处罚金 ', '处三年以上七年以下有期徒刑，并处罚金 ', '处七年以上有期徒刑，并处罚金', 'C', '《中华人民共和国刑法》第二百八十四条之一【组织考试作弊罪】在法律规定的国家考试中，<b>组织作弊的</b>，处三年以下有期徒刑或者拘役，并处或者单处罚金；<b>情节严重的</b>，处三年以上七年以下有期徒刑，并处罚金。', '', '组织作弊|构成犯罪', 0005, 0.48);
INSERT INTO `question` VALUES (1733399768836710401, 216, 'f6e50', 0001, '机动车的型号或者有关技术参数与国务院机动车产品主管部门公告不符的，不予办理注册登记。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》第十五条，有下列情形之一的，<b>不予办理注册登记</b>：<br>（五）<b>机动车的型号或者有关技术参数与国务院机动车产品主管部门公告不符的</b>；', '', '不予办理|不符', 0002, 0.05);
INSERT INTO `question` VALUES (1733399768878653441, 217, 'eb681', 0001, '机动车喷涂、粘贴标识或者车身广告，影响安全驾驶的，由公安机关交通管理部门处警告或者二百元以下罚款。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》第七十八条，有下列情形之一的，由公安机关交通管理部门处<b>警告或者二百元以下罚款</b>：<br>（二）机动车喷涂、粘贴标识或者车身广告，影响安全驾驶的；', '', '车身广告|处警告或者二百元以下', 0001, 0.03);
INSERT INTO `question` VALUES (1733399768916402177, 218, 'e2745', 0001, '机动车因质量问题退车的，机动车所有人应当向户籍所在地车辆管理所提出注销登记。', NULL, '对', '错', '空', '空', '错', '《机动车登记规定》第三十七条，机动车有下列情形之一的，机动车所有人应当向<b>登记地</b>车辆管理所申请注销登记：<br>（一）机动车已达到国家强制报废标准的；<br>（二）机动车未达到国家强制报废标准，机动车所有人自愿报废的；<br>（三）因自然灾害、失火、交通事故等造成机动车灭失的；<br>（四）机动车因故不在我国境内使用的；<br>（五）<b>因质量问题退车的</b>。', '', '退车|户籍所在地', 0005, 0.52);
INSERT INTO `question` VALUES (1733399768954150913, 219, 'fa565', 0001, '年满70周岁以上的机动车驾驶人发生责任交通事故造成人员重伤或者死亡的，应当在本记分周期结束后三十日内到公安机关交通管理部门接受审验。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十二条，年龄在70周岁以上的机动车驾驶人发生责任交通事故造成人员重伤或者死亡的，应当在本记分周期结束后三十日内到公安机关交通管理部门接受审验。', '', '年满70|重伤或者死亡|三十日|接受审验', 0002, 0.04);
INSERT INTO `question` VALUES (1733399769029648385, 220, 'b950d', 0002, '关于实习期，以下说法正确的是什么？ ', 'A、驾驶初次取得汽车类准驾车型后12个月为实习期 B、实习期内累积记分超过6分不到12分，实习期延长6个月 C、持小型汽车驾驶证增驾中型客车驾驶证，实习期为12个月 D、实习期内累积记分达12分的，还要注销最高准驾车型', '驾驶初次取得汽车类准驾车型后12个月为实习期 ', '实习期内累积记分超过6分不到12分，实习期延长6个月 ', '持小型汽车驾驶证增驾中型客车驾驶证，实习期为12个月 ', '实习期内累积记分达12分的，还要注销最高准驾车型', 'A', '《机动车驾驶证申领和使用规定》第七十六条，机动车驾驶人<b>初次取得汽车类准驾车型</b>或者初次取得摩托车类准驾车型<b>后的12个月为实习期</b>。在增加准驾车型后的实习期内，驾驶原准驾车型的机动车时不受上述限制。', '', '实习期|说法正确', 0003, 0.13);
INSERT INTO `question` VALUES (1733399769079980034, 221, 'bb441', 0002, '非营运小微型载客汽车从注册登记之日起，超过多长时间的每年检验1次？ ', 'A、5年 B、6年 C、8年 D、10年', '5年 ', '6年 ', '8年 ', '10年', 'D', '《关于深化机动车检验制度改革优化车检服务工作的意见》非营运小微型载客汽车（9座含9座以下，面包车除外）、摩托车检验周期：<br>在10年内，上线检验2次（第6年、第10年），期间每两年申领一次检验标志；<br><b>超过10年的</b>，<b>每年上线检验1次</b>，并申领检验标志。', '', '每年|1次', 0004, 0.24);
INSERT INTO `question` VALUES (1733399769130311682, 222, 'abbcc', 0002, '已达到报废标准的机动车，________上道路行驶。 ', 'A、经维修后可以 B、缴管理费后可以 C、允许临时 D、不得', '经维修后可以 ', '缴管理费后可以 ', '允许临时 ', '不得', 'D', '《道路交通安全法》第十四条　国家实行机动车强制报废制度，根据机动车的安全技术状况和不同用途，规定不同的报废标准。<br>应当报废的机动车必须及时办理注销登记。<br><b>达到报废标准的机动车不得上道路行驶</b>。报废的大型客、货车及其他营运车辆应当在公安机关交通管理部门的监督下解体。', '', '达到报废', 0001, 0.02);
INSERT INTO `question` VALUES (1733399769168060417, 223, 'd71ee', 0002, '以下哪种车辆可以在高速公路上行驶？ ', 'A、轮式专用机械车 B、铰接式客车 C、拖拉机 D、小客车', '轮式专用机械车 ', '铰接式客车 ', '拖拉机 ', '小客车', 'D', '《道路交通安全法》第五节，高速公路的特别规定，第六十七条，行人、非机动车、<b>拖拉机</b>、<b>轮式专用机械车</b>、<b>铰接式客车</b>、全挂拖斗车以及其他设计最高时速低于七十公里的机动车，<b>不得进入</b>高速公路。高速公路限速标志标明的最高时速不得超过一百二十公里。', '', '可以在', 0002, 0.04);
INSERT INTO `question` VALUES (1733399769205809153, 224, 'b21e6', 0002, '申请机动车注册登记时，以下哪项材料不需要提交？ ', 'A、身份证明 B、交强险证明 C、购车发票 D、行驶证', '身份证明 ', '交强险证明 ', '购车发票 ', '行驶证', 'D', '《机动车登记》第十二条，<b>申请注册登记</b>的，机动车所有人应当交验机动车，确认申请信息，并提交以下证明、凭证：<br>（一）机动车所有人的<b>身份证明</b>；<br>（二）<b>购车发票</b>等机动车来历证明；<br>（三）机动车整车出厂合格证明或者进口机动车进口凭证；<br>（四）机动车<b>交通事故责任强制保险凭证</b>；<br>（五）车辆购置税、车船税完税证明或者免税凭证，但法律规定不属于征收范围的除外；<br>（六）法律、行政法规规定应当在机动车注册登记时提交的其他证明、凭证。<br><b>因此，不需要提交的材料是行驶证</b>。', '', '注册登记|不需要', 0005, 0.30);
INSERT INTO `question` VALUES (1733399769251946497, 225, 'a4fe6', 0002, '申请人办理机动车登记提交的证明、凭证齐全、有效的，公安机关交通管理部门应当________办理登记手续。 ', 'A、1个月内 B、隔天 C、1周内 D、当场', '1个月内 ', '隔天 ', '1周内 ', '当场', 'D', '《道路交通安全法实施条例》第十条　办理机动车登记的申请人提交的证明、凭证齐全、有效的，公安机关交通管理部门<b>应当当场办理</b>登记手续。<br>人民法院、人民检察院以及行政执法部门依法查封、扣押的机动车，公安机关交通管理部门不予办理机动车登记。', '', '齐全|有效', 0003, 0.13);
INSERT INTO `question` VALUES (1733399769327443970, 226, 'ea9fe', 0002, '机动车已达到国家强制报废标准的，申请注销登记时应将机动车怎样处理？ ', 'A、卖给废品收购站 B、交给车辆管理所 C、交售给报废机动车回收企业 D、交给安全技术检验机构', '卖给废品收购站 ', '交给车辆管理所 ', '交售给报废机动车回收企业 ', '交给安全技术检验机构', 'C', '《机动车登记规定》第三十八条 <b>属于第三十七条第一款第一项</b>、第二项规定情形，机动车所有人<b>申请注销登记</b>的，应当<b>向报废机动车回收企业交售机动车</b>，确认申请信息，提交机动车登记证书、号牌和行驶证。<br>第三十七条 机动车有下列情形之一的，机动车所有人应当向登记地车辆管理所申请注销登记：<br>（一）机动车已达到国家强制报废标准的；', '', '注销登记|强制报废', 0003, 0.13);
INSERT INTO `question` VALUES (1733399769390358529, 227, 'a7626', 0002, 'B1表示哪种车型？ ', 'A、重型牵引挂车 B、大型货车 C、中型客车 D、大型客车', '重型牵引挂车 ', '大型货车 ', '中型客车 ', '大型客车', 'C', '<b>中型客车</b>准驾车型的<b>代号是B1</b>；准驾的车辆是中型载客汽车（含核载10人以上、19人以下的城市公共汽车）；准予驾驶的其他准驾车型：C1、C2、C3、C4、M。', '', 'B1', 0005, 0.38);
INSERT INTO `question` VALUES (1733399769444884482, 228, 'c49c9', 0001, '申请机动车注册登记的，机动车所有人必须提交机动车交通事故责任强制保险凭证。', NULL, '对', '错', '空', '空', '对', '《机动车登记》第十二条，<b>申请注册登记的</b>，机动车所有人<b>应当</b>交验机动车，确认申请信息，<b>并提交以下证明、凭证：</b><br>（一）机动车所有人的身份证明；<br>（二）购车发票等机动车来历证明；<br>（三）机动车整车出厂合格证明或者进口机动车进口凭证；<br><b>（四）机动车交通事故责任强制保险凭证；</b><br>（五）车辆购置税、车船税完税证明或者免税凭证，但法律规定不属于征收范围的除外；<br>（六）法律、行政法规规定应当在机动车注册登记时提交的其他证明、凭证。', '', '保险凭证|必须提交', 0002, 0.05);
INSERT INTO `question` VALUES (1733399769499410434, 229, 'cd079', 0002, '非营运小微型载客汽车从注册登记之日起10年内，每多长时间向公安机关申领一次检验标志？ ', 'A、1年 B、2年 C、5年 D、10年', '1年 ', '2年 ', '5年 ', '10年', 'B', '《关于深化机动车检验制度改革优化车检服务工作的意见》非营运小微型载客汽车（9座含9座以下，面包车除外）、摩托车检验周期：<br><b>在10年内</b>，上线检验2次（第6年、第10年），期间<b>每两年申领一次检验标志</b>；<br>超过10年的，每年上线检验1次，并申领检验标志。', '', '10年内|检验标志', 0005, 0.43);
INSERT INTO `question` VALUES (1733399769566519297, 230, 'ef1d9', 0001, '非营运小微型载客汽车从注册登记之日起超过10年的，每年检验一次，并向公安机关申领检验标志。', NULL, '对', '错', '空', '空', '对', '《关于深化机动车检验制度改革优化车检服务工作的意见》非营运小微型载客汽车（9座含9座以下，面包车除外）、摩托车检验周期：<br>在10年内，上线检验2次（第6年、第10年），期间每两年申领一次检验标志；<br><b>超过10年的，每年上线检验1次</b>，并申领检验标志。', '', '超过10年|每年', 0003, 0.12);
INSERT INTO `question` VALUES (1733399769612656641, 231, 'c47c6', 0001, '机动车在抵押登记、质押备案期间不可以办理转让登记。', NULL, '对', '错', '空', '空', '错', '机动车在抵押登记、质押备案期间<b>可以办理转让登记</b>，不可以办理注销登记。《机动车登记规定》第二十六条，在机动车<b>抵押登记期间申请转让登记</b>的，应当由原机动车所有人、现机动车所有人和抵押权人共同申请，车辆管理所一并办理新的抵押登记。<br>在机动车<b>质押备案期间申请转让登记</b>的，应当由原机动车所有人、现机动车所有人和质权人共同申请，车辆管理所一并办理新的质押备案。', '', '不可|转让登记', 0005, 0.55);
INSERT INTO `question` VALUES (1733399769654599682, 232, 'ce168', 0002, '机动车驾驶人有下列哪种行为，会被吊销驾驶证，终生不得重新取得驾驶证？ ', 'A、饮酒后驾驶机动车 B、使用伪造、变造的号牌 C、醉酒驾驶机动车 D、饮酒后或者醉酒驾驶机动车发生重大交通事故并构成犯罪', '饮酒后驾驶机动车 ', '使用伪造', '变造的号牌 C', '醉酒驾驶机动车 D', 'D', '《道路交通安全法》第九十一条： 饮酒后驾驶机动车的，处暂扣6个月机动车驾驶证，并处1000元以上2000元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处10日以下拘留，并处1000元以上2000元以下罚款，吊销机动车驾驶证。<br>醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；5年内不得重新取得机动车驾驶证。<br>饮酒后驾驶营运机动车的，处15日拘留，并处5000元罚款，吊销机动车驾驶证，5年内不得重新取得机动车驾驶证。<br><b>饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的</b>，<b>依法追究刑事责任，并由公安机关交通管理部门</b><b>吊销机动车驾驶证，终生不得重新取得机动车驾驶证</b>。', '', '吊销|终生不得', 0002, 0.06);
INSERT INTO `question` VALUES (1733399769700737026, 233, 'ac4cb', 0002, '机动车驾驶证损毁无法辨认的，要在多长时间内申请换证？ ', 'A、60日 B、50日 C、40日 D、30日', '60日 ', '50日 ', '40日 ', '30日', 'D', '根据《机动车驾驶证申领和使用规定》第六十六条，有下列情形之一的，机动车驾驶人应当<b>在三十日内</b>到机动车驾驶证核发地或者核发地以外的车辆管理所申请换证：<br>（二）机动车驾驶证损毁无法辨认的。', '', '换证', 0002, 0.06);
INSERT INTO `question` VALUES (1733399769746874369, 234, 'e6334', 0001, '驾驶机动车上路行驶，驾驶员应当随车携带行驶证，否则会被扣留车辆。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十五条规定：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者<b>未随车携带行驶证</b>、驾驶证的，公安机关交通管理部门应当<b>扣留机动车</b>。 因此题干表述正确。', '', '随车携带|否则', 0003, 0.09);
INSERT INTO `question` VALUES (1733399769788817410, 235, 'b1db5', 0002, '关于申请增加轻型牵引挂车准驾车型的要求，以下说法正确的是什么? ', 'A、取得小型汽车、小型自动挡汽车驾驶证1年以上 B、取得小型自动挡汽车驾驶证3年以上 C、本记分周期和最近连续一个记分周期内有记满12分记录的，已参加满分教育且考试通过 D、本记分周期和最近连续三个以上记分周期内没有记满12分记录的', '取得小型汽车', '小型自动挡汽车驾驶证1年以上 B', '取得小型自动挡汽车驾驶证3年以上 C', '本记分周期和最近连续一个记分周期内有记满12分记录的，已参加满分教育且考试通过 D', 'A', '《机动车驾驶证申领和使用规定》第十七条，已持有机动车驾驶证，申请增加准驾车型的，应当在<b>本记分周期</b>和申请前<b>最近一个</b>记分周期内<b>没有记满</b>12分记录。<br>（一）申请<b>增加轻型牵引挂车</b>准驾车型的，已取得驾驶<b>小型汽车、小型自动挡汽车</b>准驾车型资格<b>一年以上</b>；', '', '说法正确|增加轻型牵引挂车', 0004, 0.25);
INSERT INTO `question` VALUES (1733399769839149058, 236, 'cc0e9', 0001, '造成交通事故后逃逸构成犯罪的人不能申请机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第十五条 有下列情形之一的，<b>不得申请</b>机动车驾驶证:（三）<b>造成交通事故后逃逸构成犯罪</b>的。', '', '不能', 0002, 0.06);
INSERT INTO `question` VALUES (1733399769897869313, 237, '872ad', 0001, '隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在1年内不得再次申领机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第九十三条，<b>申请人隐瞒有关情况或者提供虚假材料申领机动车驾驶证的</b>，公安机关交通管理部门不予受理或者不予办理，处五百元以下罚款；申请人在<b>一年内不得</b>再次申领机动车驾驶证。', '', '虚假材料|不得', 0002, 0.04);
INSERT INTO `question` VALUES (1733399769939812354, 238, '6a227', 0001, '机动车登记证书、号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向居住地车辆管理所申请补领、换领。', NULL, '对', '错', '空', '空', '错', '《机动车登记规定》第五十一条 <b>机动车号牌</b>灭失、丢失或者损毁的，机动车所有人应当向<b><b>登记地车辆管理所</b></b>申请补领、换领。申请时，机动车所有人应当确认申请信息并提交身份证明。<br>第五十二条 <b>机动车登记证书、行驶证</b>灭失、丢失或者损毁的，机动车所有人应当向<b>登记地车辆管理所</b>申请补领、换领。申请时，机动车所有人应当确认申请信息并提交身份证明。', '', '居住地|换领', 0005, 0.54);
INSERT INTO `question` VALUES (1733399769990144001, 239, '4d9d8', 0001, '驾驶人在核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第六十四条 ，机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。<b>机动车驾驶人在核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。</b>申请时应当确认申请信息，提交机动车驾驶人的身份证明和机动车驾驶证，并申报身体条件情况。', '', '核发地|以外|居住地', 0004, 0.16);
INSERT INTO `question` VALUES (1733399770032087042, 240, 'fe7a8', 0001, '持有境外机动车驾驶证的人，经申请可直接发放中国的机动车驾驶证。', NULL, '对', '错', '空', '空', '错', '根据《机动车驾驶证申领和使用规定》第三十七条，持境外机动车驾驶证申请机动车驾驶证的，应当考试科目一。<br><b>不可</b>经申请直接发放中国驾驶证。', '', '可直接', 0004, 0.19);
INSERT INTO `question` VALUES (1733399770082418690, 241, 'd1875', 0001, '机动车驾驶证电子版与纸质版具有同等效力。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第六十一条 公安机关交通管理部门应当实行机动车驾驶证电子化，机动车驾驶人可以通过互联网交通安全综合服务管理平台申请机动车驾驶证电子版。机动车驾驶证<b>电子版与纸质版具有同等效力</b>。', '', '同等效力', 0001, 0.02);
INSERT INTO `question` VALUES (1733399770120167425, 242, 'ce7f4', 0002, '年龄在70周岁以上的机动车驾驶人发生责任交通事故造成人员重伤或者死亡的，应当在本记分周期结束后多长时间内到公安机关交通管理部门接受审验？ ', 'A、10日 B、15日 C、30日 D、60日', '10日 ', '15日 ', '30日 ', '60日', 'C', '《机动车驾驶证申领和使用规定》第七十二条，年龄在70周岁以上的机动车驾驶人<b>发生责任交通事故造成人员重伤或者死亡</b>的，应当在本记分周期结束后<b>三十日内</b>到公安机关交通管理部门接受审验。', '', '70周岁以上|重伤或者死亡|接受审验', 0004, 0.17);
INSERT INTO `question` VALUES (1733399770162110465, 243, 'c6f5b', 0001, '驾驶人在实习期内不可以单独驾驶机动车上高速公路行驶，在增加准驾车型后的实习期内，驾驶原准驾车型的机动车时不受上述限制。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十七条，机动车驾驶人在实习期内不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；驾驶的机动车不得牵引挂车。<br><b>驾驶人在实习期内驾驶机动车上高速公路行驶，应当由持相应或者包含其准驾车型驾驶证三年以上的驾驶人陪同。</b>其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。<br>在<b>增加准驾车型后</b>的实习期内，驾驶原准驾车型的机动车时<b>不受上述限制</b>。<br>因此，题干表述正确。', '', '不可以单独', 0004, 0.23);
INSERT INTO `question` VALUES (1733399770220830721, 244, 'f8309', 0001, '机动车驾驶人参加满分教育现场学习、网络学习的天数累计不得少于5天，其中，现场学习的天数不得少于1天。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十九条，<b>机动车驾驶人参加现场学习、网络学习的天数累计不得少于五天</b>，其中，<b>现场学习的天数不得少于二天</b>。大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶人参加现场学习、网络学习的天数累计不得少于十天，其中，现场学习的天数不得少于五天。满分学习的剩余天数通过自主学习完成。', '', '满分教育|5天|1天', 0005, 0.31);
INSERT INTO `question` VALUES (1733399770262773762, 245, 'c941c', 0001, '驾驶机动车追逐竞驶、超员、超速、违反危险化学品安全管理规定运输危险化学品构成犯罪依法被吊销机动车驾驶证未满五年的，不得申请机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第十五条，有下列情形之一的，<b>不得申请机动车驾驶证</b>:<br>（七）驾驶机动车追逐竞驶、超员、超速、违反危险化学品安全管理规定运输危险化学品构成犯罪依法被吊销机动车驾驶证<b>未满五年</b>的；<br>因此，题干表述正确。', '', '未满五年|构成犯罪', 0003, 0.10);
INSERT INTO `question` VALUES (1733399770317299713, 246, 'bd001', 0001, '已注册登记的机动车，机动车所有人住所在车辆管理所管辖区域内迁移或者机动车所有人姓名（单位名称）、联系方式变更的，应当向登记地车辆管理所备案。', NULL, '对', '错', '空', '空', '对', '《机动车登记规定》第二十三条 ，已注册登记的机动车<b>有下列情形之一</b>的，机动车所有人应当<b>在信息或者事项变更后三十日内，向登记地车辆管理所申请变更备案</b>：<br>（一）机动车所有人住所在车辆管理所管辖区域内迁移、机动车所有人姓名（单位名称）变更的；', '', '迁移|变更|备案', 0001, 0.03);
INSERT INTO `question` VALUES (1733399770371825666, 247, 'd0723', 0001, '机动车驾驶人同时被处以暂扣机动车驾驶证的，经满分学习考试合格且罚款已缴纳的，记分予以清除，无需等暂扣期限届满，即可发还机动车驾驶证。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第二十三条，机动车驾驶人经满分学习、考试合格且罚款已缴纳的，记分予以清除，发还机动车驾驶证。机动车驾驶人<b>同时被处以暂扣</b>机动车驾驶证的，<b>在暂扣期限届满后发还</b>机动车驾驶证。<br>因此，题干表述错误。', '', '处以暂扣|无需', 0005, 0.38);
INSERT INTO `question` VALUES (1733399770422157313, 248, 'ac75a', 0002, '以下机动车中，可以牵引挂车的是哪种车型？ ', 'A、三轮汽车 B、半挂牵引车 C、低速载货汽车 D、大型载客汽车', '三轮汽车 ', '半挂牵引车 ', '低速载货汽车 ', '大型载客汽车', 'B', '<b>半挂牵引车可以牵引挂车。</b>《道路交通安全法实施条例》第五十六条，<b>机动车牵引挂车应当符合下列规定</b>：<br>(一)载货汽车、<b>半挂牵引车</b>、拖拉机<b>只允许牵引1辆挂车</b>。挂车的灯光信号、制动、连接、安全防护等装置应当符合国家标准；<br>(二)小型载客汽车只允许牵引旅居挂车或者总质量700千克以下的挂车。挂车不得载人；<br>(三)载货汽车所牵引挂车的载质量不得超过载货汽车本身的载质量。<br><b>大型、中型载客汽车</b>，<b>低速载货汽车</b>，<b>三轮汽车</b>以及其他机动车<b>不得牵引挂车</b>。', '', '可以牵引', 0003, 0.15);
INSERT INTO `question` VALUES (1733399770468294658, 249, 'b54ed', 0001, '机动车驾驶人可以在机动车驾驶证核发地或者交通违法行为发生地、处理地参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识学习，并在学习地参加考试。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第二十条，机动车驾驶人可以在机动车驾驶证核发地或者交通违法行为发生地、处理地参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识学习，并<b>在学习地参加考试</b>。', '', '可以在', 0001, 0.02);
INSERT INTO `question` VALUES (1733399770514432002, 250, 'f7d66', 0002, 'C6车型代号对应的准驾车型是什么? ', 'A、重型牵引挂车 B、中型客车 C、轻型牵引挂车 D、小型自动挡汽车', '重型牵引挂车 ', '中型客车 ', '轻型牵引挂车 ', '小型自动挡汽车', 'C', '<b>轻型牵引挂车对应的车型代号是C6</b>，准驾的车辆是总质量小于（不包含等于）4500kg的汽车列车。', '', 'C6|准驾车型', 0004, 0.23);
INSERT INTO `question` VALUES (1733399770556375041, 251, 'a27f4', 0001, '申请人隐瞒有关情况或者提供虚假材料申请校车驾驶资格的，公安机关交通管理部门不予受理或者不予办理，处五百元以下罚款。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第九十三条 ，<b>申请人隐瞒有关情况或者提供虚假材料申请校车驾驶资格的，公安机关交通管理部门不予受理或者不予办理，处五百元以下罚款</b>；申请人在一年内不得再次申请校车驾驶资格。申请人以欺骗、贿赂等不正当手段取得校车驾驶资格的，公安机关交通管理部门撤销校车驾驶资格，处二千元以下罚款；申请人在三年内不得再次申请校车驾驶资格。', '', '申请人|虚假材料|五百元以下', 0003, 0.13);
INSERT INTO `question` VALUES (1733399770602512386, 252, 'd4a26', 0001, '驾驶人可以通过互联网交通安全综合服务管理平台申请机动车驾驶证电子版。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第六十一条，公安机关交通管理部门应当实行机动车驾驶证电子化，机动车驾驶人可以通过互联网交通安全综合服务管理平台申请机动车驾驶证电子版。', '', '互联网交通安全综合服务管理平台', 0001, 0.02);
INSERT INTO `question` VALUES (1733399770648649730, 253, 'c770f', 0002, '申请轻型牵引挂车准驾车型的，年龄应满多少周岁? ', 'A、18周岁 B、19周岁 C、22周岁 D、20周岁', '18周岁 ', '19周岁 ', '22周岁 ', '20周岁', 'D', '《机动车驾驶证申领和使用规定》第十四条，申请机动车驾驶证的人，应当符合下列规定：<b>（一）年龄条件</b>：<br>3. 申请城市公交车、中型客车、大型货车、<b>轻型牵引挂车</b>、无轨电车或者有轨电车准驾车型的，<b>在20周岁以上，60周岁以下</b>；', '', '申请轻型牵引挂车|应满', 0004, 0.20);
INSERT INTO `question` VALUES (1733399770698981377, 254, 'fc511', 0002, '以欺骗、贿赂等不正当手段取得机动车登记的，收缴机动车登记证书、号牌、行驶证，撤销机动车登记，处多少元以下罚款；申请人在多少年内不得再次申请机动车登记？ ', 'A、五百元，1年 B、五百元，3年 C、二千元，1年 D、二千元，3年', '五百元，1年 ', '五百元，3年 ', '二千元，1年 ', '二千元，3年', 'D', '《机动车登记规定》第八十一条，以欺骗、贿赂等<b>不正当手段取得</b>机动车登记的，由公安机关交通管理部门收缴机动车登记证书、号牌、行驶证，撤销机动车登记，处<b>二千元以下罚款</b>；申请人在<b>三年内不得</b>再次申请机动车登记。', '', '不正当手段|收缴|撤销|罚款|不得再次', 0005, 0.31);
INSERT INTO `question` VALUES (1733399770766090242, 255, 'a262d', 0001, '组织他人代替实际机动车驾驶人参加审验教育的，有违法所得的，由公安机关交通管理部门处违法所得三倍以下罚款，但最高不超过二万元。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第一百条 机动车驾驶人<b>参加审验教育时</b>在签注学习记录、学习过程中弄虚作假的，相应学习记录无效，重新参加审验学习，由公安机关交通管理部门处一千元以下罚款。<br>代替实际机动车驾驶人参加审验教育的，由公安机关交通管理部门处二千元以下罚款。<br><b>组织他人实施前两款行为之一，有违法所得的</b>，由公安机关交通管理部门处违法所得<b>三倍以下罚款</b>，但<b>最高不超过二万元</b>；没有违法所得的，由公安机关交通管理部门处二万元以下罚款。', '', '组织他人代替|有违法所得|二万元以下|三倍以下', 0003, 0.12);
INSERT INTO `question` VALUES (1733399770837393409, 256, 'bbc56', 0002, '驾驶机动车牵引挂车时，以下说法正确的是什么？ ', 'A、半挂牵引车可以牵引2辆挂车 B、载货汽车所牵引挂车的载质量可以超过载货汽车本身的载质量 C、低速载货汽车可以牵引挂车 D、三轮汽车不得牵引挂车', '半挂牵引车可以牵引2辆挂车 ', '载货汽车所牵引挂车的载质量可以超过载货汽车本身的载质量 ', '低速载货汽车可以牵引挂车 ', '三轮汽车不得牵引挂车', 'D', '《道路交通安全法实施条例》第五十六条，机动车<b>牵引挂车应当符合</b>下列规定：<br>(一)载货汽车、<b>半挂牵引车</b>、拖拉机<b>只允许牵引1辆挂车</b>。挂车的灯光信号、制动、连接、安全防护等装置应当符合国家标准；<br>(二)小型载客汽车只允许牵引旅居挂车或者总质量700千克以下的挂车。挂车不得载人；<br>(三)载货汽车所牵引挂车的载质量<b>不得超过载货汽车本身的载质量</b>。<br>大型、中型载客汽车，<b>低速载货汽车</b>，<b>三轮汽车</b>以及其他机动车<b>不得牵引挂车</b>。<br>综上，<b>三轮汽车不得牵引挂车，说法正确。</b>', '', '牵引挂车|说法正确', 0004, 0.23);
INSERT INTO `question` VALUES (1733399770933862402, 257, 'c57d5', 0002, '年满20周岁的，不可以初次申请下列哪种准驾车型？ ', 'A、大型货车 B、大型客车 C、小型汽车 D、城市公交车', '大型货车 ', '大型客车 ', '小型汽车 ', '城市公交车', 'B', '<b>大型客车只允许增驾，不可以初次申请</b>。《机动车驾驶证申领和使用规定》第十六条，<b>初次申领</b>机动车驾驶证的，<b>可以申请</b>准驾车型为<b>城市公交车</b>、<b>大型货车</b>、<b>小型汽车</b>、小型自动挡汽车、低速载货汽车、三轮汽车、残疾人专用小型自动挡载客汽车、普通三轮摩托车、普通二轮摩托车、轻便摩托车、轮式专用机械车、无轨电车、有轨电车的机动车驾驶证。<br><b>已持有机动车驾驶证，申请增加准驾车型的</b>，<b>可以申请增加的准驾车型为大型客车</b>、重型牵引挂车、城市公交车、中型客车、大型货车、小型汽车、小型自动挡汽车、低速载货汽车、三轮汽车、轻型牵引挂车、普通三轮摩托车、普通二轮摩托车、轻便摩托车、轮式专用机械车、无轨电车、有轨电车。', '', '20周岁|初次', 0005, 0.37);
INSERT INTO `question` VALUES (1733399771030331393, 258, 'ecf90', 0002, '申请人隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，会受到什么处罚? ', 'A、处两千元以下罚款，申请人在一年内不得再次申领机动车驾驶证 B、处五百元以下罚款，申请人在一年内不得再次申领机动车驾驶证 C、处五百元以上两千元以下罚款，申请人终生不得再次申领机动车驾驶证 D、申请人终身不得再次申领机动车驾驶证', '处两千元以下罚款，申请人在一年内不得再次申领机动车驾驶证 ', '处五百元以下罚款，申请人在一年内不得再次申领机动车驾驶证 ', '处五百元以上两千元以下罚款，申请人终生不得再次申领机动车驾驶证 ', '申请人终身不得再次申领机动车驾驶证', 'B', '《机动车驾驶证申领和使用规定》第九十三条，申请人<b>隐瞒有关情况或者提供虚假材料申领</b>机动车驾驶证的，公安机关交通管理部门不予受理或者不予办理，处<b>五百元以下罚款</b>；申请人在<b>一年内不得再次</b>申领机动车驾驶证。', '', '虚假材料', 0005, 0.38);
INSERT INTO `question` VALUES (1733399771080663041, 259, 'a7f17', 0001, '醉酒后驾驶营运机动车依法被吊销机动车驾驶证未满十年的，不得申请机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第十五条，有下列情形之一的，<b>不得申请</b>机动车驾驶证:<br>（六）<b>醉酒驾驶营运机动车</b>依法被吊销机动车驾驶证<b>未满十年</b>的；', '', '营运机动车|未满十年', 0005, 0.29);
INSERT INTO `question` VALUES (1733399771139383298, 260, '40bed', 0001, '驾驶人有使用其他车辆号牌、行驶证嫌疑的，交通警察可依法扣留车辆。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者<b>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑</b>的。', '', '扣留车辆', 0001, 0.03);
INSERT INTO `question` VALUES (1733399771185520641, 261, '45229', 0002, '上道路行驶的机动车驾驶人未携带机动车行驶证的，除扣留机动车外，并受到什么处罚？ ', 'A、警告 B、罚款 C、拘留 D、吊销驾驶证', '警告 ', '罚款 ', '拘留 ', '吊销驾驶证', 'B', '《道路交通安全法》第九十五条，上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者<b>未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车</b>，通知当事人提供相应的牌证、标志或者补办相应手续，<b>并可以依照本法第九十条的规定予以处罚</b>。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。<br><b>第九十条，</b>机动车驾驶人违反道路交通安全法律、法规关于道路通行规定的，处警告或者二十元以上二百元以下罚款。本法另有规定的，依照规定处罚。', '', '未携带|行驶证', 0004, 0.16);
INSERT INTO `question` VALUES (1733399771240046594, 262, '2c1e1', 0001, '交通警察对未放置保险标志上道路行驶的车辆可依法扣留行驶证。', NULL, '对', '错', '空', '空', '错', '<b>【两证两标一号牌，上路没有就扣车】</b>《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当<b>扣留机动车</b>，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。<br>根据规定，对于不放置保险标志上路的处罚，大家注意审题，扣留的是机动车，<b>不是扣留行驶证。</b>因此题干表述错误。', '', '扣留行驶证', 0005, 0.55);
INSERT INTO `question` VALUES (1733399771290378241, 263, '4f8d1', 0001, '对有伪造或变造号牌、行驶证嫌疑的车辆，交通警察可依法予以扣留。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十六条：<b>伪造、变造</b>或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，<b>扣留该机动车</b>，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', '', '车辆|扣留', 0002, 0.04);
INSERT INTO `question` VALUES (1733399771332321282, 264, 'cfb12', 0001, '对有使用伪造或变造检验合格标志嫌疑的车辆，交通警察只进行罚款处罚。', NULL, '对', '错', '空', '空', '错', '根据《道路交通安全法》第九十六条规定：<b>伪造、变造或者使用伪造、变造的检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处十日以下拘留，并处一千元以上三千元以下罚款；构成犯罪的，依法追究刑事责任。</b> <br>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。 当事人提供相应的合法证明或者补办相应手续的，应当及时退还机动车。', '', '只进行', 0004, 0.18);
INSERT INTO `question` VALUES (1733399771365875714, 265, 'f5287', 0001, '对使用其他车辆号牌、行驶证的车辆，交通警察可依法予以扣留。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十六条：使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。', '', '予以扣留', 0002, 0.04);
INSERT INTO `question` VALUES (1733399771403624449, 266, '3baa6', 0001, '对未按照国家规定投保交强险的车辆，交通警察可依法予以扣留。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十八条规定，机动车所有人、管理人未按照国家规定投保机动车第三者责任强制保险的，由公安机关交通管理部门<b>扣留车辆</b>至依照规定投保后，并处依照规定投保最低责任限额应缴纳的保险费的二倍罚款。', '', '予以扣留', 0002, 0.07);
INSERT INTO `question` VALUES (1733399771441373186, 267, 'e8aed', 0001, '驾驶人有使用其他车辆检验合格标志嫌疑的，交通警察可依法扣留车辆。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者<b>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的。</b>', '', '扣留车辆', 0002, 0.04);
INSERT INTO `question` VALUES (1733399771483316226, 268, '41e71', 0001, '驾驶人有使用其他车辆保险标志嫌疑的，交通警察可依法扣留车辆。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十六条 　　伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，<b>扣留该机动车</b>，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。<br>　　伪造、变造或者使用伪造、变造的检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处十日以下拘留，并处一千元以上三千元以下罚款；构成犯罪的，依法追究刑事责任。<br>　　<b>使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，</b>由公安机关交通管理部门予以收缴，<b>扣留该机动车</b>，处二千元以上五千元以下罚款。<br>　　当事人提供相应的合法证明或者补办相应手续的，应当及时退还机动车。', '', '扣留车辆', 0002, 0.04);
INSERT INTO `question` VALUES (1733399771516870658, 269, 'ac554', 0002, '驾驶人将机动车交由什么样的人驾驶的，交通警察可依法扣留机动车驾驶证？ ', 'A、实习期驾驶人 B、取得驾驶证的人 C、驾驶证被吊销的人 D、驾驶证记分达到6分的人', '实习期驾驶人 ', '取得驾驶证的人 ', '驾驶证被吊销的人 ', '驾驶证记分达到6分的人', 'C', '有下列情形之一的，交通警察可以<b>扣留机动车驾驶证</b>： ①饮酒后驾驶机动车的；②<b>将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的</b>； ③机动车行驶超过规定时速50%的； ④驾驶拼装或者已达到报废标准嫌疑的机动车上道路行驶的； ⑤在一个记分周期内累积记分达到12分的。<br>《道路交通安全法》第一百一十条　执行职务的<b>交通警察</b>认为应当对道路交通违法行为人给予暂扣或者吊销机动车驾驶证处罚的，可以先予<b>扣留机动车驾驶证</b>，并在二十四小时内将案件移交公安机关交通管理部门处理。', '', '交由|依法扣留|驾驶证', 0002, 0.07);
INSERT INTO `question` VALUES (1733399771583979521, 270, '6e510', 0001, '驾驶人将机动车交给驾驶证被吊销的人驾驶的，交通警察依法扣留驾驶证。', NULL, '对', '错', '空', '空', '对', '有下列情形之一的，<b>交通警察</b>可以<b>扣留</b>机动车<b>驾驶证</b>： ①饮酒后驾驶机动车的；<b>②将机动车交由未取得机动车<b>驾驶证</b>或者机动车<b>驾驶证</b>被吊销、暂扣的人驾驶的；</b> ③机动车行驶超过规定时速50%的； ④驾驶拼装或者已达到报废标准嫌疑的机动车上道路行驶的； ⑤在一个记分周期内累积记分达到12分的。', '', '驾驶证|扣留', 0003, 0.12);
INSERT INTO `question` VALUES (1733399771638505474, 271, 'e35c4', 0001, '驾驶人将机动车交给驾驶证被暂扣的人驾驶的，交通警察给予口头警告。', NULL, '对', '错', '空', '空', '错', '有下列情形之一的，<b>交通警察</b>可以<b>扣留</b>机动车<b>驾驶证</b>： ①饮酒后驾驶机动车的；<b>②将机动车交由未取得机动车<b>驾驶证</b>或者机动车<b>驾驶证</b>被吊销、暂扣的人驾驶的；</b> ③机动车行驶超过规定时速50%的； ④驾驶拼装或者已达到报废标准嫌疑的机动车上道路行驶的； ⑤在一个记分周期内累积记分达到12分的。', '', '口头警告', 0003, 0.12);
INSERT INTO `question` VALUES (1733399771680448513, 272, '89ffa', 0001, '驾驶人驾驶有达到报废标准嫌疑机动车上路的，交通警察依法予以拘留。', NULL, '对', '错', '空', '空', '错', '<b>应当予以收缴，强制报废，题干描述错误。</b>根据《道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门<b>应当予以收缴，强制报废</b>。对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。', '', '达到报废|予以拘留', 0005, 0.56);
INSERT INTO `question` VALUES (1733399771722391553, 273, '6211d', 0002, '饮酒后或者醉酒驾驶机动车发生重大交通事故构成犯罪的，依法追究刑事责任，吊销机动车驾驶证，将多少年内不得申请机动车驾驶证？ ', 'A、五年 B、十年 C、二十年 D、终生', '五年 ', '十年 ', '二十年 ', '终生', 'D', '《道路交通安全法》第九十一条，饮酒后或者醉酒驾驶机动车发生重大交通事故，<b>构成犯罪的</b>，依法追究刑事责任，并由公安机关交通管理部门<b>吊销机动车驾驶证</b>，<b>终生不得</b>重新取得机动车驾驶证。', '', '饮酒|醉酒|构成犯罪', 0004, 0.20);
INSERT INTO `question` VALUES (1733399771764334594, 274, '660de', 0001, '驾驶拼装的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废，并吊销机动车驾驶证。', NULL, '对', '错', '空', '空', '对', '<b>驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。</b>对驾驶前款所列机动车上道路行驶的驾驶人，处二百元以上二千元以下罚款，并吊销机动车驾驶证。', '', '拼装的机动车|强制报废', 0002, 0.05);
INSERT INTO `question` VALUES (1733399771810471937, 275, '4860b', 0001, '机动车驾驶人造成重大交通事故后逃逸，构成犯罪的，十年内不能申请机动车驾驶证。', NULL, '对', '错', '空', '空', '错', '《道路交通交通法》第一百零一条：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。<b>造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。</b>', '', '逃逸|构成犯罪|十年内', 0005, 0.34);
INSERT INTO `question` VALUES (1733399771864997889, 276, 'daa6d', 0002, '以下哪种情形不会被扣留车辆？ ', 'A、使用变造的机动车登记证书 B、使用伪造号牌 C、未随车携带灭火器 D、未随车携带行驶证', '使用变造的机动车登记证书 ', '使用伪造号牌 ', '未随车携带灭火器 ', '未随车携带行驶证', 'C', '<b>两证、两标、一号牌，</b>上路没有就扣车。未随车携带<b>灭火器</b>的，是<b>不会被扣留车辆</b>的。<br>根据《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门<b>应当扣留机动车</b>，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。<br>第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，<b>扣留该机动车</b>，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', '', '不会', 0003, 0.11);
INSERT INTO `question` VALUES (1733399771906940929, 277, 'beeac', 0001, '申请人以欺骗、贿赂等不正当手段取得校车驾驶资格的，处两千元以下罚款，申请人在三年内不得再次申请校车驾驶资格。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第九十三条，申请人隐瞒有关情况或者提供虚假材料申请校车驾驶资格的，公安机关交通管理部门不予受理或者不予办理，处五百元以下罚款；申请人在一年内不得再次申请校车驾驶资格。申请人以<b>欺骗、贿赂等不正当手段取得校车驾驶资格</b>的，公安机关交通管理部门撤销校车驾驶资格，处<b>二千元以下罚款</b>；申请人在<b>三年内不得再次申请校车</b>驾驶资格。', '', '欺骗|两千元以下|三年内不得', 0002, 0.08);
INSERT INTO `question` VALUES (1733399771948883969, 278, '02567', 0002, '机动车驾驶人造成事故后逃逸构成犯罪的，吊销驾驶证且多长时间不得重新取得驾驶证？ ', 'A、5年内 B、10年内 C、终生 D、20年内', '5年内 ', '10年内 ', '终生 ', '20年内', 'C', '《道路交通安全法》第一百零一条 违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。<br>造成交通事故后<b>逃逸且构成犯罪</b>的，由公安机关交通管理部门吊销机动车驾驶证，且<b>终生不得重新取得</b>机动车驾驶证。【逃终生】', '', '逃逸构成犯罪|多长时间', 0003, 0.15);
INSERT INTO `question` VALUES (1733399771986632705, 279, '63ec6', 0001, '对未取得驾驶证驾驶机动车的，追究其法律责任。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第十条，驾驶机动车，应当依法取得机动车驾驶证。<br><b>未取得机动车驾驶证、</b>机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处十五日以下拘留。', '', '未取得|追究', 0002, 0.06);
INSERT INTO `question` VALUES (1733399772032770049, 280, 'c30ad', 0001, '伪造、变造机动车驾驶证构成犯罪的将被依法追究刑事责任。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；<b>构成犯罪的，依法追究刑事责任。</b>', '', '伪造|依法追究', 0001, 0.03);
INSERT INTO `question` VALUES (1733399772091490306, 281, '81c2d', 0002, '机动车驾驶人有以下哪种违法行为的，暂扣六个月机动车驾驶证？ ', 'A、醉酒后驾驶机动车的 B、伪造、变造机动车驾驶证的 C、饮酒后驾驶机动车的 D、使用伪造、变造机动车驾驶证的', '醉酒后驾驶机动车的 ', '伪造', '变造机动车驾驶证的 C', '饮酒后驾驶机动车的 D', 'C', '《道路交通安全法》第九十一条，<b>饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。</b>因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。', '', '暂扣六个月', 0004, 0.22);
INSERT INTO `question` VALUES (1733399772141821954, 282, 'dd379', 0002, '醉酒驾驶非营运机动车的，由公安机关交通管理部门吊销机动车驾驶证，依法追究刑事责任，并且多少年内不得重新取得机动车驾驶证？ ', 'A、终生 B、十年 C、五年 D、二十年', '终生 ', '十年 ', '五年 ', '二十年', 'C', '《道路交通安全法》第九十一条　饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。 <br><b>醉酒驾驶机动车的</b>，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；<b>五年内不得重新取得</b>机动车驾驶证。', '', '醉酒驾驶|不得', 0005, 0.48);
INSERT INTO `question` VALUES (1733399772187959297, 283, 'a75ba', 0001, '有吸食、注射毒品后驾驶车辆行为的机动车驾驶人，会被注销驾驶证。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第七十九条 机动车驾驶人有下列情形之一的，车辆管理所<b>应当注销</b>其机动车驾驶证：<br><b>（六）被查获有吸食、注射毒品后驾驶机动车行为，依法被责令社区戒毒、社区康复或者决定强制隔离戒毒，或者长期服用依赖性精神药品成瘾尚未戒除的；</b>', '', '毒品|会被注销', 0001, 0.03);
INSERT INTO `question` VALUES (1733399772229902338, 284, 'ac0dd', 0002, '出售已达到报废标准的机动车的，没收违法所得，处销售金额等额的罚款，对该机动车予以收缴，并________。 ', 'A、吊销购车人驾驶证 B、拘留购车人 C、强制报废 D、拘留售车人', '吊销购车人驾驶证 ', '拘留购车人 ', '强制报废 ', '拘留售车人', 'C', '《道路交通安全法》第一百条<br>驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门<b>应当予以收缴，强制报废</b>。<br>对驾驶前款所列机动车上道路行驶的驾驶人，处二百元以上二千元以下罚款，并吊销机动车驾驶证。<br><b>出售已达到报废标准的机动车的，没收违法所得，处销售金额等额的罚款，对该机动车依照本条第一款的规定处理</b>。', '', '出售|罚款|予以收缴', 0005, 0.29);
INSERT INTO `question` VALUES (1733399772280233986, 285, 'b5bf0', 0002, '机动车驾驶证被吊销期间驾驶机动车的，公安机关交通管理部门除按照规定罚款外，还可以并处以下哪种处罚？ ', 'A、扣留车辆 B、15日以下拘留 C、5年不准领取驾驶证 D、吊销驾驶证', '扣留车辆 ', '15日以下拘留 ', '5年不准领取驾驶证 ', '吊销驾驶证', 'B', '《道路交通安全法》第九十九条，有下列行为之一的，由公安机关交通管理部门<b>处二百元以上二千元以下罚款</b>：<br><b>（一）</b>未取得机动车驾驶证、<b>机动车驾驶证被吊销</b>或者机动车驾驶证被暂扣期间驾驶机动车的；<br>行为人有前款<b>第一项</b>、第三项、第五项至第八项情形之一的，<b>可以并处十五日以下拘留</b>。', '', '被吊销期间驾驶|可以并处', 0005, 0.41);
INSERT INTO `question` VALUES (1733399772326371329, 286, 'b5de1', 0002, '饮酒后驾驶机动车的，处暂扣多长时间驾驶证，并处1000元以上2000元以下罚款？ ', 'A、1个月以上3个月以下 B、6个月 C、3个月以上6个月以下 D、12个月', '1个月以上3个月以下 ', '6个月 ', '3个月以上6个月以下 ', '12个月', 'B', '《道路交通安全法》第九十一条，<b>饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。</b>因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。', '', '饮酒后|暂扣|并处|驾驶证', 0004, 0.21);
INSERT INTO `question` VALUES (1733399772380897281, 287, 'a1eca', 0002, '驾驶人在下列何种情形下，可以驾驶机动车？ ', 'A、饮酒后 B、饮茶后 C、过度疲劳时 D、患有妨碍安全驾驶的疾病', '饮酒后 ', '饮茶后 ', '过度疲劳时 ', '患有妨碍安全驾驶的疾病', 'B', '《道路交通安全法》第二十二条　机动车驾驶人应当遵守道路交通安全法律、法规的规定，按照操作规范安全驾驶、文明驾驶。<br><b>饮酒</b>、服用国家管制的精神药品或者麻醉药品，或者<b>患有妨碍安全驾驶机动车的疾病</b>，或者<b>过度疲劳</b>影响安全驾驶的，<b>不得驾驶</b>机动车。', '', '可以驾驶', 0003, 0.10);
INSERT INTO `question` VALUES (1733399772422840322, 288, '7ed55', 0002, '驾驶机动车在路口直行遇到这种信号灯应怎样行驶？ ', 'A、不得越过停止线 B、加速直行通过 C、左转弯行驶 D、进入路口等待', '不得越过停止线 ', '加速直行通过 ', '左转弯行驶 ', '进入路口等待', 'A', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 <b>红灯亮</b>时，禁止车辆通行，<b>车辆不得越过停止线</b>，右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 'https://timg.mnks.cn/20230927b0cb64aa.jpg', '路口直行', 0003, 0.11);
INSERT INTO `question` VALUES (1733399772468977665, 289, '51b33', 0001, '驾驶机动车行驶到这个位置时，如果车前轮已越过停止线可以继续通过。', NULL, '对', '错', '空', '空', '错', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 <b>红灯亮</b>时，<b>禁止车辆通行</b>，车辆不得越过停止线。<br>《道路交通安全法实施条例》第三十八条 机动车信号灯和非机动车信号灯表示：<br>(一)绿灯亮时，准许车辆通行，但转弯的车辆不得妨碍被放行的直行车辆、行人通行；<br>(二)<b>黄灯亮时，已越过停止线的车辆可以继续通行</b>；<br>(三)<b>红灯亮时，禁止车辆通行</b>。<br>在未设置非机动车信号灯和人行横道信号灯的路口，非机动车和行人应当按照机动车信号灯的表示通行。<br>红灯亮时，右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 'https://timg.mnks.cn/20230927e1a0f9e9.jpg', '前轮已越过', 0002, 0.08);
INSERT INTO `question` VALUES (1733399772531892225, 290, '4e901', 0001, '驾驶机动车在这种信号灯亮的路口，可以右转弯。', NULL, '对', '错', '空', '空', '对', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 <b>红灯亮</b>时，禁止车辆通行，车辆不得越过停止线，<b>右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行</b>。<br>如图所示路口安全，因此可以右转弯。', 'https://timg.mnks.cn/20230927d35016c0.jpg', '右转弯', 0004, 0.16);
INSERT INTO `question` VALUES (1733399772594806785, 291, '2e726', 0001, '驾驶机动车在路口遇到这种信号灯禁止通行。', NULL, '对', '错', '空', '空', '错', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 <b>绿灯亮</b>时，<b>准许车辆通行</b>，但转弯的车辆不得妨碍被放行的直行车辆、行人通行。', 'https://timg.mnks.cn/20230927c9a0eb0a.jpg', '禁止', 0002, 0.05);
INSERT INTO `question` VALUES (1733399772632555522, 292, '3bad3', 0002, '前方路口这种信号灯亮表示什么意思？ ', 'A、路口警示 B、禁止通行 C、准许通行 D、提醒注意', '路口警示 ', '禁止通行 ', '准许通行 ', '提醒注意', 'B', '《道路交通安全法》第二十六条　交通信号灯由红灯、绿灯、黄灯组成。<b>红灯表示禁止通行</b>，绿灯表示准许通行，黄灯表示警示。', 'https://timg.mnks.cn/20230926f37c3935.jpg', '信号灯亮', 0003, 0.13);
INSERT INTO `question` VALUES (1733399772674498562, 293, '9085f', 0002, '前方路口这种信号灯亮表示什么意思？ ', 'A、路口警示 B、禁止通行 C、准许通行 D、提醒注意', '路口警示 ', '禁止通行 ', '准许通行 ', '提醒注意', 'C', '《道路交通安全法》第二十六条　交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，<b>绿灯表示准许通行</b>，黄灯表示警示。', 'https://timg.mnks.cn/20230926c0c75626.jpg', '信号灯', 0003, 0.11);
INSERT INTO `question` VALUES (1733399772720635906, 294, '6e1d1', 0002, '前方路口这种信号灯亮表示什么意思？ ', 'A、路口警示 B、加速直行 C、加速左转 D、禁止右转', '路口警示 ', '加速直行 ', '加速左转 ', '禁止右转', 'A', '《道路交通安全法》第二十六条　交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，绿灯表示准许通行，<b>黄灯表示警示</b>。', 'https://timg.mnks.cn/20230926db494d05.jpg', '信号灯', 0001, 0.02);
INSERT INTO `question` VALUES (1733399772796133378, 295, '95cbd', 0001, '驾驶机动车在路口遇到这种信号灯亮时，要在停止线前停车瞭望。', NULL, '对', '错', '空', '空', '错', '机动车信号灯有红、黄、绿三种颜色。<br><b>绿灯亮</b>时，<b>准许车辆通行</b>，但转弯的车辆不得妨碍被放行的直行车辆、行人通行。<br>黄灯亮时，已越过停止线的车辆可以继续通行；没有越过停止线的车辆需停车等待，不得进入路口，更不得加速抢行通过交叉路口。<br>红灯亮时，禁止车辆通行，车辆不得越过停止线；右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 'https://timg.mnks.cn/20230927d82affab.jpg', '停车瞭望', 0004, 0.16);
INSERT INTO `question` VALUES (1733399772842270721, 296, '019e1', 0001, '驾驶机动车在路口遇到这种信号灯亮时，不能右转弯。', NULL, '对', '错', '空', '空', '错', '《道路交通安全实施条例》第五十一条 机动车<b>通过有交通信号灯控制</b>的交叉路口，应当按照下列规定通行： <br>(一)在划有导向车道的路口，按所需行进方向驶入导向车道； <br>(二)准备进入环形路口的让已在路口内的机动车先行； <br>(三)向左转弯时，靠路口中心点左侧转弯。转弯时开启转向灯，夜间行驶开启近光灯； <br><b>(四)遇放行信号时，依次通过</b>；', 'https://timg.mnks.cn/20230927eb925395.jpg', '不能', 0002, 0.06);
INSERT INTO `question` VALUES (1733399772905185281, 297, 'd8fc0', 0001, '驾驶机动车遇到这种信号灯，可在对面直行车前直接向左转弯。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十一条 机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行： <br>(七)在没有方向指示信号灯的交叉路口，<b>转弯的机动车让直行的车辆、行人先行</b>。相对方向行驶的右转弯机动车让左转弯车辆先行。 <br>如图所示，对面车辆没有开启转向灯，可判断其为直行车辆；我方车辆左转向灯亮，准备左转弯行驶；因此，应当让对面直行车先行。', 'https://timg.mnks.cn/20230927d4978f79.jpg', '可|直接', 0003, 0.10);
INSERT INTO `question` VALUES (1733399772951322625, 298, 'c8941', 0002, '驾驶机动车在路口遇到这种信号灯表示什么意思？ ', 'A、路口警示 B、禁止右转 C、准许直行 D、加速通过', '路口警示 ', '禁止右转 ', '准许直行 ', '加速通过', 'A', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', 'https://timg.mnks.cn/20230927d24ffd69.jpg', '信号灯', 0002, 0.06);
INSERT INTO `question` VALUES (1733399772997459970, 299, 'b4c9b', 0001, '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 'https://timg.mnks.cn/20230927f66d7d23.jpg', '加速通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399773035208705, 300, '29934', 0001, '驾驶机动车在前方路口不能右转弯。', NULL, '对', '错', '空', '空', '错', '闪光警告信号灯为持续闪烁的黄灯时，提示车辆、行人通行时注意瞭望，确认安全后通过。因此驾驶机动车在前方路口可以右转弯。', 'https://timg.mnks.cn/20230927fee5b6f3.jpg', '不能右转弯', 0003, 0.13);
INSERT INTO `question` VALUES (1733399773068763138, 301, '1fe69', 0001, '驾驶机动车遇到这种信号灯亮时，如果已越过停止线，可以继续通行。', NULL, '对', '错', '空', '空', '对', '如图所示，黄灯亮时车辆已越过停止线，可以继续通行。《道路交通安全法实施条例》第三十八条：<b>黄灯亮时，已越过停止线的车辆可以继续通行</b>；没有越过停止线的车辆需停车等待，不得进入路口，更不得加速强行通过交叉路口。', 'https://timg.mnks.cn/20230927a656ef66.jpg', '已越过', 0004, 0.22);
INSERT INTO `question` VALUES (1733399773140066306, 302, '5bfaa', 0002, '遇到这种情况时怎样行驶？ ', 'A、禁止车辆在两侧车道通行 B、减速进入两侧车道行驶 C、进入右侧车道行驶 D、加速进入两侧车道行驶', '禁止车辆在两侧车道通行 ', '减速进入两侧车道行驶 ', '进入右侧车道行驶 ', '加速进入两侧车道行驶', 'A', '车辆行驶在设置了车道信号灯的道路上，必须<b>选择绿色箭头灯亮的车道行驶</b>。绿色箭头灯亮时，准许本车道车辆按指示方向通行；<b>红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行</b>。 <br>如图所示：<b>两侧车道红色叉形灯亮，因此禁止通行</b>。', 'https://timg.mnks.cn/20230927eee6a2c7.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399773182009345, 303, '652c5', 0001, '遇到这种情况时，中间车道不允许车辆通行。', NULL, '对', '错', '空', '空', '对', '车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶。绿色箭头灯亮时，准许本车道车辆按指示方向通行；<b>红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行</b>。 <br>如图所示，<b>中间车道红色叉形灯亮</b>，表示禁止车辆通行。', 'https://timg.mnks.cn/20230927e205e540.jpg', '不允许', 0003, 0.11);
INSERT INTO `question` VALUES (1733399773223952385, 304, '22fc3', 0001, '这辆红色轿车可以在该车道行驶。', NULL, '对', '错', '空', '空', '错', '车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶。绿色箭头灯亮时，准许本车道车辆按指示方向通行；<b>红色叉形灯</b>或者红色箭头灯<b>亮时</b>，<b>禁止本车道车辆通行</b>。 <br>如图所示：<b>红色轿车行驶的车道</b>，<b>红色叉形灯</b>亮，表示该车道<b>禁止车辆通行。</b>', 'https://timg.mnks.cn/20230927dcfe39f0.jpg', '可以在', 0002, 0.05);
INSERT INTO `question` VALUES (1733399773278478337, 305, 'f3559', 0001, '驾驶机动车要选择绿色箭头灯亮的车道行驶。', NULL, '对', '错', '空', '空', '对', '车辆行驶在设置了车道信号灯的道路上，必须<b>选择绿色箭头灯亮的车道行驶</b>。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。', 'https://timg.mnks.cn/20230927cfc50f12.jpg', '绿色箭头', 0001, 0.03);
INSERT INTO `question` VALUES (1733399773341392898, 306, 'daa75', 0002, '这个路口允许车辆怎样行驶？ ', 'A、向左、向右转弯 B、直行或向左转弯 C、向左转弯 D、直行或向右转弯', '向左', '向右转弯 B', '直行或向左转弯 C', '向左转弯 D', 'D', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。<b>绿色箭头表示允许车辆沿箭头所指方向通行</b>；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 <br>如图所示，<b>绿色箭头灯向上和向右</b>，表示<b>允许车辆直行或向右转弯行驶</b>。', 'https://timg.mnks.cn/20230927d1e1d9c9.jpg', '允许', 0002, 0.06);
INSERT INTO `question` VALUES (1733399773379141634, 307, '5dab2', 0002, '这个路口允许车辆怎样行驶？ ', 'A、向左转弯 B、直行 C、直行或向右转弯 D、向右转弯', '向左转弯 ', '直行 ', '直行或向右转弯 ', '向右转弯', 'A', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 <br>如图所示，<b>绿色箭头灯向左</b>，表示允许车辆<b>向左转弯</b>通行。', 'https://timg.mnks.cn/20230927d092b626.jpg', '允许', 0002, 0.05);
INSERT INTO `question` VALUES (1733399773421084673, 308, 'ae8b7', 0002, '这个路口允许车辆怎样行驶？ ', 'A、直行 B、向右转弯 C、直行或向左转弯 D、向左转弯', '直行 ', '向右转弯 ', '直行或向左转弯 ', '向左转弯', 'B', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 <br>如图所示，<b>绿色箭头灯向右</b>，表示<b>允许车辆向右转弯行驶</b>。', 'https://timg.mnks.cn/20230926c18e0862.jpg', '允许', 0002, 0.04);
INSERT INTO `question` VALUES (1733399773463027714, 309, 'afc58', 0001, '驾驶机动车在这种情况下不能左转弯。', NULL, '对', '错', '空', '空', '对', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；<b>红色箭头表示禁止</b>车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 <br>如图所示，<b>红色箭头灯向左</b>，表示<b>禁止车辆左转弯行驶</b>。', 'https://timg.mnks.cn/20230926dc52fd04.jpg', '不能', 0002, 0.07);
INSERT INTO `question` VALUES (1733399773504970754, 310, '0a1e1', 0001, '驾驶机动车在这种情况下可以右转弯。', NULL, '对', '错', '空', '空', '错', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；<b>红色箭头</b>表示<b>禁止车辆沿箭头所指方向通行</b>；黄色箭头表示对箭头所指方向车辆起黄灯作用。 <br>如图所示，<b><b>红色箭头</b>灯向上和向右</b>，表示<b>禁止车辆直行和右转弯</b>行驶。', 'https://timg.mnks.cn/20230926e5282da1.jpg', '可以', 0003, 0.15);
INSERT INTO `question` VALUES (1733399773551108097, 311, '89f2f', 0001, '驾驶机动车在这种情况下不能直行和左转弯。', NULL, '对', '错', '空', '空', '对', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；<b>红色箭头</b>表示<b>禁止车辆沿箭头所指方向通行</b>；黄色箭头表示对箭头所指方向车辆起黄灯作用。 <br>如图所示，红色箭头灯<b>向上和向左</b>，表示<b>禁止车辆直行和左转弯</b>行驶。', 'https://timg.mnks.cn/20230926a898a000.jpg', '不能', 0003, 0.09);
INSERT INTO `question` VALUES (1733399773593051138, 312, 'b20ef', 0002, '驾驶机动车遇到这种信号灯不断闪烁时怎样行驶？ ', 'A、尽快加速通过 B、靠边停车等待 C、注意瞭望安全通过 D、紧急制动', '尽快加速通过 ', '靠边停车等待 ', '注意瞭望安全通过 ', '紧急制动', 'C', '<b>闪光警告信号灯为持续闪烁的黄灯</b>，提示车辆、行人通行时<b>注意瞭望，确认安全后通过</b>。<br>【拓展知识】<br>黄灯的法律含义是警示，<b>只闪黄灯</b>表示此路口交通信号暂时解除，过往车辆经过此路口的时候需要提前减速观察周围情况决定停不停车，这种信号灯往往设置在车流不大的路口或是夜间等车流平峰时段。', 'https://timg.mnks.cn/20230926a065483a.jpg', '不断闪烁', 0003, 0.13);
INSERT INTO `question` VALUES (1733399773643382785, 313, '7355e', 0002, '驾驶机动车在铁路道口看到这种信号灯时怎样行驶？ ', 'A、边观察边缓慢通过 B、不换挡加速通过 C、在火车到来前通过 D、不得越过停止线', '边观察边缓慢通过 ', '不换挡加速通过 ', '在火车到来前通过 ', '不得越过停止线', 'D', '道路与铁路平面交叉道口有两个红灯交替闪烁或一个红灯亮时，表示火车要通过了，此时禁止车辆、行人通行，<b>车辆不得越过停止线</b>。红灯熄灭时，表示允许车辆行人通行。', 'https://timg.mnks.cn/20230926bcd6e32b.jpg', '铁路道口', 0002, 0.08);
INSERT INTO `question` VALUES (1733399773681131522, 314, 'a75b5', 0001, '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', NULL, '对', '错', '空', '空', '对', '<b>注意区分：黄灯VS黄闪</b>。<br>【黄闪】<b>持续闪烁的黄灯</b>是闪光警告信号灯，提示路口交通信号暂时解除，车辆、行人通行时<b>注意瞭望，确认安全后通过</b>。<br>【黄灯】黄灯是一种过渡信号灯，起到警示作用，提示信号即将变换，已越过停止线的车可以继续通过；没有越过停止线的要停车等待。', '', '持续闪烁|确认安全', 0002, 0.07);
INSERT INTO `question` VALUES (1733399773723074562, 315, '58e01', 0001, '黄灯持续闪烁，表示机动车可以加速通过。', NULL, '对', '错', '空', '空', '错', '闪光警告信号灯为持续闪烁的黄灯，提示车辆、行人通行时注意瞭望，确认安全后通过。因此，<b>加速通过做法错误</b>。<br><b>拓展知识：</b><br>【黄闪】持续闪烁的黄灯是闪光警告信号灯，提示路口交通信号暂时解除，车辆、行人通行时注意瞭望，确认安全后通过。<br>【黄灯】黄灯是一种过渡信号灯，起到警示作用，提示信号即将变换，已越过停止线的车可以继续通过；没有越过停止线的要停车等待。', '', '加速', 0002, 0.05);
INSERT INTO `question` VALUES (1733399773790183425, 316, '581c3', 0001, '交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第二十五条，全国实行统一的道路交通信号。交通信号包括<b>交通信号灯</b>、<b>交通标志</b>、<b>交通标线</b>和<b>交通警察的指挥</b>。', '', '包括', 0001, 0.02);
INSERT INTO `question` VALUES (1733399773832126466, 317, 'f4041', 0002, '这属于哪一种标志？ ', 'A、警告标志 B、指路标志 C、指示标志 D、禁令标志', '警告标志 ', '指路标志 ', '指示标志 ', '禁令标志', 'A', '红色是禁令标志，<b>黄色是警告标志</b>，蓝色是指示标志与普通路段的指路标志，绿色是高速公路的指路标志，棕色是旅游标志。', 'https://timg.mnks.cn/20230926fba94cc7.jpg', '', 0005, 0.33);
INSERT INTO `question` VALUES (1733399773874069506, 318, '74931', 0002, '这个标志是何含义？ ', 'A、T形交叉路口 B、Y形交叉路口 C、十字交叉路口 D、环形交叉路口', 'T形交叉路口 ', 'Y形交叉路口 ', '十字交叉路口 ', '环形交叉路口', 'C', '<b>十字交叉路口</b>：警告前方道路有<b>十字交叉路口</b>，应谨慎慢行，注意横向车辆。', 'https://timg.mnks.cn/20230926f25f7cf4.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399773924401154, 319, 'a4ab3', 0002, '这个标志是何含义？ ', 'A、T形交叉路口 B、Y形交叉路口 C、十字交叉路口 D、环形交叉路口', 'T形交叉路口 ', 'Y形交叉路口 ', '十字交叉路口 ', '环形交叉路口', 'A', '<b>T形交叉路口：</b>警告前方道路<b>有与上述图形相符的T形</b>交叉路口，应谨慎慢行，注意横向车辆。', 'https://timg.mnks.cn/20230926b47024a8.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399773962149890, 320, '25e59', 0002, '这个标志是何含义？ ', 'A、向左急转弯 B、向右急转弯 C、向右绕行 D、连续弯路', '向左急转弯 ', '向右急转弯 ', '向右绕行 ', '连续弯路', 'B', '【<b>一急</b>二反三连续】<b>一个弯表示急转弯</b>，两个弯表示反向弯路，三个弯表示连续弯路。\n<p><img src=\"https://timg.mnks.cn/20230926a0fe73ff.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b><b>向右</b>急弯</b>：警告前方道路<b>向右</b>急转弯，应减速慢行。</p>\n<p><img src=\"https://timg.mnks.cn/20230926db0f1fbf.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向左急弯：警告前方道路向左急转弯，应减速慢行。</p>', 'https://timg.mnks.cn/20230926a0fe73ff.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399774033453058, 321, '459b7', 0002, '这个标志是何含义？ ', 'A、向左急转弯 B、向右急转弯 C、向左绕行 D、连续弯路', '向左急转弯 ', '向右急转弯 ', '向左绕行 ', '连续弯路', 'A', '【<b>一急</b>二反三连续】<b>一个弯表示急转弯</b>，两个弯表示反向弯路，三个弯表示连续弯路。\n<p><img src=\"https://timg.mnks.cn/20230926db0f1fbf.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b><b>向左</b>急弯</b>：警告前方道路<b>向左</b>急转弯，应减速慢行。</p>\n<p><img src=\"https://timg.mnks.cn/20230926a0fe73ff.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向右急弯：警告前方道路向右急转弯，应减速慢行。</p>', 'https://timg.mnks.cn/20230926db0f1fbf.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399774083784705, 322, 'b4587', 0002, '这是什么交通标志？ ', 'A、易滑路段 B、急转弯路 C、反向弯路 D、连续弯路', '易滑路段 ', '急转弯路 ', '反向弯路 ', '连续弯路', 'C', '【一急<b>二反</b>三连续】一个弯表示急转弯，<b>两个弯表示反向弯路</b>，三个弯表示连续弯路。\n<p><img src=\"https://timg.mnks.cn/20230926c6daaf93.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>反向弯路</b>：警告车辆驾驶人前方有反向弯路。【二反】</p>\n<p><img src=\"https://timg.mnks.cn/20230926e182c44a.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>连续弯路：警告车辆驾驶人前方有连续弯路。【三连续】</p>\n<p><img src=\"https://timg.mnks.cn/20230926f496c9fb.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>易滑：提醒车辆驾驶人减速慢行，注意路滑。</p>', 'https://timg.mnks.cn/20230926c6daaf93.jpg', '', 0004, 0.25);
INSERT INTO `question` VALUES (1733399774125727746, 323, '51168', 0002, '这是什么交通标志？ ', 'A、易滑路段 B、急转弯路 C、反向弯路 D、连续弯路', '易滑路段 ', '急转弯路 ', '反向弯路 ', '连续弯路', 'D', '<p><img src=\"https://timg.mnks.cn/20230926e182c44a.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>连续弯路</b>：警告车辆驾驶人前方有<b>连续弯路</b>。【三连续】</p>\n<p><img src=\"https://timg.mnks.cn/20230926c6daaf93.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>反向弯路：警告车辆驾驶人前方有反向弯路。【二反】</p>\n<p><img src=\"https://timg.mnks.cn/20230926f496c9fb.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>易滑：提醒车辆驾驶人减速慢行，注意路滑。</p>', 'https://timg.mnks.cn/20230926e182c44a.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399774171865089, 324, '9ebe0', 0002, '这个标志是何含义？ ', 'A、堤坝路 B、上陡坡 C、连续上坡 D、下陡坡', '堤坝路 ', '上陡坡 ', '连续上坡 ', '下陡坡', 'B', '<p><img src=\"https://timg.mnks.cn/20230926a56527d8.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>上陡坡</b>：提醒前方有向上的陡坡路段，应小心驾驶。</p>\n<p><img src=\"https://timg.mnks.cn/20230926cf271a55.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>下陡坡：提醒前方有向下的陡坡路段，应小心驾驶。</p>\n<p><img src=\"https://timg.mnks.cn/20230926ed06537a.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>连续下坡：提醒车辆驾驶人前方为连续下坡 。</p>', 'https://timg.mnks.cn/20230926a56527d8.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399774213808129, 325, 'a9d04', 0002, '这个标志是何含义？ ', 'A、堤坝路 B、上陡坡 C、下陡坡 D、连续上坡', '堤坝路 ', '上陡坡 ', '下陡坡 ', '连续上坡', 'C', '<p><img src=\"https://timg.mnks.cn/20230926cf271a55.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>下陡坡</b>：提醒前方有向下的陡坡路段，应小心驾驶。</p>\n<p><img src=\"https://timg.mnks.cn/20230926a56527d8.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>上陡坡：提醒前方有向上的陡坡路段，应小心驾驶。</p>\n<p><img src=\"https://timg.mnks.cn/20230926ed06537a.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>连续下坡：提醒车辆驾驶人前方为连续下坡 。</p>', 'https://timg.mnks.cn/20230926cf271a55.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399774251556866, 326, '7fcbd', 0002, '这个标志是何含义？ ', 'A、连续上坡 B、上陡坡 C、下陡坡 D、连续下坡', '连续上坡 ', '上陡坡 ', '下陡坡 ', '连续下坡', 'D', '<p><img src=\"https://timg.mnks.cn/20230926ed06537a.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>连续下坡</b>：提醒车辆驾驶人前方为<b>连续下坡</b> 。</p>\n<p><img src=\"https://timg.mnks.cn/20230926cf271a55.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>下陡坡：提醒前方有向下的陡坡路段，应小心驾驶。</p>\n<p><img src=\"https://timg.mnks.cn/20230926a56527d8.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>上陡坡：提醒前方有向上的陡坡路段，应小心驾驶。</p>', 'https://timg.mnks.cn/20230926ed06537a.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399774310277122, 327, 'fc419', 0002, '这是什么交通标志？ ', 'A、两侧变窄 B、右侧变窄 C、左侧变窄 D、桥面变窄', '两侧变窄 ', '右侧变窄 ', '左侧变窄 ', '桥面变窄', 'A', '<p>窄路：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让 。<br><img src=\"https://timg.mnks.cn/20230926ace5a4de.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>两侧变窄</b></p>\n<p><img src=\"https://timg.mnks.cn/20230926c46b6820.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右侧变窄</p>\n<p><img src=\"https://timg.mnks.cn/20230926e90b446c.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左侧变窄</p>', 'https://timg.mnks.cn/20230926ace5a4de.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399774352220162, 328, '8ec58', 0002, '这是什么交通标志？ ', 'A、两侧变窄 B、右侧变窄 C、左侧变窄 D、桥面变窄', '两侧变窄 ', '右侧变窄 ', '左侧变窄 ', '桥面变窄', 'B', '<p>窄路：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让 。<br></p>\n<p><img src=\"https://timg.mnks.cn/20230926c46b6820.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>右侧变窄</b></p>\n<p><img src=\"https://timg.mnks.cn/20230926ace5a4de.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>两侧变窄</p>\n<p><img src=\"https://timg.mnks.cn/20230926e90b446c.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左侧变窄</p>', 'https://timg.mnks.cn/20230926c46b6820.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399774410940417, 329, 'e8ea0', 0002, '这是什么交通标志？ ', 'A、两侧变窄 B、右侧变窄 C、左侧变窄 D、桥面变窄', '两侧变窄 ', '右侧变窄 ', '左侧变窄 ', '桥面变窄', 'C', '<p>窄路：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让 。<br></p>\n<p><img src=\"https://timg.mnks.cn/20230926e90b446c.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>左侧变窄</b></p>\n<p><img src=\"https://timg.mnks.cn/20230926c46b6820.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右侧变窄</p>\n<p><img src=\"https://timg.mnks.cn/20230926ace5a4de.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>两侧变窄</p>', 'https://timg.mnks.cn/20230926e90b446c.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399774448689153, 330, '2b963', 0002, '这个标志是何含义？ ', 'A、窄路 B、右侧变窄 C、左侧变窄 D、窄桥', '窄路 ', '右侧变窄 ', '左侧变窄 ', '窄桥', 'D', '<b>窄桥</b>：用以警告车辆驾驶人注意前方桥面宽度变窄，应谨慎驾驶。设在桥面净宽较两端路面宽度变窄，且桥的净宽小于6m的桥梁以前适当位置。', 'https://timg.mnks.cn/20230926c0ef5c16.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399774494826497, 331, '75b55', 0002, '这个标志是何含义？ ', 'A、双向交通 B、分离式道路 C、潮汐车道 D、减速让行', '双向交通 ', '分离式道路 ', '潮汐车道 ', '减速让行', 'A', '<p><img src=\"https://tp.mnks.cn/tp_134.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>双向交通</b>：提醒前方是不分离双向行驶路段，应注意会车。</p>\n<p><img src=\"https://tp.mnks.cn/tp_178.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>会车让行：车辆会车时，必须停车让对向车先行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_219.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>会车先行：前方路段车辆在会车时享有优先通行权利。</p>', 'https://timg.mnks.cn/20230926ae1171f2.jpg', '', 0004, 0.21);
INSERT INTO `question` VALUES (1733399774540963842, 332, '5cbe8', 0002, '这个标志是何含义？ ', 'A、人行横道 B、注意行人 C、注意儿童 D、学校区域', '人行横道 ', '注意行人 ', '注意儿童 ', '学校区域', 'B', '<p><img src=\"https://tp.mnks.cn/tp_135.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>注意行人：</b>用以警告车辆驾驶人减速慢行，注意行人。</p>\n<p><img src=\"https://timg.mnks.cn/20230926a514b8f4.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>注意行人：</b>用以警告车辆驾驶人减速慢行，注意行人。</p>\n<p><img src=\"https://tp.mnks.cn/tp_216.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>人行横道：表示该处为人行横道，机动车驾驶人应注意观察行人，遇行人已进入人行横道时应停车让行人通过。</p>', 'https://timg.mnks.cn/20230926fa148a80.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399774587101186, 333, '33b74', 0002, '这个标志是何含义？ ', 'A、注意行人 B、人行横道 C、注意儿童 D、学校区域', '注意行人 ', '人行横道 ', '注意儿童 ', '学校区域', 'C', '<p><img src=\"https://tp.mnks.cn/tp_136.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>注意儿童：</b>警告前方是儿童频繁出入的地点，应减速慢行，注意儿童。</p>\n<p><img src=\"https://tp.mnks.cn/tp_135.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意行人：警告前方道路设有人行横道线，应减速慢行，注意行人。</p>', 'https://timg.mnks.cn/20230926ae01c63a.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399774775844865, 334, 'c2245', 0002, '这个标志是何含义？ ', 'A、大型畜牧场 B、野生动物保护区 C、注意野生动物 D、注意牲畜', '大型畜牧场 ', '野生动物保护区 ', '注意野生动物 ', '注意牲畜', 'D', '<p><img src=\"https://tp.mnks.cn/tp_137.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br><b>注意牲畜</b>：提醒前方路段经常有牲畜横穿或出入，应注意慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_138.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>注意野生动物：提醒前方路段经常有野生动物横穿或出入，应注意慢行。</p>', 'https://timg.mnks.cn/20230926f42afc68.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399774817787905, 335, '5a465', 0002, '这个标志是何含义？ ', 'A、注意野生动物 B、注意牲畜 C、动物公园 D、开放的牧区', '注意野生动物 ', '注意牲畜 ', '动物公园 ', '开放的牧区', 'A', '<p><img src=\"https://tp.mnks.cn/tp_138.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br><b>注意野生动物：提醒前方路段经常有野生动物横穿或出入，应注意慢行。</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_137.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>注意牲畜：提醒前方路段经常有牲畜横穿或出入，应注意慢行。</p>', 'https://timg.mnks.cn/20230926f11b3a3b.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399774859730946, 336, 'bbe12', 0002, '这个标志是何含义？ ', 'A、交叉路口 B、注意信号灯 C、注意行人 D、人行横道灯', '交叉路口 ', '注意信号灯 ', '注意行人 ', '人行横道灯', 'B', '<b>注意信号灯</b>：警告前方路段设有信号灯，应按信号灯指示行车。', 'https://timg.mnks.cn/20230926b6220de8.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399774910062594, 337, '8ab8a', 0002, '这个标志是何含义？ ', 'A、傍山险路 B、悬崖路段 C、注意落石 D、危险路段', '傍山险路 ', '悬崖路段 ', '注意落石 ', '危险路段', 'C', '<p><img src=\"https://tp.mnks.cn/tp_140.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>注意落石</b>：提醒前方是有落石危险的傍山路段，应<b>注意落石</b>。<br><img src=\"https://tp.mnks.cn/tp_143.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>傍山险路：提醒前方是傍山险路，应小心驾驶。</p>', 'https://timg.mnks.cn/20230926d76254d9.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399774956199937, 338, '5ca2e', 0001, '交通标志和交通标线不属于交通信号。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第二十五条，全国实行统一的道路交通信号。交通信号包括<b>交通信号灯</b>、<b>交通标志</b>、<b>交通标线</b>和<b>交通警察的指挥</b>。', '', '不属于', 0003, 0.13);
INSERT INTO `question` VALUES (1733399775035891714, 339, 'aaaad', 0002, '这个标志是何含义？ ', 'A、注意横风 B、风向标 C、隧道入口 D、气象台', '注意横风 ', '风向标 ', '隧道入口 ', '气象台', 'A', '<b>注意横风</b>标志：用以提醒车辆驾驶人谨慎驾驶，注意横风。设在经常有很强的侧向风的路段前适当位置。', 'https://timg.mnks.cn/20230926adaba52d.jpg', '', 0002, 0.08);
INSERT INTO `question` VALUES (1733399775119777794, 340, 'bc28e', 0001, '交通信号灯由红灯、绿灯和黄灯组成。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第二十六条，交通信号灯由<b>红灯、绿灯、黄灯</b>组成。红灯表示禁止通行，绿灯表示准许通行，黄灯表示警示。', '', '交通信号灯', 0001, 0.02);
INSERT INTO `question` VALUES (1733399775161720834, 341, 'a1d89', 0002, '这个标志是何含义？ ', 'A、急转弯路 B、易滑路段 C、试车路段 D、曲线路段', '急转弯路 ', '易滑路段 ', '试车路段 ', '曲线路段', 'B', '<p><img src=\"https://tp.mnks.cn/tp_142.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>易滑路段：</b>警告前方路面较滑，易发生事故，应减速慢行。</p>\n<p><img src=\"https://timg.mnks.cn/20230926c6daaf93.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>反向弯路：警告车辆驾驶人前方有反向弯路。</p>\n<p><img src=\"https://timg.mnks.cn/20230926e182c44a.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>连续弯路：警告车辆驾驶人前方有连续弯路。</p>\n<p></p>', 'https://timg.mnks.cn/20230926f496c9fb.jpg', '', 0003, 0.08);
INSERT INTO `question` VALUES (1733399775199469570, 342, 'df99e', 0001, '道路交通标线分为指示标线、警告标线、禁止标线。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第三十条 交通标志分为：指示标志、警告标志、禁令标志、指路标志、旅游区标志、道路施工安全标志和辅助标志。<b> 道路交通标线分为：指示标线、警告标线、禁止标线。</b>', '', '交通标线', 0001, 0.02);
INSERT INTO `question` VALUES (1733399775253995522, 343, '6d1df', 0002, '这个标志是何含义？ ', 'A、临崖路 B、堤坝路 C、傍山险路 D、落石路', '临崖路 ', '堤坝路 ', '傍山险路 ', '落石路', 'C', '<p><img src=\"https://tp.mnks.cn/tp_143.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>傍山险路：</b>提醒前方是傍山险路，应小心驾驶。<br><img src=\"https://tp.mnks.cn/tp_140.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意落石：提醒前方是有落石危险的傍山路段，应注意落石。</p>', 'https://timg.mnks.cn/20230926a8f45d90.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399775308521473, 344, '58802', 0002, '这个标志是何含义？ ', 'A、堤坝路 B、临崖路 C、易滑路 D、傍水路', '堤坝路 ', '临崖路 ', '易滑路 ', '傍水路', 'A', '<p><img src=\"https://tp.mnks.cn/tp_144.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>堤坝路：</b>提醒前方是沿水库、湖泊、河流等堤坝道路，应小心驾驶。<br><img src=\"https://tp.mnks.cn/tp_142.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>易滑路段：警告前方路面较滑，易发生事故，应减速慢行。</p>', 'https://timg.mnks.cn/20230926ac9cfbc6.jpg', '', 0005, 0.29);
INSERT INTO `question` VALUES (1733399775350464514, 345, '91ce6', 0002, '这个标志是何含义？ ', 'A、注意行人 B、有人行横道 C、村庄或集镇 D、有小学校', '注意行人 ', '有人行横道 ', '村庄或集镇 ', '有小学校', 'C', '<b>村庄：</b>提醒前方是紧靠村庄、集镇且视线不良的路段，应小心驾驶。', 'https://timg.mnks.cn/20230926e23a733d.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399775476293633, 346, '42c91', 0002, '这个标志是何含义？ ', 'A、涵洞 B、水渠 C、桥梁 D、隧道', '涵洞 ', '水渠 ', '桥梁 ', '隧道', 'D', '<p><img src=\"https://tp.mnks.cn/tp_146.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>隧道：</b>提醒前方是双向行驶并且照明不好的隧道，应注意慢行。<br><img src=\"https://tp.mnks.cn/tp_169.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>隧道开车灯：警告前方是无照明或照明不足的隧道，应打开前照灯，注意行驶。</p>', 'https://timg.mnks.cn/20230926cae875e0.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399775543402498, 347, '4bdfc', 0002, '这个标志是何含义？ ', 'A、过水路面 B、漫水桥 C、渡口 D、船用码头', '过水路面 ', '漫水桥 ', '渡口 ', '船用码头', 'C', '<p><img src=\"https://tp.mnks.cn/tp_147.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>渡口：</b>提醒前方是车辆渡口，应谨慎驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_152.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>过水路面：提醒前方是过水路面或漫水桥路段，应停车察明水情，确认安全后，低速通过。</p>', 'https://timg.mnks.cn/20230926c1fa38ed.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399775589539841, 348, 'd8fda', 0002, '这个标志是何含义？ ', 'A、不平路面 B、驼峰桥 C、路面高突 D、路面低洼', '不平路面 ', '驼峰桥 ', '路面高突 ', '路面低洼', 'B', '<p></p>\n<p><img src=\"https://tp.mnks.cn/tp_148.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>驼峰桥：提醒前方是拱度很大、影响视距的驼峰桥，应谨慎驾驶。</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_149.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>路面不平：提醒前方路面颠簸或有桥头跳车比较严重，应减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_150.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>减速丘：用以提醒车辆驾驶人减速，注意前方路段设有减速丘 。</p>\n<p><img src=\"https://tp.mnks.cn/tp_151.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>路面低洼：提醒前方路面比正常路面突然低洼，应减速慢行。</p>', 'https://timg.mnks.cn/20230926a177c323.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399775627288578, 349, 'dab6d', 0002, '这个标志是何含义？ ', 'A、路面低洼 B、驼峰桥 C、路面不平 D、路面高突', '路面低洼 ', '驼峰桥 ', '路面不平 ', '路面高突', 'C', '<p><img src=\"https://tp.mnks.cn/tp_149.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>路面不平：</b>提醒前方路面颠簸或有桥头跳车比较严重，应减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_151.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>路面低洼：提醒前方路面比正常路面突然低洼，应减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_148.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>驼峰桥：提醒前方是拱度很大、影响视距的驼峰桥，应谨慎驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_150.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>减速丘：用以提醒车辆驾驶人减速，注意前方路段设有减速丘。</p>', 'https://timg.mnks.cn/20230926d3c83512.jpg', '', 0005, 0.34);
INSERT INTO `question` VALUES (1733399775665037313, 350, 'a9f84', 0002, '这个标志是何含义？ ', 'A、路面低洼 B、驼峰桥 C、路面不平 D、减速丘', '路面低洼 ', '驼峰桥 ', '路面不平 ', '减速丘', 'D', '<p></p>\n<p><img src=\"https://tp.mnks.cn/tp_150.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>减速丘：用以提醒车辆驾驶人减速，注意前方路段设有减速丘 。</b>（2022新版道路交通标志国家标准修改了路面高突标志的名称 , 改为减速丘标志）</p>\n<p><img src=\"https://tp.mnks.cn/tp_149.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>路面不平：提醒前方路面颠簸或有桥头跳车比较严重，应减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_148.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>驼峰桥：提醒前方是拱度很大、影响视距的驼峰桥，应谨慎驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_151.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>路面低洼：提醒前方路面比正常路面突然低洼，应减速慢行。</p>', 'https://timg.mnks.cn/20230927b60e0ea4.jpg', '', 0005, 0.37);
INSERT INTO `question` VALUES (1733399775719563266, 351, '2428a', 0002, '这个标志是何含义？ ', 'A、路面高突 B、有驼峰桥 C、路面不平 D、路面低洼', '路面高突 ', '有驼峰桥 ', '路面不平 ', '路面低洼', 'D', '<p><img src=\"https://tp.mnks.cn/tp_151.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>路面低洼：</b>提醒前方路面比正常路面突然低洼，应减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_149.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>路面不平：提醒前方路面颠簸或有桥头跳车比较严重，应减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_148.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>驼峰桥：提醒前方是拱度很大、影响视距的驼峰桥，应谨慎驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_150.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>减速丘：用以提醒车辆驾驶人减速，注意前方路段设有减速丘。</p>', 'https://timg.mnks.cn/20230927eccc224b.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399775757312002, 352, '75d2f', 0002, '这个标志是何含义？ ', 'A、过水路面 B、渡口 C、泥泞道路 D、低洼路面', '过水路面 ', '渡口 ', '泥泞道路 ', '低洼路面', 'A', '<p><img src=\"https://tp.mnks.cn/tp_152.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>过水路面（漫水桥）</b>：提醒前方是过水路面或漫水桥路段，应停车察明水情，确认安全后，低速通过。</p>\n<p><img src=\"https://tp.mnks.cn/tp_147.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>渡口：提醒前方是车辆渡口，应谨慎驾驶。</p>', 'https://timg.mnks.cn/20230927ff652292.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399775795060738, 353, 'a12d5', 0002, '这个标志是何含义？ ', 'A、左侧绕行 B、单向通行 C、注意危险 D、右侧绕行', '左侧绕行 ', '单向通行 ', '注意危险 ', '右侧绕行', 'A', '<b>左侧绕行</b>：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，<b>左侧绕行</b>通过。<br>\n<p><img src=\"https://tp.mnks.cn/tp_163.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左右绕行</p>\n<p><img src=\"https://tp.mnks.cn/tp_164.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>左侧绕行</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_165.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右侧绕行</p>', 'https://timg.mnks.cn/20230927a7a3be4a.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399775837003777, 354, '119b7', 0002, '这个标志是何含义？ ', 'A、单向通行 B、右侧绕行 C、注意危险 D、左侧绕行', '单向通行 ', '右侧绕行 ', '注意危险 ', '左侧绕行', 'B', '<b>右侧绕行</b>：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，<b>右侧绕行</b>通过。<br>\n<p><img src=\"https://tp.mnks.cn/tp_163.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左右绕行</p>\n<p><img src=\"https://tp.mnks.cn/tp_164.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左侧绕行</p>\n<p><img src=\"https://tp.mnks.cn/tp_165.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>右侧绕行</b></p>', 'https://timg.mnks.cn/20230927f74ef983.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399775887335426, 355, 'ac3ab', 0002, '这个标志是何含义？ ', 'A、施工路段绕行 B、双向交通 C、注意危险 D、左右绕行', '施工路段绕行 ', '双向交通 ', '注意危险 ', '左右绕行', 'D', '注意障碍物：：告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。\n<p><img src=\"https://tp.mnks.cn/tp_163.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意障碍物-<b>左右绕行</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_164.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意障碍物-左侧绕行</p>\n<p><img src=\"https://tp.mnks.cn/tp_165.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意障碍物-右侧绕行</p>', 'https://timg.mnks.cn/20230927d944edaf.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399775933472770, 356, '7aab8', 0002, '这个标志是何含义？ ', 'A、施工路段 B、车多路段 C、慢行 D、拥堵路段', '施工路段 ', '车多路段 ', '慢行 ', '拥堵路段', 'C', '<b>慢行：</b>提醒前方道路发生特殊情况，影响行车安全，需减速慢行。', 'https://timg.mnks.cn/20230927f9f92e14.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399775979610114, 357, 'ea07d', 0002, '这个标志是何含义？ ', 'A、施工路段 B、事故易发路段 C、减速慢行路段 D、拥堵路段', '施工路段 ', '事故易发路段 ', '减速慢行路段 ', '拥堵路段', 'B', '<p><img src=\"https://tp.mnks.cn/tp_161.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>事故易发路段：</b>告示前方为交通事故易发路段，应谨慎驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_167.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>施工：告示前方道路施工，应减速慢行或绕道行驶。</p>', 'https://timg.mnks.cn/20230927b51cc4a1.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399776017358849, 358, 'e4804', 0002, '这个标志是何含义？ ', 'A、无人看守铁路道口 B、有人看守铁路道口 C、多股铁路与道路相交 D、立交式的铁路道口', '无人看守铁路道口 ', '有人看守铁路道口 ', '多股铁路与道路相交 ', '立交式的铁路道口', 'B', '<p><img src=\"https://tp.mnks.cn/tp_153.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>有人看守铁路道口</b>：警告前方是有人看守的铁路道口，应减速或停车观察，按照交通信号或管理人员的指挥通行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_154.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>无人看守铁路道口：警告前方是无人看守的铁路道口，应按照交通信号指示通行，无交通信号时应减速或停车观察，确认安全后通过。</p>', 'https://timg.mnks.cn/20230927fc9f6b36.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399776071884801, 359, 'af442', 0002, '这个标志是何含义？ ', 'A、多股铁路与道路相交 B、有人看守铁路道口 C、无人看守铁路道口 D、注意长时鸣喇叭', '多股铁路与道路相交 ', '有人看守铁路道口 ', '无人看守铁路道口 ', '注意长时鸣喇叭', 'C', '<p><img src=\"https://tp.mnks.cn/tp_154.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>无人看守铁路道口</b>：警告前方是无人看守的铁路道口，应按照交通信号指示通行，无交通信号时应减速或停车观察，确认安全后通过。</p>\n<p><img src=\"https://tp.mnks.cn/tp_153.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>有人看守铁路道口：警告前方是有人看守的铁路道口，应减速或停车观察，按照交通信号或管理人员的指挥通行。</p>', 'https://timg.mnks.cn/20230927becf00c3.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399776143187970, 360, '4c111', 0002, '这个标志是何含义？ ', 'A、注意避让火车 B、有人看守铁路道口 C、无人看守铁路道口 D、多股铁路与道路相交', '注意避让火车 ', '有人看守铁路道口 ', '无人看守铁路道口 ', '多股铁路与道路相交', 'D', '<b>叉形符号</b>：表示多股铁路与道路相交，设在铁路道口标志上方。', 'https://timg.mnks.cn/20230927cb96ce9d.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399776214491138, 361, 'f3862', 0002, '这个标志是何含义？ ', 'A、距无人看守铁路道口50米 B、距有人看守铁路道口50米 C、距无人看守铁路道口100米 D、距有人看守铁路道口100米', '距无人看守铁路道口50米 ', '距有人看守铁路道口50米 ', '距无人看守铁路道口100米 ', '距有人看守铁路道口100米', 'A', '<p>【<b>斜杠符号</b>】设在“<b>无人看守铁路道口</b>”标志下方的红色斜杠，表示距<b>无人看守铁路道口</b>的距离，一条斜杠代表50米。因此，图示标志一条红色斜杠，表示距<b>无人看守铁路道口</b>50米。<br><img src=\"https://tp.mnks.cn/tp_156.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>距无人看守铁路道口50米</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_157.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>距无人看守铁路道口100米</p>\n<p><img src=\"https://tp.mnks.cn/tp_158.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>距无人看守铁路道口150米</p>', 'https://timg.mnks.cn/20230927f5925da9.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399776260628481, 362, '47008', 0002, '这个标志是何含义？ ', 'A、距无人看守铁路道口50米 B、距有人看守铁路道口50米 C、距无人看守铁路道口100米 D、距有人看守铁路道口100米', '距无人看守铁路道口50米 ', '距有人看守铁路道口50米 ', '距无人看守铁路道口100米 ', '距有人看守铁路道口100米', 'C', '<p>【斜杠符号】设在“<b>无人看守铁路道口</b>”标志下方的红色斜杠，表示距<b>无人看守铁路道口</b>的距离，<b>一条斜杠代表50米</b>。因此，图示标志<b>两条</b>红色斜杠，表示距<b>无人看守铁路道口</b><b>100米</b>。<br><img src=\"https://tp.mnks.cn/tp_156.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>距无人看守铁路道口50米</p>\n<p><img src=\"https://tp.mnks.cn/tp_157.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>距无人看守铁路道口100米</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_158.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>距无人看守铁路道口150米</p>', 'https://timg.mnks.cn/20230927b3b52124.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399776310960130, 363, '79c95', 0002, '这个标志是何含义？ ', 'A、距有人看守铁路道口150米 B、距无人看守铁路道口150米 C、距无人看守铁路道口100米 D、距有人看守铁路道口100米', '距有人看守铁路道口150米 ', '距无人看守铁路道口150米 ', '距无人看守铁路道口100米 ', '距有人看守铁路道口100米', 'B', '<p>【斜杠符号】设在“<b>无人看守铁路道口</b>”标志下方的红色斜杠，表示距<b>无人看守铁路道口</b>的距离，<b>一条斜杠代表50米</b>。因此，图示标志<b>三条</b>红色斜杠，表示距<b>无人看守铁路道口</b><b>150米</b>。<br><img src=\"https://tp.mnks.cn/tp_156.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>距无人看守铁路道口50米</p>\n<p><img src=\"https://tp.mnks.cn/tp_157.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>距无人看守铁路道口100米</p>\n<p><img src=\"https://tp.mnks.cn/tp_158.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>距无人看守铁路道口150米</b></p>', 'https://timg.mnks.cn/20230927dc3785b8.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399776361291777, 364, 'fa2c1', 0002, '这个标志是何含义？ ', 'A、避让非机动车 B、非机动车道 C、禁止非机动车通行 D、注意非机动车', '避让非机动车 ', '非机动车道 ', '禁止非机动车通行 ', '注意非机动车', 'D', '黄底黑边三角形，答案优先选注意。<br>\n<p><img src=\"https://tp.mnks.cn/tp_159.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>注意非机动车</b>：提醒前方道路经常有非机动车横穿或出入，应注意慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_231.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>非机动车行驶：前方道路只供非机动车行驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_232.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>非机动车道：所指示车道只供非机动车行驶。</p>', 'https://timg.mnks.cn/20230927a88de573.jpg', '', 0004, 0.21);
INSERT INTO `question` VALUES (1733399776407429122, 365, 'b6987', 0002, '这个标志是何含义？ ', 'A、注意残疾人 B、残疾人出入口 C、残疾人休息处 D、残疾人专用通道', '注意残疾人 ', '残疾人出入口 ', '残疾人休息处 ', '残疾人专用通道', 'A', '<b>注意残疾人：</b>提醒前方是残疾人经常出入地点，应减速慢行，注意残疾人。', 'https://timg.mnks.cn/20230926b7f3a2ae.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399776449372162, 366, 'e2349', 0002, '这个标志是何含义？ ', 'A、事故多发路段 B、减速慢行 C、注意危险 D、拥堵路段', '事故多发路段 ', '减速慢行 ', '注意危险 ', '拥堵路段', 'C', '<p><img src=\"https://tp.mnks.cn/tp_166.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>注意危险：</b>提醒前方是其它警告标志不能包括的危险路段，应谨慎慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_161.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>事故易发路段：告示前方为交通事故易发路段，应谨慎驾驶。</p>', 'https://timg.mnks.cn/20230927c9d1f061.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399776491315202, 367, 'c8baa', 0002, '这个标志是何含义？ ', 'A、塌方路段 B、施工路段 C、前方工厂 D、道路堵塞', '塌方路段 ', '施工路段 ', '前方工厂 ', '道路堵塞', 'B', '<b>施工：</b>告示前方道路施工，车辆应减速慢行或绕道行驶。', 'https://timg.mnks.cn/20230927bc877c89.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399776533258241, 368, '91136', 0002, '这个标志是何含义？ ', 'A、建议速度 B、最低速度 C、最高速度 D、限制速度', '建议速度 ', '最低速度 ', '最高速度 ', '限制速度', 'A', '<p><img src=\"https://sucimg.itc.cn/sblog/od5hLEmgBfW\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>建议速度</b>：提醒车辆驾驶人<b>以建议的速度行驶</b>。<br><img src=\"https://tp.mnks.cn/tp_195.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>限制速度：本标志至前方解除限制速度标志或另一块不同限速值的限制速度标志的路段内，机动车行驶速度不准超过标志所示数值。<br><img src=\"https://tp.mnks.cn/tp_217.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>最低限速：前方道路的最低时速限制。</p>', 'https://timg.mnks.cn/20230927bc1c6486.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399776566812674, 369, 'a78fb', 0002, '这个标志是何含义？ ', 'A、隧道开远光灯 B、隧道减速 C、隧道开灯 D、隧道开示宽灯', '隧道开远光灯 ', '隧道减速 ', '隧道开灯 ', '隧道开示宽灯', 'C', '<p><img src=\"https://tp.mnks.cn/tp_169.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>隧道开车灯</b>：警告前方是无照明或照明不足的隧道，应打开前照灯，注意行驶。<br><img src=\"https://tp.mnks.cn/tp_146.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>隧道：提醒前方是双向行驶并且照明不好的隧道，应注意慢行。</p>', 'https://timg.mnks.cn/20230927d9db0e5e.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399776608755713, 370, 'e7cfa', 0002, '这个标志是何含义？ ', 'A、注意双向行驶 B、靠两侧行驶 C、可变车道 D、注意潮汐车道', '注意双向行驶 ', '靠两侧行驶 ', '可变车道 ', '注意潮汐车道', 'D', '<b>注意潮汐车道：</b>警告前方为潮汐车道，即前方车道上车辆行驶方向可随交通管理需要变化。', 'https://timg.mnks.cn/20230927edc8256f.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399776650698753, 371, '9afe2', 0002, '这个标志是何含义？ ', 'A、注意保持车距 B、车距确认路段 C、车速测试路段 D、两侧变窄路段', '注意保持车距 ', '车距确认路段 ', '车速测试路段 ', '两侧变窄路段', 'A', '<b>注意保持车距：</b>警告前方路段经常发生车辆追尾事故，应注意和前车保持安全距离。', 'https://timg.mnks.cn/20230927c4786fcc.jpg', '', 0004, 0.26);
INSERT INTO `question` VALUES (1733399776692641794, 372, 'eb784', 0002, '这个标志是何含义？ ', 'A、注意交互式道路 B、注意分离式道路 C、平面交叉路口 D、环形平面交叉', '注意交互式道路 ', '注意分离式道路 ', '平面交叉路口 ', '环形平面交叉', 'B', '如图所示，警告标志【<b>注意分离式道路</b>】：十字平面交叉，警告前方平面交叉的被交道路是分离式道路，即左、右行车道分开修建，需按标志指示方向行车。', 'https://timg.mnks.cn/20230927c16fb60f.jpg', '', 0005, 0.34);
INSERT INTO `question` VALUES (1733399776734584834, 373, '8db3c', 0002, '这个标志是何含义？ ', 'A、Y形交叉口 B、主路让行 C、注意分流 D、注意合流', 'Y形交叉口 ', '主路让行 ', '注意分流 ', '注意合流', 'D', '<b>注意合流</b>：用以警告车辆驾驶人注意前方<b>有车辆汇合</b>。', 'https://timg.mnks.cn/20230927a64f21de.jpg', '', 0004, 0.23);
INSERT INTO `question` VALUES (1733399776797499393, 374, 'e2995', 0002, '这个标志是什么意思？ ', 'A、前方路口不能右转 B、前方路口不能直行 C、前方是十字路口 D、前方路口不能左转', '前方路口不能右转 ', '前方路口不能直行 ', '前方是十字路口 ', '前方路口不能左转', 'A', '<b>T形交叉路口</b>，警告前方道路有与上述图形相符的<b>T形交叉路口</b>，应谨慎慢行。如图所示的T形交叉路，表示前方路口可以直行和左转弯行驶，<b>不能右转</b>。', 'https://timg.mnks.cn/20230926fba94cc7.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399776852025346, 375, '9e1d1', 0002, '这个标志是何含义？ ', 'A、向左变道 B、车道数变少 C、合流处 D、应急车道', '向左变道 ', '车道数变少 ', '合流处 ', '应急车道', 'B', '<p><img src=\"https://timg.mnks.cn/20230926f92473d3.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>车道数变少</b>：用以提醒车辆驾驶人注意前方车道数量变少。</p>\n<p></p>', 'https://timg.mnks.cn/20230926f92473d3.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399776902356994, 376, '94ee1', 0002, '这个标志是何含义？ ', 'A、提醒车辆驾驶人前方道路沿水库、湖泊、河流 B、提醒车辆驾驶人前方有向上的陡坡路段 C、提醒车辆驾驶人前方有两个及以上的连续上坡路段 D、提醒车辆驾驶人前方有向下的陡坡路段', '提醒车辆驾驶人前方道路沿水库', '湖泊', '河流 B', '提醒车辆驾驶人前方有向上的陡坡路段 C', 'B', '<p><img src=\"https://tp.mnks.cn/tp_127.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>上陡坡</b>：提醒前方有向上的陡坡路段，应小心驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_128.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>下陡坡：提醒前方有向下的陡坡路段，应小心驾驶。</p>', 'https://timg.mnks.cn/20230926a56527d8.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399776944300034, 377, '931bd', 0002, '这个标志是何含义？ ', 'A、提醒车辆驾驶人前方道路沿水库、湖泊、河流 B、提醒车辆驾驶人前方有向上的陡坡路段 C、提醒车辆驾驶人前方有向下的陡坡路段 D、提醒车辆驾驶人前方有两个及以上的连续上坡路段', '提醒车辆驾驶人前方道路沿水库', '湖泊', '河流 B', '提醒车辆驾驶人前方有向上的陡坡路段 C', 'C', '<p><img src=\"https://tp.mnks.cn/tp_128.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br><b>下陡坡：提醒前方有向下的陡坡路段，应小心驾驶。</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_127.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>上陡坡：提醒前方有向上的陡坡路段，应小心驾驶。</p>', 'https://timg.mnks.cn/20230926cf271a55.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399776982048769, 378, '4b642', 0002, '这个标志是何含义？ ', 'A、提醒车辆驾驶人前方有两个及以上的连续上坡路段 B、提醒车辆驾驶人前方有向上的陡坡路段 C、提醒车辆驾驶人前方有向下的陡坡路段 D、提醒车辆驾驶人前方有两个及以上的连续下坡路段', '提醒车辆驾驶人前方有两个及以上的连续上坡路段 ', '提醒车辆驾驶人前方有向上的陡坡路段 ', '提醒车辆驾驶人前方有向下的陡坡路段 ', '提醒车辆驾驶人前方有两个及以上的连续下坡路段', 'D', '<b>连续下坡</b>：提醒前方有两个及以上的<b>连续下坡</b>路段，应小心驾驶。', 'https://timg.mnks.cn/20230926ed06537a.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399777028186113, 379, '5c525', 0002, '这个标志是何含义？ ', 'A、避险车道 B、应急车道 C、路肩 D、急弯道', '避险车道 ', '应急车道 ', '路肩 ', '急弯道', 'A', '<b>避险车道：</b>提醒前方道路设置了避险车道，即供速度失控车辆驶离原车道，进行安全减速的专用车道。', 'https://timg.mnks.cn/20230927c9045672.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399777065934850, 380, 'cd5b8', 0002, '这属于哪一种标志？ ', 'A、警告标志 B、禁令标志 C、指示标志 D、指路标志', '警告标志 ', '禁令标志 ', '指示标志 ', '指路标志', 'B', '黄色-警示、<b>红色-禁令</b>、蓝色-指示指路，黑色-辅助。<b>禁令标志</b>的颜色为白底、红圈、红杠、黑图形；图形压杠；形状为圆形。个别标志例外。', 'https://timg.mnks.cn/20230926e0945470.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399777107877889, 381, '459a5', 0002, '这个标志是何含义？ ', 'A、不准车辆驶入 B、不准长时间停车 C、停车让行 D、不准临时停车', '不准车辆驶入 ', '不准长时间停车 ', '停车让行 ', '不准临时停车', 'C', '<p><img src=\"https://tp.mnks.cn/tp_176.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>停车让行</b>：表示车辆必须在进入路口前完全停止，确认安全后，方可通行。标志形状为正八边形，颜色为红底白字。</p>\n<p><img src=\"https://tp.mnks.cn/tp_177.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>减速让行：表示相交道路有优先通行权，车辆应慢行或停车，观察相交道路行车情况，让相交道路车辆优先通行和确认安全时，方可通行。标志形状为倒等边三角形，颜色为白底，红边，黑字。</p>', 'https://timg.mnks.cn/20230927afe7b54d.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399777145626625, 382, 'dcf68', 0002, '这个标志是何含义？ ', 'A、不准让行 B、会车让行 C、停车让行 D、减速让行', '不准让行 ', '会车让行 ', '停车让行 ', '减速让行', 'D', '<p><img src=\"https://tp.mnks.cn/tp_177.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>减速让行</b>：表示相交道路有优先通行权，车辆应慢行或停车，观察相交道路行车情况，让相交道路车辆优先通行和确认安全时，方可通行。标志形状为倒等边三角形，颜色为白底，红边，黑字。</p>\n<p><img src=\"https://tp.mnks.cn/tp_176.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>停车让行：表示车辆必须在进入路口前完全停止，确认安全后，方可通行。标志形状为正八边形，颜色为红底白字。</p>', 'https://timg.mnks.cn/20230927fce0a0c4.jpg', '', 0004, 0.26);
INSERT INTO `question` VALUES (1733399777195958274, 383, '0079e', 0002, '这个标志是何含义？ ', 'A、会车时停车让对方车先行 B、前方是双向通行路段 C、右侧道路禁止车通行 D、会车时停车让右侧车先行', '会车时停车让对方车先行 ', '前方是双向通行路段 ', '右侧道路禁止车通行 ', '会车时停车让右侧车先行', 'A', '<p><img src=\"https://tp.mnks.cn/tp_178.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br><b>会车让行</b>：车辆会车时，必须停车让对向车先行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_219.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>会车先行</p>\n<p><img src=\"https://tp.mnks.cn/tp_134.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>双向交通</p>', 'https://timg.mnks.cn/20230927ba6bd5b4.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399777254678530, 384, '865c9', 0002, '这个标志是何含义？ ', 'A、禁止驶入 B、禁止通行 C、减速行驶 D、限时进入', '禁止驶入 ', '禁止通行 ', '减速行驶 ', '限时进入', 'B', '<p>此标志表示<b>禁止通行</b>，禁止一切车辆和行人通行。标志里面是空旷的，可以理解为前面是未知的道路，可能是没路或者比较危险，车辆与行人一律不得入内。</p>\n<p><img src=\"https://tp.mnks.cn/tp_179.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止通行</b>：前方道路禁止一切车辆和行人通行。</p>\n<p><img src=\"https://sucimg.itc.cn/sblog/j6w5w43EziG\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>禁止驶入：前方路段禁止一切车辆驶入。</p>', 'https://timg.mnks.cn/20230927cb4b2c10.jpg', '', 0005, 0.32);
INSERT INTO `question` VALUES (1733399777355341826, 385, '59d1f', 0002, '这个标志是何含义？ ', 'A、禁止借道 B、禁止变道 C、禁止超车 D、禁止掉头', '禁止借道 ', '禁止变道 ', '禁止超车 ', '禁止掉头', 'C', '<p><img src=\"https://tp.mnks.cn/tp_188.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止超车：</b>前方路段禁止机动车超车，直到出现解除禁止超车标志。</p>\n<p><img src=\"https://tp.mnks.cn/tp_189.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>解除禁止超车：禁止超车路段结束，与禁止超车标志成对使用。</p>', 'https://timg.mnks.cn/20230927dec62f5a.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399777430839297, 386, '37779', 0002, '这个标志提示哪种车型禁止通行？ ', 'A、中型客车 B、小型货车 C、各种车辆 D、小型客车', '中型客车 ', '小型货车 ', '各种车辆 ', '小型客车', 'D', '<b>禁止小型载客汽车驶入</b>标志：表示禁止小型载客汽车驶入。', 'https://timg.mnks.cn/20230927bc139ff4.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399777481170946, 387, 'dd9a3', 0001, '这个标志是禁止电动自行车进入。', NULL, '对', '错', '空', '空', '对', '【红色-禁令，车身侧面代表禁止指定车型】。禁令标志-<b>禁止电动自行车进入</b>，表示<b>禁止电动自行车进入</b>。设在<b>禁止电动自行车进入</b>路段的入口处。', 'https://timg.mnks.cn/20230926f645c004.jpg', '禁止电动自行车', 0003, 0.09);
INSERT INTO `question` VALUES (1733399777518919682, 388, 'a6f72', 0002, '这个标志是何含义？ ', 'A、禁止向左转弯 B、禁止驶入左车道 C、禁止车辆掉头 D、禁止向左变道', '禁止向左转弯 ', '禁止驶入左车道 ', '禁止车辆掉头 ', '禁止向左变道', 'A', '<p><img src=\"https://tp.mnks.cn/tp_181.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止向左转弯</b>：前方路口禁止一切车辆向左转弯。</p>\n<p><img src=\"https://tp.mnks.cn/tp_182.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>禁止向右转弯：前方路口禁止一切车辆向右转弯。</p>', 'https://timg.mnks.cn/20230927ce68fd60.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399777665720321, 389, 'ee4fa', 0002, '这个标志是何含义？ ', 'A、禁止驶入路口 B、禁止向右转弯 C、禁止车辆掉头 D、禁止变更车道', '禁止驶入路口 ', '禁止向右转弯 ', '禁止车辆掉头 ', '禁止变更车道', 'B', '<p><img src=\"https://tp.mnks.cn/tp_182.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止向右转弯</b>：前方路口禁止一切车辆向右转弯。</p>\n<p><img src=\"https://tp.mnks.cn/tp_181.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>禁止向左转弯：前方路口禁止一切车辆向左转弯。</p>', 'https://timg.mnks.cn/20230927a3886241.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399777711857666, 390, 'e68e8', 0002, '这个标志是何含义？ ', 'A、禁止掉头 B、禁止向右转弯 C、禁止直行 D、禁止向左转弯', '禁止掉头 ', '禁止向右转弯 ', '禁止直行 ', '禁止向左转弯', 'C', '<b>禁止直行</b>：前方路口禁止一切车辆直行。此标志设在<b>禁止直行</b>的路口前适当位置。', 'https://timg.mnks.cn/20230927ef26976a.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399777749606401, 391, '6848d', 0002, '这个标志是何含义？ ', 'A、禁止在路口掉头 B、禁止向左向右变道 C、禁止车辆直行 D、禁止向左向右转弯', '禁止在路口掉头 ', '禁止向左向右变道 ', '禁止车辆直行 ', '禁止向左向右转弯', 'D', '<b>禁止向左向右转弯</b>：前方路口禁止一切车辆向左向右转弯。', 'https://timg.mnks.cn/20230927aef5e5ac.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399777791549441, 392, 'bd019', 0002, '这个标志是何含义？ ', 'A、禁止直行和向左转弯 B、禁止直行和向左变道 C、允许直行和向左变道 D、禁止直行和向右转弯', '禁止直行和向左转弯 ', '禁止直行和向左变道 ', '允许直行和向左变道 ', '禁止直行和向右转弯', 'A', '<b>禁止直行和向左转弯</b>：前方路口禁止一切车辆直行和向左转弯。', 'https://timg.mnks.cn/20230927f0cbc76b.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399777841881090, 393, '00428', 0002, '这个标志是何含义？ ', 'A、禁止直行和向左转弯 B、禁止直行和向左变道 C、允许直行和向左变道 D、禁止直行和向右转弯', '禁止直行和向左转弯 ', '禁止直行和向左变道 ', '允许直行和向左变道 ', '禁止直行和向右转弯', 'D', '<b>禁止直行和向右转弯</b>：前方路口禁止一切车辆直行和向右转弯。', 'https://timg.mnks.cn/20230927df8394d7.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399777879629826, 394, 'e948a', 0002, '这个标志是何含义？ ', 'A、禁止直行 B、禁止掉头 C、禁止变道 D、禁止左转', '禁止直行 ', '禁止掉头 ', '禁止变道 ', '禁止左转', 'B', '<p><img src=\"https://tp.mnks.cn/tp_187.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止掉头</b>：前方路口禁止机动车掉头。</p>\n<p><img src=\"https://tp.mnks.cn/tp_235.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>允许掉头：此处允许机动车掉头。</p>', 'https://timg.mnks.cn/20230927f92d080f.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399777925767170, 395, 'e1bfb', 0002, '这个标志是什么意思？ ', 'A、进入前方路口要加速通过 B、进入前方路口要停车观察 C、进入前方路口要注意车辆 D、进入前方路口要减速让行', '进入前方路口要加速通过 ', '进入前方路口要停车观察 ', '进入前方路口要注意车辆 ', '进入前方路口要减速让行', 'B', '<p><img src=\"https://tp.mnks.cn/tp_176.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>停车让行：<b>车辆必须在停止线前停车瞭望</b>，确认安全后方可以通行。</p>', 'https://timg.mnks.cn/20230927afe7b54d.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399777967710210, 396, 'aeea2', 0002, '这个标志是什么意思？ ', 'A、进入前方路口要注意观察 B、进入前方路口要停车让行 C、进入前方路口要减速让行 D、进入前方路口要注意车辆', '进入前方路口要注意观察 ', '进入前方路口要停车让行 ', '进入前方路口要减速让行 ', '进入前方路口要注意车辆', 'C', '<p><img src=\"https://tp.mnks.cn/tp_177.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>减速让行：<b>车辆必须减速慢行</b>，观察干道行车情况，在确保干道车辆优先通行且安全的前提下，方可进入路口。</p>', 'https://timg.mnks.cn/20230927fce0a0c4.jpg', '', 0004, 0.23);
INSERT INTO `question` VALUES (1733399778005458945, 397, 'eb2ef', 0002, '这个标志是什么意思？ ', 'A、会车时让对向车先行 B、会车时有优先通行权 C、前方是会车困难路段 D、会车对方应停车让行', '会车时让对向车先行 ', '会车时有优先通行权 ', '前方是会车困难路段 ', '会车对方应停车让行', 'A', '<b>会车让行</b>：车辆<b>会车时</b>，必须停车<b>让对向车</b>先行。', 'https://timg.mnks.cn/20230927ba6bd5b4.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399778043207682, 398, 'e517e', 0002, '图中标志提示应该注意什么？ ', 'A、对面可能会有来车 B、可直接驶入此路段 C、不准驶入前方路段 D、进入此路段要减速', '对面可能会有来车 ', '可直接驶入此路段 ', '不准驶入前方路段 ', '进入此路段要减速', 'C', '如图所示，<b>禁止驶入</b>禁令标志，表示<b>前方路段禁止一切车辆驶入</b>。', 'https://timg.mnks.cn/20230926e8ad0e41.jpg', '提示', 0003, 0.10);
INSERT INTO `question` VALUES (1733399778135482369, 399, '80e48', 0002, '这个标志是何含义？ ', 'A、允许长时停车 B、允许临时停车 C、禁止长时停车 D、禁止停放车辆', '允许长时停车 ', '允许临时停车 ', '禁止长时停车 ', '禁止停放车辆', 'D', '<p><img src=\"https://tp.mnks.cn/tp_190.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止车辆停放</b>：表示在限定的范围内，禁止一切车辆停、放，无论驾驶人是否离开车辆。设在禁止车辆停、放的地方，标志颜色为蓝底 、红圈、红斜杠。<b>禁止车辆停放</b>的时段、车种和范围可用辅助标志说明。</p>\n<p><img src=\"https://tp.mnks.cn/tp_191.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>禁止车辆长时停放：表示在限定的范围内，禁止一切车辆长时停、放，临时停放不受限制。设在禁止车辆长时停、放的地方，标志颜色为蓝底、红圈、红斜杠。临时停放指车辆停车上下客或装卸货等，且驾驶人在车内或车旁守候。</p>', 'https://timg.mnks.cn/20230927b835ecc8.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399778210979841, 400, 'cd63d', 0002, '这个标志是何含义？ ', 'A、允许长时停车 B、禁止临时停车 C、禁止长时停车 D、禁止停放车辆', '允许长时停车 ', '禁止临时停车 ', '禁止长时停车 ', '禁止停放车辆', 'C', '<p><img src=\"https://tp.mnks.cn/tp_191.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止车辆长时停放</b>：表示在限定的范围内，禁止一切车辆长时停、放，临时停放不受限制。设在禁止车辆长时停、放的地方，标志颜色为蓝底 、红圈、红斜杠。临时停放指车辆停车上下客或装卸货等，且驾驶人在车内或车旁守候。</p>\n<p><img src=\"https://tp.mnks.cn/tp_190.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>禁止车辆停放：表示在限定的范围内，禁止一切车辆停、放，无论驾驶人是否离开车辆。设在禁止车辆停、放的地方，标志颜色为蓝底、红圈、红斜杠。禁止车辆停放的时段、车种和范围可用辅助标志说明。</p>', 'https://timg.mnks.cn/20230927f3151b1e.jpg', '', 0005, 0.29);
INSERT INTO `question` VALUES (1733399778252922882, 401, '9be19', 0002, '这个标志是何含义？ ', 'A、限制高度为3.5米 B、限制宽度为3.5米 C、解除3.5米限高 D、限制车距为3.5米', '限制高度为3.5米 ', '限制宽度为3.5米 ', '解除3.5米限高 ', '限制车距为3.5米', 'A', '<p><img src=\"https://tp.mnks.cn/tp_194.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>限制高度</b>：前方路段禁止<b>装载高度超过标志所示数值</b>的车辆通行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_193.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>限制宽度：前方路段禁止装载宽度超过标志所示数值的车辆通行。</p>', 'https://timg.mnks.cn/20230927b094134f.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399778294865921, 402, 'e5941', 0002, '这个标志是何含义？ ', 'A、解除3米限宽 B、限制高度为3米 C、预告宽度为3米 D、限制宽度为3米', '解除3米限宽 ', '限制高度为3米 ', '预告宽度为3米 ', '限制宽度为3米', 'D', '<p><img src=\"https://tp.mnks.cn/tp_193.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>限制宽度</b>：前方路段<b>禁止装载宽度超过标志所示数值</b>的车辆通行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_194.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>限制高度：前方路段禁止装载高度超过标志所示数值的车辆通行。</p>', 'https://timg.mnks.cn/20230927b2d161d5.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399778328420354, 403, 'a8ac2', 0002, '这个标志是何含义？ ', 'A、禁止长时鸣喇叭 B、断续鸣喇叭 C、禁止鸣喇叭 D、减速鸣喇叭', '禁止长时鸣喇叭 ', '断续鸣喇叭 ', '禁止鸣喇叭 ', '减速鸣喇叭', 'C', '<p><img src=\"https://tp.mnks.cn/tp_192.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止鸣喇叭</b>：前方道路禁止车辆鸣喇叭。</p>\n<p><img src=\"https://tp.mnks.cn/tp_215.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>鸣喇叭：机动车行至本标志处应鸣喇叭，以提醒对向车辆驾驶人注意并减速慢行。</p>', 'https://timg.mnks.cn/20230927f00dcb11.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399778387140610, 404, 'cd0e8', 0002, '这个标志是何含义？ ', 'A、限制40吨轴重 B、限制最高时速40公里 C、前方40米减速 D、最低时速40公里', '限制40吨轴重 ', '限制最高时速40公里 ', '前方40米减速 ', '最低时速40公里', 'B', '<p><img src=\"https://tp.mnks.cn/tp_195.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>限制速度</b>：本标志至前方解除限制速度标志或另一块不同限速值的限制速度标志的路段内，机动车行驶速度<b>不准超过标志所示数值</b>。<br><img src=\"https://tp.mnks.cn/tp_217.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>最低限速：前方道路的最低时速限制。<br><img src=\"https://sucimg.itc.cn/sblog/od5hLEmgBfW\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>建议速度：提醒车辆驾驶人以建议的速度行驶。</p>', 'https://timg.mnks.cn/20230927bbc9a4d0.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399778433277954, 405, '78201', 0002, '这个标志是何含义？ ', 'A、左侧通行 B、右侧通行 C、两侧通行 D、不准通行', '左侧通行 ', '右侧通行 ', '两侧通行 ', '不准通行', 'C', '线形诱导标指路标志，引导行车方向，指示道路使用者前方线形变化，注意按标志指示改变行驶方向，谨慎驾驶。<br>\n<p><img src=\"https://tp.mnks.cn/tp_261.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>两侧通行</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_262.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右侧通行</p>\n<p><img src=\"https://tp.mnks.cn/tp_263.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左侧通行</p>', 'https://timg.mnks.cn/20230927e48fc69c.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399778475220994, 406, '4b350', 0002, '这个标志是何含义？ ', 'A、左侧通行 B、不准通行 C、两侧通行 D、右侧通行', '左侧通行 ', '不准通行 ', '两侧通行 ', '右侧通行', 'D', '<p><img src=\"https://tp.mnks.cn/tp_262.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>右侧通行：</b>线形诱导标，引导行车方向，指示道路使用者前方线形变化是右侧通行，注意按标志指示改变行驶方向，谨慎驾驶。<br><img src=\"https://tp.mnks.cn/tp_263.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左侧通行：线形诱导标，引导行车方向，指示道路使用者前方线形变化是左侧通行，注意按标志指示改变行驶方向，谨慎驾驶。</p>【<b>区分方法</b>】1、看线条方向，从<b>右上斜向左下</b>是左侧通行；从<b>左上斜向右下</b>是右侧通行。2、看<b>顶端三角形</b>的位置，小三角△在左侧是左侧通行，小三角△在右侧是右侧通行。', 'https://timg.mnks.cn/20230927a0837247.jpg', '', 0004, 0.21);
INSERT INTO `question` VALUES (1733399778508775426, 407, '0c80a', 0002, '这个标志是何含义？ ', 'A、左侧通行 B、不准通行 C、两侧通行 D、右侧通行', '左侧通行 ', '不准通行 ', '两侧通行 ', '右侧通行', 'A', '<p><img src=\"https://tp.mnks.cn/tp_263.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>左侧通行</b>：线形诱导标，引导行车方向，指示道路使用者前方线形变化是左侧通行，注意按标志指示改变行驶方向，谨慎驾驶。 <br><img src=\"https://tp.mnks.cn/tp_262.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右侧通行：线形诱导标，引导行车方向，指示道路使用者前方线形变化是右侧通行，注意按标志指示改变行驶方向，谨慎驾驶。</p>【<b>区分方法</b>】1、看线条方向，从右上斜向左下是左侧通行；从左上斜向右下是右侧通行。2、看<b>顶端三角形</b>的位置，小三角△在左侧是左侧通行，小三角△在右侧是右侧通行。', 'https://timg.mnks.cn/20230927eb4dd6c6.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399778550718466, 408, '91865', 0002, '这个标志是何含义？ ', 'A、边防检查 B、禁止通行 C、海关检查 D、停车检查', '边防检查 ', '禁止通行 ', '海关检查 ', '停车检查', 'D', '<p><img src=\"https://tp.mnks.cn/tp_197.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>停车检查</b>：前方是机动车必须停车接受检查的地点。</p>\n<p><img src=\"https://tp.mnks.cn/tp_198.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>海关：道路前方是海关，所有机动车必须停车接受海关检查后方可通过。</p>', 'https://timg.mnks.cn/20230927d7471848.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399778634604545, 409, 'd57ff', 0002, '这个标志是何含义？ ', 'A、海关 B、国界 C、边防 D、边界', '海关 ', '国界 ', '边防 ', '边界', 'A', '<p><img src=\"https://tp.mnks.cn/tp_198.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>海关：道路前方是海关，所有机动车必须停车接受海关检查后方可通过。</p>\n<p><img src=\"https://tp.mnks.cn/tp_197.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>停车检查：前方是机动车必须停车接受检查的地点。</p>', 'https://timg.mnks.cn/20230927ecec198e.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399778697519105, 410, 'cf6a2', 0002, '图中标志提示应该注意什么？ ', 'A、可以停下来装卸货物 B、可以停下来让同行的人下车 C、可以临时停车等待同行的人 D、不准临时或长时停放车辆', '可以停下来装卸货物 ', '可以停下来让同行的人下车 ', '可以临时停车等待同行的人 ', '不准临时或长时停放车辆', 'D', '<p><img src=\"https://tp.mnks.cn/tp_190.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止停车</b>：在限定的范围内，禁止一切车辆临时或长时停、放。</p>\n<p><img src=\"https://tp.mnks.cn/tp_191.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>禁止长时停车：在限定的范围内，禁止一切车辆长时间停、放，临时停车不受限制。</p>', 'https://timg.mnks.cn/20230927b835ecc8.jpg', '提示', 0001, 0.04);
INSERT INTO `question` VALUES (1733399778752045057, 411, '12250', 0002, '这个标志是何含义？ ', 'A、国道编号 B、省道编号 C、县道编号 D、乡道编号', '国道编号 ', '省道编号 ', '县道编号 ', '乡道编号', 'A', '<p><img src=\"https://tp.mnks.cn/tp_243.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>国（Guo）道编号</b>： 指示当前所行驶道路的国道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_244.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>省（Sheng ）道编号：指示当前所行驶道路的省道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_245.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>县（Xian）道编号：指示当前所行驶道路的县道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_246.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>乡（Xiang）道编号：指示当前所行驶道路的乡道编号。</p>', 'https://timg.mnks.cn/20230927ef4fcccd.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399778802376706, 412, '5b34f', 0002, '这个标志是何含义？ ', 'A、国道编号 B、省道编号 C、县道编号 D、乡道编号', '国道编号 ', '省道编号 ', '县道编号 ', '乡道编号', 'B', '<p><img src=\"https://tp.mnks.cn/tp_244.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>省（Sheng ）道编号</b>：指示当前所行驶道路的省道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_243.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>国（Guo）道编号： 指示当前所行驶道路的国道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_245.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>县（Xian）道编号：指示当前所行驶道路的县道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_246.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>乡（Xiang）道编号：指示当前所行驶道路的乡道编号。</p>', 'https://timg.mnks.cn/20230927be3a0cef.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399778844319745, 413, '5d8fb', 0002, '这个标志是何含义？ ', 'A、国道编号 B、省道编号 C、县道编号 D、乡道编号', '国道编号 ', '省道编号 ', '县道编号 ', '乡道编号', 'C', '<p><img src=\"https://tp.mnks.cn/tp_245.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>县（Xian）道编号</b>：指示当前所行驶道路的县道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_246.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>乡（Xiang）道编号：指示当前所行驶道路的乡道编号。</p>\n<p></p>\n<p><img src=\"https://tp.mnks.cn/tp_244.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>省（Sheng ）道编号：指示当前所行驶道路的省道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_243.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>国（Guo）道编号： 指示当前所行驶道路的国道编号。</p>', 'https://timg.mnks.cn/20230927e14c5297.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399778898845698, 414, '26468', 0002, '这个标志是何含义？ ', 'A、国道编号 B、省道编号 C、县道编号 D、乡道编号', '国道编号 ', '省道编号 ', '县道编号 ', '乡道编号', 'D', '<p><img src=\"https://tp.mnks.cn/tp_246.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>乡（Xiang）道编号</b>：指示当前所行驶道路的乡道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_244.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>省（Sheng ）道编号：指示当前所行驶道路的省道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_243.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>国（Guo）道编号： 指示当前所行驶道路的国道编号。</p>\n<p><img src=\"https://tp.mnks.cn/tp_245.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>县（Xian）道编号：指示当前所行驶道路的县道编号。</p>', 'https://timg.mnks.cn/20230927e3ca2d20.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399778953371650, 415, '30284', 0002, '这个标志是何含义？ ', 'A、解除禁止借道 B、解除禁止变道 C、准许变道行驶 D、解除禁止超车', '解除禁止借道 ', '解除禁止变道 ', '准许变道行驶 ', '解除禁止超车', 'D', '<p><img src=\"https://tp.mnks.cn/tp_189.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>解除禁止超车：</b>表示禁止超车路段结束；与禁止超车标志成对使用；此标志设在禁止超车路段的终点。</p>\n<p><img src=\"https://tp.mnks.cn/tp_188.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>禁止超车：前方路段禁止机动车超车，直到出现解除禁止超车标志。</p>', 'https://timg.mnks.cn/20230927bae67eca.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399779016286210, 416, 'a0c58', 0002, '这个标志是何含义？ ', 'A、40米减速行驶路段 B、最低时速40公里 C、解除时速40公里限制 D、最高时速40公里', '40米减速行驶路段 ', '最低时速40公里 ', '解除时速40公里限制 ', '最高时速40公里', 'C', '<p><img src=\"https://tp.mnks.cn/tp_196.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>解除限制速度：</b>限制速度路段结束，与限制速度标志配合使用。<br><img src=\"https://tp.mnks.cn/tp_195.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>限制速度：本标志至前方解除限制速度标志或另一块不同限速值的限制速度标志的路段内，机动车行驶速度不准超过标志所示数值。</p>', 'https://timg.mnks.cn/20230927fd3fff25.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399779075006466, 417, 'f2097', 0002, '这个标志是何含义？ ', 'A、禁止小型车行驶 B、机动车行驶 C、只准小型车行驶 D、不准小型车通行', '禁止小型车行驶 ', '机动车行驶 ', '只准小型车行驶 ', '不准小型车通行', 'B', '<p><img src=\"https://tp.mnks.cn/tp_229.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>机动车行驶</b>：表示<b>该道路</b>仅供机动车通行。<br><img src=\"https://tp.mnks.cn/tp_230.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>机动车车道：表示该车道仅供机动车通行。</p>', 'https://timg.mnks.cn/20230927b9f617ee.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399779133726721, 418, '7e143', 0002, '这个标志是何含义？ ', 'A、非机动车停车位 B、电动自行车行驶 C、非机动车停放区 D、非机动车行驶', '非机动车停车位 ', '电动自行车行驶 ', '非机动车停放区 ', '非机动车行驶', 'D', '<p><img src=\"https://tp.mnks.cn/tp_231.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>非机动车行驶</b>：表示<b>该道路</b>仅供非机动车通行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_232.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>非机动车车道：表示该车道仅供非机动车通行。</p>', 'https://timg.mnks.cn/20230927da76b70d.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399779188252674, 419, 'bef0e', 0002, '这个标志是何含义？ ', 'A、低速行驶 B、注意行人 C、行人先行 D、步行', '低速行驶 ', '注意行人 ', '行人先行 ', '步行', 'D', '<p><img src=\"https://tp.mnks.cn/tp_214.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>步行：</b>前方道路<b>只供步行</b>，任何车辆不准进入。</p>\n<p><img src=\"https://tp.mnks.cn/tp_216.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>人行横道：表示该处为人行横道，机动车驾驶人应注意观察行人，遇行人已进入人行横道时应停车让行人通过。</p>\n<p><img src=\"https://tp.mnks.cn/tp_135.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意行人：警告前方道路设有人行横道线，应减速慢行，注意行人。</p>\n<p><img src=\"https://tp.mnks.cn/tp_136.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意儿童：警告前方是儿童频繁出入的地点，应减速慢行，注意儿童。</p>', 'https://timg.mnks.cn/20230927e4a92e34.jpg', '', 0005, 0.28);
INSERT INTO `question` VALUES (1733399779246972929, 420, '241ef', 0002, '这个标志是何含义？ ', 'A、应当鸣喇叭 B、禁止鸣喇叭 C、禁止鸣高音喇叭 D、禁止鸣低音喇叭', '应当鸣喇叭 ', '禁止鸣喇叭 ', '禁止鸣高音喇叭 ', '禁止鸣低音喇叭', 'A', '<p>蓝底白图是指示，不要混淆成警告。<br><img src=\"https://tp.mnks.cn/tp_215.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>鸣喇叭：</b>机动车行至本标志处应鸣喇叭，以提醒对向车辆驾驶人注意并减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_192.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>禁止鸣喇叭：表示前方道路禁止车辆鸣喇叭。</p>', 'https://timg.mnks.cn/20230927f8edbca4.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399779309887490, 421, 'a6227', 0002, '这个标志是何含义？ ', 'A、最高限速50公里/小时 B、最低限速50公里/小时 C、水平高度50米 D、海拔高度50米', '最高限速50公里/小时 ', '最低限速50公里/小时 ', '水平高度50米 ', '海拔高度50米', 'B', '<p>速度标志别花眼，蓝低红高黄建议。<br><img src=\"https://tp.mnks.cn/tp_217.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>最低限速</b>：前方道路的最低时速限制。</p>\n<p><img src=\"https://tp.mnks.cn/tp_195.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>限制速度：本标志至前方解除限制速度标志或另一块不同限速值的限制速度标志的路段内，机动车行驶速度不准超过标志所示数值。</p>\n<p><img src=\"https://sucimg.itc.cn/sblog/od5hLEmgBfW\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>建议速度：提醒车辆驾驶人以建议的速度行驶。</p>', 'https://timg.mnks.cn/20230925b4fbddf1.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399779406356482, 422, '50fd7', 0002, '这个标志是何含义？ ', 'A、直行车道 B、只准直行 C、单行路 D、禁止直行', '直行车道 ', '只准直行 ', '单行路 ', '禁止直行', 'B', '<p><img src=\"https://tp.mnks.cn/tp_200.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>只准直行</b>：前方路口一切车辆只准直行。（圆胖子箭头转弯不方便-只准直行）</p>\n<p><img src=\"https://tp.mnks.cn/tp_213.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>单行路：前方道路为单向行驶，已进入车辆应按标志指示方向行车。（长瘦子箭头很单薄-单行路）</p>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927ef52e54c.jpg', '', 0005, 0.47);
INSERT INTO `question` VALUES (1733399779473465346, 423, '8dfac', 0002, '这个标志是何含义？ ', 'A、直行车道 B、单行路 C、向左转弯 D、禁止直行', '直行车道 ', '单行路 ', '向左转弯 ', '禁止直行', 'C', '<p><img src=\"https://tp.mnks.cn/tp_201.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>向左转弯</b>：前方路口一切车辆只准向左转弯。</p>\n<p><img src=\"https://tp.mnks.cn/tp_202.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向右转弯：前方路口一切车辆只准向右转弯。</p>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927b5575d15.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399779519602690, 424, '18d69', 0002, '这个标志是何含义？ ', 'A、直行车道 B、只准直行 C、单行路 D、向右转弯', '直行车道 ', '只准直行 ', '单行路 ', '向右转弯', 'D', '<p><img src=\"https://tp.mnks.cn/tp_202.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>向右转弯</b>：前方路口一切车辆只准<b>向右转弯</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_201.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向左转弯：前方路口一切车辆只准向左转弯。</p>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927d49adaa5.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399779708346370, 425, '7ff63', 0002, '这个标志是何含义？ ', 'A、直行和向右转弯 B、直行和向左转弯 C、禁止直行和向右转弯 D、只准向左和向右转弯', '直行和向右转弯 ', '直行和向左转弯 ', '禁止直行和向右转弯 ', '只准向左和向右转弯', 'A', '<p><img src=\"https://tp.mnks.cn/tp_203.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>直行和向右转弯</b>：前方路口一切车辆只准<b>直行和向右转弯</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_204.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行和向左转弯</p>\n<p><img src=\"https://tp.mnks.cn/tp_205.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向左和向右转弯</p>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927ec5c00c2.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399779771260930, 426, '86a40', 0002, '这个标志是何含义？ ', 'A、直行和向右转弯 B、直行和向左转弯 C、禁止直行和向左转弯 D、只准向右和向左转弯', '直行和向右转弯 ', '直行和向左转弯 ', '禁止直行和向左转弯 ', '只准向右和向左转弯', 'B', '<p><img src=\"https://tp.mnks.cn/tp_204.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>直行和向左转弯</b>：前方路口一切车辆只准<b>直行和向左转弯</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_203.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行和向右转弯</p>\n<p><img src=\"https://tp.mnks.cn/tp_205.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向左和向右转弯</p>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927fcfdc05c.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399779817398273, 427, '4a31c', 0002, '这个标志是何含义？ ', 'A、禁止向右转弯 B、禁止向左转弯 C、向左和向右转弯 D、禁止向左右转弯', '禁止向右转弯 ', '禁止向左转弯 ', '向左和向右转弯 ', '禁止向左右转弯', 'C', '<p><img src=\"https://tp.mnks.cn/tp_205.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>向左和向右转弯</b>：前方路口一切车辆只准<b>向左和向右转弯</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_203.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行右转弯</p>\n<p><img src=\"https://tp.mnks.cn/tp_204.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行左转弯</p>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927bef8c09c.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399779867729922, 428, '6b35a', 0002, '这个标志是何含义？ ', 'A、靠道路右侧停车 B、只准向右转弯 C、右侧是下坡路段 D、靠右侧道路行驶', '靠道路右侧停车 ', '只准向右转弯 ', '右侧是下坡路段 ', '靠右侧道路行驶', 'D', '<img src=\"https://tp.mnks.cn/tp_206.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>靠右侧道路行驶</b>：前方路段一切车辆只准靠右侧行驶。<br><img src=\"https://tp.mnks.cn/tp_207.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>靠左侧道路行驶：前方路段一切车辆只准靠左侧行驶。<br>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927d8fbd628.jpg', '', 0005, 0.31);
INSERT INTO `question` VALUES (1733399779913867266, 429, '629ee', 0002, '这个标志是何含义？ ', 'A、靠左侧道路行驶 B、只准向左转弯 C、左侧是下坡路段 D、靠道路左侧停车', '靠左侧道路行驶 ', '只准向左转弯 ', '左侧是下坡路段 ', '靠道路左侧停车', 'A', '<img src=\"https://tp.mnks.cn/tp_207.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>靠左侧道路行驶</b>：前方路段一切车辆只准靠左侧行驶。<br><img src=\"https://tp.mnks.cn/tp_206.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>靠右侧道路行驶：前方路段一切车辆只准靠右侧行驶。<br>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927c88cd8fc.jpg', '', 0005, 0.27);
INSERT INTO `question` VALUES (1733399779968393217, 430, '651f0', 0002, '这个标志是何含义？ ', 'A、立体交叉直行和右转弯行驶 B、立体交叉直行和左转弯行驶 C、直行和左转弯行驶 D、直行和右转弯行驶', '立体交叉直行和右转弯行驶 ', '立体交叉直行和左转弯行驶 ', '直行和左转弯行驶 ', '直行和右转弯行驶', 'B', '<b>立体交叉直行和左转弯行驶</b>：前方立体交叉口，一切车辆可以直行和按图示路线左转弯行驶。<br>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927a4536650.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399780010336257, 431, '57379', 0002, '这个标志是何含义？ ', 'A、直行和左转弯行驶 B、直行和右转弯行驶 C、立体交叉直行和右转弯行驶 D、立体交叉直行和左转弯行驶', '直行和左转弯行驶 ', '直行和右转弯行驶 ', '立体交叉直行和右转弯行驶 ', '立体交叉直行和左转弯行驶', 'C', '<b>立体交叉直行和右转弯行驶</b>：指示前方立体交叉口，一切车辆可以直行和按图示路线右转弯行驶。<br>【拓展延伸】指示标志，箭头指哪往哪走。', 'https://timg.mnks.cn/20230927e23464a4.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399780048084993, 432, '80ef2', 0002, '这个标志是何含义？ ', 'A、右侧通行 B、左侧通行 C、向右行驶 D、环岛行驶', '右侧通行 ', '左侧通行 ', '向右行驶 ', '环岛行驶', 'D', '<b>环岛行驶</b>：前方环岛，一切车辆只准靠右环形，车辆进入环岛时应让环岛内车辆优先通行。', 'https://timg.mnks.cn/20230927e292682a.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399780085833729, 433, 'baafc', 0002, '这属于哪一种标志？ ', 'A、警告标志 B、禁令标志 C、指示标志 D、指路标志', '警告标志 ', '禁令标志 ', '指示标志 ', '指路标志', 'C', '<b>指示标志</b>的颜色为<b>蓝底、白图形</b>；形状为圆形、长方形和正方形，个别标志例外；<b>指示标志</b>的作用是指示车辆、行人按标志指示的路线、方向行驶，道路使用者应遵循。', 'https://timg.mnks.cn/20230927aaf32b56.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399780123582465, 434, 'ce8f5', 0002, '这个标志是何含义？ ', 'A、向左单行路 B、向右单行路 C、直行单行路 D、左转让行', '向左单行路 ', '向右单行路 ', '直行单行路 ', '左转让行', 'A', '<p>单行路：前方道路为单向行驶，已进入车辆应按标志指示方向行车。<br><img src=\"https://tp.mnks.cn/tp_211.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>向左单行路</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_212.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向右单行路</p>\n<p><img src=\"https://tp.mnks.cn/tp_213.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行单行路</p>', 'https://timg.mnks.cn/20230927c17db6b2.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399780173914114, 435, '685a2', 0002, '这个标志是何含义？ ', 'A、向左单行路 B、向右单行路 C、直行单行路 D、右转让行', '向左单行路 ', '向右单行路 ', '直行单行路 ', '右转让行', 'B', '<p>单行路：前方道路为单向行驶，已进入车辆应按标志指示方向行车。<br><img src=\"https://tp.mnks.cn/tp_211.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向左单行路</p>\n<p><img src=\"https://tp.mnks.cn/tp_212.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>向右单行路</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_213.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行单行路</p>', 'https://timg.mnks.cn/20230927d2df5e27.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399780224245761, 436, 'b9c0f', 0002, '这个标志是何含义？ ', 'A、转弯诱导标志 B、线形诱导标志 C、合流诱导标志 D、分流诱导标志', '转弯诱导标志 ', '线形诱导标志 ', '合流诱导标志 ', '分流诱导标志', 'B', '<b>线形诱导标</b>组合使用指路标志，引导行车方向，指示道路使用者前方线形变化，注意按标志指示改变行驶方向，注意谨慎驾驶。<br><b>线形诱导标</b>的颜色规定为：指示性<b>线形诱导标</b>一般道路为蓝底白图案，高速公路为绿底白图案，用以提供一般性行驶指示；警告性<b>线形诱导标</b>为黄底黑图案，可使车辆驾驶人提高警觉，并准备防范应变之措施。', 'https://timg.mnks.cn/20230927e6a792a9.jpg', '', 0004, 0.24);
INSERT INTO `question` VALUES (1733399780261994498, 437, '81ee4', 0002, '这个标志是何含义？ ', 'A、隧道出口距离 B、隧道入口距离 C、隧道跟车距离 D、隧道总长度', '隧道出口距离 ', '隧道入口距离 ', '隧道跟车距离 ', '隧道总长度', 'A', '<b>隧道出口距离预告</b>指路标志，用于指示<b>到前方隧道出口的距离</b>。<br>在隧道尽头看到的亮光，表示出口；2500m表示距离，这个标志的含义是指示到前方隧道出口的距离。', 'https://timg.mnks.cn/20230927d5ee4f6d.jpg', '', 0005, 0.30);
INSERT INTO `question` VALUES (1733399780312326146, 438, '52129', 0002, '这个标志是何含义？ ', 'A、减速拍照区 B、道路流量监测 C、全路段抓拍 D、交通监控设备', '减速拍照区 ', '道路流量监测 ', '全路段抓拍 ', '交通监控设备', 'D', '<b>交通监控设备</b>标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人本路段设置了图像采集等<b>交通监控设备</b>。', 'https://timg.mnks.cn/20230926f7d953b2.jpg', '', 0004, 0.23);
INSERT INTO `question` VALUES (1733399780354269185, 439, '4645d', 0002, '这个标志是何含义？ ', 'A、向右变道 B、分流处 C、车道数增加 D、路面变宽', '向右变道 ', '分流处 ', '车道数增加 ', '路面变宽', 'C', '<p><img src=\"https://tp.mnks.cn/tp_257.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>车道数增加</b>：前车道数量增加，需谨慎驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_256.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>车道数减少：前方车道数量变少，需提高警惕</p>', 'https://timg.mnks.cn/20230927d479d05c.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399780396212226, 440, 'bc702', 0002, '这个标志是何含义？ ', 'A、停车场 B、观景台 C、休息区 D、服务区', '停车场 ', '观景台 ', '休息区 ', '服务区', 'C', '<p><img src=\"https://tp.mnks.cn/tp_253.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>休息区</b>：标志指示方向有路侧<b>休息区</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_251.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>观景台：标志指示方向有观景台，驾驶人可在路侧停车观察。</p>', 'https://timg.mnks.cn/20230927e057cdc9.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399780450738177, 441, 'f5304', 0002, '这个标志是何含义？ ', 'A、此路不通 B、T型路口 C、分流路口 D、减速通行', '此路不通 ', 'T型路口 ', '分流路口 ', '减速通行', 'A', '<b>此路不通</b>指路标志，指示前方道路无出口，不能通行。', 'https://timg.mnks.cn/20230927c95dae58.jpg', '', 0003, 0.08);
INSERT INTO `question` VALUES (1733399780484292610, 442, '7e9f3', 0002, '这个标志是何含义？ ', 'A、靠右侧行驶 B、不允许直行 C、直行单行路 D、直行车让行', '靠右侧行驶 ', '不允许直行 ', '直行单行路 ', '直行车让行', 'C', '<p>单行路：前方道路为<b>单向行驶</b>，已进入车辆应按标志指示方向行车。<br><img src=\"https://tp.mnks.cn/tp_211.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向左单行路</p>\n<p><img src=\"https://tp.mnks.cn/tp_212.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向右单行路</p>\n<p><img src=\"https://tp.mnks.cn/tp_213.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>直行单行路</b></p>', 'https://timg.mnks.cn/20230927ba52dc87.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399780522041345, 443, 'd2f6b', 0002, '这个标志是何含义？ ', 'A、横过道路设施 B、应急避难场所 C、生活服务区 D、行人专用通道', '横过道路设施 ', '应急避难场所 ', '生活服务区 ', '行人专用通道', 'B', '<b>应急避难设施（场所）</b>：附近有应急避难场所、隧道等设施的疏散通道或其他应急避难设施。', 'https://timg.mnks.cn/20230926cd9570c8.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399780568178689, 444, '60c8b', 0002, '这个标志是何含义？ ', 'A、观景台 B、停车场 C、休息区 D、停车位', '观景台 ', '停车场 ', '休息区 ', '停车位', 'A', '<p><img src=\"https://tp.mnks.cn/tp_251.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>观景台</b>：标志指示方向有<b>观景台</b>，驾驶人可在路侧停车观察。</p>\n<p><img src=\"https://tp.mnks.cn/tp_253.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>休息区：标志指示方向有路侧休息区。</p>', 'https://timg.mnks.cn/20230927efbec138.jpg', '', 0001, 0.04);
INSERT INTO `question` VALUES (1733399780618510338, 445, '052b5', 0002, '这个标志是何含义？ ', 'A、紧急停车带 B、露天停车场 C、停车位 D、错车道', '紧急停车带 ', '露天停车场 ', '停车位 ', '错车道', 'D', '<p><img src=\"https://tp.mnks.cn/tp_250.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>错车道</b>：指示前方路段设有避让来车的处所。<br><img src=\"https://tp.mnks.cn/tp_280.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>紧急停车带：前方路段设有紧急停车的位置。</p>', 'https://timg.mnks.cn/20230927fd6504b7.jpg', '', 0005, 0.31);
INSERT INTO `question` VALUES (1733399780660453378, 446, 'b6368', 0002, '这个标志是何含义？ ', 'A、单行路 B、停车让行 C、干路先行 D、两侧街道', '单行路 ', '停车让行 ', '干路先行 ', '两侧街道', 'C', '<p><img src=\"https://tp.mnks.cn/tp_218.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>路口优先通行（干路先行）</b>：前方交叉口主干道路上的车辆享有优先通行权利。</p>\n<p><img src=\"https://tp.mnks.cn/tp_213.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>单行路：前方道路为单向行驶，已进入车辆应按标志指示方向行车。</p>', 'https://timg.mnks.cn/20230927b2a5ca84.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399780702396418, 447, 'ba267', 0002, '这个标志是何含义？ ', 'A、人行横道 B、学生通道 C、儿童通道 D、注意行人', '人行横道 ', '学生通道 ', '儿童通道 ', '注意行人', 'A', '<p>蓝底白图是指示，不要混淆成警告。<br><img src=\"https://tp.mnks.cn/tp_216.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>人行横道：</b>表示该处为人行横道，机动车驾驶人应注意观察行人，遇行人已进入人行横道时应停车让行人通过。</p>\n<p><img src=\"https://tp.mnks.cn/tp_135.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意行人：用以警告车辆驾驶人减速慢行，注意行人。</p>\n<p></p>', 'https://timg.mnks.cn/20230927b0c9568c.jpg', '', 0004, 0.24);
INSERT INTO `question` VALUES (1733399780748533761, 448, '10813', 0002, '这个标志是何含义？ ', 'A、专用停车场 B、露天停车场 C、室内停车场 D、内部停车场', '专用停车场 ', '露天停车场 ', '室内停车场 ', '内部停车场', 'C', '<p><img src=\"https://tp.mnks.cn/tp_249.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>室内停车场</b>：附近设有室内停车场。</p>\n<p><img src=\"https://tp.mnks.cn/tp_248.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>露天停车场：附近设有露天停车场。</p>', 'https://timg.mnks.cn/20230927ebca786b.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399780794671105, 449, 'e30a0', 0002, '这个标志是何含义？ ', 'A、室内停车场 B、露天停车场 C、专用停车场 D、内部停车场', '室内停车场 ', '露天停车场 ', '专用停车场 ', '内部停车场', 'B', '<p><img src=\"https://tp.mnks.cn/tp_248.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>露天停车场</b>：附近设有<b>露天停车场</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_249.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>室内停车场：附近设有室内停车场。</p>', 'https://timg.mnks.cn/20230927a0a2a9da.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399780849197057, 450, '6ad66', 0002, '这个标志是何含义？ ', 'A、掉头 B、倒车 C、左转 D、绕行', '掉头 ', '倒车 ', '左转 ', '绕行', 'A', '<p><img src=\"https://tp.mnks.cn/tp_235.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>允许掉头</b>：此处允许机动车掉头。</p>', 'https://timg.mnks.cn/20230927d91b7a0a.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399780975026177, 451, 'ec007', 0002, '这个标志是何含义？ ', 'A、对向先行 B、停车让行 C、单行路 D、会车先行', '对向先行 ', '停车让行 ', '单行路 ', '会车先行', 'D', '红圈红箭头在右-会车让行；<b>蓝底红箭头在左</b>-会车<b>先行</b>。\n<p><img src=\"https://tp.mnks.cn/tp_219.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>会车<b>先行</b></b>：前方路段车辆在会车时<b>享有优先通行权利</b>。</p>\n<p></p>\n<p><img src=\"https://tp.mnks.cn/tp_178.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>会车让行：车辆会车时，必须停车让对向车先行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_134.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>双向交通：提醒前方是不分离双向行驶路段，应注意会车。</p>', 'https://timg.mnks.cn/20230927ba28b642.jpg', '', 0004, 0.18);
INSERT INTO `question` VALUES (1733399781025357825, 452, 'c0c67', 0002, '这个标志是何含义？ ', 'A、禁止左转 B、此路不通 C、禁止通行 D、超高绕行', '禁止左转 ', '此路不通 ', '禁止通行 ', '超高绕行', 'D', '<b>绕行</b>：前方道路实施交通管制，车辆需按照标志所指示的路线<b>绕行</b>。如图所示，<b>高度超过4米的车辆需按箭头指引的路线绕行通过</b>，因此是超高绕行&gt;。', 'https://timg.mnks.cn/20230927e1fd5857.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399781058912258, 453, '5e84f', 0002, '这个标志是何含义？ ', 'A、公交线路专用车道 B、大型客车专用车道 C、快速公交系统专用车道 D、多乘员车辆专用车道', '公交线路专用车道 ', '大型客车专用车道 ', '快速公交系统专用车道 ', '多乘员车辆专用车道', 'A', '<p><img src=\"https://tp.mnks.cn/tp_228.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>公交线路专用车道</b>：所指示车道专供本线路行驶的公交车辆行驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_233.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>快速公交系统（BRT）专用车道：所指示车道专供快速公交系统（BRT）车辆行驶。</p>', 'https://timg.mnks.cn/20230927c6172488.jpg', '', 0005, 0.29);
INSERT INTO `question` VALUES (1733399781113438210, 454, 'b1907', 0002, '这个标志是何含义？ ', 'A、左转车道 B、掉头车道 C、绕行车道 D、分向车道', '左转车道 ', '掉头车道 ', '绕行车道 ', '分向车道', 'B', '<b>掉头车道</b>：所指示的车道为<b>掉头车道</b>。', 'https://timg.mnks.cn/20230927c02d98f8.jpg', '', 0001, 0.04);
INSERT INTO `question` VALUES (1733399781159575553, 455, '31278', 0002, '这个标志是何含义？ ', 'A、直行和左转车道 B、直行和辅路出口车道 C、直行和右转合用车道 D、分向行驶车道', '直行和左转车道 ', '直行和辅路出口车道 ', '直行和右转合用车道 ', '分向行驶车道', 'C', '<p><img src=\"https://tp.mnks.cn/tp_223.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>直行和右转合用车道：</b>所指示的车道为直行和右转合用车道。</p>\n<p><img src=\"https://tp.mnks.cn/tp_224.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行和左转合用车道：所指示的车道为直行和左转合用车道。</p>', 'https://timg.mnks.cn/20230927a61cc031.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399781201518594, 456, '473f7', 0002, '这个标志是何含义？ ', 'A、直行和左转合用车道 B、直行和掉头合用车道 C、直行和右转车道 D、分向行驶车道', '直行和左转合用车道 ', '直行和掉头合用车道 ', '直行和右转车道 ', '分向行驶车道', 'A', '<p><img src=\"https://tp.mnks.cn/tp_224.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>直行和左转合用车道</b>：所指示的车道为<b>直行和左转合用车道</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_223.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行和右转合用车道：所指示的车道为直行和右转合用车道。</p>', 'https://timg.mnks.cn/20230927b703584c.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399781256044546, 457, '095d8', 0002, '这个标志是何含义？ ', 'A、分向车道 B、右转车道 C、掉头车道 D、左转车道', '分向车道 ', '右转车道 ', '掉头车道 ', '左转车道', 'B', '<p><img src=\"https://tp.mnks.cn/tp_220.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>右转车道：</b>所指示的车道为右转车道。</p>\n<p><img src=\"https://tp.mnks.cn/tp_221.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左转车道：所指示的车道为左转车道。</p>\n<p><img src=\"https://tp.mnks.cn/tp_222.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行车道：所指示的车道为直行车道。</p>', 'https://timg.mnks.cn/20230927f60e5ff7.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399781297987585, 458, 'ef885', 0002, '这个标志是何含义？ ', 'A、分向车道 B、右转车道 C、掉头车道 D、左转车道', '分向车道 ', '右转车道 ', '掉头车道 ', '左转车道', 'D', '<p><img src=\"https://tp.mnks.cn/tp_221.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>左转车道：</b>所指示的车道为左转车道。</p>\n<p><img src=\"https://tp.mnks.cn/tp_220.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右转车道：所指示的车道为右转车道。</p>\n<p><img src=\"https://tp.mnks.cn/tp_222.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>直行车道：所指示的车道为直行车道。</p>', 'https://timg.mnks.cn/20230927a92284ee.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399781339930625, 459, 'cb564', 0002, '这个标志是何含义？ ', 'A、直行车道 B、右转车道 C、掉头车道 D、左转车道', '直行车道 ', '右转车道 ', '掉头车道 ', '左转车道', 'A', '<p><img src=\"https://tp.mnks.cn/tp_222.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>直行车道：</b>所指示的车道为直行车道。</p>\n<p><img src=\"https://tp.mnks.cn/tp_220.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右转车道：所指示的车道为右转车道。</p>\n<p><img src=\"https://tp.mnks.cn/tp_221.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左转车道：所指示的车道为左转车道。</p>', 'https://timg.mnks.cn/20230927bf9cf88f.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399781381873665, 460, 'd6d84', 0002, '这个标志是何含义？ ', 'A、直行和左转合用车道 B、禁止左转和掉头车道 C、掉头和左转合用车道 D、分向行驶车道', '直行和左转合用车道 ', '禁止左转和掉头车道 ', '掉头和左转合用车道 ', '分向行驶车道', 'C', '<b>掉头和左转合用车道</b>：所指示的车道为<b>掉头和左转合用车道</b>。', 'https://timg.mnks.cn/20230927f23e494b.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399781419622401, 461, '6e000', 0002, '这个标志是何含义？ ', 'A、小型车车道 B、小型车专用车道 C、机动车车道 D、多乘员车辆专用车道', '小型车车道 ', '小型车专用车道 ', '机动车车道 ', '多乘员车辆专用车道', 'C', '<p><img src=\"https://tp.mnks.cn/tp_230.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>机动车车道：</b>所指示车道只供机动车行驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_229.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>机动车行驶：前方道路只供机动车行驶。</p>', 'https://timg.mnks.cn/20230927e479843a.jpg', '', 0005, 0.28);
INSERT INTO `question` VALUES (1733399781465759746, 462, 'ea07a', 0002, '这个标志是何含义？ ', 'A、非机动车车道 B、禁止自行车通行车道 C、自行车专用车道 D、停放自行车路段', '非机动车车道 ', '禁止自行车通行车道 ', '自行车专用车道 ', '停放自行车路段', 'A', '<p><img src=\"https://tp.mnks.cn/tp_232.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>非机动车道</b>：<b>所指示车道</b>只供非机动车行驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_231.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>非机动车行驶：前方道路只供非机动车行驶。</p>', 'https://timg.mnks.cn/20230927f694a275.jpg', '', 0005, 0.28);
INSERT INTO `question` VALUES (1733399781516091393, 463, '60767', 0002, '这个标志是何含义？ ', 'A、公交车专用车道 B、BRT车辆专用车道 C、大型客车专用车道 D、多乘员车专用车道', '公交车专用车道 ', 'BRT车辆专用车道 ', '大型客车专用车道 ', '多乘员车专用车道', 'B', '<p><img src=\"//wx4.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>快速公交系统（BRT）专用车道</b>：表示该车道仅供快速公交（BRT）车辆通行。</p>\n<p><img src=\"//wx2.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>公交专用车道：表示该车道仅供公交车辆通行。</p>', 'https://timg.mnks.cn/20230926f743ba16.jpg', '', 0005, 0.30);
INSERT INTO `question` VALUES (1733399781562228737, 464, '12bed', 0002, '这个标志是何含义？ ', 'A、小型汽车专用车道 B、机动车专用车道 C、多乘员车辆专用车道 D、出租汽车专用车道', '小型汽车专用车道 ', '机动车专用车道 ', '多乘员车辆专用车道 ', '出租汽车专用车道', 'C', '<p><img src=\"https://tp.mnks.cn/tp_234.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>多乘员车辆</b>专用车道：表示<b>该车道</b>仅供多乘员的车辆通行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_230.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>机动车车道：表示该车道仅供机动车通行。<br><img src=\"https://tp.mnks.cn/tp_229.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>机动车行驶：表示该道路仅供机动车通行。</p>', 'https://timg.mnks.cn/20230927abacb50f.jpg', '', 0004, 0.24);
INSERT INTO `question` VALUES (1733399781599977473, 465, '6f189', 0002, '这个标志是何含义？ ', 'A、直线行驶车道 B、左转行驶车道 C、右转行驶车道 D、分向行驶车道', '直线行驶车道 ', '左转行驶车道 ', '右转行驶车道 ', '分向行驶车道', 'D', '<b>分向行驶车道</b>：各车道车辆应按照标志指示方向行驶。', 'https://timg.mnks.cn/20230927d3f3cb17.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399781792915457, 466, '81a32', 0002, '这个标志是何含义？ ', 'A、地点距离 B、行驶路线 C、行驶方向 D、终点地名', '地点距离 ', '行驶路线 ', '行驶方向 ', '终点地名', 'A', '<b>地点距离：</b>指示前方道路要经过的重要公路编号、道路名、地名和距离。', 'https://timg.mnks.cn/20230927e014cb23.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399781864218625, 467, '35735', 0002, '这属于哪一类标志？ ', 'A、警告标志 B、禁令标志 C、指示标志 D、指路标志', '警告标志 ', '禁令标志 ', '指示标志 ', '指路标志', 'D', '<b>指路标志</b>：蓝底、白图形、白边框、蓝色衬边；形状为长方形和正方形，个别标志例外；作用是指引道路信息，为驾驶人传递道路方向、地点、距离等信息。<br><b>指路标志一般由以下几点信息组成：1、有文字表示地点；2、有箭头表示道路方向；3、有数字表示距离。</b>', 'https://timg.mnks.cn/20230927e4ebe641.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399781914550273, 468, '00624', 0002, '这个标志是何含义？ ', 'A、地点和距离预告 B、十字交叉路口预告 C、分道信息预告 D、道路分岔处预告', '地点和距离预告 ', '十字交叉路口预告 ', '分道信息预告 ', '道路分岔处预告', 'B', '<p><img src=\"https://tp.mnks.cn/tp_238.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>十字交叉路口</b>：告知前方是<b>十字交叉路口</b>、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。</p>\n<p><img src=\"https://tp.mnks.cn/tp_237.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>大交通量的四车道以上公路交叉路口预告：预告前方交叉路口形式、交叉公路的编号或交叉公路的名称、通往方向信息、地理方向信息以及距前方交叉路口的距离。</p>', 'https://timg.mnks.cn/20230927e3f4cf24.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399781952299009, 469, '30b06', 0002, '这个标志是何含义？ ', 'A、交叉路口预告 B、车道方向预告 C、分道信息预告 D、分岔处预告', '交叉路口预告 ', '车道方向预告 ', '分道信息预告 ', '分岔处预告', 'A', '指路标志【大交通量的四车道以上公路<b>交叉路口预告</b>】：预告前方交叉路口形式、交叉公路的编号或交叉公路的名称、通往方向信息、地理方向信息以及距前方交叉路口的距离。 <br>如图所示：<b>预告距离前方500米处的交叉路口，左转弯行驶通往远大路；右转弯行驶通往林园路；直行通往知泉路</b>。', 'https://timg.mnks.cn/20230927d97af49b.jpg', '', 0005, 0.48);
INSERT INTO `question` VALUES (1733399781990047745, 470, '6d67a', 0002, '这个标志是何含义？ ', 'A、Y形交叉路口预告 B、十字交叉路口预告 C、丁字交叉路口预告 D、道路分叉处预告', 'Y形交叉路口预告 ', '十字交叉路口预告 ', '丁字交叉路口预告 ', '道路分叉处预告', 'C', '<p><img src=\"https://tp.mnks.cn/tp_239.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>丁字路口：告知前方是丁字交叉路口、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。</p>\n<p><img src=\"https://tp.mnks.cn/tp_238.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>十字交叉路口：告知前方是十字交叉路口、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。</p>\n<p><img src=\"https://tp.mnks.cn/tp_240.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>Y形路口：告知前方是Y形交叉路口及通往方向信息。</p>', 'https://timg.mnks.cn/20230927c5f4d568.jpg', '', 0003, 0.08);
INSERT INTO `question` VALUES (1733399782031990785, 471, 'f4472', 0002, '这个标志是何含义？ ', 'A、环形交叉路口预告 B、十字交叉路口预告 C、丁字交叉路口预告 D、Y形交叉路口预告', '环形交叉路口预告 ', '十字交叉路口预告 ', '丁字交叉路口预告 ', 'Y形交叉路口预告', 'D', '<p><img src=\"https://tp.mnks.cn/tp_240.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>Y形路口：</b>告知前方是Y形交叉路口及通往方向信息。</p>\n<p><img src=\"https://tp.mnks.cn/tp_239.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>丁字路口：告知前方是丁字交叉路口、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。</p>\n<p><img src=\"https://tp.mnks.cn/tp_238.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>十字交叉路口：告知前方是十字交叉路口、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。</p>', 'https://timg.mnks.cn/20230927d3f359e6.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399782078128129, 472, '07682', 0002, '这个标志是何含义？ ', 'A、环形交叉路口预告 B、十字交叉路口预告 C、互通立体交叉预告 D、Y形交叉路口预告', '环形交叉路口预告 ', '十字交叉路口预告 ', '互通立体交叉预告 ', 'Y形交叉路口预告', 'A', '<p></p>\n<p><img src=\"https://tp.mnks.cn/tp_241.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>环形交叉路口预告：</b>告知前方是环形交叉路口、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。</p>\n<p><img src=\"https://tp.mnks.cn/tp_242.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>互通式立体交叉预告：告知前方互通式立体交叉路口的形式、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。</p>', 'https://timg.mnks.cn/20230927f2de201f.jpg', '', 0005, 0.27);
INSERT INTO `question` VALUES (1733399782115876865, 473, '87d56', 0002, '这个标志是何含义？ ', 'A、十字交叉路口预告 B、互通式立体交叉预告 C、Y形交叉路口预告 D、环形交叉路口预告', '十字交叉路口预告 ', '互通式立体交叉预告 ', 'Y形交叉路口预告 ', '环形交叉路口预告', 'B', '<p><img src=\"https://tp.mnks.cn/tp_242.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>互通式立体交叉预告</b>：告知前方互通式立体交叉路口的形式、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。<img src=\"https://tp.mnks.cn/tp_241.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>环形交叉路口预告：告知前方是环形交叉路口、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。</p>', 'https://timg.mnks.cn/20230927b1d47fc2.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399782157819905, 474, '49d2b', 0002, '这个标志是何含义？ ', 'A、高速公路终点预告 B、高速公路入口预告 C、高速公路起点预告 D、高速公路出口预告', '高速公路终点预告 ', '高速公路入口预告 ', '高速公路起点预告 ', '高速公路出口预告', 'B', '【绿底标志在高速，看图识字就可以】<b>入口预告</b>：预告前方进入高速公路或城市快速路入口的距离及行车方向。', 'https://timg.mnks.cn/20230927eaf35e5b.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399782195568642, 475, '4aea3', 0002, '这个标志是何含义？ ', 'A、高速公路右侧出口预告 B、高速公路下一出口预告 C、高速公路地点、方向预告 D、高速公路左侧出口预告', '高速公路右侧出口预告 ', '高速公路下一出口预告 ', '高速公路地点', '方向预告 D', 'C', '<p><img src=\"https://tp.mnks.cn/tp_265.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>地点、方向预告</b>：指示前方高速公路或城市快速路的两个行驶方向。</p>\n<p><img src=\"https://tp.mnks.cn/tp_266.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>地点距离预告：预告高速公路或城市快速路前方所有经管的重要的地点、道路的名称和距离。</p>', 'https://timg.mnks.cn/20230927b5e3fad7.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399782237511681, 476, '41ba2', 0002, '这个标志是何含义？ ', 'A、高速公路终点地名预告 B、高速公路行驶路线预告 C、高速公路行驶方向预告 D、高速公路地点距离预告', '高速公路终点地名预告 ', '高速公路行驶路线预告 ', '高速公路行驶方向预告 ', '高速公路地点距离预告', 'D', '<p><img src=\"https://tp.mnks.cn/tp_266.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>地点距离预告：</b>预告高速公路或城市快速路前方所有经管的重要的地点、道路的名称和距离。</p>\n<p><img src=\"https://tp.mnks.cn/tp_265.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>地点、方向预告：指示前方高速公路或城市快速路的两个行驶方向。</p>', 'https://timg.mnks.cn/20230927db0bf33a.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399782283649026, 477, '224fa', 0002, '这个标志是何含义？ ', 'A、高速公路界牌编号 B、高速公路里程编号 C、高速公路命名编号 D、高速公路路段编号', '高速公路界牌编号 ', '高速公路里程编号 ', '高速公路命名编号 ', '高速公路路段编号', 'C', '如图所示编号，指示<b>高速公路命名编号</b>，作为指路标志的路线信息。字母G，为“国”字拼音的首字母。', 'https://timg.mnks.cn/20230927f1c66082.jpg', '', 0004, 0.26);
INSERT INTO `question` VALUES (1733399782338174978, 478, '59f4a', 0002, '这个标志是何含义？ ', 'A、高速公路下一出口预告 B、高速公路右侧出口预告 C、高速公路目的地预告 D、高速公路左侧出口预告', '高速公路下一出口预告 ', '高速公路右侧出口预告 ', '高速公路目的地预告 ', '高速公路左侧出口预告', 'A', '<b>下一出口预告</b>：预告下一出口的信息和距离。', 'https://timg.mnks.cn/20230927e9075a18.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399782384312322, 479, '0cc93', 0002, '这个标志是何含义？ ', 'A、高速公路下一出口预告 B、高速公路右侧出口预告 C、高速公路目的地预告 D、高速公路左侧出口预告', '高速公路下一出口预告 ', '高速公路右侧出口预告 ', '高速公路目的地预告 ', '高速公路左侧出口预告', 'B', '<p><img src=\"https://tp.mnks.cn/tp_269.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>右侧出口预告</b>：预告前方右侧出口的距离、出口编号及行车方向。</p>\n<p><img src=\"https://tp.mnks.cn/tp_270.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左侧出口预告：预告前方左侧出口的距离、出口编号及行车方向。</p>', 'https://timg.mnks.cn/20230927cabce98d.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399782417866754, 480, 'e88f7', 0002, '这个标志是何含义？ ', 'A、高速公路下一出口预告 B、高速公路右侧出口预告 C、高速公路目的地预告 D、高速公路左侧出口预告', '高速公路下一出口预告 ', '高速公路右侧出口预告 ', '高速公路目的地预告 ', '高速公路左侧出口预告', 'D', '<p><img src=\"https://tp.mnks.cn/tp_270.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>左侧出口预告</b>：预告前方左侧出口的距离、出口编号及行车方向。</p>\n<p><img src=\"https://tp.mnks.cn/tp_269.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右侧出口预告：预告前方右侧出口的距离、出口编号及行车方向。</p>', 'https://timg.mnks.cn/20230927c2d5f992.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399782455615490, 481, 'ff8a3', 0002, '这个标志是何含义？ ', 'A、高速公路起点 B、高速公路出口 C、高速公路入口 D、高速公路终点', '高速公路起点 ', '高速公路出口 ', '高速公路入口 ', '高速公路终点', 'A', '<p><img src=\"https://tp.mnks.cn/tp_271.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>高速公路起点</b>：指示高速公路或城市快速路的起点。</p>\n<p><img src=\"https://tp.mnks.cn/tp_272.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>高速公路终点：指示高速公路或城市快速路的终点。</p>', 'https://timg.mnks.cn/20230927bdf0846b.jpg', '', 0004, 0.21);
INSERT INTO `question` VALUES (1733399782535307265, 482, '11692', 0002, '这个标志是何含义？ ', 'A、高速公路起点预告 B、高速公路出口预告 C、高速公路入口预告 D、高速公路终点预告', '高速公路起点预告 ', '高速公路出口预告 ', '高速公路入口预告 ', '高速公路终点预告', 'D', '<b>终点预告：</b>预告高速公路或城市快速路终点的距离。', 'https://timg.mnks.cn/20230927ec156477.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399782573056001, 483, '4c6b9', 0002, '这个标志是何含义？ ', 'A、高速公路报警电话号码 B、高速公路交通广播频率 C、高速公路救援电话号码 D、高速公路服务电话号码', '高速公路报警电话号码 ', '高速公路交通广播频率 ', '高速公路救援电话号码 ', '高速公路服务电话号码', 'B', '<b>道路交通信息</b>：指示高速公路或城市快速路上收听交通信息广播的频率。', 'https://timg.mnks.cn/20230927f3ab92bc.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399782610804738, 484, 'c89ea', 0002, '这个标志是何含义？ ', 'A、停车领卡 B、停车缴费 C、停车检查 D、ETC通道', '停车领卡 ', '停车缴费 ', '停车检查 ', 'ETC通道', 'A', '<p><img src=\"https://tp.mnks.cn/tp_274.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>停车领卡</b>：进入高速公路或城市快速路收费站入口，需<b>停车领卡</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_275.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>设有电子不停车收费（ETC）车道的收费站</p>', 'https://timg.mnks.cn/20230927c1bc6bdb.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399782644359169, 485, '675b6', 0002, '这个标志是何含义？ ', 'A、高速公路领卡处 B、高速公路收费处 C、高速公路检查站 D、设有ETC的收费站', '高速公路领卡处 ', '高速公路收费处 ', '高速公路检查站 ', '设有ETC的收费站', 'D', 'ETC = Electronic Toll Collection，电子不停车收费系统。 <br>是指车辆在通过收费站时，通过车载设备实现车辆识别、信息写入（入口）并自动从预先绑定的IC卡或银行帐户上扣除相应资金（出口），是国际上正在努力开发并推广普及的一种用于道路、大桥和隧道的电子收费系统。 <br>图中标有“ETC”、“收费”字样，可以判断是设有ETC的收费站。', 'https://timg.mnks.cn/20230927bf2c49b9.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399782686302209, 486, '0dc12', 0002, '这个标志是何含义？ ', 'A、高速公路ETC车道 B、高速公路缴费车道 C、高速公路检查车道 D、高速公路领卡车道', '高速公路ETC车道 ', '高速公路缴费车道 ', '高速公路检查车道 ', '高速公路领卡车道', 'A', '<b>ETC车道指示</b>：指示前方收费站的电子不停车收费车道，黄色箭头表示ETC车辆的行驶方向。', 'https://timg.mnks.cn/20230927a64c298c.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399782728245250, 487, 'd6319', 0002, '这个标志是何含义？ ', 'A、高速公路特殊天气最高速度 B、高速公路特殊天气建议速度 C、高速公路特殊天气最低速度 D、高速公路特殊天气平均速度', '高速公路特殊天气最高速度 ', '高速公路特殊天气建议速度 ', '高速公路特殊天气最低速度 ', '高速公路特殊天气平均速度', 'B', '<b>高速公路特殊天气建议速度</b>指路标志，提醒车辆驾驶人在雨、雪、雾等特殊天气下，根据能看清楚道路两侧的白色半圆状车距确认线数量来确定相应的<b>建议行驶速度</b>。', 'https://timg.mnks.cn/20230927a9a496bb.jpg', '', 0004, 0.23);
INSERT INTO `question` VALUES (1733399782774382594, 488, '8c8ef', 0002, '这个标志是何含义？ ', 'A、高速公路公用电话 B、高速公路报警电话 C、高速公路紧急电话 D、高速公路救援电话', '高速公路公用电话 ', '高速公路报警电话 ', '高速公路紧急电话 ', '高速公路救援电话', 'C', '<p><img src=\"//wx4.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>紧急电话：</b>指示高速公路紧急电话的位置。</p>\n<p><img src=\"//wx3.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>救援电话：指示救援电话号码。标志中号码为示例。</p>', 'https://timg.mnks.cn/20230926e1c43203.jpg', '', 0005, 0.36);
INSERT INTO `question` VALUES (1733399782824714241, 489, '5ea1b', 0002, '这个标志是何含义？ ', 'A、高速公路收费站预告 B、高速公路服务区预告 C、高速公路避险处预告 D、高速公路客车站预告', '高速公路收费站预告 ', '高速公路服务区预告 ', '高速公路避险处预告 ', '高速公路客车站预告', 'B', '<b>服务区预告</b>：预告前方服务区的位置和服务区能提供的服务，<b>服务区一般可提供停车、加油、维修、餐饮、超市、住宿等综合性服务</b>。', 'https://timg.mnks.cn/20230927b9ff391d.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399782875045889, 490, 'f5a1d', 0002, '这个标志预告什么？ ', 'A、高速公路服务区预告 B、高速公路避险处预告 C、高速公路客车站预告 D、高速公路停车场预告', '高速公路服务区预告 ', '高速公路避险处预告 ', '高速公路客车站预告 ', '高速公路停车场预告', 'D', '<p><img src=\"https://tp.mnks.cn/tp_284.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>高速公路停车场预告</b>：预告前方停车场（供车辆停放的场地）的距离和位置。【标志上无茶杯】</p>\n<p><img src=\"https://tp.mnks.cn/tp_283.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>高速公路停车区预告：预告前方停车区（设置的临时停靠点，不允许长时间停靠）的位置。【标志上有茶杯】</p>', 'https://timg.mnks.cn/20230927a6bf20f9.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399782912794626, 491, '830a8', 0002, '这个标志是何含义？ ', 'A、高速公路停车区预告 B、高速公路避险处预告 C、高速公路服务区预告 D、高速公路停车场预告', '高速公路停车区预告 ', '高速公路避险处预告 ', '高速公路服务区预告 ', '高速公路停车场预告', 'A', '<p><img src=\"https://tp.mnks.cn/tp_283.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>高速公路停车区预告</b>：预告前方停车区（设置的临时停靠点，不允许长时间停靠）的位置。【标志上有茶杯】</p>\n<p><img src=\"https://tp.mnks.cn/tp_284.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>高速公路停车场预告：预告前方停车场（供车辆停放的场地）的距离和位置。【标志上无茶杯】</p>', 'https://timg.mnks.cn/20230927c1c2a2c2.jpg', '', 0005, 0.43);
INSERT INTO `question` VALUES (1733399783101538306, 492, '05796', 0002, '这个标志是何含义？ ', 'A、高速公路服务区 B、高速公路加油站 C、高速公路休息区 D、高速公路客车站', '高速公路服务区 ', '高速公路加油站 ', '高速公路休息区 ', '高速公路客车站', 'B', '高速公路【<b>加油站</b>】：前方有通往<b>加油站</b>的入口，指示加油站的位置。', 'https://timg.mnks.cn/20230927ecd1efd4.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399783139287042, 493, '0c873', 0002, '这个标志是何含义？ ', 'A、高速公路紧急停车带 B、高速公路避让处所 C、高速公路停车区 D、高速公路客车站', '高速公路紧急停车带 ', '高速公路避让处所 ', '高速公路停车区 ', '高速公路客车站', 'A', '<b>紧急停车带</b>：前方路段设有紧急停车的位置。设在高速公路和一级公路上，供车辆发生故障或其他原因紧急停车使用的临时停车地带。 <br>紧急停车带只供紧急情况下使用，不得无故占用紧急停车带。', 'https://timg.mnks.cn/20230927c380de0b.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399783185424385, 494, 'cdd6a', 0002, '这个标志是何含义？ ', 'A、高速公路公用电话 B、高速公路报警电话 C、高速公路紧急电话 D、高速公路救援电话', '高速公路公用电话 ', '高速公路报警电话 ', '高速公路紧急电话 ', '高速公路救援电话', 'D', '<p><img src=\"//wx1.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>救援电话：</b>指示救援电话号码。标志中号码为示例。</p>\n<p><img src=\"//wx4.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>紧急电话：指示高速公路紧急电话的位置。</p>', 'https://timg.mnks.cn/20230926c0fe8c15.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399783239950337, 495, '3b20c', 0002, '这属于哪一种标志？ ', 'A、旅游区标志 B、作业区标志 C、告示标志 D、高速公路标志', '旅游区标志 ', '作业区标志 ', '告示标志 ', '高速公路标志', 'A', '旅游区标志：<b>颜色为棕底</b>、白字（图形）、白边框、棕色衬边；形状为矩形。', 'https://timg.mnks.cn/20230927e5e3aafb.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399783286087681, 496, '6d652', 0002, '这个标志是何含义？ ', 'A、旅游区类别 B、旅游区距离 C、旅游区方向 D、旅游区符号', '旅游区类别 ', '旅游区距离 ', '旅游区方向 ', '旅游区符号', 'B', '<p><img src=\"https://tp.mnks.cn/tp_286.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>旅游区距离</b>：告知道路前方旅游区的名称、有代表性的图形以及前往旅游区的距离。</p>\n<p><img src=\"https://tp.mnks.cn/tp_287.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>旅游区方向：告知道路前方旅游区的名称、有代表性的图形以及前往旅游区的方向。</p>', 'https://timg.mnks.cn/20230927ff32a889.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399783332225026, 497, '0b15e', 0002, '这个标志是何含义？ ', 'A、旅游区类别 B、旅游区距离 C、旅游区方向 D、旅游区符号', '旅游区类别 ', '旅游区距离 ', '旅游区方向 ', '旅游区符号', 'C', '<p><img src=\"https://tp.mnks.cn/tp_287.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>旅游区<b>方向</b></b>：告知道路前方旅游区的名称、有代表性的图形以及前往旅游区的<b>方向</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_286.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>旅游区距离：告知道路前方旅游区的名称、有代表性的图形以及前往旅游区的距离。</p>', 'https://timg.mnks.cn/20230927ca539544.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399783390945282, 498, '5e42e', 0002, '禁令标志的作用是什么？ ', 'A、禁止或限制行为 B、告知方向信息 C、指示车辆行进 D、警告前方危险', '禁止或限制行为 ', '告知方向信息 ', '指示车辆行进 ', '警告前方危险', 'A', '<b>禁令标志</b>的作用是<b>禁止、限制及相应解除</b>，道路使用者应严格遵守。', '', '禁令', 0003, 0.10);
INSERT INTO `question` VALUES (1733399783437082625, 499, '6002b', 0002, '指示标线的作用是什么？ ', 'A、禁止通行 B、指示通行 C、限制通行 D、警告提醒', '禁止通行 ', '指示通行 ', '限制通行 ', '警告提醒', 'B', '<b>指示标线的作用</b>是为道路使用者<b>指示</b>车行道、行车方向、路面边缘、人行道、停车位、停靠站及减速丘等。', '', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399783474831361, 500, '35d61', 0002, '路中白色虚线是什么标线？ ', 'A、禁止跨越对向车道中心线 B、限制跨越对向车道中心线 C、可跨越同向车道中心线 D、单向行驶车道分界中心线', '禁止跨越对向车道中心线 ', '限制跨越对向车道中心线 ', '可跨越同向车道中心线 ', '单向行驶车道分界中心线', 'C', '<b>可跨越同向车行道分界线</b>：道路中线为<b>白色虚线</b>，用于<b>分隔同向</b>行驶的交通流，在保证安全的情况下，<b>允许</b>车辆短时越线行驶。', 'https://timg.mnks.cn/20230927a10be529.jpg', '白色虚线', 0004, 0.17);
INSERT INTO `question` VALUES (1733399783537745921, 501, 'cc269', 0002, '路中黄色分界线的作用是什么？ ', 'A、分隔同向行驶的交通流 B、禁止跨越对向行车道 C、允许在左侧车道行驶 D、分隔对向行驶的交通流', '分隔同向行驶的交通流 ', '禁止跨越对向行车道 ', '允许在左侧车道行驶 ', '分隔对向行驶的交通流', 'D', '<b>可跨越对向车行道分界线</b>：道路中线为<b>黄色虚线</b>属于指示标线，用于分隔对向行驶的交通流。车辆在保证安全的情况下，<b>可以越线超车或转弯</b>。', 'https://timg.mnks.cn/20230927fdfe9fbf.jpg', '黄色分界线', 0004, 0.22);
INSERT INTO `question` VALUES (1733399783617437697, 502, '8341a', 0002, '路中两条双黄色虚线是什么标线？ ', 'A、单向分道线 B、可跨越分道线 C、潮汐车道线 D、双向分道线', '单向分道线 ', '可跨越分道线 ', '潮汐车道线 ', '双向分道线', 'C', '路中两条<b>双黄色虚线</b>是潮汐车道线。潮汐车道行车方向可随需要变化，以双黄虚线作为指示标线来指示潮汐车道的位置。<br>【拓展知识】<br>“潮汐车道”是指根据早晚交通流量不同情况，对有条件的道路，试点开辟潮汐车道，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数，提高车道使用效率。', 'https://timg.mnks.cn/20230927ab47ad02.jpg', '双黄色虚线', 0005, 0.27);
INSERT INTO `question` VALUES (1733399783701323777, 503, '7fdc8', 0002, '路两侧的车行道边缘白色实线是什么含义？ ', 'A、机动车可临时跨越 B、非机动车可临时跨越 C、车辆可临时跨越 D、禁止车辆跨越', '机动车可临时跨越 ', '非机动车可临时跨越 ', '车辆可临时跨越 ', '禁止车辆跨越', 'D', '<b>车行道边缘白色实线</b>：用白色实线划分机动车道与非机动车道分界（简称机非分界），<b>禁止车辆跨越</b>车行道边缘或机非分界。', 'https://timg.mnks.cn/20230927cabcd450.jpg', '白色实线', 0003, 0.11);
INSERT INTO `question` VALUES (1733399783743266818, 504, '36908', 0002, '路右侧车行道边缘白色虚线是什么含义？ ', 'A、车辆可临时越线行驶 B、车辆禁止越线行驶 C、应急车道分界线 D、人行横道分界线', '车辆可临时越线行驶 ', '车辆禁止越线行驶 ', '应急车道分界线 ', '人行横道分界线', 'A', '<b>车行道边缘白色虚线</b>：用白色虚线划分机非分界，<b>允许车辆临时越线行驶</b>，但应避让其他正常行驶的车辆、非机动车和行人。', 'https://timg.mnks.cn/20230927ff06b987.jpg', '白色虚线', 0004, 0.16);
INSERT INTO `question` VALUES (1733399783789404162, 505, '54c8e', 0002, '图中圈内黄色虚线是什么标线？ ', 'A、路口导向线 B、非机动车引导线 C、车道连接线 D、小型车转弯线', '路口导向线 ', '非机动车引导线 ', '车道连接线 ', '小型车转弯线', 'A', '<b>路口导向线</b>：可辅助车辆在路口行驶和转向。连接同向车行道分界线或机非分界线的路口导向线为白色圆曲（或直）虚线；<b>连接对向车行道分界线的路口导向线为黄色圆曲（或直）虚线</b>。', 'https://timg.mnks.cn/20230927e681f00e.jpg', '黄色虚线', 0003, 0.15);
INSERT INTO `question` VALUES (1733399783848124418, 506, '510d4', 0002, '图中圈内白色实线是什么标线？ ', 'A、可变导向车道线 B、导向车道线 C、方向引导线 D、单向行驶线', '可变导向车道线 ', '导向车道线 ', '方向引导线 ', '单向行驶线', 'B', '<b>有固定导向箭头的车道线，叫做导向车道线</b>。设置于路口驶入段的车行道分界线，车辆在该车道内应按车道行驶方向标志显示的指向行驶。具有固定行驶方向的为导向车道线；可根据信号灯或路面标志牌指示改变行驶方向的为可变导向车道线。', 'https://timg.mnks.cn/20230927c4aa94d1.jpg', '白色实线', 0005, 0.39);
INSERT INTO `question` VALUES (1733399783890067457, 507, 'e6365', 0002, '图中圈内两条白色虚线是什么标线？ ', 'A、小型车转弯线 B、掉头引导线 C、左转弯待转区线 D、交叉路停车线', '小型车转弯线 ', '掉头引导线 ', '左转弯待转区线 ', '交叉路停车线', 'C', '<b>左弯待转区线</b>： 在左转弯专用车道前端，伸入交叉路口内，以白色虚线划分的位置，指示左转弯车辆在直行时段进入待转区等待左转，但不得妨碍对向直行车辆的正常行驶。', 'https://timg.mnks.cn/20230927b4a45734.jpg', '白色虚线', 0004, 0.16);
INSERT INTO `question` VALUES (1733399783940399106, 508, 'ca309', 0002, '图中圈内的锯齿状白色实线是什么标线？ ', 'A、导向车道线 B、方向引导线 C、可变导向车道线 D、单向行驶线', '导向车道线 ', '方向引导线 ', '可变导向车道线 ', '单向行驶线', 'C', '<b>可变导向车道线</b>：顾名思义是能随时根据交通流量更改指示方向的车道，需按照对应的车道信号灯或路面标志牌的指示行驶。', 'https://timg.mnks.cn/20230927b82ca047.jpg', '锯齿状', 0005, 0.33);
INSERT INTO `question` VALUES (1733399783978147842, 509, 'f35a7', 0002, '图中圈内的路面标记是什么标线？ ', 'A、路口示意线 B、停车让行线 C、减速让行线 D、人行横道线', '路口示意线 ', '停车让行线 ', '减速让行线 ', '人行横道线', 'D', '<b>人行横道线（斑马线）</b>：<b>白色平行粗实线</b>，标示一定条件下准许行人横穿道路的路径，同时警示机动车驾驶人注意行人及非机动车过街。机动车禁止在人行横道线上停车，通过人行横道时应减速慢行，遇到行人需停车礼让。', 'https://timg.mnks.cn/20230927d00efb8c.jpg', '圈内', 0003, 0.11);
INSERT INTO `question` VALUES (1733399784057839618, 510, '994e8', 0002, '这个地面标记是什么标线？ ', 'A、人行横道预告 B、交叉路口预告 C、减速让行预告 D、停车让行预告', '人行横道预告 ', '交叉路口预告 ', '减速让行预告 ', '停车让行预告', 'A', '<b>人行横道预告</b>标识线：<b>白色菱形图案</b>，提示车辆驾驶人注意道路前方设置了人行横道线，需提前减速慢行。', 'https://timg.mnks.cn/20230927fc46cf86.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399784116559874, 511, 'c9409', 0002, '图中圈内的白色折线是什么标线？ ', 'A、减速行驶线 B、车距确认线 C、车速确认线 D、路口减速线', '减速行驶线 ', '车距确认线 ', '车速确认线 ', '路口减速线', 'B', '<b>白色折线车距确认线</b>：以白色折线作为车距确认标线，两道为一组，每组间隔50米，作为车辆驾驶人保持行车安全距离的参考。', 'https://timg.mnks.cn/20230927b9441bbe.jpg', '圈内', 0004, 0.23);
INSERT INTO `question` VALUES (1733399784183668738, 512, 'e0247', 0002, '图中圈内的白色半圆状标记是什么标线？ ', 'A、减速行驶线 B、车速确认线 C、车距确认线 D、路口减速线', '减速行驶线 ', '车速确认线 ', '车距确认线 ', '路口减速线', 'C', '<b>白色半圆状车距确认线</b>：以白色半圆状作为车距确认线，设置在气象条件复杂、影响安全行车的路段两侧，每隔50米一个，作为车辆驾驶人保持行车安全距离的参考。', 'https://timg.mnks.cn/20230927ac130541.jpg', '白色半圆状', 0004, 0.27);
INSERT INTO `question` VALUES (1733399784288526337, 513, 'c7af2', 0002, '路面由白色虚线和三角地带标线组成的是什么标线？ ', 'A、道路入口标线 B、可跨越式分道线 C、道路出口减速线 D、道路出口标线', '道路入口标线 ', '可跨越式分道线 ', '道路出口减速线 ', '道路出口标线', 'D', '<b>道路出口标线</b>，<b>一般由出口的纵向标线和三角地带标线组成</b>。用于引导驶出车辆的运行轨迹，使车辆安全交汇，减少与突出缘石碰撞的可能。', 'https://timg.mnks.cn/20230927aa56c654.jpg', '组成', 0005, 0.38);
INSERT INTO `question` VALUES (1733399784384995329, 514, 'f569d', 0002, '路面上白色虚线和三角地带标线组成的是什么标线？ ', 'A、道路入口标线 B、可跨越式分道线 C、道路入口减速线 D、道路出口标线', '道路入口标线 ', '可跨越式分道线 ', '道路入口减速线 ', '道路出口标线', 'A', '<b>道路入口标线为白色</b>，<b>一般由入口的纵向标线和三角地带标线组成</b>。用于引导驶入车辆的运行轨迹，使车辆安全交汇，减少与突出缘石碰撞的可能。', 'https://timg.mnks.cn/20230927d46bcf48.jpg', '组成', 0005, 0.31);
INSERT INTO `question` VALUES (1733399784498241538, 515, '2bcc7', 0002, '这种白色矩形标线框含义是什么？ ', 'A、出租车专用上下客停车位 B、平行式停车位 C、倾斜式停车位 D、垂直式停车位', '出租车专用上下客停车位 ', '平行式停车位 ', '倾斜式停车位 ', '垂直式停车位', 'B', '<b><b>平行</b>式停车位标线</b>：车辆<b>平行</b>于通道方向停放在停车位内。 <br>\n<p><img src=\"https://nimg.mnks.cn/yxlimg/8b5911c1.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>三种停车位', 'https://timg.mnks.cn/20230927c336f4dd.jpg', '白色矩形', 0004, 0.23);
INSERT INTO `question` VALUES (1733399784544378882, 516, '0597c', 0002, '红色圆圈内标线含义是什么？ ', 'A、临时停靠站 B、大客车停靠站 C、公交车停靠站 D、应急停车带', '临时停靠站 ', '大客车停靠站 ', '公交车停靠站 ', '应急停车带', 'C', '车种专用港湾式停靠站标线：与停靠站中间标注的车辆类型文字（如公交车、校车）相符的特定车辆停靠，除特定车辆外，其他车辆不得在此区域停留。图中标注<b>公交车</b>，因此是<b>公交车停靠站</b>。', 'https://timg.mnks.cn/20230927e6167a54.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399784598904833, 517, 'd71f1', 0002, '这个导向箭头是何含义？ ', 'A、指示车道 B、指示禁行 C、指示合流 D、指示直行', '指示车道 ', '指示禁行 ', '指示合流 ', '指示直行', 'D', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头<b>指向前方</b>，表示<b>直行</b>。', 'https://timg.mnks.cn/20230927fe82e435.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399784640847873, 518, '3e015', 0002, '这个导向箭头是何含义？ ', 'A、指示向左转弯或掉头 B、指示直行或向左变道 C、指示直行或左转弯 D、指示直行或掉头', '指示向左转弯或掉头 ', '指示直行或向左变道 ', '指示直行或左转弯 ', '指示直行或掉头', 'C', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头<b>指向前和指向左</b>，表示前方路口可以<b>直行或左转</b>。', 'https://timg.mnks.cn/20230927c28f2c30.jpg', '', 0005, 0.27);
INSERT INTO `question` VALUES (1733399784678596610, 519, '8f8c9', 0002, '这个导向箭头是何含义？ ', 'A、指示向右转弯或掉头 B、指示直行或右转弯 C、指示直行或向右变道 D、指示直行或掉头', '指示向右转弯或掉头 ', '指示直行或右转弯 ', '指示直行或向右变道 ', '指示直行或掉头', 'B', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头<b>指向前和指向右</b>，表示<b>直行或右转</b>。', 'https://timg.mnks.cn/20230927bc5bcb97.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399784712151041, 520, '1d64f', 0002, '这个导向箭头是何含义？ ', 'A、指示前方右转弯 B、指示向左变道 C、指示前方直行 D、指示前方左转弯', '指示前方右转弯 ', '指示向左变道 ', '指示前方直行 ', '指示前方左转弯', 'D', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头<b>指向左</b>，表示<b>左转</b>。', 'https://timg.mnks.cn/20230927feabe86d.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399784754094081, 521, '95fc2', 0002, '这个导向箭头是何含义？ ', 'A、指示前方右转弯 B、指示前方掉头 C、指示前方直行 D、指示向左变道', '指示前方右转弯 ', '指示前方掉头 ', '指示前方直行 ', '指示向左变道', 'A', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头<b>指向右</b>，表示<b>右转</b>。', 'https://timg.mnks.cn/20230927c69b6528.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399784791842817, 522, '1e53c', 0002, '这个导向箭头是何含义？ ', 'A、指示前方右转 B、指示前方掉头 C、指示前方直行 D、指示向左变道', '指示前方右转 ', '指示前方掉头 ', '指示前方直行 ', '指示向左变道', 'B', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头<b>指向反向</b>，表示<b>掉头</b>。', 'https://timg.mnks.cn/20230927f43cf6b7.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399784993169409, 523, 'a3f97', 0002, '路面上导向箭头是何含义？ ', 'A、指示前方道路是Y型交叉口 B、指示前方道路是分离式道路 C、指示前方道路仅可左右转弯 D、指示前方道路需向左右合流', '指示前方道路是Y型交叉口 ', '指示前方道路是分离式道路 ', '指示前方道路仅可左右转弯 ', '指示前方道路需向左右合流', 'C', '路面上导向箭头是指示前方道路仅可按照路面上的导向箭头行驶，主要给交通参与者提供导向方面的信息。箭头<b>指向左和指向右</b>，表示<b>左右转弯</b>。', 'https://timg.mnks.cn/20230927f5c97181.jpg', '', 0005, 0.42);
INSERT INTO `question` VALUES (1733399785194496001, 524, 'eac5d', 0002, '这个导向箭头是何含义？ ', 'A、提示前方有左弯或需向左合流 B、提示前方有右弯或需向右合流 C、提示前方右侧有障碍需向左合流 D、提示前方有左弯或需向左绕行', '提示前方有左弯或需向左合流 ', '提示前方有右弯或需向右合流 ', '提示前方右侧有障碍需向左合流 ', '提示前方有左弯或需向左绕行', 'A', '导向箭头颜色为白色，用以指示车辆的行驶方向，<b>箭头提示前方道路有左弯或需向左合流</b>。', 'https://timg.mnks.cn/20230927a7fb42dc.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399785236439041, 525, '78aff', 0002, '路面上导向箭头是何含义？ ', 'A、提示前方有左弯或需向左合流 B、提示前方有右弯或需向右合流 C、提示前方有障碍需向左合流 D、提示前方有左弯或需向左绕行', '提示前方有左弯或需向左合流 ', '提示前方有右弯或需向右合流 ', '提示前方有障碍需向左合流 ', '提示前方有左弯或需向左绕行', 'B', '导向箭头颜色为白色，用以指示车辆的行驶方向，<b>箭头提示前方道路有右弯或需向右合流</b>。', 'https://timg.mnks.cn/20230927bc5d322e.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399785274187778, 526, '2b151', 0002, '这种停车标线含义是什么？ ', 'A、专用待客停车位 B、专用上下客停车位 C、固定停车方向停车位 D、机动车限时停车位', '专用待客停车位 ', '专用上下客停车位 ', '固定停车方向停车位 ', '机动车限时停车位', 'C', '<b>固定停车方向停车位标线</b>：停车位对车辆停车方向有特殊要求，<b>箭头所指方向标示停车后车头的朝向</b>。', 'https://timg.mnks.cn/20230927b35e87af.jpg', '停车', 0003, 0.13);
INSERT INTO `question` VALUES (1733399785320325121, 527, 'f2df4', 0002, '这种白色矩形标线框含义是什么？ ', 'A、长时停车位 B、限时停车位 C、专用停车位 D、免费停车位', '长时停车位 ', '限时停车位 ', '专用停车位 ', '免费停车位', 'B', '<b>机动车限时停车位标线</b>：标示机动车只能在停车位内标注的时段停放，其他时段禁止停放。', 'https://timg.mnks.cn/20230927adbceb10.jpg', '白色矩形', 0004, 0.19);
INSERT INTO `question` VALUES (1733399785366462466, 528, '42e64', 0002, '红色圆圈内标线含义是什么？ ', 'A、临时停靠站 B、港湾式停靠站 C、应急停车带 D、公交车停靠站', '临时停靠站 ', '港湾式停靠站 ', '应急停车带 ', '公交车停靠站', 'B', '<b>港湾式停靠站标线</b>：标示车辆通向专门的分离引道的路径和停靠位置，<b>由渐变段引道白色虚线、正常段外缘白色实线或白色填充线组成</b>。', 'https://timg.mnks.cn/20230927c425cee5.jpg', '', 0004, 0.21);
INSERT INTO `question` VALUES (1733399785412599809, 529, 'd17ab', 0002, '图中圈内白色虚线是什么标线？ ', 'A、小型车转弯线 B、车道连接线 C、非机动车引导线 D、路口导向线', '小型车转弯线 ', '车道连接线 ', '非机动车引导线 ', '路口导向线', 'D', '<b>路口导向线</b>：可辅助车辆在路口行驶和转向。<b>连接同向车行道分界线或机非分界线的路口导向线为白色圆曲（或直）虚线；</b>连接对向车行道分界线的路口导向线为黄色圆曲（或直）虚线。', 'https://timg.mnks.cn/20230927bd323cb2.jpg', '白色虚线', 0004, 0.16);
INSERT INTO `question` VALUES (1733399785471320066, 530, '55d48', 0002, '路中心黄色虚线属于哪一类标线？ ', 'A、指示标线 B、禁止标线 C、警告标志 D、辅助标线', '指示标线 ', '禁止标线 ', '警告标志 ', '辅助标线', 'A', '根据GB 5768.3-2009《道路交通标志和标线第3部分：道路交通标线》的规定：<b>黄色虚线</b>，可跨越对向车行道分界线（可跨越道路中心线）用以分隔对向行驶的交通流。车辆在保证安全的情况下，可以越线超车或转弯。起到指示的作用，<b>属于指示标线</b>。', 'https://timg.mnks.cn/20230927e524d8d4.jpg', '黄色虚线', 0005, 0.42);
INSERT INTO `question` VALUES (1733399785513263105, 531, '4ef4f', 0002, '这个路面数字标记是何含义？ ', 'A、保持车距标记 B、最小间距标记 C、速度限制标记 D、道路编号标记', '保持车距标记 ', '最小间距标记 ', '速度限制标记 ', '道路编号标记', 'C', '<b>路面限速标记字符</b>：白色字符表示相应车行道上车辆行驶的最低限速值；黄色字符表示相应车行道上车辆行驶的最高限速值。', 'https://timg.mnks.cn/20230927fdf18ac5.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399785559400450, 532, '029f7', 0002, '这个路面标记是何含义？ ', 'A、最低限速为100公里/小时 B、平均速度为100公里/小时 C、解除100公里/小时限速 D、最高限速为100公里/小时', '最低限速为100公里/小时 ', '平均速度为100公里/小时 ', '解除100公里/小时限速 ', '最高限速为100公里/小时', 'D', '路面限速标记字符：白色字符表示相应车行道上车辆行驶的最低限速值；<b>黄色字符</b>表示相应车行道上车辆行驶的<b>最高限速值</b>。', 'https://timg.mnks.cn/20230927fe537cb2.jpg', '', 0005, 0.28);
INSERT INTO `question` VALUES (1733399785605537793, 533, '768c0', 0002, '这个路面标记是何含义？ ', 'A、最低限速为80公里/小时 B、平均速度为80公里/小时 C、解除80公里/小时限速 D、最高限速为80公里/小时', '最低限速为80公里/小时 ', '平均速度为80公里/小时 ', '解除80公里/小时限速 ', '最高限速为80公里/小时', 'A', '路面限速标记字符：<b>白色字符</b>表示相应车行道上车辆行驶的<b>最低限速值</b>；黄色字符表示相应车行道上车辆行驶的最高限速值。', 'https://timg.mnks.cn/20230927b75ecf88.jpg', '', 0005, 0.38);
INSERT INTO `question` VALUES (1733399785651675138, 534, '4684b', 0002, '这个路面标记是何含义？ ', 'A、自行车专用道 B、非机动车道 C、摩托车专用道 D、电瓶车专用道', '自行车专用道 ', '非机动车道 ', '摩托车专用道 ', '电瓶车专用道', 'B', '<b>非机动车路面标记</b>：施划于车道起点或车道中，表示该车道为非机动车道，禁止机动车占道通行。', 'https://timg.mnks.cn/20230927a98965fd.jpg', '', 0005, 0.32);
INSERT INTO `question` VALUES (1733399785702006785, 535, '05ecf', 0002, '路中心的双黄实线属于哪一类标线？ ', 'A、指示标线 B、辅助标线 C、警告标志 D、禁止标线', '指示标线 ', '辅助标线 ', '警告标志 ', '禁止标线', 'D', '路中心的<b>双黄实线</b>属于<b><b>禁止</b>标线</b>，用于分隔对向行驶的交通流，并<b>禁止</b>双方向车辆越线或压线行驶。', 'https://timg.mnks.cn/20230927ccbd6d62.jpg', '双黄实线', 0004, 0.21);
INSERT INTO `question` VALUES (1733399785743949825, 536, 'b1b9e', 0002, '路中心双黄实线是何含义？ ', 'A、可跨越对向车道分界线 B、禁止跨越对向车行道分界线 C、双侧可跨越同向车道分界线 D、单向行驶车道分界线', '可跨越对向车道分界线 ', '禁止跨越对向车行道分界线 ', '双侧可跨越同向车道分界线 ', '单向行驶车道分界线', 'B', '<b>双黄实线禁止跨越对向</b>车行道分界线：道路中线为双黄实线，用于分隔对向行驶的交通流，并禁止双方向车辆越线或压线行驶。', 'https://timg.mnks.cn/20230927d4dbc969.jpg', '双黄实线', 0002, 0.06);
INSERT INTO `question` VALUES (1733399785781698562, 537, '485cd', 0002, '路中心黄色虚实线是何含义？ ', 'A、实线一侧禁止越线 B、虚线一侧禁止越线 C、实线一侧允许越线 D、两侧均可越线行驶', '实线一侧禁止越线 ', '虚线一侧禁止越线 ', '实线一侧允许越线 ', '两侧均可越线行驶', 'A', '<b>黄色虚实线</b>禁止跨越对向车行道分界线：<b>实线一侧禁止车辆越线或压线行驶</b>，<b>虚线一侧准许车辆暂时越线或转弯</b>。越线行驶的车辆应避让正常行驶的车辆。', 'https://timg.mnks.cn/20230927cd7a4d3d.jpg', '虚实线', 0004, 0.17);
INSERT INTO `question` VALUES (1733399785832030209, 538, '75818', 0002, '路中心的黄色斜线填充是何含义？ ', 'A、可跨越对向车道分界线 B、双侧可跨越同向车道分界线 C、禁止跨越对向车行道分界线 D、单向行驶车道分界线', '可跨越对向车道分界线 ', '双侧可跨越同向车道分界线 ', '禁止跨越对向车行道分界线 ', '单向行驶车道分界线', 'C', '<b>黄色斜线填充</b>的双黄实线，<b>禁止跨越对向</b>车行道分界线。', 'https://timg.mnks.cn/20230927af9dfb5c.jpg', '黄色斜线', 0003, 0.10);
INSERT INTO `question` VALUES (1733399785878167554, 539, '23bf9', 0002, '路中心白色实线是何含义？ ', 'A、禁止跨越对向车行道分界线 B、双侧可跨越同向车道分界线 C、禁止跨越同向车行道分界线 D、单侧可跨越同向车道分界线', '禁止跨越对向车行道分界线 ', '双侧可跨越同向车道分界线 ', '禁止跨越同向车行道分界线 ', '单侧可跨越同向车道分界线', 'C', '<b>禁止跨越同向车行道分界线</b>：白色实线，禁止车辆跨越车行道分界线进行变换车道或借道超车。', 'https://timg.mnks.cn/20230927d3453681.jpg', '白色实线', 0004, 0.23);
INSERT INTO `question` VALUES (1733399785920110594, 540, 'aafb6', 0002, '路缘石上的黄色虚线是何含义？ ', 'A、禁止临时停车 B、禁止上下人员 C、禁止长时停车 D、禁止装卸货物', '禁止临时停车 ', '禁止上下人员 ', '禁止长时停车 ', '禁止装卸货物', 'C', '路缘石上的<b>黄色虚线</b>，<b>禁止路边长时间停、放车辆</b>，但一般情况下允许装卸货物或上下人员等的临时停放。 <br>黄色实线，禁止路边停、放车辆。', 'https://timg.mnks.cn/20230927b8a39958.jpg', '黄色虚线', 0004, 0.23);
INSERT INTO `question` VALUES (1733399785966247937, 541, 'd4089', 0002, '路缘石上的黄色实线是何含义？ ', 'A、仅允许上下人员 B、仅允许装卸货物 C、禁止长时间停车 D、禁止停放车辆', '仅允许上下人员 ', '仅允许装卸货物 ', '禁止长时间停车 ', '禁止停放车辆', 'D', '如图所示，路缘石上的<b>黄色实线，禁止路边停、放车辆</b>；黄色虚线，禁止路边长时间停、放车辆，但一般情况下允许装卸货物或上下人员等的临时停放。', 'https://timg.mnks.cn/20230927ddee6e0e.jpg', '黄色实线', 0004, 0.24);
INSERT INTO `question` VALUES (1733399786008190978, 542, '877a7', 0002, '图中圈内白色横实线是何含义？ ', 'A、停止线 B、让行线 C、减速线 D、待转线', '停止线 ', '让行线 ', '减速线 ', '待转线', 'A', '<b>停止线</b>：信号灯路口的<b>白色横实线</b>，表示车辆等待放行信号或者停车让行的停车位置的路面标线，并且不能超过或压下该线。 <br>【拓展知识】 <br><img src=\"https://sucimg.itc.cn/sblog/je6a55e4407d444c00aa40802e4e21c05\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>以停止线为界，停止线到路口的区域称为停止线以内，停止线与车道间的区域称为<b>停止线以外</b>。', 'https://timg.mnks.cn/20230927b43e15df.jpg', '白色横实线', 0003, 0.12);
INSERT INTO `question` VALUES (1733399786045939714, 543, '2a33c', 0002, '路口最前端的双白实线是什么含义？ ', 'A、等候放行线 B、停车让行线 C、减速让行线 D、左弯待转线', '等候放行线 ', '停车让行线 ', '减速让行线 ', '左弯待转线', 'B', '<b>停车让行线</b>：两条平行白色实线和一个白色“停”字，表示车辆在此路口应停车让干道车辆先行。', 'https://timg.mnks.cn/20230927be381802.jpg', '双白实线', 0003, 0.11);
INSERT INTO `question` VALUES (1733399786079494146, 544, '74031', 0002, '路口最前端的双白虚线是什么含义？ ', 'A、等候放行线 B、停车让行线 C、减速让行线 D、左弯待转线', '等候放行线 ', '停车让行线 ', '减速让行线 ', '左弯待转线', 'C', '<b>减速让行线</b>：两条平行白色虚线和一个白色倒三角形，表示车辆在此路口应减速让干道车辆先行。', 'https://timg.mnks.cn/20230927f9e26402.jpg', '双白虚线', 0005, 0.34);
INSERT INTO `question` VALUES (1733399786121437186, 545, 'a30d3', 0002, '图中圈内三角填充区域是什么标线？ ', 'A、网状线 B、停车线 C、减速线 D、导流线', '网状线 ', '停车线 ', '减速线 ', '导流线', 'D', '<b>导流线的颜色为白色</b>，起到引导和分流车辆的作用。与道路中心线相连时，也可用黄色。标线形式可分为单实线、<b>V形线</b>和斜纹线三种。车辆在交叉口需按规定的路线行驶，不得压线或越线行驶。', 'https://timg.mnks.cn/20230927c14409da.jpg', '三角填充区域', 0004, 0.19);
INSERT INTO `question` VALUES (1733399786159185922, 546, 'd344d', 0002, '这个路面标记是什么标线？ ', 'A、禁驶区 B、网状线 C、导流线 D、中心圈', '禁驶区 ', '网状线 ', '导流线 ', '中心圈', 'D', '<b>菱形中心圈</b>标线，设在平面交叉路口的中心，用以区分车辆大、小转弯或作为交叉口车辆左右转弯的指示，车辆不得压线行驶。', 'https://timg.mnks.cn/20230927a52d63ed.jpg', '', 0005, 0.30);
INSERT INTO `question` VALUES (1733399786201128962, 547, '83656', 0002, '这个路面标记是什么标线？ ', 'A、禁驶区 B、网状线 C、中心圈 D、导流线', '禁驶区 ', '网状线 ', '中心圈 ', '导流线', 'C', '<b>圆形中心圈</b>，设置在交叉路口中心的白色圆形或菱形区域，用以区分车辆大、小转弯，及交叉路口车辆左右转弯的指示，车辆不得压线行驶。', 'https://timg.mnks.cn/20230927aa91e81d.jpg', '', 0004, 0.17);
INSERT INTO `question` VALUES (1733399786243072001, 548, 'bbed2', 0002, '图中所示红圈内道路右侧车行道边缘白色虚线的含义是什么？ ', 'A、人行横道分界线 B、机动车可临时越线行驶 C、机动车禁止越线行驶 D、潮汐车道分界线', '人行横道分界线 ', '机动车可临时越线行驶 ', '机动车禁止越线行驶 ', '潮汐车道分界线', 'B', '【虚线可越】<b>车行道边缘白色虚线</b>：用白色虚线划分机非分界，<b>允许车辆临时越线行驶</b>，但应避让其他正常行驶的车辆、非机动车和行人。', 'https://timg.mnks.cn/20230927ff06b987.jpg', '白色虚线', 0004, 0.20);
INSERT INTO `question` VALUES (1733399786301792257, 549, '61ec8', 0002, '图中路口中央黄色路面标记是什么标线？ ', 'A、中心圈 B、导流线 C、网状线 D、停车区', '中心圈 ', '导流线 ', '网状线 ', '停车区', 'C', '<b>网状线</b>：严格禁止一切车辆长时或临时停车，防止交通阻塞。', 'https://timg.mnks.cn/20230927bf365000.jpg', '黄色', 0003, 0.14);
INSERT INTO `question` VALUES (1733399786343735297, 550, '34536', 0002, '图中圈内两条黄色虚线间的区域是何含义？ ', 'A、大客车专用车道 B、营运客车专用车道 C、出租车专用车道 D、公交专用车道', '大客车专用车道 ', '营运客车专用车道 ', '出租车专用车道 ', '公交专用车道', 'D', '<b>公交车专用车道线</b>：由黄色虚线及白色文字组成，表示除公交车外，其他车辆及行人不得进入该车道。', 'https://timg.mnks.cn/20230927d74c1c71.jpg', '黄色虚线间', 0001, 0.03);
INSERT INTO `question` VALUES (1733399786381484034, 551, '3c29a', 0002, '道路最左侧白色虚线区域是何含义？ ', 'A、多乘员车辆专用车道 B、小型客车专用车道 C、未载客出租车专用车道 D、大型客车专用车道', '多乘员车辆专用车道 ', '小型客车专用车道 ', '未载客出租车专用车道 ', '大型客车专用车道', 'A', '<b>多乘员车辆专用车道线</b>：由白色虚线及白色文字“多乘员专用”组成，表示该车道为有多个乘车人的多乘员车辆专用的车道，未载乘客或乘员数未达规定的车辆不得入内行驶。', 'https://timg.mnks.cn/20230927ecef771b.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399786415038465, 552, '86f18', 0002, '路面上的黄色标记是何含义？ ', 'A、禁止转弯 B、禁止掉头 C、允许掉头 D、禁止直行', '禁止转弯 ', '禁止掉头 ', '允许掉头 ', '禁止直行', 'B', '<b>禁止掉头标记</b>：由黄色导向箭头和黄色叉形标记左右组合而成，黄色叉形标记位于左侧，表示本路口或区间禁止车辆掉头。', 'https://timg.mnks.cn/20230927fbc124f8.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399786465370114, 553, '27e7c', 0002, '路面上的黄色标记是何含义？ ', 'A、禁止左转 B、禁止掉头 C、禁止右转 D、禁止直行', '禁止左转 ', '禁止掉头 ', '禁止右转 ', '禁止直行', 'C', '<b>箭头指向右，表示禁止右转。</b>由黄色导向箭头和黄色叉形标记左右组合而成的禁止转弯标记，黄色叉形标记位于左侧，表示本路口或区间禁止车辆转弯。', 'https://timg.mnks.cn/20230927bee8043e.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399786503118850, 554, '033e5', 0002, '路面上的白色标线是何含义？ ', 'A、车行道横向减速标线 B、道路施工提示标线 C、车行道纵向减速标线 D、车道变少提示标线', '车行道横向减速标线 ', '道路施工提示标线 ', '车行道纵向减速标线 ', '车道变少提示标线', 'A', '车行道<b>横向</b>减速标线：警告车辆驾驶人本车道需减速慢行。', 'https://timg.mnks.cn/20230927e7671dfc.jpg', '', 0004, 0.17);
INSERT INTO `question` VALUES (1733399786633142274, 555, 'cd5b1', 0002, '路面上的黄色标线是何含义？ ', 'A、车行道变多标线 B、路面宽度渐变标线 C、接近障碍物标线 D、施工路段提示线', '车行道变多标线 ', '路面宽度渐变标线 ', '接近障碍物标线 ', '施工路段提示线', 'B', '<b>三车行道变为双车行道渐变段标线</b>（路面宽度渐变）：警告车辆驾驶人路宽或车道数变化，应谨慎行车，并禁止超车。', 'https://timg.mnks.cn/20230927b57a0efd.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399786683473922, 556, '3f6d6', 0002, '路面上的黄色填充标线是何含义？ ', 'A、接近移动障碍物标线 B、加宽隔离带标线 C、接近障碍物标线 D、接近狭窄路面标线', '接近移动障碍物标线 ', '加宽隔离带标线 ', '接近障碍物标线 ', '接近狭窄路面标线', 'C', '<b>接近障碍物标线</b>：指示路面有固定性障碍物，警告车辆驾驶人谨慎行车，引导交通流顺畅驶离障碍物区域。', 'https://timg.mnks.cn/20230927b6493842.jpg', '', 0005, 0.37);
INSERT INTO `question` VALUES (1733399786737999874, 557, '30115', 0002, '这种黄黑相间的倾斜线条是什么标记？ ', 'A、减速标记 B、实体标记 C、突起标记 D、立面标记', '减速标记 ', '实体标记 ', '突起标记 ', '立面标记', 'D', '黄黑相间的斜纹线条标记是<b>立面标记</b>警告标线，提醒驾驶人注意，在车行道或近旁有高出路面的构造物。', 'https://timg.mnks.cn/20230927b7edd0ca.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399786779942914, 558, '1de22', 0002, '路面上的菱形块虚线是何含义？ ', 'A、车行道纵向减速标线 B、道路施工提示标线 C、车行道横向减速标线 D、车道变少提示标线', '车行道纵向减速标线 ', '道路施工提示标线 ', '车行道横向减速标线 ', '车道变少提示标线', 'A', '车行道<b>纵向</b>减速标线：警告车辆驾驶人本车道需减速慢行。', 'https://timg.mnks.cn/20230927f8b6159c.jpg', '', 0004, 0.18);
INSERT INTO `question` VALUES (1733399786813497346, 559, 'cb878', 0001, '路中心黄色虚线的含义是分隔对向交通流，在保证安全的前提下，可越线超车或转弯。', NULL, '对', '错', '空', '空', '对', '<b>可跨越对向车行道分界线</b>：道路中线为<b>黄色虚线</b>属于指示标线，用于分隔对向行驶的交通流。车辆在保证安全的情况下，<b>可以越线超车或转弯</b>。', 'https://timg.mnks.cn/20230927e524d8d4.jpg', '黄色虚线|可越线', 0003, 0.14);
INSERT INTO `question` VALUES (1733399786851246082, 560, '37796', 0001, '这个地面标记的含义是预告前方设有交叉路口。', NULL, '对', '错', '空', '空', '错', '<b>白色菱形图案</b>是人行横道预告标识线，指示车辆驾驶人注意道路前方设置了人行横道线，需提前减速慢行。', 'https://timg.mnks.cn/20230927fc46cf86.jpg', '交叉路口', 0004, 0.16);
INSERT INTO `question` VALUES (1733399786893189122, 561, '94a14', 0001, '路中心的双黄实线作用是分隔对向交通流，在保证安全的前提下，可越线超车或转弯。', NULL, '对', '错', '空', '空', '错', '<b>双黄实线禁止跨越</b>对向车行道分界线：道路中心为双黄实线，用于分隔对向行驶的交通流，并禁止双方向车辆越线或压线行驶。', 'https://timg.mnks.cn/20230927ccbd6d62.jpg', '双黄实线|可越线', 0002, 0.05);
INSERT INTO `question` VALUES (1733399786926743554, 562, 'c7414', 0002, '请判断这是一个什么手势？ ', 'A、不准前方车辆通行手势 B、准许右方直行车辆通行手势 C、准许车辆左转弯手势 D、准许右方车辆右转弯手势', '不准前方车辆通行手势 ', '准许右方直行车辆通行手势 ', '准许车辆左转弯手势 ', '准许右方车辆右转弯手势', 'A', '<p><b>【停止信号】</b><br>动作要领：交警左臂向前上方直伸，掌心向前；右臂沿裤中缝对齐，无任何动作。<br>手势示意：<b>交警面向的车辆不准通行</b>。</p>\n<p><img src=\"https://timg.mnks.cn/20230928e1684774.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927ec409cf4.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399786968686593, 563, 'bef02', 0002, '图中这组交警手势是什么信号？ ', 'A、靠边停车信号 B、左转弯待转信号 C、右转弯待转信号 D、直行信号', '靠边停车信号 ', '左转弯待转信号 ', '右转弯待转信号 ', '直行信号', 'B', '<p><b>【左转弯待转信号】</b><br>动作要领：交警左臂向左下方平伸，掌心向下；左臂与手掌平直向下方摆动。<br>手势示意：准许交警左侧的左转弯车辆进入路口，沿左转弯行驶方向靠近路口中心，等待左转弯信号。</p>\n<p><img src=\"https://timg.mnks.cn/20230928b8243114.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927fda38691.jpg', '', 0005, 0.38);
INSERT INTO `question` VALUES (1733399787014823938, 564, 'f5269', 0002, '请判断这是一个什么手势？ ', 'A、不准前方车辆通行手势 B、准许右方直行车辆通行手势 C、准许车辆左转弯手势 D、准许右方车辆右转弯手势', '不准前方车辆通行手势 ', '准许右方直行车辆通行手势 ', '准许车辆左转弯手势 ', '准许右方车辆右转弯手势', 'C', '<p><b>【左转弯信号】</b>交警摆<b>＞</b>（大于号）<br>动作要领：交警右臂向前平伸，掌心向前；左臂与手掌平直向右前方摆动，掌心向右。<br>手势示意：<b>准许交警左侧的车辆左转弯</b>，在不妨碍被放行车辆通行的情况下可以掉头。</p>\n<p><img src=\"https://timg.mnks.cn/20230928ec80de06.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。\n<p></p>', 'https://timg.mnks.cn/20230927eb567cc3.jpg', '', 0004, 0.24);
INSERT INTO `question` VALUES (1733399787081932801, 565, 'ba64d', 0002, '请判断这是一个什么手势？ ', 'A、不准前方车辆通行手势 B、准许右方直行车辆通行手势 C、准许车辆左转弯手势 D、准许车辆右转弯手势', '不准前方车辆通行手势 ', '准许右方直行车辆通行手势 ', '准许车辆左转弯手势 ', '准许车辆右转弯手势', 'D', '<p><b>【右转弯信号】</b>交警摆<b>＜</b>（小于号）<br>动作要领：交警左臂向前平伸，掌心向前；右臂与手掌平直向左前方摆动，掌心向左。<br>手势示意：<b>准许交警右侧的车辆右转弯</b>。</p>\n<p><img src=\"https://timg.mnks.cn/20230928a18dbb9c.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927bc74a191.jpg', '', 0005, 0.34);
INSERT INTO `question` VALUES (1733399787136458754, 566, 'b3f60', 0002, '请判断这是一个什么手势？ ', 'A、不准前方车辆通行手势 B、准许右方直行车辆通行手势 C、准许车辆左转弯手势 D、准许右方车辆右转弯手势', '不准前方车辆通行手势 ', '准许右方直行车辆通行手势 ', '准许车辆左转弯手势 ', '准许右方车辆右转弯手势', 'B', '<p><b>【直行信号】</b><br>动作要领：交警左臂向左平伸，掌心向前；右臂向右平伸，掌心向前，向左摆动。<br>手势示意：<b>准许交警右侧的直行车辆通行</b>。</p>\n<p><img src=\"https://timg.mnks.cn/20230928d5ceaa2e.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927f9d90f9a.jpg', '', 0004, 0.21);
INSERT INTO `question` VALUES (1733399787182596097, 567, '50791', 0002, '这一组交通警察手势是什么信号？ ', 'A、左转弯信号 B、停止信号 C、右转弯信号 D、靠边停车信号', '左转弯信号 ', '停止信号 ', '右转弯信号 ', '靠边停车信号', 'B', '<p><b>【停止信号】</b><br>动作要领：交警左臂向前上方直伸，掌心向前；右臂沿裤中缝对齐，无任何动作。<br>手势示意：交警面向的车辆不准通行。</p>\n<p><img src=\"https://timg.mnks.cn/20230928e1684774.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927ec409cf4.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399787220344834, 568, '76d87', 0002, '这一组交通警察手势是什么信号？ ', 'A、直行信号 B、转弯信号 C、停止信号 D、靠边停车信号', '直行信号 ', '转弯信号 ', '停止信号 ', '靠边停车信号', 'A', '<p><b>【直行信号】</b><br>动作要领：交警左臂向左平伸，掌心向前；右臂向右平伸，掌心向前，向左摆动。<br>手势示意：准许交警右侧的直行车辆通行。</p>\n<p><img src=\"https://timg.mnks.cn/20230928d5ceaa2e.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927f9d90f9a.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399787253899265, 569, '48172', 0002, '这一组交通警察手势是什么信号？ ', 'A、靠边停车信号 B、左转弯待转信号 C、左转弯信号 D、右转弯信号', '靠边停车信号 ', '左转弯待转信号 ', '左转弯信号 ', '右转弯信号', 'C', '<p><b>【左转弯信号】</b>交警摆＞（大于号）<br>动作要领：交警右臂向前平伸，掌心向前；左臂与手掌平直向右前方摆动，掌心向右。<br>手势示意：准许交警左侧的车辆左转弯，在不妨碍被放行车辆通行的情况下可以掉头。</p>\n<p><img src=\"https://timg.mnks.cn/20230928ec80de06.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927eb567cc3.jpg', '', 0005, 0.30);
INSERT INTO `question` VALUES (1733399787295842305, 570, 'e7424', 0002, '这一组交通警察手势是什么信号？ ', 'A、靠边停车信号 B、左转弯待转信号 C、减速慢行信号 D、左转弯信号', '靠边停车信号 ', '左转弯待转信号 ', '减速慢行信号 ', '左转弯信号', 'D', '<p><b>【左转弯信号】</b><br>动作要领：交警右臂向前平伸，掌心向前；左臂与手掌平直向右前方摆动，掌心向右。<br>手势示意：准许交警左侧的车辆左转弯，在不妨碍被放行车辆通行的情况下可以掉头。</p>\n<p><img src=\"https://timg.mnks.cn/20230928ec80de06.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927beb761bc.jpg', '', 0005, 0.39);
INSERT INTO `question` VALUES (1733399787350368257, 571, '0da57', 0002, '这一组交通警察手势是什么信号？ ', 'A、左转弯待转信号 B、左转弯信号 C、减速慢行信号 D、右转弯信号', '左转弯待转信号 ', '左转弯信号 ', '减速慢行信号 ', '右转弯信号', 'A', '<p><b>【左转弯待转信号】</b><br>动作要领：交警左臂向左下方平伸，掌心向下；左臂与手掌平直向下方摆动。<br>手势示意：准许交警左侧的左转弯车辆进入路口，沿左转弯行驶方向靠近路口中心，等待左转弯信号。</p>\n<p><img src=\"https://timg.mnks.cn/20230928b8243114.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927fda38691.jpg', '', 0005, 0.47);
INSERT INTO `question` VALUES (1733399787388116994, 572, 'a3036', 0002, '这一组交通警察手势是什么信号？ ', 'A、靠边停车信号 B、减速慢行信号 C、变道信号 D、右转弯信号', '靠边停车信号 ', '减速慢行信号 ', '变道信号 ', '右转弯信号', 'B', '<p><b>【减速慢行信号】</b><br>动作要领：交警右臂向前方平伸，掌心向下；右臂与手掌平直向下方摆动。<br>手势示意：交警右侧的车辆应当减速慢行。</p>\n<p><img src=\"https://timg.mnks.cn/20230928cb1be248.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927e4f50785.jpg', '', 0005, 0.42);
INSERT INTO `question` VALUES (1733399787434254337, 573, '6f23b', 0002, '这一组交通警察手势是什么信号？ ', 'A、右转弯信号 B、减速慢行信号 C、变道信号 D、靠边停车信号', '右转弯信号 ', '减速慢行信号 ', '变道信号 ', '靠边停车信号', 'C', '<p><b>【变道信号】</b><br>动作要领：交警右臂向前平伸，掌心向左；右臂向左水平摆动。<br>手势示意：准许交警面向的车辆应由正在行驶的车道改变到交警指定的车道，减速慢行。</p>\n<p><img src=\"https://timg.mnks.cn/20230928b9e3748d.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927d03d0dd1.jpg', '', 0005, 0.51);
INSERT INTO `question` VALUES (1733399787476197377, 574, 'e6da6', 0002, '这一组交通警察手势是什么信号？ ', 'A、右转弯信号 B、减速慢行信号 C、左转弯待转信号 D、靠边停车信号', '右转弯信号 ', '减速慢行信号 ', '左转弯待转信号 ', '靠边停车信号', 'A', '<p><b>【右转弯信号】</b><br>动作要领：交警左臂向前平伸，掌心向前；右臂与手掌平直向左前方摆动，掌心向左。<br>手势示意：准许交警右侧的车辆右转弯。</p>\n<p><img src=\"https://timg.mnks.cn/20230928a18dbb9c.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927bc74a191.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399787543306242, 575, '70d0f', 0002, '这一组交通警察手势是什么信号？ ', 'A、靠边停车信号 B、减速慢行信号 C、停止信号 D、右转弯信号', '靠边停车信号 ', '减速慢行信号 ', '停止信号 ', '右转弯信号', 'D', '<p><b>【右转弯信号】</b><br>动作要领：交警左臂向前平伸，掌心向前；右臂与手掌平直向左前方摆动，掌心向左。<br>手势示意：准许交警右侧的车辆右转弯。</p>\n<p><img src=\"https://timg.mnks.cn/20230928a18dbb9c.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p>【<b>识别技巧</b>】交警的<b>面部</b>对着交警的哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230927af8dcb57.jpg', '', 0005, 0.31);
INSERT INTO `question` VALUES (1733399787593637889, 576, 'ce6f4', 0002, '请判断这是一个什么标志？ ', 'A、警告标志 B、禁令标志 C、指示标志 D、指路标志', '警告标志 ', '禁令标志 ', '指示标志 ', '指路标志', 'D', '<b>指路标志</b>的作用是指引道路信息，为驾驶人传递道路方向、地点、距离等信息。一般道路上的<b>指路标志</b>为蓝底、白图形、白边框、蓝色衬边；高速公路和城市快速路<b>指路标志</b>为绿底、白图形、白边框、绿色衬边。<b>指路标志</b>的形状为长方形和正方形，个别标志例外。', 'https://timg.mnks.cn/20230927e4ebe641.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399787635580929, 577, 'c53aa', 0002, '请判断这是一个什么标志？ ', 'A、警告标志 B、禁令标志 C、指示标志 D、指路标志', '警告标志 ', '禁令标志 ', '指示标志 ', '指路标志', 'B', '<b>禁令标志</b>的颜色为白底、红圈、红杠、黑图形；图形压杠；形状为圆形，个别标志例外；<b>禁令标志</b>的作用是禁止、限制及相应解除，道路使用者应严格遵守。', 'https://timg.mnks.cn/20230927cb4b2c10.jpg', '', 0003, 0.14);
INSERT INTO `question` VALUES (1733399787698495489, 578, 'a8c73', 0002, '请判断这是一个什么标志？ ', 'A、警告标志 B、禁令标志 C、指示标志 D、指路标志', '警告标志 ', '禁令标志 ', '指示标志 ', '指路标志', 'A', '<b>警告标志</b>的颜色为黄底、黑边、黑图形；形状为等边三角形（顶角朝上）或矩形，个别标志例外；<b>警告标志</b>的作用是警告车辆驾驶人、行人前方有危险，道路使用者需谨慎通过。', 'https://timg.mnks.cn/20230926f25f7cf4.jpg', '', 0005, 0.29);
INSERT INTO `question` VALUES (1733399787748827138, 579, 'c236f', 0002, '请判断这是一个什么标志？ ', 'A、警告标志 B、禁令标志 C、指示标志 D、指路标志', '警告标志 ', '禁令标志 ', '指示标志 ', '指路标志', 'C', '<b>指示标志</b>的颜色为<b>蓝底、白图形</b>；形状为圆形、长方形和正方形，个别标志例外；<b>指示标志</b>的作用是指示车辆、行人按标志指示的路线、方向行驶，道路使用者应遵循。', 'https://timg.mnks.cn/20230927aaf32b56.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399787803353090, 580, '415be', 0001, '以下交通标志表示除小客车和货车外，其他车辆可以直行。', NULL, '对', '错', '空', '空', '对', '图中组合标志的含义是<b>小客车和货车禁止直行</b>。附设在禁止直行标志下方的辅助标志，用以辅助说明具体禁止哪种车辆直行，因此，图中组合标志表示禁止小客车和货车直行，即<b>除小客车和货车外，其他车辆可以直行</b>。', 'https://timg.mnks.cn/20230927bc93ca75.jpg', '小客车和货车', 0004, 0.21);
INSERT INTO `question` VALUES (1733399787862073346, 581, 'c230c', 0002, '这属于哪一类标志？ ', 'A、指路标志 B、指示标志 C、禁令标志 D、警告标志', '指路标志 ', '指示标志 ', '禁令标志 ', '警告标志', 'A', '<b>指路标志的特征：</b>一般道路上的指路标志为蓝底、白图形、白边框、蓝色衬边；高速公路和城市快速路指路标志为绿底、白图形、白边框、绿色衬边。 <br><b>指路标志形状：</b>长方形和正方形，个别标志例外。 <br><b>指路标志的作用</b>：指引道路信息，为驾驶人传递道路方向、地点。距离等信息。 <br><b>图中指路标志，从左到右依次为：交叉路口预告、国道编号、高速公路地点距离。</b>', 'https://timg.mnks.cn/20230927be4651a0.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399787916599298, 582, 'cb7a5', 0002, '这属于哪一种标志？ ', 'A、作业区标志 B、告示标志 C、高速公路标志 D、旅游区标志', '作业区标志 ', '告示标志 ', '高速公路标志 ', '旅游区标志', 'D', '旅游区标志的颜色为棕底、白字（图形）、白边框、棕色衬边，形状为矩形；旅游区标志的作用是方便旅游者识别通往旅游区的方向和距离，了解旅游项目的类别。', 'https://timg.mnks.cn/20230927ded7a506.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399787996291074, 583, '5314d', 0002, '这属于哪一种标志？ ', 'A、禁令标志 B、指示标志 C、指路标志 D、警告标志', '禁令标志 ', '指示标志 ', '指路标志 ', '警告标志', 'A', '禁令标志是交通标志中主要标志的一种，对车辆加以禁止或限制的标志，禁令标志的颜色除个别标志外，通常为白底、红圈、红杠、黑图形，图形压杠；禁令标志的形状为圆形、八角形、顶角向下的等边三角形。 <br>如图所示的禁令标志，分别是停车让行、减速让行、禁止小型客车驶入。', 'https://timg.mnks.cn/20230927de677ce3.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399788046622721, 584, '92eea', 0002, '这属于哪一种标志？ ', 'A、指路标志 B、指示标志 C、禁令标志 D、警告标志', '指路标志 ', '指示标志 ', '禁令标志 ', '警告标志', 'B', '【蓝底白图是指示，不要混淆成警告】<b>指示标志</b>的颜色为蓝底、白图案；形状分为圆形、长方形和正方形。', 'https://timg.mnks.cn/20230927e670e919.jpg', '', 0004, 0.17);
INSERT INTO `question` VALUES (1733399788088565762, 585, '03ce2', 0002, '下列哪个标志禁止一切车辆长时间停放，临时停车不受限制？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '图1表示禁止停车；图2表示停车让行；图3表示禁止驶入；<b>图4表示禁止长时停车</b>。', 'https://timg.mnks.cn/20230927d0bb0921.jpg', '禁止|长时间停放', 0005, 0.31);
INSERT INTO `question` VALUES (1733399788130508802, 586, '7bf6f', 0002, '遇到下列哪个标志，你不需要主动让行？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '图1：【停车让行】八角停车让；图2：【减速让行】三角减速让；图3：【会车让行】红色箭头我方，表示我方让行；<b>图4：【会车先行】红色箭头在对向，表示对向车让行</b>。', 'https://timg.mnks.cn/20230927a2767c08.jpg', '不需要', 0003, 0.15);
INSERT INTO `question` VALUES (1733399788164063234, 587, 'ebaea', 0002, '以下交通标志中，表示禁止一切车辆和行人通行的是？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'A', '图1是<b>禁止通行</b>标志，表示<b>禁止一切车辆和行人通行</b>，此标志设在禁止通行的道路入口处；<br>图2是<b>禁止驶入</b>标志，表示禁止一切车辆驶入，此标志设在单行路的出口处或禁止驶入的路段入口；<br>图3是<b>禁止机动车驶入</b>标志，表示禁止各类机动车驶入，此标志设在禁止机动车通行路段的入口处；<br>图4是<b>禁止直行</b>标志，表示前方路口禁止一切车辆直行。<br>所以该题选择<b>图1</b>。<br><a href=\"http://www.jsyks.com/jtbz_1111/\" target=\"_blank\">更多禁令标志详解，可以进入驾驶员考试网《交通标志》频道查看</a>', 'https://timg.mnks.cn/20230927e06506a9.jpg', '禁止一切', 0004, 0.21);
INSERT INTO `question` VALUES (1733399788239560706, 588, '09d17', 0002, '下列哪个表示一般道路车道数变少？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'B', '图1表示错车道；<b>图2表示车道数变少</b>；图3表示车道数增加；图4表示隧道出口距离预告。', 'https://timg.mnks.cn/20230927e93f8e51.jpg', '变少', 0002, 0.07);
INSERT INTO `question` VALUES (1733399788281503746, 589, '03074', 0002, '下列哪个标志提示驾驶人下陡坡？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '图1是向左急转弯标志，警告驾驶人减速慢行；图2是连续弯路标志，警告驾驶人减速慢行；图3是反向弯路标志，警告驾驶人减速慢行；<b>图4是下坡路标志</b>，提醒驾驶人下陡坡小心驾驶。', 'https://timg.mnks.cn/20230927ce05a2b2.jpg', '下陡坡', 0001, 0.02);
INSERT INTO `question` VALUES (1733399788323446785, 590, 'a1e3d', 0002, '下列哪个标志提示驾驶人连续弯路？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'B', '图1表示向左急转弯，警告驾驶人减速慢行；<b>图2表示连续弯路</b>，警告驾驶人减速慢行；图3表示反向弯路，警告驾驶人减速慢行；图4表示环形交叉路口，警告驾驶人谨慎慢行，注意横向来车。', 'https://timg.mnks.cn/20230927ba3674f2.jpg', '连续弯路', 0003, 0.08);
INSERT INTO `question` VALUES (1733399788361195521, 591, 'a78a5', 0002, '下列高速公路交通标志与其含义对应的正确的一项是？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'C', '图1：高速公路终点；图2：高速公路服务区预告；<b>图3</b>：<b>高速公路紧急停车带</b>；图4：紧急电话。因此，<b>图3</b>的标志与含义对应。', 'https://timg.mnks.cn/20230926ea6f585f.jpg', '含义对应', 0004, 0.24);
INSERT INTO `question` VALUES (1733399788415721473, 592, '31b40', 0002, '下列哪个交通标志表示不能停车？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'B', '图1：禁止长时停车；<b>图2：禁止停车</b>；图3：停车检查；图4：停车让行。', 'https://timg.mnks.cn/20230927aa869142.jpg', '不能', 0004, 0.19);
INSERT INTO `question` VALUES (1733399788457664513, 593, '37fe2', 0001, '这种标志的作用是警告车辆驾驶人前方有危险，谨慎通行。', NULL, '对', '错', '空', '空', '对', '<b>警告标志的作用</b>是警告车辆驾驶人、行人前方有危险，道路使用者需谨慎通过。<b>警告标志的颜色</b>为黄底、黑边、黑图形；<b>形状为</b>等边三角形（顶角朝上）或矩形。个别标志例外。因此，题干表述正确。', 'https://timg.mnks.cn/20230926ba7d8324.jpg', '警告|谨慎通行', 0004, 0.23);
INSERT INTO `question` VALUES (1733399788495413250, 594, '11dbb', 0001, '看到这个标志的时候，您应该开启前照灯。', NULL, '对', '错', '空', '空', '对', '<b>隧道开车灯</b>：警告前方是无照明或照明不足的隧道，应打开前照灯，注意行驶。', 'https://timg.mnks.cn/20230927aff2d5b1.jpg', '前照灯', 0003, 0.11);
INSERT INTO `question` VALUES (1733399788549939201, 595, '9c2eb', 0001, '如图所示，这个标志设置在有人看守的铁路道口，提示驾驶人距有人看守的铁路道口的距离还有100米。', NULL, '对', '错', '空', '空', '错', '【有人看守是栅栏，<b>无人看守火车头</b>】驾驶机动车遇到<b>无人看守铁路道口</b>警告标志且下方设有<b>两道斜杠符号</b>时，警告前方距离<b>无人看守</b>的铁路道口<b>100米</b>，注意提前控制车速。', 'https://timg.mnks.cn/20230927b3b52124.jpg', '有人看守', 0004, 0.16);
INSERT INTO `question` VALUES (1733399788596076545, 596, '56848', 0001, '如图所示，铁路道口设置这个标志，是提示驾驶人前方路口有单股铁道。', NULL, '对', '错', '空', '空', '错', '<b>叉形符号</b>：表示多股铁路与道路相交，设在铁路道口标志上方。', 'https://timg.mnks.cn/20230927cb96ce9d.jpg', '单股铁道', 0003, 0.15);
INSERT INTO `question` VALUES (1733399788629630978, 597, '03284', 0001, '遇到这个标志时，您应该主动确认您与前车之间的距离。', NULL, '对', '错', '空', '空', '对', '图片是<b>注意保持车距</b>警告标志：警告前方路段经常发生车辆追尾事故，应<b>注意和前车保持安全距离</b>。', 'https://timg.mnks.cn/20230927afaf6772.jpg', '主动确认|距离', 0002, 0.06);
INSERT INTO `question` VALUES (1733399788684156929, 598, 'b4715', 0001, '这个标志是提醒注意潮汐车道。', NULL, '对', '错', '空', '空', '对', '<b>注意潮汐车道</b>：警告前方为潮汐车道，即前方车道上车辆行驶方向可随交通管理需要变化。<br>【拓展知识】<br>潮汐车道是指根据城市早晚交通流量的不同情况，对有条件的道路，通过车道灯的指示方向变化，调整主干道车道数量，控制车道的行驶方向，以达到加速引导车流的作用。', 'https://timg.mnks.cn/20230927edc8256f.jpg', '潮汐车道', 0002, 0.05);
INSERT INTO `question` VALUES (1733399788730294274, 599, 'fb5f0', 0001, '图中标志提示前方道路有Y形交叉路口，会有横向来车。', NULL, '对', '错', '空', '空', '对', '<p><img src=\"//wx3.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>Y形交叉路口</b>：警告前方道路有与上述图形相符的<b>Y形交叉路口</b>，应谨慎慢行，注意横向车辆。</p>\n<p><img src=\"//wx3.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>注意合流：警告前方有车辆汇合进来，需提高警惕。</p>', 'https://timg.mnks.cn/20230926fdb12179.jpg', 'Y形交叉', 0004, 0.23);
INSERT INTO `question` VALUES (1733399788776431617, 600, 'cc289', 0001, '图中标志提示前方道路是向右急转弯。', NULL, '对', '错', '空', '空', '错', '箭头指哪往哪急转，向左急转弯标志，警告前方道路<b>向左急转弯</b>，应减速慢行。', 'https://timg.mnks.cn/20230926db0f1fbf.jpg', '向右', 0003, 0.09);
INSERT INTO `question` VALUES (1733399788818374658, 601, 'e9c49', 0001, '图中标志提示前方路段是易发生车辆追尾的路段。', NULL, '对', '错', '空', '空', '错', '图中标志是告示前方为交通<b>事故易发路段</b>的警告标志，提醒驾驶人应谨慎驾驶。交通事故是总称，车辆追尾只是交通事故的一种，题目中只描述了追尾，因此是错误的是不严谨的，这个标志代表事故多发路段，提醒要谨慎驾驶。', 'https://timg.mnks.cn/20230927b51cc4a1.jpg', '车辆追尾', 0005, 0.28);
INSERT INTO `question` VALUES (1733399788864512002, 602, '4a23e', 0001, '这个标志的作用是用以警告车辆驾驶人谨慎慢行，注意横向来车。', NULL, '对', '错', '空', '空', '对', '<b>【十字交叉路口】</b>警告标志，警告前方道路有十字交叉路口，应谨慎慢行，<b>注意横向车辆</b>。', 'https://timg.mnks.cn/20230926f25f7cf4.jpg', '警告|谨慎慢行', 0004, 0.19);
INSERT INTO `question` VALUES (1733399788902260738, 603, '52045', 0001, '这个标志的含义是前方即将行驶至Y形交叉路口。', NULL, '对', '错', '空', '空', '错', '<p>如图所示，前方是<b>T形</b>交叉路口。<br><img src=\"https://tp.mnks.cn/tp_122.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b><b>T形</b>交叉路口</b></p>\n<p><img src=\"https://nimg.mnks.cn/yxlimg/832d357b.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>Y形交叉路口</p>', 'https://timg.mnks.cn/20230926b47024a8.jpg', 'Y形交叉', 0003, 0.13);
INSERT INTO `question` VALUES (1733399788940009474, 604, '9f6b9', 0001, '这个标志的含义是警告前方道路有障碍物，车辆减速绕行。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"https://tp.mnks.cn/tp_123.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>如图所示：急弯路警告标志，<b>警告前方道路向右急转弯</b>，应减速慢行。</p>', 'https://timg.mnks.cn/20230926a0fe73ff.jpg', '障碍物|减速绕行', 0002, 0.07);
INSERT INTO `question` VALUES (1733399788977758210, 605, 'adf73', 0001, '这个标志的含义是警告前方出现向左的急转弯路。', NULL, '对', '错', '空', '空', '对', '【<b>一急</b>二反三连续】<b>一个弯表示急转弯</b>，两个弯表示反向弯路，三个弯表示连续弯路。\n<p><img src=\"https://tp.mnks.cn/tp_124.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b><b>向左</b>急弯</b>：警告前方道路<b>向左</b>急转弯，应减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_123.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>向右急弯：警告前方道路向右急转弯，应减速慢行。</p>', 'https://timg.mnks.cn/20230926db0f1fbf.jpg', '向左', 0003, 0.09);
INSERT INTO `question` VALUES (1733399789015506946, 606, '09bc9', 0001, '这个标志的含义是警告前方道路易滑，注意慢行。', NULL, '对', '错', '空', '空', '错', '<p>【一急<b>二反</b>三连续】一个弯表示急转弯，<b>两个弯表示反向弯路</b>，三个弯表示连续弯路。<br><img src=\"https://tp.mnks.cn/tp_125.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>反向弯路：警告前方道路连续有两个方向相反的急弯路，应减速慢行。</b><br><img src=\"https://tp.mnks.cn/tp_142.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>易滑路段：警告前方路面较滑，易发生事故，应减速慢行。</p>', 'https://timg.mnks.cn/20230926c6daaf93.jpg', '易滑', 0003, 0.11);
INSERT INTO `question` VALUES (1733399789065838593, 607, '95829', 0001, '这个标志的含义是警告前方有两个相邻的反向转弯道路。', NULL, '对', '错', '空', '空', '错', '<p>【1急2反3连续】如图所示标志的含义是警告前方有<b>连续3个或以上方向相反</b>的弯路。<br><img src=\"https://tp.mnks.cn/tp_126.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>连续弯路：警告前方道路有连续三个或三个以上的方向相反的弯路，应减速慢行</b>。</p>\n<p><img src=\"https://tp.mnks.cn/tp_125.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>反向弯路：警告前方道路连续有两个方向相反的急弯路，应减速慢行。</p>', 'https://timg.mnks.cn/20230926e182c44a.jpg', '反向转弯', 0004, 0.19);
INSERT INTO `question` VALUES (1733399789116170242, 608, 'ba83d', 0001, '这个标志的含义是提醒前方桥面宽度变窄。', NULL, '对', '错', '空', '空', '错', '【<b>上窄下宽是窄路</b>，中间窄是窄桥】图中标志为<b>两侧变窄</b>警告标志，<b>是路面变窄不是桥面变窄</b>。常设在两侧变窄道路，提示司机看到此标志时应提前减速，前方道路即将变窄，车辆密集度增加，行使空间降低，易发生车祸，所以请一定要提前减速。', 'https://timg.mnks.cn/20230926ace5a4de.jpg', '桥面', 0005, 0.27);
INSERT INTO `question` VALUES (1733399789162307585, 609, 'eb1f7', 0001, '这个标志的含义是提醒前方右侧行车道或路面变窄。', NULL, '对', '错', '空', '空', '对', '<p>【<b>上窄下宽</b>是窄路，中间窄是窄桥】<br><img src=\"https://tp.mnks.cn/tp_131.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>右侧变窄</b>：警告前方是右侧车行道或路面变窄的路段，遇来车应减速避让。</p>\n<p><img src=\"https://tp.mnks.cn/tp_132.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左侧变窄：警告前方是左侧车行道或路面变窄的路段，遇来车应减速避让。</p>', 'https://timg.mnks.cn/20230926c46b6820.jpg', '右侧', 0002, 0.06);
INSERT INTO `question` VALUES (1733399789204250625, 610, '68fb4', 0001, '这个标志的含义是提醒前方左侧行车道或路面变窄。', NULL, '对', '错', '空', '空', '对', '<p>【<b>上窄下宽</b>是窄路，中间窄是窄桥】<br><img src=\"https://tp.mnks.cn/tp_132.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>左侧变窄：警告前方是左侧车行道或路面变窄的路段，遇来车应减速避让。</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_131.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>右侧变窄：警告前方是右侧车行道或路面变窄的路段，遇来车应减速避让。</p>', 'https://timg.mnks.cn/20230926e90b446c.jpg', '左侧', 0002, 0.06);
INSERT INTO `question` VALUES (1733399789250387969, 611, 'd5376', 0001, '这个标志的含义是提醒前方两侧行车道或路面变窄。', NULL, '对', '错', '空', '空', '错', '<p>【上窄下宽是窄路，<b>中间窄是窄桥</b>】<br><img src=\"https://tp.mnks.cn/tp_133.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>窄桥</b>：警告前方<b>桥面宽度变窄</b>，应谨慎驾驶。<br><img src=\"https://tp.mnks.cn/tp_130.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>两侧变窄：警告前方是车行道或路面变窄的路段，遇来车应减速避让。</p>', 'https://timg.mnks.cn/20230926c0ef5c16.jpg', '两侧|变窄', 0004, 0.26);
INSERT INTO `question` VALUES (1733399789292331010, 612, '542d4', 0001, '这个标志的含义是提醒前方道路变为不分离双向行驶路段。', NULL, '对', '错', '空', '空', '对', '<b>双向交通</b>警告标志，提醒前方是<b>不分离双向行驶</b>路段，应注意会车。因此，题干表述正确。', 'https://timg.mnks.cn/20230926ae1171f2.jpg', '不分离双向', 0004, 0.21);
INSERT INTO `question` VALUES (1733399789334274050, 613, '5458c', 0001, '这个标志的含义是警告车辆驾驶人前方是人行横道。', NULL, '对', '错', '空', '空', '错', '请注意！黄底黑字三角形，表示警告、注意、提醒，不要混淆成指示。如图所示<b>注意行人</b>标志，警告前方道路设有人行横道线，应减速慢行，<b>注意行人</b>。警告的是<b>注意</b>人行横道上的<b>行人</b>，不是注意人行横道。', 'https://timg.mnks.cn/20230926fa148a80.jpg', '人行横道', 0005, 0.35);
INSERT INTO `question` VALUES (1733399789380411394, 614, '0ece5', 0001, '这个标志的含义是警告车辆驾驶人前方是学校区域。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"https://tp.mnks.cn/tp_136.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>注意儿童</b>：警告前方是儿童频繁出入的地点，应减速慢行，<b>注意儿童</b>。</p>因此，题干表述错误。', 'https://timg.mnks.cn/20230926ae01c63a.jpg', '学校区域', 0004, 0.21);
INSERT INTO `question` VALUES (1733399789451714562, 615, '1f3da', 0001, '这个标志的含义是警告车辆驾驶人注意前方设有信号灯。', NULL, '对', '错', '空', '空', '对', '<b>注意信号灯</b>：警告前方路段设有信号灯，应按信号灯指示行车。', 'https://timg.mnks.cn/20230926b6220de8.jpg', '设有', 0002, 0.06);
INSERT INTO `question` VALUES (1733399789485268993, 616, '4912c', 0001, '这个标志的含义是提醒车辆驾驶人前方是傍山险路路段。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"https://tp.mnks.cn/tp_140.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>注意落石</b>：提醒前方是<b>有落石危险的傍山路段</b>，应注意落石。<br><img src=\"https://tp.mnks.cn/tp_143.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>傍山险路：提醒前方是傍山险路，应小心驾驶。</p>', 'https://timg.mnks.cn/20230926d76254d9.jpg', '傍山险路', 0004, 0.18);
INSERT INTO `question` VALUES (1733399789523017730, 617, '4c7a0', 0001, '这个标志的含义是提醒车辆驾驶人前方有很强的侧向风。', NULL, '对', '错', '空', '空', '对', '<b>注意横风</b>：提醒<b>前方路段经常有很强的侧向风</b>，应小心驾驶。', 'https://timg.mnks.cn/20230926adaba52d.jpg', '侧向风', 0003, 0.13);
INSERT INTO `question` VALUES (1733399789560766466, 618, 'ff099', 0001, '这个标志的含义是提醒车辆驾驶人前方是急转弯路段。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"https://tp.mnks.cn/tp_142.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br><b>易滑：警告前方路面较滑，易发生事故，应减速慢行。</b> <br><img src=\"https://tp.mnks.cn/tp_123.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>向右急弯路 <br><img src=\"https://tp.mnks.cn/tp_124.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>向左急弯路 <br></p>', 'https://timg.mnks.cn/20230926f496c9fb.jpg', '急转弯', 0003, 0.10);
INSERT INTO `question` VALUES (1733399789594320897, 619, '30490', 0001, '这个标志的含义是提醒车辆驾驶人前方是堤坝路段。', NULL, '对', '错', '空', '空', '错', '<img src=\"https://tp.mnks.cn/tp_143.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>如图所示，<b>傍山险路</b>警告标志，提醒车辆驾驶人前方是<b>傍山险路</b>，应小心驾驶。 <br>\n<p><img src=\"https://tp.mnks.cn/tp_144.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>堤坝路</p>', 'https://timg.mnks.cn/20230926a8f45d90.jpg', '堤坝', 0004, 0.18);
INSERT INTO `question` VALUES (1733399789632069633, 620, '08049', 0001, '这个标志的含义是提醒车辆驾驶人前方路段通过村庄或集镇。', NULL, '对', '错', '空', '空', '对', '村庄：提醒前方是<b>紧靠村庄、集镇且视线不良的路段</b>，应小心驾驶。', 'https://timg.mnks.cn/20230926e23a733d.jpg', '村庄或集镇', 0001, 0.02);
INSERT INTO `question` VALUES (1733399789674012673, 621, 'fdb07', 0001, '这个标志的含义是提醒车辆驾驶人前方是单向行驶并且照明不好的涵洞。', NULL, '对', '错', '空', '空', '错', '<b>注意隧道</b>警告标志，用以提醒车辆驾驶人谨慎驾驶，<b>注意前方为隧道 </b>。因此，题干表述错误。', 'https://timg.mnks.cn/20230926cae875e0.jpg', '单向行驶|涵洞', 0004, 0.24);
INSERT INTO `question` VALUES (1733399789715955714, 622, '34919', 0001, '这个标志的含义是提醒车辆驾驶人前方是车辆渡口。', NULL, '对', '错', '空', '空', '对', '<p><img src=\"https://tp.mnks.cn/tp_147.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>渡口：</b>提醒前方是车辆渡口，应谨慎驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_152.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>过水路面：提醒前方是过水路面或漫水桥路段，应停车察明水情，确认安全后，低速通过。</p>', 'https://timg.mnks.cn/20230926c1fa38ed.jpg', '渡口', 0002, 0.06);
INSERT INTO `question` VALUES (1733399789762093057, 623, 'e1d38', 0001, '这个标志的含义是提醒车辆驾驶人前方是桥头跳车较严重的路段。', NULL, '对', '错', '空', '空', '错', '<b>驼峰桥</b>：提醒前方是<b>拱度很大、影响视距</b>的驼峰桥，应谨慎驾驶。因此，题干表述错误。', 'https://timg.mnks.cn/20230926a177c323.jpg', '桥头跳车', 0004, 0.23);
INSERT INTO `question` VALUES (1733399789799841794, 624, 'e0a02', 0001, '这个标志的含义是提醒车辆驾驶人前方路面颠簸或有桥头跳车现象。', NULL, '对', '错', '空', '空', '对', '<p><img src=\"https://tp.mnks.cn/tp_149.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br><b>路面不平：提醒前方路面颠簸或有桥头跳车比较严重，应减速慢行。</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_148.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>驼峰桥：提醒前方是拱度很大、影响视距的驼峰桥，应谨慎驾驶。</p>\n<p><img src=\"https://tp.mnks.cn/tp_150.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>路面高突：提醒前方路面比正常路面突然高突，应减速慢行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_151.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>路面低洼：提醒前方路面比正常路面突然低洼，应减速慢行。</p>', 'https://timg.mnks.cn/20230926d3c83512.jpg', '路面颠簸', 0005, 0.30);
INSERT INTO `question` VALUES (1733399789854367746, 625, '313d5', 0001, '这个标志的含义是提醒车辆驾驶人前方是过水路面或漫水桥路段。', NULL, '对', '错', '空', '空', '对', '如图所示，<b>过水路面（漫水桥）</b>：提醒车辆驾驶人<b>前方是过水路面或漫水桥路段</b>，应停车察明水情，确认安全后，低速通过。', 'https://timg.mnks.cn/20230927ff652292.jpg', '过水路面', 0003, 0.09);
INSERT INTO `question` VALUES (1733399789896310785, 626, '1b1a6', 0001, '这个标志的含义是提醒车辆驾驶人前方是无人看守铁路道口。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"https://tp.mnks.cn/tp_153.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>如图所示<b>有人看守铁路道口</b>：警告前方是有人看守的铁路道口，应减速或停车观察，按照交通信号或管理人员的指挥通行。</p>\n<p><img src=\"https://tp.mnks.cn/tp_154.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>无人看守铁路道口：警告前方是无人看守的铁路道口，应按照交通信号指示通行，无交通信号时应减速或停车观察，确认安全后通过。</p>', 'https://timg.mnks.cn/20230927fc9f6b36.jpg', '无人看守', 0003, 0.12);
INSERT INTO `question` VALUES (1733399789959225345, 627, '817ce', 0001, '这个标志的含义是提醒车辆驾驶人前方是无人看守铁路道口。', NULL, '对', '错', '空', '空', '对', '<p><img src=\"https://tp.mnks.cn/tp_154.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>无人看守铁路道口</b>：警告前方是无人看守的铁路道口，应按照交通信号指示通行，无交通信号时应减速或停车观察，确认安全后通过。</p>\n<p><img src=\"https://tp.mnks.cn/tp_153.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>有人看守铁路道口：警告前方是有人看守的铁路道口，应减速或停车观察，按照交通信号或管理人员的指挥通行。</p>', 'https://timg.mnks.cn/20230927becf00c3.jpg', '无人看守', 0003, 0.12);
INSERT INTO `question` VALUES (1733399790001168386, 628, 'db169', 0001, '这个标志的含义是提醒车辆驾驶人前方是非机动车道。', NULL, '对', '错', '空', '空', '错', '<p>警告标志，提醒前方道路经常<b>有非机动车横穿或出入</b>，应注意慢行。<br><img src=\"https://tp.mnks.cn/tp_159.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b><b>注意非机动车【警告标志】</b></b></p>\n<p><b><img src=\"https://tp.mnks.cn/tp_232.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>非机动车道【指示标志】</b></p>', 'https://timg.mnks.cn/20230927a88de573.jpg', '非机动车道', 0005, 0.37);
INSERT INTO `question` VALUES (1733399790043111426, 629, 'f0275', 0001, '这个标志的含义是告示前方是拥堵路段，注意减速慢行。', NULL, '对', '错', '空', '空', '错', '<b>事故易发路段</b>，告示前方为交通<b>事故易发路段</b>，应谨慎驾驶。', 'https://timg.mnks.cn/20230927b51cc4a1.jpg', '拥堵路段', 0003, 0.12);
INSERT INTO `question` VALUES (1733399790093443073, 630, '36814', 0001, '这个标志的含义是告示前方道路施工，车辆左右绕行。', NULL, '对', '错', '空', '空', '错', '如图所示<b> [左右绕行] 标志</b>，用以告示驾驶人<b>前方道路有障碍物</b>，车辆应按照标志的指示减速绕行通过，<b>而不是因为道路施工要绕行</b>，题干中绕行的原因说法错误。<br>\n<p><img src=\"https://tp.mnks.cn/tp_163.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左右绕行标志</p>\n<p><img src=\"https://tp.mnks.cn/tp_167.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>前方施工标志</p>', 'https://timg.mnks.cn/20230927d944edaf.jpg', '道路施工', 0005, 0.32);
INSERT INTO `question` VALUES (1733399790131191809, 631, 'd120a', 0001, '这个标志的含义是告示前方道路有障碍物，车辆左侧绕行。', NULL, '对', '错', '空', '空', '对', '注意障碍物警告标志—<b>左侧绕行</b>：告示前方道路有障碍物，应按标志指示减速慢行，从左侧绕行通过。', 'https://timg.mnks.cn/20230927a7a3be4a.jpg', '左侧绕行', 0003, 0.12);
INSERT INTO `question` VALUES (1733399790168940546, 632, 'f107c', 0001, '这个标志的含义是告示前方道路是单向通行路段。', NULL, '对', '错', '空', '空', '错', '<b>右侧绕行</b>：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，<b>右侧绕行</b>通过。<br>\n<p><img src=\"https://tp.mnks.cn/tp_163.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左右绕行</p>\n<p><img src=\"https://tp.mnks.cn/tp_164.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>左侧绕行</p>\n<p><img src=\"https://tp.mnks.cn/tp_165.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>右侧绕行</b></p>', 'https://timg.mnks.cn/20230927f74ef983.jpg', '单向通行', 0003, 0.11);
INSERT INTO `question` VALUES (1733399790215077889, 633, 'b545e', 0001, '这个标志的含义是告示前方是塌方路段，车辆应绕道行驶。', NULL, '对', '错', '空', '空', '错', '【<b>施工</b>】 警告标志：用以告示前方<b>道路施工</b>，车辆应减速慢行或绕道行驶。该标志可以作为临时标志支设在施工路段以前适当位置。因此，题干表述错误。', 'https://timg.mnks.cn/20230927bc877c89.jpg', '塌方路段', 0003, 0.10);
INSERT INTO `question` VALUES (1733399790277992449, 634, '7233c', 0001, '这个标志的含义是告示车辆驾驶人应慢行或停车，确保干道车辆优先。', NULL, '对', '错', '空', '空', '对', '<b>减速让行</b>标志，告示车辆驾驶人<b>必须慢行或停车</b>，观察干道行车情况，在确保干道车辆优先通行且安全的前提下，方可进入路口。因此，题干表述正确。', 'https://timg.mnks.cn/20230927fce0a0c4.jpg', '确保', 0004, 0.25);
INSERT INTO `question` VALUES (1733399790315741185, 635, 'aa190', 0001, '遇到这个标志，您不可以左转，但是可以掉头。', NULL, '对', '错', '空', '空', '错', '《道路交通全法实施条例》第四十九条 机动车<b>在有禁止掉头或者禁止左转弯标志</b>、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，<b>不得掉头</b>。', 'https://timg.mnks.cn/20230927ce68fd60.jpg', '可以掉头', 0003, 0.08);
INSERT INTO `question` VALUES (1733399790357684225, 636, 'f4212', 0001, '图中标志提示前方路段只禁止一切车辆驶入。', NULL, '对', '错', '空', '空', '错', '如图所示，<b>禁止通行</b>标志，表示此路段禁止<b>一切车辆和行人</b>通行。', 'https://timg.mnks.cn/20230927cb4b2c10.jpg', '只禁止', 0005, 0.36);
INSERT INTO `question` VALUES (1733399790395432962, 637, '5a7b6', 0001, '这个标志的含义是表示车辆会车时，对方车辆应停车让行。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"https://tp.mnks.cn/tp_178.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>会车让行-表示车辆会车时, 应停车让对方车先行。标志形状为圆形， 颜色为白底红圈，红黑两种箭头。</b></p>\n<p><img src=\"https://tp.mnks.cn/tp_219.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>会车先行- 前方路段车辆在会车时享有优先通行权利。标志颜色为蓝底，对向来车为红色箭头，优先行进方向为白色箭头。</p><b>【区分方法】</b>看红色箭头位置（红色表示禁止），红箭头在左-对向让行，我方先行；红箭头在右-我方让行，对向先行。', 'https://timg.mnks.cn/20230927ba6bd5b4.jpg', '对方|让行', 0004, 0.21);
INSERT INTO `question` VALUES (1733399790437376002, 638, '5f66f', 0001, '这个标志的含义是指示此处设有室内停车场。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"https://tp.mnks.cn/tp_248.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>露天停车场</b>：附近<b>设有</b>露天停车场。</p>\n<p><img src=\"https://tp.mnks.cn/tp_249.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>室内停车场：附近设有室内停车场。</p>', 'https://timg.mnks.cn/20230927a0a2a9da.jpg', '设有', 0003, 0.12);
INSERT INTO `question` VALUES (1733399790487707650, 639, '1f1db', 0001, '这个标志的含义是指示此处设有室内停车场。', NULL, '对', '错', '空', '空', '对', '<p><img src=\"https://tp.mnks.cn/tp_249.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>室内停车场</b>：附近<b>设有</b>室内停车场。</p>\n<p><img src=\"https://tp.mnks.cn/tp_248.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>露天停车场：附近设有露天停车场。</p><b>请注意</b>，不要将题干中【设有】看成【没有】。', 'https://timg.mnks.cn/20230927ebca786b.jpg', '设有', 0002, 0.06);
INSERT INTO `question` VALUES (1733399790538039298, 640, 'c122c', 0002, '这是什么交通标志？ ', 'A、行人 B、人行天桥 C、注意残疾人 D、应急避难设施', '行人 ', '人行天桥 ', '注意残疾人 ', '应急避难设施', 'A', '如图所示，<b>行人标志</b>，表示该段道路<b>仅供行人步行</b>，任何车辆不准进入。设在步行街两端起点处 。有时段规定时，应用辅助标志说明 。', 'https://timg.mnks.cn/20230809b9ba5ef4.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399790638702593, 641, 'e8c6d', 0002, '这个标志是何含义？ ', 'A、只允许货车通行 B、只允许小型汽车行驶 C、指示大型货车靠右侧车道行驶 D、指示大型货车通行', '只允许货车通行 ', '只允许小型汽车行驶 ', '指示大型货车靠右侧车道行驶 ', '指示大型货车通行', 'C', '如图所示，由<b>靠右侧车道行驶</b>主标志和<b>大型货车靠右行驶</b>辅助标志组成的标志，表示<b>大型货车除必要的超车行为外应靠右侧车道行驶</b>。设在高速公路 、一 级公路，货车比例 较高的路段起点 、交叉口入口或互通立交加速车道终点后 。', 'https://timg.mnks.cn/20230809a6b0e9b6.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399790714200066, 642, 'b5420', 0002, '这个标志是何含义？ ', 'A、避让处所 B、港湾式紧急停车带 C、客车站 D、停车区', '避让处所 ', '港湾式紧急停车带 ', '客车站 ', '停车区', 'B', '如图所示，<b>港湾式紧急停车带</b>标志，用以指引<b>港湾式紧急停车带</b>的位置。设在<b>港湾式紧急停车带</b>前适当位置 。必要时，可设置预告标志。', 'https://timg.mnks.cn/20230809e1f50250.jpg', '', 0002, 0.08);
INSERT INTO `question` VALUES (1733399790756143105, 643, 'ff45f', 0002, '这个标志是何含义？ ', 'A、充电停车位 B、校车专用停车位 C、公交车专用停车位 D、出租车专用停车位', '充电停车位 ', '校车专用停车位 ', '公交车专用停车位 ', '出租车专用停车位', 'C', '<b>公交车专用停车位</b>标志：表示此处仅允许公交车停放 。', 'https://timg.mnks.cn/20230809c6195b44.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399790798086145, 644, 'b2ee6', 0002, '这个标志是何含义？ ', 'A、低速行驶 B、注意行人 C、行人先行 D、仅供行人步行', '低速行驶 ', '注意行人 ', '行人先行 ', '仅供行人步行', 'D', '如图所示，<b>行人标志</b>，表示该段道路<b>仅供行人步行</b>，任何车辆不准进入。设在步行街两端起点处 。有时段规定时，应用辅助标志说明 。', 'https://timg.mnks.cn/20230809b9ba5ef4.jpg', '', 0005, 0.44);
INSERT INTO `question` VALUES (1733399790835834882, 645, 'd2bf1', 0001, '这个标志为公交专用车道标志，表示该车道仅供公交车辆、通勤班车等大型载客汽车通行。', NULL, '对', '错', '空', '空', '错', '指示标志-<b>有轨电车专用车道</b>，表示该车道<b>仅供有轨电车</b>通行。设在进入该车道的起点及各交叉口的入口处。所以，题干表述错误。', 'https://timg.mnks.cn/20230926f24d1722.jpg', '公交专用车道', 0004, 0.24);
INSERT INTO `question` VALUES (1733399790877777922, 646, 'cff44', 0002, '这个标志是何含义？ ', 'A、公交专用车道 B、HOV专用车道 C、靠右侧车道行驶 D、有轨电车专用车道', '公交专用车道 ', 'HOV专用车道 ', '靠右侧车道行驶 ', '有轨电车专用车道', 'C', '指示标志-<b>靠右侧车道行驶</b>，表示车辆除必要的超车行为外应<b>靠右侧车道行驶</b>。设在高速公路 、一 级公路，货车比例较高的路段起点 、交叉口入口或互通立交加速车道终点后 。应加辅助标志说明靠右侧车道行驶的车型。', 'https://timg.mnks.cn/20230926e48ddd3c.jpg', '', 0005, 0.32);
INSERT INTO `question` VALUES (1733399790957469698, 647, 'bd220', 0001, '这个标志的含义是指示前方道路仅供行人步行，任何车辆不准进入。', NULL, '对', '错', '空', '空', '错', '指示标志-<b>人行横道</b>，表示该处为<b>人行横道</b>，机动车驾驶人应注意观察行人，遇行人已进入<b>人行横道</b>时应停车让行人通过。外加荧光黄绿边框的<b>人行横道</b>指示标志，进一步提高斑马线上的安全性。', 'https://timg.mnks.cn/20230926b42e9fab.jpg', '行人步行', 0005, 0.29);
INSERT INTO `question` VALUES (1733399791011995650, 648, 'cbd24', 0002, '这个标志是何含义？ ', 'A、限制高度 B、注意积水 C、限制轴重 D、限制宽度', '限制高度 ', '注意积水 ', '限制轴重 ', '限制宽度', 'B', '警告标志-<b>注意积水</b>，用以提醒车辆驾驶人<b>注意前方路段积水</b>。设在下穿道路等雨天易积水的路段，车行道路面标高最低处附近的位置，可在注意积水标志的上方设在“注意积水”辅助标志。', 'https://timg.mnks.cn/20230926b9f1426a.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399791053938690, 649, 'e3643', 0002, '这个标志是何含义？ ', 'A、停车点 B、观景台 C、休息区 D、服务站', '停车点 ', '观景台 ', '休息区 ', '服务站', 'A', '指路标志-<b>停车点</b>，用以指引<b>停车点</b>的标志，设在停车点入口附近。', 'https://timg.mnks.cn/20230926aea92a74.jpg', '', 0002, 0.08);
INSERT INTO `question` VALUES (1733399791091687426, 650, 'a13a7', 0002, '这个标志是何含义？ ', 'A、小型客车车道 B、中型客车专用车道 C、机动车车道 D、多乘员车辆专用车道', '小型客车车道 ', '中型客车专用车道 ', '机动车车道 ', '多乘员车辆专用车道', 'A', '<b>【矩形蓝底、两侧虚线，中间是什么车型就指示什么车道】</b>指示标志-<b>小型客车车道</b>，表示<b>该车道仅供小型客车通行</b>。设在进入该车道的起点及各交叉口的入口处。', 'https://timg.mnks.cn/20230926b8517d91.jpg', '', 0005, 0.47);
INSERT INTO `question` VALUES (1733399791129436162, 651, 'f7a2b', 0002, '这个标志是何含义？ ', 'A、加油站 B、急救站 C、电动汽车充电站 D、地铁', '加油站 ', '急救站 ', '电动汽车充电站 ', '地铁', 'C', '地点识别指路标志-<b>电动汽车充电站</b>，为道路使用者提供各种重要场所的识别和指向。设在所识别场所前适当位置。', 'https://timg.mnks.cn/20230926c4e891c0.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399791167184898, 652, 'db7b4', 0002, '这个标志是何含义？ ', 'A、非机动车与行人分开空间通行 B、非机动车与行人共享空间通行 C、非机动车骑行 D、非机动车推行', '非机动车与行人分开空间通行 ', '非机动车与行人共享空间通行 ', '非机动车骑行 ', '非机动车推行', 'D', '圆形蓝底白图形指示标志-<b>非机动车推行</b>，表示该道路仅供<b>非机动车推行</b>，不准骑行。', 'https://timg.mnks.cn/20230926d26b06a3.jpg', '', 0005, 0.30);
INSERT INTO `question` VALUES (1733399791209127937, 653, 'd12a3', 0002, '这个标志是何含义？ ', 'A、非机动车与行人分开空间通行 B、非机动车与行人共享空间通行 C、非机动车骑行 D、非机动车推行', '非机动车与行人分开空间通行 ', '非机动车与行人共享空间通行 ', '非机动车骑行 ', '非机动车推行', 'A', '行人和非机动车<b>分开</b>，是<b>非机动车与行人<b>分开</b>空间通行</b>，表示该道路仅供非机动车和行人通行，机动车不准进入。', 'https://timg.mnks.cn/20230926daa7d70f.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399791251070978, 654, 'c4200', 0002, '这个标志是何含义？ ', 'A、公交专用车道 B、有轨电车专用车道 C、快速公交系统专用车道 D、多乘员车辆（HOV）专用车道', '公交专用车道 ', '有轨电车专用车道 ', '快速公交系统专用车道 ', '多乘员车辆（HOV）专用车道', 'B', '<b>【矩形蓝底、两侧虚线，中间是什么车型就指示什么车道】</b>指示标志-<b>有轨电车专用车道</b>，表示该车道仅供有轨电车通行。设在进入该车道的起点及各交叉口的入口处。', 'https://timg.mnks.cn/20230926f24d1722.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399791288819713, 655, 'cdcd2', 0002, '这个标志是何含义？ ', 'A、硬路肩允许行驶路段开始 B、硬路肩允许行驶路段即将开始 C、硬路肩允许行驶路段结束 D、硬路肩允许行驶路段即将结束', '硬路肩允许行驶路段开始 ', '硬路肩允许行驶路段即将开始 ', '硬路肩允许行驶路段结束 ', '硬路肩允许行驶路段即将结束', 'C', '白线左侧表示行车道，白线右侧表示硬路肩。白线右侧<b>红杠压直行箭头</b>，表示硬路肩允许行驶路段<b>结束</b>，设置硬路肩允许行驶路段的终点处。', 'https://timg.mnks.cn/20230926e60f5a79.jpg', '', 0005, 0.31);
INSERT INTO `question` VALUES (1733399791330762754, 656, 'e0805', 0001, '这个标志的含义是表示硬路肩允许行驶路段结束。', NULL, '对', '错', '空', '空', '错', '白线左侧表示行车道，白线右侧表示硬路肩。<b>白线右侧直行箭头</b>表示硬路肩<b>允许行驶路段开始</b>，设置硬路肩允许行驶路段的起点处。<br>硬路肩就是道路右侧白实线以外的部分，在高速公路上可以理解为应急车道，在规定时间内可以驶入，缓解交通压力', 'https://timg.mnks.cn/20230926c28ca64f.jpg', '结束', 0004, 0.22);
INSERT INTO `question` VALUES (1733399791368511489, 657, 'cec22', 0002, '这个标志是何含义？ ', 'A、非机动车推行 B、注意电动自行车 C、禁止电动自行车通行 D、电动自行车行驶', '非机动车推行 ', '注意电动自行车 ', '禁止电动自行车通行 ', '电动自行车行驶', 'D', '圆形蓝底内有<b>电动自行车</b>，是指示标志-<b>电动自行车</b>行驶，表示该道路仅供<b>电动自行车</b>通行。设在该道路的起点及各交叉口的入口处。', 'https://timg.mnks.cn/20230926d87a5c69.jpg', '', 0004, 0.23);
INSERT INTO `question` VALUES (1733399791410454529, 658, 'a17f2', 0002, '这个标志是何含义？ ', 'A、开远光灯 B、开示廓灯 C、开车灯 D、开前雾灯', '开远光灯 ', '开示廓灯 ', '开车灯 ', '开前雾灯', 'C', '指示标志-<b>开车灯</b>，表示机动车开至该标志处应<b>开启车灯</b>，设在隧道口前等需要<b>开车灯</b>处。', 'https://timg.mnks.cn/20230926d920b5fa.jpg', '', 0005, 0.32);
INSERT INTO `question` VALUES (1733399791469174786, 659, 'c7942', 0002, '这个标志是何含义？ ', 'A、绿色通道 B、挂车、半挂车通行 C、货车通行 D、禁止货车通行', '绿色通道 ', '挂车', '半挂车通行 C', '货车通行 D', 'C', '指示标志-<b>货车通行</b>，表示<b>货车应当</b>在该道路上行驶，其他车辆<b>也可以</b>在该道路上行驶。标志的形状为圆形，颜色为白底、绿边、黑图案。', 'https://timg.mnks.cn/20230926f3f3c47b.jpg', '', 0005, 0.30);
INSERT INTO `question` VALUES (1733399791515312129, 660, 'e105a', 0002, '这个标志是何含义？ ', 'A、人行横道 B、注意行人 C、注意儿童 D、学校区域', '人行横道 ', '注意行人 ', '注意儿童 ', '学校区域', 'B', '警告标志-<b>注意行人</b>，用以警告车辆驾驶人<b>减速慢行，注意行人</b>。标志底色可采用荧光黄绿色，设在行人密集，或不易被驾驶人发现的人行横道线前适当位置。', 'https://timg.mnks.cn/20230926a514b8f4.jpg', '', 0005, 0.41);
INSERT INTO `question` VALUES (1733399791586615298, 661, 'b8634', 0002, '这个标志是何含义？ ', 'A、非机动车推行 B、注意电动自行车 C、禁止电动自行车通行 D、电动自行车行驶', '非机动车推行 ', '注意电动自行车 ', '禁止电动自行车通行 ', '电动自行车行驶', 'B', '黄底黑边三角形标志里有<b>电动自行车</b>，是警告标志-注意<b>电动自行车</b>，用以提醒车辆驾驶人谨慎驾驶，注意<b>电动自行车</b>。设在有<b>电动自行车</b>经常出入的地点前适当位置。', 'https://timg.mnks.cn/20230926ba0820f8.jpg', '', 0004, 0.21);
INSERT INTO `question` VALUES (1733399791628558337, 662, 'd42b3', 0002, '这个标志是何含义？ ', 'A、停车领卡 B、人工收费车道 C、停车检查 D、电子不停车（ETC）车道', '停车领卡 ', '人工收费车道 ', '停车检查 ', '电子不停车（ETC）车道', 'D', '指路标志-<b>电子不停车收费（ETC）车道</b>，用以指明<b>电子不停车收费（ETC）车道</b>，附着于收费大棚收费车道上方。', 'https://timg.mnks.cn/20230926bb5d899b.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399791674695681, 663, 'bb911', 0002, '这个标志是何含义？ ', 'A、交叉路口 B、连续弯路 C、反向弯路 D、急弯路', '交叉路口 ', '连续弯路 ', '反向弯路 ', '急弯路', 'A', '<b>交叉路口标志</b>，用以警告车辆驾驶人前方是被交路比当前道路窄的交叉路口，谨慎驾驶，注意横向来车。', 'https://timg.mnks.cn/20230809a58eadcd.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399791716638722, 664, 'ed5d8', 0002, '这个标志是何含义？ ', 'A、分流诱导标志 B、合流诱导标志 C、线形诱导标志 D、转弯诱导标志', '分流诱导标志 ', '合流诱导标志 ', '线形诱导标志 ', '转弯诱导标志', 'C', '如图所示的<b>线形诱导标</b>，设置在视线不好的T形交叉口，用以引导行车方向，提醒驾驶人谨慎驾驶，注意前方线形变化。<b>线形诱导标</b>为黄底黑图形 、无边框，形状为矩形。', 'https://timg.mnks.cn/20230809a4787f67.jpg', '', 0005, 0.37);
INSERT INTO `question` VALUES (1733399791762776066, 665, 'a5d81', 0002, '下列哪个标志表示禁止挂车、半挂车驶入？ ', 'A、C图 B、D图 C、B图 D、A图', 'C图 ', 'D图 ', 'B图 ', 'A图', 'A', '请注意：【<b>图片的ABCD并不是对应选项的ABCD</b>】。如图所示，ABCD图对应的禁令标志分别为：A图-禁止大型载客汽车驶入；B图-禁止小型载客汽车驶入；<b>C图-禁止挂车 、半挂车驶入</b>；D图-禁止电动自行车进入。<br>所以，<b>C图标志</b>表示禁止挂车、半挂车驶入。', 'https://timg.mnks.cn/20230809cb235179.jpg', '禁止挂车、半挂车', 0003, 0.09);
INSERT INTO `question` VALUES (1733399791804719105, 666, 'bfe3f', 0002, '这个标志是何含义？ ', 'A、停车领卡 B、人工收费车道 C、停车检查 D、ETC通道', '停车领卡 ', '人工收费车道 ', '停车检查 ', 'ETC通道', 'B', '指路标志-<b>人工收费车道</b>，用以指明<b>人工收费车道</b>，附着于收费大棚收费车道上方。', 'https://timg.mnks.cn/20230926efb9cc22.jpg', '', 0005, 0.29);
INSERT INTO `question` VALUES (1733399791842467841, 667, 'c5b50', 0002, '这个标志是何含义？ ', 'A、非机动车车道 B、禁止电动自行车通行车道 C、电动自行车车道 D、停放电动自行车路段', '非机动车车道 ', '禁止电动自行车通行车道 ', '电动自行车车道 ', '停放电动自行车路段', 'C', '<b>【矩形蓝底、两侧虚线，中间是什么车型就指示什么车道】</b>指示标志-<b>电动自行车车道</b>，表示<b>该车道</b>仅供电动自行车通行。设在该车道的起点及各交叉口的入口处。设置在车道正上方时，向下的箭头可以省略；在标志无法正对车道时，可调整箭头方向，指向车道。', 'https://timg.mnks.cn/20230926dd9a7f8b.jpg', '', 0004, 0.23);
INSERT INTO `question` VALUES (1733399792027017217, 668, 'cf036', 0002, '这个标志是何含义？ ', 'A、事故易发路段 B、施工路段 C、交通事故管理 D、注意危险', '事故易发路段 ', '施工路段 ', '交通事故管理 ', '注意危险', 'C', '警告标志-<b>交通事故管理</b>，用以警告前方路段正在进行道路<b>交通事故管理</b>，车辆驾驶人应减速慢行、停车等候或绕道行驶。该标志为<b>粉红色底或荧光粉红色底、黑文字</b>。作为临时性标志支设在进行交通事故管理的路段前适当位置。', 'https://timg.mnks.cn/20230926ca97045d.jpg', '', 0005, 0.35);
INSERT INTO `question` VALUES (1733399792068960257, 669, 'c4885', 0002, '这个标志是何含义？ ', 'A、右侧车辆向左行驶 B、硬路肩禁止行驶 C、向左边变更车道 D、硬路肩允许行驶即将结束', '右侧车辆向左行驶 ', '硬路肩禁止行驶 ', '向左边变更车道 ', '硬路肩允许行驶即将结束', 'D', '白线左侧表示行车道，白线右侧表示硬路肩。<b>白线右侧左弯箭头</b>，表示硬路肩允许行驶路段<b>即将结束</b>，车辆应尽快合流，设在合流前适当位置。', 'https://timg.mnks.cn/20230926a48ded19.jpg', '', 0004, 0.23);
INSERT INTO `question` VALUES (1733399792110903298, 670, '35ab4', 0002, '下列哪个标志为最低限速标志？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'B', '图1表示鸣喇叭；<b>图2表示最低限速</b>；图3表示机动车行驶标志；图4表示非机动车行驶标志。', 'https://timg.mnks.cn/20230927b4731c77.jpg', '最低限速', 0001, 0.03);
INSERT INTO `question` VALUES (1733399792152846337, 671, 'f10c5', 0002, '下列哪个标志表示该车道仅供有轨电车通行？ ', 'A、D图 B、C图 C、B图 D、A图', 'D图 ', 'C图 ', 'B图 ', 'A图', 'C', '请注意：【<b>图片的ABCD并不是对应选项的ABCD</b>】。如图所示，ABCD图对应的<b>车道指示标志及含义</b>分别为：<br>A图-小型客车车道，表示该车道仅供小型客车通行；<br><b>B图-有轨电车专用车道，表示该车道仅供有轨电车通行</b>；<br>C图-多乘员车辆（HOV）专用车道标志，表示该车道仅供多乘员的车辆通行；<br>D图-公交专用车道，表示该车道仅供公交车辆 、通勤班车等大型载客汽车通行。<br>所以，<b>B图标志</b>表示<b>仅供有轨电车通行</b>。', 'https://timg.mnks.cn/20230809b6485bf6.jpg', '有轨电车', 0003, 0.09);
INSERT INTO `question` VALUES (1733399792198983682, 672, 'd133c', 0002, '下列哪个标志表示硬路肩允许行驶路段即将结束，车辆应尽快合流？ ', 'A、A图 B、B图C、C图 D、D图', 'A图 ', 'B图', 'C图 ', 'D图', 'B', '如图所示，ABCD图对应的标志及含义分别为：<br>A图- 硬路肩允许行驶标志，表示硬路肩允许行驶路段开始；<br><b><b>B图</b>- 硬路肩允许行驶标志，表示硬路肩允许行驶路段即将结束，车辆应尽快合流；</b><br>C图- 硬路肩允许行驶标志，表示硬路肩允许行驶路段结束；<br>D图-注意车道数变少标志，用以提醒车辆驾驶人注意前方车道数量变少 。<br>所以，<b>B图标志</b>表示硬路肩允许行驶路段即将结束。', 'https://timg.mnks.cn/20230809bf3450f3.jpg', '即将结束', 0005, 0.40);
INSERT INTO `question` VALUES (1733399792232538113, 673, 'ef836', 0002, '下列哪个标志用以指引无障碍设施的位置？ ', 'A、B图 B、C图 C、A图 D、D图', 'B图 ', 'C图 ', 'A图 ', 'D图', 'B', '请注意：【<b>图片的ABCD并不是对应选项的ABCD</b>】。如图所示，ABCD图对应的标志及含义分别为：<br>A图-注意残疾人标志，用以警告车辆驾驶人减速慢行，注意残疾人；<br>B图-注意残疾人标志，用以警告车辆驾驶人减速慢行，注意残疾人；<br><b>C图-无障碍设施标志，用以指引无障碍设施的位置</b>；<br>D图-残疾人专用停车位标志，表示此处仅允许残疾人驾驶的车辆停放。<br>所以，<b>C图标志</b>用以指引无障碍设施的位置。', 'https://timg.mnks.cn/20230809c1a8658a.jpg', '指引无障碍设施', 0005, 0.31);
INSERT INTO `question` VALUES (1733399792266092546, 674, 'ffded', 0002, '下列哪个标志提醒驾驶人注意前方车道数量变少？ ', 'A、A图 B、B图C、C图 D、D图', 'A图 ', 'B图', 'C图 ', 'D图', 'D', '如图所示，ABCD图对应的警告标志及含义分别为：<br>A图-交叉路口标志，用以警告车辆驾驶人前方有Y形交叉路口，注意横向来车；<br>B图-交叉路口标志，用以警告车辆驾驶人前方被交路比当前道路窄的交叉路口；<br>C图-交叉路口标志，以警告车辆驾驶人前方有T形交叉路口，注意横向来车；<br><b>D图-注意车道数变少标志，用以提醒车辆驾驶人注意前方车道数量变少 。</b><br>所以，<b>D图标志</b>提醒驾驶人注意前方车道数量变少。', 'https://timg.mnks.cn/20230809c74fdcf6.jpg', '车道数量变少', 0002, 0.05);
INSERT INTO `question` VALUES (1733399792316424194, 675, 'fa068', 0002, '以下哪个标志，表示干路先行？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '图1表示单行路；图2表示直行；图3表示直行车道；<b>图4表示路口优先通行（干路先行）。</b>', 'https://timg.mnks.cn/20230927da343c5d.jpg', '干路先行', 0003, 0.14);
INSERT INTO `question` VALUES (1733399792366755841, 676, '07b04', 0002, '以下交通标志表示单行线的是哪一项？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'A', '<b>图1表示单行路</b>；图2表示直行车道；图3表示路口优先通行；图4表示靠右侧道路行驶。', 'https://timg.mnks.cn/20230927a780d2bc.jpg', '单行线', 0005, 0.28);
INSERT INTO `question` VALUES (1733399792425476097, 677, '32b13', 0002, '下列哪个标志，指示车辆直行和右转合用车道？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'A', '图1：<b>直行和右转合用车道</b>；图2：干路先行；图3：右转车道；图4：分向行驶车道。', 'https://timg.mnks.cn/20230927e382f46b.jpg', '直行和右转', 0002, 0.04);
INSERT INTO `question` VALUES (1733399792475807745, 678, '0b5ae', 0002, '有这种信号灯的路口允许机动车如何行驶？ ', 'A、向左转弯 B、直行通过 C、向右转弯 D、停车等待', '向左转弯 ', '直行通过 ', '向右转弯 ', '停车等待', 'C', '方向指示信号灯，箭头方向向左、向上、向右分别表示左转、直行、右转。<b>绿色箭头</b>表示<b>允许车辆沿箭头所指方向通行</b>；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示箭头所指方向车辆起黄灯作用。 <br>如图所示，<b>准许右转弯</b>信号灯，表示允许<b>机动车向右转弯行驶</b>。', 'https://timg.mnks.cn/20230926c87ef290.jpg', '信号灯|如何行驶', 0001, 0.03);
INSERT INTO `question` VALUES (1733399792517750785, 679, '62fb9', 0002, '遇到这种情况的路口怎样通过？ ', 'A、左转弯加速通过 B、加速直行通过 C、右转弯加速通过 D、确认安全后通过', '左转弯加速通过 ', '加速直行通过 ', '右转弯加速通过 ', '确认安全后通过', 'D', '【黄灯表示警示】闪光警告信号灯为持续闪烁的黄灯，提示车辆、行人通行时注意瞭望，<b>确认安全后</b>通过。', 'https://timg.mnks.cn/20230926c58a32fc.jpg', '怎样通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399792551305217, 680, 'f4a97', 0002, '这段道路红车所在车道是什么车道？ ', 'A、快速车道 B、慢速车道 C、专用车道 D、应急车道', '快速车道 ', '慢速车道 ', '专用车道 ', '应急车道', 'A', '《道路交通安全法实施条例》第四十四条：在道路同方向划有2条以上机动车道的，<b>左侧为快速车道</b>，右侧为慢速车道。 <br>如图所示，红色车在最左侧，因此所在的车道是快速车道。', 'https://timg.mnks.cn/20230926ef4a886f.jpg', '红车', 0002, 0.05);
INSERT INTO `question` VALUES (1733399792589053953, 681, '27071', 0001, '在这种情况下可以借右侧公交车道超车。', NULL, '对', '错', '空', '空', '错', '1、道路上划设专用车道的，在专用车道内只准许规定的车辆通行，<b>其他车辆不得进入专用车道内行驶</b>。2、安全超车应<b>从被超越车辆的左侧</b>超越。', 'https://timg.mnks.cn/20230926a8939bc5.jpg', '可以借|超车', 0002, 0.04);
INSERT INTO `question` VALUES (1733399792639385601, 682, '62dd9', 0001, '在这种情况下可加速通过交叉路口。', NULL, '对', '错', '空', '空', '错', '黄灯的法律含义是警示，<b>黄灯亮，加速通过交叉路口的做法是错误的。</b><br>【黄闪】持续闪烁的黄灯是闪光警告信号灯，提示路口交通信号暂时解除，车辆、行人通行时注意瞭望，确认安全后通过。<br>【黄灯】黄灯是一种过渡信号灯，起到警示作用，提示信号即将变换，已越过停止线的车可以继续通过；没有越过停止线的要停车等待。', 'https://timg.mnks.cn/20230926eafd404b.jpg', '加速通过', 0002, 0.04);
INSERT INTO `question` VALUES (1733399792689717249, 683, '9b782', 0001, '在路口这个位置时可以加速通过路口。', NULL, '对', '错', '空', '空', '错', '《道路交通安全实施条例》第三十八条 机动车信号灯和非机动车信号灯表示：<b>红灯亮时，禁止车辆通行</b>，车辆不得越过停止线；右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 'https://timg.mnks.cn/20230926f7739763.jpg', '加速通过', 0001, 0.01);
INSERT INTO `question` VALUES (1733399792731660289, 684, 'b4ad5', 0001, '在铁路道口遇到两个红灯交替闪烁时要停车等待。', NULL, '对', '错', '空', '空', '对', '道路与铁路平面交叉道口有<b>两个红灯交替闪烁</b>或一个红灯亮时，表示火车要通过了，此时<b>禁止车辆、行人通行，车辆不得越过停止线</b>。红灯熄灭时，表示允许车辆行人通行。', 'https://timg.mnks.cn/20230926c5ee2b21.jpg', '交替闪烁|停车', 0002, 0.05);
INSERT INTO `question` VALUES (1733399792765214721, 685, 'a9c37', 0001, '在这段路的最高时速为每小时50公里。', NULL, '对', '错', '空', '空', '错', '【<b>红高</b><b>蓝低</b>黄建议】白底红圈表示<b>最高限速</b>，蓝色牌子表示<b>最低限速</b>。因此，在这段路的最高限速为80公里/小时，最低限速50公里/小时。', 'https://timg.mnks.cn/20230926e1674ed4.jpg', '最高时速|50', 0003, 0.10);
INSERT INTO `question` VALUES (1733399792815546370, 686, 'b8a26', 0002, '行车中遇列队横过道路的学生时，应怎样做？ ', 'A、提前加速抢行 B、停车让行 C、降低车速、缓慢通过 D、连续鸣喇叭催促', '提前加速抢行 ', '停车让行 ', '降低车速', '缓慢通过 D', 'B', '驾驶机动车遇列队横过道路时，应及时<b>停车礼让</b>，待列队学生全部通过后再通过。不可加速抢行、从他们中间穿行或从两侧绕行通过，避免发生事故。', '', '列队横过|学生', 0004, 0.24);
INSERT INTO `question` VALUES (1733399792865878017, 687, '67c8f', 0001, '这辆红色轿车变更车道的方法和路线是正确的。', NULL, '对', '错', '空', '空', '错', '如图所示，红车没有按规定开启转向灯，并且<b>强行在正常行驶的黄车前方变道</b>，干扰黄车正常行驶的行为<b>属于违法行为</b>，很容易引发交通事故，所以，<b>红车变道的方法和路线都是错误的。</b>', 'https://timg.mnks.cn/20230926cf6638a5.jpg', '变更车道|正确', 0001, 0.02);
INSERT INTO `question` VALUES (1733399792907821057, 688, '238c0', 0002, '车辆通过凹凸路面时，应怎样做？ ', 'A、低速缓慢平稳通过 B、依靠惯性加速冲过 C、挂空挡滑行驶过 D、保持原速通过', '低速缓慢平稳通过 ', '依靠惯性加速冲过 ', '挂空挡滑行驶过 ', '保持原速通过', 'A', '通过凹凸路面时，需要<b>低速慢行，并且匀速通过这种路段</b>，不可以出现忽快忽慢、加速冲、空挡滑行等现象。', '', '凹凸路面', 0001, 0.03);
INSERT INTO `question` VALUES (1733399792949764098, 689, '11543', 0002, '驾驶机动车在路口遇到这种情况如何行驶？ ', 'A、可以向右转弯 B、靠右侧直行 C、遵守交通信号灯 D、停车等待', '可以向右转弯 ', '靠右侧直行 ', '遵守交通信号灯 ', '停车等待', 'D', '<p>驾驶机动车在路口遇到交警发出停止手势信号时，<b>交警面向的车辆应停车等待</b>。</p>\n<p><img src=\"https://timg.mnks.cn/20230928e1684774.gif\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>点击查看交警手势动图</p>\n<p><b>【停止信号】</b><br>动作要领：交警左臂向前上方直伸，掌心向前；右臂沿裤中缝对齐，无任何动作。<br>手势示意：交警面向的车辆不准通行。<br>【<b>识别技巧</b>】<b>交警的</b>面部对着<b>交警的</b>哪个方向，就是在指挥哪个方向的车。</p>', 'https://timg.mnks.cn/20230926d6a60ac7.jpg', '如何行驶', 0002, 0.06);
INSERT INTO `question` VALUES (1733399793037844482, 690, '550a2', 0002, '驾驶机动车在道路上向左变更车道时如何使用灯光？ ', 'A、不用开启转向灯 B、提前开启右转向灯 C、提前开启左转向灯 D、提前开启近光灯', '不用开启转向灯 ', '提前开启右转向灯 ', '提前开启左转向灯 ', '提前开启近光灯', 'C', '驾驶机动车在道路上向左变更车道时，应提前开启左转向灯。根据《道路交通安全法实施条例》第五十七条规定：机动车应当按照下列规定使用转向灯：<br><b>(一)向左转弯、<b>向左变更车道</b>、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；</b><br> (二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', '', '向左变更|使用灯光', 0002, 0.06);
INSERT INTO `question` VALUES (1733399793083981826, 691, '80cb4', 0002, '行车中超越同向行驶的自行车时，应怎样做？ ', 'A、让自行车先行 B、注意观察动态，减速慢行，留有足够的安全距离 C、连续鸣喇叭提醒其让路 D、持续鸣喇叭并加速超越', '让自行车先行 ', '注意观察动态，减速慢行，留有足够的安全距离 ', '连续鸣喇叭提醒其让路 ', '持续鸣喇叭并加速超越', 'B', '行车中超越同向行驶的自行车时，<b>注意观察其动态，减速慢行，保持安全距离</b>，从左侧绕行，防止骑车人摔倒或突然改变骑行方向。', '', '超越|自行车', 0003, 0.09);
INSERT INTO `question` VALUES (1733399793130119170, 692, '02d0b', 0002, '驾驶机动车在道路上靠路边停车过程中如何使用灯光？ ', 'A、变换使用远近光灯 B、不用指示灯提示 C、开启危险报警闪光灯 D、提前开启右转向灯', '变换使用远近光灯 ', '不用指示灯提示 ', '开启危险报警闪光灯 ', '提前开启右转向灯', 'D', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： (一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯； <b>(二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。</b>', '', '靠边停车', 0005, 0.31);
INSERT INTO `question` VALUES (1733399793176256514, 693, '7e387', 0002, '夜间驾驶车辆遇自行车对向驶来时，应怎样做？ ', 'A、连续变换远、近光灯 B、不断鸣喇叭 C、使用远光灯 D、使用近光灯，减速或停车避让', '连续变换远', '近光灯 B', '不断鸣喇叭 C', '使用远光灯 D', 'D', '安全行车，文明礼让。夜间驾驶机动车遇非机动车对向驶来时，应使用近光灯，减速慢行，必要的时候停车避让。交替使用远近光灯强光会使对方看不清道路，导致危险。', '', '夜间|自行车对向驶来', 0002, 0.05);
INSERT INTO `question` VALUES (1733399793218199554, 694, 'ab1b1', 0002, '在这种天气条件下行车如何使用灯光？ ', 'A、使用近光灯 B、不使用灯光 C、使用远光灯 D、使用雾灯', '使用近光灯 ', '不使用灯光 ', '使用远光灯 ', '使用雾灯', 'A', '驾驶机动车在雨、雪、沙尘、冰雹等低能见度情况下行驶时，开启前照灯、示廓灯和后位灯，<b>但同方向行驶的后车与前车近距离行驶时，使用近光灯，不得使用远光灯</b>。 <br>【拓展知识】 <br>1，跟车行驶应开启近光灯，帮助驾驶员瞭望前方情况，使驾驶员对前方情况看得清楚一些；2，开启示廓灯和前后位灯，示意自己车辆的存在，使前后行驶的车辆，能够看到自己的具体位置及通行情况，方便他们采取相应的措施。', 'https://timg.mnks.cn/20230926c8290d3c.jpg', '如何使用', 0005, 0.35);
INSERT INTO `question` VALUES (1733399793272725505, 695, '08a3a', 0002, '车辆在主干道上行驶，驶近主支干道交汇处时，为防止与从支路突然驶入的车辆相撞，应怎样做？ ', 'A、提前减速、观察，谨慎驾驶 B、保持正常速度行驶 C、鸣喇叭，迅速通过 D、提前加速通过', '提前减速', '观察，谨慎驾驶 B', '保持正常速度行驶 C', '鸣喇叭，迅速通过 D', 'A', '车辆在主干道上行驶，驶近主支干道交汇处时，应<b>提前减速、观察并谨慎驾驶</b>，以防与从支路突然驶入的车辆发生碰撞事故。', '', '主支干道交汇处', 0001, 0.01);
INSERT INTO `question` VALUES (1733399793310474242, 696, '92233', 0002, '车辆在交叉路口有优先通行权的，遇有车辆抢行时，应怎样做？ ', 'A、抢行通过 B、提前加速通过 C、按优先权规定正常行驶不予避让 D、减速避让，必要时停车让行', '抢行通过 ', '提前加速通过 ', '按优先权规定正常行驶不予避让 ', '减速避让，必要时停车让行', 'D', '通过交叉路口时要<b>注意减速避让</b>正在通行的车辆和行人，随时做好<b>停车让行</b>的准备。即使有优先通行权，也不能忽视对面来车抢先左转弯或左右车道车辆抢行带来的危险。', '', '优先通行权|车辆抢行', 0002, 0.04);
INSERT INTO `question` VALUES (1733399793352417282, 697, 'a4cc6', 0002, '在这种雨天跟车行驶使用灯光，以下做法正确的是？ ', 'A、使用远光灯 B、不能使用近光灯 C、不能使用远光灯 D、使用雾灯', '使用远光灯 ', '不能使用近光灯 ', '不能使用远光灯 ', '使用雾灯', 'C', '《道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、<b>雨</b>、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，<b>但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。</b>机动车雾天行驶应当开启雾灯和危险报警闪光灯。 <br>记住，只要你的车前方有车，不管是相向还是同向，都不能使用远光灯。', 'https://timg.mnks.cn/20230926fc4b1467.jpg', '跟车', 0005, 0.30);
INSERT INTO `question` VALUES (1733399793394360321, 698, 'ef513', 0002, '机动车在道路边临时停车时，应怎样做？ ', 'A、可逆向停放 B、可并列停放 C、不得逆向或并列停放 D、只要出去方便，可随意停放', '可逆向停放 ', '可并列停放 ', '不得逆向或并列停放 ', '只要出去方便，可随意停放', 'C', '在道路上<b>临时停车</b>时，不得妨碍其他机动车和行人通行，应按指定的位置停放，<b>不得在道路两侧并列停放或逆向停放</b>。', '', '临时停车', 0002, 0.08);
INSERT INTO `question` VALUES (1733399793432109058, 699, '45ff2', 0002, '在这种环境下通过路口如何使用灯光？ ', 'A、关闭远光灯 B、使用危险报警闪光灯 C、使用远光灯 D、交替使用远近光灯', '关闭远光灯 ', '使用危险报警闪光灯 ', '使用远光灯 ', '交替使用远近光灯', 'D', '如图所示，<b>夜间通过没有交通信号灯控制的路口</b>时，应<b>交替使用远近光灯</b>。根据《道路交通安全法实施条例》第五十九条规定：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 'https://timg.mnks.cn/20230926f7819610.jpg', '如何使用', 0003, 0.08);
INSERT INTO `question` VALUES (1733399793482440706, 700, 'c9a4c', 0002, '车辆在雨天临时停车时，应开启什么灯？ ', 'A、前后雾灯 B、危险报警闪光灯 C、前大灯 D、倒车灯', '前后雾灯 ', '危险报警闪光灯 ', '前大灯 ', '倒车灯', 'B', '夜间或遇风、<b>雨</b>、雪、雾天在<b>路边临时停车</b>，要关闭前照灯，<b>开启</b>示廓灯和<b>危险报警闪光灯</b>。', '', '雨天临时停车', 0003, 0.10);
INSERT INTO `question` VALUES (1733399793520189441, 701, 'f8d8e', 0002, '在这种路段如何行驶？ ', 'A、减速鸣喇叭示意 B、加速鸣喇叭通过 C、在弯道中心转弯 D、占对方道路转弯', '减速鸣喇叭示意 ', '加速鸣喇叭通过 ', '在弯道中心转弯 ', '占对方道路转弯', 'A', '如图所示，右侧是<b>向左<b>急转弯</b></b>警告标志，表示在<b>急转弯</b>路段。根据《道路交通安全法实施条例》第五十九条规定：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。<b>机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当减速慢行，并鸣喇叭示意。</b>', 'https://timg.mnks.cn/20230926c730099f.jpg', '如何行驶', 0002, 0.06);
INSERT INTO `question` VALUES (1733399793562132481, 702, 'd6e57', 0002, '车辆在雪天临时停车时，应开启什么灯？ ', 'A、前后雾灯 B、倒车灯 C、前大灯 D、危险报警闪光灯', '前后雾灯 ', '倒车灯 ', '前大灯 ', '危险报警闪光灯', 'D', '夜间或遇风、雨、<b>雪</b>、雾天在<b>路边临时停车</b>，要关闭前照灯，<b>开启</b>示廓灯和<b>危险报警闪光灯</b>。', '', '雪天临时停车', 0003, 0.10);
INSERT INTO `question` VALUES (1733399793604075521, 703, 'e6b24', 0001, '驾驶机动车在道路上超车时可以不使用转向灯。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯：<b>（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；</b>（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。<br>超车必须使用转向灯，否则极易造成严重的交通事故。题目描述有误，本题答案应为错误。', '', '可以不', 0002, 0.04);
INSERT INTO `question` VALUES (1733399793646018561, 704, '923b2', 0001, '驾驶机动车不能进入红色叉形灯或者红色箭头灯亮的车道。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第四十条 车道信号灯表示：(一)绿色箭头灯亮时，准许本车道车辆按指示方向通行；(二)<b>红色叉形灯或者箭头灯亮时</b>，<b>禁止本车道车辆通行</b>。', '', '不能进入', 0002, 0.06);
INSERT INTO `question` VALUES (1733399793683767297, 705, 'fcff5', 0002, '驾驶人行车中看到注意儿童标志的时候，应怎样做？ ', 'A、加速行驶 B、绕道行驶 C、谨慎选择行车速度 D、保持正常车速行驶', '加速行驶 ', '绕道行驶 ', '谨慎选择行车速度 ', '保持正常车速行驶', 'C', '一般在幼儿园或者是学校的附近，都会设置注意儿童的标志牌。行车中看到注意儿童标志时，应<b>谨慎行车，减速慢行，注意儿童</b>，一旦发现有异常情况，应及时停车。', '', '注意儿童', 0002, 0.06);
INSERT INTO `question` VALUES (1733399793738293249, 706, 'ab37e', 0001, '在路口遇有交通信号灯和交通警察指挥不一致时，按照交通信号灯通行。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第三十八条：车辆、行人应当按照交通信号通行；<b>遇有交通警察现场指挥时，应当按照交通警察的指挥通行；</b>在没有交通信号的道路上，应当在确保安全、畅通的原则下通行。因此，题干表述错误。', '', '不一致', 0003, 0.13);
INSERT INTO `question` VALUES (1733399793788624898, 707, 'b263f', 0001, '驾驶机动车在道路上超车完毕驶回原车道时需提前开启右转向灯。', NULL, '对', '错', '空', '空', '对', '题干表述正确。根据《道路交通安全法实施条例》第四十七条：<b>机动车超车时</b>，应当：（一）提前<b><b>开启</b>左转向灯</b>、变换使用远、近光灯或者鸣喇叭；（二）在确认有充足的安全距离后，从前车的左侧超越，在与被超车辆拉开必要的安全距离后，<b>开启</b><b>右转向灯</b>，<b>驶回原车道。</b>', '', '超车完毕|需提前', 0003, 0.10);
INSERT INTO `question` VALUES (1733399793830567937, 708, '5d209', 0002, '车辆驶近停在车站的公交车辆时，为预防公交车突然起步或行人从车前穿出，应怎样做？ ', 'A、减速，保持足够间距，随时准备停车 B、保持正常车速行驶 C、随时准备紧急制动 D、鸣喇叭提醒，加速通过', '减速，保持足够间距，随时准备停车 ', '保持正常车速行驶 ', '随时准备紧急制动 ', '鸣喇叭提醒，加速通过', 'A', '驾驶机动车驶近停有公交车的车站时，要<b>减速慢行</b>，与公交车<b>保持较大的安全间距</b>，预防公交车突然起步或车站上下车的乘客从车前、车后横穿道路，同时注意对向公交车前后是否有行人横穿道路，<b>做好随时停车的准备</b>。', '', '预防|突然起步|行人从车前穿出', 0001, 0.02);
INSERT INTO `question` VALUES (1733399793868316673, 709, '0d19e', 0002, '雨天行车，遇撑雨伞和穿雨衣的行人在公路上行走时，应怎样做？ ', 'A、以正常速度行驶 B、持续鸣喇叭示意其让道 C、加速绕行 D、提前鸣喇叭，并适当降低车速', '以正常速度行驶 ', '持续鸣喇叭示意其让道 ', '加速绕行 ', '提前鸣喇叭，并适当降低车速', 'D', '下雨天遇撑雨伞和穿雨衣的行人在道路上行走时，要<b>提前鸣喇叭进行提示，并适当降低车速</b>，注意观察其动态，保持一定的距离通过，同时要随时准备应付突发状况。', '', '雨天|行人|行走', 0001, 0.03);
INSERT INTO `question` VALUES (1733399793910259713, 710, 'b0a5e', 0001, '驾驶机动车在道路上向右变更车道可以不使用转向灯。', NULL, '对', '错', '空', '空', '错', '变更车道<b>不开启转向灯属于违法行为</b>，变更车道前，通过内、外后视镜观察后方道路交通情况，确认安全后提前3秒钟开启转向灯。<br>根据《道路交通安全法实施条例》第五十七条，机动车应当按照下列规定使用转向灯： （二）向右转弯、<b>向右变更车道</b>、超车完毕驶回原车道、靠路边停车时，<b>应当提前开启右转向灯</b>。', '', '可以不', 0002, 0.04);
INSERT INTO `question` VALUES (1733399793952202753, 711, '0cb3e', 0002, '车辆行至交叉路口，遇有转弯的车辆抢行，应怎样做？ ', 'A、停车避让 B、保持正常车速行驶 C、提高车速抢先通过 D、鸣喇叭抢先通过', '停车避让 ', '保持正常车速行驶 ', '提高车速抢先通过 ', '鸣喇叭抢先通过', 'A', '通过交叉路口时要注意避让正在通行的车辆和行人，随时做好<b>停车避让</b>的准备。即使有优先通行权，也不能忽视对面来车抢先左转弯或左右车道车辆抢行带来的危险。', '', '转弯的车辆抢行', 0001, 0.03);
INSERT INTO `question` VALUES (1733399793998340098, 712, '8bf19', 0001, '驾驶机动车在沙尘天气条件下行车不用开启前照灯、示廓灯和后位灯。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十八条： 机动车在夜间没有路灯、照明不良或者<b>遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，</b>但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', '', '沙尘天气|不用', 0004, 0.17);
INSERT INTO `question` VALUES (1733399794036088833, 713, '8e953', 0001, '驾驶车辆变更车道时，应提前开启转向灯，注意观察，保持安全距离，驶入要变更的车道。', NULL, '对', '错', '空', '空', '对', '驾驶车辆变更车道时，应通过内、外后视镜观察后方道路交通情况，确认安全后<b>提前3秒钟开启转向灯</b>，再次<b>观察道路两侧交通情况</b>，在<b>不妨碍</b>其他车辆正常行驶的情况下逐渐变更车道。', '', '提前开启|注意观察', 0001, 0.01);
INSERT INTO `question` VALUES (1733399794078031874, 714, '0a5d4', 0001, '在这种环境里行车使用近光灯。', NULL, '对', '错', '空', '空', '对', '如图所示，夜间通过人行横道或者没有交通信号灯控制的路口，<b>遇行人通行时，应使用近光灯</b>，同时注意减速慢行，防止其突然横穿。', 'https://timg.mnks.cn/20230926eccef2cd.jpg', '使用近光灯', 0003, 0.12);
INSERT INTO `question` VALUES (1733399794132557826, 715, '5611a', 0002, '夜间行车，可选择下列哪个地段超车？ ', 'A、窄路窄桥 B、交叉路口 C、路宽车少 D、弯道陡坡', '窄路窄桥 ', '交叉路口 ', '路宽车少 ', '弯道陡坡', 'C', '夜间行车超车前，应选择视线良好、平直宽阔、左右均无障碍且前方路段200米范围内没有来车的道路，在保证安全的前提下方能超车。', '', '夜间|超车', 0002, 0.05);
INSERT INTO `question` VALUES (1733399794174500866, 716, '41041', 0001, '行车中遇到正在进行作业的道路养护车辆、工程作业车时要注意避让。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第五十四条：道路养护车辆、工程作业车进行作业时，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，<b>过往车辆和人员应当注意避让</b>。【特殊车辆都该让】', '', '注意避让', 0001, 0.02);
INSERT INTO `question` VALUES (1733399794216443906, 717, '388fd', 0001, '驾驶车辆向右变更车道时，应提前开启右转向灯，注意观察，在确保安全的情况下，驶入要变更的车道。', NULL, '对', '错', '空', '空', '对', '驾驶机动车<b>向右变更车道</b>时，应<b>提前开启右转向灯</b>，通过内、外后视镜<b>观察</b>后方车辆和右侧车道的交通状况，在<b>确保安全</b>的情况下，驶入要变更的车道。', '', '向右变更车道', 0001, 0.02);
INSERT INTO `question` VALUES (1733399794258386945, 718, '0b58c', 0001, '变更车道时只需开启转向灯，并迅速转向驶入相应的车道，以不妨碍同车道机动车正常行驶。', NULL, '对', '错', '空', '空', '错', '<b>不确认安全，迅速变道会干扰其他车辆正常行驶</b>，极易引发交通事故。所以<b>做法错误</b>。<br>变更车道时应<b>提前开启转向灯</b>，然后<b>观察</b>有无安全变更车道的条件，在<b>不妨碍</b>其他车辆正常行驶的情况下，<b>保持安全距离逐渐驶入</b>要变更的车道。', '', '只需|迅速转向', 0004, 0.23);
INSERT INTO `question` VALUES (1733399794300329986, 719, '0146a', 0001, '驾驶机动车在雾天行车开启雾灯和危险报警闪光灯。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第五十八条 机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。<b>机动车雾天行驶应当开启雾灯和危险报警闪光灯。</b>', '', '雾天|雾灯', 0003, 0.09);
INSERT INTO `question` VALUES (1733399794342273026, 720, '6d45d', 0002, '驾驶机动车需要在路边停车时怎样选择停车地点？ ', 'A、在人行道上停放 B、在路边随意停放 C、在停车泊位内停放 D、靠左侧路边逆向停放', '在人行道上停放 ', '在路边随意停放 ', '在停车泊位内停放 ', '靠左侧路边逆向停放', 'C', '<b>机动车停放</b>应当遵守下列规定：（一）在停车场或者交通标志、标线指示的道路<b>停车泊位</b>等规定地点停放。（二）在道路<b>停车泊位</b>内停放时，车身不得超出<b>停车泊位</b>。', '', '停车地点', 0001, 0.03);
INSERT INTO `question` VALUES (1733399794384216066, 721, '046cc', 0001, '变更车道时只需开启转向灯，便可迅速转向驶入相应的行车道。', NULL, '对', '错', '空', '空', '错', '<b>不确认安全，迅速变道会干扰其他车辆正常行驶</b>，极易引发交通事故。所以<b>做法错误</b>。<br>变更车道时应<b>提前开启转向灯</b>，然后<b>观察</b>有无安全变更车道的条件，在<b>不妨碍</b>其他车辆正常行驶的情况下，<b>保持安全距离逐渐驶入</b>要变更的车道。', '', '只需|迅速转向', 0003, 0.10);
INSERT INTO `question` VALUES (1733399794434547714, 722, '0f847', 0001, '在这种急弯道路上行车应交替使用远近光灯。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第五十九条：机动车在<b>夜间通过急弯</b>、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，<b>应当交替使用远近光灯示意</b>。因此，题干表述正确。', 'https://timg.mnks.cn/20230926b1cd18ae.jpg', '急弯|交替使用', 0002, 0.06);
INSERT INTO `question` VALUES (1733399794484879361, 723, 'dd61c', 0001, '驾驶机动车变更车道前应仔细观察，目的是判断有无变更车道的条件。', NULL, '对', '错', '空', '空', '对', '变更车道前仔细观察、分析道路交通流的状态，以判断有无安全变道的条件，<b>正确选择变道时机</b>，<b>安全变道行驶</b>。所以，题干表述正确。', '', '目的是', 0001, 0.03);
INSERT INTO `question` VALUES (1733399794531016705, 724, '48ae6', 0001, '超车时，如果无法保证与被超车辆的安全间距，应主动放弃超车。', NULL, '对', '错', '空', '空', '对', '<b>超车时</b>要保持与被超越车辆的安全距离，观察好超车条件，有充分把握保证万无一失再开始超越，<b>无法保证与被超车辆的安全间距</b>说明不具备超车条件，应<b>主动放弃</b>超车。', '', '无法保证|主动放弃', 0001, 0.02);
INSERT INTO `question` VALUES (1733399794593931266, 725, '0613c', 0002, '在距这段路多少米以内的路段不能停放机动车？ ', 'A、5米以内 B、10米以内 C、30米以内 D、50米以内', '5米以内 ', '10米以内 ', '30米以内 ', '50米以内', 'D', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br><b>（2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；</b>【口5】<br>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；【站3】', 'https://timg.mnks.cn/20230927fcdd4dac.jpg', '不能停放', 0004, 0.19);
INSERT INTO `question` VALUES (1733399794635874305, 726, 'd1a91', 0001, '驾驶车辆汇入车流时，应提前开启转向灯，保持直线行驶，通过后视镜观察左右情况，确认安全后汇入合流。', NULL, '对', '错', '空', '空', '对', '驾驶机动车汇入车流时，要通过后视镜随时注意观察两侧道路情况，提前开启转向灯，保持直线行驶，在不影响其他车辆通行的前提下，逐渐驶入正常行驶道路。', '', '汇入车流|确认安全', 0001, 0.01);
INSERT INTO `question` VALUES (1733399794677817346, 727, '50446', 0002, '驾驶机动车正在被其他车辆超越时，被超车辆减速靠右侧行驶的目的是什么？ ', 'A、给该车让出足够的超车空间 B、以便随时停车 C、避让行人与非机动车 D、以上选项都不正确', '给该车让出足够的超车空间 ', '以便随时停车 ', '避让行人与非机动车 ', '以上选项都不正确', 'A', '在被其他车辆超越时，在不妨碍安全行车的原则下，应主动减速靠右让行，目的是<b>为了给该车留出足够的超车空间，安全超越</b>。', '', '被其他车辆超越|减速靠右', 0003, 0.11);
INSERT INTO `question` VALUES (1733399794723954690, 728, 'bfd2d', 0001, '在道路与铁路道口遇到一个红灯亮时要尽快通过道口。', NULL, '对', '错', '空', '空', '错', '道路与铁路平面交叉道口有两个红灯交替闪烁或<b>一个红灯亮时</b>，表示火车要通过了，此时<b>禁止车辆、行人通行</b>，车辆不得越过停止线。红灯熄灭时，表示允许车辆行人通行。', 'https://timg.mnks.cn/20230926e87f10e6.jpg', '尽快通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399794795257858, 729, 'c8d7c', 0001, '夜间驾驶机动车通过人行横道时需要交替使用远近光灯。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过<b>急弯</b>、<b>坡路</b>、<b>拱桥</b>、<b>人行横道</b>或者<b>没有交通信号灯控制的路口</b>时，应当<b>交替使用远近光灯</b>示意。因此，题干表述正确。', '', '交替使用', 0003, 0.09);
INSERT INTO `question` VALUES (1733399795000778753, 730, 'e65b5', 0001, '在道路上临时停车不得妨碍其他车辆和行人通行。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第五十六条，机动车应当在规定地点停放。禁止在人行道上停放机动车；但是，依照本法第三十三条规定施划的停车泊位除外。<br><b>在道路上临时停车的，不得妨碍其他车辆和行人通行</b>。', '', '不得妨碍', 0003, 0.10);
INSERT INTO `question` VALUES (1733399795046916097, 731, '50de0', 0001, '驾驶机动车找不到停车位时可以借人行道停放。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第五十六条，机动车应当在规定地点停放。<b>禁止在人行道上停放机动车</b>；但是，依照本法第三十三条规定施划的停车泊位除外。<br>在道路上临时停车的，不得妨碍其他车辆和行人通行。', '', '可以借', 0002, 0.04);
INSERT INTO `question` VALUES (1733399795101442049, 732, 'fda5b', 0002, '人行横道上禁止掉头的原因是什么？ ', 'A、人行横道禁止车辆通行 B、避免妨碍行人正常通行，确保行人安全 C、人行横道禁止停车 D、路段有监控设备', '人行横道禁止车辆通行 ', '避免妨碍行人正常通行，确保行人安全 ', '人行横道禁止停车 ', '路段有监控设备', 'B', '人行横道线是为行人横过道路而设置的安全通道上的标线，常被称为“生命线”，驾驶人驾车行经人行横道时，应减速观察，礼让行人，不得在人行横道上进行掉头、停车、倒车等操作，以免妨碍行人正常通行，确保行人安全。', '', '禁止掉头', 0002, 0.05);
INSERT INTO `question` VALUES (1733399795143385089, 733, '68905', 0001, '距离交叉路口50米以内的路段不能停车。', NULL, '对', '错', '空', '空', '对', '【口五站三】距离交叉路口50米内路段，<b>不得停车</b>。根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br>（2）<b>交叉路口、</b>铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及<b>距离上述地点50米以内的路段</b>，<b>不得停车</b>；【口5】<br>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；【站3】', '', '50米以内', 0002, 0.07);
INSERT INTO `question` VALUES (1733399795185328129, 734, '83949', 0001, '在划有道路中心线的道路上会车时，应当保持安全车速、不越线行驶。', NULL, '对', '错', '空', '空', '对', '在划有道路中心线的道路上会车，为了保障安全，要保持安全车速。同时因为越过中心线对向车容易相撞，所以不能越过中心线会车。', '', '道路中心线|会车', 0001, 0.02);
INSERT INTO `question` VALUES (1733399795227271170, 735, '4ccf0', 0001, '驾驶机动车上坡时，在将要到达坡道顶端时要加速并鸣喇叭。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。<b>机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当减速慢行，并鸣喇叭示意。</b> <br>冲坡不冲顶，题目所说的“到达坡道顶端时要加速”描述错误，本题答案应为错误。', '', '上坡|要加速', 0004, 0.19);
INSERT INTO `question` VALUES (1733399795265019906, 736, '749e8', 0001, '这个路段可以在非机动车道上临时停车。', NULL, '对', '错', '空', '空', '错', '在设有禁停标志、标线的路段，在机动车道与<b>非机动车道</b>、人行道之间设有隔离设施的路段以及人行横道、施工地段，<b>不得停车</b>。', 'https://timg.mnks.cn/20230927edeead85.jpg', '可以在', 0001, 0.03);
INSERT INTO `question` VALUES (1733399795323740161, 737, 'c5d70', 0002, '如图所示，在超车过程中，遇对向有来车时要放弃超车是因为什么？ ', 'A、前车车速快 B、如继续超车，易与对面机动车发生刮擦、相撞 C、对向来车车速快 D、我方车辆提速太慢', '前车车速快 ', '如继续超车，易与对面机动车发生刮擦', '相撞 C', '对向来车车速快 D', 'B', '<b>借用对向车道超车，首先得保证对向无来车。</b>如图所示，发现与对面来车有会车可能时，要主动放弃超车。因为如果继续超车，无法保证安全的横向间距，很容易与对向来车发生刮擦、碰撞事故。', 'https://timg.mnks.cn/20230926ad8e67a9.jpg', '超车|对向有来车', 0001, 0.03);
INSERT INTO `question` VALUES (1733399795365683202, 738, 'c2c0f', 0001, '社会车辆距离消防栓或者消防队(站)门前30米以内的路段不能停车。', NULL, '对', '错', '空', '空', '对', '【口五站三】距离消防站门前30米以内路段不得停车。根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br>（2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；【口5】<br><b>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；</b>【站3】', '', '30米|不能停车', 0002, 0.08);
INSERT INTO `question` VALUES (1733399795420209153, 739, '8759a', 0001, '距离桥梁、陡坡、隧道50米以内的路段不能停车。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：交叉路口、铁路道口、急弯路、宽度不足４米的窄路、<b>桥梁、陡坡、隧道</b>以及距离上述地点<b>50米以内的路段</b>，<b>不得停车</b>。', '', '50米以内', 0002, 0.06);
INSERT INTO `question` VALUES (1733399795470540802, 740, '04e39', 0001, '行车中从其他道路汇入车流前，应注意观察侧后方车辆的动态。', NULL, '对', '错', '空', '空', '对', '驾驶机动车从其他道路汇入车流不得影响车道内其他车辆的正常行驶，因此，汇入车流前，<b>应注意观察侧后方车辆的动态</b>，要分析道路交通流的状态，正确地选择汇入时机，安全汇入。', '', '汇入|注意观察', 0001, 0.02);
INSERT INTO `question` VALUES (1733399795520872449, 741, '729d0', 0001, '如图所示，当越过停在人行横道前的A车时，B车应减速，准备停车让行。', NULL, '对', '错', '空', '空', '对', '交通法规定：机动车在<b>通过人行横道时，应减速慢行</b>，遇到<b>行人通过人行横道</b>时，应<b>停车礼让</b>。如图所示，B车越过A车后，前方是人行横道，有行人在通行，B车应当减速行驶，随时准备停车礼让行人。', 'https://timg.mnks.cn/20230926dd34e77b.jpg', '减速|停车让行', 0003, 0.13);
INSERT INTO `question` VALUES (1733399795579592706, 742, 'b708c', 0001, '距离宽度不足4米的窄路50米以内的路段不能停车。', NULL, '对', '错', '空', '空', '对', '《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br>（2）交叉路口、铁路道口、急转弯、<b>宽度不足4米的窄路、</b>桥梁、陡坡、隧道以及距离上述地点<b>50米以内的路段</b>，不得停车；<b>【口5】</b><br>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点<b>30米以内</b>的路段，除使用上述设施的以外，不得停车；<b>【站3】</b>', '', '50米以内', 0002, 0.06);
INSERT INTO `question` VALUES (1733399795621535746, 743, '39554', 0001, '如图所示，A车在这种情况下应适当减速。', NULL, '对', '错', '空', '空', '对', '如图所示，B车要减速转弯，A车也要适当减速，以免两车发生碰撞。', 'https://timg.mnks.cn/20230926f364bd4d.jpg', '适当减速', 0002, 0.04);
INSERT INTO `question` VALUES (1733399795659284481, 744, 'fd962', 0001, '机动车停稳前不能开车门和上下人员。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第六十三条　机动车在道路上临时停车，应当遵守下列规定： <b>(四)车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行</b>；', '', '不能', 0001, 0.03);
INSERT INTO `question` VALUES (1733399795713810433, 745, 'b457a', 0001, '驾驶车辆通过人行横道线时，应注意礼让行人。', NULL, '对', '错', '空', '空', '对', '驾驶机动车接近人行横道线时，提前减速观察，注意观察人行横道左右两侧是否有行人通行，随时准备停车礼让行人。', '', '人行横道|礼让行人', 0001, 0.01);
INSERT INTO `question` VALUES (1733399795759947778, 746, 'f006c', 0002, '当您即将通过交叉路口的时候，才意识到要左转而不是向前，以下说法正确的是什么？ ', 'A、继续向前行驶 B、停在交叉路口，等待安全时左转 C、在确保安全的情况下，倒车然后左转 D、以上说法都不正确', '继续向前行驶 ', '停在交叉路口，等待安全时左转 ', '在确保安全的情况下，倒车然后左转 ', '以上说法都不正确', 'A', '左转弯或右转弯前需要变更车道时，需按导向箭头的指示在虚线区变更车道，进入实线区后不能变更车道。题干中情形是<b>即将通过</b>交叉路口，说明此时机动车已在实线区，因此<b>应当继续向前行驶</b>。按规定，交叉路口既不可以停车，也不可以倒车。', '', '交叉路口', 0004, 0.24);
INSERT INTO `question` VALUES (1733399795818668034, 747, '34c8e', 0001, '打开机动车车门时，不得妨碍其他车辆和行人通行。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第七十七条：乘坐机动车应当遵守下列规定：<br>(一)不得在机动车道上拦乘机动车;<br>(二)在机动车道上不得从机动车左侧上下车;<br><b>(三)开关车门不得妨碍其他车辆和行人通行;</b><br>(四)机动车行驶中，不得干扰驾驶，不得将身体任何部分伸出车外，不得跳车;<br>(五)乘坐两轮摩托车应当正向骑坐。', '', '不得妨碍', 0001, 0.03);
INSERT INTO `question` VALUES (1733399795873193985, 748, 'be031', 0002, '遇到前方车辆停车排队或者缓慢行驶时，强行穿插，以下说法正确的是什么？ ', 'A、禁止，因为这样不利于省油 B、禁止，因为这样扰乱车流，加重拥堵 C、允许，因为可以快速的通过拥堵区 D、允许，因为可以省油', '禁止，因为这样不利于省油 ', '禁止，因为这样扰乱车流，加重拥堵 ', '允许，因为可以快速的通过拥堵区 ', '允许，因为可以省油', 'B', '驾驶车辆遇前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待；连续鸣喇叭催促、抢行、加塞或<b>穿插绕行</b>、选择空当逐车超越等做法都<b>会加剧道路拥堵</b>或路口堵塞，甚至会发生剐碰事故。', '', '强行穿插', 0002, 0.06);
INSERT INTO `question` VALUES (1733399795910942721, 749, '81f6f', 0001, '车辆起步前，驾驶人应对车辆周围交通情况进行观察，确认安全时再开始起步。', NULL, '对', '错', '空', '空', '对', '车辆起步前，驾驶人应绕车一周进行检查，确认安全，观察车辆外观有无损毁，周围环境是否安全，车底有无异常情况。打开车门前应观察后方交通情况。', '', '确认安全', 0001, 0.01);
INSERT INTO `question` VALUES (1733399795957080066, 750, '9af24', 0002, '如图所示，在高速公路同方向两条机动车道左侧车道行驶，应保持什么车速？ ', 'A、110公里/小时～130公里/小时 B、100公里/小时～120公里/小时 C、90公里/小时～110公里/小时 D、60公里/小时～120公里/小时', '110公里/小时～130公里/小时 ', '100公里/小时～120公里/小时 ', '90公里/小时～110公里/小时 ', '60公里/小时～120公里/小时', 'B', '<b>首先看清楚是两条车道还是3条车道。</b>《道路交通安全法实施条例》第七十八条：高速公路最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。<br>1、同方向有<b>2条车道</b>的，<b>左侧车道</b>的最低车速为<b>每小时100公里</b>；\n<p></p><img src=\"https://timg.mnks.cn/20231009c289c086.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\">\n<p></p>', 'https://timg.mnks.cn/20230926da635dee.jpg', '同方向两条|左侧', 0004, 0.21);
INSERT INTO `question` VALUES (1733399796015800321, 751, '4d11d', 0002, '如图所示，在高速公路同方向三条机动车道右侧车道行驶，车速不能低于多少？ ', 'A、100公里/小时 B、60公里/小时 C、110公里/小时 D、80公里/小时', '100公里/小时 ', '60公里/小时 ', '110公里/小时 ', '80公里/小时', 'B', '根据《道路交通安全法实施条例》第七十八条规定，高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，<b>最低车速不得低于每小时60公里</b>。\n<p><img src=\"https://timg.mnks.cn/20231009fb8a2501.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>', 'https://timg.mnks.cn/20230926a273cef2.jpg', '同方向三条|右侧', 0004, 0.24);
INSERT INTO `question` VALUES (1733399796057743361, 752, 'c2c3a', 0002, '如图所示，在高速公路同方向三条机动车道中间车道行驶，车速不能低于多少？ ', 'A、100公里/小时 B、90公里/小时 C、110公里/小时 D、60公里/小时', '100公里/小时 ', '90公里/小时 ', '110公里/小时 ', '60公里/小时', 'B', '《道路交通安全法实施条例》第七十八条：同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。三车道最低限速从左到右分别是：110、90、60。<br>\n<p><img src=\"https://timg.mnks.cn/20231009fb8a2501.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>', 'https://timg.mnks.cn/20230926b40beb7f.jpg', '同方向三条|中间车道', 0004, 0.23);
INSERT INTO `question` VALUES (1733399796112269314, 753, '9588c', 0002, '如图所示，在高速公路同方向三条机动车道最左侧道行驶，应保持什么车速？ ', 'A、110公里/小时～120公里/小时 B、100公里/小时～120公里/小时 C、90公里/小时～110公里/小时 D、60公里/小时～120公里/小时', '110公里/小时～120公里/小时 ', '100公里/小时～120公里/小时 ', '90公里/小时～110公里/小时 ', '60公里/小时～120公里/小时', 'A', '《道路交通安全法实施条例》第七十八条：高速公路最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。<br>1、同方向有2条车道的，左侧车道的最低车速为每小时100公里；<br>2、<b>同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里</b>，中间车道的最低车速为每小时90公里；<br>3、道路限速标志标明的车速与上述车道行驶车速的规定不一致的，技照道路限速标志标明的车速行驶。<br>\n<p><img src=\"https://timg.mnks.cn/20231009fb8a2501.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>', 'https://timg.mnks.cn/20230926dc888a10.jpg', '同方向三条|最左侧', 0005, 0.31);
INSERT INTO `question` VALUES (1733399796158406657, 754, '0bb47', 0002, '如图所示，当您车速为95km/h时，您可以在哪条车道内行驶？ ', 'A、车道A B、车道B C、车道C D、车道D', '车道A ', '车道B ', '车道C ', '车道D', 'B', '如图所示，A车道限速为100~120km/h，<b>B车道</b>限速为<b>80~100km/h</b>，C车道限速为60~80km/h，因此，车速为95km/h时，可以在<b>B车道</b>内行驶。', 'https://timg.mnks.cn/20230926f9770bdd.jpg', '95km/h', 0003, 0.10);
INSERT INTO `question` VALUES (1733399796208738306, 755, '97370', 0002, '在这段高速公路上行驶的最高车速是多少？ ', 'A、120公里/小时 B、100公里/小时 C、90公里/小时 D、60公里/小时', '120公里/小时 ', '100公里/小时 ', '90公里/小时 ', '60公里/小时', 'A', '<b>限制速度</b>标志：本标志至前方解除限制速度标志或另一块不同限速值的限制速度标志的路段内，<b>机动车行驶速度不准超过标志所示数值</b>。如图所示<b>数值为120</b>，因此，在这段高速公路上行驶的<b>最高车速是120公里/小时</b>。', 'https://timg.mnks.cn/20230927e0ebf88d.jpg', '最高车速', 0002, 0.05);
INSERT INTO `question` VALUES (1733399796250681346, 756, 'f2824', 0002, '在这段高速公路上行驶的最低车速是多少？ ', 'A、50公里/小时 B、60公里/小时 C、80公里/小时 D、100公里/小时', '50公里/小时 ', '60公里/小时 ', '80公里/小时 ', '100公里/小时', 'B', '在这段高速公路上行驶的<b>最低车速是60公里/小时</b>。<br>\n<p><img src=\"https://tp.mnks.cn/tp_217.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>最低限速</b>：前方道路的最低时速限制。<br><img src=\"https://tp.mnks.cn/tp_195.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>限制速度：本标志至前方解除限制速度标志或另一块不同限速值的限制速度标志的路段内，机动车行驶速度不准超过标志所示数值。</p>', 'https://timg.mnks.cn/20230927e21fd054.jpg', '最低车速', 0002, 0.04);
INSERT INTO `question` VALUES (1733399796363927553, 757, '3f5c8', 0002, '在这条高速公路上行驶时的最高速度不能超过多少？ ', 'A、100公里/小时 B、110公里/小时 C、120公里/小时 D、90公里/小时', '100公里/小时 ', '110公里/小时 ', '120公里/小时 ', '90公里/小时', 'B', '如图所示，<b>前方限速标志数值110</b>，表示在这条高速公路上行驶时的<b>最高速度不能超过110公里/小时</b>。 <br>根据《道路交通安全法实施条例》第七十八条规定，道路限速标志标明的车速与车道行驶车速的规定不一致的，按照道<b>路限速标志标明的车速</b>行驶。', 'https://timg.mnks.cn/20230926b5740cd5.jpg', '最高速度', 0003, 0.12);
INSERT INTO `question` VALUES (1733399796481368066, 758, '2bb47', 0002, '在这条车道行驶的最高车速是多少？ ', 'A、120公里/小时 B、110公里/小时 C、100公里/小时 D、90公里/小时', '120公里/小时 ', '110公里/小时 ', '100公里/小时 ', '90公里/小时', 'D', '道路限速标志标明的车速与车道行驶车速的规定不一致的，<b>按照道路限速标志标明的车速行驶</b>。如图所示，<b>道路限速标志90</b>公里/小时，因此车道行驶的最高车速是90公里/小时。', 'https://timg.mnks.cn/20230927ae4ad470.jpg', '最高车速', 0003, 0.12);
INSERT INTO `question` VALUES (1733399796577837058, 759, '554bd', 0002, '在这条车道行驶的最低车速是多少？ ', 'A、60公里/小时 B、90公里/小时 C、100公里/小时 D、110公里/小时', '60公里/小时 ', '90公里/小时 ', '100公里/小时 ', '110公里/小时', 'C', '《道路交通安全法实施条例》第七十八条：1、高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。 <br>2、<b>同方向有2条车道的，左侧车道的最低车速&lt;为<b>每小时100公里</b></b>；同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。 <br>如图所示同方向2条车道，且车辆在最左侧，所以<b>最低车速</b>为<b>每小时100公里</b>。 <br>图中白底红圈的120禁令标志，表示高速公路最高限速120公里/小时。', 'https://timg.mnks.cn/20230927f32e78c8.jpg', '最低车速', 0005, 0.43);
INSERT INTO `question` VALUES (1733399796623974401, 760, 'c98cc', 0002, '在这条车道行驶的最低车速是多少？ ', 'A、60公里/小时 B、90公里/小时 C、100公里/小时 D、110公里/小时', '60公里/小时 ', '90公里/小时 ', '100公里/小时 ', '110公里/小时', 'B', '如图所示机动车行驶在三车道的<b>中间车道</b>，最低车速是<b>90公里/小时</b>。《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。在高速公路上行驶的小型载客汽车最高车速不得超过每小时120公里，其他机动车不得超过每小时100公里，摩托车不得超过每小时80公里。<br>2、<b>同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。</b>', 'https://timg.mnks.cn/20230927f0298b58.jpg', '最低车速', 0005, 0.36);
INSERT INTO `question` VALUES (1733399796665917442, 761, 'e39ab', 0002, '在这条城市道路上行驶的最高速度不能超过多少？ ', 'A、30公里/小时 B、40公里/小时 C、50公里/小时 D、70公里/小时', '30公里/小时 ', '40公里/小时 ', '50公里/小时 ', '70公里/小时', 'C', '如图所示，同方向<b>只有1条机动车道的城市道路</b>，最高速度<b>不能超过50公里/小时</b>。<br>根据《道路交通安全法》第四十五条规定，在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；【无线】城3公4<br>(二)<b>同方向只有1条机动车道的道路</b>，<b>城市道路为每小时50公里</b>，公路为每小时70公里。<b>【有线】城5公7</b>。', 'https://timg.mnks.cn/20230926e1ca20ce.jpg', '城市道路', 0005, 0.46);
INSERT INTO `question` VALUES (1733399796720443394, 762, '97f42', 0002, '在这段城市道路上行驶的最高速度不能超过多少？ ', 'A、30公里/小时 B、40公里/小时 C、50公里/小时 D、70公里/小时', '30公里/小时 ', '40公里/小时 ', '50公里/小时 ', '70公里/小时', 'A', '根据《道路交通安全法实施条例》第四十五条规定 机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br>(一)<b>没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；</b><b>【无线】城3公4</b>。<br>(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。【有线】城5公7。', 'https://timg.mnks.cn/20230926c3a6c05a.jpg', '城市道路', 0003, 0.14);
INSERT INTO `question` VALUES (1733399796762386434, 763, '80c79', 0002, '在这条公路上行驶的最高速度不能超过多少？ ', 'A、30公里/小时 B、40公里/小时 C、50公里/小时 D、70公里/小时', '30公里/小时 ', '40公里/小时 ', '50公里/小时 ', '70公里/小时', 'D', '《道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；【无线】城3公4 <br>(二)<b>同方向</b>只有1条机动车道的道路，城市道路为每小时50公里，<b>公路为每小时70公里</b>。<b>【有线】城5公7</b>。<br>这道题看周围环境，路两边全是树木所以是公路。', 'https://timg.mnks.cn/20230926abbe1e74.jpg', '公路|最高速度', 0005, 0.36);
INSERT INTO `question` VALUES (1733399796800135170, 764, '0092c', 0002, '在这条公路上行驶的最高速度不能超过多少？ ', 'A、30公里/小时 B、40公里/小时 C、50公里/小时 D、70公里/小时', '30公里/小时 ', '40公里/小时 ', '50公里/小时 ', '70公里/小时', 'B', '如图所示，<b>在没有道路中心线的公路上行驶，最高限速是40</b>。《道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br><b>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；</b>【无线】城3公4<br>(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。【有线】城5公7', 'https://timg.mnks.cn/20230926cf833a11.jpg', '公路上', 0005, 0.43);
INSERT INTO `question` VALUES (1733399796837883905, 765, 'a413d', 0001, '如图所示，驾驶机动车遇到这种情况，即使人行横道上没有行人，也不能加速通过。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第四十七条，<b>机动车行经人行横道时，应当减速行驶</b>；遇行人正在通过人行横道，应当停车让行。因此，即使人行横道上没有行人，也不能加速通过，应减速注意观察，随时做好停车礼让的准备。', 'https://timg.mnks.cn/20230926c2a9799c.jpg', '不能加速', 0003, 0.11);
INSERT INTO `question` VALUES (1733399796875632642, 766, '4fbc1', 0001, '如图所示，机动车遇行人正在通过人行横道时，要停车让行，是因为行人享有优先通行权。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第四十七条：<b>机动车行经人行横道时，应当减速行驶；遇行人正在通过人行横道，应当停车让行。</b>机动车行经没有交通信号的道路时，遇行人横过道路，应当避让。', 'https://timg.mnks.cn/20230926cfd768a2.jpg', '停车让行|优先通行权', 0002, 0.04);
INSERT INTO `question` VALUES (1733399796921769985, 767, 'c28c2', 0001, '如图所示，在这种道路上行驶，应在道路中间通行的主要原因是在道路中间通行速度快。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第三十六条　根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。<b>没有划分</b>机动车道、非机动车道和人行道的，<b>机动车在道路中间通行</b>，非机动车和行人在道路两侧通行。', 'https://timg.mnks.cn/20230926caecbed0.jpg', '速度快|主要原因', 0002, 0.08);
INSERT INTO `question` VALUES (1733399796984684545, 768, 'da5e4', 0001, '如图所示，驾驶机动车遇到没有行人通过的人行横道时不用减速慢行。', NULL, '对', '错', '空', '空', '错', '机动车<b>行经人行横道</b>时，<b>应当减速</b>行驶；遇行人正在通过人行横道，应当停车让行。所以，题干表述错误。', 'https://timg.mnks.cn/20230926afea45c5.jpg', '不用减速', 0002, 0.05);
INSERT INTO `question` VALUES (1733399797076959233, 769, '61d97', 0002, '如图所示，铁路道口禁止掉头的原因是什么？ ', 'A、有铁路道口标志 B、容易引发事故 C、铁路道口车流量大 D、有铁路道口信号灯', '有铁路道口标志 ', '容易引发事故 ', '铁路道口车流量大 ', '有铁路道口信号灯', 'B', '铁道路口行车条件比较复杂，掉头容易引发事故。 交通法规定在<b>铁路道口</b>、人行横道、桥梁、急弯、陡坡、隧道等<b>容易发生危险</b>的路段<b>禁止掉头</b>。', 'https://timg.mnks.cn/20230926b9f2e5a4.jpg', '铁路|禁止掉头', 0003, 0.14);
INSERT INTO `question` VALUES (1733399797156651010, 770, 'f7fa2', 0002, '如图所示，驾车通过此路口注意观察是否有行人的同时，应如何操作？ ', 'A、加速左转 B、加速右转 C、减速慢行右转 D、减速慢行左转', '加速左转 ', '加速右转 ', '减速慢行右转 ', '减速慢行左转', 'C', '驾驶机动车通过有人行横道的交叉路口时应注意观察是否有行人；在路口右转弯遇到红灯亮时，可<b>减速慢行转弯通过</b>，但不应影响被放行方向的其他车辆和行人通行。', 'https://timg.mnks.cn/20230927b0cb64aa.jpg', '注意观察', 0002, 0.05);
INSERT INTO `question` VALUES (1733399797219565569, 771, '888e1', 0002, '如图所示，在这种情况下通过交叉路口时，不得超车的原因是什么？ ', 'A、机动车速度慢，不足以超越前车 B、路口有交通监控设备 C、路口设有信号灯 D、路口内交通情况复杂，易发生交通事故', '机动车速度慢，不足以超越前车 ', '路口有交通监控设备 ', '路口设有信号灯 ', '路口内交通情况复杂，易发生交通事故', 'D', '交叉路口车流量大，交通情况复杂，容易发生交通事故，不具备超车条件，所以<b>不得超车</b>。《道路交通安全法》第四十三条：行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、<b>市区交通流量大的路段</b>等没有超车条件的，<b>不得超车</b>。', 'https://timg.mnks.cn/20230926eb722587.jpg', '交叉路口|不得超车', 0002, 0.06);
INSERT INTO `question` VALUES (1733399797286674433, 772, '08d36', 0001, '车辆在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', NULL, '对', '错', '空', '空', '错', '机动车在路边起步后，要随时注意两侧道路情况，<b>向左缓慢转向</b>，在不影响其他车辆通行的前提下，<b>逐渐驶入</b>正常行驶道路。<b>尽快提速、迅速驶入是容易引发交通事故的错误做法。</b>', '', '尽快提速|迅速转向', 0003, 0.15);
INSERT INTO `question` VALUES (1733399797332811777, 773, '7d94f', 0001, '如图所示，在这种情况下只要后方没有来车，可以倒车。', NULL, '对', '错', '空', '空', '错', '如图所示，前方设有<b>单行路</b>指示标志，指示当前道路为<b><b>单行路</b></b>。《道路交通安全法实施条例》第五十条规定，机动车<b>倒车</b>时，应当察明车后情况，确认安全后<b>倒车</b>。<b>不得在</b>铁路道口、交叉路口、<b><b>单行路</b></b>、桥梁、急弯、陡坡或者隧道中<b>倒车</b>。', 'https://timg.mnks.cn/20230926fbe05ecb.jpg', '没有来车|倒车', 0003, 0.10);
INSERT INTO `question` VALUES (1733399797374754818, 774, '454f5', 0001, '驾驶小型载客汽车在高速公路行驶的最低车速为90公里/小时。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，<b>最低车速不得低于每小时60公里。</b>', '', '小型载客汽车|最低车速', 0004, 0.17);
INSERT INTO `question` VALUES (1733399797416697858, 775, '29e6c', 0001, '如图所示，在这种雾天情况下，通过交叉路口时必须鸣喇叭，加速通过，以免造成交通拥堵。', NULL, '对', '错', '空', '空', '错', '雾天通过交叉路口时，要将车速控制在能及时停车的范围内，保持足够的安全间距，<b>谨慎缓慢行驶</b>，可多使用喇叭引起行人和车辆注意。<b>加速通过，做法错误。</b>', 'https://timg.mnks.cn/20230926a77d93c8.jpg', '雾天|加速通过', 0002, 0.06);
INSERT INTO `question` VALUES (1733399797467029506, 776, '18bc1', 0001, '驾驶机动车在高速公路要按照限速标志标明的车速行驶。', NULL, '对', '错', '空', '空', '对', '在高速公路行车道行驶，要严格遵守“分道行驶、各行其道”和限速规定，根据车辆行驶速度正确选择行车道，不得随意穿行越线，不准骑、轧车行道分界线，不得占用应急车道。有限速标志的路段，严格将车速控制在限速范围内。<b>限速标志标明的车速</b>与<b>车道行驶车速</b>的规定<b>不一致时</b>，按照<b>限速标志标明的车速</b>行驶。', '', '要按照', 0001, 0.03);
INSERT INTO `question` VALUES (1733399797513166849, 777, '54eed', 0002, '如图所示，在高速公路最左侧车道行驶，想驶离高速公路，以下说法正确的是什么？ ', 'A、每次变更一条车道，直到最右侧车道 B、为了快速变更车道，可以加速超越右侧车辆后变更车道 C、找准机会一次变更到最右侧车道 D、立即减速后向右变更车道', '每次变更一条车道，直到最右侧车道 ', '为了快速变更车道，可以加速超越右侧车辆后变更车道 ', '找准机会一次变更到最右侧车道 ', '立即减速后向右变更车道', 'A', '高速公路“左进右出”，驶离高速要提前变更到最右侧车道。如图所示，从最左侧车道变更到最右侧车道最安全的做法是：<b>每次只变更一条车道</b>，不可连续跨多车道变道，而且一定要在确保安全的情况下及时合理地变更。', 'https://timg.mnks.cn/20230926ba03799a.jpg', '最左侧|驶离', 0003, 0.14);
INSERT INTO `question` VALUES (1733399797550915586, 778, 'd790c', 0002, '在这个位置时怎样使用灯光？ ', 'A、开启左转向灯 B、开启右转向灯 C、开启危险报警闪光灯 D、开启前照灯', '开启左转向灯 ', '开启右转向灯 ', '开启危险报警闪光灯 ', '开启前照灯', 'A', '根据《道路交通安全法实施条例》第七十九条：机动车<b>从匝道驶入高速</b>公路，应当<b>开启左转向灯</b>，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道。', 'https://timg.mnks.cn/20230927f4bc4d85.jpg', '', 0005, 0.29);
INSERT INTO `question` VALUES (1733399797601247234, 779, '95624', 0001, '倒车过程中要缓慢行驶，注意观察车辆两侧和后方的情况，随时做好停车准备。', NULL, '对', '错', '空', '空', '对', '倒车与前进相比，死角盲区非常多，操作难度大。<b>倒车过程中应缓慢行驶、<b>注意观察</b>车辆两侧和后方的情况，并随时注意车头两侧的安全空间位置，随时做好停车让行的准备</b>，以免发生剐蹭事故。', '', '倒车|注意观察', 0001, 0.02);
INSERT INTO `question` VALUES (1733399797643190274, 780, 'f5951', 0002, '进入减速车道时怎样使用灯光？ ', 'A、开启左转向灯 B、开启右转向灯 C、开启危险报警闪光灯 D、开启前照灯', '开启左转向灯 ', '开启右转向灯 ', '开启危险报警闪光灯 ', '开启前照灯', 'B', '<b>向右驶入减速车道</b>，驾驶人应在距出口500米时，打开<b>右转向灯</b>，适时减速，平顺地进入减速车道。', 'https://timg.mnks.cn/20230927db421693.jpg', '减速车道', 0003, 0.11);
INSERT INTO `question` VALUES (1733399797676744706, 781, '16c8e', 0001, '如图所示，在这种情况下准备进入环形路口时，为了保证车后车流的通畅，应加速超越红车进入路口。', NULL, '对', '错', '空', '空', '错', '环岛是交通事故多发地点，通过时应在距环岛50-100米处减速慢行，适时控制车速，以逆时针方向进入环岛。<b>驶近环岛时，注意观察左侧已在环岛内行驶车辆的动态，适时汇入车流，</b><b>必要时减速或停车让行</b>。加速超越做法错误。', 'https://timg.mnks.cn/20230926d6f79687.jpg', '环形路口|加速超越', 0001, 0.03);
INSERT INTO `question` VALUES (1733399797722882049, 782, 'd8347', 0001, '如图所示，右侧标志表示前方路口要停车让行。', NULL, '对', '错', '空', '空', '对', '如图所示，交叉路口右侧为<b>停车让行</b>标志，表示车辆应当<b>停车让行</b>，观察主路情况，确认安全后再通行。', 'https://timg.mnks.cn/20230926a922d127.jpg', '停车让行', 0002, 0.08);
INSERT INTO `question` VALUES (1733399797769019393, 783, '557a6', 0002, '在以下哪些路段不能倒车？ ', 'A、交叉路口 B、隧道 C、急弯 D、以上皆是', '交叉路口 ', '隧道 ', '急弯 ', '以上皆是', 'D', '《道路交通安全法实施条例》第五十条，机动车倒车时，应当察明车后情况，确认安全后倒车。<b>不得在</b>铁路道口、<b>交叉路口</b>、单行路、桥梁、<b>急弯</b>、陡坡或者<b>隧道中倒车</b>。', '', '不能倒车', 0003, 0.12);
INSERT INTO `question` VALUES (1733399797806768130, 784, 'f9150', 0002, '驾驶小型载客汽车在高速公路上时速低于100公里时的最小跟车距离是多少？ ', 'A、不得少于50米 B、不得少于30米 C、不得少于20米 D、不得少于10米', '不得少于50米 ', '不得少于30米 ', '不得少于20米 ', '不得少于10米', 'A', '根据《道路交通安全法实施条例》第八十条 机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离；车速<b>低于每小时100公里</b>时，与同车道前车距离可以适当缩短，但<b>最小距离不得少于50米</b>。', '', '低于100公里', 0002, 0.06);
INSERT INTO `question` VALUES (1733399797840322561, 785, 'd4180', 0001, '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', NULL, '对', '错', '空', '空', '错', '预计在超车过程中与对面来车有会车可能时，应<b>提前减速</b>，主动<b>放弃超车</b>。若加速超越，有可能会引起不必要的事故。', '', '超车|会车|加速超越', 0002, 0.06);
INSERT INTO `question` VALUES (1733399797878071297, 786, '4c137', 0001, '如图所示，在这种情形下，对方车辆具有先行权。', NULL, '对', '错', '空', '空', '对', '<b>【遇障碍“有让无”】</b>如图所示，在有障碍的路段会车时，有障碍的一方应让无障碍的一方先行。因此<b>对方车辆具有先行权</b>。', 'https://timg.mnks.cn/20230926f9450893.jpg', '对方|先行权', 0001, 0.03);
INSERT INTO `question` VALUES (1733399797920014338, 787, 'aafb9', 0001, '遇前方路段车道减少，车辆行驶缓慢，为保证道路通畅，应借对向车道迅速通过。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十三条，机动车在<b>遇有前方机动车停车排队等候或者缓慢行驶</b>时，<b>应当</b>依次排队，<b>不得</b>从前方车辆两侧穿插或者超越行驶，<b>不得</b>在人行横道，网状线区域内停车等候。<br>借对向车道迅速通过的做法是错误的，容易加剧拥堵，甚至发生剐碰事故，不利于道路通畅。', '', '借对向车道', 0003, 0.09);
INSERT INTO `question` VALUES (1733399797957763073, 788, '05dde', 0002, '驾驶机动车在高速公路遇到能见度低于50米的气象条件时，车速不得超过20公里/小时，还应怎么做？ ', 'A、进入应急车道行驶 B、尽快驶离高速公路 C、在路肩低速行驶 D、尽快在路边停车', '进入应急车道行驶 ', '尽快驶离高速公路 ', '在路肩低速行驶 ', '尽快在路边停车', 'B', '《道路交通安全法实施条例》第八十一条：<b>机动车在高速公路上行驶</b>，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：(一)能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；(三)<b>能见度小于50米时</b>，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，<b>车速不得超过每小时20公里</b>，并从最近的出口<b>尽快驶离</b>高速公路。', '', '能见度低于50米', 0003, 0.15);
INSERT INTO `question` VALUES (1733399797999706114, 789, 'b296d', 0001, '通过隧道时，不得超车。', NULL, '对', '错', '空', '空', '对', '由于隧道内光线差，并且通常只有两三条车道，因此<b>隧道内严禁</b>在变更车道、<b>超车</b>和随意停车。<br>《道路交通安全法》 第四十三条 同车道行驶的机动车，有下列情形之一的，不得<b>超车</b>： <br>（四）<b>行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有<b>超车</b>条件的。</b>', '', '不得超车', 0001, 0.03);
INSERT INTO `question` VALUES (1733399798041649153, 790, '3398c', 0001, '如图所示，当您超越右侧车辆时，应该尽快超越，减少并行时间。', NULL, '对', '错', '空', '空', '对', '超车之前观察好超车条件<b>，确认安全</b>；超车时<b>切忌犹豫不决</b>，要果断超越，减少两车的并行时间。<br>如图所示，对向无来车，具备超车条件，因此要快速超越，不要犹豫不决。', 'https://timg.mnks.cn/20230926c2a583c7.jpg', '尽快超越', 0004, 0.16);
INSERT INTO `question` VALUES (1733399798083592193, 791, '4fba9', 0002, '在这个弯道上行驶时的最高速度不能超过多少？ ', 'A、30公里/小时 B、40公里/小时 C、50公里/小时 D、70公里/小时', '30公里/小时 ', '40公里/小时 ', '50公里/小时 ', '70公里/小时', 'A', '机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，通过铁路道口、<b>急弯路</b>、窄路、窄桥时；　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在冰雪、泥泞的道路上行驶时；', 'https://timg.mnks.cn/20230926a2ba0b94.jpg', '弯道', 0003, 0.13);
INSERT INTO `question` VALUES (1733399798133923841, 792, 'edadb', 0002, '驾驶机动车驶离高速公路时，在这个位置怎样行驶？ ', 'A、继续向前行驶 B、驶入减速车道 C、车速保持100公里/小时 D、车速降到40公里/小时以下', '继续向前行驶 ', '驶入减速车道 ', '车速保持100公里/小时 ', '车速降到40公里/小时以下', 'B', '根据《道路交通安全法实施条例》第七十九条规定，机动车从匝道驶入高速公路，应当开启左转向灯，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道。 <br><b>机动车驶离高速公路时</b>，应当开启右转向灯，<b>驶入减速车道</b>，降低车速后驶离。', 'https://timg.mnks.cn/20230927e2788585.jpg', '驶离', 0005, 0.31);
INSERT INTO `question` VALUES (1733399798175866881, 793, '9b75f', 0002, '如图所示，当车辆驶近这样的路口时，以下说法错误的是什么？ ', 'A、为避免车辆从路口突然冲出引发危险，应适当降低车速 B、本车有优先通行权，可加速通过 C、因为视野受阻，应鸣喇叭提醒侧方道路来车 D、右前方路口视野受阻，如有突然冲出车辆，容易引发事故', '为避免车辆从路口突然冲出引发危险，应适当降低车速 ', '本车有优先通行权，可加速通过 ', '因为视野受阻，应鸣喇叭提醒侧方道路来车 ', '右前方路口视野受阻，如有突然冲出车辆，容易引发事故', 'B', '请注意，题干要求选出说法错误的选项。驾驶机动车驶近没有交通信号灯控制的路口时，应仔细观察左、右两侧道路交通情况，全面预防险情，减速或停车瞭望，做到“一看，二慢，三通过”，直行车辆优先通行。<b>因此，有加速通过的选项错误。</b>', 'https://timg.mnks.cn/20230926d2b29cb3.jpg', '说法错误', 0004, 0.18);
INSERT INTO `question` VALUES (1733399798222004225, 794, '27363', 0001, '驾驶车辆可以从这个位置直接驶入高速公路行车道。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第七十九条：机动车从匝道驶入高速公路，应当开启左转向灯，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道，<b>不得从匝道直接驶入行车道</b>。所以本题错在“直接驶入”。', 'https://timg.mnks.cn/20230927e05d72c7.jpg', '直接驶入', 0002, 0.05);
INSERT INTO `question` VALUES (1733399798305890305, 795, 'bbc65', 0002, '如图所示，驾驶机动车遇这种情况时，不能超越前方车辆的原因是什么？ ', 'A、本车车速不足以超越前车 B、前车速度过快 C、路中心为黄线 D、前车正在超车', '本车车速不足以超越前车 ', '前车速度过快 ', '路中心为黄线 ', '前车正在超车', 'D', '《道路交通安全法》第四十三条　同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。<b>有下列情形之一的，不得超车</b>：<br>（一）<b>前车正在</b>左转弯、掉头、<b>超车的</b>；<br>如图所示，前方蓝色车辆正在超车，因此这种情况下，我方车辆不得超车。<br>【前车正在超车，后车为何不能超越？】<br>因为正在超车的前车，车速同时也正在加快，若后车强行超越正在超车的车辆，会形成超速行驶，增大危险系数。', 'https://timg.mnks.cn/20230926b63b76b1.jpg', '不能|超车', 0002, 0.07);
INSERT INTO `question` VALUES (1733399798368804865, 796, '51bb7', 0001, '驾驶车辆驶离高速公路可以从这个位置直接驶入匝道。', NULL, '对', '错', '空', '空', '错', '根据图片可知，箭头所指方向是白色实线，<b>实线禁止跨越</b>，因此，只能继续前行寻找下一个出口，<b>不能从这里直接驶入匝道</b>。', 'https://timg.mnks.cn/20230927a21349a5.jpg', '直接驶入', 0003, 0.14);
INSERT INTO `question` VALUES (1733399798444302337, 797, '141c7', 0001, '通过铁路道口时，不得超车。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》 第四十三条 同车道行驶的机动车，有下列情形之一的，<b>不得超车</b>： <br>（一）前车正在左转弯、掉头、超车的； <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）<b>行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。</b>', '', '不得|铁路道口', 0001, 0.03);
INSERT INTO `question` VALUES (1733399798486245378, 798, '07787', 0002, '大雾天行车，多鸣喇叭是为了什么？ ', 'A、催促前车让行 B、准备超越前车 C、催促前车提速，避免发生追尾 D、引起对方注意，避免发生危险', '催促前车让行 ', '准备超越前车 ', '催促前车提速，避免发生追尾 ', '引起对方注意，避免发生危险', 'D', '雾天能见度低，驾驶人的视线模糊、视距变短、视野变窄，在道路上行车，<b>多鸣喇叭引起行人和车辆注意</b>，避免发生交通事故。', '', '雾天|多鸣喇叭', 0002, 0.04);
INSERT INTO `question` VALUES (1733399798528188418, 799, 'cfca2', 0002, '雨天对安全行车的主要影响是什么？ ', 'A、电器设备易受潮短路 B、路面湿滑，视线受阻 C、发动机易熄火 D、行驶阻力增大', '电器设备易受潮短路 ', '路面湿滑，视线受阻 ', '发动机易熄火 ', '行驶阻力增大', 'B', '雨天行驶，<b>视线受阻、路面湿滑</b>，车轮的附着力随车速的增加急剧变小，车轮很容易发生侧滑，当车辆高速行驶时也易发生“水滑”现象，因此一定要合理控制车速。', '', '雨天|主要影响', 0002, 0.06);
INSERT INTO `question` VALUES (1733399798578520066, 800, '2eaa5', 0002, '下雨后路面湿滑，车辆行驶中紧急制动时，容易导致什么？ ', 'A、引起发动机熄火 B、不被其他车辆驾驶人发现 C、因视线模糊而撞车 D、发生侧滑、引发交通事故', '引起发动机熄火 ', '不被其他车辆驾驶人发现 ', '因视线模糊而撞车 ', '发生侧滑', 'D', '下雨后路面湿滑，车辆行驶中一定要合理控制车速，充分利用发动机制动减速，不要急转向或紧急制动，以免<b>发生侧滑，引发交通事故</b>。', '', '路面湿滑|紧急制动', 0002, 0.04);
INSERT INTO `question` VALUES (1733399798624657410, 801, 'fc42c', 0002, '雾天对安全行车的主要影响是什么？ ', 'A、发动机易熄火 B、易发生侧滑 C、行驶阻力增大 D、能见度低，视线不清', '发动机易熄火 ', '易发生侧滑 ', '行驶阻力增大 ', '能见度低，视线不清', 'D', '雾天<b>能见度低</b>，驾驶人的<b>视线模糊、视距变短、视野变窄</b>；浓雾天时方向难辨，行进中很难看清前方障碍（如慢行车、行人、故障车、凹坑等），容易发生交通事故。', '', '雾天|主要影响', 0002, 0.04);
INSERT INTO `question` VALUES (1733399798670794754, 802, '8a1fa', 0002, '夜间道路环境对安全行车的主要影响是什么？ ', 'A、能见度低、不利于观察道路交通情况 B、路面复杂多变 C、驾驶人体力下降 D、驾驶人易产生冲动、幻觉', '能见度低', '不利于观察道路交通情况 B', '路面复杂多变 C', '驾驶人体力下降 D', 'A', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄，不利于观察道路交通情况；同时驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。', '', '夜间|主要影响', 0002, 0.05);
INSERT INTO `question` VALUES (1733399798721126401, 803, '2b861', 0002, '行人参与道路交通的主要特点是什么？ ', 'A、行动迟缓 B、喜欢聚集、围观 C、行走随意性大，方向多变 D、以上都是', '行动迟缓 ', '喜欢聚集', '围观 C', '行走随意性大，方向多变 D', 'C', '道路上的行人<b>各有差异</b>，但行人参与道路交通的<b>主要特点</b>是<b>行走随意性大，方向多变</b>，很容易引起险情。有些行人参与道路交通的时候还喜欢聚集、围观在一起，但这属于行人参与道路交通的特点之一，不属于主要特点。', '', '行人|主要特点', 0004, 0.27);
INSERT INTO `question` VALUES (1733399798767263746, 804, 'f3b7a', 0002, '夜间驾驶人对物体的观察明显比白天差，视距会有什么变化？ ', 'A、变长 B、变短 C、不变 D、无规律', '变长 ', '变短 ', '不变 ', '无规律', 'B', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的<b>视力变差</b>、<b>视距变短</b>、<b>视野变窄</b>。同时，驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。', '', '夜间|视距', 0003, 0.10);
INSERT INTO `question` VALUES (1733399798805012481, 805, '88f79', 0002, '驾驶机动车遇到沙尘、冰雹、雨、雾、结冰等气象条件如何行驶？ ', 'A、按平常速度行驶 B、保持匀速行驶 C、降低行驶速度 D、适当提高车速', '按平常速度行驶 ', '保持匀速行驶 ', '降低行驶速度 ', '适当提高车速', 'C', '根据《道路交通安全法》 第四十二条：机动车上道路行驶，不得超过限速标志标明的最高时速。在没有限速标志的路段，应当保持安全车速。　<br>夜间行驶或者在容易发生危险的路段行驶，以及<b>遇有沙尘、冰雹、雨、雪、雾、结冰等气象条件时</b>，应当<b>降低行驶速度</b>。', '', '气象条件', 0001, 0.03);
INSERT INTO `question` VALUES (1733399798867927041, 806, '833f6', 0002, '冰雪路行车时应注意什么？ ', 'A、制动距离延长 B、抗滑能力变大 C、路面附着力增大 D、制动性能没有变化', '制动距离延长 ', '抗滑能力变大 ', '路面附着力增大 ', '制动性能没有变化', 'A', '车辆在冰雪道路上行驶，车辆制动性能差，<b>车辆很容易侧滑跑偏</b>。因路面湿滑，汽车轮胎摩擦力减少、<b>附着力大大降低</b>，汽车驱动轮很容易打滑或空转，尤其是上坡、起步、停车时还会出现后溜车的现象；车辆在行驶中如果突然加速或减速，很容易造成侧滑及方向跑偏现象；遇情况紧急制动时，<b>制动距离会大大延长</b>。（制动距离指从踩下刹车到完全停车所需要的距离。）', '', '冰雪路', 0005, 0.27);
INSERT INTO `question` VALUES (1733399798943424514, 807, '46343', 0002, '泥泞道路对安全行车的主要影响是什么？ ', 'A、行驶阻力变小 B、车轮极易滑转和侧滑 C、能见度低，视野模糊 D、路面附着力增大', '行驶阻力变小 ', '车轮极易滑转和侧滑 ', '能见度低，视野模糊 ', '路面附着力增大', 'B', '泥泞路路面特别松软、粘稠，<b>行驶阻力大</b>，<b>附着力减小</b>，车轮<b>易滑转和侧滑</b>，容易陷车。', '', '泥泞道路', 0003, 0.13);
INSERT INTO `question` VALUES (1733399798985367554, 808, '145c8', 0002, '水淹路面影响行车安全，不易通行的原因是什么？ ', 'A、无法观察到暗坑和凸起的路面 B、路面附着力增大 C、能见度低，视野模糊 D、日光反射阻挡视线', '无法观察到暗坑和凸起的路面 ', '路面附着力增大 ', '能见度低，视野模糊 ', '日光反射阻挡视线', 'A', '被水淹没的路面，驾驶人<b>无法观察到路面状况</b>，无法判断被淹路面上是否有暗坑或凸起的障碍物影响行车安全，因此不易通行。', '', '水淹路面|不易通行', 0003, 0.09);
INSERT INTO `question` VALUES (1733399799031504897, 809, '498f6', 0002, '山区道路对安全行车的主要影响是什么？ ', 'A、道路标志少 B、交通情况单一 C、坡长弯急，视距不足 D、车流密度大', '道路标志少 ', '交通情况单一 ', '坡长弯急，视距不足 ', '车流密度大', 'C', '山区道路大多依山傍崖、陡坡路窄、急弯、隧道等危险路段多，车流海拔高的山区气候多变，<b>坡长急弯还会造成视距不足</b>，山口的横风也会引起车辆行驶位置偏移等。', '', '山区道路', 0002, 0.05);
INSERT INTO `question` VALUES (1733399799073447938, 810, 'cd1b4', 0002, '行车中突遇对方车辆强行超车，占据自己车道，正确的做法是什么？ ', 'A、加速行驶 B、尽可能减速避让，直至停车 C、保持原车速行驶 D、挡住其去路', '加速行驶 ', '尽可能减速避让，直至停车 ', '保持原车速行驶 ', '挡住其去路', 'B', '驾驶机动车遇后方强行超车后，占据自己车道时，<b>要减速或靠右停车避让</b>，千万不要开赌气车。', '', '强行超车', 0001, 0.02);
INSERT INTO `question` VALUES (1733399799119585281, 811, '82729', 0001, '夜间行车，驾驶人视距变短，影响观察，同时注意力高度集中，易产生疲劳。', NULL, '对', '错', '空', '空', '对', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄影响观察。为了保证行车安全必须注意力高度集中驾驶，易产生疲劳。因此，题干表述正确。', '', '视距变短|易产生', 0001, 0.02);
INSERT INTO `question` VALUES (1733399799165722626, 812, 'e3bf1', 0001, '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人目眩而产生错觉。', NULL, '对', '错', '空', '空', '对', '光学原理，当太阳光照在白色的冰雪上，反射的光线会使驾驶人的眼睛聚焦能力变差，从而感到目眩，产生错觉。为预防积雪反射引起炫目，驾驶人可以佩戴墨镜。', '', '冰雪道路|积雪|目眩', 0001, 0.02);
INSERT INTO `question` VALUES (1733399799216054274, 813, 'c3b8b', 0001, '在冰雪道路上行车时，车辆的稳定性降低，加速过急时车轮极易空转或溜滑。', NULL, '对', '错', '空', '空', '对', '【冰雪道路对安全行车的影响】车辆在冰雪道路上行驶，车辆制动性能差，车辆很容易侧滑跑偏。因<b>路面湿滑，轮胎摩擦力减少、附着力大大降低，加速过急时车轮很容易打滑或空转</b>，尤其是上坡、起步、停车时还会出现后溜车的现象；遇情况紧急制动时，制动距离会大大延长。', '', '冰雪道路|空转或溜滑', 0003, 0.09);
INSERT INTO `question` VALUES (1733399799257997313, 814, '4da8a', 0001, '在泥泞路上制动时，车轮易发生侧滑或甩尾，导致交通事故。', NULL, '对', '错', '空', '空', '对', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且<b>车轮易滑转、侧滑</b>。因此行至泥泞路段时，要停车查看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速档位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量<b>避免使用行车制动器，以防整车滑移</b>。', '', '泥泞路|侧滑或甩尾', 0001, 0.01);
INSERT INTO `question` VALUES (1733399799304134657, 815, '67cd9', 0001, '这辆小型载客汽车进入高速公路行车道的行为是正确的。', NULL, '对', '错', '空', '空', '错', '驾驶机动车从匝道驶入高速公路，开启左转向灯，在不妨碍已在高速公路内的车辆正常行驶的情况下驶入加速车道。因此，如图所示机动车驶入行车道的<b>行为是错误的</b>：①直接压实线驶入；②影响左侧车道内其他车辆的正常行驶。题干表述错误。', 'https://timg.mnks.cn/20230927c6931ba7.jpg', '进入|正确的', 0002, 0.07);
INSERT INTO `question` VALUES (1733399799350272001, 816, '62e7a', 0001, '行车中突遇对向车辆强行超车，占据自己车道时，可不予避让，迫使对方让路。', NULL, '对', '错', '空', '空', '错', '行车中遇对向车辆强行超车，占据自己车道时，<b>要减速或靠右停车避让，千万不要开赌气车</b>。', '', '强行超车|迫使', 0002, 0.08);
INSERT INTO `question` VALUES (1733399799417380866, 817, '80520', 0002, '行车中遇到对向来车占道行驶，应怎样做？ ', 'A、紧靠道路中心行驶 B、主动给对方让行 C、用大灯警示对方 D、逼对方靠右行驶', '紧靠道路中心行驶 ', '主动给对方让行 ', '用大灯警示对方 ', '逼对方靠右行驶', 'B', '安全行车，文明驾驶，遇对向来车占道行驶时，应主动礼让，减速靠右或停车让行。紧靠道路中心行驶、用大灯警示、逼迫对方都是不安全不文明的做法。', '', '对向|占道行驶', 0002, 0.05);
INSERT INTO `question` VALUES (1733399799463518210, 818, '1b468', 0002, '行车中发现前方道路拥堵时，应怎样做？ ', 'A、寻找机会超越前车 B、从车辆空间穿插通过 C、减速停车，依次排队等候 D、鸣喇叭催促', '寻找机会超越前车 ', '从车辆空间穿插通过 ', '减速停车，依次排队等候 ', '鸣喇叭催促', 'C', '行车中遇前方车辆行进缓慢或道路、路口因故堵塞时，<b>应减速或停车，依次缓慢行驶或耐心排队等待</b>；连续鸣喇叭催促、抢行、加塞或穿插绕行、选择空当逐车超越等做法都会加剧道路拥堵或路口堵塞，甚至会发生剐碰事故。', '', '道路拥堵', 0001, 0.01);
INSERT INTO `question` VALUES (1733399799509655554, 819, 'd58be', 0002, '会车中遇到对方来车行进有困难需借道时，应怎样做？ ', 'A、不侵占对方道路，正常行驶 B、示意对方停车让行 C、靠右侧加速行驶 D、尽量礼让对方先行', '不侵占对方道路，正常行驶 ', '示意对方停车让行 ', '靠右侧加速行驶 ', '尽量礼让对方先行', 'D', '【礼让三先】会车中遇到对方来车行进有困难需借道时，尽量礼让<b>让对方先行</b>。', '', '对方来车|借道', 0003, 0.10);
INSERT INTO `question` VALUES (1733399799559987201, 820, 'a1889', 0002, '行车中遇到后方车辆要求超车时，应怎样做？ ', 'A、及时减速、观察后靠右行驶让行 B、保持原有车速行驶 C、靠右侧加速行驶 D、不让行', '及时减速', '观察后靠右行驶让行 B', '保持原有车速行驶 C', '靠右侧加速行驶 D', 'A', '行车中遇后方车辆发出超车信号时，若具备让车条件，及时开启右转向灯，<b>减速靠右让行</b>，必要时辅以手势示意让超，不得故意不让或让路不让速。', '', '后方车辆|超车', 0001, 0.03);
INSERT INTO `question` VALUES (1733399799618707457, 821, '79033', 0002, '驾驶人在超车时，前方车辆不减速、不让道，应怎样做？ ', 'A、连续鸣喇叭加速超越 B、加速继续超越 C、停止继续超车 D、紧跟其后，伺机再超', '连续鸣喇叭加速超越 ', '加速继续超越 ', '停止继续超车 ', '紧跟其后，伺机再超', 'C', '超车之前一定要观察好超车条件，能够保证万无一失再开始超越。<b>遇前方车辆不减速、不让道时，应主动减速，保持安全距离，停止继续超车</b>。因为紧随其后、连续鸣喇叭和加速超越，都无法预防突然出现的危险。', '', '超车|不减速|不让道', 0002, 0.08);
INSERT INTO `question` VALUES (1733399799660650498, 822, 'ae097', 0002, '驾驶人在行车中经过积水路面时，应怎样做？ ', 'A、减速慢行 B、保持正常车速通过 C、空挡滑行通过 D、加速通过', '减速慢行 ', '保持正常车速通过 ', '空挡滑行通过 ', '加速通过', 'A', '驾驶机动车<b>行经有积水</b>、泥泞、碎石或者易产生扬尘<b>的道路</b>，应当<b>减速慢行或者避让</b>，不得加速通过，以免溅起的泥水、沙石、尘土飞溅到路边行人身上。', '', '积水路面', 0001, 0.03);
INSERT INTO `question` VALUES (1733399799799062530, 823, 'a777e', 0002, '发现前方道路堵塞，正确的做法是什么？ ', 'A、继续穿插绕行 B、选择空挡逐车超越 C、鸣喇叭示意前方车辆快速行驶 D、按顺序停车等候', '继续穿插绕行 ', '选择空挡逐车超越 ', '鸣喇叭示意前方车辆快速行驶 ', '按顺序停车等候', 'D', '驾驶车辆遇前方车辆行进缓慢或道路、路口因故堵塞时，应<b>减速或停车，依次缓慢行驶或耐心排队等待</b>；连续鸣喇叭催促、抢行、加塞或穿插绕行、选择空当逐车超越等做法都会加剧道路拥堵或路口堵塞，甚至会发生剐碰事故。', '', '道路堵塞', 0001, 0.03);
INSERT INTO `question` VALUES (1733399799841005570, 824, 'e353b', 0002, '车辆在拥挤路段低速行驶时，遇其他车辆强行插队，应怎样做？ ', 'A、鸣喇叭警告，不得进入 B、加速行驶，紧跟前车，不让其进入 C、主动礼让，确保行车安全 D、挤靠“加塞”车辆，逼其离开', '鸣喇叭警告，不得进入 ', '加速行驶，紧跟前车，不让其进入 ', '主动礼让，确保行车安全 ', '挤靠“加塞”车辆，逼其离开', 'C', '车辆在拥挤路段低速行驶时，遇其他车辆<b>强行插队</b>，应<b>主动礼让</b>，确保行车安全，不得加速行驶不让其进入或挤靠“加塞”车辆。', '', '拥挤路段|强行插队', 0001, 0.03);
INSERT INTO `question` VALUES (1733399799882948609, 825, 'a778a', 0002, '当驾驶车辆行经两侧有行人且有积水的路面时，应怎样做？ ', 'A、加速通过 B、正常行驶 C、减速慢行 D、连续鸣喇叭', '加速通过 ', '正常行驶 ', '减速慢行 ', '连续鸣喇叭', 'C', '驾驶机动车行经<b>两侧有行人且有积水</b>的道路，<b>应当</b>减速慢行或者避让，<b>不得</b>加速通过，以免溅起的泥水等飞溅到路边行人身上。', '', '行人|积水', 0001, 0.01);
INSERT INTO `question` VALUES (1733399799920697346, 826, '20dee', 0001, '一个合格的驾驶人，不仅表现在技术的娴熟上，更重要的是应该具有良好的驾驶行为习惯和道德修养。', NULL, '对', '错', '空', '空', '对', '驾驶人娴熟的驾驶技术、良好的行为习惯和道德修养，是保障道路交通安全和谐的基础，也是安全驾驶的最高境界。题干表述正确。', '', '驾驶行为习惯|道德修养', 0001, 0.01);
INSERT INTO `question` VALUES (1733399799958446081, 827, '39117', 0002, '冰雪道路对安全行车的主要影响是什么？ ', 'A、电器设备易受潮短路 B、能见度降低，视野模糊 C、行驶阻力增大 D、制动性能差，方向易跑偏', '电器设备易受潮短路 ', '能见度降低，视野模糊 ', '行驶阻力增大 ', '制动性能差，方向易跑偏', 'D', '车辆在冰雪道路上行驶，<b>车辆制动性能差，车辆很容易侧滑跑偏</b>。因路面湿滑，汽车轮胎摩擦力减少、附着力大大降低，汽车驱动轮很容易打滑或空转，尤其是上坡、起步、停车时还会出现后溜车的现象；车辆在行驶中如果突然加速或减速，很容易造成侧滑及方向跑偏现象；遇情况紧急制动时，制动距离会大大延长。', '', '冰雪道路', 0004, 0.21);
INSERT INTO `question` VALUES (1733399800004583425, 828, '52ebf', 0001, '如图所示，在这种情况下，驾驶机动车要停车让行。', NULL, '对', '错', '空', '空', '对', '根据相关规定：机动车行经人行横道时，应当减速行驶；<b>遇行人正在通过人行横道，应当停车让行</b>。机动车行经没有交通信号的道路时，遇行人横过道路，应当避让。', 'https://timg.mnks.cn/20230926f577853b.jpg', '停车让行', 0001, 0.02);
INSERT INTO `question` VALUES (1733399800054915073, 829, '7d72c', 0001, '这辆小型载客汽车驶离高速公路行车道的方法是正确的。', NULL, '对', '错', '空', '空', '对', '驾驶机动车驶离高速公路，<b>打开右转向灯，适当减速</b>，平顺地进入减速车道。如图所示机动车做法正确。', 'https://timg.mnks.cn/20230927bfcc4b7d.jpg', '驶离', 0003, 0.13);
INSERT INTO `question` VALUES (1733399800088469506, 830, 'c6acf', 0002, '如图所示，直行车辆遇到前方路口堵塞，以下说法正确的是什么？ ', 'A、等前方道路疏通后，且信号灯为绿灯时方可继续行驶 B、等有其他机动车进入路口时跟随行驶 C、只要信号灯为绿灯，就可通过 D、可以直接驶入路口内等待通行', '等前方道路疏通后，且信号灯为绿灯时方可继续行驶 ', '等有其他机动车进入路口时跟随行驶 ', '只要信号灯为绿灯，就可通过 ', '可以直接驶入路口内等待通行', 'A', '机动车遇有前方交叉路口交通阻塞时，<b>即便是绿灯亮，也要停在路口外等候</b>，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。等前方道路疏通后，且绿灯亮时方可继续行驶。', 'https://timg.mnks.cn/20230926e9fd48a6.jpg', '路口堵塞', 0002, 0.08);
INSERT INTO `question` VALUES (1733399800138801154, 831, '9f301', 0001, '通过急转弯路段时，在车辆较少的情况下可以超车。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》 第四十三条 同车道行驶的机动车，……有下列情形之一的，不得超车： <br>（一）前车正在左转弯、掉头、超车的； <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 <br> <b>通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。</b>', '', '急转弯|可以超车', 0002, 0.05);
INSERT INTO `question` VALUES (1733399800193327105, 832, '25caf', 0001, '驾驶车辆在道路上行驶时，应当按照规定的速度安全行驶。', NULL, '对', '错', '空', '空', '对', '机动车在道路上行驶，不得超过限速标志、标线标明的速度，<b>应当按照规定的速度</b>安全行驶。', '', '按照规定', 0001, 0.02);
INSERT INTO `question` VALUES (1733399800273018882, 833, '17a45', 0002, '行车中需要借道绕过前方障碍物，但对向来车已接近障碍物时，应怎样做？ ', 'A、降低速度或停车，让对向来车优先通行 B、加速提前抢过 C、鸣喇叭示意对向车辆让道 D、迅速占用车道，迫使对向来车停车让道', '降低速度或停车，让对向来车优先通行 ', '加速提前抢过 ', '鸣喇叭示意对向车辆让道 ', '迅速占用车道，迫使对向来车停车让道', 'A', '驾驶机动车在有障碍的路段遇对面来车时，有障碍一方让无障碍的一方先行；但<b>有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行</b>。因此，当需要借道绕过障碍物，但对向来车已接近障碍物时，应<b>降低速度或停车，让对向来车优先通行</b>。', '', '接近障碍物', 0001, 0.03);
INSERT INTO `question` VALUES (1733399800314961921, 834, '3d310', 0002, '驾驶车辆在交叉路口前变更车道时，应怎样驶入要变更的车道？ ', 'A、在路口前实线区内根据需要 B、进入路口实线区内 C、在虚线区按导向箭头指示 D、在路口停止线前', '在路口前实线区内根据需要 ', '进入路口实线区内 ', '在虚线区按导向箭头指示 ', '在路口停止线前', 'C', '驾驶机动车在交叉路口前需要变更车道时，应<b>按导向箭头的指示在虚线区</b>变更车道，进入实线区后不能变更车道。', '', '路口前|怎样驶入', 0003, 0.10);
INSERT INTO `question` VALUES (1733399800365293569, 835, 'bd5a0', 0002, '车辆驶近人行横道时，应怎样做？ ', 'A、加速通过 B、立即停车 C、鸣喇叭示意行人让道 D、先减速注意观察行人、非机动车动态，确认安全后再通过', '加速通过 ', '立即停车 ', '鸣喇叭示意行人让道 ', '先减速注意观察行人', 'D', '驾驶机动车接近人行横道线时，提前减速观察，注意观察人行横道左右两侧是否有行人、非机动车通行，随时准备停车礼让行人。', '', '人行横道', 0001, 0.03);
INSERT INTO `question` VALUES (1733399800453373953, 836, '4bdab', 0002, '车辆临时靠边停车后准备起步时，应先怎样做？ ', 'A、加油起步 B、鸣喇叭 C、观察周围交通情况 D、提高发动机转速', '加油起步 ', '鸣喇叭 ', '观察周围交通情况 ', '提高发动机转速', 'C', '车辆起步时，<b>观察</b>内、外后视镜，侧头<b>观察</b>左后方交通情况，开启左转向灯，向左缓慢转向，在不影响其他车辆通行的前提下，逐渐驶入正常行驶道路。', '', '准备起步|先怎样做', 0001, 0.02);
INSERT INTO `question` VALUES (1733399800503705601, 837, 'f730d', 0002, '行驶车道绿灯亮时，但车辆前方人行横道仍有行人行走，应怎样做？ ', 'A、直接起步通过 B、起步后从行人后方绕过 C、起步后从行人前方绕过 D、等行人通过后再起步', '直接起步通过 ', '起步后从行人后方绕过 ', '起步后从行人前方绕过 ', '等行人通过后再起步', 'D', '路口绿灯亮时，只代表可以通行，但并不意味着路口处安全无风险，应<b>注意避让其他车辆及行人，随时做好停车让行的准备</b>，即使有优先通行权，也不能忽视正在通过人行横道的行人、对面来车抢先左转弯或左右车道车辆抢行带来的危险。所以，绿灯亮遇有行人仍在行走时，应<b>等行人通过后再起步</b>。', '', '绿灯亮|仍有行人行走', 0001, 0.02);
INSERT INTO `question` VALUES (1733399800554037250, 838, '23f56', 0002, '在一般道路倒车时，若发现有过往车辆通过，应怎样做？ ', 'A、继续倒车 B、鸣喇叭示意 C、主动停车避让 D、加速倒车', '继续倒车 ', '鸣喇叭示意 ', '主动停车避让 ', '加速倒车', 'C', '倒车时，若发现有过往车辆通过，应<b>主动停车避让</b>。', '', '倒车|车辆通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399800612757506, 839, '7f074', 0002, '会车前选择的交会位置不理想时，应怎样做？ ', 'A、加速选择理想位置 B、减速、低速会车或停车让行 C、向左占道，让对方减速让行 D、打开前照灯，示意对方停车让行', '加速选择理想位置 ', '减速', '低速会车或停车让行 C', '向左占道，让对方减速让行 D', 'B', '会车前选择的交会地方不理想时，应<b>提前降低车速缓慢会车</b>，必要时可停车或倒车让行，以确保道路畅通。', '', '会车|不理想', 0002, 0.04);
INSERT INTO `question` VALUES (1733399800654700545, 840, '4159f', 0002, '进入左侧道路超车，无法保证与正常行驶前车的横向安全间距时，应怎样做？ ', 'A、加速超越 B、并行一段距离后再超越 C、放弃超车 D、谨慎超越', '加速超越 ', '并行一段距离后再超越 ', '放弃超车 ', '谨慎超越', 'C', '驾驶机动车从左侧超车时，<b>无法保证与正常行驶前车的横向安全间距</b>，或发现与对面来车有会车可能时，要<b>主动放弃超车</b>。', '', '超车|无法|横向安全间距', 0003, 0.08);
INSERT INTO `question` VALUES (1733399800688254978, 841, 'dce58', 0002, '驾驶的车辆正在被其他车辆超越时，应怎样做？ ', 'A、靠道路中心行驶 B、加速让路 C、继续加速行驶 D、减速，靠右侧行驶', '靠道路中心行驶 ', '加速让路 ', '继续加速行驶 ', '减速，靠右侧行驶', 'D', '驾驶的机动车正在被其他车辆超越时，应主动<b>减速靠右让行</b>，必要时辅以手势示意让超，不得故意不让或让路不让速。', '', '被其他车辆超越', 0001, 0.01);
INSERT INTO `question` VALUES (1733399800721809409, 842, '1798b', 0002, '驾驶机动车在这种道路上如何通行？ ', 'A、在道路两边通行 B、在道路中间通行 C、实行分道通行 D、可随意通行', '在道路两边通行 ', '在道路中间通行 ', '实行分道通行 ', '可随意通行', 'B', '《道路交通安全法》第三十六条：根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。<b>没有划分机动车道、非机动车道和人行道的</b>，<b>机动车在道路中间</b>通行，非机动车和行人在道路两侧通行。', 'https://timg.mnks.cn/20230926e93a2898.jpg', '如何通行', 0003, 0.08);
INSERT INTO `question` VALUES (1733399800759558145, 843, 'bf822', 0002, '遇后车发出超车信号后，只要具备让超条件应怎样做？ ', 'A、迅速减速或紧急制动 B、让出适当空间加速行驶 C、主动减速并靠右侧行驶 D、靠道路右侧加速行驶', '迅速减速或紧急制动 ', '让出适当空间加速行驶 ', '主动减速并靠右侧行驶 ', '靠道路右侧加速行驶', 'C', '安全行车，文明礼让。发现<b>后车发出超车信号时</b>，如果具备让超条件，应<b>及时减速靠右让行</b>。', '', '具备让超条件', 0002, 0.07);
INSERT INTO `question` VALUES (1733399800851832833, 844, 'e1f87', 0002, '道路上划设这种标线的车道内允许下列哪类车辆通行？ ', 'A、出租车 B、公务用车 C、公交车 D、私家车', '出租车 ', '公务用车 ', '公交车 ', '私家车', 'C', '如图所示，路面由黄色虚线和“<b>公交专用</b>”文字组成的车道，表示除公交车外，其他车辆及行人不得进入该车道。', 'https://timg.mnks.cn/20230926b9b244e7.jpg', '允许', 0001, 0.03);
INSERT INTO `question` VALUES (1733399800910553089, 845, '86d7c', 0001, '在道路上超车时，应尽量加大横向距离，必要时可越实线超车。', NULL, '对', '错', '空', '空', '错', '【虚线可越，实线禁止】<b>路中心线为实线时，禁止越线超车</b>。在道路上超车时，首先应确认该地段是否可以超车，<b>不得越实线超车</b>。<br>【拓展知识】<br>在允许超车路段超车时，要保持与被超越车辆的安全距离，观察左侧交通情况，选择合理时机，开启左转向灯，确认安全后从被超越车辆的左侧超越。超越后，在不影响被超越车辆正常行驶的情况下，逐渐驶回原车道。', '', '越实线', 0003, 0.09);
INSERT INTO `question` VALUES (1733399801057353730, 846, 'c15e8', 0002, '驾驶车辆行至道路急转弯处，应怎样做？ ', 'A、借对向车道行驶 B、急剧制动低速通过 C、靠弯道外侧行驶 D、充分减速并靠右侧行驶', '借对向车道行驶 ', '急剧制动低速通过 ', '靠弯道外侧行驶 ', '充分减速并靠右侧行驶', 'D', '驾驶车辆行至道路急转弯处，应<b>充分减速，尽量靠右侧行驶</b>，避免与对向来车相撞，同时应注意道路状况。不要占用对向车道、紧急制动或靠弯道外侧行驶，以免发生危险。', '', '急转弯', 0002, 0.05);
INSERT INTO `question` VALUES (1733399801107685378, 847, '6b182', 0002, '在堵车的交叉路口绿灯亮时，车辆应怎样做？ ', 'A、可直接驶入交叉路口 B、不能驶入交叉路口 C、在保证安全的情况下驶入交叉路口 D、可借对向车道通过路口', '可直接驶入交叉路口 ', '不能驶入交叉路口 ', '在保证安全的情况下驶入交叉路口 ', '可借对向车道通过路口', 'B', '驾驶机动车遇到路口交通阻塞时，<b>即便是绿灯亮，也要停在路口外等候</b>，<b>不得进入路口或停在路口内等候</b>，以免加剧阻塞或被夹在路口内进退两难。', '', '堵车|绿灯亮', 0005, 0.47);
INSERT INTO `question` VALUES (1733399801149628417, 848, '79aa5', 0002, '驾驶车辆通过无人看守的铁路道口时，应怎样做？ ', 'A、加速通过 B、减速通过 C、匀速通过 D、一停、二看、三通过', '加速通过 ', '减速通过 ', '匀速通过 ', '一停', 'D', '通过无信号控制或无人看守的铁路道口时，要在道口外停车观察，做到<b>一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）</b>。', '', '无人看守|铁路道口', 0001, 0.01);
INSERT INTO `question` VALUES (1733399801191571458, 849, 'bccb0', 0002, '山区道路车辆进入弯道前，在对面没有来车的情况下，应怎样做？ ', 'A、应“减速、鸣喇叭、靠右行” B、可靠弯道外侧行驶 C、可短时间借用对方的车道 D、可加速沿弯道切线方向通过', '应“减速', '鸣喇叭', '靠右行” B', '可靠弯道外侧行驶 C', 'A', '山区弯道较多，要注意观察，提前发现急弯路警告标志，<b>做到“减速、鸣号、靠右行”</b>。通过连续急弯路时，要注意靠右侧减速慢行，不要占对向车道', '', '山区|弯道|没有来车', 0001, 0.02);
INSERT INTO `question` VALUES (1733399801237708802, 850, '35789', 0002, '行车中遇儿童时，应怎样做？ ', 'A、长鸣喇叭催促 B、减速慢行，必要时停车避让 C、迅速从一侧通过 D、加速绕行', '长鸣喇叭催促 ', '减速慢行，必要时停车避让 ', '迅速从一侧通过 ', '加速绕行', 'B', '行车中，要<b>注意礼让儿童</b>，发现儿童在路边玩耍、嬉戏打闹时，应当<b>减速慢行</b>，随时注意观察儿童动态，发<b>现异常要及时停车避让</b>。', '', '儿童', 0001, 0.01);
INSERT INTO `question` VALUES (1733399801283846146, 851, 'cda6d', 0002, '行车中遇抢救伤员的救护车从本车道逆向驶来时，应怎样做？ ', 'A、靠边减速或停车让行 B、占用其他车道行驶 C、加速变更车道避让 D、在原车道内继续行驶', '靠边减速或停车让行 ', '占用其他车道行驶 ', '加速变更车道避让 ', '在原车道内继续行驶', 'A', '行车中遇到抢救伤员的救护车逆向驶来时，，应当<b>靠边减速或停车让行</b>，确保救护车通行无阻。', '', '救护车|逆向驶来', 0001, 0.02);
INSERT INTO `question` VALUES (1733399801342566401, 852, 'f1345', 0001, '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', NULL, '对', '错', '空', '空', '错', '跟车行驶时，<b>必须与前车保持安全的距离</b>，即保证前车制动时，本车随之制动而不与前车相撞的停车距离，<b>控制好与前车的安全距离</b>，是<b>避免发生追尾、剐碰等事故的前提</b>。', '', '不是主要|即可', 0004, 0.19);
INSERT INTO `question` VALUES (1733399801388703745, 853, '008da', 0001, '谨慎驾驶的三个原则是集中注意力、仔细观察和提前预防。', NULL, '对', '错', '空', '空', '对', '机动车驾驶人应牢记谨慎驾驶的三条黄金原则：1、集中注意力；2、仔细观察；3、提前预防。', '', '谨慎驾驶', 0001, 0.01);
INSERT INTO `question` VALUES (1733399801468395522, 854, '08a95', 0001, '遇到路口情况复杂时，应做到“宁停三分，不抢一秒”。', NULL, '对', '错', '空', '空', '对', '“宁停三分，不抢一秒”这是一句交通标语。意思是说，路口情况复杂，宁可把车停下来等三分钟，也不抢一秒钟。”宁停三分保平安，不抢一秒惹祸端”。', '', '应做到', 0001, 0.01);
INSERT INTO `question` VALUES (1733399801640361986, 855, '87e91', 0002, '机动车在道路上变更车道需要注意什么？ ', 'A、尽快加速进入左侧车道 B、不能影响其他车辆正常行驶 C、进入左侧车道时适当减速 D、开启转向灯迅速向左转向', '尽快加速进入左侧车道 ', '不能影响其他车辆正常行驶 ', '进入左侧车道时适当减速 ', '开启转向灯迅速向左转向', 'B', '道路交通法规定，变更车道的机动车<b>不得影响</b>相关车道内行驶的机动车的正常行驶。<br>安全变道前开启转向灯，观察道路两侧有无车辆超越，<b>在不妨碍</b>其他车辆正常行驶的情况下<b>逐渐变更</b>到所需车道，关闭转向灯。<br>随意变道、不观察交通情况或者强行变道、加速变道，会干扰其他车辆正常行驶而发生事故。', '', '变更车道', 0003, 0.12);
INSERT INTO `question` VALUES (1733399801908797442, 856, 'd8f06', 0002, '行车中超越右侧停放的车辆时，为预防其突然起步或开启车门，应怎样做？ ', 'A、预留出横向安全距离，减速行驶 B、保持正常速度行驶 C、长鸣喇叭 D、加速通过', '预留出横向安全距离，减速行驶 ', '保持正常速度行驶 ', '长鸣喇叭 ', '加速通过', 'A', '行车中超越右侧停放的车辆时，为预防其突然起步或开启开车，<b>最有效的方式是预留出横向安全距离，减速行驶</b>。因为长鸣喇叭、加速通过和保持正常速度行驶，都无法预防突然出现的危险。', '', '突然起步|开启车门', 0001, 0.02);
INSERT INTO `question` VALUES (1733399801963323393, 857, 'cd313', 0001, '行车中要文明驾驶，礼让行车，做到不开英雄车、冒险车、赌气车和带病车。', NULL, '对', '错', '空', '空', '对', '文明驾驶要做到“礼让三先”：先慢、先让、先停。驾驶人要加强个人修养，注重行车中的礼让。自己首先要做到不开“冒险车”、“英雄车”、“带病车”；对他人的不良驾驶行为做到宽容、大度、礼让，正确处理好有理与无理的关系，不开“斗气车”，保持冷静的心态，宁可“有理让无理，不可无理对无理”。’', '', '做到不开', 0001, 0.01);
INSERT INTO `question` VALUES (1733399802001072130, 858, '07f3e', 0002, '驶近没有人行横道的交叉路口时，发现有人横穿道路，应怎样做？ ', 'A、减速或停车让行 B、鸣喇叭示意其让道 C、抢在行人之前通过 D、立即变道绕过行人', '减速或停车让行 ', '鸣喇叭示意其让道 ', '抢在行人之前通过 ', '立即变道绕过行人', 'A', '【行人优先】机动车进出道路或者在没有交通信号、人行横道的路段行驶，应当<b>减速或停车避让</b>横过道路的行人。不可采取加速绕行的方法通过，也不要鸣喇叭示意其让路。', '', '有人横穿道路', 0001, 0.02);
INSERT INTO `question` VALUES (1733399802038820865, 859, '97665', 0002, '行车中遇有非机动车准备绕过停放的车辆时，应怎样做？ ', 'A、鸣喇叭示意其让道 B、让其先行 C、加速绕过 D、紧随其后鸣喇叭', '鸣喇叭示意其让道 ', '让其先行 ', '加速绕过 ', '紧随其后鸣喇叭', 'B', '行车中<b>遇有非机动车准备绕过停放的车辆</b>或抢道行驶时，驾驶人应<b>主动减速让行</b>。连续鸣喇叭示意其让道或加速绕过，容易使骑车人惊慌失措而摔倒，引起剐碰事故。', '', '绕过停放的车辆', 0003, 0.09);
INSERT INTO `question` VALUES (1733399802093346817, 860, '59790', 0001, '驾驶人在观察后方无来车的情况下，未开转向灯就变更车道也是合理的。', NULL, '对', '错', '空', '空', '错', '<b>不开转向灯就变道属于违法行为</b>。变更车道前，通过内、外后视镜观察后方道路交通情况，<b>确认安全后提前3秒钟开启转向灯</b>。', '', '未开转向灯|变更车道', 0002, 0.07);
INSERT INTO `question` VALUES (1733399802168844289, 861, '6183e', 0002, '行车中，遇非机动车抢行时，应怎样做？ ', 'A、鸣喇叭警告 B、加速通过 C、减速让行 D、临近时突然加速', '鸣喇叭警告 ', '加速通过 ', '减速让行 ', '临近时突然加速', 'C', '行车中遇有非机动车抢行时，驾驶人<b>应主动减速让行</b>。连续鸣喇叭示意其让道或加速绕过，容易使骑车人惊慌失措而摔倒，引起剐碰事故。', '', '非机动车抢行', 0001, 0.02);
INSERT INTO `question` VALUES (1733399802219175938, 862, '8922f', 0001, '女驾驶人穿高跟鞋驾驶车辆，不利于安全行车。', NULL, '对', '错', '空', '空', '对', '高跟鞋会影响到踩油门踏板和制动踏板的动作。在紧急刹车时，高跟鞋很容易出现歪扭，甚至卡住制动踏板导致刹车失灵，而且也容易扭伤脚，所以不利于安全行车。题干表述是正确的。', '', '穿高跟鞋|不利', 0001, 0.03);
INSERT INTO `question` VALUES (1733399802256924674, 863, 'e725f', 0001, '驾驶车辆时，长时间左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆，是一种驾驶陋习。', NULL, '对', '错', '空', '空', '对', '长时间左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆，是一种妨碍安全驾驶的行为，如果出现紧急情况，驾驶员很难做出良好及时的应对，属于驾驶陋习。', '', '一种', 0002, 0.06);
INSERT INTO `question` VALUES (1733399802311450626, 864, 'c1a09', 0001, '驾驶人一边驾车，一边吸烟对安全行车无影响。', NULL, '对', '错', '空', '空', '错', '行车中驾驶人一边驾驶一边做或想其他使注意力分散的事情，是分心驾驶的行为，违背了安全驾驶的“集中注意力”这个黄金法则，<b>对安全行车非常不利</b>。分心驾驶的危害在于：1、<b>驾驶人注意力分散</b>、视线离开路面，无法提前观察或自然忽略道路交通中的信息；2、会引起驾驶姿势或操作动作的变化，<b>不利于安全平稳操纵车辆</b>；3、<b>无法专注于观察和判断</b>道路上的交通情况，遇紧急情况反应不及。', '', '驾车|吸烟|无影响', 0002, 0.05);
INSERT INTO `question` VALUES (1733399802365976578, 865, '9bf91', 0001, '在狭窄的路段会车时，应做到礼让三先：先慢、先让、先停。', NULL, '对', '错', '空', '空', '对', '在狭窄的路段会车时，应做到礼让三先，即先慢、先让、先停，以保证行车安全。', '', '做到', 0001, 0.01);
INSERT INTO `question` VALUES (1733399802416308225, 866, '853d4', 0002, '如图所示，机动车在这种道路上行驶，在道路中间通行的原因是什么？ ', 'A、在道路中间通行视线好 B、防止车辆冲出路外 C、给两侧的非机动车和行人留有充足的通行空间 D、在道路中间通行速度快', '在道路中间通行视线好 ', '防止车辆冲出路外 ', '给两侧的非机动车和行人留有充足的通行空间 ', '在道路中间通行速度快', 'C', '没有划分车道的，在遵循右侧通行前提下，混合通行。行人在最右侧通行，非机动车在靠右侧，机动车在中间偏右的位置通行。因为对向来车也是如此，所以对整条路而言，就是机动车在中间，非机动车在两边。给两侧的非机动车和行人留有充足的通行空间，相对来讲更加安全。', 'https://timg.mnks.cn/20230926ad01d8f5.jpg', '中间通行', 0001, 0.02);
INSERT INTO `question` VALUES (1733399802454056962, 867, '53a9c', 0002, '驾驶机动车遇雾、雨、雪等能见度在50米以内时，最高速度不能超过多少？ ', 'A、70公里/小时 B、50公里/小时 C、40公里/小时 D、30公里/小时', '70公里/小时 ', '50公里/小时 ', '40公里/小时 ', '30公里/小时', 'D', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）<b>遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时</b>； <br>（四）在冰雪、泥泞的道路上行驶时；', '', '能见度在50米以内', 0002, 0.07);
INSERT INTO `question` VALUES (1733399802496000002, 868, 'a4471', 0001, '通过漫水路时要谨慎慢行，不得空挡滑行。', NULL, '对', '错', '空', '空', '对', '机动车行经漫水桥路段时，应当停车察明水情，确认安全后，低速通过。<b>空挡滑行</b>时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，<b>很容易引发严重的交通事故</b>。', '', '谨慎|不得', 0002, 0.04);
INSERT INTO `question` VALUES (1733399802537943042, 869, 'c3309', 0001, '超车时应从前车的左侧超越，是因为左侧超车便于观察，有利于安全。', NULL, '对', '错', '空', '空', '对', '交通法规定车辆必须从左侧超车，右侧超车是被交通法规明令禁止的行为。主要原因为：1、<b>由于驾驶位置在左边，左边视野较为宽阔，从左侧超车的安全系数更高。</b>2、驾驶员右侧的盲区较大，视野受限严重，如果从右侧超车，极易发生交通事故。', '', '左侧超越|有利于', 0002, 0.04);
INSERT INTO `question` VALUES (1733399802579886081, 870, '3c180', 0002, '这个导向箭头是何含义？ ', 'A、指示前方可左转或掉头 B、指示前方可直行或左转 C、指示前方可直行或掉头 D、指示前方直行向左变道', '指示前方可左转或掉头 ', '指示前方可直行或左转 ', '指示前方可直行或掉头 ', '指示前方直行向左变道', 'C', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头<b>指向前和指向反向</b>，表示<b>直行或掉头</b>。', 'https://timg.mnks.cn/20230927aa63dc67.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399802630217730, 871, '3043a', 0001, '通过窄路、窄桥时，不得超车。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第四十三条，同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>：（四）行经交叉路口、铁路道口、人行横道、急弯路、宽度不足4米的<b>窄路或者窄桥</b>、陡坡、隧道或者市区交通流量大的路段等没有超车条件的。', '', '不得超车', 0001, 0.02);
INSERT INTO `question` VALUES (1733399802672160769, 872, 'ab06d', 0002, '如图所示，在这种情况下通过前方路口，应该怎么行驶？ ', 'A、加速通过 B、减速或停车避让行人 C、赶在行人前通过 D、靠左侧行驶', '加速通过 ', '减速或停车避让行人 ', '赶在行人前通过 ', '靠左侧行驶', 'B', '驾驶机动车在道路通行中要坚持行人优先的原则，当遇行人横过道路时，<b>应当减速或停车避让</b>，待行人通过后再通行，不得加速抢行。', 'https://timg.mnks.cn/20230926bf4fd79d.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399802747658241, 873, 'b153d', 0002, '驾驶机动车在进出非机动车道时，最高速度不能超过多少？ ', 'A、30公里/小时 B、40公里/小时 C、50公里/小时 D、60公里/小时', '30公里/小时 ', '40公里/小时 ', '50公里/小时 ', '60公里/小时', 'A', '机动车行驶中遇有下列情形之一的，<b>最高行驶速度不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）<b>进出非机动车道</b>，通过铁路道口、急弯路、窄路、窄桥时；　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在冰雪、泥泞的道路上行驶时；', '', '进出非机动车道', 0002, 0.06);
INSERT INTO `question` VALUES (1733399802802184194, 874, '39ef9', 0002, '这个导向箭头是何含义？ ', 'A、指示前方可直行或向左变道 B、指示前方可直行或左转 C、指示前方可直行或掉头 D、指示前方可左转或掉头', '指示前方可直行或向左变道 ', '指示前方可直行或左转 ', '指示前方可直行或掉头 ', '指示前方可左转或掉头', 'D', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头<b>指向反向和指向左</b>，表示<b>左转或掉头</b>。', 'https://timg.mnks.cn/20230927b15470f1.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399802995122178, 875, 'e50c6', 0002, '交叉路口不得倒车的原因是什么？ ', 'A、交通情况复杂，容易造成交通堵塞甚至引发事故 B、交通监控设备多 C、交通警察多 D、车道数量少', '交通情况复杂，容易造成交通堵塞甚至引发事故 ', '交通监控设备多 ', '交通警察多 ', '车道数量少', 'A', '交叉路口车流最多，情况很复杂，如果倒车极易造车拥堵或易发事故。', '', '不得倒车', 0001, 0.02);
INSERT INTO `question` VALUES (1733399803037065217, 876, '781b5', 0002, '行至漫水路时，应当怎样做？ ', 'A、高速通过，减少涉水时间 B、空挡滑行 C、低速通过涉水路段 D、高挡位低速通过', '高速通过，减少涉水时间 ', '空挡滑行 ', '低速通过涉水路段 ', '高挡位低速通过', 'C', '驾驶机动车通过漫水路时，驾驶人应<b>挂低速挡</b>保持车辆足够动力，低速缓慢驶入水中，稳住方向<b>、匀速一气通过</b>，中途不要换挡、停车和急转向。不得加速、快速通过，以防发动机进水。', '', '漫水路', 0002, 0.06);
INSERT INTO `question` VALUES (1733399803074813953, 877, '6d753', 0001, '车辆转弯时应沿道路右侧行驶，不要侵占对方的车道，做到“左转转大弯，右转转小弯”。', NULL, '对', '错', '空', '空', '对', '由于<b>我国规定机动车要靠右行驶，无论在十字路口，弯路都是各行其路，所以左转弧度大，右转弧度就小。</b>右转转小弯左转转大弯，就能最大程度避免与对方来车发生碰撞。', '', '不要侵占|做到', 0002, 0.04);
INSERT INTO `question` VALUES (1733399803188060162, 878, 'd5473', 0001, '在后方无来车的情况下，在隧道中倒车应靠边行驶。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十条 机动车倒车时，应当察明车后情况，确认安全后倒车。<b>不得在</b>铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者<b>隧道中倒车</b>。', '', '隧道|倒车', 0002, 0.07);
INSERT INTO `question` VALUES (1733399803242586113, 879, '43282', 0002, '驾驶机动车通过铁路道口时，最高速度不能超过多少？ ', 'A、15公里/小时 B、20公里/小时 C、30公里/小时 D、40公里/小时', '15公里/小时 ', '20公里/小时 ', '30公里/小时 ', '40公里/小时', 'C', '机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，<b>通过铁路道口</b>、急弯路、窄路、窄桥时；　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在冰雪、泥泞的道路上行驶时；', '', '铁路道口', 0003, 0.11);
INSERT INTO `question` VALUES (1733399803284529153, 880, '2afa2', 0001, '如图所示，驾驶机动车遇到这种情况能够加速通过，是因为人行横道没有行人通过。', NULL, '对', '错', '空', '空', '错', '【礼让斑马线】机动车行经人行横道时，<b>应当减速行驶</b>；遇行人正在通过人行横道，应当停车让行。', 'https://timg.mnks.cn/20230926c2a9799c.jpg', '加速通过', 0002, 0.04);
INSERT INTO `question` VALUES (1733399803318083585, 881, '7c495', 0002, '超车需从前车左侧超越，以下说法正确是什么？ ', 'A、左侧为慢速车道 B、我国实行左侧通行原则 C、右侧为快速车道 D、便于观察，有利于安全', '左侧为慢速车道 ', '我国实行左侧通行原则 ', '右侧为快速车道 ', '便于观察，有利于安全', 'D', '我国实行右侧通行原则，左侧为快速车道，右侧为慢速车道。选项描述中只有【便于观察，有利于安全】的说法是正确的。', '', '左侧', 0003, 0.09);
INSERT INTO `question` VALUES (1733399803360026625, 882, '43faf', 0002, '驾驶车辆驶入铁路道口前减速降挡，进入道口后应怎样做？ ', 'A、不能变换挡位 B、可以变换挡位 C、可换为高挡 D、停车观察', '不能变换挡位 ', '可以变换挡位 ', '可换为高挡 ', '停车观察', 'A', '驾驶车辆驶入铁路道口，在道口外提前减速、减挡，<b>不得在路口内变换</b>档位，以防操作不当，导致车辆熄火，如果车辆停在铁路上，是非常危险的。', '', '进入道口后', 0005, 0.32);
INSERT INTO `question` VALUES (1733399803477467137, 883, 'd9d60', 0001, '驾驶车辆进入交叉路口前，应降低行驶速度，注意观察，确认安全。', NULL, '对', '错', '空', '空', '对', '驾驶机动车进入交叉路口前，应减速慢行，注意观察左、右方交通情况，确认安全，切勿抢行通过。', '', '交叉路口', 0001, 0.01);
INSERT INTO `question` VALUES (1733399803557158914, 884, '2b4d3', 0002, '驾驶机动车向右变更车道前应仔细观察右侧车道车流情况的原因是什么？ ', 'A、判断有无变更车道的条件 B、准备抢行 C、迅速变更车道 D、准备迅速停车', '判断有无变更车道的条件 ', '准备抢行 ', '迅速变更车道 ', '准备迅速停车', 'A', '向右变更车道前，<b>仔细观察</b>右侧车道车流的原因是为了<b>判断有无安全变更车道的条件</b>，如果把握不好变更车道的时机，很容易引起相邻车道的车辆因无法控制速度而发生事故。因此，变道前要分析道路交通流的状态，正确地选择行驶车道和变更时机，安全变道行驶。', '', '向右|观察右侧', 0002, 0.05);
INSERT INTO `question` VALUES (1733399803620073474, 885, 'dc36f', 0002, '牵引发生事故的机动车时，最高车速不得超过多少？ ', 'A、20公里/小时 B、30公里/小时 C、40公里/小时 D、50公里/小时', '20公里/小时 ', '30公里/小时 ', '40公里/小时 ', '50公里/小时', 'B', '《道路交通安全法实施条例》第四十六条，机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：<br>1、进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；<br>2、掉头、转弯、下陡坡时；<br>3、遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；<br>4、在冰雪、泥泞的道路上行驶时；<br>5、<b>牵引发生故障的机动车时</b>。', '', '不得超过', 0003, 0.12);
INSERT INTO `question` VALUES (1733399803662016513, 886, '9e042', 0002, '驾驶机动车在高速公路遇到能见度低于100米的气象条件时，最高车速是多少？ ', 'A、不得超过40公里/小时 B、不得超过60公里/小时 C、不得超过80公里/小时 D、不得超过90公里/小时', '不得超过40公里/小时 ', '不得超过60公里/小时 ', '不得超过80公里/小时 ', '不得超过90公里/小时', 'A', '《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：(一)能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；<b>(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；</b>(三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', '', '低于100米', 0005, 0.30);
INSERT INTO `question` VALUES (1733399803703959554, 887, '3f35b', 0002, '驾驶机动车在高速公路遇到能见度低于200米的气象条件时，最高车速是多少？ ', 'A、不得超过100公里/小时 B、不得超过90公里/小时 C、不得超过80公里/小时 D、不得超过60公里/小时', '不得超过100公里/小时 ', '不得超过90公里/小时 ', '不得超过80公里/小时 ', '不得超过60公里/小时', 'D', '《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：(一)<b>能见度小于200米时</b>，开启雾灯、近光灯、示廓灯和前后位灯，<b>车速不得超过每小时60公里</b>，<b>与同车道前车保持100米以上的距离</b>；(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；(三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', '', '低于200米', 0004, 0.23);
INSERT INTO `question` VALUES (1733399803745902594, 888, 'a2737', 0002, '驾驶机动车通过急弯路时，最高速度不能超过多少？ ', 'A、20公里/小时 B、30公里/小时 C、40公里/小时 D、50公里/小时', '20公里/小时 ', '30公里/小时 ', '40公里/小时 ', '50公里/小时', 'B', '机动车行驶中遇有下列情形之一的，<b>最高行驶速度不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在冰雪、泥泞的道路上行驶时；', '', '急弯路', 0003, 0.09);
INSERT INTO `question` VALUES (1733399803800428546, 889, '22679', 0002, '驾驶人连续驾驶不得超过多长时间？ ', 'A、4小时 B、6小时 C、8小时 D、10小时', '4小时 ', '6小时 ', '8小时 ', '10小时', 'A', '《道路交通安全法实施条例》第六十二条：连续驾驶机动车超过4小时应停车休息，停车休息时间不少于20分钟。', '', '不得超过', 0003, 0.08);
INSERT INTO `question` VALUES (1733399803854954497, 890, 'c205e', 0002, '驾驶人连续驾驶4小时以上，停车休息的时间不得少于多少？ ', 'A、5分钟 B、10分钟 C、15分钟 D、20分钟', '5分钟 ', '10分钟 ', '15分钟 ', '20分钟', 'D', '《道路交通安全法实施条例》第六十二条：连续驾驶机动车超过4小时应停车休息，停车休息时间不少于20分钟。', '', '不得少于', 0002, 0.06);
INSERT INTO `question` VALUES (1733399803913674754, 891, '3dd8f', 0001, '车辆上坡行驶，要提前观察路况、坡道长度，及时减挡使车辆保持充足的动力。', NULL, '对', '错', '空', '空', '对', '驾驶机动车在山区上坡时，提前观察路况、坡道长度和坡度，在车速下降前减挡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火。', '', '上坡|及时减挡', 0001, 0.03);
INSERT INTO `question` VALUES (1733399803968200705, 892, '8ff26', 0002, '进入这个路口应该怎样做？ ', 'A、交替变换远近光灯提醒路口内车辆让行 B、从路口内车辆前迅速插入 C、让已在路口内的车辆先行 D、鸣喇叭直接进入路口', '交替变换远近光灯提醒路口内车辆让行 ', '从路口内车辆前迅速插入 ', '让已在路口内的车辆先行 ', '鸣喇叭直接进入路口', 'C', '驾驶机动车准备进入环形路口的，注意观察左侧已在环岛内行驶车辆的动态，<b>让已在路口内的机动车先行</b>。【环岛让行原则】<b>进环岛让出环岛</b>：准备进入环形路口的让已在路口内的机动车先行。', 'https://timg.mnks.cn/20230927cccbb2a4.jpg', '怎样做', 0003, 0.13);
INSERT INTO `question` VALUES (1733399804018532354, 893, '92638', 0001, '如图所示，在前方路口可以掉头。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第四十九条 <b>机动车在</b>有禁止掉头或者禁止左转弯标志、标线的地点以及在<b>铁路道口</b>、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，<b>不得掉头</b>。', 'https://timg.mnks.cn/20230926ccb9fc78.jpg', '可以掉头', 0001, 0.03);
INSERT INTO `question` VALUES (1733399804169527297, 894, '3d06f', 0002, '在什么情况下不得行车？ ', 'A、车窗没关闭 B、车门没关闭 C、音响没关闭 D、顶窗没关闭', '车窗没关闭 ', '车门没关闭 ', '音响没关闭 ', '顶窗没关闭', 'B', '《道路交通安全法实施条例》第六十二条：驾驶机动车<b>不得在车门、车厢没有关好时</b>行车。', '', '不得', 0001, 0.02);
INSERT INTO `question` VALUES (1733399804228247554, 895, '4749b', 0001, '车辆下坡行驶，要适当控制车速，充分利用发动机进行制动。', NULL, '对', '错', '空', '空', '对', '驾驶机动车<b>下坡行驶</b>，要挂中低速档位，<b>充分利用发动机制动来控制车速</b>，严禁空挡滑行。', '', '下坡|发动机', 0002, 0.07);
INSERT INTO `question` VALUES (1733399804278579202, 896, '2225d', 0002, '在路口右转弯遇同车道前车等候放行信号时如何行驶？ ', 'A、从前车左侧转弯 B、从右侧占道转弯 C、鸣喇叭让前车让路 D、依次停车等候', '从前车左侧转弯 ', '从右侧占道转弯 ', '鸣喇叭让前车让路 ', '依次停车等候', 'D', '《道路交通安全法实施条例》第五十一条：机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行，向右转弯遇有同车道前车<b>正在等候放行信号</b>时，依次<b>停车等候</b>。', '', '右转弯', 0001, 0.03);
INSERT INTO `question` VALUES (1733399804358270977, 897, 'b5bd3', 0001, '车辆通过铁路道口时，应用低速挡安全通过，中途不得换挡，以避免发动机熄火。', NULL, '对', '错', '空', '空', '对', '车辆通过铁路道口时，要使用低速挡一气通过，通行途中不得变换档位。因为换挡容易熄火，如果发生熄火，并且有火车开过来，很容易因为紧张无法正常打火而产生危险。', '', '铁路道口|低速挡|不得换挡', 0002, 0.05);
INSERT INTO `question` VALUES (1733399804450545665, 898, 'f7b1b', 0002, '在路口看到这种信号灯亮时，应该怎样做才正确？ ', 'A、在不妨碍被放行车辆、行人的情况下，可以通行 B、在不妨碍被放行车辆、行人的情况下，可以直行 C、在不妨碍被放行车辆、行人的情况下，可以左转 D、在不妨碍被放行车辆、行人的情况下，可以右转', '在不妨碍被放行车辆', '行人的情况下，可以通行 B', '在不妨碍被放行车辆', '行人的情况下，可以直行 C', 'D', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。<b>红灯亮</b>时，禁止车辆通行，车辆不得越过停止线，<b>右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下可以通行</b>。', 'https://timg.mnks.cn/20230927b0cb64aa.jpg', '正确', 0003, 0.08);
INSERT INTO `question` VALUES (1733399804513460225, 899, '3b61d', 0001, '如图所示，在这种情况下遇右侧车辆变更车道，应减速保持间距，注意避让。', NULL, '对', '错', '空', '空', '对', '如图所示，右侧车辆开启左转向灯欲向左变更至本车道，遇到这种情况时应<b>减速行驶，注意避让，预留出横向安全距离</b>。题干表述正确。', 'https://timg.mnks.cn/20230926d030ef40.jpg', '右侧|减速|间距', 0001, 0.03);
INSERT INTO `question` VALUES (1733399804551208962, 900, '9e192', 0002, '驾驶机动车通过窄路、窄桥时，最高速度不能超过多少？ ', 'A、60公里/小时 B、50公里/小时 C、40公里/小时 D、30公里/小时', '60公里/小时 ', '50公里/小时 ', '40公里/小时 ', '30公里/小时', 'D', '机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，通过铁路道口、急弯路、<b>窄路、窄桥时；</b>　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在冰雪、泥泞的道路上行驶时；', '', '窄路、窄桥', 0002, 0.05);
INSERT INTO `question` VALUES (1733399804584763393, 901, 'fc569', 0001, '车辆下长坡时要减挡行驶，以充分利用发动机的制动作用。', NULL, '对', '错', '空', '空', '对', '驾驶机动车下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降。<b>控制车速最好的方法</b><b>是使用低速挡行驶，充分利用发动机制动减速。</b>', '', '下长坡|减挡行驶', 0002, 0.08);
INSERT INTO `question` VALUES (1733399804630900737, 902, 'e3998', 0002, '在路口看到这种信号灯亮时，怎样做才正确？ ', 'A、在确保通行安全的情况下加速通过路口 B、停在路口停车线以外等待下一个绿灯信号 C、不妨碍被放行车辆、行人通行也不能右转弯 D、不妨碍被放行车辆、行人通行可以直行通过', '在确保通行安全的情况下加速通过路口 ', '停在路口停车线以外等待下一个绿灯信号 ', '不妨碍被放行车辆', '行人通行也不能右转弯 D', 'B', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 <br><b>黄灯亮</b>时，是一种过渡信号灯，起到警示作用，提示信号即将变换，已越过停止线的车辆可以继续通行；<b>没有越过停止线的车辆需停车等待，不得进入路口，更不得加速抢行通过交叉路口</b>。<br>如图所示，机动车没有越过停止线，因此应停车等待。<br>【黄闪】持续闪烁的黄灯是闪光警告信号灯，提示车辆、行人通行时注意瞭望，确认安全后通过。<br>【拓展知识】<br>\n<p><img src=\"https://sucimg.itc.cn/sblog/jbddb7c36bc0697f5fac8ec0a74265881\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>以停止线为界，停止线到路口的区域称为停止线以内，停止线与车道间的区域称为<b>停止线以外</b>。', 'https://timg.mnks.cn/20230926f613243e.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399804672843777, 903, 'a27ac', 0002, '在这个路口右转弯如何通行？ ', 'A、先让对面车左转弯 B、直接向右转弯 C、抢在对面车前右转弯 D、鸣喇叭催促', '先让对面车左转弯 ', '直接向右转弯 ', '抢在对面车前右转弯 ', '鸣喇叭催促', 'A', '【直行&gt;左转&gt;右转】三个先行原则：转弯的机动车让直行的车辆先行，右方道路来车先行，<b>右转弯车让左转弯车先行</b>。如图所示，对面车辆左转弯，因此在这个路口右转弯时应<b>先让对面车左转弯。</b>', 'https://timg.mnks.cn/20230927e5e0ad5a.jpg', '右转弯', 0001, 0.03);
INSERT INTO `question` VALUES (1733399804744146946, 904, '3857d', 0002, '驾驶机动车下陡坡时不得有哪些危险行为？ ', 'A、提前减挡 B、空挡滑行 C、低挡行驶 D、制动减速', '提前减挡 ', '空挡滑行 ', '低挡行驶 ', '制动减速', 'B', '驾驶机动车<b>下陡坡行驶</b>，要挂中低速档位，充分利用发动机制动来控制车速，<b>严禁空挡滑行</b>。<br>【拓展知识-空挡滑行不省油而且危险大】<br>空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很容易引发严重的交通事故。', '', '不得有', 0003, 0.11);
INSERT INTO `question` VALUES (1733399804882558977, 905, 'b2025', 0002, '如图所示，路口遇到黄灯不断闪烁的情况时，说明是什么情况？ ', 'A、路口禁止一切车辆通行 B、路口发生道路交通事故 C、路口交通管制需要清空 D、路口交通信号暂时解除', '路口禁止一切车辆通行 ', '路口发生道路交通事故 ', '路口交通管制需要清空 ', '路口交通信号暂时解除', 'D', '闪光警告信号灯为持续闪烁的黄灯，提示路口交通信号暂时解除，车辆、行人通行时注意瞭望，确认安全后通过。<br>【拓展知识】<br>黄灯的法律含义是警示，只闪黄灯表示此路口交通信号暂时解除，过往车辆经过此路口的时候需要自己观察周围情况决定停不停车，这种信号灯往往设置在车流不大的路口或是夜间等车流平峰时段。', 'https://timg.mnks.cn/20230927d24ffd69.jpg', '黄灯', 0004, 0.24);
INSERT INTO `question` VALUES (1733399804928696322, 906, 'b6e67', 0001, '车辆在通过山区道路弯道时，要做到“减速、鸣喇叭、靠右行”。', NULL, '对', '错', '空', '空', '对', '驾驶机动车通过弯道时，要注意观察，提前发现急弯路警告标志，并做到“<b>减速、鸣号、靠右行</b>”，不要占对向车道。', '', '要做到', 0001, 0.02);
INSERT INTO `question` VALUES (1733399804962250753, 907, '8f2ee', 0002, '如图所示，以下哪种情况可以超车。 ', 'A、C图 B、D图 C、B图 D、A图', 'C图 ', 'D图 ', 'B图 ', 'A图', 'D', '<b>请注意：图片的ABCD并不是对应选项的ABCD。</b>如图所示路况，安全超车需要满足两个条件：1、确认安全，即对向车道<b>无来车</b>；2、虚实线，实线一侧禁止车辆越线或压线行驶，<b>虚线一侧准许</b>车辆暂时越线。因此，只有<b>A图</b>所示的情况下可以超车。', 'https://timg.mnks.cn/20230926ed220f30.jpg', '超车', 0004, 0.19);
INSERT INTO `question` VALUES (1733399805004193794, 908, 'cc7a6', 0002, '如图所示，在有车道信号灯的路段，哪辆机动车行驶的车道是正确的？ ', 'A、红色小型客车（E）行驶车道是正确的 B、灰色小型客车（A）行驶车道是正确的 C、黄色小型客车（B）行驶车道是正确的 D、蓝色小型客车（C）行驶车道是正确的', '红色小型客车（E）行驶车道是正确的 ', '灰色小型客车（A）行驶车道是正确的 ', '黄色小型客车（B）行驶车道是正确的 ', '蓝色小型客车（C）行驶车道是正确的', 'D', '车辆行驶在设置了车道信号灯的道路上，<b>必须选择绿色箭头灯亮的车道行驶</b>。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。 <br><b>请注意：选项的ABCD并不是对应车道的ABCD。</b>如图所示，蓝色小型客车（C）行驶的车道是绿色箭头灯亮，表示允许通行。', 'https://timg.mnks.cn/20230926c92ece92.jpg', '正确', 0002, 0.08);
INSERT INTO `question` VALUES (1733399805037748226, 909, '10d89', 0002, '在路口直行时，遇这种情形如何通行？ ', 'A、让左方道路车辆先行 B、让右方道路车辆先行 C、直接加速直行通过 D、开启危险报警闪光灯通行', '让左方道路车辆先行 ', '让右方道路车辆先行 ', '直接加速直行通过 ', '开启危险报警闪光灯通行', 'B', '根据《道路交通安全法》第五十二条 机动车通过没有交通信号灯控制也没有交通警察指挥的交叉路口，除应当遵守第五十一条第(二)项、第(三)项的规定外，还应当遵守下列规定： <br><b>(二)没有交通标志、标线控制的，在进入路口前停车瞭望，让右方道路的来车先行；</b><br>如图所示交叉路口<b>直行</b>时，我方车辆应<b>让右侧路口车辆先行</b>。', 'https://timg.mnks.cn/20230927de97614b.jpg', '直行', 0005, 0.40);
INSERT INTO `question` VALUES (1733399805083885570, 910, '5910e', 0001, '在这段道路上一定要减少鸣喇叭的频率。', NULL, '对', '错', '空', '空', '错', '如图所示，右侧设有<b>禁止车辆停放</b>标志和<b>禁止鸣喇叭</b>标志，表示在这段道路上<b>禁止车辆鸣喇叭</b>和禁止一切车辆停 、放，无论驾驶人是否离开车辆。', 'https://timg.mnks.cn/20230927d2440523.jpg', '减少', 0003, 0.14);
INSERT INTO `question` VALUES (1733399805138411522, 911, 'f43a3', 0002, '遇到图中这种有信号灯的路段，选择哪条车道行驶才正确？ ', 'A、选择左侧车道行驶 B、选择中间车道行驶 C、选择右侧车道行驶 D、选择任一车道行驶', '选择左侧车道行驶 ', '选择中间车道行驶 ', '选择右侧车道行驶 ', '选择任一车道行驶', 'B', '<b>车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶</b>。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。 <br>如图所示，中间车道绿色箭头灯亮，表示允许通行。', 'https://timg.mnks.cn/20230927eee6a2c7.jpg', '信号灯', 0001, 0.03);
INSERT INTO `question` VALUES (1733399805180354562, 912, 'f1903', 0001, '如图所示，红圈中标记车辆使用灯光的方法是正确的。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第四十八条规定：在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯。 <br>因此，红圈中标记车辆<b>使用远光灯的做法是错误的</b>。', 'https://timg.mnks.cn/20230926de3fc24c.jpg', '使用灯光', 0002, 0.04);
INSERT INTO `question` VALUES (1733399805222297601, 913, 'c5223', 0002, '遇到图中这种情况时，应该如何驾驶？ ', 'A、直行通过路口 B、向左转弯通过路口 C、向右转弯通过路口 D、在路口掉头行驶', '直行通过路口 ', '向左转弯通过路口 ', '向右转弯通过路口 ', '在路口掉头行驶', 'C', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。<b>绿色箭头表示允许车辆沿箭头所指方向通行</b>；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 <br>如图所示，<b>绿色箭头灯向右</b>，表示<b>允许车辆向右转弯</b>行驶。', 'https://timg.mnks.cn/20230926c87ef290.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399805264240641, 914, '59e23', 0001, '车辆在山区道路行车下陡坡时，不得超车。', NULL, '对', '错', '空', '空', '对', '根据《道路交通安全法》第四十三条　同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>： <br>1、前车正在左转弯、掉头、超车的； <br>2、与对面来车有会车可能的； <br>3、前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>4、<b>行经</b>铁路道口、交叉路口、窄桥、弯道、<b>陡坡</b>、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '不得超车', 0001, 0.03);
INSERT INTO `question` VALUES (1733399805314572289, 915, '6e0d5', 0001, '驾驶机动车超车时，可以鸣喇叭替代开启转向灯。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第四十七条 <b>机动车超车时</b>，应当<b>提前开启左转向灯</b>、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下，应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越，在与被超车辆拉开必要的安全距离后，开启右转向灯，驶回原车道。', '', '超车|替代', 0002, 0.08);
INSERT INTO `question` VALUES (1733399805360709633, 916, 'e4190', 0002, '如图所示，驶近这种铁路道口，怎样做才正确？ ', 'A、如果没有看到列车驶来，可以快速横过道口 B、通过铁路道口要做到“一停、二看、三通过” C、只要路口的红色信号灯不亮，就可以加速通过 D、没有看到铁路管理人员指挥，说明可以迅速通过', '如果没有看到列车驶来，可以快速横过道口 ', '通过铁路道口要做到“一停', '二看', '三通过” C', 'B', '驾驶机动车通过无信号控制或无人看守的铁路道口时，要在道口外停车观察，做到<b>一停</b>（在停止线以外停车）、<b>二看</b>（观察左右是否有驶来的列车）、<b>三通过</b>（确认安全后，低速通过）', 'https://timg.mnks.cn/20230926b9f2e5a4.jpg', '铁路道口', 0001, 0.01);
INSERT INTO `question` VALUES (1733399805499121665, 917, 'e02f1', 0002, '在路口遇这种情形怎样通行？ ', 'A、鸣喇叭告知让行 B、直接加速转弯 C、让左方来车先行 D、减速缓慢转弯', '鸣喇叭告知让行 ', '直接加速转弯 ', '让左方来车先行 ', '减速缓慢转弯', 'C', '如图所示，前方路口有<b>减速让行标志和标线</b>，因此应减速<b>让左方来车先行</b>。根据《道路交通安全法实施条例》第五十二条规定，机动车通过没有交通信号灯控制也没有交通警察指挥的交叉路口，应当遵守下列规定：有交通标志、标线控制的，<b>让优先通行的一方先行</b>。', 'https://timg.mnks.cn/20230927b657cf3f.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399805562036226, 918, 'e5949', 0002, '遇前方路段车道减少，车辆行驶缓慢，为保证安全有序应该怎样做？ ', 'A、穿插到前方排队车辆中通过 B、依次交替通行 C、加速从前车左右超越 D、借对向车道迅速通过', '穿插到前方排队车辆中通过 ', '依次交替通行 ', '加速从前车左右超越 ', '借对向车道迅速通过', 'B', '《道路交通安全法》第四十五条，机动车遇有前方车辆停车排队等候或者缓慢行驶时，不得借道超车或者占用对面车道，不得穿插等候的车辆。<br><b>在车道减少的路段、路口</b>，或者在没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口<b>遇到停车排队等候或者缓慢行驶时</b>，机动车应当<b>依次交替通行</b>。', '', '减少|缓慢', 0002, 0.04);
INSERT INTO `question` VALUES (1733399805612367873, 919, 'b0833', 0002, '驾驶机动车变更车道时，以下做法正确的是什么？ ', 'A、开启转向灯的同时变更车道 B、在道路同方向划有2条以上机动车道的，不得影响相关车道内行驶的机动车的正常行驶 C、在车辆较少路段，可以随意变更车道 D、遇前方道路拥堵，可以向应急车道变更', '开启转向灯的同时变更车道 ', '在道路同方向划有2条以上机动车道的，不得影响相关车道内行驶的机动车的正常行驶 ', '在车辆较少路段，可以随意变更车道 ', '遇前方道路拥堵，可以向应急车道变更', 'B', '《道路交通安全法实施条例》第四十四条，在道路同方向划有2条以上机动车道的，<b>变更车道的机动车不得影响</b>相关车道内行驶的机动车的正常行驶。<br>需要变道时，应<b>提前开启</b>转向灯；<b>不得随意</b>变更车道；非紧急情况<b>不得占用</b>应急车道行驶。', '', '变更车道', 0003, 0.09);
INSERT INTO `question` VALUES (1733399805654310913, 920, 'ccda4', 0001, '在车门、车厢没有关好时不要驾驶机动车起步。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在车门、车厢没有关好时行车。', '', '不要', 0001, 0.02);
INSERT INTO `question` VALUES (1733399805692059650, 921, 'ed1cc', 0001, '驾驶机动车应在变更车道的同时开启转向灯。', NULL, '对', '错', '空', '空', '错', '变更车道有一定的风险，一定要在确保安全的前提下谨慎变更。需要变更车道时，应当<b>提前打开转向灯</b>，提醒后方来车注意。', '', '同时开启', 0005, 0.27);
INSERT INTO `question` VALUES (1733399805750779906, 922, 'eaaba', 0001, '车辆在下坡行驶时，可充分利用空挡滑行。', NULL, '对', '错', '空', '空', '错', '驾驶机动车下坡行驶，要挂低速挡位，充分利用发动机制动来控制车速，<b>严禁空挡滑行</b>。 <br>【拓展知识-空挡滑行不省油而且危险大】 <br>空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很容易引发严重的交通事故。', '', '下坡|空挡滑行', 0002, 0.08);
INSERT INTO `question` VALUES (1733399805792722945, 923, '07a25', 0001, '如图所示，A车可以从左侧超越B车。', NULL, '对', '错', '空', '空', '错', '如图所示，路中心黄色虚实线，<b>实线一侧禁止</b>车辆越线或压线行驶。因此，A车不具备超车条件，<b>不可以从左侧超越B车</b>。', 'https://timg.mnks.cn/20230926e7e4263a.jpg', '左侧', 0002, 0.04);
INSERT INTO `question` VALUES (1733399805843054593, 924, 'cd97e', 0001, '行车中，发现行人突然横过道路时，应迅速减速避让。', NULL, '对', '错', '空', '空', '对', '道路上的行人各有差异，但行人的主要特点是行走随意性大，方向多变，很容易引起险情。由于行人缺少相应的保护，很容易受到伤害，<b>道路通行中应当坚持行人优先的原则</b>，即使行人出现不遵守交通规则的行为，驾驶人也要做到<b>减速礼让</b>，<b>优先保证行人的安全</b>。', '', '行人|减速避让', 0002, 0.06);
INSERT INTO `question` VALUES (1733399805884997634, 925, '8bd03', 0002, '如图所示，驾驶机动车行驶至桥梁涵洞时，以下做法正确的是什么？ ', 'A、加速，在对向车到达前通过 B、减速靠右通过 C、保持原速继续正常行驶 D、鸣喇叭后加速通过', '加速，在对向车到达前通过 ', '减速靠右通过 ', '保持原速继续正常行驶 ', '鸣喇叭后加速通过', 'B', '如图所示，一辆车已经行至涵洞里了，而涵洞较窄，所以我方车辆应该减速靠右让对向车辆先行。', 'https://timg.mnks.cn/20230926a5c503d9.jpg', '桥梁涵洞', 0001, 0.03);
INSERT INTO `question` VALUES (1733399805931134977, 926, '6b226', 0002, '为什么规定辅路车让主路车先行？ ', 'A、辅路车便于观察 B、主路车流量大、速度快 C、主路车流量小、速度快 D、辅路车速度快', '辅路车便于观察 ', '主路车流量大', '速度快 C', '主路车流量小', 'B', '主路是<b>路面的主线</b>，路面较宽、车道较多、车流量较大、车速较快；<br>辅路是路面的支线，路面窄，车速慢；<br>所以，辅路车在行驶时需要给主路车主动的让行，更利用交通安全。', '', '主路车先行', 0003, 0.15);
INSERT INTO `question` VALUES (1733399805989855233, 927, '8b78f', 0002, '如图所示，驾驶机动车遇到这种情况，以下做法正确的是什么？ ', 'A、减速慢行、鸣喇叭示意 B、为拓宽视野，临时占用左侧车道行驶 C、加速行驶 D、停车观察', '减速慢行', '鸣喇叭示意 B', '为拓宽视野，临时占用左侧车道行驶 C', '加速行驶 D', 'A', '机动车<b>驶近急弯</b>、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当<b>减速慢行，并鸣喇叭示意</b>。', 'https://timg.mnks.cn/20230926c8dab726.jpg', '做法正确', 0002, 0.05);
INSERT INTO `question` VALUES (1733399806035992578, 928, '2ae9b', 0002, '驾驶机动车下陡坡、转弯、掉头时，最高速度不能超过多少？ ', 'A、30公里/小时 B、40公里/小时 C、50公里/小时 D、60公里/小时', '30公里/小时 ', '40公里/小时 ', '50公里/小时 ', '60公里/小时', 'A', '机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br>（二）<b>掉头、转弯、下陡坡</b>时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在冰雪、泥泞的道路上行驶时；', '', '陡坡|转弯|掉头', 0001, 0.03);
INSERT INTO `question` VALUES (1733399806090518530, 929, '4904b', 0001, '车辆进入山区道路后，要特别注意“连续转弯”标志，并主动避让车辆及行人，适时减速和提前鸣喇叭。', NULL, '对', '错', '空', '空', '对', '山区弯道较多，要注意观察，提前发现急弯路警告标志，特别注意“<b>连续转弯</b>”标志，并做到“<b>减速、鸣号、靠右行</b>”，<b>主动避让</b>车辆及行人。', '', '山区道路|主动避让', 0001, 0.01);
INSERT INTO `question` VALUES (1733399806136655873, 930, '154d9', 0001, '驾驶机动车在隧道内行驶时，可以临时停车。', NULL, '对', '错', '空', '空', '错', '机动车在交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、<b>隧道</b>以及距离上述地点50米以内的路段，<b>不得停车</b>。', '', '隧道|临时停车', 0001, 0.03);
INSERT INTO `question` VALUES (1733399806195376130, 931, '128a1', 0002, '驾驶机动车通过没有交通信号和管理人员的铁路道口怎样通过？ ', 'A、适当减速通过 B、空挡滑行通过 C、停车确认安全后通过 D、加速尽快通过', '适当减速通过 ', '空挡滑行通过 ', '停车确认安全后通过 ', '加速尽快通过', 'C', '通过无信号控制或无人看守的铁路道口时，要在道口外停车观察，做到<b>一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）</b>。', '', '没有|铁路道口', 0002, 0.06);
INSERT INTO `question` VALUES (1733399806228930561, 932, 'b0955', 0001, '不要在驾驶室的前后窗范围内悬挂和放置妨碍驾驶人视线的物品。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品。', '', '不要', 0001, 0.02);
INSERT INTO `question` VALUES (1733399806270873601, 933, 'e68db', 0001, '车辆在山区上坡路驾驶，减挡要及时、准确、迅速，避免拖挡行驶导致发动机动力不足。', NULL, '对', '错', '空', '空', '对', '驾驶机动车在山区上坡时，提前观察路况、坡道长度和坡度，在车速下降前减挡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火。', '', '上坡路|动力不足', 0002, 0.05);
INSERT INTO `question` VALUES (1733399806317010945, 934, '22e9c', 0001, '如图所示，机动车在这样的城市道路上行驶，最高的行驶速度不得超过50公里/小时。', NULL, '对', '错', '空', '空', '错', '如图所示，驾驶机动车在<b>没有道路中心线的城市道路</b>，行驶速度<b>不得超过30公里/小时</b>。根据《道路交通安全法实施条例》中规定：第四十五条 机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： <br>（一）<b>没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；</b> 【城3公4】<br>（二）同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。【城5公7】', 'https://timg.mnks.cn/20230926a2cab35c.jpg', '城市道路|50公里/小时', 0003, 0.14);
INSERT INTO `question` VALUES (1733399806363148289, 935, '33d26', 0001, '行车中在道路情况良好的条件下可以观看车载视频。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第六十二条，驾驶机动车不得有下列行为： <br>(一)在车门、车厢没有关好时行车； <br>(二)在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品； <br><b>(三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为；</b> <br>(四)下陡坡时熄火或者空挡滑行； <br>(五)向道路上抛撒物品； <br>(六)驾驶摩托车手离车把或者在车把上悬挂物品； <br>(七)连续驾驶机动车超过4小时未停车休息或者停车休息时间少于20分钟； <br>(八)在禁止鸣喇叭的区域或者路段鸣喇叭。', '', '行车中|可以', 0002, 0.05);
INSERT INTO `question` VALUES (1733399806400897025, 936, 'd0f47', 0001, '通过山区危险路段，尤其是通过经常发生塌方、泥石流的山区地段，应谨慎驾驶，避免停车。', NULL, '对', '错', '空', '空', '对', '驾驶机动车通过经常有落石或经常发生塌方、泥石流的山区路段时，要<b>减速慢行，注意观察，尽快通过，不要停留</b>。因此，题干表述正确。', '', '山区|谨慎驾驶', 0001, 0.02);
INSERT INTO `question` VALUES (1733399806442840066, 937, 'd70e2', 0001, '驾驶机动车在没有交通信号的路口要尽快通过。', NULL, '对', '错', '空', '空', '错', '驾驶机动车通过没有交通信号灯控制的路口时，应在距路口50-100米时减速，<b>行至路口时仔细观察左右两侧道路交通情况，减速或停车瞭望，做到“一看，二慢、三通过”</b>，直行车辆优先通行。遇到有停车让行标志的路口，要停车观察主路情况，确认安全后再通过。', '', '要尽快', 0002, 0.04);
INSERT INTO `question` VALUES (1733399806497366017, 938, '5cba2', 0002, '在涉水路段跟车行驶时，应当怎样做？ ', 'A、紧跟其后 B、超越前车，抢先通过 C、适当增加车距 D、并行通过', '紧跟其后 ', '超越前车，抢先通过 ', '适当增加车距 ', '并行通过', 'C', '在涉水路段行驶时，应<b>适当的增加车距，保持安全的距离</b>，这样可以防止前车出现事故时，自己有足够的时间进行应急处理。', '', '涉水|跟车', 0003, 0.13);
INSERT INTO `question` VALUES (1733399806539309058, 939, '216ec', 0001, '驾驶小型汽车下陡坡时允许熄火滑行。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第六十二条：驾驶机动车<b>不得</b>在下陡坡时熄火或者空挡滑行。', '', '下陡坡', 0002, 0.05);
INSERT INTO `question` VALUES (1733399806614806529, 940, 'd0a64', 0002, '指路标志的作用是什么？ ', 'A、限制车辆通行 B、提示限速信息 C、提供方向信息 D、警告前方危险', '限制车辆通行 ', '提示限速信息 ', '提供方向信息 ', '警告前方危险', 'C', '指路标志的作用是<b>指引道路信息</b>，为驾驶人<b>传递道路方向</b>、地点、距离等信息。', '', '指路', 0004, 0.16);
INSERT INTO `question` VALUES (1733399806652555265, 941, '1d5b0', 0001, '通过经常发生塌方、泥石流的山区地段，避免停车。', NULL, '对', '错', '空', '空', '对', '驾驶机动车通过经常有落石或经常发生塌方、泥石流的山区路段时，要减速慢行，注意观察，尽快通过，不要停留。因此，题干表述正确。', '', '避免停车', 0001, 0.03);
INSERT INTO `question` VALUES (1733399806694498306, 942, '34623', 0002, '如图所示，夜间驾驶机动车遇对方使用远光灯，无法看清前方路况时，以下做法正确的是什么？ ', 'A、保持行驶方向和车速不变 B、自己也打开远光灯行驶 C、降低车速，谨慎会车 D、加速通过，尽快摆脱眩目光线', '保持行驶方向和车速不变 ', '自己也打开远光灯行驶 ', '降低车速，谨慎会车 ', '加速通过，尽快摆脱眩目光线', 'C', '夜间会车遇对向车辆使用远光灯，无法看清前方路况时，要<b>及时减速</b>靠右侧行驶或停车让行，以防发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 'https://timg.mnks.cn/20230926e2b10e3a.jpg', '夜间|远光灯', 0001, 0.03);
INSERT INTO `question` VALUES (1733399806744829954, 943, '06ec7', 0001, '在这种情况的铁路道口要加速通过。', NULL, '对', '错', '空', '空', '错', '驾驶机动车通过有交通信号控制的铁路道口时，在道口外提前减速、减挡，按照信号灯的指示<b>低速通行</b>，不得在路口内变换档位。遇报警器鸣响或红灯亮时，停车等候，不准抢行通过铁路道口。', 'https://timg.mnks.cn/20230927f5083664.jpg', '要加速', 0001, 0.02);
INSERT INTO `question` VALUES (1733399806790967297, 944, 'e1ee2', 0001, '当行人出现交通安全违法行为时，车辆可以不给行人让行。', NULL, '对', '错', '空', '空', '错', '由于行人缺少相应的保护，很容易受到伤害，道路通行中<b>应当坚持行人优先的原则</b>，即使行人出现不遵守交通规则的行为，驾驶人也要做到文明礼让，优先保证行人的安全。', '', '可以不', 0002, 0.04);
INSERT INTO `question` VALUES (1733399806828716033, 945, '9617a', 0001, '如图所示，驾驶机动车经过这种道路时，如果前方没有其他交通参与者，可在道路上随意通行。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第三十六条：根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。<b>没有划分机动车道、非机动车道和人行道的，机动车在道路中间通行，非机动车和行人在道路两侧通行。</b> <br>因此，驾驶机动车在如图所示道路上<b>随意通行做法错误</b>。', 'https://timg.mnks.cn/20230926d1887cf3.jpg', '随意通行', 0002, 0.06);
INSERT INTO `question` VALUES (1733399806874853378, 946, '1536e', 0001, '如图所示，在这种情况下，会车时必须减速靠右通过。', NULL, '对', '错', '空', '空', '对', '【会车的安全驾驶】在<b>没有中心隔离设施或中心线的道路上</b>会车时，应<b>减速靠右行驶</b>，与对方车辆保持横向安全间距。', 'https://timg.mnks.cn/20230926c76df540.jpg', '会车|减速靠右', 0001, 0.03);
INSERT INTO `question` VALUES (1733399806937767937, 947, '214c7', 0001, '如图所示，驾驶机动车直行遇前方道路堵塞时，车辆可以在黄色网格线区域临时停车等待，但不得在人行横道停车。', NULL, '对', '错', '空', '空', '错', '驾驶机动车遇有路口堵塞时，车辆既不可在黄色网格线区域临时停车等待，也不可在人行横道停车。即便是绿灯亮，也要在路口外停车等待，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。', 'https://timg.mnks.cn/20230926f7951cff.jpg', '可以在', 0004, 0.22);
INSERT INTO `question` VALUES (1733399806988099585, 948, '5e8f3', 0002, '驾驶机动车在冰雪道路行驶时，最高速度不能超过多少？ ', 'A、20公里/小时 B、30公里/小时 C、40公里/小时 D、50公里/小时', '20公里/小时 ', '30公里/小时 ', '40公里/小时 ', '50公里/小时', 'B', '机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在<b>冰雪</b>、泥泞的道路上行驶时；', '', '冰雪道路', 0003, 0.11);
INSERT INTO `question` VALUES (1733399807046819842, 949, '86d89', 0002, '车辆驶入双向行驶隧道前，应开启什么灯？ ', 'A、危险报警闪光灯 B、远光灯 C、雾灯 D、示廓灯或近光灯', '危险报警闪光灯 ', '远光灯 ', '雾灯 ', '示廓灯或近光灯', 'D', '驶入双向通行的隧道前，<b>应开启示廓灯或近光灯</b>，靠右行驶，注意对向来车；会车时，加大横向距离低速安全会车，会车不可开启远光灯。', '', '双向行驶隧道', 0003, 0.12);
INSERT INTO `question` VALUES (1733399807105540097, 950, '6fae0', 0001, '如图所示，驾驶机动车在路口遇到这种交通信号时，右转弯的车辆在不妨碍被放行的车辆、行人的情况下，可以通行。', NULL, '对', '错', '空', '空', '对', '如图所示，驾驶机动车在路口遇到<b>机动车信号灯（圆形灯）红灯亮</b>时，右转弯的车辆在不妨碍被放行的车辆、行人的情况下，<b>可以右转弯行驶</b>。', 'https://timg.mnks.cn/20230926efa35e58.jpg', '不妨碍', 0004, 0.19);
INSERT INTO `question` VALUES (1733399807151677442, 951, '2827b', 0001, '行至这种情况的铁路道口要停车观察。', NULL, '对', '错', '空', '空', '对', '驾驶机动车通过无信号控制或无人看守的铁路道口时，要在道口外<b>停车观察</b>，做到一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）。', 'https://timg.mnks.cn/20230927ab67d8a9.jpg', '停车观察', 0002, 0.05);
INSERT INTO `question` VALUES (1733399807193620481, 952, 'ffa17', 0001, '如图所示，驾驶机动车遇到右侧车辆强行变道，应减速慢行，让右前方车辆顺利变道。', NULL, '对', '错', '空', '空', '对', '驾驶机动车遇右侧车辆强行变道，占据自己车道时，要<b>减速或靠右停车避让</b>，千万不要开赌气车。', 'https://timg.mnks.cn/20230926b7518910.jpg', '应减速', 0001, 0.03);
INSERT INTO `question` VALUES (1733399807260729346, 953, '89301', 0001, '立交桥上一般都是单向行驶，车辆不必减速行驶。', NULL, '对', '错', '空', '空', '错', '立交桥的形式多种多样，驾驶机动车通过立交桥时必须<b>按照限速标志标线规定的速度</b>行驶。', '', '立交桥|不必减速', 0002, 0.08);
INSERT INTO `question` VALUES (1733399807315255298, 954, '96863', 0002, '在狭窄的山路会车，规定不靠山体的一方优先行驶的原因是什么？ ', 'A、靠山体的一方相对安全 B、靠山体的一方视野宽阔 C、不靠山体的一方车速较快 D、三项都正确', '靠山体的一方相对安全 ', '靠山体的一方视野宽阔 ', '不靠山体的一方车速较快 ', '三项都正确', 'A', '狭窄的山路会车，因为不靠山体一方缺少山体的保护，如果减速靠右停车让行，有掉下山崖的危险；靠山体一方有山体的保护，停车让行相对安全。', '', '山路会车|不靠山体', 0004, 0.25);
INSERT INTO `question` VALUES (1733399807382364162, 955, '79e7e', 0002, '如图所示，驾驶机动车遇到这种情况，以下做法正确的是什么？ ', 'A、长鸣喇叭催促行人快速通过 B、开启远光灯警示行人有车辆驶近 C、降低行驶速度，避让行人 D、适当加速从行人前方绕行', '长鸣喇叭催促行人快速通过 ', '开启远光灯警示行人有车辆驶近 ', '降低行驶速度，避让行人 ', '适当加速从行人前方绕行', 'C', '机动车进出道路，遇行人横穿道路时，应当<b>及时减速或停车避让</b>。', 'https://timg.mnks.cn/20230926cdeb01f9.jpg', '做法正确', 0001, 0.02);
INSERT INTO `question` VALUES (1733399807449473025, 956, '77849', 0001, '在这种情况的交叉路口转弯要让直行车先行。', NULL, '对', '错', '空', '空', '对', '交叉路口三个优先通行原则：<b>转弯的</b>机动车<b>让直行</b>的车辆先行，右方道路来车先行，右转弯车让左转弯车先行。<br>法律上之所以这么规定，主要是为了优化路口的通行效率，一般路口直行车多且快，如果让直行车去避让转弯车辆，很容易造成直行车大量停滞，使路口出现拥堵。', 'https://timg.mnks.cn/20230927d217b6bf.jpg', '让直行', 0002, 0.06);
INSERT INTO `question` VALUES (1733399807483027457, 957, 'a2eff', 0002, '夜间车辆通过照明条件良好的路段时，应使用什么灯？ ', 'A、雾灯 B、近光灯 C、远光灯 D、危险报警闪光灯', '雾灯 ', '近光灯 ', '远光灯 ', '危险报警闪光灯', 'B', '夜间驾驶，灯光有照明和信号两方面的作用，应根据行驶中的实际情况正确使用。夜间通过<b>照明条件良好</b>的道路时，使用<b>近光灯</b>。', '', '夜间|照明|良好', 0003, 0.11);
INSERT INTO `question` VALUES (1733399807537553410, 958, '9cab6', 0001, '在交叉路口遇到这种情况享有优先通行权。', NULL, '对', '错', '空', '空', '对', '<b>交叉路口让行原则：1、转弯让直行；2、右转让左转；3、让右原则。</b>即转弯的机动车让直行的车辆先行；右方道路来车先行；<b>右转弯车让左转弯车先行</b>。如图所示，<b>红车</b><b>右转</b>灯亮表示要<b>右转</b>，我方左转灯亮，右转让左转。因此我方优先通过。', 'https://timg.mnks.cn/20230927e1b37c0d.jpg', '优先', 0005, 0.31);
INSERT INTO `question` VALUES (1733399807587885058, 959, '6d664', 0001, '车辆在交叉路口绿灯亮后，遇非机动车抢道行驶时，可以不让行。', NULL, '对', '错', '空', '空', '错', '路口绿灯亮时，只代表可以通行，但并不意味着路口处安全无风险，应注意避让行人和非机动车，随时做好停车的准备，<b>即使有优先通行权，也不能忽视对面来车抢先左转弯或左右车道车辆抢行带来的危险</b>。', '', '绿灯亮|抢道|不让行', 0002, 0.04);
INSERT INTO `question` VALUES (1733399807764045826, 960, '11bdc', 0002, '夜间行车中，前方出现弯道时，灯光照射会发生怎样的变化？ ', 'A、距离不变 B、由高变低 C、离开路面 D、由路中移到路侧', '距离不变 ', '由高变低 ', '离开路面 ', '由路中移到路侧', 'D', '夜间行车，可以通过灯光位置的变化来识别和判断路面的情况。 <br>灯光照射<b>由路中移到路侧</b>：前方道路可能是一般弯道或连续弯道，当是连续弯道时灯光会随之从道路的一侧移到另一侧。', '', '弯道|灯光照射', 0004, 0.19);
INSERT INTO `question` VALUES (1733399807839543298, 961, 'bf897', 0001, '雾天行车为了提高能见度，应该开启远光灯。', NULL, '对', '错', '空', '空', '错', '雾天<b>不能使用远光灯</b>，因为远光灯的灯光是照向前上方的，射出的光线被雾气反射后，会在车前形成白茫茫的一片，不但不能起到照明作用还会严重阻挡驾驶人视线、视野，当后面车辆使用远光灯时也<b>会严重影响前车驾驶人观察后方情况</b>，因此<b>雾天行车，<b>不能使用远光灯</b></b>。', '', '雾天|远光灯', 0002, 0.08);
INSERT INTO `question` VALUES (1733399807915040769, 962, '1af1d', 0002, '驾驶机动车在泥泞道路行驶时，最高速度不能超过多少？ ', 'A、15公里/小时 B、20公里/小时 C、30公里/小时 D、40公里/小时', '15公里/小时 ', '20公里/小时 ', '30公里/小时 ', '40公里/小时', 'C', '机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在冰雪、<b>泥泞</b>的道路上行驶时；', '', '泥泞道路', 0003, 0.13);
INSERT INTO `question` VALUES (1733399807956983809, 963, '4855e', 0001, '在交叉路口遇到这种情况享有优先通行权。', NULL, '对', '错', '空', '空', '错', '【直行 &gt;左转&gt;右转】三个先行原则：转弯的机动车让直行的车辆先行，右方道路来车先行，右转弯车让左转弯车先行。<b>我方是右转，对方左转，故对方优先通过</b>。 <br>【拓展延伸】 <br>题目未说明主体是谁的情况下， 一般<b>默认为主视角车辆(我方车辆)</b>。', 'https://timg.mnks.cn/20230927b671a80a.jpg', '优先', 0004, 0.20);
INSERT INTO `question` VALUES (1733399808015704065, 964, '4a8ad', 0001, '夜间行车，驾驶人的视野受限，很难观察到灯光照射区域以外的交通情况，因此要减速行驶。', NULL, '对', '错', '空', '空', '对', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄。同时，驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。因此，夜间行车一定要仔细观察、合理控制车速、正确使用灯光。', '', '减速行驶', 0001, 0.02);
INSERT INTO `question` VALUES (1733399808070230017, 965, '53748', 0001, '驾驶机动车在没有道路中心线的道路上行驶，应该在道路的左侧通行。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》规定：在没有中心线的道路上，机动车应该靠中间行驶，行人和非机动车在道路两侧行驶。', '', '没有|中心线|左侧', 0004, 0.16);
INSERT INTO `question` VALUES (1733399808149921793, 966, '898ae', 0001, '在交叉路口、隧道内均不能倒车。', NULL, '对', '错', '空', '空', '对', '《道路交通交通法》规定：机动车在交叉路口、隧道内均不能倒车。', '', '不能', 0001, 0.03);
INSERT INTO `question` VALUES (1733399808212836353, 967, 'c6eec', 0002, '遇有这种排队等候的情形怎么做？ ', 'A、依次排队等候 B、从右侧借道超越 C、从左侧跨越实线超越 D、从两侧随意超越', '依次排队等候 ', '从右侧借道超越 ', '从左侧跨越实线超越 ', '从两侧随意超越', 'A', '驾驶车辆遇到前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，<b>依次缓慢行驶或耐心排队等待</b>。即便是绿灯亮，也要依次停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。', 'https://timg.mnks.cn/20230927c4a8996a.jpg', '排队等候', 0001, 0.01);
INSERT INTO `question` VALUES (1733399808284139522, 968, 'bc9c1', 0002, '夜间驾驶机动车在没有中心隔离设施或者没有中心线的道路上行驶，以下哪种情况下应当改用近光灯？ ', 'A、接近没有交通信号灯控制的交叉路口时 B、与对向机动车会车时 C、接近人行横道时 D、城市道路照明条件不良时', '接近没有交通信号灯控制的交叉路口时 ', '与对向机动车会车时 ', '接近人行横道时 ', '城市道路照明条件不良时', 'B', '《道路交通安全法实施条例》第四十八条 在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：(五)<b>夜间会车应当在距相对方向来车150米以外改用近光灯</b>，在窄路、窄桥与非机动车会车时应当使用近光灯。', '', '夜间|近光灯', 0005, 0.29);
INSERT INTO `question` VALUES (1733399808359636993, 969, 'aed15', 0001, '当机动车驾驶人准备超车，但无法保证与被超车辆的安全距离时，应当加速超过。', NULL, '对', '错', '空', '空', '错', '<b>超车时</b>要保持与被超越车辆的安全距离，观察好超车条件，有充分把握保证万无一失再开始超越，<b>无法保证与被超车辆的安全间距</b>说明不具备超车条件，应<b>主动放弃</b>超车。继续超车或者加速超车，容易引发剐碰事故。', '', '无法保证|加速超', 0002, 0.06);
INSERT INTO `question` VALUES (1733399808414162945, 970, 'bff1b', 0001, '夜间驾驶人对事物的观察能力明显比白天差，视距变短。', NULL, '对', '错', '空', '空', '对', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的<b>视力变差、视距变短、视野变窄</b>。同时，驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。', '', '视距变短', 0001, 0.02);
INSERT INTO `question` VALUES (1733399808447717378, 971, '0649d', 0002, '如图所示，车辆在拥挤路段排队行驶时，遇到其他车辆强行穿插行驶，以下说法正确的是什么？ ', 'A、迅速左转躲避 B、减速或停车让行 C、持续鸣喇叭警告 D、迅速提高车速不让其穿插', '迅速左转躲避 ', '减速或停车让行 ', '持续鸣喇叭警告 ', '迅速提高车速不让其穿插', 'B', '驾驶车辆遇前方车辆行进缓慢或道路、路口因故堵塞时，应<b>减速或停车</b>，<b>依次缓慢行驶或耐心排队等待</b>；连续鸣喇叭催促、抢行、加塞或穿插绕行、选择空当逐车超越等做法都会加剧道路拥堵或路口堵塞，甚至会发生剐碰事故。', 'https://timg.mnks.cn/20230926e5bebe15.jpg', '拥挤|强行穿插', 0001, 0.03);
INSERT INTO `question` VALUES (1733399808493854721, 972, '4a5b3', 0001, '如图所示，驾驶机动车遇前方车流行驶缓慢时，借用公交专用道超车是正确的。', NULL, '对', '错', '空', '空', '错', '驾驶机动车遇有前方机动车停车排队等候或者缓慢行驶的，应当依次排队等候或缓慢行驶，不得借公交专用道超车。 <br>《道路交通安全法》第三十七条　道路划设专用车道的，在专用车道内，只准许规定的车辆通行，<b>其他车辆不得进入</b>专用车道内行驶。', 'https://timg.mnks.cn/20230926b8d6d15b.jpg', '借用公交', 0001, 0.03);
INSERT INTO `question` VALUES (1733399808535797762, 973, 'dbb32', 0002, '遇到前方车辆缓慢行驶时怎样行驶？ ', 'A、从两侧随意超越 B、从右侧借道超越 C、占对向车道超越 D、依次排队行驶', '从两侧随意超越 ', '从右侧借道超越 ', '占对向车道超越 ', '依次排队行驶', 'D', '驾驶车辆<b>遇到前方车辆行进缓慢</b>或道路、路口因故堵塞时，应减速或停车，<b>依次缓慢行驶或耐心排队等待</b>。即便是绿灯亮，也要依次停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。', 'https://timg.mnks.cn/20230927b2403448.jpg', '缓慢行驶', 0001, 0.01);
INSERT INTO `question` VALUES (1733399808623878146, 974, '32f33', 0002, '如图所示，驾驶机动车行驶至车道减少的路段时，遇前方机动车排队等候或行驶缓慢时，以下做法正确的是什么？ ', 'A、右侧车让左侧车先行 B、每车道一辆依次交替驶入左侧车道 C、左侧车让右侧车先行 D、右侧车寻找空隙提前进入左侧车道', '右侧车让左侧车先行 ', '每车道一辆依次交替驶入左侧车道 ', '左侧车让右侧车先行 ', '右侧车寻找空隙提前进入左侧车道', 'B', '如图所示，前方有<b>右侧变窄警告标志</b>，表示前方右侧车行道或路面变窄。因此<b>正确的做法是</b>：每车道一辆依次交替驶入左侧车道。<br>《道路交通安全法》第四十五条，<b>在车道减少的路段、路口</b>，或者在没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口<b>遇到停车排队等候或者缓慢行驶时</b>，机动车应当<b>依次交替通行</b>。', 'https://timg.mnks.cn/20230926f6a9ce59.jpg', '车道减少', 0003, 0.13);
INSERT INTO `question` VALUES (1733399808741318657, 975, 'f283a', 0002, '机动车在没有交通标志，标线的道路上，应当怎样行驶？ ', 'A、随意行驶 B、加速行驶 C、停车观察周围情况后行驶 D、在确保安全、畅通的原则下通行', '随意行驶 ', '加速行驶 ', '停车观察周围情况后行驶 ', '在确保安全', 'D', '《道路交通安全法》第三十八条，车辆、行人应当按照交通信号通行；遇有交通警察现场指挥时，应当按照交通警察的指挥通行；<b>在没有交通信号的道路上</b>，应当<b>在确保安全、畅通的原则下</b>通行。', '', '没有|标志|标线', 0004, 0.19);
INSERT INTO `question` VALUES (1733399808791650305, 976, '4fbc8', 0001, '掉头过程中，应严格控制车速，仔细观察道路前后方情况，确认安全后方可前进或倒车。', NULL, '对', '错', '空', '空', '对', '【安全掉头】驾驶机动车掉头时，<b>严格控制车速</b>，<b>仔细观察</b>前、后方交通情况，<b>确认安全</b>后减速或停车，开启左转向灯，<b>正确选择掉头地点和时机进行掉头</b>。在没有掉头成功的情况下可以倒车调整，但不要妨碍其他车辆和行人的正常通行。', '', '确认安全', 0002, 0.06);
INSERT INTO `question` VALUES (1733399808846176257, 977, '58a51', 0001, '夜间起步前，应当先开启近光灯。', NULL, '对', '错', '空', '空', '对', '夜间起步，先开启近光灯观察路况，确认周围情况后，再开启左转向灯，起步。', '', '起步|近光灯', 0003, 0.08);
INSERT INTO `question` VALUES (1733399808904896513, 978, '231fd', 0001, '如图所示，在这种情况下，A车可以向左变更车道。', NULL, '对', '错', '空', '空', '错', '如图所示，A车不能向左变更。<b>变更车道，首先不得妨碍要驶入车道内车辆的正常行驶</b>。若此时A车向左变更车道，会与C车发生碰撞事故。<b>因此，题干表述错误</b>。', 'https://timg.mnks.cn/20230926eef9137b.jpg', '向左变更车道', 0002, 0.07);
INSERT INTO `question` VALUES (1733399808959422465, 979, '908a5', 0001, '驾驶机动车上道路行驶，不允许超过限速标志标明的最高时速。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第四十二条：机动车上道路行驶，<b>不得超过</b>限速标志标明的最高时速。在没有限速标志的路段，应当保持安全车速。', '', '不允许', 0002, 0.04);
INSERT INTO `question` VALUES (1733399809064280066, 980, '8bfea', 0002, '在路口遇到这种情形时怎样做？ ', 'A、停在网状线区域内等待 B、停在路口以外等待 C、跟随前车通过路口 D、停在路口内等待', '停在网状线区域内等待 ', '停在路口以外等待 ', '跟随前车通过路口 ', '停在路口内等待', 'B', '《道路交通安全法实施条例》第五十三条：<b>机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，</b>不得从前方车辆两侧穿插或者超越行驶，<b>不得在人行横道、网状线区域内停车等候。</b> <br>如图所示，虽然是绿灯，但<b>前方路况拥堵</b>，因此，机动车应该<b>停在路口以外等待</b>。 <br>【拓展知识】 <br>\n<p><img src=\"https://sucimg.itc.cn/sblog/jbddb7c36bc0697f5fac8ec0a74265881\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>以停止线为界，停止线到路口的区域称为停止线以内，<b>停止线与车道间的区域</b>称为<b>停止线以外</b>。', 'https://timg.mnks.cn/20230927a790acf0.jpg', '', 0005, 0.28);
INSERT INTO `question` VALUES (1733399809123000321, 981, 'd9471', 0001, '夜间会车时，若对方车辆不关闭远光灯，可变换灯光提示对向车辆，同时减速靠右侧行驶或停车。', NULL, '对', '错', '空', '空', '对', '夜间会车遇对向车辆<b>不关闭</b>远光灯，可交替使用远、近光灯提示对向车辆，当对向来车仍<b>不关闭</b>远光灯时，要<b>及时减速靠右侧行驶或停车让行</b>。 <br>因为开启远光灯会使对面车辆驾驶人因<b>强烈灯光照射造成炫目而无法看清前方道路情况</b>，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', '', '会车|不关闭|减速靠右', 0002, 0.05);
INSERT INTO `question` VALUES (1733399809215275010, 982, '69db8', 0001, '如图所示，驾驶机动车接打电话容易导致发生交通事故。', NULL, '对', '错', '空', '空', '对', '<b>边开车边接打电话，是一种妨碍安全驾驶的行为。</b>驾驶机动车时接打电话，会影响驾驶人的判断力，增加驾驶人的大脑负担、延迟反应时间，而且接打电话时容易导致目光凝聚，无法关注周围环境。更有甚者，在电话中争论会加剧驾驶人的负面情绪，导致无意识急剧踩油门等行为，危害自身及他人安全。', 'https://timg.mnks.cn/20230926e517308e.jpg', '接打电话', 0002, 0.04);
INSERT INTO `question` VALUES (1733399809273995266, 983, '8dbe2', 0001, '如图所示，驾驶机动车经过这种道路时，应降低车速在道路中间通行。', NULL, '对', '错', '空', '空', '对', '如图所示，<b>非机动车和行人在两侧通行，机动车在道路中间通行。</b>《道路交通安全法》第三十六条，根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。<b>没有划分</b>机动车道、非机动车道和人行道的，<b>机动车在道路中间通行</b>，非机动车和行人在道路两侧通行。', 'https://timg.mnks.cn/20230926c7f24d71.jpg', '道路中间', 0001, 0.03);
INSERT INTO `question` VALUES (1733399809320132609, 984, 'bf8f2', 0002, '驾驶机动车在车道减少的路口，遇到前方车辆依次停车或缓慢行驶时怎么办？ ', 'A、从前车右侧路肩进入路口 B、从有空隙一侧进入路口 C、每车道一辆依次交替驶入路口 D、向左变道穿插进入路口', '从前车右侧路肩进入路口 ', '从有空隙一侧进入路口 ', '每车道一辆依次交替驶入路口 ', '向左变道穿插进入路口', 'C', '《道路交通安全法实施条例》第五十三条：机动车在车道减少的路口、路段，遇有前方机动车停车排队等候或者缓慢行驶的，应当每车道一辆<b>依次交替</b>驶入车道减少后的路口、路段。', '', '车道减少', 0001, 0.03);
INSERT INTO `question` VALUES (1733399809362075650, 985, 'f60c3', 0001, '如图所示，驾驶机动车遇到这种情况，可以轻按喇叭提醒前方非机动车和行人后方有来车。', NULL, '对', '错', '空', '空', '对', '行车中遇前方有行人和非机动车占道行驶，当超越他们时，应当提前<b>轻按喇叭提示</b>，减速慢行，并随时准备停车。', 'https://timg.mnks.cn/20230926d50b8360.jpg', '轻按', 0003, 0.09);
INSERT INTO `question` VALUES (1733399809399824386, 986, '4ccc2', 0002, '驾驶机动车通过未设置交通信号灯的交叉路口时，下列说法错误的是什么？ ', 'A、转弯的机动车让直行的车辆、行人先行 B、没有交通标志、标线控制时，在进入路口前停车瞭望，让右方道路的来车先行 C、相对方向行驶的右转弯机动车让左转弯的车辆先行 D、相对方向行驶的左转弯机动车让右转弯的车辆先行', '转弯的机动车让直行的车辆', '行人先行 B', '没有交通标志', '标线控制时，在进入路口前停车瞭望，让右方道路的来车先行 C', 'D', '<b>请注意：</b>题干要求选出<b>说法错误</b>的选项。根据《道路交通安全法实施条例》第五十二条规定，驾驶机动车通过未设置交通信号灯的交叉路口时：<b>转弯的机动车应当让直行的车辆、行人先行</b>；没有交通标志、标线控制时，在进入路口前停车瞭望，<b>让右方道路的来车先行</b>；相对方向行驶的<b>右转弯机动车让左转弯的车辆先行。</b>【转弯让直行；右转让左转】', '', '交叉路口|说法错误', 0005, 0.43);
INSERT INTO `question` VALUES (1733399809441767426, 987, 'ae47a', 0001, '夜间通过没有路灯或路灯照明不良的道路时，应将近光灯转换为远光灯，但同向行驶的后车不得使用远光灯。', NULL, '对', '错', '空', '空', '对', '夜间通过没有路灯或路段照明不良的道路时，应将近光灯转换为远光灯，低速行驶；但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。因此，题干表述正确。', '', '不得使用', 0004, 0.20);
INSERT INTO `question` VALUES (1733399809475321857, 988, '3125b', 0001, '驾驶机动车下长坡时，利用惯性滑行可以减少燃油消耗，值得提倡。', NULL, '对', '错', '空', '空', '错', '下长坡利用惯性空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很容易引发严重的交通事故。 <br>下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降。控制车速最好的方法是使用低速挡行驶，充分利用发动机制动减速，<b>严禁利用惯性空挡滑行</b>，不但不省油，反而会有很大的安全隐患。', '', '下长坡|滑行|值得提倡', 0003, 0.10);
INSERT INTO `question` VALUES (1733399809517264898, 989, '26435', 0002, '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？ ', 'A、应停车察明水情，确认安全后，低速通过 B、应停车察明水情，确认安全后，快速通过 C、应减速观察水情，然后加速行驶通过 D、可随意通行', '应停车察明水情，确认安全后，低速通过 ', '应停车察明水情，确认安全后，快速通过 ', '应减速观察水情，然后加速行驶通过 ', '可随意通行', 'A', '如图所示，过水路面警告标志，提醒前方是过水路面或漫水桥路段，应<b>停车察明水情，确认安全后，低速通过。</b>', 'https://timg.mnks.cn/20230926de61819e.jpg', '做法正确', 0003, 0.10);
INSERT INTO `question` VALUES (1733399809555013633, 990, 'da568', 0002, '在大暴雨的天气驾车，刮水器无法正常工作时，应怎样做？ ', 'A、立即减速行驶 B、提高注意力谨慎驾驶 C、立即减速靠边停车 D、保持正常速度行驶', '立即减速行驶 ', '提高注意力谨慎驾驶 ', '立即减速靠边停车 ', '保持正常速度行驶', 'C', '暴雨天气驾车，刮水器无法刮净雨水的话，驾驶人视线受阻，很难完全看清楚路况，十分危险。为了安全起见，一定要立即减速靠边停车，等待雨量变小之后再上路行驶。 <br>【拓展延伸】 <br>发生这种情况后，司机绝不能继续向前开，“A减速行驶”、“B集中注意力谨慎驾驶”、“D以正常速度行驶”都包含了继续行驶的意思，是错误的。 <br>而很多人会认为C项“立即”是错误的，可以这样理解：<b>立即减速靠边停车≠紧急制动</b>，是说司机意识到危险情况后，立即采取了减速并靠边停车这样的应对措施。是没有问题的，注意审题。', '', '大暴雨|刮水器', 0003, 0.12);
INSERT INTO `question` VALUES (1733399809605345281, 991, '4aa6c', 0001, '在这种情况下可以加速通过人行横道。', NULL, '对', '错', '空', '空', '错', '如图所示，驾驶机动车<b>看到人行横道前有停止的车辆时</b>，<b>一定要停车</b>，不要盲目通过，更不得加速通过，前车可能是停车避让行人。不要在人行横道及其附近直行超车和变向超车，尤其要提防有些行动缓慢的人可能还滞留在人行横道上。', 'https://timg.mnks.cn/20230926b2cbadda.jpg', '加速通过', 0001, 0.03);
INSERT INTO `question` VALUES (1733399809647288322, 992, '581fd', 0002, '驾驶机动车遇到前方车辆停车排队等候或缓慢行驶时怎么办？ ', 'A、可借道超车 B、占用对面车道 C、穿插等候的车辆 D、依次行驶', '可借道超车 ', '占用对面车道 ', '穿插等候的车辆 ', '依次行驶', 'D', '驾驶车辆遇到前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，<b>依次缓慢行驶或耐心排队等待</b>。即便是绿灯亮，也要依次停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。 <br>《道路交通安全法》第四十五条：机动车遇有前方车辆停车排队等候或者缓慢行驶时，不得借道超车或者占用对面车道，不得穿插等候的车辆。', '', '停车排队', 0001, 0.03);
INSERT INTO `question` VALUES (1733399809685037058, 993, 'f3181', 0002, '驾驶机动车变更车道为什么要提前开启转向灯？ ', 'A、开阔视野，便于观察路面情况 B、提示前车让行 C、提示行人让行 D、提示其他车辆我方准备变更车道', '开阔视野，便于观察路面情况 ', '提示前车让行 ', '提示行人让行 ', '提示其他车辆我方准备变更车道', 'D', '变更车道，一定要提前开启转向灯，主要是<b>为了提示其它车辆，我方准备要变更车道</b>了，避免发生两车相碰的情况，在变道前驾驶人要观察前后方车辆的行驶情况，确认安全后开始变更车道。', '', '变更车道', 0002, 0.07);
INSERT INTO `question` VALUES (1733399809718591490, 994, '6f82e', 0001, '如图所示，通过有这个标志的路口时应该减速让行。', NULL, '对', '错', '空', '空', '错', '如图所示，在设有<b>停车让行</b>标志标线的交叉路口，车辆应当在停止线以外停车观察主路情况，确认安全后再通行。因此，题干表述错误。<br>【拓展延伸】<br>\n<p><img src=\"https://tp.mnks.cn/tp_176.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>停车让行</b><br><img src=\"https://tp.mnks.cn/tp_177.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>减速让行</p>', 'https://timg.mnks.cn/20230926a922d127.jpg', '减速让行', 0005, 0.31);
INSERT INTO `question` VALUES (1733399809802477570, 995, '5bddc', 0001, '行车中遇残疾人影响通行时，应主动减速礼让。', NULL, '对', '错', '空', '空', '对', '驾驶机动车遇到<b>行动不便</b>的行人横过道路时，要<b>主动减速</b>或停车礼让。', '', '主动减速', 0001, 0.01);
INSERT INTO `question` VALUES (1733399809844420609, 996, 'a734e', 0001, '在图中这种环境下超车时，要变换远近光灯告知前车，待前车让行后，再开启远光灯超越。', NULL, '对', '错', '空', '空', '错', '<b>开启远光灯超车的做法是错误的。</b>夜间行车应当尽量避免超车，确实需要超车时，要变换远、近光灯提示前车，待前车让行后方可超越，超车时要保持与被超车辆的安全距离，开启左转向灯，从被超车辆的左侧超越。', 'https://timg.mnks.cn/20230926f558c724.jpg', '超车|开启远光灯', 0004, 0.25);
INSERT INTO `question` VALUES (1733399809882169346, 997, '1ed7e', 0001, '在高速公路变更车道时，应提前开启转向灯，观察情况，确认安全后，驶入需要变更的车道。', NULL, '对', '错', '空', '空', '对', '高速公路行车变更车道时，应<b>提前开启转向灯</b>，然后观察道路交通情况，判断有无安全变更车道的条件，在不妨碍其他车辆正常行驶的情况下，保持安全距离逐渐驶入要变更的车道。', '', '变更车道|确认安全', 0001, 0.01);
INSERT INTO `question` VALUES (1733399809936695298, 998, 'bc6be', 0002, '如图所示，当A车后方有执行任务的救护车驶来时以下做法正确的是什么？ ', 'A、不必理会，继续行驶 B、靠右减速让路 C、向左转弯让路 D、立即停车让路', '不必理会，继续行驶 ', '靠右减速让路 ', '向左转弯让路 ', '立即停车让路', 'B', '<b>【特殊车辆都要让】</b>行车中遇执行任务的救护车驶来时，应主动<b>靠右减速让行，确保救护车畅行无阻</b>。', 'https://timg.mnks.cn/20230926b2a456f4.jpg', '救护车', 0003, 0.12);
INSERT INTO `question` VALUES (1733399809978638338, 999, '43946', 0001, '夜间行车，遇对面来车未关闭远光灯时，应减速行驶，以防两车灯光的交汇处有行人通过时发生事故。', NULL, '对', '错', '空', '空', '对', '夜间会车遇对向车辆<b>不关闭</b>远光灯，可交替使用远、近光灯提示对向车辆，当对向来车仍<b>不关闭</b>远光灯时，要<b>及时减速靠右侧行驶或停车让行</b>。因为远光灯会使对面车辆驾驶人因<b>强烈灯光照射造成炫目而无法看清前方道路情况</b>，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', '', '未关闭远光灯|减速', 0001, 0.02);
INSERT INTO `question` VALUES (1733399810033164289, 1000, 'e89cf', 0001, '如图所示，在这种情形超车时，要提前开启左转向灯，连续鸣喇叭或开启远光灯提示，催促前车让行。', NULL, '对', '错', '空', '空', '错', '<b>如图所示，对向有来车且预计在超车过程中与橙色车辆有会车的可能，所以这种路况下，不具备安全的超车条件，应放弃超车。</b>并且开启远光灯、连续鸣喇叭、催促等均是不利于安全行车的做法。<br>在没有中心线的道路上超车时，要确认有足够的横向间距且对向无来车。预计在超车过程中与对向来车有会车可能时，应提前减速，主动放弃超车。', 'https://timg.mnks.cn/20230927a13c54c1.jpg', '超车|开启远光灯', 0002, 0.05);
INSERT INTO `question` VALUES (1733399810083495937, 1001, 'd2dd5', 0001, '遇到这种情况下可以从右侧超车。', NULL, '对', '错', '空', '空', '错', '超车时要保持与被超越车辆的安全距离，观察左侧交通情况，选择合理时机，开启左转向灯，<b>从被超越车辆的左侧超越</b>。超越后，在不影响被超越车辆正常行驶的情况下，逐渐驶回原车道。不得从被超车辆的右侧超越。', 'https://timg.mnks.cn/20230926a650e5e2.jpg', '可以从', 0002, 0.06);
INSERT INTO `question` VALUES (1733399810138021890, 1002, '2d73b', 0001, '驾驶机动车超车时，前方车辆不减速让路，应停止超车并适当减速，与前方车辆保持安全距离。', NULL, '对', '错', '空', '空', '对', '超车应在确认前车让超并有充足的安全距离后，与前车保持一定的安全距离从前车的左侧超越。进入左侧道路超车，<b>无法保证与正常行驶前车的横向安全间距或被超车无让超空间时，应主动放弃超车。</b> <br>前方车辆不减速让路，说明不具备超车条件，因此<b>应停止超车并适当减速，与前方车辆保持安全距离</b>。', '', '不减速|停止|安全距离', 0001, 0.01);
INSERT INTO `question` VALUES (1733399810226102273, 1003, '723d4', 0002, '机动车向左转弯、向左变更车道、驶离停车地点或者掉头时，提前开启左转向灯是为了什么？ ', 'A、提示前车，将要向左变更行驶路线 B、提示后车，将要向右变更行驶路线 C、提示后车，将要向左变更行驶路线 D、提示前车，将要向右变更行驶路线', '提示前车，将要向左变更行驶路线 ', '提示后车，将要向右变更行驶路线 ', '提示后车，将要向左变更行驶路线 ', '提示前车，将要向右变更行驶路线', 'C', '驾驶机动车向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯，用以<b>提示后车，将要向左变更行驶路线</b>，以防发生意外。', '', '开启左转向灯', 0003, 0.14);
INSERT INTO `question` VALUES (1733399810272239618, 1004, 'ef5e5', 0001, '如图，夜间行车遇到这种交叉路口，不管有没有车辆和行人横过路口，都要开启远光灯提示。', NULL, '对', '错', '空', '空', '错', '<b>通过交叉路口时不能使用远光灯</b>。夜间通过人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 'https://timg.mnks.cn/20230926eea62716.jpg', '开启远光灯', 0005, 0.27);
INSERT INTO `question` VALUES (1733399810314182657, 1005, '0f560', 0001, '驾驶机动车在没有中心线的城市道路上，最高速度不能超过每小时50公里。', NULL, '对', '错', '空', '空', '错', '【<b>无线：城3公4</b>】《道路交通安全法实施条例》第四十五条：在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br><b>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里。</b>【无线：城3公4】', '', '没有中心线|不能超过', 0004, 0.21);
INSERT INTO `question` VALUES (1733399810364514305, 1006, '6358c', 0002, '驾驶机动车遇有前方交叉路口交通阻塞时怎么办？ ', 'A、依次停在路口外等候 B、可借对向车道通过 C、从前车两侧穿插通过 D、进入路口内等候', '依次停在路口外等候 ', '可借对向车道通过 ', '从前车两侧穿插通过 ', '进入路口内等候', 'A', '《道路交通安全法实施条例》第五十三条：机动车遇有前方交叉路口交通阻塞时，应当<b>依次停在路口以外等候</b>，不得进入路口。', '', '交通阻塞', 0001, 0.03);
INSERT INTO `question` VALUES (1733399810410651649, 1007, '830b4', 0001, '如图所示，A车具有优先通行权。', NULL, '对', '错', '空', '空', '对', '<b>【转弯让直行】如图所示：A车直行，B车左转弯，因此，A车具有优先通行权。</b><br>【拓展知识】<br>《道路交通安全法实施条例》第五十二条 机动车通过交叉路口，除应当遵守第五十一条第(二)项、第(三)项的规定外，还应当遵守下列规定：<br>(一)有交通标志、标线控制的，让优先通行的一方先行；<br>(二)没有交通标志、标线控制的，在进入路口前停车瞭望，让右方道路的来车先行；【让右原则】<br>(三)<b>转弯的机动车让直行的车辆先行</b>；【转弯让直行】<br>(四)相对方向行驶的右转弯的机动车让左转弯的车辆先行。【右转让左转】', 'https://timg.mnks.cn/20230926e6a915eb.jpg', 'A车', 0003, 0.13);
INSERT INTO `question` VALUES (1733399810469371906, 1008, 'e24d5', 0001, '如图，在这种环境下会车前，要先与对面来车交替变换远近光灯观察前方道路情况，会车时两车都要关闭远光灯交会。', NULL, '对', '错', '空', '空', '对', '如图所示环境下会车前，应交替使用远近光灯观察前方路况，会车时两车都要<b>关闭远光灯</b>。<br>如果夜间不按规定使用灯光，尤其是会车<b>不关闭远光灯</b>，会使得对面车辆驾驶人因强烈灯光照射造成炫目而无法看清前方道路情况，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 'https://timg.mnks.cn/20230926d704673a.jpg', '交替变换|关闭远光灯', 0003, 0.08);
INSERT INTO `question` VALUES (1733399810507120642, 1009, 'ed289', 0002, '如图所示，在这种情况下通过路口，交替使用远近光灯的目的是什么？ ', 'A、检查灯光是否能正常使用 B、提示其他交通参与者注意来车 C、准备变更车道 D、超车前提示前车', '检查灯光是否能正常使用 ', '提示其他交通参与者注意来车 ', '准备变更车道 ', '超车前提示前车', 'B', '夜间驾驶机动车通过人行横道或没有交通信号灯控制的路口，应在距路口约100米处减速慢行，并且<b>交替使用远、近光灯示意</b>，以<b>提示其他交通参与者注意来车</b>，确认安全后低速通过，同时要注意黑暗中的行人和非机动车。', 'https://timg.mnks.cn/20230926af2111f9.jpg', '交替使用远近光灯', 0001, 0.02);
INSERT INTO `question` VALUES (1733399810549063682, 1010, 'c3ed9', 0001, '如图，在这种铁路道口，如果没有看到列车驶来就要加速通过道口。', NULL, '对', '错', '空', '空', '错', '驾驶机动车遇铁路道口红灯亮时，表示火车要通过了，此时禁止车辆、行人通行，车辆不得越过停止线，更不得加速通过道口。', 'https://timg.mnks.cn/20230926acdf3f13.jpg', '加速通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399810591006721, 1011, '42f37', 0001, '驾驶机动车行经城市没有列车通过的铁路道口时允许超车。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。<b>有下列情形之一的，不得超车</b>：<br>（一）前车正在左转弯、掉头、超车的；<br>（二）与对面来车有会车可能的；<br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；<br>（四）行经<b>铁路道口、</b>交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '允许超车', 0002, 0.06);
INSERT INTO `question` VALUES (1733399810632949761, 1012, '30775', 0002, '如图所示，在这种情况下跟车行驶，不能使用远光灯的原因是什么？ ', 'A、不利于看清远方的路况 B、会影响自己的视线 C、会影响前车驾驶人的视线 D、不利于看清车前的路况', '不利于看清远方的路况 ', '会影响自己的视线 ', '会影响前车驾驶人的视线 ', '不利于看清车前的路况', 'C', '夜间不按规定使用灯光，尤其是跟车行驶不关闭远光灯，会使得<b>前车驾驶人</b>因强烈灯光照射造成炫目而无法看清前方道路情况，导致操纵失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 'https://timg.mnks.cn/20230926f1bfeb19.jpg', '不能使用', 0002, 0.05);
INSERT INTO `question` VALUES (1733399810670698497, 1013, 'e1b9b', 0001, '如图，驶近一个铁路道口时，只要看到栏杆还没放下来，就可以加速通过道口。', NULL, '对', '错', '空', '空', '错', '通过有交通信号控制的铁路道口<b>遇红灯亮</b>时，要将车停在<b>停止线以外等待</b>，等红灯熄灭时再通行，<b>不得加速抢行</b>通过。', 'https://timg.mnks.cn/20230926b927d6cf.jpg', '加速通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399810716835841, 1014, '03512', 0001, '夜间通过无交通信号灯控制的交叉路口时，不得变换远、近光灯。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十九条，<b>机动车在夜间通过</b>急弯、坡路、拱桥、人行横道或者<b>没有交通信号灯控制的路口</b>时，<b>应当交替</b>使用远近光灯示意。', '', '无交通信号灯|不得变换', 0004, 0.20);
INSERT INTO `question` VALUES (1733399810754584578, 1015, 'f3e7b', 0002, '在超越前车时，提前开启左转向灯，变换使用远、近光灯或者鸣喇叭是为了什么？ ', 'A、提醒后车以及前车 B、提醒行人 C、仅提醒后车 D、仅提醒前车', '提醒后车以及前车 ', '提醒行人 ', '仅提醒后车 ', '仅提醒前车', 'A', '超车时交替使用远近光灯或者鸣喇叭是为了提醒前车，开启左转向灯是为了提醒后车，所以是为了提醒后车和前车。', '', '超越', 0002, 0.07);
INSERT INTO `question` VALUES (1733399810800721921, 1016, 'b2490', 0001, '如图，驶近这种路段时，只要没有车辆和行人在人行横道上通过，就可以加速通过。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第四十七条，机动车行经人行横道时，<b>应当减速行驶</b>；遇行人正在通过人行横道，应当停车让行。', 'https://timg.mnks.cn/20230926bfb9da5f.jpg', '加速通过', 0002, 0.04);
INSERT INTO `question` VALUES (1733399810851053569, 1017, '0b05d', 0001, '驾驶机动车在没有交通信号的路口遇到前方车辆缓慢行驶时要依次交替通行。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第四十五条明确规定：在车道减少的路段、路口，或者在没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口遇到停车排队等候或者缓慢行驶时，机动车应当<b>依次交替</b>通行。', '', '依次交替', 0001, 0.03);
INSERT INTO `question` VALUES (1733399810918162433, 1018, '1eb97', 0001, '行驶在高速公路上遇大雾视线受阻时，应当立即紧急制动停车。', NULL, '对', '错', '空', '空', '错', '行驶在高速公路遇大雾视线受阻时，应开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过20公里/小时，并从最近的出口尽快驶离高速公路。 <br>高速公路停车应选择到服务区停车，不得在行车道内停车。', '', '立即紧急', 0003, 0.15);
INSERT INTO `question` VALUES (1733399810960105474, 1019, '17955', 0002, '机动车驶近急弯、坡道顶端等影响安全视距的路段时，减速慢行并鸣喇叭示意是为了什么？ ', 'A、测试喇叭是否能正常使用 B、提示前车后方车辆准备超车 C、提示对向交通参与者我方有来车 D、避免行至坡道顶端车辆动力不足', '测试喇叭是否能正常使用 ', '提示前车后方车辆准备超车 ', '提示对向交通参与者我方有来车 ', '避免行至坡道顶端车辆动力不足', 'C', '驾驶机动车驶近急弯、坡道顶端等影响安全视距的路段时，应当减速慢行并鸣喇叭示意，<b>以提示对向交通参与者我方有来车，注意安全</b>。', '', '影响安全视距', 0003, 0.11);
INSERT INTO `question` VALUES (1733399811014631425, 1020, 'b7392', 0001, '在路口直行看到图中这种情况，要在停止线外停车等待对面直行车通过后再起步。', NULL, '对', '错', '空', '空', '错', '<b>直行绿灯亮，允许直行</b>。方向指示信号灯设置于路口，用于指定某一方向上机动车通行，箭头方向向左、向上、向右分别表示左转、直行、右转。<br>如图所示信号灯指示牌，向上和向右的绿色箭头灯亮，表示<b>准许直行</b>车道和右转车道上的机动车直行和右转弯通行。<br>【拓展知识】<br>\n<p><img src=\"https://timg.mnks.cn/20231009a9d41c6b.png\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>以停止线为界，停止线到路口的区域称为停止线以内，停止线与车道间的区域称为<b>停止线以外</b>。', 'https://timg.mnks.cn/20230926dc52fd04.jpg', '路口直行|停车', 0004, 0.21);
INSERT INTO `question` VALUES (1733399811048185857, 1021, 'd16b8', 0001, '夜间行车，需要超车时，变换远近光灯示意是为了提示前车。', NULL, '对', '错', '空', '空', '对', '夜间超车，由于光线差、驾驶员视野变小，仅仅通过打左转向灯有时候很难让前车注意到后车发出的超车信号。为了保证安全，可以在需要超车时，<b>变换远近光灯提示前车注意避让</b>。题目描述无误，本题答案为对。', '', '提示', 0003, 0.10);
INSERT INTO `question` VALUES (1733399811090128898, 1022, '75253', 0001, '闪光警告信号灯为持续闪烁的黄灯，其作用是提示车辆、行人需要快速通过。', NULL, '对', '错', '空', '空', '错', '闪光警告信号灯为<b>持续闪烁</b>的黄灯，提示车辆、行人通行时注意瞭望，<b>确认安全后通过</b>。因此，<b>快速通过做法错误</b>。<br>【拓展知识】<br>黄灯的法律含义是警示，只闪黄灯表示此路口交通信号暂时解除，过往车辆经过此路口的时候需要提前减速观察周围情况决定停不停车，这种信号灯往往设置在车流不大的路口或是夜间等车流平峰时段。', '', '快速通过', 0003, 0.12);
INSERT INTO `question` VALUES (1733399811127877634, 1023, 'dc8e3', 0001, '遇到图中这种情况，要在停止线以外停车等待绿灯亮后再向右转弯。', NULL, '对', '错', '空', '空', '错', '红灯能不能右转，首先看信号灯是方向信号灯（箭头灯）还是机动车信号灯（圆形灯）。<br><b>圆形灯红灯亮可以右转</b>，但同时注意不得妨碍被放行的车辆、行人通行。因此，题干表述有错误。', 'https://timg.mnks.cn/20230927b0cb64aa.jpg', '停车等待', 0004, 0.24);
INSERT INTO `question` VALUES (1733399811169820674, 1024, '2086e', 0002, '如图所示，在这种情况下驶近路口，车辆可以怎么行驶？ ', 'A、只能直行 B、左转或者直行 C、左转或右转 D、直行或右转', '只能直行 ', '左转或者直行 ', '左转或右转 ', '直行或右转', 'B', '<b>注意观察地面车道箭头和信号灯</b>。如图所示，1、机动车所在车道的箭头指向左侧和前方，表示允许机动车<b>左转或直行</b>；2、前方车道信号灯显示绿灯，表示允许通行，因此，车辆可以<b>左转或直行</b>行驶。', 'https://timg.mnks.cn/20230926fbf4297c.jpg', '怎么行驶', 0003, 0.10);
INSERT INTO `question` VALUES (1733399811270483970, 1025, 'f0afd', 0001, '夜间行车，要尽量避免超车，确需超车时，可变换远近光灯向前车示意。', NULL, '对', '错', '空', '空', '对', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄。同时，驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。因此，<b>夜间行车应尽量避免超车，确实需要超车时，可变换远、近光灯提示前车</b>，待前车让行后方可超越；遇前车不让行时，不得强行超车。', '', '避免超车|变换远近光灯', 0002, 0.08);
INSERT INTO `question` VALUES (1733399811358564353, 1026, 'c024a', 0001, '驾车直行通过路口，看到图中这种信号灯亮起时，不能减速或停车等待，必须尽快加速通过，避免出现意外。', NULL, '对', '错', '空', '空', '错', '<b>加速通过路口的做法是错误的。</b>图中黄灯是一种过渡信号灯，起到警示作用，提示信号即将变换，在此处应该注意观察，已越过停止线的可以继续通过，<b>没有越过停止线的需要停车等待</b>。<br>如图所示，车辆没有越过停止线，因此应停车等待。', 'https://timg.mnks.cn/20230927f66d7d23.jpg', '尽快加速通过', 0002, 0.05);
INSERT INTO `question` VALUES (1733399811408896002, 1027, 'a73a5', 0001, '机动车在高速公路上遇前方交通受阻时，应当跟随前车顺序排队，并立即开启危险报警闪光灯，防止追尾。', NULL, '对', '错', '空', '空', '对', '开启危险警报闪光灯是为了提醒后车：前方有故障，请减速慢行，谨防追尾。', '', '交通受阻|顺序排队|危险报警闪光灯', 0002, 0.04);
INSERT INTO `question` VALUES (1733399811450839041, 1028, '03de2', 0002, '关于机动车灯光的使用，以下说法正确的是什么？ ', 'A、夜间驾驶机动车在照明条件良好的路段必须使用远光灯 B、夜间驾驶机动车在照明条件良好的路段可以不使用灯光 C、机动车灯光一个重要的作用是提示其他机动车驾驶人和行人 D、机动车灯光的作用仅仅是为了在夜间照明', '夜间驾驶机动车在照明条件良好的路段必须使用远光灯 ', '夜间驾驶机动车在照明条件良好的路段可以不使用灯光 ', '机动车灯光一个重要的作用是提示其他机动车驾驶人和行人 ', '机动车灯光的作用仅仅是为了在夜间照明', 'C', '对于车辆来说，车灯就好比人的眼睛一样重要，正常的开启或关闭关乎到驾驶人能否安全驾驶车辆到达目的地。简单的说，<b>灯光有照明和信号两方面作用</b>，一个是为驾驶人提供照明，在天气不好或是阴天的时候提供良好的视野；另一个重要作用是起到警示的作用，为的是提醒其余车辆与人员。', '', '说法正确', 0003, 0.13);
INSERT INTO `question` VALUES (1733399811496976385, 1029, '59908', 0001, '遇到这种情况时，要加速从红车前变更车道。', NULL, '对', '错', '空', '空', '错', '如图所示路况下不具备变更车道条件，加速从正常行驶的红车前方变道<b>做法错误</b>，会干扰红车正常行驶，<b>严重扰乱道路通行秩序</b>，导致交通事故。', 'https://timg.mnks.cn/20230927fa12b24d.jpg', '要加速', 0001, 0.02);
INSERT INTO `question` VALUES (1733399811538919425, 1030, 'a1646', 0001, '如图所示，在路口看到这种信号灯不断闪烁时，要减速或停车瞭望，确认安全后通过。', NULL, '对', '错', '空', '空', '对', '路口黄灯<b>持续闪烁</b>，提示车辆、行人通行时<b>要注意减速或停车瞭望</b>，确认安全后通过。<br>【拓展知识】<br>黄灯的法律含义是警示，只闪黄灯表示此路口交通信号暂时解除，过往车辆经过此路口的时候需要提前减速观察周围情况决定停不停车，这种信号灯往往设置在车流不大的路口或是夜间等车流平峰时段。', 'https://timg.mnks.cn/20230926db494d05.jpg', '减速或停车瞭望', 0003, 0.10);
INSERT INTO `question` VALUES (1733399811643777025, 1031, '86f88', 0002, '遇到这种情形怎样行驶？ ', 'A、停车让对方车辆通过 B、开启左转向灯向左行驶 C、加速超越障碍后会车 D、开前照灯告知对方让行', '停车让对方车辆通过 ', '开启左转向灯向左行驶 ', '加速超越障碍后会车 ', '开前照灯告知对方让行', 'A', '驾驶机动车在有障碍的路段<b>遇对向来车时</b>，<b>有障碍的一方让无障碍的一方先行</b>；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。', 'https://timg.mnks.cn/20230926d5632d3f.jpg', '怎样行驶', 0001, 0.04);
INSERT INTO `question` VALUES (1733399811694108673, 1032, '36e03', 0001, '设有安全带装置的车辆，应要求车内乘员系安全带。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。 <br>【拓展知识】 <br>座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。', '', '系安全带', 0001, 0.02);
INSERT INTO `question` VALUES (1733399811740246017, 1033, 'ff7ae', 0001, '如图所示，A车要在前方掉头行驶，可以在此处变换车道，进入左侧车道准备掉头。', NULL, '对', '错', '空', '空', '错', '图中虚线箭头代表的是车辆行驶路线，仔细观察虚线路径，可以看到变道时压到了白色实线。变道是不能压线的，因此图中A车要在前方掉头行驶，是不能在此处变换车道的。题目描述错误，本题答案为错。', 'https://timg.mnks.cn/20230926cba6140c.jpg', '可以在', 0003, 0.12);
INSERT INTO `question` VALUES (1733399811773800449, 1034, '02e80', 0002, '如图所示，驾驶机动车驶出这个路口时应当怎样使用灯光? ', 'A、开启右转向灯 B、开启危险报警闪光灯 C、不用开启转向灯 D、开启左转向灯', '开启右转向灯 ', '开启危险报警闪光灯 ', '不用开启转向灯 ', '开启左转向灯', 'A', '驾驶机动车驶出环岛前，<b>打开右转向灯</b>，注意观察右侧车辆、行人的动态。在有两条或两条以上车道的环岛驶出时，应提前开启右转向灯变更至外侧车道，严禁直接从内侧车道驶出环岛。<b>因此，图中机动车，驶出环岛时应当开启右转向灯</b>。', 'https://timg.mnks.cn/20230926d50e6593.jpg', '驶出|使用灯光', 0004, 0.23);
INSERT INTO `question` VALUES (1733399811815743490, 1035, 'a84a9', 0001, '如图所示，如果遇到这种情况需要超车时，可以在不影响公交车通行的前提下借公交车道超越。', NULL, '对', '错', '空', '空', '错', '交通法规定，超车时必须<b>从前车的左侧超越</b>。另外<b>公交车道属于专用车道</b>，只允许公交车通行的，其他车辆是不可以进入行驶的。<br>所以，<b>借右侧公交车道超车，是交通违法行为。</b>', 'https://timg.mnks.cn/20230926a8939bc5.jpg', '可以在|借公交车道', 0002, 0.06);
INSERT INTO `question` VALUES (1733399811853492225, 1036, 'd654f', 0001, '驾驶机动车在没有中心线的公路上，最高速度不能超过每小时70公里。', NULL, '对', '错', '空', '空', '错', '<b>没有</b><b>中心线</b><b>的公路</b>，限速要求<b>≤40公里/小时</b>。《道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在<b>没有</b>限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br><b>(一)<b>没有</b>道路<b>中心线</b>的道路，城市道路为每小时30公里，公路为每小时40公里；</b><br>(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', '', '没有|公路', 0004, 0.21);
INSERT INTO `question` VALUES (1733399811891240961, 1037, 'bd639', 0001, '图中这辆红色机动车选择的行车道是正确的。', NULL, '对', '错', '空', '空', '错', '车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶。绿色箭头灯亮时，准许本车道车辆按指示方向通行；<b>红色叉形灯</b>或者红色箭头灯<b>亮时</b>，<b>禁止本车道车辆通行</b>。 <br>如图所示：<b>红色轿车行驶的车道</b>，<b>红色叉形灯</b>亮，表示该车道<b>禁止车辆通行。</b>', 'https://timg.mnks.cn/20230927dcfe39f0.jpg', '红色|正确', 0002, 0.05);
INSERT INTO `question` VALUES (1733399811933184001, 1038, '7fa2a', 0001, '在高速公路上遇分流交通管制时，可不驶出高速公路，就地靠边停靠等待管制结束后继续前行。', NULL, '对', '错', '空', '空', '错', '在高速公路上遇到分流交通管制时，<b>应听从交警指挥，按指示通行</b>，不得就地靠边停车等待，以免造成更大的交通堵塞。', '', '交通管制|可不', 0004, 0.24);
INSERT INTO `question` VALUES (1733399811970932738, 1039, '3087e', 0001, '如图所示，在这种情况下，您应该轻踩制动踏板减速。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第四十二条：机动车上道路行驶，<b>不得超过限速标志标明的最高时速</b>。 <br>如图所示，道路最高限速60公里/小时，速度仪表盘显示速度大于60公里/小时，因此，应当轻踏制动踏板减速行驶。', 'https://timg.mnks.cn/20230926eb5f4cda.jpg', '轻踩|减速', 0002, 0.07);
INSERT INTO `question` VALUES (1733399812021264386, 1040, 'ac40b', 0001, '如图所示，遇到这种有车道信号灯的路段，应该选择右侧或者左侧车道行驶。', NULL, '对', '错', '空', '空', '对', '车辆行驶在设置了车道信号灯的道路上，必须<b>选择绿色箭头灯亮的车道行驶</b>。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。 <br>如图所示，<b>右侧和左侧车道绿色箭头灯亮，表示可以通行</b>；中间车道红色叉形灯亮，表示禁止车辆通行。', 'https://timg.mnks.cn/20230927e205e540.jpg', '右侧|左侧', 0003, 0.11);
INSERT INTO `question` VALUES (1733399812071596034, 1041, '5a227', 0002, '驾驶机动车在没有道路中心线的狭窄山路怎样会车？ ', 'A、速度慢的先行 B、重车让空车先行 C、靠山体的一方先行 D、不靠山体的一方先行', '速度慢的先行 ', '重车让空车先行 ', '靠山体的一方先行 ', '不靠山体的一方先行', 'D', '在狭窄的坡道会车：1，下坡车让上坡车先行。2、如果下坡车已行至中途而上坡车还未上坡时，下坡车先行。在没有道路中心线的<b>狭窄山路会车</b>：<b>不靠山体一方的车辆先行</b>。【狭窄路“靠山让行”】', '', '狭窄山路', 0004, 0.20);
INSERT INTO `question` VALUES (1733399812117733378, 1042, 'eb828', 0001, '夜间尾随前车行驶时，后车可以使用远光灯。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，<b>但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。</b>机动车雾天行驶应当开启雾灯和危险报警闪光灯。', '', '尾随|使用远光灯', 0002, 0.05);
INSERT INTO `question` VALUES (1733399812319059970, 1043, 'eb3eb', 0002, '夜间会车，遇对方使用远光灯，应如何安全驾驶？ ', 'A、加速通过 B、直视对方车辆，确定来车位置 C、开启远光灯，与对车对射示意 D、降低车速，靠右行驶，必要时停车让行', '加速通过 ', '直视对方车辆，确定来车位置 ', '开启远光灯，与对车对射示意 ', '降低车速，靠右行驶，必要时停车让行', 'D', '夜间会车前，应当在距对方来车150米以外改用近光灯，遇对方车辆使用远光灯，应<b>及时减速靠右侧行驶或停车让行</b>。不要开启远光灯对射；也不要直视对向车辆的灯光，视线向右避开，以防眩光刺眼。', '', '会车|远光灯', 0001, 0.02);
INSERT INTO `question` VALUES (1733399812365197314, 1044, 'e0558', 0001, '如图，这辆红色机动车行驶的车道是慢速车道。', NULL, '对', '错', '空', '空', '错', '<b>红车行驶的车道是快速车道</b>。交通法规定，在道路<b>同方向划有2条以上</b>机动车道的，<b>左侧为快速车道</b>，右侧为慢速车道。', 'https://timg.mnks.cn/20230926ef4a886f.jpg', '红色|慢速车道', 0002, 0.05);
INSERT INTO `question` VALUES (1733399812402946050, 1045, 'df1fc', 0001, '在交叉路口遇到这种情况时，要在红灯亮以前加速通过路口。', NULL, '对', '错', '空', '空', '错', '驾驶机动车在黄色信号灯亮的路口，已越过停止线的车辆可以继续通行；<b>没有越过停止线</b>的车辆<b>不得加速抢行</b>通过，要在停止线以外停车等待。 <br>如图所示机动车，在停止线以外，因此应当停车等待，不得加速通过。', 'https://timg.mnks.cn/20230927c5c2f2b2.jpg', '加速通过', 0001, 0.03);
INSERT INTO `question` VALUES (1733399812444889090, 1046, 'b7f2e', 0001, '如图所示，驶近这种路口时，必须先停车，再重新起步通过路口。', NULL, '对', '错', '空', '空', '错', '【一停<b>二看</b>三通过】如图所示，车辆必须在<b>停止线外停车观察左、右两侧道路交通情况</b>，确认安全后，方可重新起步通过路口。<br>驾驶机动车在没有交通信号灯控制的路口直行，应在距路口50-100米时减速，行至路口时<b>仔细观察左、右两侧道路交通情况</b>，遇到有停车让行标志的路口，在停止线以外停车观察主路情况，<b>确认安全后</b>再通过。', 'https://timg.mnks.cn/20230927b8ee8f41.jpg', '先停车', 0005, 0.43);
INSERT INTO `question` VALUES (1733399812482637826, 1047, '0d1e2', 0001, '小型客车行驶在平坦的高速公路上，突然有颠簸感觉时，应迅速降低车速，防止爆胎。', NULL, '对', '错', '空', '空', '对', '高速行车时，如果车身突然出现颠簸现象，则有可能存在轮胎漏气问题，应迅速反应，及时降低车速，尽快驶离主车道，不要紧急制动，以免造成翻车或后车制动不急导致追尾事故。<b>请注意：</b>这里的“迅速”指的是迅速反应减速行驶，不是指紧急停车的意思。', '', '颠簸|降低车速', 0003, 0.12);
INSERT INTO `question` VALUES (1733399812524580866, 1048, 'd39f1', 0001, '如图所示，驾驶机动车遇到这种情况时，A车应当注意避让。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第五十四条，<b>道路养护车辆</b>、工程作业车<b>进行作业时</b>，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，<b>过往车辆和人员应当注意避让</b>。', 'https://timg.mnks.cn/20230926d3bf0579.jpg', '注意避让', 0002, 0.05);
INSERT INTO `question` VALUES (1733399812566523905, 1049, '1274f', 0002, '路口转弯过程中，持续开启转向灯，主要是因为什么？ ', 'A、让其他驾驶人知道您正在转弯 B、完成转弯动作前，关闭转向灯会对车辆造成损害 C、让其他驾驶人知道您正在超车 D、完成转弯动作前，关闭转向灯是习惯动作', '让其他驾驶人知道您正在转弯 ', '完成转弯动作前，关闭转向灯会对车辆造成损害 ', '让其他驾驶人知道您正在超车 ', '完成转弯动作前，关闭转向灯是习惯动作', 'A', '路口转弯过程中，持续开启转向灯，是为了提醒别的行人和车辆此车正在转弯，注意安全。直至转弯动作完成后，才能关闭转向灯。', '', '转弯|转向灯', 0004, 0.17);
INSERT INTO `question` VALUES (1733399812616855554, 1050, 'dc6ed', 0001, '如图所示，机动车驾驶人驾车通过此路口时，应减速慢行，时刻注意观察行人的动态变化，因为行人可能随时改变方向。', NULL, '对', '错', '空', '空', '对', '行人参与交通的主要特点是行走随意性大，方向多变，很容易引起险情，因此，驾车通过<b>有行人正在通行的路口</b>时，应当减速慢行，时刻注意观察行人的动态变化，主动减速避让，必要时停车让行，以确保安全通过路口。', 'https://timg.mnks.cn/20230926e3e2c844.jpg', '减速慢行', 0003, 0.09);
INSERT INTO `question` VALUES (1733399812658798594, 1051, '60567', 0001, '如图所示，A车具有优先通行权。', NULL, '对', '错', '空', '空', '对', '【直行<b>让右原则</b>】<b>A车在右方道路，所以A车具有优先通行权。</b>法律依据《道路交通安全法实施条例》第五十二条，机动车通过没有交通信号灯控制也没有交通警察指挥的交叉路口，应当遵守下列规定：<br> （一）有交通标志、标线控制的，让优先通行的一方先行； <br>（二）没有交通标志、标线控制的，在进入路口前停车瞭望，<b>让右方道路的来车先行；</b><br>（三）转弯的机动车让直行的车辆先行； <br>（四）相对方向行驶的右转弯的机动车让左转弯的车辆先行。', 'https://timg.mnks.cn/20230926a825691c.jpg', 'A车', 0003, 0.14);
INSERT INTO `question` VALUES (1733399812700741634, 1052, '57e0b', 0002, '夜间在道路上会车时，距离对向来车多远将远光灯改用近光灯？ ', 'A、不必变换灯光 B、150米以外 C、100米以内 D、50米以内', '不必变换灯光 ', '150米以外 ', '100米以内 ', '50米以内', 'B', '《道路交通安全法》第四十八条：在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定： <br>(一)减速靠右行驶，并与其他车辆、行人保持必要的安全距离； <br>(二)在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行； <br>(三)在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行； <br>(四)在狭窄的山路，不靠山体的一方先行； <br>(五)<b>夜间会车应当在距相对方向来车150米以外改用近光灯，</b>在窄路、窄桥与非机动车会车时应当使用近光灯。', '', '会车|改用近光', 0003, 0.14);
INSERT INTO `question` VALUES (1733399812742684674, 1053, 'ff1ec', 0002, '行车中通过窄桥时怎样控制车速？ ', 'A、不超过60公里/小时 B、不超过50公里/小时 C、不超过40公里/小时 D、不超过30公里/小时', '不超过60公里/小时 ', '不超过50公里/小时 ', '不超过40公里/小时 ', '不超过30公里/小时', 'D', '机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里： <br>（一）进出非机动车道，<b>通过铁路道口、急弯路、窄路、窄桥时</b>； <br>（二）掉头、转弯、下陡坡时； <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时； <br>（四）在冰雪、泥泞的道路上行驶时；', '', '窄桥', 0002, 0.04);
INSERT INTO `question` VALUES (1733399812784627713, 1054, 'e254b', 0001, '遇到一个图中所示的漫水路段时，要提前减速慢行进入水区，在涉水路段行驶，一定要低速缓慢行驶，涉水途中禁止停车。', NULL, '对', '错', '空', '空', '错', '行经漫水路段，要<b>提前察明水情，确认安全</b>后，减速慢行进入水区。<br><b>【通过漫水路时的安全驾驶】</b><br>1、涉水前，应<b>停车察明水情</b>，确认安全后，低速通过；<br>2、涉水时，保持<b>车速均匀平稳</b>且有足够动力，<b>尽量不要中途换挡、停车和急转弯</b>；不要注视水流的变化，以防因视觉上判断错误而导致行驶方向偏移发生意外；<br>3、涉水后，擦干被水浸湿部位，保持低速行驶，并<b>间断轻踏</b>制动踏板，以恢复制动效能。', 'https://timg.mnks.cn/20230927da3bcd82.jpg', '漫水路段', 0005, 0.45);
INSERT INTO `question` VALUES (1733399812826570754, 1055, 'be57c', 0001, '如图所示，驾驶机动车在路口前遇到这种情况时，A车具有优先通行权。', NULL, '对', '错', '空', '空', '对', '道路上行驶时，右转弯让左转弯，<b>转弯让直行</b>。因此图中的A车具有优先通行权。', 'https://timg.mnks.cn/20230926d45926dd.jpg', 'A车', 0003, 0.09);
INSERT INTO `question` VALUES (1733399812889485313, 1056, 'f582c', 0001, '如图所示，在城市道路上，遇到这种情况需要超车时，可以直接开启右转向灯，借公交车道行驶。', NULL, '对', '错', '空', '空', '错', '车辆行驶途中即使专用车道无车辆行驶，也不可因超车占用专用车道。', 'https://timg.mnks.cn/20230926d75103a4.jpg', '借公交车道行驶', 0001, 0.03);
INSERT INTO `question` VALUES (1733399812935622657, 1057, '14f83', 0001, '在高速公路上行驶感觉疲劳时，应立即停车休息。', NULL, '对', '错', '空', '空', '错', '在高速公路上行驶感觉疲劳时，应选择到最近的服务区停车休息，<b>禁止</b>在高速公路车道内停车。', '', '疲劳|立即停车', 0003, 0.11);
INSERT INTO `question` VALUES (1733399812973371393, 1058, '3ddfb', 0002, '在暴雨天气驾车，刮水器无法刮净雨水时，应怎样做？ ', 'A、减速行驶 B、集中注意力谨慎驾驶 C、立即减速靠边停车 D、以正常速度行驶', '减速行驶 ', '集中注意力谨慎驾驶 ', '立即减速靠边停车 ', '以正常速度行驶', 'C', '当大雨、暴雨天气，<b>靠刮水器难以改善视线时，要立即减速行驶，选择安全地点停车</b>，开启示廓灯和危险报警闪光灯，待雨小或雨停后再继续行驶。 <br>请注意：<b>立即减速靠边停车≠紧急制动</b>。', '', '暴雨|无法刮净', 0004, 0.17);
INSERT INTO `question` VALUES (1733399813011120129, 1059, 'a88fc', 0002, '驾驶机动车在高速公路上行驶，遇到图中所示的情形，怎么做才正确？ ', 'A、可以借右侧应急车道行驶 B、与前车保持安全距离跟车行驶 C、紧跟左侧车道红色小客车行驶 D、鸣喇叭或变换远近光灯催促', '可以借右侧应急车道行驶 ', '与前车保持安全距离跟车行驶 ', '紧跟左侧车道红色小客车行驶 ', '鸣喇叭或变换远近光灯催促', 'B', '驾驶机动车在高速公路上跟车行驶时，必须<b>与前车保持安全的距离</b>，即保证前车制动时，本车随之制动而不与前车相撞的停车距离。', 'https://timg.mnks.cn/20230927b2178a0a.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399813057257474, 1060, '8ddfd', 0002, '驾驶机动车在没有中心线的道路上遇相对方向来车时怎样行驶？ ', 'A、减速靠右行驶 B、借非机动车道行驶 C、紧靠路边行驶 D、靠路中心行驶', '减速靠右行驶 ', '借非机动车道行驶 ', '紧靠路边行驶 ', '靠路中心行驶', 'A', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，<b>减速靠右行驶</b>，并与其他车辆、行人保持必要的安全距离。', '', '没有中心线', 0002, 0.07);
INSERT INTO `question` VALUES (1733399813095006209, 1061, '97cc9', 0001, '驾驶机动车遇前方交叉路口交通阻塞时，路口内无网状线的，可停在路口内等候。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十三条：机动车遇有前方交叉路口<b>交通阻塞</b>时，应当依次<b>停在路口以外</b>等候，不得进入路口。', '', '交通阻塞|可停', 0004, 0.18);
INSERT INTO `question` VALUES (1733399813128560641, 1062, '17d76', 0001, '遇到这种情况的路段，可以进入网状线区域内停车等候。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第五十三条：机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，<b>不得在人行横道、网状线区域内停车等候。</b>', 'https://timg.mnks.cn/20230925c672ff96.jpg', '网状线', 0002, 0.06);
INSERT INTO `question` VALUES (1733399813170503681, 1063, 'b6755', 0002, '在如图所示的道路跟车行驶时，为什么要保持较大的安全距离？ ', 'A、因为不能正确判断水的深度 B、因为路面积水的反光会影响距离的判断 C、因为前车驾驶人的反应会变得迟缓 D、因为溅起来的水会影响视线', '因为不能正确判断水的深度 ', '因为路面积水的反光会影响距离的判断 ', '因为前车驾驶人的反应会变得迟缓 ', '因为溅起来的水会影响视线', 'D', '驾驶机动车行经积水路面，<b>一定要和前车保持足够的安全距离</b>，便于发生突发情况时自己有反应和采取措施的时间。如果跟车距离较近，前车溅起的水花会直接打在车辆的挡风玻璃上，导致一时视线受阻无法看清前面路况，引发危险。', 'https://timg.mnks.cn/20230926b9caa8f2.jpg', '跟车', 0005, 0.33);
INSERT INTO `question` VALUES (1733399813216641026, 1064, '319d5', 0001, '如图所示，驾驶过程中遇到这种情况时，A车可以长鸣喇叭提醒道路养护车辆暂停喷水。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第五十四条，规定：道路养护车辆、工程作业车进行作业时，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，过往车辆和人员应当注意避让。所以，题干表述错误。', 'https://timg.mnks.cn/20230926cda439fe.jpg', '长鸣喇叭', 0003, 0.09);
INSERT INTO `question` VALUES (1733399813250195458, 1065, '629e1', 0001, '驾驶机动车在高速公路上行驶，错过出口时，如果确认后方无来车，可以倒回出口驶离高速公路。', NULL, '对', '错', '空', '空', '错', '高速公路行车注意事项：如果因疏忽驶过出口，只能继续向前行驶至立交桥掉头或在下一出口驶出高速公路后掉头，<b>不得</b>紧急制动；即便是刚刚错过出口且距下一出口很远，也<b>不得</b>掉头逆行或<b>倒回出口</b>。', '', '可以倒回', 0002, 0.05);
INSERT INTO `question` VALUES (1733399813317304321, 1066, '23520', 0001, '《道路交通安全法实施条例》规定，高速公路上最高时速不得超过120公里。因此在高速公路上行驶只要时速不超过120公里就不违法。', NULL, '对', '错', '空', '空', '错', '<b>不同车型的限速不同，不同车道的限速也不同</b>。而且高速上不仅有最高限速，还有最低限速，并不是不超过120就不违法。因此，题干表述错误。', '', '只要|不违法', 0003, 0.15);
INSERT INTO `question` VALUES (1733399813359247362, 1067, 'b59f9', 0002, '夜间驾驶机动车在窄路、窄桥会车怎样使用灯光？ ', 'A、关闭所有灯光 B、开启近光灯 C、关闭前照灯 D、开启远光灯', '关闭所有灯光 ', '开启近光灯 ', '关闭前照灯 ', '开启远光灯', 'B', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，<b>在窄路、窄桥与非机动车会车时应当使用近光灯。</b>', '', '会车', 0002, 0.07);
INSERT INTO `question` VALUES (1733399813430550529, 1068, '54ab2', 0001, '行车中前方遇自行车影响通行时，可鸣喇叭提示，加速绕行。', NULL, '对', '错', '空', '空', '错', '机动车行驶中遇前方有自行车影响通行时，应<b>主动减速让行</b>。连续鸣喇叭示意其让道或加速绕过，容易使骑车人惊慌失措而摔倒等，引起剐碰事故。', '', '影响通行|加速绕行', 0002, 0.05);
INSERT INTO `question` VALUES (1733399813480882177, 1069, '29fcd', 0002, '在山区冰雪道路上行车，遇有前车正在爬坡时，后车应怎样做？ ', 'A、选择适当地点停车，等前车通过后再爬坡 B、迅速超越前车 C、低速爬坡 D、紧随其后爬坡', '选择适当地点停车，等前车通过后再爬坡 ', '迅速超越前车 ', '低速爬坡 ', '紧随其后爬坡', 'A', '在山区冰雪道路上行车，遇前车正在爬坡时，不得紧随或超越前车爬坡，<b>要等前车通过坡顶后再上坡</b>，避免前车突然停车或后溜造成事故。', '', '山区冰雪|爬坡', 0002, 0.04);
INSERT INTO `question` VALUES (1733399813531213825, 1070, '07a67', 0001, '车辆在这种条件的道路上，最高速度不能超过每小时50公里。', NULL, '对', '错', '空', '空', '错', '如图所示，机动车在冰雪路行驶，因此最高行驶速度<b>不得超过30</b>公里/小时。因此题干表述错误。<br>机动车行驶中遇有下列情形之一的，<b>最高行驶速度不得超过每小时30公里，</b>其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 <br>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br>（二）掉头、转弯、下陡坡时；　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　 <br><b>（四）在冰雪、泥泞的道路上行驶时。</b>', 'https://timg.mnks.cn/20230926c49d19e6.jpg', '最高速度|50', 0003, 0.13);
INSERT INTO `question` VALUES (1733399813577351170, 1071, '01ea3', 0001, '这辆小轿车不能在这个位置停车。', NULL, '对', '错', '空', '空', '对', '机动车应当在规定地点停放，<b>禁止在<b>人行横道</b>上停放车辆</b>，因此题干表示<b>正确</b>。<br>《道路交通安全法实施条例》第六十三条，机动车<b>在道路上临时停车</b>，<b>应当遵守</b>下列规定：(一)在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及<b>人行横道</b>、施工地段，<b>不得停车</b>；', 'https://timg.mnks.cn/20230927f0ff2aab.jpg', '不能', 0002, 0.07);
INSERT INTO `question` VALUES (1733399813623488513, 1072, '10e81', 0002, '以下交通标志表示的含义是什么？ ', 'A、禁止机动车驶入 B、禁止小客车驶入 C、禁止所有车辆驶入 D、禁止非机动车驶入', '禁止机动车驶入 ', '禁止小客车驶入 ', '禁止所有车辆驶入 ', '禁止非机动车驶入', 'A', '如图所示，右前方禁令标志如下\n<p><img src=\"https://sucimg.itc.cn/sblog/j7c9e6f019c4be48536bcc33486dc369c\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>禁止机动车驶入</b>：表示前方路段禁止各类机动车驶入。</p>', 'https://timg.mnks.cn/20230927f960ea36.jpg', '', 0004, 0.24);
INSERT INTO `question` VALUES (1733399813665431553, 1073, '14166', 0001, '车辆在高速公路行驶时，可以仅凭感觉确认车速。', NULL, '对', '错', '空', '空', '错', '车辆在高速公路高速行驶时，驾驶人对车速的判断会有偏差，即“速度错觉”，因此<b>不要单纯凭感觉判断车速</b>，一定要通过观察车速表来判断车速。', '', '凭感觉', 0002, 0.05);
INSERT INTO `question` VALUES (1733399813698985986, 1074, 'ed2fb', 0001, '遇到这种情况可以优先通行。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。', 'https://timg.mnks.cn/20230926ab5dbe93.jpg', '优先', 0002, 0.06);
INSERT INTO `question` VALUES (1733399813745123329, 1075, '5b32f', 0002, '雾天行车时，应及时开启什么灯？ ', 'A、倒车灯 B、近光灯 C、远光灯 D、雾灯', '倒车灯 ', '近光灯 ', '远光灯 ', '雾灯', 'D', '雾天行车时，应开启<b>雾灯</b>，及时让其他车辆驾驶人发现自己。', '', '雾天', 0002, 0.07);
INSERT INTO `question` VALUES (1733399813791260674, 1076, 'b0c04', 0002, '对驾驶人开展日常安全教育是增强_____意识，提高安全文明素质的重要手段。 ', 'A、驾驶人社会责任 B、文明礼貌 C、得与失 D、优先发展交通', '驾驶人社会责任 ', '文明礼貌 ', '得与失 ', '优先发展交通', 'A', '驾驶人的社会责任是在参与道路交通时，从思想到行为应当遵循的道德标准。<b>驾驶人的社会责任感越高，安全文明素质也就越高，公共道路交通也愈加安全</b>。所以，对驾驶人开展日常教育是<b>增强驾驶人社会责任意识、提高安全文明素质的重要手段</b>。', '', '安全教育', 0004, 0.21);
INSERT INTO `question` VALUES (1733399813833203713, 1077, '991b0', 0002, '驾驶机动车遇到这种桥时首先怎样办？ ', 'A、保持匀速通过 B、尽快加速通过 C、低速缓慢通过 D、停车察明水情', '保持匀速通过 ', '尽快加速通过 ', '低速缓慢通过 ', '停车察明水情', 'D', '<b>请注意题干关键词，首先</b>。《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当<b>停车察明水情</b>，确认安全后，低速通过。', 'https://timg.mnks.cn/20230927da3bcd82.jpg', '首先', 0003, 0.13);
INSERT INTO `question` VALUES (1733399813875146753, 1078, '98e6e', 0002, '车辆在山区道路跟车行驶时，应怎样做？ ', 'A、紧随前车之后 B、适当加大安全距离 C、适当减小安全距离 D、尽可能寻找超车机会', '紧随前车之后 ', '适当加大安全距离 ', '适当减小安全距离 ', '尽可能寻找超车机会', 'B', '<b>山区道路跟车</b>，要<b>适当加大与前车的安全距离</b>，仔细观察前车动态。在视野不佳或道路条件差的路段，要进一步加大跟车距离，以防前车突然停车或停车后溜。', '', '山区|跟车', 0002, 0.07);
INSERT INTO `question` VALUES (1733399813925478401, 1079, 'ffdb2', 0001, '驾驶机动车在高速公路上行驶，能见度小于200米时，车速不得超过每小时60公里。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等<b>低能见度气象条件时</b>，应当遵守下列规定：<b>能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离。</b>', '', '小于200米|60公里', 0004, 0.17);
INSERT INTO `question` VALUES (1733399813967421441, 1080, '31c18', 0001, '未上坡的车辆遇到这种情况让对向下坡车先行。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第四十八条： 在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方<b>未上坡</b>时，下坡的一方先行。如图所示，下坡车已行驶至中途而上坡车<b>未上坡</b>，应<b>让下坡车先行。</b>因此，题干表述正确。', 'https://timg.mnks.cn/20230926f79ec4f6.jpg', '未上坡|让|下坡', 0002, 0.05);
INSERT INTO `question` VALUES (1733399814005170177, 1081, 'd0012', 0002, '遇有浓雾或特大雾天能见度过低，行车困难时，应怎样做？ ', 'A、开启前照灯，继续行驶 B、开启示廓灯、雾灯，靠右行驶 C、开启危险报警闪光灯和雾灯，选择安全地点停车 D、开启危险报警闪光灯，继续行驶', '开启前照灯，继续行驶 ', '开启示廓灯', '雾灯，靠右行驶 C', '开启危险报警闪光灯和雾灯，选择安全地点停车 D', 'C', '雾天行车，应开启<b>雾灯</b>；雾较大时还应同时开启<b>危险报警闪光灯</b>；当遇到浓雾或特大雾天能见度过低<b>行车困难时</b>，应<b>选择安全地点停车</b>等待雾散。', '', '浓雾|行车困难', 0003, 0.13);
INSERT INTO `question` VALUES (1733399814047113218, 1082, '2fa27', 0002, '如图所示，驾驶机动车行驶至此位置时，以下做法正确的是什么？ ', 'A、观察左侧无车后，可以左转 B、从该处直接左转 C、不得左转，应当直行 D、倒车退到虚线处换到左转车道', '观察左侧无车后，可以左转 ', '从该处直接左转 ', '不得左转，应当直行 ', '倒车退到虚线处换到左转车道', 'C', '上道路行驶的机动车，应按道路交通标志、标线的指示通行。如图所示，驾驶机动车行驶在路面导向箭头为直行的车道上，因此只能直行通过路口。', 'https://timg.mnks.cn/20230926d3552442.jpg', '做法正确', 0002, 0.07);
INSERT INTO `question` VALUES (1733399814084861954, 1083, '8edc9', 0001, '驾驶机动车通过漫水路时要加速行驶。', NULL, '对', '错', '空', '空', '错', '<b>要低速通过</b>，低速将车平稳开进水中。<b>千万不要加速通过</b>，否则由于车速快，造成水浪大，极有可能引起发动机进水。《道路交通安全法实施条例》第六十四条，机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，<b>低速通过</b>。', '', '要加速', 0001, 0.03);
INSERT INTO `question` VALUES (1733399814118416385, 1084, 'e9da2', 0001, '机动车在环形路口内行驶，遇有其他车辆强行驶入时，只要有优先权就可以不避让。', NULL, '对', '错', '空', '空', '错', '通过环岛时一定要提前用信号告知自己的行驶方向，减速慢行，保持安全间距，随时预防其他车辆的异常行为。遇强行驶入的机动车，即使有优先通行权，为确保行车安全，也要注意避让。', '', '只要|可以不', 0002, 0.07);
INSERT INTO `question` VALUES (1733399814156165121, 1085, 'e4e87', 0002, '在山区道路超车时，应怎样超越？ ', 'A、选择较缓的下坡路段超车 B、选择任意路段超车 C、选择宽阔的缓上坡路段超车 D、选择较长的下坡路段超车', '选择较缓的下坡路段超车 ', '选择任意路段超车 ', '选择宽阔的缓上坡路段超车 ', '选择较长的下坡路段超车', 'C', '山区道路行车要尽量避免超车，<b>需要超车时</b>，要<b>选择路面宽阔的缓上坡</b>路段，开启左转向灯，提前鸣喇叭，在确认前车让行后再超越。不得强行超车，严禁在路面狭窄、急转弯、连续转弯或对面有来车等不具备条件的路段超车。', '', '山区|超车', 0003, 0.09);
INSERT INTO `question` VALUES (1733399814198108161, 1086, 'e3cab', 0001, '驾驶机动车掉头、转弯、下陡坡时的最高速度不能超过每小时40公里。', NULL, '对', '错', '空', '空', '错', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过<b>每小时30公里</b>： <br>（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 <br><b>（二）掉头、转弯、下陡坡时；</b>　　 <br>（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 <br>（四）在冰雪、泥泞的道路上行驶时。 <br>题干描述错误，本题答案为错。', '', '最高速度', 0004, 0.19);
INSERT INTO `question` VALUES (1733399814244245505, 1087, '8cb88', 0001, '夜间驾驶机动车在窄路、窄桥会车时正确的做法是使用远光灯。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，<b>在窄路、窄桥与非机动车会车时应当使用近光灯。</b>', '', '会车|使用远光灯', 0003, 0.08);
INSERT INTO `question` VALUES (1733399814290382850, 1088, '3ec5b', 0002, '车辆涉水后，应保持低速行驶，怎样操作制动踏板，以恢复制动效果？ ', 'A、持续重踏 B、间断重踏 C、持续轻踏 D、间断轻踏', '持续重踏 ', '间断重踏 ', '持续轻踏 ', '间断轻踏', 'D', '车辆涉水后，擦干被水浸湿的部位，保持低速行驶，并<b>间断轻踩制动踏板</b>，以恢复制动效果。<br>【拓展知识】<br>间断轻踏：踩一下制动踏板，然后松开，再踩一下制动踏板，循环往复。这种方法适合路面摩擦系数低、涉水、爆胎可以间歇踩踏制动踏板以降低车速。<br>持续轻踏：慢慢的踩踏制动踏板，直到车辆完全停下来，并且这个踩踏的动作持续。这种方法适合路况比较好的路段。', '', '涉水|恢复制动效果', 0005, 0.35);
INSERT INTO `question` VALUES (1733399814353297410, 1089, '3bc19', 0002, '当驾驶员看到以下标志时，需减速慢行，是因为什么？ ', 'A、前方车行道或路面变窄 B、前方有弯道 C、前方车流量较大 D、前方有窄桥', '前方车行道或路面变窄 ', '前方有弯道 ', '前方车流量较大 ', '前方有窄桥', 'A', '<b>【右侧变窄】</b>警告标志，警告前方是右侧<b>车行道或路面变窄</b>的路段，遇来车应减速避让。', 'https://timg.mnks.cn/20230927d78f4d21.jpg', '减速慢行', 0002, 0.04);
INSERT INTO `question` VALUES (1733399814395240449, 1090, 'f8f05', 0002, '在山区道路遇对向来车时，应怎样会车？ ', 'A、不减速 B、紧靠道路中心 C、加速 D、减速或停车让行', '不减速 ', '紧靠道路中心 ', '加速 ', '减速或停车让行', 'D', '山区会车时，应尽量选择路面较宽的路段，并主动减速、选择安全路段减速或停车交会，不得加速或靠路中心行驶，防止发生剐碰事故。', '', '山区|对向来车', 0001, 0.01);
INSERT INTO `question` VALUES (1733399814437183489, 1091, '7d9ae', 0002, '驾驶机动车在高速公路上行驶，遇低能见度气象条件时，能见度在200米以下，车速不得超过每小时多少公里，与同车道前车至少保持多少米的距离？ ', 'A、60，100 B、70，100 C、40，80 D、30，80', '60，100 ', '70，100 ', '40，80 ', '30，80', 'A', '《道路交通安全法实施条例》第八十一条 机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：<br>(一)<b>能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；</b><br>(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；<br>(三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。<br>遇有前款规定情形时，高速公路管理部门应当通过显示屏等方式发布速度限制、保持车距等提示信息。', '', '200米以下', 0003, 0.14);
INSERT INTO `question` VALUES (1733399814479126530, 1092, '6d143', 0002, '如何通过这种交叉路口？ ', 'A、鸣笛催促 B、保持速度通过 C、减速慢行 D、加速通过', '鸣笛催促 ', '保持速度通过 ', '减速慢行 ', '加速通过', 'C', '驾驶机动车在没有交通信号的道路，<b>遇行人</b>横过道路，要<b>及时减速慢行或停车避让</b>。', 'https://timg.mnks.cn/20230927dc195fa9.jpg', '交叉路口', 0001, 0.02);
INSERT INTO `question` VALUES (1733399814546235393, 1093, '10b65', 0001, '如图所示，驾驶机动车遇左侧车道有车辆正在超车时，可以迅速变道，伺机反超。', NULL, '对', '错', '空', '空', '错', '驾驶机动车遇左侧车道有车辆正在超车时，应<b>减速或靠右停车避让</b>，千万不要开赌气车。', 'https://timg.mnks.cn/20230926d2977413.jpg', '正在超车|伺机', 0001, 0.03);
INSERT INTO `question` VALUES (1733399814596567041, 1094, '74417', 0001, '风、雨、雪、雾等复杂气象条件，遇前车速度较低时，应开启前照灯，连续鸣喇叭迅速超越。', NULL, '对', '错', '空', '空', '错', '风、雨、雪、雾等复杂气象条件，会对驾驶人的判断和操作产生不利的影响，应合理控制车速，保持足够的安全距离行驶，当遇前车速度较低时，<b>严禁</b>开启前照灯，连续鸣喇叭迅速超越。', '', '连续鸣喇叭', 0003, 0.09);
INSERT INTO `question` VALUES (1733399814634315777, 1095, 'f9331', 0001, '如图所示，驾驶机动车遇到这种情况，不仅要控制车辆留出会车空间，而且要注意与右侧的儿童保持足够的安全距离。', NULL, '对', '错', '空', '空', '对', '行车中，要注意礼让儿童，发现儿童在路边玩耍、嬉笑打闹时，应当减速慢行，随时注意观察儿童动态，发现异常要及时停车避让。如图所示路况，不但要控制车辆留出会车空间，还要注意与右侧的儿童保持足够的安全距离。', 'https://timg.mnks.cn/20230926ee010a79.jpg', '不仅|而且|足够的', 0001, 0.02);
INSERT INTO `question` VALUES (1733399814672064514, 1096, '6c961', 0002, '下长坡时，控制车速除了刹车制动以外还有什么有效的辅助方法？ ', 'A、挂入空挡滑行 B、利用发动机制动 C、踏下离合器滑行 D、关闭发动机熄火滑行', '挂入空挡滑行 ', '利用发动机制动 ', '踏下离合器滑行 ', '关闭发动机熄火滑行', 'B', '驾驶机动车下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降。控制车速最好的方法是使用低速挡行驶，充分<b>利用发动机制动减速</b>。', '', '下长坡|控制车速', 0003, 0.13);
INSERT INTO `question` VALUES (1733399814722396161, 1097, '30d2e', 0002, '遇到这种情形时，应怎么办？ ', 'A、从行人前方绕行 B、停车让行人先行 C、鸣喇叭提醒行人 D、从行人后方绕行', '从行人前方绕行 ', '停车让行人先行 ', '鸣喇叭提醒行人 ', '从行人后方绕行', 'B', '驾驶机动车遇到行人通过人行横道时，<b>应及时减速停车让行</b>，不得加速从行人两侧绕行通过。', 'https://timg.mnks.cn/20230927eb53711b.jpg', '怎么办', 0001, 0.01);
INSERT INTO `question` VALUES (1733399814776922114, 1098, '4f94f', 0001, '车辆行至交叉路口时，左转弯车辆在任何时段都可以进入左转弯待转区。', NULL, '对', '错', '空', '空', '错', '有左转弯待转区线的路口，应<b>在直行绿灯亮时</b>进入待转区等待放行信号。', '', '任何时段|左转弯待转区', 0003, 0.12);
INSERT INTO `question` VALUES (1733399814814670850, 1099, 'b0eb7', 0002, '怎样通过这样的路口？ ', 'A、不减速通过 B、加速尽快通过 C、空挡滑行通过 D、减速或停车观察', '不减速通过 ', '加速尽快通过 ', '空挡滑行通过 ', '减速或停车观察', 'D', '驾驶机动车通过无信号灯控制或无人看守的铁路道口时，要在道口外停车观察，做到一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）。', 'https://timg.mnks.cn/20230927c9628c08.jpg', '怎样通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399814890168321, 1100, '177f7', 0002, '如图所示，驾驶机动车在这种情况下，当C车减速让超车时，A车应该如何行驶？ ', 'A、放弃超越C车 B、加速超越C车 C、鸣喇叭示意B车让行后超车 D、直接向左变更车道，迫使B车让行', '放弃超越C车 ', '加速超越C车 ', '鸣喇叭示意B车让行后超车 ', '直接向左变更车道，迫使B车让行', 'A', '超车之前一定要观察好超车条件，能够保证万无一失再开始超越，以避免在超车时形成“三点一线”的状况，也就是说本车、被超车与对向来车千万不能在一条横向的直线上。一旦发生这种情况，被夹在两辆车中间完全没有躲避的条件，很容易发生剐碰事故。如图所示，虽然C车减速让超，但对向有B车，如果A车超车，ABC三车将会形成“三点一线”的状况非常危险，因此，在<b>不具备安全超车</b>的情况下，<b>A车应放弃超车</b>。', 'https://timg.mnks.cn/20230926c43a089d.jpg', '让超', 0003, 0.10);
INSERT INTO `question` VALUES (1733399814940499969, 1101, 'be050', 0002, '驶近一个图中所示的路口时，怎么做是正确的？ ', 'A、如果路口没有车辆和行人，就可以加速通过 B、只要不影响车辆和行人通行，就可以减速通过 C、在路口停车后向左转头观察，确认安全后通过 D、在路口减速后向左转头观察，确认安全后通过', '如果路口没有车辆和行人，就可以加速通过 ', '只要不影响车辆和行人通行，就可以减速通过 ', '在路口停车后向左转头观察，确认安全后通过 ', '在路口减速后向左转头观察，确认安全后通过', 'D', '直行通过没有信号灯，有<b>减速让行</b>标志的路口时，要注意减速避让正在通行的车辆和行人，随时做好停车的准备，即使有优先通行权，也不能忽视左侧对面行人有可能抢先横过马路。因此，通过此路口时要注意减速，向左观察，确认安全后通过。', 'https://timg.mnks.cn/20230927fc9b9b32.jpg', '正确', 0005, 0.40);
INSERT INTO `question` VALUES (1733399814990831617, 1102, 'bdf05', 0001, '机动车在路边道路临时停车时，可以暂时并列停放。', NULL, '对', '错', '空', '空', '错', '在道路上<b>临时停车</b>时，不得妨碍其他机动车和行人通行，应按指定的位置停放，<b>不得在道路两侧并列停放或逆向停放</b>。', '', '并列', 0003, 0.11);
INSERT INTO `question` VALUES (1733399815036968961, 1103, 'fd49d', 0002, '下长坡连续使用行车制动会导致什么？ ', 'A、会缩短发动机寿命 B、增加驾驶人的劳动强度 C、会使制动器温度升高而使制动效果急剧下降 D、容易造成车辆倾翻', '会缩短发动机寿命 ', '增加驾驶人的劳动强度 ', '会使制动器温度升高而使制动效果急剧下降 ', '容易造成车辆倾翻', 'C', '下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度会使<b>制动效果急剧下降</b>。', '', '下长坡|行车制动', 0004, 0.20);
INSERT INTO `question` VALUES (1733399815083106305, 1104, 'f41a2', 0001, '雨天路面湿滑，车辆制动距离增大，行车中尽量使用紧急制动减速。', NULL, '对', '错', '空', '空', '错', '雨天驾驶机动车在积水路面上高速行驶时，轮胎与路面间行成“水膜”，车辆的转向和制动将有失效的风险，车辆制动距离增大，行车中应松抬加速踏板，充分利用发动机制动减速，<b>切勿急转向或紧急制动</b>。因此，题干表述错误。', '', '路面湿滑|尽量使用', 0005, 0.35);
INSERT INTO `question` VALUES (1733399815125049345, 1105, '57f7c', 0002, '如图所示，夜间驾驶机动车与同方向行驶的前车距离较近时，以下做法正确的是什么？ ', 'A、使用远光灯，有利于观察路面情况 B、禁止使用远光灯，避免灯光照射至前车后视镜造成前车驾驶人眩目 C、使用远光灯，有利于告知前方驾驶人后方有来车 D、禁止使用远光灯，避免灯光照射至前车后视镜造成自己眩目', '使用远光灯，有利于观察路面情况 ', '禁止使用远光灯，避免灯光照射至前车后视镜造成前车驾驶人眩目 ', '使用远光灯，有利于告知前方驾驶人后方有来车 ', '禁止使用远光灯，避免灯光照射至前车后视镜造成自己眩目', 'B', '夜间驾驶机动车与同方向行驶的前车距离较近时，应使用近光灯，<b>禁止使用远光灯</b>，避免灯光照射至前车后视镜<b>造成前车驾驶人炫目</b>，影响行车安全。', 'https://timg.mnks.cn/20230926b615e69a.jpg', '距离较近', 0004, 0.19);
INSERT INTO `question` VALUES (1733399815179575297, 1106, 'a4203', 0002, '行车中遇到这种情况，应当怎样做？ ', 'A、提前变更到中间车道超越 B、提前变更到最左侧车道超越 C、停在校车后方使用灯光催促 D、适当鸣喇叭低速从左侧超越', '提前变更到中间车道超越 ', '提前变更到最左侧车道超越 ', '停在校车后方使用灯光催促 ', '适当鸣喇叭低速从左侧超越', 'B', '如图所示，<b>可以提前变更到最左侧车道超越</b>，因为校车后方及相邻车道的车都应停车等待。《校车安全管理条例》规定：校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越。<b>校车在同方向有两条以上机动车道的道路上停靠时</b>，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，<b>其他机动车道上的机动车应当减速通过</b>，校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促。<br>\n<p><img src=\"https://timg.mnks.cn/20231009b8a00fee.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>', 'https://timg.mnks.cn/20231101ebc444d5.jpg', '行车中', 0005, 0.40);
INSERT INTO `question` VALUES (1733399815217324034, 1107, '179be', 0002, '在这个路口左转弯选择哪条车道？ ', 'A、最左侧车道 B、中间车道 C、最右侧车道 D、不用变道', '最左侧车道 ', '中间车道 ', '最右侧车道 ', '不用变道', 'A', '路口左转弯应选择左转弯车道。因此，图示机动车应提前变更到<b>最左侧</b>导向箭头为<b>左转弯的车道</b>。', 'https://timg.mnks.cn/20230927bee22b3c.jpg', '左转弯', 0003, 0.10);
INSERT INTO `question` VALUES (1733399815267655681, 1108, 'cce31', 0001, '行经这种交通标线的路段要加速行驶。', NULL, '对', '错', '空', '空', '错', '要减速通过。路面上的<b>白色菱形图案</b>是<b>人行横道预告</b>标识线，提示车辆驾驶人注意道路前方设置了人行横道线，需提前<b>减速慢行</b>。', 'https://timg.mnks.cn/20230927abe984a4.jpg', '要加速', 0002, 0.04);
INSERT INTO `question` VALUES (1733399815305404418, 1109, 'c1cfb', 0002, '道路没有划分机动车道、非机动车道和人行道的，以下说法正确的是什么？ ', 'A、机动车在道路左侧通行，非机动车和行人随意通行 B、机动车在道路左侧通行，非机动车和行人在道路两侧通行 C、机动车在道路中间通行，非机动车和行人在道路两侧通行 D、机动车、非机动车和行人可随意通行', '机动车在道路左侧通行，非机动车和行人随意通行 ', '机动车在道路左侧通行，非机动车和行人在道路两侧通行 ', '机动车在道路中间通行，非机动车和行人在道路两侧通行 ', '机动车', 'C', '《道路交通安全法》第三十五条　机动车、非机动车实行右侧通行。第三十六条　根据道路要求和通行需要，道路区分为机动行车道、非机动行车道和人行道的，机动车、非机动车、路人实行分道通行。 <br><b>无区分机动行车道、非机动行车道和人行道的，机动车在道路中间通行，非机动车和路人在道路两侧通行</b>。', '', '没有划分', 0003, 0.12);
INSERT INTO `question` VALUES (1733399815347347458, 1110, '839d5', 0001, '驾驶机动车经过无划分车道的道路时，可以随意通行。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第三十六条：根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。<b>没有划分机动车道、非机动车道和人行道的，机动车在道路中间通行，非机动车和行人在道路两侧通行。</b> <br>因此，<b>随意通行做法错误</b>。', '', '随意通行', 0002, 0.05);
INSERT INTO `question` VALUES (1733399815385096193, 1111, 'd92c3', 0002, '车辆在较窄的山路上行驶时，如果靠山体的一方不让行，应怎样做？ ', 'A、向左占道，谨慎驶过 B、提前减速或停车避让 C、保持正常车速行驶 D、鸣喇叭催其让行', '向左占道，谨慎驶过 ', '提前减速或停车避让 ', '保持正常车速行驶 ', '鸣喇叭催其让行', 'B', '车辆在较窄的山路上行驶时，要尽量靠右侧低速行驶，遇靠山体一侧车辆不让行时，要提前减速并选择安全的地方停车避让。', '', '较窄|不让行', 0002, 0.05);
INSERT INTO `question` VALUES (1733399815448010754, 1112, '670a1', 0001, '专用车道规定的专用使用时间之外，其他车辆可以进入专用车道行驶。', NULL, '对', '错', '空', '空', '对', '在规定时间内，道路划设为专用车道的，在专用车道内，只准许规定的车辆通行，其他车辆不得进入专用车道内行驶；在规定时间<b>之外</b>，其他车辆<b>可以</b>行驶。', '', '规定|之外|可以', 0004, 0.24);
INSERT INTO `question` VALUES (1733399815489953793, 1113, 'f2b36', 0001, '浓雾天气能见度低，开启远光灯会提高能见度。', NULL, '对', '错', '空', '空', '错', '在雾较大时，可开启近光灯照明，以便看清前方路口，同时开启危险报警闪光灯，<b>不能使用远光灯</b>。<br>【拓展知识】<br><b>雾天不能使用远光灯</b>的原因：汽车远光灯的灯光是照向前上方的，射出的光线被雾气反射后，会在车前形成白茫茫的一片，这不但不能起到照明作用还会严重阻挡驾驶人视线、视野，当后面车辆使用远光灯时也<b>会严重影响前车驾驶人观察后方情况</b>，因此<b>雾天行车，<b>不能使用远光灯</b></b>。', '', '浓雾|远光灯', 0003, 0.11);
INSERT INTO `question` VALUES (1733399815536091138, 1114, 'b89c8', 0002, '在这个路口怎样左转弯？ ', 'A、靠路口中心点右侧转弯 B、靠路口中心点左侧转弯 C、不能左转弯 D、骑路口中心点转弯', '靠路口中心点右侧转弯 ', '靠路口中心点左侧转弯 ', '不能左转弯 ', '骑路口中心点转弯', 'B', '<b>左转通过路口，靠路口中心点左侧转弯</b>的法律依据如下： <br>根据《道路交通安全法》第五十一条　机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行： <br>(一)在划有导向车道的路口，按所需行进方向驶入导向车道； <br>(二)准备进入环形路口的让已在路口内的机动车先行； <br>(三)<b>向左转弯时，靠路口中心点左侧转弯</b>。转弯时开启转向灯，夜间行驶开启近光灯；', 'https://timg.mnks.cn/20230927fe24c89b.jpg', '左转弯', 0005, 0.35);
INSERT INTO `question` VALUES (1733399815573839874, 1115, '4580a', 0001, '驾驶机动车在路口右转弯时，应提前开启右转向灯，不受信号灯限制，不受车速限制，迅速通过，防止路口堵塞。', NULL, '对', '错', '空', '空', '错', '驾驶机动车在路口右转弯时，应提前开启右转向灯，注意观察后方和右转弯方向道路交通动态，同时观察右转弯的车辆和行人。右转弯遇圆形红灯亮时，可<b>减速靠右侧</b>转弯通过，但不应影响被放行方向的其他车辆和行人通行。<br><br>【拓展知识】<br>1，信号灯是圆形时，右转弯不受信号灯的限制，但不得影响被放行方向的其他车辆和行人通行；<br>2，信号灯是箭头形状时，红灯停，绿灯行。', '', '转弯|不受', 0003, 0.15);
INSERT INTO `question` VALUES (1733399815636754433, 1116, '1639c', 0001, '遇到这种情形时要停车避让行人。', NULL, '对', '错', '空', '空', '对', '根据《道路交通安全法》第四十七条，机动车行经没有交通信号的道路时，<b>遇行人横过道路，应当避让</b>。', 'https://timg.mnks.cn/20230927cc3daf85.jpg', '停车避让', 0001, 0.02);
INSERT INTO `question` VALUES (1733399815678697474, 1117, '03617', 0001, '车辆行至急转弯处时，应减速并靠右侧行驶，防止与越过弯道中心线的对方车辆相撞。', NULL, '对', '错', '空', '空', '对', '驾驶机动车<b>行至急转弯处时，要注意靠右侧减速慢行</b>，防止与越过弯道中心线的对方车辆相撞。', '', '急转弯|减速并靠右侧', 0001, 0.02);
INSERT INTO `question` VALUES (1733399815724834818, 1118, 'f4446', 0002, '驾驶机动车时接打电话容易引发事故，以下原因错误的是什么? ', 'A、单手握方向盘，对机动车控制力下降 B、驾驶人注意力不集中，不能及时判断危险 C、电话的信号会对汽车电子设备的运行造成干扰 D、驾驶人对路况观察不到位，容易导致操作失误', '单手握方向盘，对机动车控制力下降 ', '驾驶人注意力不集中，不能及时判断危险 ', '电话的信号会对汽车电子设备的运行造成干扰 ', '驾驶人对路况观察不到位，容易导致操作失误', 'C', '请注意！题干要求选出原因<b>错误</b>的选项。<br>驾驶机动车时接打电话容易引发事故，主要是因为接电话时单手握方向盘，对机动车控制力下降；驾驶人注意力不集中，不能及时判断危险；驾驶人对路况观察不到位，容易导致操作失误。<b>电话信号对汽车电子设备干扰很小，可忽略不计，不会引发事故。</b>因此是错误的说法。', '', '错误', 0004, 0.20);
INSERT INTO `question` VALUES (1733399815817109505, 1119, 'b65f5', 0002, '这两辆车发生追尾的主要原因是什么？ ', 'A、后车未与前车保持安全距离 B、后车超车时距离前车太近 C、前车采取制动过急 D、前车采取制动时没看后视镜', '后车未与前车保持安全距离 ', '后车超车时距离前车太近 ', '前车采取制动过急 ', '前车采取制动时没看后视镜', 'A', '如图所示，后车跟车太近，未与前车保持安全距离，导致追尾。驾驶机动车与同车道行驶的前车，注意保持足以采取紧急制动措施的安全距离。', 'https://timg.mnks.cn/20230926ec3add04.jpg', '追尾|原因', 0002, 0.08);
INSERT INTO `question` VALUES (1733399815871635457, 1120, 'e5ff7', 0001, '连续降雨天气，山区公路可能会出现路肩疏松和堤坡坍塌现象，行车时应选择道路中间坚实的路面，避免靠近路边行驶。', NULL, '对', '错', '空', '空', '对', '连续降雨天气，山区公路可能会出现路肩疏松和堤坡坍塌现象，道路泥泞、黏稠，行驶阻力大且车轮易滑转、侧滑。因此，<b>行车时应尽量选择道路中间平整、坚实或有车辙的路段通过</b>，避免靠近路边行驶，以免出现危险。', '', '山区公路|应选择|避免', 0001, 0.03);
INSERT INTO `question` VALUES (1733399815913578497, 1121, '42481', 0002, '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？ ', 'A、应减速观察水情，然后加速行驶通过 B、应停车察明水情，确认安全后，低速通过 C、应停车察明水情，确认安全后，快速通过 D、可随意通行', '应减速观察水情，然后加速行驶通过 ', '应停车察明水情，确认安全后，低速通过 ', '应停车察明水情，确认安全后，快速通过 ', '可随意通行', 'B', '机动车行经漫水路或者漫水桥时，应当停<b>车察明水情，确认安全后，挂低速挡匀速通过。</b>', 'https://timg.mnks.cn/20230926b0a10df4.jpg', '做法正确', 0002, 0.07);
INSERT INTO `question` VALUES (1733399816014241794, 1122, '059be', 0002, '在这种路口怎样进行掉头？ ', 'A、在人行横道上掉头 B、进入路口后掉头 C、从右侧车道掉头 D、从中心线虚线处掉头', '在人行横道上掉头 ', '进入路口后掉头 ', '从右侧车道掉头 ', '从中心线虚线处掉头', 'D', '路口左侧黄色虚实线，实线一侧禁止车辆越线或压线行驶，<b>虚线一侧准许</b>车辆暂时越线或转弯。路口信号灯绿灯亮时，在不影响过往车辆的情况下，可以<b>从中心线虚线处掉头</b>。', 'https://timg.mnks.cn/20230927dc95477c.jpg', '掉头', 0004, 0.17);
INSERT INTO `question` VALUES (1733399816072962050, 1123, 'ed08c', 0001, '驾驶机动车行经漫水路或者漫水桥时，应当停车察明水情，快速通过。', NULL, '对', '错', '空', '空', '错', '漫水路对安全行车的主要影响是路面被水覆盖，看不清水底路面情况，无法观察到暗坑和凸起的路面。如果<b>快速通过漫水路，溅起的水浪会阻挡驾驶人的视线</b>。所以，题干表述错误，应当停车察明水情，<b>低速通过</b>。', '', '快速通过', 0003, 0.14);
INSERT INTO `question` VALUES (1733399816119099393, 1124, 'c9b71', 0001, '雾天行车多使用喇叭可引起对方注意；听到对方车辆鸣喇叭，也应鸣喇叭回应。', NULL, '对', '错', '空', '空', '对', '雾天能见度低，驾驶人视线模糊，在道路上行车，可多使用喇叭引起对向车辆注意，<b>听到对向车辆鸣喇叭时，要鸣喇叭回应</b>，提示自己的行车位置。', '', '可引起|回应', 0002, 0.04);
INSERT INTO `question` VALUES (1733399816215568386, 1125, '65ddf', 0002, '遇到这种情况的路口，以下做法正确的是什么？ ', 'A、沿左侧车道掉头 B、该路口不能掉头 C、选择中间车道掉头 D、在路口内掉头', '沿左侧车道掉头 ', '该路口不能掉头 ', '选择中间车道掉头 ', '在路口内掉头', 'B', '如图所示，前方有<b>禁止掉头</b>标志，因此该路口<b>不能掉头</b>。根据《道路交通安全法实施条例》第四十九条规定，机动车在有<b>禁止掉头</b>或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 'https://timg.mnks.cn/20230927b5094a60.jpg', '做法正确', 0002, 0.08);
INSERT INTO `question` VALUES (1733399816278482946, 1126, '7c0a4', 0001, '驾驶机动车遇到前方道路拥堵时，可以借用无人通行的非机动车道行驶。', NULL, '对', '错', '空', '空', '错', '驾驶机动车遇到道路拥堵时，应按顺序排队等待，<b>不得借用</b>非机动车道行驶。', '', '可以借', 0002, 0.07);
INSERT INTO `question` VALUES (1733399816328814594, 1127, '3cae1', 0002, '驾驶机动车前，以下说法错误的是什么？ ', 'A、调整驾驶座椅，保证踩踏踏板舒适 B、调整安全带的松紧与高低 C、调整适合驾驶的方向盘位置 D、调整安全头枕高度，使头枕正对驾驶人的颈椎', '调整驾驶座椅，保证踩踏踏板舒适 ', '调整安全带的松紧与高低 ', '调整适合驾驶的方向盘位置 ', '调整安全头枕高度，使头枕正对驾驶人的颈椎', 'D', '起步前的调整：1、<b>调整座椅的前后位置</b>、靠背角度，保证踩踏踏板舒适：左脚能够轻松自如地控制离合器踏板，右脚能够自如地控制加速踏板和行车制动器踏板，<b>两手能自如地抓住转向盘上端</b>；<br>2、<b>调整头枕高度</b>：头枕顶部与头平齐，<b>头枕中央对准脑后部；</b><br>3、<b>调整安全带的松紧与高低</b>：系安全带时，先根据身高扳动安全带高度调整钮调整好高度，再用右手缓慢将安全带向下平顺拉出。', '', '说法错误', 0004, 0.20);
INSERT INTO `question` VALUES (1733399816370757634, 1128, '3d91c', 0001, '车辆长时间停放时，应选择停车场停车。', NULL, '对', '错', '空', '空', '对', '车辆停放时间较长时，选择停车场或准许长时间停放车辆的地点，在规定的位置内依次停放。车辆长期停放时，尽量停入车库。不准在车行道、人行道和设有禁止长时停放标志的地点停放。', '', '停车场', 0001, 0.01);
INSERT INTO `question` VALUES (1733399816416894978, 1129, 'd2071', 0001, '在冰雪路面上行车，必须降低车速、加大安全距离。', NULL, '对', '错', '空', '空', '对', '雪天驾驶，积雪路面行驶阻力增大，路面被积雪覆盖，驾驶人很难辨别方向。雪融化后会结成薄冰，路面非常滑，因此雪天及冰雪路行车，一定要加大安全距离，低速行驶。因此，题干表述正确。', '', '降低|加大', 0001, 0.02);
INSERT INTO `question` VALUES (1733399816475615234, 1130, '4fac5', 0001, '在这个路口可以掉头。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第四十九条：机动车在有禁止掉头或者<b>禁止左转弯标志</b>、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，<b>不得掉头</b>。', 'https://timg.mnks.cn/20230927b1794977.jpg', '可以', 0002, 0.04);
INSERT INTO `question` VALUES (1733399816513363969, 1131, 'd93ec', 0001, '如图所示，驾驶机动车行经该路段时，应减速慢行，避免因眩目导致的交通事故。', NULL, '对', '错', '空', '空', '对', '行经狭窄、黑暗的隧道时，驾驶人的眼睛会有一个明暗适应过程，因此，应合理使用灯光和减速慢行，避免因隧道外的炫目导致交通事故。', 'https://timg.mnks.cn/20230926f9b6c64b.jpg', '减速慢行', 0001, 0.02);
INSERT INTO `question` VALUES (1733399816551112705, 1132, 'd50e9', 0002, '驾驶机动车在下列哪种情形下不能超越前车？ ', 'A、前车减速让行 B、前车正在左转弯 C、前车靠边停车 D、前车正在右转弯', '前车减速让行 ', '前车正在左转弯 ', '前车靠边停车 ', '前车正在右转弯', 'B', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。<b>有下列情形之一的，不得超车</b>： <br><b>（一）前车正在左转弯、掉头、超车的； </b> <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '不能超越', 0004, 0.17);
INSERT INTO `question` VALUES (1733399816597250049, 1133, '71c33', 0001, '雪天行车中，在有车辙的路段应循车辙行驶。', NULL, '对', '错', '空', '空', '对', '当路面被积雪覆盖或道路轮廓难以辨别时，可根据路边树木、电线杆等参照物判断行驶路线，控制车辆低速行驶；<b>在有车辙的路段要循车辙低速行驶</b>。', '', '雪天|循车辙', 0002, 0.08);
INSERT INTO `question` VALUES (1733399816639193089, 1134, 'bffbd', 0001, '在这个路口不能掉头。', NULL, '对', '错', '空', '空', '错', '如图所示，在设置<b>允许掉头标志和标线</b>的路段，<b>可以</b>掉头。因此，题干表述错误。', 'https://timg.mnks.cn/20230927cf013252.jpg', '不能', 0002, 0.06);
INSERT INTO `question` VALUES (1733399816672747521, 1135, 'a302d', 0002, '驾驶机动车遇到前方低速行驶的洒水车作业时，以下做法错误的是什么？ ', 'A、注意避让 B、若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道 C、若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更 D、通过洒水车时应急加速通过', '注意避让 ', '若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道 ', '若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更 ', '通过洒水车时应急加速通过', 'D', '请注意，题干要求选出<b>做法错误</b>的选项。驾驶机动车遇到前方低速行驶的洒水车作业时，需要注意避让；若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道；若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更；不能加速通过。', '', '错误', 0004, 0.22);
INSERT INTO `question` VALUES (1733399816727273474, 1136, 'ad9e0', 0001, '车辆在冰雪路面紧急制动易产生侧滑，应低速行驶，可利用发动机制动进行减速。', NULL, '对', '错', '空', '空', '对', '车辆在冰雪路面紧急制动易产生侧滑，应低速行驶，<b>充分利用发动机牵阻作用减速或停车</b>，不能紧急制动和急转向。', '', '冰雪|发动机制动', 0002, 0.08);
INSERT INTO `question` VALUES (1733399816781799426, 1137, 'e80c3', 0001, '在这段道路上不能掉头。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第四十九条：机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、<b>急弯</b>、陡坡、隧道或者容易发生危险的路段，<b>不得掉头</b>。', 'https://timg.mnks.cn/20230927dd47be07.jpg', '不能', 0002, 0.06);
INSERT INTO `question` VALUES (1733399816827936769, 1138, '51ff9', 0002, '驾驶机动车在高速公路上遇到雨雪天气时，需要降低车速、保持安全距离的原因，以下说法错误的是什么？ ', 'A、能见度下降，驾驶人难以及时发现前方车辆 B、此类天气条件下的道路上，车辆的制动距离变长 C、为车辆安全行驶提供足够的安全距离 D、降低恶劣天气对车辆造成的损害', '能见度下降，驾驶人难以及时发现前方车辆 ', '此类天气条件下的道路上，车辆的制动距离变长 ', '为车辆安全行驶提供足够的安全距离 ', '降低恶劣天气对车辆造成的损害', 'D', '<b>请注意</b>，题干要求选出<b>说法错误</b>的选项。<br>驾驶机动车在高速公路上遇到雨雪天气时，<b>能见度下降，驾驶人难以及时发现前方车辆，在此类天气条件下的道路上，车辆的制动距离变长</b>，因此需要降低车速，为车辆安全行驶提供足够的安全距离。但<b>降低车速、保持安全距离，与降低恶劣天气对车辆造成的损害并无直接关系</b>。', '', '需要降低|说法错误', 0004, 0.20);
INSERT INTO `question` VALUES (1733399816869879809, 1139, 'f325f', 0001, '车辆通过学校和小区应注意观察标志标线，低速行驶，不要鸣喇叭。', NULL, '对', '错', '空', '空', '对', '驾驶机动车通过学校和居民小区时，一定要及时减速，注意观察道路两侧及周围的情况，遵守标志标线的规定，<b>低速行驶，不得鸣喇叭</b>和加速抢行。', '', '学校和小区|不要', 0002, 0.04);
INSERT INTO `question` VALUES (1733399816911822849, 1140, '6a566', 0001, '车辆行至泥泞或翻浆路段时，应停车观察，选择平整、坚实的路段缓慢通过。', NULL, '对', '错', '空', '空', '对', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。因此行至泥泞或翻浆路段时，<b>要停车察看路况，尽量选择平整、坚实或有车辙的路段通过</b>。一般选用中低速档位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防整车滑移。', '', '停车观察|缓慢通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399816949571585, 1141, 'b5fe8', 0002, '假如您在高速公路上不小心错过了准备驶出的路口，正确的操作应该是？ ', 'A、紧急刹车，倒车至想要驶出的路口 B、继续前行，到下一出口驶离高速公路掉头 C、在应急停车道上停车，等待车辆较少的时候再伺机倒车 D、借用应急停车道进行掉头，逆向行驶', '紧急刹车，倒车至想要驶出的路口 ', '继续前行，到下一出口驶离高速公路掉头 ', '在应急停车道上停车，等待车辆较少的时候再伺机倒车 ', '借用应急停车道进行掉头，逆向行驶', 'B', '高速公路行车注意事项：如果<b>因疏忽驶过出口</b>，<b>只能继续向前行驶</b>至立交桥掉头或在下一出口驶出高速公路后掉头，<b>不得紧急制动</b>；即便是刚刚错过出口且距下一出口很远，也<b>不得掉头逆行或倒回出口</b>。', '', '高速公路|错过', 0001, 0.03);
INSERT INTO `question` VALUES (1733399816987320321, 1142, '0eec6', 0002, '在没有中心线的道路上发现后车发出超车信号时，如果条件许可如何行驶？ ', 'A、保持原状态行驶 B、加速行驶 C、迅速停车让行 D、降速靠右让路', '保持原状态行驶 ', '加速行驶 ', '迅速停车让行 ', '降速靠右让路', 'D', '驾驶机动车在没有中心线的道路上发现后车发出超车信号时，若具备让车条件，应及时开启右转向灯，<b>减速靠右让行</b>，必要时辅以手势示意让超，不得故意不让或让路不让速。', '', '没有中心线|超车信号', 0004, 0.16);
INSERT INTO `question` VALUES (1733399817029263362, 1143, 'ee6ef', 0002, '驾驶车辆进入高速公路加速车道后，须尽快将车速提高到每小时60公里以上的原因是什么？ ', 'A、以防被其他车辆超过 B、以防后方车辆发生追尾事故 C、以防汇入车流时影响主线车道上行驶的车辆 D、以防违反最低限速要求受到处罚', '以防被其他车辆超过 ', '以防后方车辆发生追尾事故 ', '以防汇入车流时影响主线车道上行驶的车辆 ', '以防违反最低限速要求受到处罚', 'C', '《道路交通安全法实施条例》第七十八条：<b>高速公路</b>应当标明车道的行驶速度，最高车速不得超过每小时120公里，<b>最低车速不得低于每小时60公里</b>。 <br>因此，驾驶机动车从匝道<b>驶入加速车道后</b>，<b>尽快将车速提高到60公里/小时以上</b>，<b>以防汇入车流时影响主线车道上行驶的车辆</b>，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机，不得在加速车道内紧急制动或停车。', '', '加速车道|须尽快', 0004, 0.20);
INSERT INTO `question` VALUES (1733399817071206401, 1144, 'bb20e', 0001, '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', NULL, '对', '错', '空', '空', '对', '大雨天行车，路面湿滑，车轮的附着力随车速的增加急剧变小，车辆很容易发生侧滑，当车辆高速行驶时，易发生“水滑”现象。因此，避免“水滑”而造成的危险，应当<b>合理控制车速，减速行驶</b>。', '', '水滑|控制速度', 0001, 0.02);
INSERT INTO `question` VALUES (1733399817113149442, 1145, '1095e', 0002, '在高速公路上开车遇到图中所示的情况时，以下操作不正确的是什么？ ', 'A、应该打开雾灯、近光灯、示廓灯、前后位灯，危险报警灯光 B、能见度低，应该与同车道前车间距保持一定距离 C、降低车速，防止紧急情况下无法及时制动 D、继续维持高速行驶，防止后面车辆堵塞', '应该打开雾灯', '近光灯', '示廓灯', '前后位灯，危险报警灯光 B', 'D', '如图所示，浓雾天行车应开启雾灯、近光灯、示廓灯以便看清前方路况及让其他车辆驾驶人发现自己，同时开启危险报警闪光灯，不能使用远光灯。将车速控制在能及时停车的范围内，与同车道前车保持足够的安全距离，谨慎缓慢行驶。<br>请注意：题干要求选出操作不正确的选项，因此<b>继续维持高速行驶，做法错误</b>。', 'https://timg.mnks.cn/20230925bca58148.jpg', '不正确', 0004, 0.25);
INSERT INTO `question` VALUES (1733399817201229826, 1146, '0beac', 0001, '在这段道路上，只要不影响其他车辆通行的前提下可以掉头。', NULL, '对', '错', '空', '空', '对', '黄线用来区分不同方向的车道，一般画在马路正中，好像一条隔离带，把马路隔成两个方向： 1、无论单黄线还是双黄线，看到实线=禁止跨越，比如超车、转弯、掉头等。 <b>2、看到虚线=可越（在保证安全的情况下）。</b>', 'https://timg.mnks.cn/20230927ee562d5d.jpg', '可以', 0003, 0.13);
INSERT INTO `question` VALUES (1733399817247367169, 1147, '7e6e9', 0001, '行人参与道路交通的特点除了行走随意性大、方向多变以外，还喜欢聚集、围观。', NULL, '对', '错', '空', '空', '对', '在行人参与道路交通的时候，往往具有行走随意性大、方向多变的主要特点，同时在行人参与道路交通的时候还喜欢聚集、围观在一起，这也属于行人参与道路交通的特点之一。', '', '行人', 0002, 0.06);
INSERT INTO `question` VALUES (1733399817280921601, 1148, '1bac1', 0002, '雪天在高速公路上驾驶时，关于安全车距错误的说法是什么？ ', 'A、雪天路滑，制动距离比干燥柏油路更长 B、雪天能见度低，应该根据能见度控制安全距离 C、能见度小于200m时，与前车至少保持50m的安全距离 D、能见度小于50m时，应该驶离高速公路', '雪天路滑，制动距离比干燥柏油路更长 ', '雪天能见度低，应该根据能见度控制安全距离 ', '能见度小于200m时，与前车至少保持50m的安全距离 ', '能见度小于50m时，应该驶离高速公路', 'C', '<b>请注意，题干要求选出说法错误的选项。</b>雪天在高速公路上驾驶时，积雪路面行驶阻力增大，路面被积雪覆盖，驾驶人很难辨别方向，应<b>根据能见度控制安全距离</b>，<b>低速行驶</b>。 <br>《道路交通安全法实施条例》第八十一条 机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定： <br>(一)<b>能见度小于200米时</b>，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，<b>与同车道前车保持100米以上的距离</b>； <br>(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离； <br>(三)<b>能见度小于50米时</b>，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口<b>尽快驶离</b>高速公路。 <br>综上，“能见度200m时，与前车保持50m的安全距离”<b>说法错误</b>。', '', '错误的说法', 0005, 0.30);
INSERT INTO `question` VALUES (1733399817327058946, 1149, '7b626', 0002, '前轮胎爆裂已出现转向时，驾驶人不要过度矫正，应在控制住方向的情况下，应怎样做，使车辆缓慢减速？ ', 'A、采取紧急制动 B、使用驻车制动 C、轻踏制动踏板 D、迅速踏下制动踏板', '采取紧急制动 ', '使用驻车制动 ', '轻踏制动踏板 ', '迅速踏下制动踏板', 'C', '前轮胎爆裂已出现方向偏移时，双手要稳握转向盘、适度地修正行驶方向，尽量按既定路线行驶，<b>轻踏制动踏板减速</b>，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或倾翻。<br>【拓展知识】<br>后轮爆胎时，要先控制行驶方向并平缓减速。', '', '前轮胎爆裂|缓慢减速', 0004, 0.20);
INSERT INTO `question` VALUES (1733399817364807681, 1150, 'c1701', 0002, '如图所示，在这段无限速标志和标线的城市道路上行驶的最高速度不能超过多少？ ', 'A、70公里每小时 B、50公里每小时 C、30公里每小时 D、40公里每小时', '70公里每小时 ', '50公里每小时 ', '30公里每小时 ', '40公里每小时', 'C', '如图所示，驾驶机动车在<b>没有限速标志和标线的城市道路</b>，行驶速度<b>不得超过30公里/小时</b>。根据《道路交通安全法实施条例》中规定：第四十五条 机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： <br>（一）<b>没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；</b> 【城3公4】<br>（二）同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。【城5公7】', 'https://timg.mnks.cn/20230926a2cab35c.jpg', '无|城市道路|最高速度', 0003, 0.10);
INSERT INTO `question` VALUES (1733399817406750722, 1151, '68fb5', 0002, '驾驶机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，能见度在50米以下时，以下做法正确的是什么？ ', 'A、加速驶离高速公路 B、在应急车道上停车等待 C、可以继续行驶，但车速不得超过每小时40公里 D、以不超过每小时20公里的车速从最近的出口尽快驶离高速公路', '加速驶离高速公路 ', '在应急车道上停车等待 ', '可以继续行驶，但车速不得超过每小时40公里 ', '以不超过每小时20公里的车速从最近的出口尽快驶离高速公路', 'D', '《道路交通安全法》规定：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', '', '50米以下', 0003, 0.12);
INSERT INTO `question` VALUES (1733399817444499458, 1152, '7c0be', 0001, '在高速公路上长期骑、轧车行道分界线行驶，会同时占用两个车道，导致后方车辆行驶困难，易引发交通事故。', NULL, '对', '错', '空', '空', '对', '高速公路行车不得随意穿行越线，不准骑、轧车行道分界线，不得占用应急车道。机动车同时占用两个车道行驶，会导致后方车辆行驶困难，从而引发交通事故。', '', '易引发', 0001, 0.02);
INSERT INTO `question` VALUES (1733399817545162753, 1153, 'cd94f', 0002, '驶入高速公路的收费口时，应选择怎样的入口？ ', 'A、车辆多 B、红灯亮 C、暂停服务 D、绿灯亮', '车辆多 ', '红灯亮 ', '暂停服务 ', '绿灯亮', 'D', '驾驶机动车驶入高速公路收费站时，应严格遵守限速规定，<b>选择通道上方绿色灯亮且车辆较少的收费口</b>，依次排队通过，切勿争道抢行。', '', '高速|入口', 0001, 0.03);
INSERT INTO `question` VALUES (1733399817633243138, 1154, 'ba719', 0002, '轮胎气压过低时，高速行驶轮胎会出现波浪变形温度升高而导致什么？ ', 'A、气压不稳 B、气压更低 C、行驶阻力增大 D、爆胎', '气压不稳 ', '气压更低 ', '行驶阻力增大 ', '爆胎', 'D', '车辆<b>高速行驶</b>时，若<b>轮胎气压过低</b>，会出现波浪变形、温度升高极易<b>导致爆胎</b>。', '', '轮胎气压过低', 0003, 0.12);
INSERT INTO `question` VALUES (1733399817691963393, 1155, 'fd52c', 0001, '下列做法是否正确？', NULL, '对', '错', '空', '空', '错', '<b>1、小型汽车不得作为牵引车；2、故障车不得在最左侧快车道行驶。</b><br>根据《道路交通安全法实施条例》的有关规定，机动车在高速公路上发生故障或者交通事故，<b>无法正常行驶的</b>，应当由<b>救援车、清障车</b>拖曳、牵引，牵引车和被牵引车均应当开启危险报警闪光灯，<b>在最右侧慢车道行驶</b>。', 'https://timg.mnks.cn/20230926d24c7350.jpg', '正确', 0002, 0.07);
INSERT INTO `question` VALUES (1733399817733906434, 1156, 'a1b06', 0002, '这种情况超车时，从前车的哪一侧超越？ ', 'A、从前车的右侧超越 B、左右两侧均可超越 C、从前车的左侧超越 D、从无障碍一侧超越', '从前车的右侧超越 ', '左右两侧均可超越 ', '从前车的左侧超越 ', '从无障碍一侧超越', 'C', '我国规定，机动车靠右行驶，且车辆均为左舵车，所以右侧盲区相对较大，从而容易与从右侧超越的车辆发生碰撞。正因存在这样的安全隐患，我国交通法禁止右侧超车的行为。<br><b>正确的超车方式，一定要从左侧进行</b>，这样后车只需要在往左偏离车道最小的状况下，驾驶员就能观察到前方是否可以超车，更能确保安全。', 'https://timg.mnks.cn/20230926b4cb49e8.jpg', '超车|哪一侧', 0002, 0.08);
INSERT INTO `question` VALUES (1733399817792626689, 1157, '28956', 0002, '避免爆胎的错误做法是什么？ ', 'A、降低轮胎气压 B、定期检查轮胎 C、及时清理轮胎沟槽里的异物 D、更换掉有裂纹或有很深损伤的轮胎', '降低轮胎气压 ', '定期检查轮胎 ', '及时清理轮胎沟槽里的异物 ', '更换掉有裂纹或有很深损伤的轮胎', 'A', '请注意，题干要求<b>选出做法错误的选项</b>。降低轮胎气压不仅不能避免爆胎，还会增加爆胎的风险。避免爆胎的正确做法是定期检查轮胎，及时清理轮胎沟槽里的异物，更换有裂纹或有很深损伤的轮胎。<br>【拓展知识】<br>当轮胎气压降低，轮胎与地面的摩擦成倍增加，胎温急剧升高，轮胎变软，强度急剧下降。这种情况下，如果车辆高速行驶，就可能导致爆胎。而如果车辆在低速行驶，也会伤胎，而且潜伏期长，隐蔽性高，危害性更大，会埋下日后爆胎的隐患。', '', '错误做法', 0003, 0.15);
INSERT INTO `question` VALUES (1733399817834569730, 1158, 'ad5a8', 0001, '驾驶人发现轮胎漏气，将车辆驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', NULL, '对', '错', '空', '空', '对', '车辆高速行驶时，若轮胎气压过低，会出现波浪变形、温度升高极易导致爆胎。 <br>1、<b>发现轮胎漏气或气压过低时，要平稳制动减速，尽快驶离主车道，不要紧急制动，以免造成翻车或后车制动不及时导致追尾事故。</b> <br>2、行驶当中遇到突然爆胎，双手要紧握转向盘控制住方向、松抬制动踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', '', '漏气|不要|以免', 0001, 0.03);
INSERT INTO `question` VALUES (1733399817868124162, 1159, '0f24d', 0002, '驾驶车辆进入高速公路加速车道后，应尽快将车速提高到每小时多少公里以上？ ', 'A、30 B、40 C、50 D、60', '30 ', '40 ', '50 ', '60', 'D', '《道路交通安全法实施条例》第七十八条：<b>高速公路</b>应当标明车道的行驶速度，最高车速不得超过每小时120公里，<b>最低车速不得低于每小时60公里</b>。 <br>因此，驾驶机动车从匝道<b>驶入加速车道后</b>，要开启左转向灯，<b>尽快将车速提高到60公里/小时以上</b>，以防汇入车流时影响主线车道上行驶的车辆，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机。不得在加速车道内紧急制动或停车。', '', '加速车道', 0003, 0.11);
INSERT INTO `question` VALUES (1733399817905872897, 1160, 'f2cf0', 0001, '车辆后轮胎爆裂，车尾会摇摆不定，驾驶人应双手紧握转向盘，控制车辆保持直线行驶，减速停车。', NULL, '对', '错', '空', '空', '对', '车辆后轮爆胎时，<b>双手要先紧握转向盘控制行驶方向</b>，保持直线行驶，并平缓减速，直至平稳停车。', '', '轮胎爆裂|紧握转向盘|直线行驶', 0001, 0.03);
INSERT INTO `question` VALUES (1733399817947815937, 1161, 'f33f8', 0002, '对驾驶过程中接打手机的看法正确的是？ ', 'A、开车过程中不主动打电话，但是有重要电话打进来是可以边开车边接听手持电话的 B、根据驾龄和驾车技术，经验丰富的驾驶人可以在驾驶过程中接打手持电话 C、在车流量不大的道路上驾驶时，短时接听手持电话是可以的 D、开车需要接打电话时，应该先找到安全的地方停车再操作', '开车过程中不主动打电话，但是有重要电话打进来是可以边开车边接听手持电话的 ', '根据驾龄和驾车技术，经验丰富的驾驶人可以在驾驶过程中接打手持电话 ', '在车流量不大的道路上驾驶时，短时接听手持电话是可以的 ', '开车需要接打电话时，应该先找到安全的地方停车再操作', 'D', '驾驶过程中接打手机，是一种妨碍安全驾驶的行为，违背了安全驾驶的“集中注意力”这个黄金原则，驾驶人无法专注于观察和判断道路上的交通情况，对安全行车非常不利。因此，当开车需要接打电话时，应先<b>找到安全的地方停车后再操作</b>。', '', '接打手机', 0002, 0.05);
INSERT INTO `question` VALUES (1733399817985564674, 1162, 'ebd1c', 0001, '行车中当驾驶人意识到爆胎时，应在控制住方向的情况下，轻踏制动踏板，使车辆缓慢减速，逐渐平稳地停靠于路边。', NULL, '对', '错', '空', '空', '对', '行车中驾驶人意识到爆胎时，应双手紧握转向盘控制住方向，平缓的制动减速，尽快驶离主车道直至平稳地靠边停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', '', '爆胎|轻踏制动', 0001, 0.03);
INSERT INTO `question` VALUES (1733399818031702018, 1163, 'bae07', 0001, '如图所示，驾驶机动车时，前风窗玻璃处悬挂放置干扰视线的物品是错误的。', NULL, '对', '错', '空', '空', '对', '如图所示，在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品，属于驾驶机动车的禁止行为之一，因此题干表述正确。', 'https://timg.mnks.cn/20230926fba308f8.jpg', '错误', 0002, 0.07);
INSERT INTO `question` VALUES (1733399818094616577, 1164, '4d1d3', 0002, '蓝色车辆遇到图中的情形时，下列做法正确的是？ ', 'A、按照前方交通信号灯指示直接通行 B、鸣喇叭提醒，让学生队伍中空出一个缺口，从缺口中穿行过去 C、停车等待，直到学生队伍完全通过 D、鸣喇叭，催促还未通过的学生加快速度通过', '按照前方交通信号灯指示直接通行 ', '鸣喇叭提醒，让学生队伍中空出一个缺口，从缺口中穿行过去 ', '停车等待，直到学生队伍完全通过 ', '鸣喇叭，催促还未通过的学生加快速度通过', 'C', '按规定，驾驶机动车遇到人行横道上有行人通过时需要停车等待。行人的流动很随意，需要谨慎对待。', 'https://timg.mnks.cn/20230926d6d7aeda.jpg', '蓝色|做法正确', 0001, 0.02);
INSERT INTO `question` VALUES (1733399818140753921, 1165, 'b8955', 0002, '同车道行驶的车辆遇前车有下列哪种情形时不得超车？ ', 'A、正在停车 B、减速让行 C、正常行驶 D、正在超车', '正在停车 ', '减速让行 ', '正常行驶 ', '正在超车', 'D', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>： <br><b>（一）前车正在左转弯、掉头、超车的；</b> <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '遇前车|不得超车', 0003, 0.12);
INSERT INTO `question` VALUES (1733399818195279873, 1166, '1e918', 0002, '驾驶车辆时在道路上抛撒物品，以下说法不正确的是？ ', 'A、抛撒纸张等轻质物品会阻挡驾驶人视线，分散驾驶人注意力 B、有可能引起其他驾驶人紧急躲避等应激反应，进而引发事故 C、破坏环境，影响环境整洁，甚至造成路面的损坏 D、保持车内整洁，减少燃油消耗', '抛撒纸张等轻质物品会阻挡驾驶人视线，分散驾驶人注意力 ', '有可能引起其他驾驶人紧急躲避等应激反应，进而引发事故 ', '破坏环境，影响环境整洁，甚至造成路面的损坏 ', '保持车内整洁，减少燃油消耗', 'D', '驾驶车辆时在道路上抛撒物品，容易阻挡驾驶员视线、引起其他驾驶人紧急躲避等应激反应，也会污染环境造成路面损坏。与车内卫生和燃油消耗并无直接影响，因此不正确。', '', '不正确', 0003, 0.14);
INSERT INTO `question` VALUES (1733399818241417217, 1167, '21535', 0002, '下面关于下坡熄火滑行的说法错误的是？ ', 'A、对于采用真空助力刹车系统的车辆而言，下坡时的熄火会使刹车系统失效 B、对于采用了助力转向系统的车辆而言，下坡时熄火会使转向盘变重，难以控制 C、下坡道熄火时，车辆不能使用发动机制动 D、下坡滑行是利用坡道的位能推动汽车前进，发动机不工作，可以节油，应大力提倡', '对于采用真空助力刹车系统的车辆而言，下坡时的熄火会使刹车系统失效 ', '对于采用了助力转向系统的车辆而言，下坡时熄火会使转向盘变重，难以控制 ', '下坡道熄火时，车辆不能使用发动机制动 ', '下坡滑行是利用坡道的位能推动汽车前进，发动机不工作，可以节油，应大力提倡', 'D', '<b>请注意，题干要求选出【说法错误】的选项。</b>空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很<b>容易引发严重的交通事故</b>。所以，下坡空挡滑行不省油而且危险大，<b>不应该</b>大力提倡。', '', '熄火滑行|错误', 0003, 0.14);
INSERT INTO `question` VALUES (1733399818279165954, 1168, '5ef26', 0001, '行车中当车辆突然爆胎时，驾驶人切忌慌乱中急踏制动踏板，尽量采用“抢挡”的方法，利用发动机制动使车辆减速。', NULL, '对', '错', '空', '空', '对', '行驶当中遇到突然爆胎，驾驶人采取抢挂低速挡的方法，利用发动机制动使车辆缓慢减速，<b>切忌猛打转向盘和紧急制动</b>，否则容易造成车辆侧滑或侧翻。', '', '突然爆胎|抢挡|发动机制动', 0003, 0.12);
INSERT INTO `question` VALUES (1733399818321108993, 1169, 'e277d', 0001, '驾驶机动车下长坡时，仅靠行车制动器制动，容易引起行车制动器失灵。', NULL, '对', '错', '空', '空', '对', '长下坡时，车速会因为惯性而越来越快，连续使用行车制动，会因制动器温度升高而使制动效果急剧下降，严重的会引起行车制动器失灵，丧失制动能力。', '', '下长坡|失灵', 0002, 0.05);
INSERT INTO `question` VALUES (1733399818371440642, 1170, '9bf4e', 0001, '如图所示，当机动车行驶至交叉口时的做法是正确的。', NULL, '对', '错', '空', '空', '对', '如图所示，A车行驶在<b>右转弯车道</b>，且信号灯为绿灯，因此，A车<b>开启右转向灯</b>，进行<b>右转弯行驶</b>的做法正确。', 'https://timg.mnks.cn/20230926ffb1eb2d.jpg', '交叉口', 0002, 0.08);
INSERT INTO `question` VALUES (1733399818413383682, 1171, 'dc354', 0002, '指示标志的作用是什么？ ', 'A、限制车辆、行人通行 B、指示车辆、行人行进 C、告知方向信息 D、警告前方危险', '限制车辆', '行人通行 B', '指示车辆', '行人行进 C', 'B', '<b>指示</b>标志的作用是<b>指示</b>车辆、行人按标志<b>指示</b>的路线、方向行驶，道路使用者应遵循。', '', '指示', 0004, 0.17);
INSERT INTO `question` VALUES (1733399818455326722, 1172, '476d1', 0001, '车辆后备箱门未关好，是可以上路行驶的。', NULL, '对', '错', '空', '空', '错', '驾驶机动车<b>禁止</b>在车门、车厢没有关好时行车。', 'https://timg.mnks.cn/20230926f414b7d5.jpg', '未关好|可以', 0001, 0.02);
INSERT INTO `question` VALUES (1733399818497269761, 1173, '29a0f', 0002, '高速公路上行车，如果因疏忽驶过出口，应怎样做？ ', 'A、在原地倒车驶回 B、继续向前行驶，寻找下一个出口 C、立即停车 D、在原地掉头', '在原地倒车驶回 ', '继续向前行驶，寻找下一个出口 ', '立即停车 ', '在原地掉头', 'B', '高速公路行车注意事项：如果<b>因疏忽驶过出口</b>，<b>只能继续向前行驶</b>至立交桥掉头或在下一出口驶出高速公路后掉头，<b>不得紧急制动</b>；即便是刚刚错过出口且距下一出口很远，也<b>不得掉头逆行或倒回出口</b>。', '', '驶过出口', 0001, 0.01);
INSERT INTO `question` VALUES (1733399818581155842, 1174, 'aaddb', 0001, '车辆前轮胎爆裂，危险较大，方向会立刻向爆胎车轮一侧跑偏，直接影响驾驶人对转向盘的控制。', NULL, '对', '错', '空', '空', '对', '车辆高速行驶时，若轮胎气压过低，会出现波浪变形、温度升高极易导致爆胎。 <br>1、发现轮胎漏气或气压过低时，要平稳制动减速，尽快驶离主车道，不要紧急制动，以免造成翻车或后车制动不及时导致追尾事故。 <br>2、行驶当中遇到突然爆胎，双手要紧握转向盘控制住方向、松抬制动踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。 <br>3、后轮爆胎时，要先控制行驶方向并平缓减速；<b>前轮爆胎时，危险较大，一定要极力控制转向盘</b>，在控制住行驶方向后，采取抢挂低速挡的方法减速停车。前轮爆裂已出现转向困难时，驾驶人不要过度矫正，应在控制住方向的情况下，轻踏制动踏板，使车辆缓慢减速。 <br>4、避免爆胎的正确做法是定期检查轮胎，及时清理轮胎沟槽里的异物，更换有裂纹或有很深损伤的轮胎。', '', '前轮胎爆裂', 0001, 0.03);
INSERT INTO `question` VALUES (1733399818648264706, 1175, 'a4085', 0001, '驾驶机动车在行经交通流量大的路段，只要不是红灯就可以随时超车。', NULL, '对', '错', '空', '空', '错', '驾驶机动车在<b>行经交通流量大的路段，不得超车</b>。根据《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>：<br>（一）前车正在左转弯、掉头、超车的；<br>（二）与对面来车有会车可能的；<br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；<br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、<b>市区交通流量大的路段</b>等没有超车条件的。', '', '交通流量大|随时', 0001, 0.03);
INSERT INTO `question` VALUES (1733399818686013442, 1176, '6a3e7', 0001, '图中深色车辆在该地点临时停车是可以的。', NULL, '对', '错', '空', '空', '错', '机动车<b>不得在</b>人行横道上停车，因此图中深色车辆临时停车是错误的，不可以的。', 'https://timg.mnks.cn/20230927c853f6f8.jpg', '临时停车', 0001, 0.02);
INSERT INTO `question` VALUES (1733399818727956482, 1177, '9fe2a', 0002, '同车道行驶的车辆遇前车有下列哪种情形时不得超车？ ', 'A、正在停车 B、减速让行 C、正在掉头 D、正常行驶', '正在停车 ', '减速让行 ', '正在掉头 ', '正常行驶', 'C', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： <br><b>（一）前车正在左转弯、掉头、超车的；</b> <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '遇前车|不得超车', 0003, 0.11);
INSERT INTO `question` VALUES (1733399818769899522, 1178, '48e87', 0001, '车辆发生爆胎后，驾驶人在尚未控制住车速前，不要冒险使用行车制动器停车，以避免车辆横甩发生更大的险情。', NULL, '对', '错', '空', '空', '对', '行驶中遇到突然爆胎时，驾驶人要双手要紧握转向盘<b>控制住方向</b>、松抬加速踏板，尽量按既定路线行驶，然后用力间断轻踩制动踏板减速，直至平稳停车。<b>爆胎瞬间切忌猛打转向盘和紧急制动</b>，否则容易造成车辆侧滑或侧翻。', '', '爆胎|不要|行车制动器', 0001, 0.02);
INSERT INTO `question` VALUES (1733399818820231169, 1179, 'f3685', 0001, '图中标注车辆在该地点停车是可以的。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第六十三条，机动车在道路上<b>临时停车</b>，应当遵守下列规定：<br>(三)公共汽车站、急救站、<b>加油站</b>、消防栓或者消防队(站)门前以及距离上述地点<b>30米以内的路段</b>，除使用上述设施的以外，<b>不得停车</b>；', 'https://timg.mnks.cn/20230927bfbd22f4.jpg', '停车', 0001, 0.03);
INSERT INTO `question` VALUES (1733399818866368514, 1180, 'c5599', 0001, '图中小型汽车的停车地点是正确的。', NULL, '对', '错', '空', '空', '错', '<b>图中小型汽车停在消防车停车位的做法是错误的</b>。根据《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：(三)公共汽车站、急救站、加油站、消防栓或者<b>消防队(站)门前</b>以及距离上述地点30米以内的路段，除使用上述设施的以外，<b>不得停车</b>。', 'https://timg.mnks.cn/20230927f13aee74.jpg', '正确', 0001, 0.03);
INSERT INTO `question` VALUES (1733399818916700162, 1181, 'd6c6e', 0001, '关门时，将车门拉至与车门框约10cm左右，再适当用力。', NULL, '对', '错', '空', '空', '对', '下车前，首先应通过内外后视镜观察，车辆左后方道路交通情况，并向左方侧头观察，确定前后方无车辆、行人靠近，准备开车门，先将车门稍微打开约10cm左右，再次侧头观察后方，确定安全后再开大车门，按左腿、臀部、右腿的顺序下车。<b>下车后，应及时关闭车门，先将车门关到车门接近车身约10cm时，再用力将车门关闭并锁好</b>。', '', '10cm', 0002, 0.06);
INSERT INTO `question` VALUES (1733399818962837506, 1182, 'fa045', 0001, '行驶过程中发现车门未关好，应及时关闭车门，否则车辆在转弯等激烈运动过程中会造成人员或货物被甩到车外。', NULL, '对', '错', '空', '空', '对', '驾驶机动车禁止行为：1、在车门、车厢没有关好时行车；因此，行驶过程中发现车门未关好，应及时关闭车门，否则车辆在转弯等激烈运动过程中会造成人员或货物被甩到车外，发生事故。', '', '及时关闭', 0001, 0.02);
INSERT INTO `question` VALUES (1733399819000586242, 1183, '8ba9a', 0001, '如图所示，A车在此处临时停车是可以的。', NULL, '对', '错', '空', '空', '错', '<b>交叉路口</b>、铁道路口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点<b>50米以内的路段，不得停车</b>。图上的车离路口只有30米，是不能在此停车的。', 'https://timg.mnks.cn/20230927fa116744.jpg', '临时停车', 0004, 0.17);
INSERT INTO `question` VALUES (1733399819046723585, 1184, 'fd426', 0001, '长时间在高速公路上行驶，容易导致驾驶人对车速的判断出现偏差，往往会低估车辆的行驶速度。', NULL, '对', '错', '空', '空', '对', '高速公路长时间行车，驾驶人对车速的判断会有偏差，即“速度错觉”，往往会低估车辆的行驶速度，因此不要单纯凭感觉判断车速，一定要通过观察车速表来判断车速。题干表述正确。', '', '长时间|出现偏差|会低估', 0002, 0.04);
INSERT INTO `question` VALUES (1733399819092860929, 1185, 'f6034', 0001, '行车中当车辆前轮爆胎已发生转向时，驾驶人应双手紧握转向盘，尽力控制车辆直线行驶。', NULL, '对', '错', '空', '空', '对', '前轮爆裂已<b>出现转向</b>时，驾驶人不要过度矫正，应双手紧握转向盘，在控制住直线行驶的情况下，轻踏制动踏板，使车辆缓慢减速。', '', '前轮爆胎|紧握转向盘|直线行驶', 0002, 0.07);
INSERT INTO `question` VALUES (1733399819130609665, 1186, 'c8db9', 0001, '如图所示，A车在此处临时停车是可以的。', NULL, '对', '错', '空', '空', '对', '如图所示，图中A车停在距离公交车车站<b>30米以外</b>的位置，因此做法正确。根据《道路交通安全法实施条例》第六十三条规定：机动车在道路上临时停车，应当遵守下列规定：(二)交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；<b>【口5】</b><br>(三)<b>公共汽车站</b>、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点<b>30米以内的路段</b>，除使用上述设施的以外，不得停车。<b>【站3】</b>', 'https://timg.mnks.cn/20230927bb0c8c3c.jpg', '临时停车', 0004, 0.26);
INSERT INTO `question` VALUES (1733399819168358402, 1187, 'c20e5', 0001, '在路口遇这种情形要减速让行。', NULL, '对', '错', '空', '空', '对', '<p><img src=\"https://tp.mnks.cn/tp_177.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>减速让行</b>：车辆必须慢行或停车，观察干道行车情况，在确保干道车辆优先通行且安全的前提下，方可进入路口。<br><img src=\"https://tp.mnks.cn/tp_176.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>停车让行：车辆必须在停止线前停车瞭望，确认安全后方可以通行。</p>', 'https://timg.mnks.cn/20230927b657cf3f.jpg', '减速让行', 0002, 0.04);
INSERT INTO `question` VALUES (1733399819210301441, 1188, 'f25cd', 0001, '行车中当驾驶人意识到车辆爆胎时，应在控制住方向的情况下采取紧急制动，迫使车辆迅速停住。', NULL, '对', '错', '空', '空', '错', '行驶当中遇到突然爆胎，双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间<b>切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻</b>。', '', '爆胎|迅速停住', 0005, 0.29);
INSERT INTO `question` VALUES (1733399820527312897, 1189, '69410', 0001, '驾驶机动车在山路行驶时，为了减少油耗，下坡时可以空挡滑行，并使用行车制动器控制速度。', NULL, '对', '错', '空', '空', '错', '驾驶机动车下坡行驶，要挂中低速档位，充分利用发动机制动来控制车速，<b>严禁空挡滑行</b>。 <br>【拓展知识-<b>空挡滑行不省油而且危险大</b>】 <br>空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效。转向失灵等极端情况，很容易引发严重的交通事故。', '', '空挡滑行', 0003, 0.13);
INSERT INTO `question` VALUES (1733399821185818625, 1190, '116bd', 0002, '夜间会车规定150米以内使用近光灯的原因是什么？ ', 'A、提示后方车辆 B、两车之间相互提示 C、使用远光灯会造成驾驶人出现眩目，易引发危险 D、驾驶人的操作习惯行为', '提示后方车辆 ', '两车之间相互提示 ', '使用远光灯会造成驾驶人出现眩目，易引发危险 ', '驾驶人的操作习惯行为', 'C', '夜间会车<b>使用远光灯</b>，会使得对面车辆驾驶人因强烈灯光照射造成炫目而无法看清前方道路情况，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', '', '会车|150米|近光灯', 0002, 0.07);
INSERT INTO `question` VALUES (1733399821320036354, 1191, '38189', 0001, '避让特种车辆使其顺利通过后，车辆应有序回到原车道继续行驶，不要尾随特种车辆，以免发生交通事故。', NULL, '对', '错', '空', '空', '对', '特种车辆本身车速较快，而且由于其特殊性，驾驶路线与常规车辆也不同。如果跟随特种车会十分危险，因此避让特种车后，要有序回到原车道继续行驶。', '', '有序回到|不要尾随', 0001, 0.01);
INSERT INTO `question` VALUES (1733399821370368002, 1192, 'f41d8', 0002, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？ ', 'A、执行任务的警车 B、大型客货车 C、出租汽车 D、城市公交车', '执行任务的警车 ', '大型客货车 ', '出租汽车 ', '城市公交车', 'A', '《道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br>（一）前车正在左转弯、掉头、超车的；<br>（二）与对面来车有会车可能的；<br>（三）<b>前车为执行紧急任务的警车</b>、消防车、救护车、工程救险车的；<br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '前方遇到', 0001, 0.02);
INSERT INTO `question` VALUES (1733399821412311042, 1193, 'c8188', 0002, '在这种情形中前车怎样行驶？ ', 'A、正常行驶 B、及时让行 C、开启危险报警闪光灯行驶 D、不得变更车道', '正常行驶 ', '及时让行 ', '开启危险报警闪光灯行驶 ', '不得变更车道', 'B', '《道路交通安全法》第五十三条　警车、消防车、救护车、工程救险车执行紧急任务时，可以使用警报器、标志灯具；在确保安全的前提下，不受行驶路线、行驶方向、行驶速度和信号灯的限制，其他车辆和行人应当让行。【特殊车辆都该让】', 'https://timg.mnks.cn/20230927bc7f7424.jpg', '怎样行驶', 0002, 0.04);
INSERT INTO `question` VALUES (1733399821479419905, 1194, 'c8372', 0001, '驾驶机动车进入隧道前，戴太阳镜的驾驶人应提前摘掉太阳镜。', NULL, '对', '错', '空', '空', '对', '墨镜的暗色虽然可以有效地遮挡强光，但同时也可延迟视觉信号传送到大脑的时间，造成驾驶人感知的速度失真，做出错误的判断。进入隧道行驶，驾驶人本就会有一个暗适应的过程，因此应提前摘掉太阳镜。', '', '进入隧道|提前摘掉', 0002, 0.04);
INSERT INTO `question` VALUES (1733399821529751554, 1195, 'c0b35', 0001, '安全文明行车是机动车驾驶人必备的驾驶素质。', NULL, '对', '错', '空', '空', '对', '文明驾驶是驾驶人良好行为习惯和道德修养的表现，也是保障道路交通安全和谐的基础。《道路交通安全法》第二十二条，机动车驾驶人应当遵守道路交通安全法律、法规的规定，按照操作规范<b>安全驾驶、文明驾驶</b>。', '', '必备', 0001, 0.01);
INSERT INTO `question` VALUES (1733399821596860418, 1196, '087de', 0001, '雾天行车听到对方鸣喇叭，也应该鸣喇叭回应，以提示对方车辆。', NULL, '对', '错', '空', '空', '对', '雾天能见度低，驾驶人视线模糊，在道路上行车，可多使用喇叭引起对向车辆注意，<b>听到对向车辆鸣喇叭时，要鸣喇叭回应</b>，提示自己的行车位置。', '', '回应', 0001, 0.03);
INSERT INTO `question` VALUES (1733399821663969282, 1197, '0752f', 0002, '驾驶机动车行驶过程中，遇道路养护车辆从本车道逆向驶来时，以下做法正确的是什么？ ', 'A、靠边减速或停车让行 B、在原车道继续行驶 C、占用非机动车道行驶 D、鸣喇叭示意其让道', '靠边减速或停车让行 ', '在原车道继续行驶 ', '占用非机动车道行驶 ', '鸣喇叭示意其让道', 'A', '遇道路养护车辆逆向驶来时，应<b>靠边减速或停车让行</b>。根据《道路交通安全法》第五十四条条明确规定：道路养护车辆、工程作业车进行作业时，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，<b>过往车辆和人员应当注意避让</b>。', '', '逆向驶来', 0001, 0.03);
INSERT INTO `question` VALUES (1733399821701718017, 1198, 'e36d9', 0001, '驾驶纯电动汽车出行前，应确保电池电量充足，并提前规划路线，了解途中充电桩或充电站位置。', NULL, '对', '错', '空', '空', '对', '纯电动汽车的<b>主要动力来源是电池</b>，因此在出行前一定要查看剩余电量，确保出行电量充足，并了解途中的充电桩或者充电站的位置，做到有备无患，以免途中发生电量不足导致无法行驶的情况。', '', '应确保|提前规划|纯电动汽车', 0001, 0.01);
INSERT INTO `question` VALUES (1733399821756243970, 1199, '1f992', 0002, '驾驶机动车由加速车道进入高速公路行驶，以下做法错误的是什么？ ', 'A、在加速车道上加速，同时要开启左转向灯 B、密切注意左侧行车道的车流状态，同时用后视镜观察后方的情况 C、充分利用加速车道的长度加速，确认安全后，平顺地进入行车道 D、经加速车道充分加速后，可直接驶入最左侧车道', '在加速车道上加速，同时要开启左转向灯 ', '密切注意左侧行车道的车流状态，同时用后视镜观察后方的情况 ', '充分利用加速车道的长度加速，确认安全后，平顺地进入行车道 ', '经加速车道充分加速后，可直接驶入最左侧车道', 'D', '驾驶机动车驶入加速车道后，要开启左转向灯，尽快将车速提高到60公里/小时以上，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机。<b>不得直接驶入最左侧车道，不得在加速车道内紧急制动或停车</b>。请注意，题干要求选出<b>做法错误</b>的选项。', '', '进入高速|做法错误', 0004, 0.27);
INSERT INTO `question` VALUES (1733399821810769921, 1200, '6c7ee', 0002, '在标志、标线齐全的高速公路上行车，应当按照什么规定的车道和车速行驶？ ', 'A、《道路交通安全法》 B、标志或标线 C、车辆说明书 D、地方法规', '《道路交通安全法》 ', '标志或标线 ', '车辆说明书 ', '地方法规', 'B', '<b>标志标线齐全的高速公路上应按照标志标线规定的车道和车速行车。</b>行车时遵循的指示按以下顺序优先级依次递增：交通安全法-地方法规-明确的标志标线-交警指挥。原因是适用范围大的法律不一定适用于所有路况，需要更有针对性的指示来告诉行驶在该路段的驾驶员该如何行驶，以此来维护道路的安全和有序。', '', '标志|标线齐全', 0005, 0.29);
INSERT INTO `question` VALUES (1733399821852712962, 1201, 'c046f', 0002, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？ ', 'A、大型客货车 B、出租汽车 C、执行任务的救护车 D、公共汽车', '大型客货车 ', '出租汽车 ', '执行任务的救护车 ', '公共汽车', 'C', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： <br>（一）前车正在左转弯、掉头、超车的； <br>（二）与对面来车有会车可能的； <br>（三）<b>前车为执行紧急任务的</b>警车、消防车、<b>救护车、</b>工程救险车的； <br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '不得超车', 0001, 0.01);
INSERT INTO `question` VALUES (1733399821886267393, 1202, 'ef8b3', 0002, '驾驶机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件下，能见度在100米以下时，车速不得超过每小时多少公里，与同车道前车至少保持多少米的距离？ ', 'A、40，50 B、40，40 C、50，40 D、50，30', '40，50 ', '40，40 ', '50，40 ', '50，30', 'A', '能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；<b>能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；</b>能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', '', '100米以下', 0003, 0.14);
INSERT INTO `question` VALUES (1733399821936599042, 1203, '9826a', 0001, '驾驶机动车在高速公路上行驶，能见度小于200米时，与同车车道前车应保持100米以上的距离。', NULL, '对', '错', '空', '空', '对', '低能见度条件下高速公路通行原则：<br><b>【261】</b><b>能见度小于200米时</b>，开启雾灯、近光灯、示廓灯和前后位灯，<b>车速不得超过每小时60公里</b>，与同车道前车<b>保持100米以上的距离</b>； <br>【145】能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离； <br>【52离】能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', '', '小于200米', 0002, 0.08);
INSERT INTO `question` VALUES (1733399821986930689, 1204, '68a5a', 0002, '机动车在高速公路行驶，下列做法正确的是？ ', 'A、可在路肩停车上下人员 B、可在紧急停车带停车装卸货物 C、可在减速车道或加速车道上超车、停车 D、非紧急情况时不得在应急车道行驶或者停车', '可在路肩停车上下人员 ', '可在紧急停车带停车装卸货物 ', '可在减速车道或加速车道上超车', '停车 D', 'D', '《道路交通安全法实施条例》第八十二条 机动车在高速公路上行驶，<b>不得有</b>下列行为： <br>(一)倒车、逆行、穿越中央分隔带掉头或者在车道内停车； <br>(二)在匝道、加速车道或者减速车道上超车； <br>(三)骑、轧车行道分界线或者在路肩上行驶； <br>(四)非紧急情况时在应急车道行驶或者停车； <br>(五)试车或者学习驾驶机动车。', '', '高速公路', 0002, 0.06);
INSERT INTO `question` VALUES (1733399822033068033, 1205, '8ea3f', 0002, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？ ', 'A、大型客货车 B、执行任务的消防车 C、公共汽车 D、出租汽车', '大型客货车 ', '执行任务的消防车 ', '公共汽车 ', '出租汽车', 'B', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：<br>（三）前车为执行紧急任务的警车、<b>消防车、</b>救护车、工程救险车的；', '', '不得超车', 0001, 0.01);
INSERT INTO `question` VALUES (1733399822083399682, 1206, '7392d', 0002, '在同向4车道高速公路上行车，车速高于每小时110公里的车辆应在哪条车道上行驶？ ', 'A、最左侧 B、第二条 C、第三条 D、最右侧', '最左侧 ', '第二条 ', '第三条 ', '最右侧', 'A', '最左侧为快车道，最低限速110公里/小时，因此高于110的车辆应在最左侧车道上行驶。<br>根据《道路交通安全法实施条例》第七十八条 高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。<br>同方向有2条车道的，左侧车道的最低车速为每小时100公里；<b>同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里</b>，中间车道的最低车速为每小时90公里。道路限速标志标明的车速与上述车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。', '', '同向4车道|110', 0003, 0.12);
INSERT INTO `question` VALUES (1733399822133731329, 1207, '8fcee', 0002, '驾驶机动车行经市区下列哪种道路时不得超车？ ', 'A、主要街道 B、单向行驶路段 C、单向两条行车道 D、交通流量大的路段', '主要街道 ', '单向行驶路段 ', '单向两条行车道 ', '交通流量大的路段', 'D', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>： <br>（一）前车正在左转弯、掉头、超车的； <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、<b>市区交通流量大的路段</b>等没有超车条件的。', '', '不得超车|行经', 0004, 0.18);
INSERT INTO `question` VALUES (1733399822175674369, 1208, 'f068b', 0001, '如图，在这种天气行车，由于能见度较低，需要提前开启远光灯告知对向来车。', NULL, '对', '错', '空', '空', '错', '雨天行驶，一定要选择安全车速，同时要与其他车辆、行人等保持足够的安全距离，遇行人和骑车人时，要提前减速、鸣喇叭，与其保持一定的安全距离低速通过，不得抢行或从其身边急加速绕行，<b>不得使用远光灯</b>或持续鸣喇叭催促。', 'https://timg.mnks.cn/20230926d3c0eeb0.jpg', '远光灯', 0004, 0.19);
INSERT INTO `question` VALUES (1733399822230200322, 1209, 'fbc94', 0001, '如图，在高速公路行车道跟随前车行驶时，最小跟车距离不得少于100米。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第八十条 机动车在高速公路上行驶，车速<b>超过每小时100公里时</b>，应当与同车道前车<b>保持100米以上</b>的距离，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。 <br>第七十八条 高速公路应当标明车道的行驶速度，同方向有2条车道的，左侧车道的最低车速为每小时100公里；<b>同方向有3条以上车道的</b>，<b>最左侧车道的最低车速为每小时110公里</b>，中间车道的最低车速为每小时90公里。 <br>如图所示，<b>机动车行驶在左侧车道，最低车速为110公里/小时</b>，<b>因此应当与前车保持100米以上的跟车距离</b>。', 'https://timg.mnks.cn/20230926f6f6a8ea.jpg', '不得少于', 0003, 0.13);
INSERT INTO `question` VALUES (1733399822276337666, 1210, '192c0', 0001, '车辆驶入高速公路匝道后，迅速将车速提高到每小时60公里以上。', NULL, '对', '错', '空', '空', '错', '应当<b>从匝道驶入加速车道后</b>，尽快将车速提高到每小时60公里以上。所以，是在加速车道上提速，不是在匝道上。<br>《高速公路交通管理办法》第十二条 机动车进入高速公路起点后，应当<b>尽快</b>将车速提高到60公里以上。从匝道入口进入高速公路的车辆，<b>必须在加速车道上提高车速</b>，并开启左转向灯。驶入行车道时，不准妨碍其他车辆的正常行驶。', '', '匝道|迅速', 0005, 0.27);
INSERT INTO `question` VALUES (1733399822326669314, 1211, '18b72', 0002, '驾驶机动车行经下列哪种路段不得超车？ ', 'A、主要街道 B、高架路 C、人行横道 D、环城高速', '主要街道 ', '高架路 ', '人行横道 ', '环城高速', 'C', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>： <br>（一）前车正在左转弯、掉头、超车的； <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、<b>人行横道、</b>市区交通流量大的路段等没有超车条件的。', '', '不得超车|行经', 0003, 0.12);
INSERT INTO `question` VALUES (1733399822381195265, 1212, '46aeb', 0001, '驾驶机动车下长坡时，空挡滑行会导致再次挂挡困难。', NULL, '对', '错', '空', '空', '对', '下长坡时，由于重力作用，车速越来越快，<b>空挡滑行的速度和正常的挡位不匹配导致再次挂挡困难</b>，会造成很大的安全隐患。因此，严禁空挡滑行。', '', '下长坡|空挡滑行', 0003, 0.10);
INSERT INTO `question` VALUES (1733399822423138305, 1213, '3cb76', 0001, '驾驶机动车超车时，被超越车辆未减速让路，应迅速提速超越前方车辆完成超车。', NULL, '对', '错', '空', '空', '错', '被超车辆未减速让行，说明不具备超车条件，此时应该放弃超车，否则容易撞在一起，非常危险。', '', '未减速让路|迅速提速', 0003, 0.11);
INSERT INTO `question` VALUES (1733399822481858561, 1214, '0c8ce', 0001, '车辆在高速公路匝道上可以停车。', NULL, '对', '错', '空', '空', '错', '车辆进入减速车道后关闭右转向灯，注意观察车速表，逐渐减速后进入匝道，车速不得超过限速标志标明的速度，不得超车、停车，也不得未经减速车道直接驶入匝道。', '', '匝道|停车', 0002, 0.04);
INSERT INTO `question` VALUES (1733399822540578818, 1215, '62513', 0001, '警车、消防车、救护车、工程救险车执行紧急任务时，耽误或影响其通行可能会导致严重后果，所以其他车辆和行人应当主动让行。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第五十三条：<b>警车、消防车、救护车、工程救险车执行紧急任务时，</b>可以使用警报器、标志灯具；在确保安全的前提下，<b>不受行驶路线、行驶方向、行驶速度和信号灯的限制，其他车辆和行人应当让行。</b>', '', '主动让行', 0001, 0.01);
INSERT INTO `question` VALUES (1733399822586716162, 1216, 'db127', 0002, '请判断图中前面蓝色小型客车在提示什么？ ', 'A、准备直行通过路口 B、准备向右转弯 C、准备在路口停车 D、准备向左转弯', '准备直行通过路口 ', '准备向右转弯 ', '准备在路口停车 ', '准备向左转弯', 'B', '如图所示，前方<b>蓝车的右转向灯亮起</b>，表示其准备<b>向右转弯行驶</b>，行车途中要随时注意观察前车动态。', 'https://timg.mnks.cn/20230926e1a5aa4b.jpg', '蓝色', 0002, 0.06);
INSERT INTO `question` VALUES (1733399822628659202, 1217, 'be45b', 0002, '如图，请判断前方小型客车在提示什么？ ', 'A、准备向左转弯 B、前方有障碍物 C、准备向左变道 D、超越前方车辆', '准备向左转弯 ', '前方有障碍物 ', '准备向左变道 ', '超越前方车辆', 'C', '如图所示，前方车辆<b>左转向灯</b>亮，提示其<b>准备向左变道</b>。', 'https://timg.mnks.cn/20230926cd8efca3.jpg', '提示', 0004, 0.26);
INSERT INTO `question` VALUES (1733399822687379457, 1218, 'd90f9', 0002, '在如图这种环境中行车，应该怎样使用灯光？ ', 'A、变换远近光灯 B、关闭前照灯 C、开启远光灯 D、开启近光灯', '变换远近光灯 ', '关闭前照灯 ', '开启远光灯 ', '开启近光灯', 'A', '如图所示，前方路面施划有<b><b>人行横道</b>线</b>，因此，在这种环境中行车，应当<b>交替远近光灯</b>。根据《道路交通安全法》第四十八条规定：<b>机动车在夜间通过</b>急弯、坡路、拱桥、<b>人行横道</b>或者没有交通信号灯控制的路口时<b>，应当交替使用远近光灯示意。 </b>', 'https://timg.mnks.cn/20230926eea62716.jpg', '应该', 0004, 0.23);
INSERT INTO `question` VALUES (1733399822741905410, 1219, 'deb39', 0002, '如图，如果想超越前方这辆机动车，怎么做是正确的？ ', 'A、连续鸣喇叭提示前车 B、从前车的左侧直接超越 C、在条件具备时从右侧超越 D、开启左转向灯等待让超', '连续鸣喇叭提示前车 ', '从前车的左侧直接超越 ', '在条件具备时从右侧超越 ', '开启左转向灯等待让超', 'D', '超车时要保持与被超越车辆的安全距离，观察左侧交通情况，<b>选择合理时机</b>，开启左转向灯，从被超越车辆的左侧超越。超越后，在不影响被超越车辆正常行驶的情况下，逐渐驶回原车道。不得从被超车辆的右侧超越。因此，连续鸣喇叭提示、直接超越和从右侧超越做法错误；<b>正确的做法应当是开启左转向灯等待让超。</b>', 'https://timg.mnks.cn/20230926b4cb49e8.jpg', '超越', 0004, 0.16);
INSERT INTO `question` VALUES (1733399822788042753, 1220, 'f6c90', 0002, '驶近一个没有信号灯的路口，遇到图中这种情况，怎么做正确？ ', 'A、停车让行人先通过 B、减速缓慢行驶通过 C、连续鸣喇叭告知行人 D、加速从行人的前方通过', '停车让行人先通过 ', '减速缓慢行驶通过 ', '连续鸣喇叭告知行人 ', '加速从行人的前方通过', 'A', '驶近一个没有信号灯的路口，遇行人横过道路时，应主动停车礼让，待行人通过后再通过。', 'https://timg.mnks.cn/20230927eb53711b.jpg', '没有信号灯', 0002, 0.05);
INSERT INTO `question` VALUES (1733399822834180098, 1221, 'c46c0', 0002, '如图，看到路边有一个黄灯在闪烁时，正确的做法是？ ', 'A、只要没有行人横过就可以加速通过 B、提前减速观察确认是否能安全通过 C、鸣喇叭告知两边的行人和非机动车 D、如果来不及减速就直接按常速通过', '只要没有行人横过就可以加速通过 ', '提前减速观察确认是否能安全通过 ', '鸣喇叭告知两边的行人和非机动车 ', '如果来不及减速就直接按常速通过', 'B', '<b>闪光警告信号灯为持续闪烁的黄灯</b>，提示车辆、行人通行时<b>注意瞭望，确认安全后通过</b>。<br>【拓展知识】<br>黄灯的法律含义是警示，只闪黄灯表示此路口交通信号暂时解除，过往车辆经过此路口的时候需要提前减速观察周围情况决定停不停车，这种信号灯往往设置在车流不大的路口或是夜间等车流平峰时段。', 'https://timg.mnks.cn/20230926c0f27253.jpg', '黄灯在闪烁', 0001, 0.03);
INSERT INTO `question` VALUES (1733399822880317441, 1222, 'd1c98', 0002, '在绿灯亮的路口右转，遇到图中所示的情况，应该怎么做？ ', 'A、加速在第一个行人的前方右转弯 B、绕到第一个行人的后方向右转弯 C、等待两个行人都通过路口再右转弯 D、鸣喇叭让行人停止通行后向右转弯', '加速在第一个行人的前方右转弯 ', '绕到第一个行人的后方向右转弯 ', '等待两个行人都通过路口再右转弯 ', '鸣喇叭让行人停止通行后向右转弯', 'C', '驾驶机动车接近人行横道线时，提前减速观察，注意观察人行横道左右两侧是否有行人通行，随时准备停车礼让行人。遇行人或非机动车通过人行横道时，及时停车让行，不得抢行或绕行。<b>右转弯通行时，更要注意礼让通过人行横道的行人</b>。', 'https://timg.mnks.cn/20230926e1a5aa4b.jpg', '右转', 0002, 0.04);
INSERT INTO `question` VALUES (1733399822943232002, 1223, 'ec98b', 0002, '如图这种情况下，遇到对面车辆发出左转信号，怎样做是正确的？ ', 'A、只要不影响对面车辆左转就可以向右转弯 B、不要考虑对面车辆直接向右转弯 C、等待对面车辆向左转后再向右转弯 D、如果已经越过停止线就可以加速向右转弯', '只要不影响对面车辆左转就可以向右转弯 ', '不要考虑对面车辆直接向右转弯 ', '等待对面车辆向左转后再向右转弯 ', '如果已经越过停止线就可以加速向右转弯', 'C', '交叉路口优先通行三原则：1、让右原则；2、转弯让直行；3、<b>右转让左转</b>。因此，遇到图中对面车辆发出左转信号时，我方右转弯车辆，应当等待对面车辆向左转弯后再右转弯行驶。', 'https://timg.mnks.cn/20230927b671a80a.jpg', '左转信号', 0002, 0.06);
INSERT INTO `question` VALUES (1733399822985175041, 1224, 'ff8da', 0002, '如图这种情况下，遇到路口对面有车辆直行，怎么做是正确的？ ', 'A、如果已经越过停止线就可以加速向左转弯 B、不用考虑对面车辆直接向左转弯 C、只要不影响对面车辆直行就可以向左转弯 D、等待对面车辆直行通过后再向左转弯', '如果已经越过停止线就可以加速向左转弯 ', '不用考虑对面车辆直接向左转弯 ', '只要不影响对面车辆直行就可以向左转弯 ', '等待对面车辆直行通过后再向左转弯', 'D', '【交叉路口通行原则】<b>转弯让直行</b>，因此应当等待对面车辆直行通过后再向左转弯行驶。', 'https://timg.mnks.cn/20230927d217b6bf.jpg', '直行', 0002, 0.05);
INSERT INTO `question` VALUES (1733399823027118082, 1225, 'dc306', 0002, '如图这种情况下，遇到左侧路口有车辆直行，怎样做是正确的？ ', 'A、如果已经越过停止线就可以加速向右转弯 B、不用考虑左侧车辆直接向右转弯 C、只要不影响左侧车辆直行就可以向右转弯 D、等待左侧车辆直行通过后再向右转弯', '如果已经越过停止线就可以加速向右转弯 ', '不用考虑左侧车辆直接向右转弯 ', '只要不影响左侧车辆直行就可以向右转弯 ', '等待左侧车辆直行通过后再向右转弯', 'D', '根据《道路交通安全法实施条例》第五十二条规定，驾驶机动车通过未设置交通信号灯的交叉路口时： <br><b>转弯的机动车应当让直行的车辆、行人先行</b>； <br>没有交通标志、标线控制时，在进入路口前停车瞭望，让右方道路的来车先行； <br>相对方向行驶的右转弯机动车让左转弯的车辆先行。 <br>因此，<b>在左侧车辆直行，我方车辆右转弯</b>的情况下，应当<b>等待左侧车辆直行通过后再向右转弯</b>。', 'https://timg.mnks.cn/20230926e4c855c3.jpg', '左侧|直行', 0002, 0.07);
INSERT INTO `question` VALUES (1733399823069061121, 1226, 'dd87e', 0002, '驶近一个设有信号灯的路口，遇到如图所示信号灯亮着，但有行人通过，应该怎么做？ ', 'A、从行人的前方绕行通过路口 B、在停止线以外停车等待行人通过 C、鸣喇叭告知行人停止通过路口 D、可从两个行人中间低速缓慢穿过', '从行人的前方绕行通过路口 ', '在停止线以外停车等待行人通过 ', '鸣喇叭告知行人停止通过路口 ', '可从两个行人中间低速缓慢穿过', 'B', '驾驶机动车接近人行横道线时，提前减速观察，注意观察人行横道左右两侧是否有行人通行，随时准备停车礼让行人，即使行人出现不遵守交通规则的行为，驾驶人也要做到文明礼让，优先保证行人的安全。\n<p>【拓展知识】<br><img src=\"https://timg.mnks.cn/20231009a9d41c6b.png\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>以停止线为界，停止线到路口的区域称为停止线以内，停止线与车道间的区域称为<b>停止线以外</b>。', 'https://timg.mnks.cn/20230926c21605a5.jpg', '设有信号灯|行人', 0001, 0.02);
INSERT INTO `question` VALUES (1733399823111004162, 1227, 'a4472', 0002, '驶近一个铁路道口，遇到图中所示信号灯亮着，但栏杆还没落下的情况，怎么做才正确？ ', 'A、只要栏杆还没落下就继续行驶 B、如果没有看到列车驶来，可以快速横过道口 C、在道口的停止线以外停车等待 D、如果已过道口停止线，就可以急速通过', '只要栏杆还没落下就继续行驶 ', '如果没有看到列车驶来，可以快速横过道口 ', '在道口的停止线以外停车等待 ', '如果已过道口停止线，就可以急速通过', 'C', '驾驶机动车驶近<b>红灯亮</b>的铁路道口时，<b>要在道口停止线以外停车等待</b>，不得抢行通过铁路道口。<br>\n<p>【拓展知识】</p>\n<p><img src=\"https://sucimg.itc.cn/sblog/jbddb7c36bc0697f5fac8ec0a74265881\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>以停止线为界，停止线到路口的区域称为停止线以内，停止线与车道间的区域称为<b>停止线以外</b>。', 'https://timg.mnks.cn/20230926b927d6cf.jpg', '铁路道口|栏杆还没落下', 0001, 0.03);
INSERT INTO `question` VALUES (1733399823152947201, 1228, 'bc436', 0002, '遇到图中所示车辆停车等待的情形，怎样做是正确的？ ', 'A、穿插到红色小型客车前停车 B、依次在红色小型客车后停车等待 C、向前直行至不能继续行驶为止 D、鸣喇叭催促红色小型客车向前移', '穿插到红色小型客车前停车 ', '依次在红色小型客车后停车等待 ', '向前直行至不能继续行驶为止 ', '鸣喇叭催促红色小型客车向前移', 'B', '《道路交通安全法实施条例》第五十三条 机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当<b>依次</b>排队，<b>不得从前方车辆两侧穿插或者超越行驶</b>，不得在人行横道、网状线区域内停车等候。', 'https://timg.mnks.cn/20230926cc6bca47.jpg', '停车等待', 0001, 0.01);
INSERT INTO `question` VALUES (1733399823207473153, 1229, 'd1428', 0002, '如图，在辅路上行驶，遇到一辆机动车从主路进入辅路时，应该怎样做？ ', 'A、减速或停车让主路驶出的车辆先进入辅路 B、鸣喇叭告知进入辅路的车辆停车让行 C、只要不影响主路驶出的车辆正常行驶就可加速通过 D、在辅路行驶的车辆有优先通行权，不用减速行驶', '减速或停车让主路驶出的车辆先进入辅路 ', '鸣喇叭告知进入辅路的车辆停车让行 ', '只要不影响主路驶出的车辆正常行驶就可加速通过 ', '在辅路行驶的车辆有优先通行权，不用减速行驶', 'A', '【文明礼让】驾驶机动车从辅路汇入主路，遇到左侧车辆较多时，<b>让左侧车辆先行</b>，不要向左突然急加速转向汇入车流。从主路汇入辅路时，要提前减速，注意观察右侧车辆是否让行，确认安全后，逐渐汇入。', 'https://timg.mnks.cn/20230927d71f0971.jpg', '辅路', 0002, 0.05);
INSERT INTO `question` VALUES (1733399823241027586, 1230, 'cfa72', 0002, '如图所示，跟车进入一段漫水路段时，怎样做才正确？ ', 'A、如果跟车距离太近，可空挡滑行减速 B、增加与前车的距离，谨慎跟车慢行 C、紧跟前车，沿前车留下的痕迹行驶 D、如果前车速度太慢，可适当鸣喇叭示意', '如果跟车距离太近，可空挡滑行减速 ', '增加与前车的距离，谨慎跟车慢行 ', '紧跟前车，沿前车留下的痕迹行驶 ', '如果前车速度太慢，可适当鸣喇叭示意', 'B', '跟车进入一段漫水路段时，应当<b>增加与前车的安全距离，谨慎跟车慢行</b>，以防前车因故障停车，迫使后车也停在水中而进退两难，甚至发生意外。', 'https://timg.mnks.cn/20230926f0b3b24f.jpg', '跟车|漫水路', 0002, 0.07);
INSERT INTO `question` VALUES (1733399823282970625, 1231, 'abf8b', 0002, '遇到图中所示的情景，怎样做才正确？ ', 'A、借左侧车道超越校车 B、变换远近光灯催促校车离开 C、停在校车后面等待 D、鸣喇叭催促校车离开', '借左侧车道超越校车 ', '变换远近光灯催促校车离开 ', '停在校车后面等待 ', '鸣喇叭催促校车离开', 'C', '1、校车在同方向<b>只有一条机动车道</b>的道路上停靠时，<b>后方车辆应当停车等待</b>，不得超越； <br>2、校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。<br>另外，校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。\n<p><img src=\"https://timg.mnks.cn/20231009b8a00fee.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>', 'https://timg.mnks.cn/20230926af94d944.jpg', '正确', 0002, 0.04);
INSERT INTO `question` VALUES (1733399823337496577, 1232, 'a3a0f', 0002, '如图所示，行车中遇到停在路边校车时，怎么做才正确？ ', 'A、提前变更到中间车道超越 B、停在校车后方等待 C、停在校车后方使用灯光催促 D、适当鸣喇叭低速从左侧超越', '提前变更到中间车道超越 ', '停在校车后方等待 ', '停在校车后方使用灯光催促 ', '适当鸣喇叭低速从左侧超越', 'B', '校车在同方向有<b>两条以上</b>机动车道的道路上停靠时，<b>校车停靠车道</b><b>后方</b>和相邻机动车道上的机动车应当<b>停车等待</b>，其他机动车道上的机动车<b>应当减速通过</b>。在校车后方<b>停车等待</b>的机动车<b>不得鸣喇叭或者使用灯光</b>催促校车。\n<p><img src=\"https://timg.mnks.cn/20231009b8a00fee.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>', 'https://timg.mnks.cn/20230926b2e2e115.jpg', '校车', 0003, 0.15);
INSERT INTO `question` VALUES (1733399823375245314, 1233, 'f13d8', 0002, '在图中所示的道路上需要停车时，怎样选择正确的停放位置？ ', 'A、选择在路边不妨碍通行的地方停放 B、选择在标志前方安全的位置停车 C、只要没有禁止停车标线的路段都能停车 D、在这段道路上的任何地方都不能停车', '选择在路边不妨碍通行的地方停放 ', '选择在标志前方安全的位置停车 ', '只要没有禁止停车标线的路段都能停车 ', '在这段道路上的任何地方都不能停车', 'D', '如图所示，右侧路边有<b>禁止停车标志和标线</b>，因此在这段道路上的<b>任何地方都不能停车</b>。 <br>根据《道路交通安全法实施条例》第六十三条规定，机动车在道路上临时停车，应当遵守下列规定：(一)在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车。', 'https://timg.mnks.cn/20230927d702a97b.jpg', '停放位置', 0004, 0.17);
INSERT INTO `question` VALUES (1733399823417188354, 1234, 'bfd01', 0002, '如图所示，在能见度小于200米的高速公路上以60km/h速度行驶时，与同车道前车保持的安全距离是多少？ ', 'A、保持100米以上的距离 B、保持100米以内的距离 C、保持与车速相同数据的距离 D、保持不小于50米的安全距离', '保持100米以上的距离 ', '保持100米以内的距离 ', '保持与车速相同数据的距离 ', '保持不小于50米的安全距离', 'A', '《道路交通安全法实施条例》第八十一条 机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：<br>(一)能见度<b>小于200米</b>时，开启雾灯、近光灯、示廓灯和前后位灯，车速<b>不得超过每小时60</b>公里，与同车道前车<b>保持100米以上</b>的距离；', 'https://timg.mnks.cn/20230925bca58148.jpg', '安全距离', 0003, 0.14);
INSERT INTO `question` VALUES (1733399823467520002, 1235, 'da7d1', 0002, '如图所示，高速公路上遇到车辆无法继续行驶的情况时，怎样按规定放置危险警告标志？ ', 'A、在车后50米处放置警告标志 B、在车后50米至100米处放置警告标志 C、在车后150米以外放置警告标志 D、根据道路交通情况在适当位置放置警告标志', '在车后50米处放置警告标志 ', '在车后50米至100米处放置警告标志 ', '在车后150米以外放置警告标志 ', '根据道路交通情况在适当位置放置警告标志', 'C', '驾驶机动车在高速公路发生故障时，车辆无法继续行驶的，应当尽可能将车停入应急车道内，开启危险报警闪光灯，夜间开启示廓灯和后位灯，在<b>车后150米外</b>摆放三角警告标志，车上人员转移到护栏外或前方安全地带，再报警处理。', 'https://timg.mnks.cn/20230926c554db6c.jpg', '按规定放置', 0002, 0.08);
INSERT INTO `question` VALUES (1733399823501074434, 1236, 'a190c', 0001, '遇到这种情况不能超车。', NULL, '对', '错', '空', '空', '对', '如图所示前方车辆左转向灯亮，表示前车正在超车，遇到这种情况应<b>放弃超车</b>。<br>根据《道路交通安全法》第四十三条，同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>：<br>（一）<b>前车正在左转弯、掉头、超车的</b>；', 'https://timg.mnks.cn/20230926d73cb675.jpg', '不能', 0002, 0.04);
INSERT INTO `question` VALUES (1733399823543017473, 1237, 'c99ad', 0002, '如图所示，在这段道路上行驶需要注意什么？ ', 'A、只要有逆向行驶的车辆就不能越线行驶 B、既不能越中心线也不能压中心线行驶 C、如果没有逆向行驶的车辆允许越中心线行驶 D、只有在超车的时候才能越中心线行驶', '只要有逆向行驶的车辆就不能越线行驶 ', '既不能越中心线也不能压中心线行驶 ', '如果没有逆向行驶的车辆允许越中心线行驶 ', '只有在超车的时候才能越中心线行驶', 'B', '如图所示，路中心黄色单实线，<b>禁止跨越对向车行道分界线</b>，<b>禁止双方向车辆越线或压线行驶</b>。因此，在这段道路上既不能越中心线也不能压中心线行驶。', 'https://timg.mnks.cn/20230926ba25c572.jpg', '注意', 0003, 0.11);
INSERT INTO `question` VALUES (1733399823584960514, 1238, 'cd52a', 0002, '驾驶机动车，必须遵循什么原则？ ', 'A、左侧通行 B、右侧通行 C、内侧通行 D、中间通行', '左侧通行 ', '右侧通行 ', '内侧通行 ', '中间通行', 'B', '《道路交通安全法》第三十五条，机动车、非机动车实行<b>右侧通行</b>。', '', '', 0004, 0.24);
INSERT INTO `question` VALUES (1733399823626903553, 1239, 'bad10', 0001, '车辆在高速公路匝道提速到每小时60公里以上时，可直接驶入行车道。', NULL, '对', '错', '空', '空', '错', '驾驶车辆从匝道进入高速公路<b>加速车道</b>后，尽快将车速提高到每小时60公里以上，<b>并仔细观察</b>车道上车辆行驶的情况，选择驶入行车道的时机。因此，在<b>匝道提速</b>和<b>直接驶入行车道</b>做法错误。', '', '匝道提速|可直接', 0003, 0.15);
INSERT INTO `question` VALUES (1733399823668846593, 1240, 'b5225', 0001, '遇前方路段车道减少行驶缓慢，为了有序、安全，应依次交替通行。', NULL, '对', '错', '空', '空', '对', '在车道减少的路段缓慢行驶时，应当<b>依次交替通行</b>，确保行车安全、有序。', '', '依次交替', 0001, 0.02);
INSERT INTO `question` VALUES (1733399823706595329, 1241, '94429', 0001, '车辆不得在高速公路匝道上倒车。', NULL, '对', '错', '空', '空', '对', '《高速公路交通管理办法》第十七条 机动车在高速公路上正常行驶时，必须遵循下列规定： （一）不准倒车、逆行，不准穿越中央分隔带掉头或者转弯； （二）不准进行试车和学习驾驶机动车； <b>（三）不准在匝道、加速车道或者减速车道上倒车、超车、停车； </b>（四）不准骑、压车道分界线和在超车道上连续行驶； （五）不准右侧超车； （六）除遇障碍，发生故障等必须停车的情况外，不准随意停车、停车上下人员或者装卸货物； （七）除因停车驶入或者驶出紧急停车带和路肩外，不准在紧急停车带和路肩上行车。', '', '不得|倒车', 0002, 0.07);
INSERT INTO `question` VALUES (1733399823744344065, 1242, 'a9bed', 0001, '驾驶机动车遇到沙尘、冰雹、雨、雾、结冰等气候条件时应降低行驶速度。', NULL, '对', '错', '空', '空', '对', '沙尘、冰雹、雨、雾、结冰等恶劣气候条件，会对驾驶人的判断和操作产生不利的影响，一定要<b>降低行驶速度</b>，保持安全距离行驶。', '', '降低行驶速度', 0001, 0.02);
INSERT INTO `question` VALUES (1733399823798870018, 1243, 'c5cc8', 0001, '遇后车超车时，在条件许可的情况下应减速靠右让路，是为了后车留出超车空间。', NULL, '对', '错', '空', '空', '对', '超车一般都是从左侧超越。如果后方有车辆想要超车，为了避免发生事故，我方车辆在条件许可的情况下，应该减速靠右，给后车空出超车的空间。', '', '减速靠右', 0002, 0.04);
INSERT INTO `question` VALUES (1733399823840813057, 1244, '299b0', 0001, '车辆不得在高速公路匝道上掉头。', NULL, '对', '错', '空', '空', '对', '机动车在高速公路上行驶规定：<b>在匝道内不许超车、掉头、停车和倒车</b>。', '', '不得', 0002, 0.07);
INSERT INTO `question` VALUES (1733399823882756098, 1245, 'c1f19', 0001, '雪天行车时，应该开启近光灯和雾灯。', NULL, '对', '错', '空', '空', '错', '<b>雪天行车不可以开启雾灯</b>，因为雾灯的穿透力较强，雪天开雾灯会反光，眩目，影响行车安全。', '', '雪天|近光灯|雾灯', 0005, 0.42);
INSERT INTO `question` VALUES (1733399823924699138, 1246, '7d2dd', 0001, '车辆应靠高速公路右侧的路肩上行驶。', NULL, '对', '错', '空', '空', '错', '在高速公路路肩上行驶，属于违法行为。根据《道路交通安全法实施条例》第八十二条规定，机动车在高速公路上行驶，<b>不得有下列行为</b>： <br>(一)倒车、逆行、穿越中央分隔带掉头或者在车道内停车； <br>(二)在匝道、加速车道或者减速车道上超车； <br>(三)骑、轧车行道分界线或者<b>在路肩上行驶</b>； <br>(四)非紧急情况时在应急车道行驶或者停车； <br>(五)试车或者学习驾驶机动车。', '', '路肩', 0004, 0.19);
INSERT INTO `question` VALUES (1733399823975030786, 1247, 'e00ea', 0002, '驾驶的车辆正在被其他车辆超越时，若此时后方有跟随行驶的车辆，应怎样做？ ', 'A、继续加速行驶 B、稍向右侧行驶，保证横向安全距离 C、靠道路中心行驶 D、加速向右侧让路', '继续加速行驶 ', '稍向右侧行驶，保证横向安全距离 ', '靠道路中心行驶 ', '加速向右侧让路', 'B', '行车中正在被其他车辆超越且后方有跟随行驶的车辆时，<b>最有效的方式是预留出横向安全距离，稍向右侧让行</b>。不得加速或靠道路中心不让超。', '', '超越|跟随', 0001, 0.03);
INSERT INTO `question` VALUES (1733399824046333954, 1248, '9f7d4', 0001, '驾驶机动车超车应该提前开启左转向灯、变换使用远近光灯或鸣喇叭。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第四十七条：机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。', '', '超车|变换使用', 0004, 0.16);
INSERT INTO `question` VALUES (1733399824079888385, 1249, '7eb97', 0002, '驾驶机动车在夜间超车时怎样使用灯光？ ', 'A、变换远、近光灯 B、开启雾灯 C、开启远光灯 D、关闭前大灯', '变换远', '近光灯 B', '开启雾灯 C', '开启远光灯 D', 'A', '《道路交通安全法实施条例》第四十七条规定：机动车超车时，应当提前开启左转向灯、<b>变换使用远、近光灯或者鸣喇叭</b>。', '', '夜间超车', 0001, 0.02);
INSERT INTO `question` VALUES (1733399824121831426, 1250, '0d8da', 0002, '驾驶机动车行经下列哪种路段时不得超车？ ', 'A、高架路 B、交叉路口 C、环城高速 D、中心街道', '高架路 ', '交叉路口 ', '环城高速 ', '中心街道', 'B', '《道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>： <br>（一）前车正在左转弯、掉头、超车的； <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）行经铁路道口、<b>交叉路口、</b>窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '不得超车|行经', 0004, 0.17);
INSERT INTO `question` VALUES (1733399824163774466, 1251, 'e2fc6', 0001, '如图所示，在这种情况下遇到红灯交替闪烁时，要尽快通过道口。', NULL, '对', '错', '空', '空', '错', '如图所示，道路与铁路平面交叉道口<b>遇红灯交替闪烁时</b>，表示火车要通过了，此时<b>禁止车辆、行人通行</b>，车辆不得越过停止线。因此题干表述错误。', 'https://timg.mnks.cn/20230926aafdecd1.jpg', '尽快通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399824214106113, 1252, '0c708', 0002, '如图所示，以下哪种情况可以超车。 ', 'A、C图 B、D图 C、B图 D、图A', 'C图 ', 'D图 ', 'B图 ', '图A', 'A', '<b>请注意：图片的ABCD并不是对应选项的ABCD。</b>如图所示路况，安全超车需要满足两个条件：1、确认安全，即对向车道<b>无来车</b>；2、虚实线，实线一侧禁止车辆越线或压线行驶，<b>虚线一侧准许</b>车辆暂时越线。因此，<b>C图</b>所示的情况下可以超车。', 'https://timg.mnks.cn/20230706c1d05948.jpg', '可以超车', 0003, 0.13);
INSERT INTO `question` VALUES (1733399824256049154, 1253, 'ce94d', 0001, '车辆在高速公路以每小时100公里的速度行驶时，距同车道前车100米以上为安全距离。', NULL, '对', '错', '空', '空', '对', '根据《道路交通安全法实施条例》第八十条 机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', '', '每小时100公里|100米以上', 0003, 0.10);
INSERT INTO `question` VALUES (1733399824297992194, 1254, '0c87f', 0001, '如图所示，这种情况下只要后方、对向无来车，可以掉头。', NULL, '对', '错', '空', '空', '对', '路中心线为<b>黄色虚线</b>，用于分隔对向行驶的交通流，车辆在保证安全的情况下，<b>可以越线超车、掉头或转弯</b>。在如图所示的道路上能不能掉头，重点要看地面标线，是黄色实线还是黄色虚线。', 'https://timg.mnks.cn/20230926aaa0f4e0.jpg', '只要|可以', 0003, 0.14);
INSERT INTO `question` VALUES (1733399824339935234, 1255, 'a2c91', 0001, '雨天行车视线受阻，开启远光灯会提高能见度。', NULL, '对', '错', '空', '空', '错', '远光灯在雨雾天会形成眩目的光幕，影响周围车辆行车安全，所以<b>不能开启远光灯</b>。', '', '雨天|远光灯', 0003, 0.12);
INSERT INTO `question` VALUES (1733399824381878273, 1256, '69c76', 0001, '驾驶机动车超车后立即开启右转向灯驶回原车道。', NULL, '对', '错', '空', '空', '错', '驾驶机动车超车后，<b>应与被超车辆保持横向安全间距</b>，打开右转向灯，在不影响被超越车辆正常行驶的情况下，<b>逐渐平稳地驶回原车道</b>。因此，<b>题干表述错误</b>。', '', '立即', 0005, 0.29);
INSERT INTO `question` VALUES (1733399824423821314, 1257, 'd0a22', 0001, '驾驶机动车在隧道中超车时，应该提前开启左转向灯。', NULL, '对', '错', '空', '空', '错', '根据《道路交通安全法实施条例》的规定，行经繁华路段、交叉路口、铁路道口、人行横道、急弯路、宽度不足4米窄路或窄桥、陡坡、<b>隧道</b>或容易发生危险的路段，<b>不得超车</b>。因此，题干表述错误。', '', '隧道|超车', 0004, 0.20);
INSERT INTO `question` VALUES (1733399824465764353, 1258, '724c3', 0001, '如图所示，B车具有优先通行权。', NULL, '对', '错', '空', '空', '错', '<b>【转弯让直行】</b>如图所示：A车直行，B车右转弯，因此，A车具有优先通行权。<br>《道路交通安全法实施条例》第五十二条 机动车通过交叉路口，除应当遵守第五十一条第(二)项、第(三)项的规定外，还应当遵守下列规定：<br>(一)有交通标志、标线控制的，让优先通行的一方先行；<br>(二)没有交通标志、标线控制的，在进入路口前停车瞭望，让右方道路的来车先行；【让右原则】<br>(三)<b>转弯的机动车让直行的车辆先行；</b>【转弯让直行】<br>(四)相对方向行驶的右转弯的机动车让左转弯的车辆先行。【右转让左转】', 'https://timg.mnks.cn/20230926c033f2bf.jpg', 'B车', 0003, 0.08);
INSERT INTO `question` VALUES (1733399824511901697, 1259, 'abe98', 0001, '在狭窄的山路会车，靠山体的一方视野宽阔，所以要让不靠山体的一方优先行驶。', NULL, '对', '错', '空', '空', '错', '在狭窄的山路会车，靠山体的一方要让不靠山体的一方先行，原因是：<b>靠山体的一方有山体保护，靠右让行相对比较安全</b>。并不是因为靠山体的一方视野宽阔，所以<b>题干对于让行的因果关系表述错误</b>。', '', '视野宽阔', 0005, 0.37);
INSERT INTO `question` VALUES (1733399824549650433, 1260, 'cd989', 0001, '车辆在高速公路上行车，可以频繁地变更车道。', NULL, '对', '错', '空', '空', '错', '每变更一次车道，就会隐含一次风险，因此<b>禁止频繁变更车道</b>。随意频繁变更车道或强行突然变道，会严重扰乱道路通行秩序，影响其他车辆的正常通行，容易导致道路拥堵和剐蹭、碰撞等交通事故的发生。所以<b>题干表述错误</b>。', '', '频繁|变更车道', 0002, 0.05);
INSERT INTO `question` VALUES (1733399824595787777, 1261, '6c477', 0001, '如图所示，在这种情况下只要后方、对向无来车，可以掉头。', NULL, '对', '错', '空', '空', '错', '如图所示，路面<b>黄色实线</b>，是<b>禁止</b>标线，禁止跨越对向车行道分界线，因此<b>禁止</b>车辆越线掉头行驶；黄色虚线，确认安全后可以掉头。在如图所示的道路上能不能掉头，重点要看地面标线，是黄色实线还是黄色虚线。', 'https://timg.mnks.cn/20230926d1dfc877.jpg', '可以掉头', 0003, 0.08);
INSERT INTO `question` VALUES (1733399824658702337, 1262, 'cfb83', 0001, '在这种情况下，驾驶机动车可以向左变更车道。', NULL, '对', '错', '空', '空', '错', '如图所示，A车不能向左变更。<b>变更车道，首先不得妨碍要驶入车道内车辆的正常行驶</b>。若此时A车向左变更车道，会与C车发生碰撞事故。<b>因此，题干表述错误</b>。', 'https://timg.mnks.cn/20230926eef9137b.jpg', '可以向左变更车道', 0002, 0.08);
INSERT INTO `question` VALUES (1733399824692256769, 1263, '05016', 0001, '在道路上遇到这种情况可以从两侧超车。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>： <br>（一）前车正在左转弯、掉头、超车的； <br>（二）与对面来车有会车可能的； <br><b>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；</b> <br>（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 'https://timg.mnks.cn/20230926e64261b3.jpg', '可以', 0001, 0.02);
INSERT INTO `question` VALUES (1733399824734199810, 1264, 'c95ab', 0001, '如图所示，驾驶机动车在人行横道前遇到这种情况要停车让行。', NULL, '对', '错', '空', '空', '对', '【礼让斑马线】机动车行经人行横道时，应当减速行驶；<b>遇行人正在通过人行横道，应当停车让行</b>。', 'https://timg.mnks.cn/20230926f577853b.jpg', '停车让行|人行横道前', 0001, 0.02);
INSERT INTO `question` VALUES (1733399824788725762, 1265, 'a8621', 0001, '如图所示，A车具有优先通行权。', NULL, '对', '错', '空', '空', '错', '如图所示，A车右转弯；B车左转弯，根据<b>右转让左转</b>原则，A车应当让B车先行，所以<b>具有优先通行权的是B车</b>。<br>拓展：<br>【交叉路口三个先行原则】1、转弯的机动车让直行的车辆先行；2、右方道路来车先行；3、<b>右转弯车让左转弯车先行</b>。', 'https://timg.mnks.cn/20230926eeea904e.jpg', 'A车', 0005, 0.29);
INSERT INTO `question` VALUES (1733399824839057409, 1266, 'eef6b', 0002, '驾驶机动车准备驶离环岛时，应如何使用灯光？ ', 'A、提前开启左转向灯 B、不用指示灯提示 C、开启危险报警闪光灯 D、提前开启右转向灯', '提前开启左转向灯 ', '不用指示灯提示 ', '开启危险报警闪光灯 ', '提前开启右转向灯', 'D', '<b>驶出环岛前，打开右转向灯</b>，注意观察右侧车辆、行人的动态。在有两条或两条以上车道的环岛驶出时，应提前开启右转向灯变更至外侧车道，严禁直接从内侧车道驶出环岛。', '', '驶离环岛', 0004, 0.22);
INSERT INTO `question` VALUES (1733399824889389057, 1267, 'c3c0f', 0001, '在高速公路上行驶感觉疲劳时，应当立即停车休息，以保证行车安全，避免因疲劳驾驶而导致的交通事故。', NULL, '对', '错', '空', '空', '错', '高速公路长时间行车，驾驶人容易出现注意力分散、知觉减弱、反应迟钝、放松警惕、昏昏欲睡的“高速催眠”现象，因此要间歇到<b>服务区休息调整</b>，不得在高速公路行车中立即停车，更不要在行车道内停车。', '', '立即停车休息', 0005, 0.29);
INSERT INTO `question` VALUES (1733399824931332097, 1268, '0f280', 0001, '如图所示，A车在此时进入左侧车道是因为进入实线区不得变更车道。', NULL, '对', '错', '空', '空', '对', '进入<b>实线区前</b>，根据选择的行驶路线按导向箭头方向变更车道；进入<b>实线区后</b>，<b>不能变更车道</b>。如图所示，A车选择此时变更到左侧车道，是因为此时正处在白色虚线区；如果这个时候不变更车道，继续向前行驶进入实线区后，就不能变更到左侧掉头车道了。<b>题目表述的是A车选择此时进入左侧车道的原因</b>，故<b>说法正确</b>。', 'https://timg.mnks.cn/20230926f48cf870.jpg', '实线区不得', 0003, 0.15);
INSERT INTO `question` VALUES (1733399824964886529, 1269, '7e748', 0001, '车辆驶离高速公路时，应当经减速车道减速后进入匝道。', NULL, '对', '错', '空', '空', '对', '车辆驶离高速公路进入减速车道后关闭右转向灯，注意观察车速表，<b>逐渐减速后进入匝道</b>，车速不得超过限速标志标明的速度，也<b>不得未经减速车道直接驶入</b>匝道。', '', '驶离|减速车道', 0002, 0.06);
INSERT INTO `question` VALUES (1733399824998440961, 1270, '47f92', 0002, '如图所示，在同向三车道高速公路上行车，车速每小时115公里应在哪条行车道上行驶？ ', 'A、最右侧行车道 B、最左侧行车道 C、中间行车道 D、哪条都行', '最右侧行车道 ', '最左侧行车道 ', '中间行车道 ', '哪条都行', 'B', '道路限速标志标明的车速与车道行驶车速的规定<b>不一致时</b>，应当<b>按照道路限速标志标明的车速行驶</b>。如图所示，<b>左侧车道：限速区间100-120公里/小时</b>；中间车道：限速区间80-100公里/小时；右侧车道：限速区间60-80公里/小时。<br>因此，当<b>车速在115公里/小时</b>时，应当在<b>最左侧车道行驶</b>。', 'https://timg.mnks.cn/20230926bf5570a7.jpg', '每小时115公里', 0002, 0.06);
INSERT INTO `question` VALUES (1733399825036189698, 1271, 'f772b', 0002, '如图所示，A车若想左转，以下做法中错误的是什么？①直接变更到左转车道②过停车线进入路口后左转③直行通过，此车道不得左转。 ', 'A、①③ B、①② C、②③ D、①②③', '①③ ', '①② ', '②③ ', '①②③', 'B', '请注意，题干要求<b>选出错误的</b>做法。驾驶机动车在有导向箭头的路口左转弯时，要提前按导向箭头指示向左变更车道。如图所示，A车此时在路口直行车道，因此只能继续直行通过，<b>不得在</b>当前车道左转弯行驶。所以<b>①②的做法是错误的</b>。', 'https://timg.mnks.cn/20230926d965e1bb.jpg', '错误的是|想左转', 0005, 0.37);
INSERT INTO `question` VALUES (1733399825078132737, 1272, 'deda0', 0002, '如图所示，在这种天气条件下行车如何使用灯光？ ', 'A、开启右转向灯、示廓灯、后位灯和危险报警闪光灯 B、使用雾灯、示廓灯、后位灯和危险报警闪光灯 C、使用远光灯、示廓灯、后位灯和危险报警闪光灯 D、不使用灯光', '开启右转向灯', '示廓灯', '后位灯和危险报警闪光灯 B', '使用雾灯', 'B', '如图所示，在大雾天跟车行驶，<b>应开启</b>雾灯、示廓灯、后位灯和危险报警闪光灯，及时让其他车辆驾驶人发现自己；同方向行驶的后车与前车近距离行驶时，<b>不得使用远光灯</b>。', 'https://timg.mnks.cn/20230926eb8ef843.jpg', '如何使用', 0003, 0.12);
INSERT INTO `question` VALUES (1733399825120075778, 1273, 'a06f3', 0001, '在道路上行车时，驾驶人通过车窗丢弃垃圾不会干扰到其他车辆正常行驶。', NULL, '对', '错', '空', '空', '错', '车辆在行驶的过程中，如果前方车辆突然投掷物体，后车驾驶人会因受惊或为躲避垃圾而紧急变道等避险操作，极易引发交通事故。因此，车窗抛物，不仅破坏了环境卫生，还是<b>会干扰</b>到其他车辆的正常行驶，妨碍安全行车。', '', '不会干扰', 0003, 0.13);
INSERT INTO `question` VALUES (1733399825162018817, 1274, 'e0d13', 0001, '机动车从匝道驶入高速公路，应当开启左转向灯。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第七十九条规定，机动车从匝道<b>驶入</b>高速公路，应当<b>开启左转向灯</b>，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道。', '', '驶入|左转向灯', 0004, 0.17);
INSERT INTO `question` VALUES (1733399825203961857, 1275, 'fd154', 0002, '关于机动车掉头地点的规定，以下说法正确的是什么？ ', 'A、机动车可以在有禁止左转弯标志的地点掉头 B、机动车在高速公路不得掉头 C、机动车可以在人行横道掉头 D、机动车可以在隧道掉头', '机动车可以在有禁止左转弯标志的地点掉头 ', '机动车在高速公路不得掉头 ', '机动车可以在人行横道掉头 ', '机动车可以在隧道掉头', 'B', '《道路交通安全法实施条例》第四十九条，机动车在有禁止掉头或者<b>禁止左转弯标志、标线的地点</b>以及在铁路道口、<b>人行横道</b>、桥梁、急弯、陡坡、<b>隧道</b>或者容易发生危险的路段，<b>不得掉头</b>。', '', '掉头地点|说法正确', 0004, 0.18);
INSERT INTO `question` VALUES (1733399825250099201, 1276, 'a2bdf', 0002, '驾驶机动车在道路上行驶突然熄火，以下做法错误的是什么？ ', 'A、开启危险报警闪光灯 B、放置警告标志 C、车辆移到安全地点 D、立即停车检修', '开启危险报警闪光灯 ', '放置警告标志 ', '车辆移到安全地点 ', '立即停车检修', 'D', '<b>请注意，题干要求选出做法错误的选项</b>。行车中车辆发动机突然熄火后，驾驶人<b>正确的做法</b>应开启危险报警闪光灯，依靠惯性，<b>将车辆驶向不妨碍交通的地点停车</b>，按规定在车后放置警告标志，然后查明原因。<br>因此，<b>立即停车检修的做法是错误的</b>。', '', '突然熄火|做法错误', 0004, 0.23);
INSERT INTO `question` VALUES (1733399825296236545, 1277, 'f322b', 0001, '机动车驾驶人驾驶车辆前可以饮用少量啤酒。', NULL, '对', '错', '空', '空', '错', '<b>严禁酒驾</b>。酒后驾驶，会对驾驶人的注意力、判断力、操作能力和驾驶心理都产生不利影响，因此，为了自己和他人的安全，驾驶人一定要做到：<b>喝酒不开车，开车不喝酒</b>。', '', '可以饮用', 0001, 0.02);
INSERT INTO `question` VALUES (1733399825346568194, 1278, 'c5e5c', 0002, '驾驶机动车在夜间通过人行横道时怎样使用灯光？ ', 'A、变换远、近光灯 B、开启雾灯 C、开启远光灯 D、关闭前大灯', '变换远', '近光灯 B', '开启雾灯 C', '开启远光灯 D', 'A', '《道路交通安全法实施条例》第五十九条　机动车在<b>夜间通过</b>急弯、坡路、拱桥、<b>人行横道</b>或者没有交通信号灯控制的路口时，应当<b>交替使用远近光灯示意</b>。<br>机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当减速慢行，并鸣喇叭示意。', '', '人行横道', 0001, 0.03);
INSERT INTO `question` VALUES (1733399825396899842, 1279, 'e4d26', 0002, '下列驾驶情形中，需要使用转向灯的是？①准备超车②路口转弯③靠边停车④变更车道 ', 'A、只有① B、只有①② C、只有①②③ D、①②③④', '只有① ', '只有①② ', '只有①②③ ', '①②③④', 'D', '《道路交通安全法实施条例》第五十七条　机动车应当<b>按照下列规定</b>使用转向灯：<br>(一)向左<b>转弯</b>、向左<b>变更车道</b>、<b>准备超车</b>、驶离停车地点或者掉头时，应当提前开启左转向灯；<br>(二)向右<b>转弯</b>、向右<b>变更车道</b>、超车完毕驶回原车道、<b>靠路边停车</b>时，应当提前开启右转向灯。', '', '需要使用', 0002, 0.08);
INSERT INTO `question` VALUES (1733399825430454273, 1280, 'ef020', 0001, '驾驶机动车在地下车库寻找停车位时，应注意观察行人的动态，遇行人横穿车道时及时减速，停车避让。', NULL, '对', '错', '空', '空', '对', '在地下车库寻找停车位时，由于转弯和盲点多，经常会遇到离开停车位的车辆或行人。这些车辆和行人经常突然出现在行驶的车辆前面，驾驶人的注意力一般集中在寻找停车位上。如果没有及时发现或者速度过快，很容易造成事故。因此，<b>在寻找车位时要低速行驶</b>，遇有行人时，应注意观察行人动态，<b>及时减速或停车避让</b>。', '', '注意观察|及时减速', 0001, 0.01);
INSERT INTO `question` VALUES (1733399825535311874, 1281, 'ee6d1', 0002, '机动车在设有最高限速标志的道路上行驶时，下列说法正确的是什么？ ', 'A、不得超过标明的最高时速 B、可以超过车辆的最高设计时速 C、允许超过标明最高时速的10% D、按规定的最高车速行驶', '不得超过标明的最高时速 ', '可以超过车辆的最高设计时速 ', '允许超过标明最高时速的10% ', '按规定的最高车速行驶', 'A', '《道路交通安全法》第四十二条，机动车上道路行驶，<b>不得超过限速标志标明的最高时速</b>。在没有限速标志的路段，应当保持安全车速。', '', '最高限速标志', 0004, 0.17);
INSERT INTO `question` VALUES (1733399825610809346, 1282, 'c252a', 0002, '如图所示，在这种环境下通过路口，应如何使用灯光？ ', 'A、交替使用远、近光灯 B、使用危险报警闪光灯 C、关闭远光灯 D、使用远光灯', '交替使用远', '近光灯 B', '使用危险报警闪光灯 C', '关闭远光灯 D', 'A', '<b>夜间驾驶机动车通过人行横道或没有交通信号灯控制的路口</b>，应在距路口约100米处减速慢行，并且<b>交替使用远、近光灯</b>示意，停车瞭望确认安全后，低速通过，同时要注意黑暗中的行人和非机动车。', 'https://timg.mnks.cn/20230926af2111f9.jpg', '如何使用', 0002, 0.07);
INSERT INTO `question` VALUES (1733399825652752385, 1283, 'ed4fa', 0001, '如图所示，在铁路道口遇到两个红灯交替闪烁时，确认安全后可通过。', NULL, '对', '错', '空', '空', '错', '道路与铁路平面交叉道口有<b>两个红灯交替闪烁</b>或一个红灯亮时，表示火车要通过了，此时<b>禁止车辆、行人通行，车辆不得越过停止线</b>。红灯熄灭时，表示允许车辆行人通行。', 'https://timg.mnks.cn/20230926aafdecd1.jpg', '交替闪烁|可通过', 0004, 0.19);
INSERT INTO `question` VALUES (1733399825715666945, 1284, 'e65c2', 0001, '驾驶机动车在高速公路上不应频繁变更车道。', NULL, '对', '错', '空', '空', '对', '变更车道有一定的风险，每变更一次车道，就会隐含着一次风险，一定要在确保安全的前提下谨慎变更，<b>禁止频繁</b>变更车道。', '', '不应频繁', 0002, 0.08);
INSERT INTO `question` VALUES (1733399825774387202, 1285, 'dd569', 0001, '夜间驾车遇到对向来车未关闭远光灯时，应鸣喇叭并使用远光灯，以提示对方。', NULL, '对', '错', '空', '空', '错', '夜间会车，遇对向车辆不关闭远光灯时，应当：1、交替使用远近光灯提示对方，<b>不要</b>鸣喇叭并开启远光灯对射，以防两车灯光的交汇处有行人或非机动车通过时发生事故。；2、不要直视对向车辆的灯光，视线向右避开，以防眩光刺眼；3、在不确定前方和右侧道路交通情况时，要及时减速避让，必要时停车让行，不可盲目前行或靠右让行，以防撞上路边的行人、非机动车等。', '', '未关闭|使用远光灯', 0005, 0.30);
INSERT INTO `question` VALUES (1733399825820524545, 1286, 'fe537', 0001, '大雾天行车，多鸣喇叭是为了引起对方注意，避免发生危险。', NULL, '对', '错', '空', '空', '对', '雾天能见度低，驾驶人的视线模糊、视距变短、视野变窄，在道路上行车，<b>多鸣喇叭引起行人和车辆注意</b>，避免发生交通事故。', '', '引起|避免', 0002, 0.05);
INSERT INTO `question` VALUES (1733399825862467586, 1287, 'a369a', 0001, '机动车驾驶人及乘车人下车时，用远离车门一侧的手开门，转头观察车辆侧方和后方通行状况，避免妨碍他人通行。', NULL, '对', '错', '空', '空', '对', '<b>下车时应确认周边情况，确认安全后再开门下车，不得妨碍他人</b>。当我们用远离车门一侧的手去开车门，同时上半身会自然转动，眼睛会通过后视镜观察，然后转身时向后看，这一个完整的动作能避免很多事故。在这个过程当中，无论后方是有车还是有人，我们都能观察到，从而避免了后方车辆或行人撞上突然打开的车门的悲剧。因此，题干表述正确。', '', '远离车门|观察|避免妨碍', 0002, 0.05);
INSERT INTO `question` VALUES (1733399825900216321, 1288, 'ad404', 0001, '高温炎热天气，电动汽车出行时要避免在户外长时间暴晒。', NULL, '对', '错', '空', '空', '对', '高温炎热天气，如果新能源汽车长时间在阳光下暴晒，温度过高的环境会使蓄电池内部压力增加而使电池失水，引发电池活性下降，加速极板老化；而如果长时间在寒冷环境停止，也将会对续航能力产生急剧下滑的影响，并且对电池组性能会产生不利影响。', '', '要避免|暴晒', 0001, 0.03);
INSERT INTO `question` VALUES (1733399825950547969, 1289, 'a2f9e', 0001, '机动车行经视线受阻的急弯路段时，如遇对方车辆鸣喇叭示意，也应当及时鸣喇叭进行回应。', NULL, '对', '错', '空', '空', '对', '行经<b>视线受阻的急弯路段</b>时，可多使用喇叭<b>引起对向车辆注意</b>，听到对方车辆鸣喇叭示意，也应当<b>及时鸣喇叭进行回应</b>提示自己的行车位置，以保证急弯路段的通行安全。', '', '视线受阻|回应', 0001, 0.03);
INSERT INTO `question` VALUES (1733399825992491010, 1290, 'f66da', 0002, '超车时，前方车辆不让出超车空间，应该怎么做？ ', 'A、开启前照灯超越 B、连续鸣喇叭超越 C、停止超车 D、迅速超越', '开启前照灯超越 ', '连续鸣喇叭超越 ', '停止超车 ', '迅速超越', 'C', '超车应在确认前车让超并有充足的安全距离后，与前车保持一定的安全距离从前车的左侧超越。进入左侧道路超车，<b>无法保证与正常行驶前车的横向安全间距或被超车无让超空间时，应主动放弃超车。</b> <br>前方车辆不减速让路，说明不具备超车条件，因此<b>应停止超车并适当减速，与前方车辆保持安全距离</b>。', '', '不让', 0001, 0.03);
INSERT INTO `question` VALUES (1733399826034434050, 1291, 'f5ec1', 0001, '驾驶机动车遇到大雾或特大雾等能见度过低天气时，应选择安全地点停车。', NULL, '对', '错', '空', '空', '对', '遇有浓雾或特大雾天等能见度过低天气，方向难辨，行进中很难看清前方障碍（如慢行车、行人、故障车、凹坑等），容易发生交通事故，因此应<b>选择安全地点停车</b>，等能见度好转时再上路行驶。', '', '能见度过低|安全地点', 0002, 0.05);
INSERT INTO `question` VALUES (1733399826076377090, 1292, 'abfc1', 0001, '驾驶机动车驶入高速公路收费口应减速慢行，有序行驶，选择绿灯亮起的收费口进入。', NULL, '对', '错', '空', '空', '对', '驾驶机动车驶近高速公路收费站时，要严格遵守限速规定，<b>选择通道上方绿灯亮且车辆较少的收费口</b>，<b>依次排队通过</b>，切勿争道抢行。进入收费口<b>尽量将车身靠近收费亭</b>，驾驶室门窗对齐收费窗口时停车，在入口处领到通行卡后，要妥善收存好，以备出口时交回通行卡和缴纳通行费。', '', '绿灯亮', 0001, 0.02);
INSERT INTO `question` VALUES (1733399826114125825, 1293, 'a4663', 0001, '驾驶机动车在高速公路行驶时，要严格按照车载导航指示的车速行驶。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第七十八条 在高速公路上驾驶机动车<b>应按照道路限速标志</b>标明的车速行驶。<br>所以，题干表述错误。', '', '导航指示', 0005, 0.36);
INSERT INTO `question` VALUES (1733399826151874561, 1294, 'd8bee', 0001, '夜间行经车流量较少的公路，可以临时超速驾驶。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第四十二条　机动车上道路行驶，<b>不得超过限速标志标明的最高时速</b>。在没有限速标志的路段，应当保持安全车速。<br>　　<b>夜间行驶</b>或者在容易发生危险的路段行驶，以及遇有沙尘、冰雹、雨、雪、雾、结冰等气象条件时，<b>应当降低行驶速度</b>。', '', '超速|可以', 0002, 0.06);
INSERT INTO `question` VALUES (1733399826189623297, 1295, '79c12', 0002, '夜间行驶，与对向车道车辆交会时，以下做法正确的是？ ', 'A、保持使用远光灯 B、远光灯与近光灯之间不断来回切换 C、切换为近光灯 D、关闭灯光', '保持使用远光灯 ', '远光灯与近光灯之间不断来回切换 ', '切换为近光灯 ', '关闭灯光', 'C', '夜间会车或跟车行驶，应当使用近光灯。<b>会车前</b>，应当在距对向来车150米之外<b>改用近光灯</b>。', 'https://timg.mnks.cn/20230926abc911f6.jpg', '夜间|交会', 0004, 0.19);
INSERT INTO `question` VALUES (1733399826244149250, 1296, '439f3', 0001, '驾驶机动车遇紧急事务，可以边开车边接打电话。', NULL, '对', '错', '空', '空', '错', '<b>边开车边接打电话，是一种妨碍安全驾驶的行为。</b>驾驶机动车时接打电话，会影响驾驶人的判断力，增加驾驶人的大脑负担、延迟反应时间，而且接打电话时容易导致目光凝聚，无法关注周围环境。更有甚者，在电话中争论会加剧驾驶人的负面情绪，导致无意识急剧踩油门等行为，危害自身及他人安全。', '', '可以|接打电话', 0001, 0.03);
INSERT INTO `question` VALUES (1733399826281897986, 1297, 'd17d2', 0001, '驾驶机动车在坡道路段跟车行驶时，应保持比平路跟车时更大的安全距离。', NULL, '对', '错', '空', '空', '对', '驾驶机动车在坡道路段跟车行驶时，<b>要适当加大与前车的安全距离</b>，仔细观察前车动态，在视线不佳或道路条件差的路段，要进一步加大跟车距离，以防前车突然停车或停车后溜。', '', '更大的|坡道路段跟车', 0001, 0.02);
INSERT INTO `question` VALUES (1733399826315452417, 1298, 'd94d1', 0001, '驾驶机动车通过无划分车道的道路时，机动车在道路中间通行，非机动车和行人在道路两侧通行。', NULL, '对', '错', '空', '空', '对', '根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。<b>没有划分</b>机动车道、非机动车道和人行道的，机动车在<b>道路中间</b>通行，非机动车和行人在<b>道路两侧</b>通行。', '', '道路中间|道路两侧', 0002, 0.05);
INSERT INTO `question` VALUES (1733399826378366977, 1299, 'c05fc', 0001, '驾驶机动车遇到校车在道路右侧停车上下学生，同向有两条机动车道时，左侧车道后方机动车应当停车等待。', NULL, '对', '错', '空', '空', '对', '1、校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越；<br>2、校车在<b>同方向有两条以上</b>机动车道的道路上停靠时，校车停靠车道<b>后方和相邻</b>机动车道上的机动车<b>应当停车等待</b>，其他机动车道上的机动车应当减速通过。校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。\n<p><img src=\"https://timg.mnks.cn/20231009b8a00fee.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>', '', '校车|两条|左侧', 0003, 0.15);
INSERT INTO `question` VALUES (1733399826420310018, 1300, 'fb5d0', 0002, '雾天驾驶机动车跟车行驶，以下做法错误的是什么？ ', 'A、加大两车间的距离 B、时刻注意前车刹车灯的变化 C、降低行车速度 D、鸣喇叭提醒前车提高车速，避免后车追尾', '加大两车间的距离 ', '时刻注意前车刹车灯的变化 ', '降低行车速度 ', '鸣喇叭提醒前车提高车速，避免后车追尾', 'D', '请注意，题干要求选出<b>做法错误的选项</b>。雾天跟车距离较近，容易发生追尾事故，因此，驾驶人一定要<b>控制车速</b>，<b>加大与前车的安全距离</b>，<b>可多使用喇叭引起车辆注意</b>，但是鸣喇叭提醒前车加速的做法是错误的。', '', '雾天|跟车行驶', 0004, 0.18);
INSERT INTO `question` VALUES (1733399826462253058, 1301, 'abb5d', 0002, '机动车在夜间通过没有交通信号灯控制的交叉路口时，要怎样使用灯光？ ', 'A、使用远光灯 B、使用近光灯 C、使用危险报警闪光灯 D、交替使用远近光灯示意', '使用远光灯 ', '使用近光灯 ', '使用危险报警闪光灯 ', '交替使用远近光灯示意', 'D', '《道路交通安全法实施条例》第五十九条 机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当<b>交替使用远近光灯示意</b>。', '', '没有|信号灯', 0003, 0.15);
INSERT INTO `question` VALUES (1733399826500001793, 1302, 'b3314', 0001, '在机动车道上不得从机动车右侧上下车。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第七十七条 （二）在机动车道上<b>不得从机动车左侧</b>上下车。', '', '不得从|右侧', 0005, 0.28);
INSERT INTO `question` VALUES (1733399826537750530, 1303, 'de8c4', 0001, '开车过程中不得主动打电话，但在车流量少的道路上，当他人打来电话时，可以临时边开车边接电话。', NULL, '对', '错', '空', '空', '错', '<b>边开车边接打电话，是一种妨碍安全驾驶的行为</b>。驾驶机动车时接打电话，会影响驾驶人的判断力，增加驾驶人的大脑负担、延迟反应时间，而且接打电话时容易导致目光凝聚，无法关注周围环境。更有甚者，在电话中争论会加剧驾驶人的负面情绪，导致无意识急剧踩油门等行为，危害自身及他人安全。', '', '边开车边接|可以', 0002, 0.08);
INSERT INTO `question` VALUES (1733399826575499266, 1304, 'e542d', 0001, '雾天驾驶机动车在道路上行驶，由于能见度低，应加速行驶，尽快到达安全地点。', NULL, '对', '错', '空', '空', '错', '雾天能见度低，驾驶人视线模糊，上道路行驶应严格控制车速，正确使用灯光，同时加大行车安全距离，谨慎缓慢行驶，<b>不得加速行驶</b>，以免引发交通事故。', '', '雾天|应加速', 0003, 0.09);
INSERT INTO `question` VALUES (1733399826613248001, 1305, 'c076f', 0002, '在隧道内行驶，发现行驶路线错误，应如何行驶？ ', 'A、在紧急停车带掉头 B、在确认后方无来车的情况下逆行驶出隧道 C、在隧道内停车，查找清楚路线之后再通行 D、继续行驶，驶出隧道后寻找合适路线', '在紧急停车带掉头 ', '在确认后方无来车的情况下逆行驶出隧道 ', '在隧道内停车，查找清楚路线之后再通行 ', '继续行驶，驶出隧道后寻找合适路线', 'D', '隧道内行驶，<b>禁止</b>有停车、倒车、超车、掉头、逆行等操作。如果发现行驶路线错误，只能<b>继续向前行驶</b>，驶出隧道后再寻找合适的路线。', '', '路线错误|隧道', 0002, 0.07);
INSERT INTO `question` VALUES (1733399826655191041, 1306, 'fb147', 0002, '专属停车位的停车标线用什么颜色表示？ ', 'A、红色 B、白色 C、蓝色 D、黄色', '红色 ', '白色 ', '蓝色 ', '黄色', 'D', '停车位标线标示车辆停放的位置，可在停车场或路边空地、车行道边缘或道路中间适当位置设置。标线颜色不同表示的含义不同：<br>1、白色，表示此停车位为收费停车位，多位于人行道及退缩线上，适用于交投公司停车收费管理；<br>2、<b>黄色</b>，<b>表示此停车位为专属停车位</b>，适用于承租的车位或有关单位申请设置的，供办公、方便群众车辆停放的车位；<br>3、蓝色，表示此停车位为免费停车位，适用于政府部门办公场地周边规划的泊位。', '', '收费停车位', 0005, 0.36);
INSERT INTO `question` VALUES (1733399826692939777, 1307, 'ae36d', 0002, '以下说法正确的是什么？ ', 'A、从匝道驶入高速公路，应提前开启右转向灯 B、驶离高速公路进入匝道，应提前开启右转向灯 C、匝道路段可以超车 D、驶入错误的匝道后，可倒车驶回高速公路', '从匝道驶入高速公路，应提前开启右转向灯 ', '驶离高速公路进入匝道，应提前开启右转向灯 ', '匝道路段可以超车 ', '驶入错误的匝道后，可倒车驶回高速公路', 'B', '驾驶机动车从匝道<b>驶入高速公路，要开启左转向灯</b>；<b>驶离</b>高速公路时，提前<b>开启右转向灯</b>，在不妨碍已在高速公路内的车辆正常行驶的情况下驶入减速车道，降低车速后驶入匝道。<b>注意：在匝道内不许超车、掉头、停车和倒车。</b>', '', '说法正确', 0004, 0.21);
INSERT INTO `question` VALUES (1733399826739077122, 1308, 'c919c', 0002, '驾驶机动车在没有中心线的城市道路上行驶，以下说法正确的是什么？ ', 'A、最低速度为每小时40公里 B、最低速度为每小时30公里 C、最高速度为每小时30公里 D、行驶速度可以为每小时50公里', '最低速度为每小时40公里 ', '最低速度为每小时30公里 ', '最高速度为每小时30公里 ', '行驶速度可以为每小时50公里', 'C', '【<b>无线：城3公4</b>】《道路交通安全法实施条例》第四十五条 在没有限速标志、标线的道路上，机动车<b>不得超过下列最高行驶速度</b>：<br><b>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里。</b>【无线：城3公4】', '', '没有|城市道路|说法正确', 0005, 0.41);
INSERT INTO `question` VALUES (1733399826789408770, 1309, 'a8429', 0002, '驾驶机动车上高速公路行驶，以下说法正确的是什么？ ', 'A、可在匝道、加速车道、减速车道上超车 B、不准倒车、逆行、穿越中央分隔带掉头 C、非紧急情况时可在应急车道行驶 D、可以试车或学习驾驶', '可在匝道', '加速车道', '减速车道上超车 B', '不准倒车', 'B', '《道路交通安全法实施条例》第八十二条 机动车在高速公路上行驶，<b>不得有</b>下列行为：<br><b>（一）倒车、逆行、穿越中央分隔带掉头或者在车道内停车</b>； 　　<br>（二）在匝道、加速车道或者减速车道上超车； 　　<br>（三）骑、轧车行道分界线或者在路肩上行驶； 　　<br>（四）非紧急情况时在应急车道行驶或者停车； 　　<br>（五）试车或者学习驾驶机动车。', '', '高速公路|说法正确', 0003, 0.09);
INSERT INTO `question` VALUES (1733399826827157505, 1310, 'c0ab4', 0002, '车辆行至隧道出口或凿开的山谷出口处，容易遇到横风，驾驶人感到车辆行驶方向偏移时，应如何安全驾驶？ ', 'A、采取紧急制动 B、迅速逆风向转动转向盘 C、迅速向顺风方向转动转向盘 D、双手稳握转向盘，适当减速', '采取紧急制动 ', '迅速逆风向转动转向盘 ', '迅速向顺风方向转动转向盘 ', '双手稳握转向盘，适当减速', 'D', '车辆行至隧道出口或凿开的山谷出口处遭遇横风时，驾驶人应<b>双手握紧转向盘</b>，<b>向来风的一侧适当修正</b>，并且<b>力度要轻</b>，<b>不要急打转向盘</b>。同时<b>应降低车速</b>，防止高速行驶造成车辆失控。', '', '横风|方向偏移', 0003, 0.11);
INSERT INTO `question` VALUES (1733399826869100546, 1311, 'cdd5a', 0001, '驾驶机动车行至山谷出口处，遇到横风导致车辆行驶方向偏移时，应双手握稳转向盘，向来风相反的一侧适当修正，力度要轻，不可急转转向盘。', NULL, '对', '错', '空', '空', '错', '车辆行至隧道出口或凿开的山谷出口处遭遇横风时，驾驶人应双手握紧转向盘，<b>向来风的一侧适当修正</b>，并且力度要轻，不要急打转向盘。同时应降低车速，防止高速行驶造成车辆失控。所以，题干做法错误。', '', '来风相反', 0005, 0.57);
INSERT INTO `question` VALUES (1733399826915237890, 1312, 'e27b1', 0001, '如图所示，驾驶机动车在这段道路上，在不影响左侧车辆通行的前提下可以变更到左侧车道掉头。', NULL, '对', '错', '空', '空', '错', '如图所示，前方路口有<b>禁止掉头</b>标志，表示禁止一切车辆掉头。因此，题干表述错误。', 'https://timg.mnks.cn/20230927b5094a60.jpg', '可以变更|掉头', 0003, 0.12);
INSERT INTO `question` VALUES (1733399826990735361, 1313, 'bfcf5', 0001, '夜间驾驶机动车通过急弯、坡路、拱桥、人行横道时，应当交替使用远近光灯示意。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远、近光灯示意。 因此，题干表述正确。', '', '交替使用', 0002, 0.05);
INSERT INTO `question` VALUES (1733399827036872706, 1314, 'a34b8', 0001, '驾车在高速公路上行驶时，应尽量避免长时间与其他车辆并行。', NULL, '对', '错', '空', '空', '对', '在高速公路行车道行驶，要严格遵守“分道行驶、各行其道”和限速规定，根据车辆行驶速度正确选择行车道，<b>不得随意穿行越线，不准骑、轧车行道分界线，</b>不得占用应急车道。', '', '避免|长时间并行', 0001, 0.03);
INSERT INTO `question` VALUES (1733399827074621441, 1315, 'f5123', 0002, '在山区冰雪道路上行车，应当采取在_____上安装防滑链等安全防范措施。 ', 'A、驱动轮 B、被动轮 C、备胎 D、驱动轮和被动轮', '驱动轮 ', '被动轮 ', '备胎 ', '驱动轮和被动轮', 'A', '防滑链是规定<b>要装在驱动轮上</b>，以增加驱动轮的抓地力，防止车辆打滑，根据不同的前后驱动模式进行安装。', '', '山区冰雪道路|防滑链', 0005, 0.35);
INSERT INTO `question` VALUES (1733399827137536001, 1316, 'c49fc', 0001, '如图所示，驾驶机动车可以从匝道直接驶入行车道。', NULL, '对', '错', '空', '空', '错', '机动车<b>不得从匝道直接驶入行车道</b>，应先驶入加速车道充分加速，达到行车道规定车速后，在确认安全的前提下，选择驶入行车道的时机。', 'https://timg.mnks.cn/20230926ef4e611c.jpg', '直接驶入', 0003, 0.12);
INSERT INTO `question` VALUES (1733399827183673346, 1317, 'f0409', 0001, '如图所示，A车的行为是正确的。', NULL, '对', '错', '空', '空', '错', '如图所示，<b>A车直接从行车道驶入匝道的做法是错误的</b>。<br>因为驶离高速公路时<b>禁止未通过减速车道，直接从行车道驶入匝道</b>。减速车道是高速公路行车道和匝道的过渡车道，车辆进入匝道前需要在减速车道把车速降到合理速度范围内。', 'https://timg.mnks.cn/20230926ee53fd5c.jpg', '正确', 0002, 0.04);
INSERT INTO `question` VALUES (1733399827229810690, 1318, 'a288c', 0001, '在大暴雨的天气行车，刮水器无法正常工作时，应立即减速行驶。', NULL, '对', '错', '空', '空', '错', '暴雨天气驾车，刮水器无法刮净雨水，严重影响驾驶人视线，看不清路况行车十分危险。为了安全起见，一定要<b>立即减速靠边停车，并开启危险报警闪光灯</b>，等雨变小再继续行驶。因此，题干表述错误。', '', '大暴雨|减速行驶', 0005, 0.43);
INSERT INTO `question` VALUES (1733399827267559426, 1319, 'bf9d7', 0001, '警告标线的作用是促使道路使用者了解道路上的特殊情况，提高警觉准备应变防范措施。', NULL, '对', '错', '空', '空', '对', '道路交通标线按功能可分为三类： <br>1）指示标线：指示车行道、行车方向、路面边缘、人行道等的标线； <br>2）禁止标线：告示道路交通的遵行、禁止、限制等特殊规定的标线； <br>3）<b>警告标线：促使道路使用者了解道路上的特殊情况，提高警觉，准备防范或应变措施的标线。</b>', '', '警告标线', 0001, 0.02);
INSERT INTO `question` VALUES (1733399827301113857, 1320, 'b3747', 0001, '停车位标线为蓝色表示收费停车位。', NULL, '对', '错', '空', '空', '错', '白线泊位为收费车位、黄线泊位为专用车位、<font color=\"#FF0000\">蓝线泊位为免费车位。</font> <br><b>扩展资料</b> <br>1、颜色为白色的泊位，表示此停车位为收费停车位，多位于人行道及退缩线上，适用于交投公司停车收费管理； <br>2、颜色为黄色的泊位，表示此停车位为专属停车位，适用于承租的车位或有关单位申请设置的，供办公、方便群众车辆停放的车位； <br>3、颜色为蓝色的泊位，表示此停车位为免费停车位，适用于政府部门办公场地周边规划的泊位。', '', '蓝色|收费', 0005, 0.32);
INSERT INTO `question` VALUES (1733399827343056897, 1321, 'd5073', 0001, '驾驶机动车距离铁路道口50米内禁止停放车辆。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定：<br>（2）<b>交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；</b>【口5】<br>（3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；【站3】', '', '禁止停放', 0003, 0.09);
INSERT INTO `question` VALUES (1733399827384999937, 1322, 'fb2dd', 0001, '驾驶机动车在同方向只有1条机动车道的城市道路上，最高速度不得超过每小时70公里。', NULL, '对', '错', '空', '空', '错', '同方向<b>只有1条</b>机动车道的道路，城市道路<b>限速50公里</b>/小时。因此，题干表述错误。根据《道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：<br>(一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里。【城3公4】<br>(二)<b>同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里</b>。【城5公7】', '', '只有1条|城市道路|不能超过|70公里', 0004, 0.18);
INSERT INTO `question` VALUES (1733399827418554370, 1323, 'e5b73', 0001, '驾驶人上车时观察开门是否安全，右手开车门，同时左手扶车门角。', NULL, '对', '错', '空', '空', '错', '站在车外开车门时先确认安全，然后用<b>左手开车门</b>，右手扶车门角。因此，题干表述错误。', '', '右手开|左手扶', 0005, 0.44);
INSERT INTO `question` VALUES (1733399827464691713, 1324, 'afd21', 0001, '驾驶机动车在没有划分道路中心线的城市道路上行驶速度不得超过50公里/小时。', NULL, '对', '错', '空', '空', '错', '驾驶机动车在<b>没有道路中心线的城市道路</b>，行驶速度<b>不得超过30公里/小时</b>。根据《道路交通安全法实施条例》中规定：第四十五条 机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： <br>（一）<b>没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里；</b> <b>【城3公4】</b><br>（二）同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。【城5公7】', '', '城市道路|不得超过', 0004, 0.24);
INSERT INTO `question` VALUES (1733399827506634753, 1325, 'c8529', 0001, '如图所示，驾驶机动车遇到这种情况时，应注意减速慢行并持续鸣喇叭提醒其避让。', NULL, '对', '错', '空', '空', '错', '驾驶机动车遇低头看手机或打电话等不集中精力观察路口的行人时，一定要提前鸣喇叭提示，并减速慢行，<b>不得持续鸣喇叭</b>，防止临近鸣喇叭使其慌不择路，出现危险。', 'https://timg.mnks.cn/20230926d2cddc8b.jpg', '持续鸣喇叭', 0004, 0.22);
INSERT INTO `question` VALUES (1733399827548577793, 1326, 'b016b', 0002, '驾驶机动车在高速公路上遇前方车流行驶缓慢时，以下做法正确的是什么? ', 'A、跟随车流行驶，保持安全车距 B、进入应急车道行驶 C、可以倒车 D、立即停车', '跟随车流行驶，保持安全车距 ', '进入应急车道行驶 ', '可以倒车 ', '立即停车', 'A', '高速公路遇有前方车流行驶缓慢时，应当保持安全车距，跟随车流行驶，<br>不得借道超车或者占用应急车道，不得穿插等候的车辆。', '', '行驶缓慢', 0001, 0.03);
INSERT INTO `question` VALUES (1733399827590520833, 1327, 'fa28f', 0001, '在开启车门下车前，驾驶员左手扶车把手，用右手进行开门解锁，身体随右手解锁方向转动，回头观察后方交通情况；右手解锁后，先小幅度打开车门约10cm左右，并再次回头观察后方交通情况。', NULL, '对', '错', '空', '空', '对', '【下车动作及顺序】当需要下车时，务必将车辆停好，拉紧驻车制动器，如果有关闭发动机，还应将变速挡杆置于低速挡或倒挡。下车前，首先应通过内外后视镜观察，车辆左后方道路交通情况，并向左方侧头观察，确定前后方无车辆、行人靠近，准备开车门，先将车门稍微打开约10cm左右，再次侧头观察后方，确定安全后再开大车门，按左腿、臀部、右腿的顺序下车。下车后，应及时关闭车门，先将车门关到车门接近车身约10cm时，再用力将车门关闭并锁好。', '', '下车前|左手扶|观察', 0002, 0.07);
INSERT INTO `question` VALUES (1733399827649241090, 1328, 'b201d', 0001, '驾驶机动车在高速公路上行驶，可直接借用应急车道行驶、超车或停车。', NULL, '对', '错', '空', '空', '错', '在高速公路行车道行驶，要严格遵守“分道行驶、各行其道”和限速规定，根据车辆行驶速度正确选择行车道，不得随意穿行越线，不准骑、轧车行道分界线，<b>不得占用应急车道</b>，非紧急情况时<b>不得在应急车道行驶或者停车</b>。', '', '高速公路|可直接', 0002, 0.06);
INSERT INTO `question` VALUES (1733399827686989826, 1329, '8bdd8', 0002, '在这种天气条件下行车如何使用灯光？ ', 'A、使用远光灯 B、使用雾灯 C、开启右转向灯 D、不使用灯光', '使用远光灯 ', '使用雾灯 ', '开启右转向灯 ', '不使用灯光', 'B', '如图所示，在大雾天跟车行驶，应<b>开启雾灯</b>、示廓灯、后位灯和危险报警闪光灯，及时让其他车辆驾驶人发现自己；同方向行驶的后车与前车近距离行驶时，不得使用远光灯。', 'https://timg.mnks.cn/20230926eb8ef843.jpg', '如何使用', 0002, 0.05);
INSERT INTO `question` VALUES (1733399827728932865, 1330, 'c79d1', 0001, '驾驶机动车在道路上掉头时提前开启左转向灯。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： <br><b>（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯。</b> <br>（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。 <br>因此，本题答案应为正确。', '', '掉头时', 0002, 0.06);
INSERT INTO `question` VALUES (1733399827762487298, 1331, 'a1cd2', 0002, '驾驶小型载客汽车在高速公路上时速超过100公里时的跟车距离是多少？ ', 'A、保持50米以上 B、保持60米以上 C、保持80米以上 D、保持100米以上', '保持50米以上 ', '保持60米以上 ', '保持80米以上 ', '保持100米以上', 'D', '《道路交通安全法实施条例》第八十条：机动车在高速公路上行驶，<b>车速超过每小时100公里时</b>，应当与同车道前车<b>保持100米以上的距离</b>；车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', '', '超过100公里', 0004, 0.22);
INSERT INTO `question` VALUES (1733399827804430338, 1332, '77b66', 0002, '当驾驶车辆行经两侧有非机动车行驶且有积水的路面时，应怎样做？ ', 'A、减速慢行 B、正常行驶 C、加速通过 D、连续鸣喇叭', '减速慢行 ', '正常行驶 ', '加速通过 ', '连续鸣喇叭', 'A', '驾驶机动车行经积水路面时，要<b>低速缓慢通过</b>，以免溅起的泥水让路边的骑车人无法躲避甚至摔倒。', '', '非机动车|积水', 0001, 0.01);
INSERT INTO `question` VALUES (1733399827858956290, 1333, '52868', 0002, '牵引发生故障的机动车时，最高车速不得超过多少？ ', 'A、50公里/小时 B、40公里/小时 C、30公里/小时 D、20公里/小时', '50公里/小时 ', '40公里/小时 ', '30公里/小时 ', '20公里/小时', 'C', '《道路交通安全法实施条例》第四十六条，机动车行驶中遇有下列情形之一的，最高行驶速度<b>不得超过每小时30公里</b>，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：<br>1、进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；<br>2、掉头、转弯、下陡坡时；<br>3、遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；<br>4、在冰雪、泥泞的道路上行驶时；<br>5、<b>牵引发生故障的机动车时</b>。', '', '牵引|最高车速', 0003, 0.11);
INSERT INTO `question` VALUES (1733399827900899329, 1334, 'aa4fa', 0001, '在划有道路中心线的道路上会车时，应做到保持安全车速、不越线行驶。', NULL, '对', '错', '空', '空', '对', '在有中心线的道路上会车时，注意观察道路两侧交通情况，做到保持安全车速、安全距离，不越线行驶。遇对方车辆超过中心线或开启转向灯示意占道行驶时，立即靠路右侧减速或停车让行。', '', '会车|做到', 0001, 0.02);
INSERT INTO `question` VALUES (1733399827947036673, 1335, 'f8049', 0002, '车辆在高速公路以每小时100公里的速度行驶时，与前车距离在多少米时是危险车间距？ ', 'A、50米 B、100米 C、110米 D、120米', '50米 ', '100米 ', '110米 ', '120米', 'A', '<b>车速100公里/小时，距前车50米时，是危险车间距。</b>根据《道路交通安全法实施条例》第八十条：机动车在高速公路上行驶，车速超过每小时100公里时，<b>应当与同车道前车保持100米以上的距离</b>，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', '', '每小时100公里|危险车间距', 0005, 0.39);
INSERT INTO `question` VALUES (1733399827993174018, 1336, 'ba86e', 0001, '雾天行车时，可持续鸣喇叭催促前车提速，避免发生追尾事故。', NULL, '对', '错', '空', '空', '错', '雾天行车，要将车速控制在能及时停车的范围内，保持足够的安全间距，谨慎缓慢行驶，可多使用喇叭<b>引起行人和其他车辆注意</b>。鸣喇叭的目的并不是为了催促前车提速，题干表述错误。', '', '雾天|持续鸣喇叭|提速', 0004, 0.18);
INSERT INTO `question` VALUES (1733399828047699969, 1337, '0b105', 0002, '驾驶机动车在下列哪种路段不得超车？ ', 'A、山区道路 B、城市高架路 C、城市快速路 D、窄桥、弯道', '山区道路 ', '城市高架路 ', '城市快速路 ', '窄桥', 'D', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，<b>不得超车</b>： <br>（一）前车正在左转弯、掉头、超车的； <br>（二）与对面来车有会车可能的； <br>（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； <br>（四）行经铁路道口、交叉路口、<b>窄桥、弯道、</b>陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', '', '不得超车|行经', 0002, 0.04);
INSERT INTO `question` VALUES (1733399828098031618, 1338, 'c95aa', 0001, '夜间在道路上会车时，应在距离对向来车100米以内将远光灯改用近光灯。', NULL, '对', '错', '空', '空', '错', '《道路安全交通法》规定：在没有中心隔离设施或没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', '', '夜间|会车|100米以内', 0005, 0.37);
INSERT INTO `question` VALUES (1733399828156751873, 1339, 'a5691', 0001, '驾驶机动车遇到后方有执行紧急任务的特种车辆时，可临时使用无人通行的非机动车道让行。', NULL, '对', '错', '空', '空', '对', '驾驶机动车遇到后方有执行紧急任务的特种车辆时，应减速靠边避让。如果因为避让执行紧急任务的车辆而造成交通违法行为的，可以向交警部门进行申诉，核实后可依法撤销。', '', '可临时|让行', 0004, 0.16);
INSERT INTO `question` VALUES (1733399828215472130, 1340, 'a430a', 0001, '驾驶过程中需接听电话时应先将车辆停在安全的地点再接听电话。', NULL, '对', '错', '空', '空', '对', '边开车边接打电话，属于违法行为。不但影响其他车辆的通行效率，造成道路拥堵，甚至还会引发交通事故。<b>正确的做法</b>应当先将车停在安全的地点再接听电话，以确保个人以及道路的通行安全。因此，题干表述正确。', '', '停在安全', 0001, 0.03);
INSERT INTO `question` VALUES (1733399828261609473, 1341, 'aad6f', 0001, '机动车在高速公路行驶时，可以不受速度限制。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第七十八条 高速公路应当标明车道的行驶速度，<b>最高车速不得超过</b>每小时120公里，<b>最低车速不得低于</b>每小时60公里。<br>　　在高速公路上行驶的小型载客汽车最高车速不得超过每小时120公里，其他机动车不得超过每小时100公里，摩托车不得超过每小时80公里。<br>　　同方向有2条车道的，左侧车道的最低车速为每小时100公里；同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。道路限速标志标明的车速与上述车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。', '', '可以不', 0002, 0.04);
INSERT INTO `question` VALUES (1733399828915920898, 1342, 'e4ac4', 0001, '如图所示，B车具有优先通行权。', NULL, '对', '错', '空', '空', '错', '<b>【转弯让直行】</b>如图所示：A车直行，B车左转弯，因此，<b>A车具有优先通行权</b>，题干表述错误。<br>【拓展知识】<br>《道路交通安全法实施条例》第五十二条 机动车通过交叉路口，除应当遵守第五十一条第(二)项、第(三)项的规定外，还应当遵守下列规定：<br>(一)有交通标志、标线控制的，让优先通行的一方先行；<br>(二)没有交通标志、标线控制的，在进入路口前停车瞭望，让右方道路的来车先行；【让右原则】<br>(三)<b>转弯的机动车让直行的车辆先行</b>；【转弯让直行】<br>(四)相对方向行驶的右转弯的机动车让左转弯的车辆先行。【右转让左转】', 'https://timg.mnks.cn/20230926e6a915eb.jpg', 'B车|优先', 0004, 0.20);
INSERT INTO `question` VALUES (1733399828974641154, 1343, 'e6939', 0001, '如图所示，驾车遇到这种情形时，应尽快加速通过。', NULL, '对', '错', '空', '空', '错', '如图所示，驾驶机动车看到行人正在通过人行横道时，一定<b>要停车避让行人</b>，不要盲目通过，<b>更不得加速通过</b>。不要在人行横道及其附近直行超车和变向超车，尤其要提防有些行动缓慢的行人可能还滞留在人行横道上。', 'https://timg.mnks.cn/20230926ed444654.jpg', '加速通过', 0001, 0.02);
INSERT INTO `question` VALUES (1733399829024972802, 1344, 'b4c6e', 0001, '如图所示，这种情形下，B车可加速通过。', NULL, '对', '错', '空', '空', '错', '如图所示，<b>B车应当停车等待</b>，加速通过的做法是错误的。机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 <b>黄灯亮</b>时，是一种过渡信号灯，起到警示作用，提示信号即将变换，已越过停止线的车辆可以继续通行；<b>没有越过停止线的车辆需停车等待通行信号，不得进入路口，更不得加速抢行通过交叉路口</b>。', 'https://timg.mnks.cn/20230926d676d553.jpg', '加速通过', 0001, 0.03);
INSERT INTO `question` VALUES (1733399829075304449, 1345, 'e7f55', 0002, '机动车驾驶人________车辆。 ', 'A、可按照自己的习惯驾驶 B、在没有交通警察时可随意驾驶 C、应当依法、安全和文明驾驶 D、在没有交通信号时可以任意驾驶', '可按照自己的习惯驾驶 ', '在没有交通警察时可随意驾驶 ', '应当依法', '安全和文明驾驶 D', 'C', '《道路交通安全法》第二十二条<br>机动车驾驶人<b>应当遵守道路交通安全法律、法规的规定</b>，<b>按照操作规范安全驾驶、文明驾驶</b>。<br>饮酒、服用国家管制的精神药品或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，不得驾驶机动车。<br>任何人不得强迫、指使、纵容驾驶人违反道路交通安全法律、法规和机动车安全驾驶要求驾驶机动车。', '', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399829129830401, 1346, 'f322e', 0001, '在单位院内驾车时，应低速行驶，避让行人；有限速标志的，按照限速标志行驶。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第六十七条　在<b>单位院内、居民居住区内</b>，机动车<b>应当</b>低速行驶，避让行人；有限速标志的，按照限速标志行驶。', '', '低速行驶|限速标志', 0001, 0.02);
INSERT INTO `question` VALUES (1733399829188550658, 1347, 'a3cd5', 0002, '在这个路口右转弯应如何通行？ ', 'A、直接向右转弯 B、加速通过 C、鸣喇叭催促 D、先让对面车左转弯', '直接向右转弯 ', '加速通过 ', '鸣喇叭催促 ', '先让对面车左转弯', 'D', '【直行&gt;左转&gt;右转】三个先行原则：转弯的机动车让直行的车辆先行，右方道路来车先行，<b>右转弯车让左转弯车先行</b>。如图所示，对面车辆左转弯，因此在这个路口右转弯时应<b>先让对面车左转弯。</b>', 'https://timg.mnks.cn/20230927bcd2b000.jpg', '右转弯', 0001, 0.02);
INSERT INTO `question` VALUES (1733399829234688002, 1348, 'fa09e', 0001, '如图所示，驾驶机动车通过该路段时应交替使用远、近光灯。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第五十九条：机动车在<b>夜间通过急弯</b>、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，<b>应当交替使用远近光灯示意</b>。因此，题干表述正确。', 'https://timg.mnks.cn/20230926b1cd18ae.jpg', '交替使用', 0002, 0.05);
INSERT INTO `question` VALUES (1733399829276631041, 1349, 'e7b63', 0001, '车辆、行人应当按照交通信号通行；遇有交通警察现场指挥时，应当按照交通警察的指挥通行。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第三十八条：车辆、行人应当按照交通信号通行；<b>遇有交通警察现场指挥时，应当按照交通警察的指挥通行；</b>在没有交通信号的道路上，应当在确保安全、畅通的原则下通行。', '', '应当按照', 0001, 0.01);
INSERT INTO `question` VALUES (1733399829322768386, 1350, 'fb81f', 0001, '夜间会车时，对面来车使用远光灯，会给驾驶人造成眩目，而看不清前方道路情况，易引发事故。', NULL, '对', '错', '空', '空', '对', '夜间会车使用远光灯，会使得对面车辆驾驶人<b>因强烈灯光照射造成炫目而无法看清前方道路情况</b>，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', '', '会车|造成眩目', 0001, 0.01);
INSERT INTO `question` VALUES (1733399829385682946, 1351, '02dec', 0002, '驾驶机动车在道路上违反道路交通安全法的行为，属于什么行为？ ', 'A、违章行为 B、违法行为 C、过失行为 D、违规行为', '违章行为 ', '违法行为 ', '过失行为 ', '违规行为', 'B', '<b>驾驶机动车在道路上违反道路交通安全法的行为属于违法行为。</b>根据《道路交通安全法》第二条规定：中华人民共和国境内的车辆驾驶人、行人、乘车人以及与道路交通活动有关的单位和个人，都<b>应当遵守本法</b>。<br>第八十八条，对道路交通安全违法行为的处罚种类包括：警告、罚款、暂扣或者吊销机动车驾驶证、拘留。', '', '违反道路交通安全法', 0002, 0.04);
INSERT INTO `question` VALUES (1733399829423431682, 1352, '0d977', 0002, '机动车驾驶人违法驾驶造成重大交通事故构成犯罪的，依法追究什么责任？ ', 'A、刑事责任 B、民事责任 C、经济责任 D、直接责任', '刑事责任 ', '民事责任 ', '经济责任 ', '直接责任', 'A', '《道路交通安全法》第一百零一条，违反道路交通安全法律、法规规定，发生重大交通事故，<b>构成犯罪</b>的，依法<b>追究刑事责任</b>，并由公安机关交通管理部门吊销机动车驾驶证。', '', '构成犯罪', 0002, 0.04);
INSERT INTO `question` VALUES (1733399829473763329, 1353, '84832', 0001, '驾驶机动车违反道路交通安全法律法规发生交通事故属于交通违章行为。', NULL, '对', '错', '空', '空', '错', '驾驶机动车在道路上<b>违反道路交通安全法的行为</b>属于<b>违法行为</b>。 <br> 依据《道路交通安全法》第二条规定，中华人民共和国境内的车辆驾驶人、行人、乘车人以及与道路交通活动有关的单位和个人，都应当遵守本法。 <br>第八十八条规定，对道路交通安全<b>违法行为</b>的处罚种类包括：警告、罚款、暂扣或者吊销机动车驾驶证、拘留。', '', '违反|违章行为', 0004, 0.24);
INSERT INTO `question` VALUES (1733399829519900673, 1354, '3fd79', 0001, '驾驶机动车在道路上违反道路通行规定应当接受相应的处罚。', NULL, '对', '错', '空', '空', '对', '驾驶机动车在道路上违反道路交通安全法的行为属于违法行为。 依据《道路交通安全法》第八十七条，公安机关交通管理部门及其交通警察应当依据事实和本法的有关规定对<b>道路交通安全违法行为予以处罚。</b>对于情节轻微，未影响道路通行的，指出违法行为，给予口头警告后放行。', '', '违反|规定', 0001, 0.02);
INSERT INTO `question` VALUES (1733399829561843714, 1355, 'bd93b', 0001, '对违法驾驶发生重大交通事故且构成犯罪的，不追究其刑事责任。', NULL, '对', '错', '空', '空', '错', '<b>犯罪要追究刑事责任。</b>《道路交通安全法》第一百零一条规定：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，<b>依法追究刑事责任</b>，并由公安机关交通管理部门吊销机动车驾驶证。', '', '不追究', 0003, 0.08);
INSERT INTO `question` VALUES (1733399829624758273, 1356, '8525d', 0001, '驾驶机动车在道路上违反交通安全法规的行为属于违法行为。', NULL, '对', '错', '空', '空', '对', '<b>驾驶机动车在道路上违反道路交通安全法的行为属于违法行为。</b>根据《道路交通安全法》第二条规定，中华人民共和国境内的车辆驾驶人、行人、乘车人以及与道路交通活动有关的单位和个人，都应当遵守本法。 <br>第八十八条&nbsp;对道路交通安全违法行为的处罚种类包括：警告、罚款、暂扣或者吊销机动车驾驶证、拘留。', '', '违法行为', 0001, 0.03);
INSERT INTO `question` VALUES (1733399829666701313, 1357, '85990', 0001, '上路行驶的机动车未随车携带身份证的，交通警察可依法扣留机动车。', NULL, '对', '错', '空', '空', '错', '<b>身份证，不是驾驶人必须要随车携带的证件</b>。《道路交通安全法》第九十五条规定：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者<b>未随车携带行驶证、驾驶证</b>的，公安机关交通管理部门应当扣留机动车。', '', '身份证|依法扣留', 0004, 0.17);
INSERT INTO `question` VALUES (1733399829708644354, 1358, 'a6565', 0001, '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失的可能处3年以下徒刑或拘役。', NULL, '对', '错', '空', '空', '对', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役。', '', '重大损失|拘役', 0003, 0.14);
INSERT INTO `question` VALUES (1733399829754781698, 1359, '1fd42', 0001, '驾驶人违反交通运输管理法规发生重大事故后，逃逸或者有其他特别恶劣情节的，处7年以上有期徒刑。', NULL, '对', '错', '空', '空', '错', '【事故<b>后逃逸</b>：<b>3年以上7年以下</b>】《刑法》第一百三十三条规定：1、违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；<br>2、<b>交通运输肇事<b>后逃逸</b>或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；</b><br>3、因逃逸致人死亡的，处七年以上有期徒刑。', '', '逃逸|有期徒刑', 0005, 0.39);
INSERT INTO `question` VALUES (1733399829792530434, 1360, 'a7d77', 0001, '驾驶人一边驾车，一边打手持电话是违法行为。', NULL, '对', '错', '空', '空', '对', '<b>边开车边接打电话是一种妨碍安全驾驶的违法行为</b>。《道路交通安全法实施条例》第六十二条 驾驶机动车<b>不得有下列行为</b>：(三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为；<br>【<b>拓展知识</b>】<br>驾驶人一边驾驶一边做或想其他使注意力分散的事情，违背了安全驾驶的“集中注意力”这个黄金原则，对安全行车非常不利。', '', '打手持电话', 0003, 0.09);
INSERT INTO `question` VALUES (1733399829834473473, 1361, '996f6', 0002, '下列哪种行为会受到200元以上2000元以下罚款，并处吊销机动车驾驶证？ ', 'A、违反道路通行规定 B、超过规定时速50% C、造成交通事故后逃逸 D、驾车没带驾驶证', '违反道路通行规定 ', '超过规定时速50% ', '造成交通事故后逃逸 ', '驾车没带驾驶证', 'B', '【超速50%的行政处罚】罚款200元以上2000元以下，吊销驾驶证。《道路交通安全法》第九十九条　有下列行为之一的，由公安机关交通管理部门处<b>二百元以上二千元以下罚款</b>：<br>（一）未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的；<br>（二）将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的；<br>（三）造成交通事故后逃逸，<b>尚不构成犯罪的</b>；<br><b>（四）机动车行驶超过规定时速百分之五十的；（超过规定时速50%）</b><br>（五）强迫机动车驾驶人违反道路交通安全法律、法规和机动车安全驾驶要求驾驶机动车，造成交通事故，尚不构成犯罪的；<br>行为人有前款第二项、<b>第四项情形</b>之一的，可以并处<b>吊销机动车驾驶证</b>；<br><b>因此，满足既要罚款200-2000，又要被吊销驾驶证题干要求的，只有超速50%选项正确。</b>', '', '罚款|吊销', 0005, 0.44);
INSERT INTO `question` VALUES (1733399829901582338, 1362, '12eb5', 0002, '这位驾驶人违反法律规定的行为是什么？ ', 'A、没按规定握转向盘 B、座椅角度不对 C、没系安全带 D、驾驶姿势不正确', '没按规定握转向盘 ', '座椅角度不对 ', '没系安全带 ', '驾驶姿势不正确', 'C', '《道路交通安全法》第五十一条，机动车行驶时，驾驶人、乘坐人员<b>应当按规定使用安全带</b>，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。如图所示，驾驶人没有系安全带。', 'https://timg.mnks.cn/20230927d0014553.jpg', '违反法律规定', 0001, 0.01);
INSERT INTO `question` VALUES (1733399829939331074, 1363, '440f9', 0001, '驾驶机动车在上道路行驶前驾驶人要按规定系好安全带。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当<b>按规定使用安全带</b>，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。<br>【拓展知识】<br>座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。', '', '按规定|前', 0001, 0.01);
INSERT INTO `question` VALUES (1733399829977079810, 1364, '39fe8', 0001, '机动车上路行驶时，前排乘车人可不系安全带。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。', '', '可不', 0001, 0.03);
INSERT INTO `question` VALUES (1733399830031605761, 1365, '7e701', 0001, '机动车行驶中，车上少年儿童可不使用安全带。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。<b>驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法</b>，在遇到意外危险情况时可避免受到致命的伤害。', '', '可不', 0001, 0.03);
INSERT INTO `question` VALUES (1733399830077743105, 1366, '70b02', 0002, '找出这辆故障车有哪种违法行为？ ', 'A、没有设置警告标志 B、没有开启危险报警闪光灯 C、没有将车停到路边 D、没有立即排除故障', '没有设置警告标志 ', '没有开启危险报警闪光灯 ', '没有将车停到路边 ', '没有立即排除故障', 'A', '当发生事故或者车辆故障时，牢记第一时间打开双闪灯。在确保安全的前提下，立即将车移动至右侧应急车道，<b>在车辆后方正确放置三角警示牌</b>，提醒后方来车注意安全。《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；<b>警告标志应当设置在故障车来车方向150米以外</b>，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 'https://timg.mnks.cn/20230927ee12d711.jpg', '违法行为', 0003, 0.12);
INSERT INTO `question` VALUES (1733399830119686146, 1367, '3b0fc', 0001, '这辆停在路边的机动车没有违法行为。', NULL, '对', '错', '空', '空', '错', '<b>图中故障车没有开启危险报警闪光灯，是违法行为。</b>《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当<b>按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志</b>，夜间还应当同时开启示廓灯和后位灯。', 'https://timg.mnks.cn/20230927df634398.jpg', '没有违法', 0002, 0.07);
INSERT INTO `question` VALUES (1733399830174212097, 1368, '7d0ce', 0002, '这样在路边临时停放机动车有什么违法行为？ ', 'A、在非机动车道停车 B、停车占用机动车道 C、距离路边超过30厘米 D、在有禁停标线路段停车', '在非机动车道停车 ', '停车占用机动车道 ', '距离路边超过30厘米 ', '在有禁停标线路段停车', 'D', '如图所示右侧路缘石上的<b>黄色实线</b>，是<b>禁止停车【标线】</b>，表明此处禁止停、放车辆。因此，这样在路边临时停放机动车，违反了禁停规定，即违法行为是：<b>在有禁停标线路段停车。</b>重点看标线。', 'https://timg.mnks.cn/20230927d9ef596e.jpg', '临时停放', 0004, 0.19);
INSERT INTO `question` VALUES (1733399830237126657, 1369, '05cc3', 0002, '这样在路边临时停放机动车有什么违法行为？ ', 'A、在非机动车道停车 B、在人行横道上停车 C、距离路边超过30厘米 D、在有禁停标线路段停车', '在非机动车道停车 ', '在人行横道上停车 ', '距离路边超过30厘米 ', '在有禁停标线路段停车', 'B', '《道路交通安全法实施条例》第六十三条：在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及<b>人行横道</b>、施工地段<b>，不得停车</b>。', 'https://timg.mnks.cn/20230927c621446a.jpg', '临时停放', 0003, 0.09);
INSERT INTO `question` VALUES (1733399830304235521, 1370, '99729', 0002, '这样临时停放红色轿车有什么违法行为？ ', 'A、距离加油站不到30米 B、停车占用非机动车道 C、距离路边超过30厘米 D、在有禁停标线路段停车', '距离加油站不到30米 ', '停车占用非机动车道 ', '距离路边超过30厘米 ', '在有禁停标线路段停车', 'A', '《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：(三)公共汽车站、急救站、<b>加油站</b>、消防栓或者消防队(站)门前以及<b>距离上述地点30米以内的路段</b>，除使用上述设施的以外，<b>不得停车</b>；', 'https://timg.mnks.cn/20230927d3928703.jpg', '临时停放', 0002, 0.08);
INSERT INTO `question` VALUES (1733399830354567170, 1371, '1494e', 0002, '这样停放机动车有什么违法行为？ ', 'A、停车占用人行道 B、在公共汽车站停车 C、在有禁停标志路段停车 D、在非机动车道停车', '停车占用人行道 ', '在公共汽车站停车 ', '在有禁停标志路段停车 ', '在非机动车道停车', 'B', '《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：(三)<b>公共汽车站</b>、急救站、加油站、消防栓或者消防队(站)门前以及<b>距离上述地点30米以内的路段</b>，除使用上述设施的以外，<b>不得停车</b>；', 'https://timg.mnks.cn/20230927fb52c1dd.jpg', '停放', 0002, 0.07);
INSERT INTO `question` VALUES (1733399830396510210, 1372, '6bb2e', 0002, '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失，可能会受到什么刑罚？ ', 'A、处5年以上徒刑 B、处3年以下徒刑或者拘役 C、处3年以上徒刑 D、处3年以上7年以下徒刑', '处5年以上徒刑 ', '处3年以下徒刑或者拘役 ', '处3年以上徒刑 ', '处3年以上7年以下徒刑', 'B', '《中华人民共和国刑法》第一百三十三条，【交通肇事罪】<b>违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失</b>的，处<b>三年以下有期徒刑或者拘役</b>；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', '', '重大损失', 0004, 0.23);
INSERT INTO `question` VALUES (1733399830442647554, 1373, '7daae', 0002, '驾驶人违反交通运输管理法规发生重大事故致人重伤、死亡，可能会受到什么刑罚？ ', 'A、处3年以下徒刑或者拘役 B、处3年以上7年以下徒刑 C、处5年以上徒刑 D、处7年以上徒刑', '处3年以下徒刑或者拘役 ', '处3年以上7年以下徒刑 ', '处5年以上徒刑 ', '处7年以上徒刑', 'A', '《中华人民共和国刑法》第一百三十三条，【交通肇事罪】<b>违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失</b>的，处<b>三年以下有期徒刑或者拘役</b>；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', '', '重大事故', 0005, 0.41);
INSERT INTO `question` VALUES (1733399830480396289, 1374, 'a6b53', 0001, '驾驶人违反交通运输管理法规发生重大事故致人死亡的处3年以上有期徒刑。', NULL, '对', '错', '空', '空', '错', '【<b>无逃逸：3年以下</b>】据《刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，<b>处三年以下有期徒刑或者拘役</b>。因此，题干表述错误。', '', '致人死亡|3年以上', 0005, 0.33);
INSERT INTO `question` VALUES (1733399830547505154, 1375, 'acda3', 0002, '驾驶人违反交通运输管理法规发生重大事故致人死亡且逃逸的，处多少年有期徒刑？ ', 'A、7年以上 B、3年以下 C、3年以上7年以下 D、10年以上', '7年以上 ', '3年以下 ', '3年以上7年以下 ', '10年以上', 'C', '《刑法》第一百三十三条规定： 1、违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；【未逃逸】<br> 2、交通运输<b>肇事后逃逸</b>或者有其他特别恶劣情节的，处<b>三年以上七年以下</b>有期徒刑；【<b>死亡且逃逸</b>】<br>3、因逃逸致人死亡的，处七年以上有期徒刑。【逃逸后死亡】<br>【普法内容】<b>致人死亡且逃逸和逃逸致人死亡的区别：</b><br>1、法律定义不同。逃逸致死亡：交通事故发生的时候，被害人还有生命体征，但是如果不及时就医可能有生命危险，肇事者害怕自己受到处罚，驾车逃跑，没有及时将受害人送到医院，造成受害人死亡的情况；致人死亡且逃逸：当场把人撞死了，之后怕受到惩罚逃跑。<br>2、量刑不同：交通肇事罪中，逃逸致人死亡的，处七年以上有期徒刑，而交通肇事后逃逸的，处三年以上七年以下有期徒刑。也就是说，逃逸致人死亡的违法行为定刑要高。', '', '死亡且逃逸', 0004, 0.27);
INSERT INTO `question` VALUES (1733399830593642497, 1376, '394ec', 0002, '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处多少年有期徒刑？ ', 'A、2年以下 B、3年以下 C、7年以下 D、7年以上', '2年以下 ', '3年以下 ', '7年以下 ', '7年以上', 'D', '《中华人民共和国刑法》第一百三十三条：因<b>逃逸致人死亡</b>的，处<b>七年以上</b>有期徒刑。<br>【普法内容】<br><b>致人死亡且逃逸和逃逸致人死亡的区别：</b><br>1、法律定义不同。逃逸致死亡：交通事故发生的时候，被害人还有生命体征，但是如果不及时就医可能有生命危险，肇事者害怕自己受到处罚，驾车逃跑，没有及时将受害人送到医院，造成受害人死亡的情况；致人死亡且逃逸：当场把人撞死了，之后怕受到惩罚逃跑。<br>2、量刑不同：交通肇事罪中，逃逸致人死亡的，处七年以上有期徒刑，而交通肇事后逃逸的，处三年以上七年以下有期徒刑。也就是说，逃逸致人死亡的违法行为定刑要高。', '', '逃逸致人死亡', 0005, 0.27);
INSERT INTO `question` VALUES (1733399830631391234, 1377, 'dbdb6', 0001, '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处3年以上7年以下有期徒刑。', NULL, '对', '错', '空', '空', '错', '【<b>逃逸致死</b>：<b>7年以上</b>】发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；<b>因逃逸致人死亡的，处七年以上有期徒刑。</b><br>【普法内容】<br><b>致人死亡且逃逸和逃逸致人死亡的区别：</b><br>1、法律定义不同。逃逸致死亡：交通事故发生的时候，被害人还有生命体征，但是如果不及时就医可能有生命危险，肇事者害怕自己受到处罚，驾车逃跑，没有及时将受害人送到医院，造成受害人死亡的情况；致人死亡且逃逸：当场把人撞死了，之后怕受到惩罚逃跑。<br>2、量刑不同：交通肇事罪中，逃逸致人死亡的，处七年以上有期徒刑，而交通肇事后逃逸的，处三年以上七年以下有期徒刑。也就是说，逃逸致人死亡的违法行为定刑要高。', '', '逃逸致人死亡|3年以上7年以下', 0005, 0.43);
INSERT INTO `question` VALUES (1733399830681722881, 1378, 'e9599', 0002, '驾驶机动车在道路上追逐竞驶，情节恶劣，会受到什么处罚？ ', 'A、处拘役，并处罚金 B、处管制，并处罚金 C、处1年以上徒刑 D、处6个月徒刑', '处拘役，并处罚金 ', '处管制，并处罚金 ', '处1年以上徒刑 ', '处6个月徒刑', 'A', '《刑法》第一百三十三条 ，在道路上驾驶机动车，有下列情形之一的，<b>处拘役，并处罚金</b>： （一）<b>追逐竞驶，情节恶劣的</b>； （二）醉酒驾驶机动车的； （三）从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶的； （四）违反危险化学品安全管理规定运输危险化学品，危及公共安全的。 机动车所有人、管理人对前款第三项、第四项行为负有直接责任的，依照前款的规定处罚。 有前两款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', '', '追逐竞驶', 0003, 0.11);
INSERT INTO `question` VALUES (1733399830732054530, 1379, '83a9a', 0001, '驾驶人在道路上驾驶机动车追逐竞驶，情节恶劣的处3年以下有期徒刑。', NULL, '对', '错', '空', '空', '错', '<b>【追逐竞驶，处拘役处罚金】</b>《刑法》第一百三十三条之一 ，在道路上驾驶机动车，有下列情形之一的，<b>处拘役，并处罚金</b>： <b>（一）追逐竞驶，情节恶劣的；</b> （二）醉酒驾驶机动车的； （三）从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶的； （四）违反危险化学品安全管理规定运输危险化学品，危及公共安全的。 机动车所有人、管理人对前款第三项、第四项行为负有直接责任的，依照前款的规定处罚。 有前两款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', '', '追逐竞驶|有期徒刑', 0005, 0.42);
INSERT INTO `question` VALUES (1733399830790774785, 1380, '0d2db', 0001, '因避让特种车辆而发生违法行为，被电子警察拍到时，可向交管部门复议。', NULL, '对', '错', '空', '空', '对', '根据规定，遇到特种车辆在执行紧急任务时，一定要让出通道，给特种车辆让行。<b>如果因为给特种车辆让道而出现的违法行为，</b><b>可以通过向交管部门申请行政复议，撤销违法行为的处罚</b>。', '', '特种车辆', 0001, 0.03);
INSERT INTO `question` VALUES (1733399830836912129, 1381, 'd5a61', 0002, '醉酒驾驶机动车在道路上行驶会受到什么处罚？ ', 'A、处2年以下徒刑 B、处拘役，并处罚金 C、处2年以上徒刑 D、处管制，并处罚金', '处2年以下徒刑 ', '处拘役，并处罚金 ', '处2年以上徒刑 ', '处管制，并处罚金', 'B', '《中华人民共和国刑法》第一百三十三条之一，【危险驾驶罪】在道路上驾驶机动车，有下列情形之一的，<b>处拘役，并处罚金</b>：<br>（一）追逐竞驶，情节恶劣的；<br><b>（二）醉酒驾驶机动车的；</b><br>（三）从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶的；<br>（四）违反危险化学品安全管理规定运输危险化学品，危及公共安全的。<br>机动车所有人、管理人对前款第三项、第四项行为负有直接责任的，依照前款的规定处罚。', '', '醉酒', 0003, 0.12);
INSERT INTO `question` VALUES (1733399830874660866, 1382, '2c748', 0001, '驾驶人在道路上醉酒驾驶机动车的处3年以上有期徒刑。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第九十一条，饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。<br><b>醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；五年内不得重新取得机动车驾驶证。</b>', '', '醉酒|3年以上', 0004, 0.22);
INSERT INTO `question` VALUES (1733399830912409602, 1383, 'f1e81', 0002, '以下哪种行为处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证？ ', 'A、醉酒驾驶机动车的 B、故意遮挡机动车号牌的 C、使用其他车辆保险标志的 D、因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的', '醉酒驾驶机动车的 ', '故意遮挡机动车号牌的 ', '使用其他车辆保险标志的 ', '因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的', 'D', '根据《道路交通安全法》第九十一条规定，饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。<b>因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。</b>', '', '一千元以上二千元以下', 0003, 0.15);
INSERT INTO `question` VALUES (1733399830954352642, 1384, '49277', 0002, '驾驶人有哪种情形，交通警察可依法扣留机动车驾驶证？ ', 'A、饮酒后驾驶机动车 B、超过规定速度10% C、疲劳后驾驶机动车 D、行车中未系安全带', '饮酒后驾驶机动车 ', '超过规定速度10% ', '疲劳后驾驶机动车 ', '行车中未系安全带', 'A', '六种情形下可以扣留驾驶证。新法规定，有下列情形之一的，可以扣留机动车驾驶证：<br> <b> (一)饮酒、醉酒后驾驶机动车的；</b> <br>(二)机动车驾驶人将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的； <br>(三)机动车行驶超过规定时速百分之五十的； <br>(四)驾驶拼装或者已达到报废标准的机动车的； <br>(五)发生重大交通事故，构成犯罪的； <br>(六)在一个记分周期内累积记分达到12分的。', '', '驾驶证', 0002, 0.06);
INSERT INTO `question` VALUES (1733399830996295681, 1385, 'bacdf', 0001, '驾驶机动车造成交通事故后逃逸，尚不构成犯罪的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处15日以下拘留。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十九条，有下列行为之一的，由公安机关交通管理部门处<b>二百元以上二千元以下罚款</b>： <br><b>（三）</b>造成交通事故后逃逸，尚不构成犯罪的； <br>行为人有前款第一项、<b>第三项</b>、第五项至第八项情形之一的，可以<b>并处十五日以下拘留</b>。', '', '逃逸|不构成犯罪', 0003, 0.09);
INSERT INTO `question` VALUES (1733399831042433026, 1386, '4f855', 0002, '驾驶机动车跨越双实线行驶属于什么行为？ ', 'A、违章行为 B、违法行为 C、过失行为 D、违规行为', '违章行为 ', '违法行为 ', '过失行为 ', '违规行为', 'B', '《道路交通安全法》第四十四条　机动车通过交叉路口，<b>应当按照</b>交通信号灯、交通标志、<b>交通标线</b>或者交通警察的指挥通过；通过没有交通信号灯、交通标志、<b>交通标线</b>或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通行的车辆先行。<br>路面双实线属于禁止标线，机动车禁止跨越。因此，<b>违反道路交通安全法的行为属于违法行为，</b>驾驶机动车违反禁令标志、禁止标线指示的违法行为，一次记1分。', '', '双实线', 0003, 0.10);
INSERT INTO `question` VALUES (1733399831105347586, 1387, '39e04', 0002, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪？ ', 'A、未及时报警 B、未抢救受伤人员 C、严重超载驾驶的 D、未带驾驶证', '未及时报警 ', '未抢救受伤人员 ', '严重超载驾驶的 ', '未带驾驶证', 'C', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释》：<b>交通肇事致一人以上重伤，负事故全部或者主要责任，</b>并具有下列情形之一的，以<b>交通肇事罪定罪</b>处罚： <br>(一)酒后、吸食毒品后驾驶机动车辆的; <br>(二)无驾驶资格驾驶机动车辆的; <br>(三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; <br>(四)明知是无牌证或者已报废的机动车辆而驾驶的; <br><b>(五)严重超载驾驶的;</b> <br>(六)为逃避法律追究逃离事故现场的。', '', '交通肇事罪', 0004, 0.24);
INSERT INTO `question` VALUES (1733399831147290625, 1388, 'da25f', 0002, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪？ ', 'A、未报警 B、未抢救受伤人员 C、酒后、吸食毒品后驾驶机动车辆的 D、未带驾驶证', '未报警 ', '未抢救受伤人员 ', '酒后', '吸食毒品后驾驶机动车辆的 D', 'C', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释：交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列情形之一的，以<b>交通肇事罪定罪</b>处罚： <br>(一)<b>酒后、吸食毒品后驾驶机动车辆的;</b> <br>(二)无驾驶资格驾驶机动车辆的; <br>(三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; <br>(四)明知是无牌证或者已报废的机动车辆而驾驶的; <br>(五)严重超载驾驶的; <br>(六)为逃避法律追究逃离事故现场的。', '', '交通肇事罪', 0002, 0.08);
INSERT INTO `question` VALUES (1733399831189233666, 1389, 'da9e9', 0002, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪？ ', 'A、未带驾驶证 B、未报警 C、无驾驶资格驾驶机动车辆的 D、未抢救受伤人员', '未带驾驶证 ', '未报警 ', '无驾驶资格驾驶机动车辆的 ', '未抢救受伤人员', 'C', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释》：交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列情形之一的，以<b>交通肇事罪定罪</b>处罚： <br>(一)酒后、吸食毒品后驾驶机动车辆的; <br>(二)<b>无驾驶资格驾驶机动车辆的;</b> <br>(三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; <br>(四)明知是无牌证或者已报废的机动车辆而驾驶的; <br>(五)严重超载驾驶的; <br>(六)为逃避法律追究逃离事故现场的。', '', '交通肇事罪', 0003, 0.13);
INSERT INTO `question` VALUES (1733399831231176706, 1390, '4f971', 0002, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪？ ', 'A、未抢救受伤人员 B、未带驾驶证 C、未报警 D、为逃避法律追究逃离事故现场的', '未抢救受伤人员 ', '未带驾驶证 ', '未报警 ', '为逃避法律追究逃离事故现场的', 'D', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释》：交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列情形之一的，以<b>交通肇事罪定罪</b>处罚： <br>(一)酒后、吸食毒品后驾驶机动车辆的; <br>(二)无驾驶资格驾驶机动车辆的; <br>(三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; <br>(四)明知是无牌证或者已报废的机动车辆而驾驶的; <br>(五)严重超载驾驶的; <br>(六)<b>为逃避法律追究逃离事故现场的。</b>', '', '交通肇事罪', 0002, 0.08);
INSERT INTO `question` VALUES (1733399831285702658, 1391, 'bbd55', 0001, '当驾驶人的血液中酒精含量为100毫克/100毫升时，属于醉酒驾驶。', NULL, '对', '错', '空', '空', '对', '驾驶人的血液中酒精含量在80毫克/100毫升（含）以上属于<b>醉酒驾驶</b>；驾驶人血液中的酒精含量大于或者等于20毫克/100毫升，小于80毫克/100毫升时，属于酒后驾驶。', '', '醉酒驾驶', 0002, 0.07);
INSERT INTO `question` VALUES (1733399831331840001, 1392, 'c45de', 0002, '以下哪项行为可构成危险驾驶罪？ ', 'A、闯红灯 B、无证驾驶 C、疲劳驾驶 D、醉驾', '闯红灯 ', '无证驾驶 ', '疲劳驾驶 ', '醉驾', 'D', '根据《刑法》第一百三十三条的规定：<b>危险驾驶罪</b>是指在道路上驾驶机动车追逐竞驶，情节恶劣的；<b>醉酒驾驶机动车的</b>；从事校车业务或者旅客运输，严重超过定额乘员载客，或者严重超过规定时速行驶的；违反危险化学品安全管理规定运输危险化学品，危害公共安全的行为。', '', '可构成', 0003, 0.11);
INSERT INTO `question` VALUES (1733399831377977346, 1393, '606af', 0002, '机动车驾驶人血液中酒精含量大于或者等于多少可认定为醉驾？ ', 'A、20毫克/100毫升 B、60毫克/100毫升 C、80毫克/100毫升 D、50毫克/100毫升', '20毫克/100毫升 ', '60毫克/100毫升 ', '80毫克/100毫升 ', '50毫克/100毫升', 'C', '当驾驶人血液中的酒精含量大于或者等于20毫克/100毫升，小于80毫克/100毫升时，就是酒后驾驶；<b>当驾驶人血液中的酒精含量大于或者等于80毫克/100毫升时，就是醉酒驾驶</b>。醉酒驾驶触犯了刑法，是会受到刑事处罚的。【20/80分酒醉：20酒驾；80醉驾】', '', '酒精含量|醉驾', 0003, 0.13);
INSERT INTO `question` VALUES (1733399831424114689, 1394, '4e7d9', 0001, '如图所示，红圈标注的深色车辆的做法是违法的。', NULL, '对', '错', '空', '空', '对', '如图所示，红圈标注的深色车辆骑轧道路中心线行驶，影响其他车辆行驶，极易引发交通事故，是违反道路交通安全法的行为。', 'https://timg.mnks.cn/20230926a8d446c4.jpg', '红圈标注', 0002, 0.06);
INSERT INTO `question` VALUES (1733399831466057730, 1395, '81c71', 0002, '关于醉酒驾驶机动车的处罚，以下说法错误的是什么？ ', 'A、公安机关交通管理部门约束至酒醒 B、吊销驾驶证 C、五年内不得重新取得机动车驾驶证 D、记6分', '公安机关交通管理部门约束至酒醒 ', '吊销驾驶证 ', '五年内不得重新取得机动车驾驶证 ', '记6分', 'D', '<b>请注意，题干要求选出说法错误的选项。</b>根据《道路交通安全法》第九十一条规定，<b>醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；五年内不得重新取得机动车驾驶证。</b><br>《道路交通安全违法行为记分分值》一、机动车驾驶人有下列违法行为之一，<b>一次记12分</b> ：<br>（二）饮酒后驾驶机动车的；', '', '醉酒驾驶|说法错误', 0004, 0.22);
INSERT INTO `question` VALUES (1733399831541555202, 1396, 'd3984', 0002, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪？ ', 'A、未带驾驶证 B、未报警 C、明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的 D、未抢救受伤人员的', '未带驾驶证 ', '未报警 ', '明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的 ', '未抢救受伤人员的', 'C', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释》：交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列情形之一的，以<b>交通肇事罪定罪</b>处罚： <br>(一)酒后、吸食毒品后驾驶机动车辆的; <br>(二)无驾驶资格驾驶机动车辆的; <br><b>(三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; </b> <br>(四)明知是无牌证或者已报废的机动车辆而驾驶的; <br>(五)严重超载驾驶的; <br>(六)为逃避法律追究逃离事故现场的。', '', '交通肇事罪', 0003, 0.11);
INSERT INTO `question` VALUES (1733399831596081153, 1397, 'ad076', 0002, '如图，请判断左侧这辆小型客车有几种违法行为？ ', 'A、有两种违法行为 B、有三种违法行为 C、有四种违法行为 D、有五种违法行为', '有两种违法行为 ', '有三种违法行为 ', '有四种违法行为 ', '有五种违法行为', 'C', '图中左侧这辆小型客车共有<b>四种</b>违法行为：1、开车打电话；2、未系安全带；3、未悬挂机动车号牌；4、占用应急车道。', 'https://timg.mnks.cn/20230926f2a6c5c0.jpg', '左侧|违法行为', 0005, 0.27);
INSERT INTO `question` VALUES (1733399831642218498, 1398, 'b08b1', 0002, '请判断图中这辆黄色机动车有几种违法行为？ ', 'A、有一种违法行为 B、有两种违法行为 C、有三种违法行为 D、有四种违法行为', '有一种违法行为 ', '有两种违法行为 ', '有三种违法行为 ', '有四种违法行为', 'C', '如图所示，黄色机动车的违法行为有：1、交叉路口处不得超车（实线）；2、未按规定使用灯光；3、未与被超车辆保持安全距离。', 'https://timg.mnks.cn/20230926ff51c395.jpg', '黄色', 0004, 0.21);
INSERT INTO `question` VALUES (1733399831692550145, 1399, 'e61c7', 0002, '请判断图中这辆小型客车存在什么违法行为？ ', 'A、强行从右侧超车 B、越黄线行驶 C、占用专用车道 D、未开启右转向灯', '强行从右侧超车 ', '越黄线行驶 ', '占用专用车道 ', '未开启右转向灯', 'C', '最右车道上写有公交字样，占用公交专用道属于违法行为。', 'https://timg.mnks.cn/20230926b8d6d15b.jpg', '违法行为', 0002, 0.04);
INSERT INTO `question` VALUES (1733399831734493185, 1400, 'cbe0b', 0002, '请判断图中右侧灰色机动车逆向行驶是属于什么行为？ ', 'A、违法行为 B、违规行为 C、违章行为 D、违纪行为', '违法行为 ', '违规行为 ', '违章行为 ', '违纪行为', 'A', '如图所示，右侧灰色机动车在<b>逆向行驶</b>，是违反道路交通安全法的行为，属于<b>违法行为</b>。', 'https://timg.mnks.cn/20230926a3aeb8f2.jpg', '灰色|逆向行驶', 0002, 0.06);
INSERT INTO `question` VALUES (1733399831776436225, 1401, 'cf0a9', 0002, '图中车辆存在什么违法行为？ ', 'A、没开启远光灯 B、没有及时让行 C、占用内侧车道 D、没开启灯光', '没开启远光灯 ', '没有及时让行 ', '占用内侧车道 ', '没开启灯光', 'D', '<b>图中车辆的违法行为是，没按规定开启车灯。</b>《道路交通安全法实施条例》第五十八条，<b>机动车在夜间没有路灯、照明不良</b>或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，<b>应当开启前照灯、示廓灯和后位灯</b>，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 'https://timg.mnks.cn/20230926fe862de2.jpg', '违法行为', 0002, 0.07);
INSERT INTO `question` VALUES (1733399831814184961, 1402, 'd66c9', 0002, '图中前方机动车存在什么违法行为？ ', 'A、没开启信号灯 B、没有及时让行 C、没开启远光灯 D、行驶速度缓慢', '没开启信号灯 ', '没有及时让行 ', '没开启远光灯 ', '行驶速度缓慢', 'A', '雨天行车应合理使用灯光，灯光的作用不只是照明，更重要的是让别人看到你，知道你下一步要做什么来规避危险： <br>① 在雨势较小、光线还可以的时候，开示宽灯和近光灯； <br>② 在雨势比较大、光线不太好的时候，同时打开雾灯； <br>③ 如果遇到大暴雨，能见度非常低时，那就尽量不要开车了，把车停在路边，打开双闪，等雨势稍微小一点再走。 <br>前方机动车未开启任何信号灯，属于违法行为。', 'https://timg.mnks.cn/20230926fc4b1467.jpg', '违法行为', 0002, 0.07);
INSERT INTO `question` VALUES (1733399831860322306, 1403, 'a09b6', 0001, '对行驶中的公共交通工具的驾驶人员使用暴力或者抢控驾驶操纵装置，干扰公共交通工具正常行驶，危及公共安全构成犯罪的，处一年以下有期徒刑、拘役或管制，并处或单处罚金。', NULL, '对', '错', '空', '空', '对', '《中华人民共和国刑法》第一百三十三条之二 【妨害安全驾驶罪】<b>对行驶中的公共交通工具的驾驶人员使用暴力或者抢控驾驶操纵装置，干扰公共交通工具正常行驶，危及公共安全的，处一年以下有期徒刑、拘役或者管制，并处或者单处罚金</b>。<br>前款规定的驾驶人员在行驶的公共交通工具上擅离职守，与他人互殴或者殴打他人，危及公共安全的，依照前款的规定处罚。<br>【择一重处】有前两款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', '', '使用暴力|公共安全', 0002, 0.04);
INSERT INTO `question` VALUES (1733399831902265346, 1404, 'b5c2e', 0001, '驾驶人员在行驶的公共交通工具上擅离职守，与他人互殴或者殴打他人，危及公共安全，构成犯罪的，处一年以下有期徒刑、拘役或者管制，并处或者单处罚金。', NULL, '对', '错', '空', '空', '对', '《中华人民共和国刑法》第一百三十三条之二 【妨害安全驾驶罪】对行驶中的公共交通工具的驾驶人员使用暴力或者抢控驾驶操纵装置，干扰公共交通工具正常行驶，危及公共安全的，处一年以下有期徒刑、拘役或者管制，并处或者单处罚金。<br>前款规定的<b>驾驶人员在行驶的公共交通工具上擅离职守，与他人互殴或者殴打他人，危及公共安全的，依照前款的规定处罚。</b><br>【择一重处】有前两款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', '', '擅离职守|危及公共安全', 0002, 0.04);
INSERT INTO `question` VALUES (1733399831944208385, 1405, 'ce481', 0001, '机动车驾驶人违反交通管制的规定强行通行，不听劝阻的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处十五日以下拘留。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第九十九条，有下列行为之一的，由公安机关交通管理部门<b>处二百元以上二千元以下</b>罚款：<br><b>（六）违反交通管制的规定强行通行，不听劝阻的；</b><br>行为人<b>第五项至第八项情形之一的</b>，可以<b>并处十五日以下拘留</b>。<br>因此，违反交通管制的规定强行通行，不听劝阻的，罚款200-2000元，并处拘留15日。', '', '强行通行|二百元以上二千元以下', 0002, 0.05);
INSERT INTO `question` VALUES (1733399831986151425, 1406, 'a67df', 0001, '驾驶机动车在道路上车辆发生故障、事故停车后，不按规定使用灯光或者设置警告标志的，一次记3分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十一条 机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：<br>（十一）在道路上车辆发生故障、事故停车后，不按规定使用灯光或者设置警告标志的；', '', '故障|警告标志|3分', 0004, 0.24);
INSERT INTO `question` VALUES (1733399832023900162, 1407, 'f7247', 0002, '机动车驾驶人存在下列哪种交通违法行为的，一次记9分？ ', 'A、驾驶7座以上载客汽车载人超过核定人数百分之二十以上未达到百分之五十的 B、驾驶7座以上载客汽车载人超过核定人数百分之五十以上未达到百分之百的 C、驾驶除校车、公路客运汽车、旅游客运汽车以外的其他载客汽车载人超过核定人数百分之百以上的 D、驾驶校车、公路客运汽车、旅游客运汽车、7座以上载客汽车以外的其他载客汽车载人超过核定人数百分之二十以上未达到百分之五十的', '驾驶7座以上载客汽车载人超过核定人数百分之二十以上未达到百分之五十的 ', '驾驶7座以上载客汽车载人超过核定人数百分之五十以上未达到百分之百的 ', '驾驶除校车', '公路客运汽车', 'B', '《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，一次<b>记9分</b>：<br>（一）驾驶<b>7座以上</b>载客汽车载人超过核定人数<b>百分之五十以上未达到百分之百</b>的；<br>【拓展知识】<br>7座以上超载20%-50%的，记6分；其他载客超载100%以上的，记12分；其他载客超载20%-50%的，记3分。', '', '记9分', 0005, 0.43);
INSERT INTO `question` VALUES (1733399832070037505, 1408, 'b2502', 0002, '对道路交通安全违法行为的处罚种类不包括下列哪项？ ', 'A、罚款 B、训诫 C、警告 D、拘留', '罚款 ', '训诫 ', '警告 ', '拘留', 'B', '处罚种类不包括训诫。《道路交通安全法》第八十八条，对道路交通安全违法行为的<b>处罚种类</b>包括：警告、罚款、暂扣或者吊销机动车驾驶证、拘留。', '', '不包括', 0003, 0.14);
INSERT INTO `question` VALUES (1733399832149729282, 1409, 'ff602', 0001, '驾驶机动车违反禁令标志、禁止标线指示的，一次记1分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<br>（四）驾驶机动车违反禁令标志、禁止标线指示的。', '', '禁令标志|禁止标线|1分', 0003, 0.15);
INSERT INTO `question` VALUES (1733399832212643841, 1410, 'ec2a2', 0001, '驾驶机动车在高速公路上违法停车的，一次记12分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，<b>一次记9分</b>：<br>（三）驾驶机动车在高速公路或者城市快速路上<b>违法停车</b>的；', '', '12分|违法停车', 0005, 0.40);
INSERT INTO `question` VALUES (1733399832267169794, 1411, 'b7f04', 0001, '驾驶机动车行经人行横道，不按规定减速、停车、避让行人的，一次记3分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：（七）驾驶机动车行经人行横道<b>不按规定减速、停车、避让行人</b>的。', '', '不按规定|避让|3分', 0003, 0.10);
INSERT INTO `question` VALUES (1733399832317501441, 1412, 'b6416', 0002, '当事人应当自收到罚款的行政处罚决定书之日起多长时间内，到指定的银行缴纳罚款？ ', 'A、三日 B、五日 C、十五日 D、三十日', '三日 ', '五日 ', '十五日 ', '三十日', 'C', '《道路交通安全法》第一百〇八条，当事人应当自收到罚款的行政处罚决定书之日起<b>十五日内</b>，到<b>指定的银行缴纳罚款</b>。<br>对行人、乘车人和非机动车驾驶人的罚款，当事人无异议的，可以当场予以收缴罚款。<br>罚款应当开具省、自治区、直辖市财政部门统一制发的罚款收据；不出具财政部门统一制发的罚款收据的，当事人有权拒绝缴纳罚款。', '', '行政处罚决定书|缴纳罚款', 0005, 0.34);
INSERT INTO `question` VALUES (1733399832363638785, 1413, 'c3bf0', 0002, '机动车驾驶人存在下列哪种交通违法行为的，一次记3分？ ', 'A、使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的 B、驾驶不按规定安装机动车号牌的机动车上道路行驶的 C、驾驶未悬挂机动车号牌的机动车上道路行驶的 D、驾驶故意遮挡、污损机动车号牌的机动车上道路行驶的', '使用伪造', '变造的机动车号牌', '行驶证', '驾驶证', 'B', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，一次<b>记3分</b>：<br>（十）驾驶<b>不按规定安装机动车号牌</b>的机动车上道路行驶的；<br>【拓展知识】<br>使用伪造、变造的机动车号牌，一次记12分；<br>未悬挂或故意遮挡机动车号牌，一次记9分。', '', '记3分', 0004, 0.26);
INSERT INTO `question` VALUES (1733399832468496386, 1414, 'd37c7', 0001, '因违法停车现场已被处理过的违法行为，可以申请消除电子眼处罚。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为处理程序规定》第二十二条 交通技术监控设备记录或者录入道路交通违法信息管理系统的违法行为信息，<b>有下列情形之一并经核实的，违法行为发生地或者机动车登记地公安机关交通管理部门应当自核实之日起三日内予以消除</b>：<br>（一）警车、消防救援车辆、救护车、工程救险车执行紧急任务期间交通技术监控设备记录的违法行为；<br>（二）机动车所有人或者管理人提供报案记录证明机动车被盗抢期间、机动车号牌被他人冒用期间交通技术监控设备记录的违法行为；<br>（三）违法行为人或者机动车所有人、管理人提供证据证明机动车因救助危难或者紧急避险造成的违法行为；<br><b>（四）已经在现场被交通警察处理的交通技术监控设备记录的违法行为</b>；<br>（五）因交通信号指示不一致造成的违法行为；<br>（六）作为处理依据的交通技术监控设备收集的违法行为记录资料，不能清晰、准确地反映机动车类型、号牌、外观等特征以及违法时间、地点、事实的；<br>（七）经比对交通技术监控设备记录的违法行为照片、道路交通违法信息管理系统登记的机动车信息，确认记录的机动车号牌信息错误的；<br>（八）其他应当消除的情形。', '', '已被处理|申请消除', 0003, 0.13);
INSERT INTO `question` VALUES (1733399832523022337, 1415, 'b4328', 0001, '在本记分周期内或者上一个记分周期内，机动车驾驶人有二次以上参加满分教育记录的，不得参加学法减分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第二十六条 机动车驾驶人申请接受交通安全教育扣减交通违法行为记分的，公安机关交通管理部门应当受理。但有以下情形之一的，<b>不予受理</b>：<br>（一）在本记分周期内或者上一个记分周期内，机动车驾驶人<b>有二次以上参加满分教育记录的</b>；', '', '二次以上|不得参加', 0003, 0.15);
INSERT INTO `question` VALUES (1733399832564965377, 1416, 'fa44c', 0002, '造成交通事故后逃逸，尚不构成犯罪的，公安机关交通管理部门除按照规定罚款外，还可以并处________？ ', 'A、扣留车辆 B、15日以下拘留 C、吊销驾驶证 D、5年内不得重新取得驾驶证', '扣留车辆 ', '15日以下拘留 ', '吊销驾驶证 ', '5年内不得重新取得驾驶证', 'B', '《道路交通安全法》第九十九条　有下列行为之一的，由公安机关交通管理部门<b>处二百元以上二千元以下罚款</b>：<br><b>（三）</b> 造成交通事故后逃逸，尚不构成犯罪的；<br>行为人有前款第二项、第四项情形之一的，可以并处吊销机动车驾驶证；有第一项、<b>第三项</b>、第五项至第八项情形之一的，可以<b>并处十五日以下拘留</b>。', '', '逃逸|不构成', 0005, 0.50);
INSERT INTO `question` VALUES (1733399832619491329, 1417, 'e155c', 0001, '因交通信号指示不一致造成的违法行为，经核实后应当予以消除。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为处理程序规定》第二十一条规定 交通技术监控设备记录或者录入道路交通违法信息管理系统的违法行为信息，<b>有下列情形之一并经核实的，应当予以消除：</b> （一）警车、消防车、救护车、工程救险车执行紧急任务的； （二）机动车被盗抢期间发生的； （三）有证据证明救助危难或者紧急避险造成的； （四）现场已被交通警察处理的； <b>（五）因交通信号指示不一致造成的；</b> （六）不符合本规定第十八条规定要求的； （七）记录的机动车号牌信息错误的； （八）因使用伪造、变造或者其他机动车号牌发生违法行为造成合法机动车被记录的； （九）其他应当消除的情形。', '', '不一致|应当', 0002, 0.06);
INSERT INTO `question` VALUES (1733399832661434370, 1418, 'dba9f', 0001, '代替实际机动车驾驶人参加审验教育的，由公安机关交通管理部门处二千元以下罚款。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第一百条，机动车驾驶人参加审验教育时在签注学习记录、学习过程中弄虚作假的，相应学习记录无效，重新参加审验学习，由公安机关交通管理部门处一千元以下罚款。<br><b>代替实际机动车驾驶人</b>参加审验教育的，由公安机关交通管理部门处<b>二千元以下罚款。</b>', '', '代替|参加审验教育', 0003, 0.11);
INSERT INTO `question` VALUES (1733399832753709058, 1419, 'ed503', 0001, '驾驶校车、中型以上载客载货汽车、危险物品运输车辆以外的机动车在高速公路、城市快速路以外的道路上行驶超过规定时速百分之五十以上的，一次记9分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（三）驾驶校车、中型以上载客载货汽车、危险物品运输车辆<b>以外的</b>机动车在高速公路、城市快速路上行驶超过规定时速百分之二十以上未达到百分之五十，或者在高速公路、城市快速路<b>以外的</b>道路上行驶超过<b>规定时速百分之五十以上</b>的；', '', '以外的|百分之五十以上', 0005, 0.41);
INSERT INTO `question` VALUES (1733399832845983746, 1420, 'c9891', 0001, '驾驶机动车不按规定避让校车的，一次记6分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：<br>（八）驾驶机动车<b>不按规定避让校车的</b>。', '', '避让校车|6分', 0005, 0.37);
INSERT INTO `question` VALUES (1733399832896315394, 1421, 'c4976', 0002, '道路交通违法行为累积记分周期是多长时间？ ', 'A、14个月 B、12个月 C、10个月 D、6个月', '14个月 ', '12个月 ', '10个月 ', '6个月', 'B', '《机动车驾驶证申领和使用规定》第七十一条 公安机关交通管理部门对机动车驾驶人的道路交通安全违法行为，除依法给予行政处罚外，实行道路交通安全违法行为累积记分制度，<b>记分周期为12个月</b>，满分为12分。', '', '记分周期', 0001, 0.02);
INSERT INTO `question` VALUES (1733399832946647042, 1422, '147b7', 0002, '驾驶人有下列哪种违法行为一次记6分？ ', 'A、饮酒后驾驶机动车 B、使用其他车辆行驶证 C、使用伪造、变造驾驶证 D、驾驶机动车在高速公路违法占用应急车道行驶的', '饮酒后驾驶机动车 ', '使用其他车辆行驶证 ', '使用伪造', '变造驾驶证 D', 'D', '《道路交通安全违法行为记分管理办法》第十条机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：（十一）驾驶机动车在高速公路或者城市快速路上违法<b>占用应急车道行驶的</b>。<br>【高速记分】慢3<b>占6</b>停9倒逆12。', '', '记6分', 0003, 0.14);
INSERT INTO `question` VALUES (1733399832996978689, 1423, 'e2782', 0001, '造成致人轻伤以上或者死亡的交通事故后逃逸，尚不构成犯罪的一次记12分。', NULL, '对', '错', '空', '空', '对', '【逃逸记分】<b>轻伤以上，记12分</b>；轻微伤，记6分。《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：<br>（二）造成致人轻伤以上或者死亡的交通事故后逃逸，<b>尚不构成犯罪的</b>；', '', '不构成', 0004, 0.16);
INSERT INTO `question` VALUES (1733399833047310337, 1424, 'ca49b', 0002, '驾驶机动车不按规定使用灯光的，将被一次记多少分？ ', 'A、1分 B、2分 C、3分 D、6分', '1分 ', '2分 ', '3分 ', '6分', 'A', '行灯会禁检安，全都记1分。《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<b>（三）驾驶机动车不按规定使用灯光的</b>。', '', '使用灯光', 0004, 0.23);
INSERT INTO `question` VALUES (1733399833093447681, 1425, 'dd447', 0002, '驾驶机动车违反禁令标志、禁止标线指示的，将被一次记多少分？ ', 'A、9分 B、6分 C、3分 D、1分', '9分 ', '6分 ', '3分 ', '1分', 'D', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：（四）驾驶机动车<b>违反禁令标志</b>、<b>禁止标线指示</b>的。', '', '标志|标线', 0005, 0.51);
INSERT INTO `question` VALUES (1733399833147973634, 1426, 'a8b27', 0002, '代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的，一次记多少分？ ', 'A、3分 B、6分 C、9分 D、12分', '3分 ', '6分 ', '9分 ', '12分', 'D', '《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：<b>代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的</b>。<br>【拓展知识】<br> 买分卖分，代人销分 ，买卖双方，买卖中介都要受到处罚。', '', '代替|牟取经济利益', 0004, 0.22);
INSERT INTO `question` VALUES (1733399833185722370, 1427, 'b26ae', 0002, '驾驶机动车行经人行横道，不按规定减速、停车避让行人的，一次记多少分？ ', 'A、9分 B、6分 C、3分 D、1分', '9分 ', '6分 ', '3分 ', '1分', 'C', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：（七）驾驶机动车行经人行横道<b>不按规定减速、停车、避让行人</b>的。', '', '避让行人', 0004, 0.26);
INSERT INTO `question` VALUES (1733399833261219842, 1428, 'ee734', 0001, '驾驶机动车不按交通信号灯指示通行的，一次记3分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十条 机动车驾驶人有下列交通违法行为之一，一次<b>记6分</b>：<br>（八）驾驶机动车<b>不按交通信号灯指示</b>通行的。', '', '交通信号灯', 0005, 0.31);
INSERT INTO `question` VALUES (1733399833311551489, 1429, 'bcda4', 0001, '交通违法行为累积记分未满12分的，可以处理其驾驶的其他机动车的交通违法行为记录。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十四条，机动车驾驶人有二起以上交通违法行为应当予以记分的，记分分值累积计算。<br>机动车驾驶人可以一次性处理完毕同一辆机动车的多起交通违法行为记录，记分分值累积计算。<b>累积记分未满12分的，可以处理其驾驶的其他机动车的交通违法行为记录</b>；累积记分满12分的，不得再处理其他机动车的交通违法行为记录。', '', '未满12分|其驾驶', 0003, 0.12);
INSERT INTO `question` VALUES (1733399833387048962, 1430, 'e8e56', 0001, '代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的，一次记12分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第八条 ，机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：（七）<b>代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益</b>的。', '', '代替|牟取经济利益', 0002, 0.07);
INSERT INTO `question` VALUES (1733399833437380610, 1431, 'af34d', 0001, '驾驶机动车在高速公路上行驶时，机动车驾驶人未按规定系安全带的一次记3分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<br>（九）驾驶机动车在道路上行驶时，机动车驾驶人<b>未按规定系安全带</b>的；', '', '高速公路|安全带|记3分', 0005, 0.35);
INSERT INTO `question` VALUES (1733399833491906562, 1432, 'f4985', 0001, '机动车驾驶人有二起以上交通违法行为应当予以记分的，记分分值不累积计算。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十四条，机动车驾驶人<b>有二起以上</b>交通违法行为应当予以记分的，<b>记分分值累积计算</b>。', '', '二起以上|不累积', 0005, 0.30);
INSERT INTO `question` VALUES (1733399833546432513, 1433, 'a9701', 0002, '下列交通违法行为，一次记12分的是什么？ ', 'A、驾驶机动车运载超限的不可解体的物品，未按指定的时间、路线行驶的 B、代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的 C、驾驶机动车载运爆炸物品，未按指定的时间、路线行驶的 D、驾驶小型客车在高速公路上行驶超过规定时速20%以上未达到50%的', '驾驶机动车运载超限的不可解体的物品，未按指定的时间', '路线行驶的 B', '代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的 C', '驾驶机动车载运爆炸物品，未按指定的时间', 'B', '《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，一次<b>记12分</b>：<br>（七）<b>代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的</b>。<br>【拓展知识】<br>记6分：不按规定运载超限不可解体物品、爆炸物品、高速公路超速20%-50%。', '', '记12分', 0004, 0.21);
INSERT INTO `question` VALUES (1733399833592569857, 1434, 'd26f1', 0002, '下列交通违法行为，一次记1分的是什么？ ', 'A、驾驶与准驾车型不符的机动车的 B、驾驶机动车在道路上行驶时，机动车驾驶人未按规定系安全带的 C、驾驶机动车不按交通信号灯指示通行的 D、驾驶载货汽车载物超过最大允许总质量50%以上的', '驾驶与准驾车型不符的机动车的 ', '驾驶机动车在道路上行驶时，机动车驾驶人未按规定系安全带的 ', '驾驶机动车不按交通信号灯指示通行的 ', '驾驶载货汽车载物超过最大允许总质量50%以上的', 'B', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<br>（九）驾驶机动车在道路上行驶时，机动车驾驶人<b>未按规定系安全带的</b>；<br>【拓展知识】<br>准驾车型不符，扣9分；不按交通信号，扣6分；货车超载50%，扣6分。', '', '记1分', 0003, 0.14);
INSERT INTO `question` VALUES (1733399833638707202, 1435, 'e1777', 0001, '驾驶机动车不按规定会车，或者在高速公路、城市快速路以外的道路上不按规定倒车、掉头的，一次记3分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<br>（二）驾驶机动车<b>不按规定会车</b>，或者在高速公路、城市快速路<b>以外的道路上不按规定倒车、掉头</b>的；', '', '以外的道路|记3分|会车|倒车|掉头', 0005, 0.51);
INSERT INTO `question` VALUES (1733399833705816065, 1436, 'ca7dc', 0002, '下列交通违法行为，一次记9分的是什么？ ', 'A、驾驶7座以上载客汽车载人超过核定人数50%以上未达到100%的 B、驾驶校车载人超过核定人数20%以上的 C、代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的 D、使用伪造、变造的机动车号牌或者使用其他机动车号牌的', '驾驶7座以上载客汽车载人超过核定人数50%以上未达到100%的 ', '驾驶校车载人超过核定人数20%以上的 ', '代替实际机动车驾驶人接受交通违法行为处罚和记分牟取经济利益的 ', '使用伪造', 'A', '《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，一次<b>记9分</b>：<br>（一）驾驶<b>7座以上</b>载客汽车载人超过核定人数<b>百分之五十以上未达到百分之百</b>的；<br>【拓展知识】<br>校车超载20%以上12分；代扣分获利12分；伪造、变造12分。', '', '记9分', 0005, 0.34);
INSERT INTO `question` VALUES (1733399834792140801, 1437, 'd4a84', 0002, '驾驶校车、公路客运汽车、旅游客运汽车载人超过核定人数未达到百分之二十的，一次记多少分？ ', 'A、3分 B、6分 C、9分 D、12分', '3分 ', '6分 ', '9分 ', '12分', 'B', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（一）<b>驾驶校车、公路客运汽车、旅游客运汽车载人超过核定人数未达到百分之二十</b>，或者驾驶7座以上载客汽车载人超过核定人数百分之二十以上未达到百分之五十，或者驾驶其他载客汽车载人超过核定人数百分之五十以上未达到百分之百的；', '', '未达到百分之二十', 0005, 0.47);
INSERT INTO `question` VALUES (1733399834859249666, 1438, 'f6a9a', 0002, '下列交通违法行为，一次记9分的是什么? ', 'A、驾驶故意遮挡、污损机动车号牌的机动车上道路行驶的 B、造成致人轻微伤或者财产损失的交通事故后逃逸，尚不构成犯罪的 C、驾驶机动车不按交通信号灯指示通行的 D、机动车驾驶证被暂扣或者扣留期间驾驶机动车的', '驾驶故意遮挡', '污损机动车号牌的机动车上道路行驶的 B', '造成致人轻微伤或者财产损失的交通事故后逃逸，尚不构成犯罪的 C', '驾驶机动车不按交通信号灯指示通行的 D', 'A', '《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，<b>一次记9分</b>：<br>（四）驾驶未悬挂机动车号牌或者<b>故意遮挡、污损机动车号牌的机动车上道路行驶的；</b><br>【拓展知识】<br>记6分：不构成犯罪的轻微伤、不按信号灯指示通行、扣留期间驾驶机动车。', '', '记9分', 0004, 0.16);
INSERT INTO `question` VALUES (1733399834917969921, 1439, 'a2ecf', 0002, '驾驶校车、中型以上载客载货汽车、危险物品运输车辆以外的机动车在高速公路、城市快速路以外的道路上行驶超过规定时速50%以上的，一次记多少分? ', 'A、1分 B、3分 C、6分 D、9分', '1分 ', '3分 ', '6分 ', '9分', 'C', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（三）驾驶校车、中型以上载客载货汽车、危险物品运输车辆<b>以外的机动车</b>在高速公路、城市快速路上行驶超过规定时速百分之二十以上未达到百分之五十，或者在高速公路、城市快速路<b>以外的道路上行驶超过规定时速百分之五十以上的</b>；', '', '以外的|50%以上', 0005, 0.41);
INSERT INTO `question` VALUES (1733399834976690178, 1440, 'd3c0c', 0001, '小型载客汽车驾驶人在一个记分周期内累积记分满12分的，应当参加为期七天的道路交通安全法律、法规和相关知识学习。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十八条，机动车驾驶人在一个记分周期内累积记分满12分的，应当<b>参加为期七天的道路交通安全法律、法规和相关知识学习</b>。其中，大型客车、重型牵引挂车、城市公交车、中型客车、大型货车驾驶人应当参加为期三十天的道路交通安全法律、法规和相关知识学习。', '', '满12分|为期七天', 0001, 0.03);
INSERT INTO `question` VALUES (1733399835027021826, 1441, 'a44d1', 0001, '驾驶机动车在高速公路上行驶低于规定最低时速的，一次记3分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，一次<b>记3分</b>：<br>（十五）驾驶机动车在高速公路上行驶<b>低于规定最低时速</b>的。', '', '低于规定|记3分', 0004, 0.17);
INSERT INTO `question` VALUES (1733399835085742082, 1442, 'cc762', 0001, '驾驶校车、中型以上载客载货汽车、危险物品运输车辆以外的机动车在高速公路、城市快速路以外的道路上行驶超过规定时速60%的，一次记6分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（三）驾驶校车、中型以上载客载货汽车、危险物品运输车辆<b>以外的机动车</b>在高速公路、城市快速路上行驶超过规定时速百分之二十以上未达到百分之五十，或者在高速公路、城市快速路<b>以外的道路上行驶超过规定时速百分之五十以上的</b>；', '', '以外的|6分', 0005, 0.29);
INSERT INTO `question` VALUES (1733399835140268034, 1443, 'b186e', 0001, '驾驶未按规定定期进行安全技术检验的公路客运汽车、旅游客运汽车、危险物品运输车辆以外的机动车上道路行驶的，一次记1分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<br>（七）驾驶<b>未按规定定期</b>进行安全技术检验的公路客运汽车、旅游客运汽车、危险物品运输车辆<b>以外的机动车</b>上道路行驶的；', '', '以外的|安全技术检验', 0005, 0.29);
INSERT INTO `question` VALUES (1733399835198988289, 1444, 'c2b4e', 0002, '在最近几个记分周期内，机动车驾驶人饮酒后驾驶机动车受过处罚的，不得通过交通安全教育扣减交通违法行为记分？ ', 'A、1个 B、2个 C、3个 D、4个', '1个 ', '2个 ', '3个 ', '4个', 'C', '《道路交通安全违法行为记分管理办法》第二十六条，机动车驾驶人申请接受交通安全教育扣减交通违法行为记分的，公安机关交通管理部门应当受理。但<b>有以下情形之一的，不予受理</b>：<br>（二）在<b>最近三个记分周期</b>内，机动车驾驶人因造成交通事故后逃逸，或者<b>饮酒后驾驶机动车</b>，或者使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌，或者使用其他机动车号牌、行驶证，或者买分卖分受到过处罚的；', '', '几个|饮酒后驾驶|不得', 0005, 0.37);
INSERT INTO `question` VALUES (1733399835249319937, 1445, 'a91d0', 0002, '驾驶校车、公路客运汽车、旅游客运汽车载人超过核定人数多少以上的，一次记12分？ ', 'A、百分之五 B、百分之十 C、百分之十五 D、百分之二十', '百分之五 ', '百分之十 ', '百分之十五 ', '百分之二十', 'D', '《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，一次<b>记12分</b>：<br>（四）<b>驾驶校车、公路客运汽车、旅游客运汽车载人超过核定人数百分之二十以上</b>，或者驾驶其他载客汽车载人超过核定人数百分之百以上的；', '', '校车|超过核定人数|12分', 0003, 0.12);
INSERT INTO `question` VALUES (1733399835312234497, 1446, 'e754f', 0001, '驾驶载货汽车载物超过最大允许总质量未达到百分之三十的，一次记1分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<br>（六）驾驶载货汽车载物超过最大允许总质量<b>未达到</b>百分之三十的；', '', '未达到百分之三十|记1分', 0005, 0.29);
INSERT INTO `question` VALUES (1733399835391926273, 1447, 'ddf2a', 0002, '下列交通违法行为，一次记9分的是什么? ', 'A、驾驶机动车在高速公路上逆行的 B、驾驶机动车在城市快速路上违法停车的 C、驾驶载货汽车违反规定载客的 D、驾驶机动车在高速公路上穿越中央分隔带掉头的', '驾驶机动车在高速公路上逆行的 ', '驾驶机动车在城市快速路上违法停车的 ', '驾驶载货汽车违反规定载客的 ', '驾驶机动车在高速公路上穿越中央分隔带掉头的', 'B', '《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，一次<b>记9分</b>：<br>（三）驾驶机动车在<b>高速公路或者城市快速路上违法停车</b>的；<br>【拓展知识】<br>高速逆行12分；货车载客3分；高速掉头12分。', '', '记9分', 0005, 0.40);
INSERT INTO `question` VALUES (1733399835459035137, 1448, 'c1f2e', 0001, '驾驶载货汽车载物超过最大允许总质量百分之三十以上未达到百分之五十的，或者违反规定载客的，一次记3分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：<br>（九）驾驶载货汽车载物超过最大允许<b>总质量百分之三十以上未达到百分之五十的，或者违反规定载客的</b>；', '', '载货汽车|百分之三十以上未达到百分之五十|3分', 0005, 0.27);
INSERT INTO `question` VALUES (1733399835526144002, 1449, 'fffe2', 0001, '驾驶校车、公路客运汽车、旅游客运汽车、7座以上载客汽车以外的其他载客汽车载人超过核定人数百分之六十的，一次记6分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（一）驾驶校车、公路客运汽车、旅游客运汽车载人超过核定人数未达到百分之二十，或者驾驶7座以上载客汽车载人超过核定人数百分之二十以上未达到百分之五十，或者<b>驾驶其他载客汽车载人超过核定人数百分之五十以上未达到百分之百的</b>；', '', '记6分|以外的|百分之六十', 0005, 0.28);
INSERT INTO `question` VALUES (1733399835584864257, 1450, 'd3111', 0002, '下列交通违法行为，一次记1分的是什么? ', 'A、驾驶校车在高速公路、城市快速路以外的道路上行驶超过规定时速百分之二十以上未达到百分之五十的 B、驾驶机动车在高速公路或者城市快速路上违法停车的 C、驾驶机动车不按规定超车让行的 D、驾驶机动车不按规定使用灯光的', '驾驶校车在高速公路', '城市快速路以外的道路上行驶超过规定时速百分之二十以上未达到百分之五十的 B', '驾驶机动车在高速公路或者城市快速路上违法停车的 C', '驾驶机动车不按规定超车让行的 D', 'D', '《道路交通安全违法行为记分管理办法》第十二条，机动车驾驶人有下列交通违法行为之一，<b>一次记1分</b>：<br>（三）驾驶机动车不按规定使用灯光的；<br>【灯光记分】信号灯6分；故障灯3分；违规用灯1分。', '', '记1分', 0004, 0.20);
INSERT INTO `question` VALUES (1733399835643584513, 1451, 'dbc27', 0002, '下列交通违法行为，一次记12分的是什么? ', 'A、连续驾驶中型以上载客汽车、危险物品运输车超过4小时未停车休息的 B、驾驶机动车在城市快速路上违法占用应急车道行驶的 C、机动车驾驶证被暂扣或者扣留期间驾驶机动车 D、驾驶机动车在高速公路或者城市快速路上倒车的', '连续驾驶中型以上载客汽车', '危险物品运输车超过4小时未停车休息的 B', '驾驶机动车在城市快速路上违法占用应急车道行驶的 C', '机动车驾驶证被暂扣或者扣留期间驾驶机动车 D', 'D', '【高速记分】慢3占6停9<b>倒逆12</b>。《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，<b>一次记12分：</b><br>（六）驾驶机动车在高速公路、城市快速路上<b>倒车、逆行、穿越中央分隔带掉头的；</b><br>【拓展知识】<br>连续驾驶中型以上载客汽车、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟的，一次记9分；<br>驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的，一次记6分；<br>机动车驾驶证被暂扣或者扣留期间驾驶机动车的，一次记6分。', '', '记12分', 0004, 0.25);
INSERT INTO `question` VALUES (1733399835710693378, 1452, 'c4c7f', 0001, '驾驶未悬挂机动车号牌的机动车上道路行驶的，一次记12分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第九条机动车驾驶人有下列交通违法行为之一，一次记9分：（四）驾驶<b>未悬挂机动车号牌</b>或者故意遮挡、污损机动车号牌的机动车上道路行驶的。', '', '未悬挂|12分', 0005, 0.39);
INSERT INTO `question` VALUES (1733399835781996546, 1453, 'b9c7c', 0001, '参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识网上学习三日内累计满三十分钟且考试合格的，一次扣减1分交通违法行为记分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第二十七条，参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识<b>网上学习三日内累计满三十分钟且考试合格的，一次扣减1分。</b><br>参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识现场学习满一小时且考试合格的，一次扣减2分。<br>参加公安机关交通管理部门组织的交通安全公益活动的，满一小时为一次，一次扣减1分。', '', '网上学习三日|扣减1分', 0003, 0.14);
INSERT INTO `question` VALUES (1733399835849105410, 1454, 'd40b5', 0001, '组织他人参加审验教育时在签注学习记录、学习过程中弄虚作假的，没有违法所得的，由公安机关交通管理部门处二万元以下罚款。', NULL, '对', '错', '空', '空', '对', '《机动车驾驶证申领和使用规定》第一百条，机动车驾驶人参加审验教育时在签注学习记录、学习过程中弄虚作假的，相应学习记录无效，重新参加审验学习，由公安机关交通管理部门处一千元以下罚款。<br>代替实际机动车驾驶人参加审验教育的，由公安机关交通管理部门处二千元以下罚款。<br><b>组织他人实施前两款行为之一，</b>有违法所得的，由公安机关交通管理部门处违法所得三倍以下罚款，但最高不超过二万元；<b>没有违法所得的，由公安机关交通管理部门处二万元以下罚款。</b>', '', '审验教育|弄虚作假|二万元以下', 0003, 0.14);
INSERT INTO `question` VALUES (1733399835920408577, 1455, 'a0a05', 0002, '未取得校车驾驶资格驾驶校车的，一次记几分? ', 'A、12分 B、9分 C、6分 D、3分', '12分 ', '9分 ', '6分 ', '3分', 'B', '《道路交通安全违法行为记分管理办法》第九条，机动车驾驶人有下列交通违法行为之一，<b>一次记9分</b>：<br>（六）<b>未取得校车驾驶资格驾驶校车</b>的；', '', '未取得|记分', 0005, 0.43);
INSERT INTO `question` VALUES (1733399835979128834, 1456, 'c13a0', 0002, '参加公安机关交通管理部门组织的交通安全公益活动的，满几小时为一次，一次扣减几分? ', 'A、2小时，2分 B、1小时，1分 C、2小时，1分 D、1小时，2分', '2小时，2分 ', '1小时，1分 ', '2小时，1分 ', '1小时，2分', 'B', '《道路交通安全违法行为记分管理办法》第二十七条，参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识网上学习三日内累计满三十分钟且考试合格的，一次扣减1分。<br>参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识现场学习满一小时且考试合格的，一次扣减2分。<br>参加公安机关交通管理部门组织的<b>交通安全公益活动</b>的，<b>满一小时为一次</b>，一次<b>扣减1分</b>。', '', '公益活动', 0005, 0.32);
INSERT INTO `question` VALUES (1733399836759269378, 1457, 'dda96', 0001, '驾驶机动车不按照规定避让校车的，一次记12分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分：</b><br>（八）驾驶机动车<b>不按规定避让校车的</b>。', '', '避让校车|12分', 0003, 0.12);
INSERT INTO `question` VALUES (1733399837015121922, 1458, 'cd86b', 0001, '机动车驾驶人使用伪造、变造的行驶证的，一次记9分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第八条机动车驾驶人有下列交通违法行为之一，<b>一次记12分</b>：<br>（三）<b>使用伪造、变造的机动车</b>号牌、<b>行驶证</b>、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的。', '', '伪造、变造', 0004, 0.26);
INSERT INTO `question` VALUES (1733399837090619394, 1459, 'b37fe', 0001, '驾驶机动车遇前方机动车停车排队或者缓慢行驶时，借道超车或者占用对面车道、穿插等候车辆的，一次记1分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，<b>一次记3分</b>：<br>（五）驾驶机动车遇前方机动车停车排队或者缓慢行驶时，<b>借道超车或者占用对面车道、穿插等候车辆</b>的；', '', '借道超车|记1分', 0005, 0.49);
INSERT INTO `question` VALUES (1733399837212254209, 1460, 'b79d4', 0001, '参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识现场学习满一小时且考试合格的，一次扣减12分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第二十七条，参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识网上学习三日内累计满三十分钟且考试合格的，一次扣减1分。<br>参加公安机关交通管理部门组织的道路交通安全法律、法规和相关知识<b>现场学习满一小时且考试合格</b>的，一次<b>扣减2分</b>。<br>参加公安机关交通管理部门组织的交通安全公益活动的，满一小时为一次，一次扣减1分。', '', '现场学习|12分', 0004, 0.22);
INSERT INTO `question` VALUES (1733399837291945985, 1461, 'c9751', 0001, '驾驶校车、公路客运汽车、旅游客运汽车、7座以上载客汽车以外的其他载客汽车载人超过核定人数百分之五十以上未达到百分之百的，一次记9分。', NULL, '对', '错', '空', '空', '错', '<b>请注意关键词：以外的。</b>《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（一）驾驶校车、公路客运汽车、旅游客运汽车载人超过核定人数未达到百分之二十，或者驾驶7座以上载客汽车载人超过核定人数百分之二十以上未达到百分之五十，或者<b>驾驶其他载客汽车载人超过核定人数百分之五十以上未达到百分之百的</b>；', '', '以外的|未达到百分之百|9分', 0005, 0.50);
INSERT INTO `question` VALUES (1733399837367443457, 1462, 'cbaec', 0001, '驾驶校车、公路客运汽车、旅游客运汽车以外的其他载客汽车载人超过核定人数百分之百以上的，一次记12分。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第八条，机动车驾驶人有下列交通违法行为之一，一次<b>记12分</b>：<br>（四）驾驶校车、公路客运汽车、旅游客运汽车载人超过核定人数百分之二十以上，或者<b>驾驶其他载客汽车载人超过核定人数百分之百以上的</b>；', '', '以外的|百分之百以上|12分', 0003, 0.12);
INSERT INTO `question` VALUES (1733399837434552322, 1463, 'fd2e6', 0002, '驾驶校车、中型以上载客载货汽车、危险物品运输车辆以外的机动车在高速公路、城市快速路上行驶超过规定时速百分之二十以上未达到百分之五十的，一次记多少分？ ', 'A、9分 B、6分 C、3分 D、12分', '9分 ', '6分 ', '3分 ', '12分', 'B', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：（三）驾驶校车、中型以上载客载货汽车、危险物品运输车辆<b>以外的机动车</b>在<b>高速公路、城市快速路上</b>行驶超过规定时速<b>百分之二十以上未达到百分之五十</b>，或者在高速公路、城市快速路以外的道路上行驶超过规定时速百分之五十以上的；', '', '以外的|在高速公路|百分之二十以上未达到百分之五十', 0005, 0.40);
INSERT INTO `question` VALUES (1733399837489078274, 1464, 'ae347', 0001, '驾驶不按规定安装机动车号牌的机动车上道路行驶的，一次记1分。', NULL, '对', '错', '空', '空', '错', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，一次<b>记3分</b>：<br>（十）驾驶<b>不按规定安装机动车号牌</b>的机动车上道路行驶的；', '', '不按规定安装|1分', 0005, 0.32);
INSERT INTO `question` VALUES (1733399837572964353, 1465, 'd4a5e', 0002, '驾驶载货汽车载物超过最大允许总质量百分之五十以上的，一次记多少分？ ', 'A、3分 B、6分 C、9分 D、12分', '3分 ', '6分 ', '9分 ', '12分', 'B', '《道路交通安全违法行为记分管理办法》第十条，机动车驾驶人有下列交通违法行为之一，<b>一次记6分</b>：<br>（四）驾驶<b>载货汽车载物</b>超过最大允许总质量<b>百分之五十以上的；</b>', '', '百分之五十以上', 0005, 0.49);
INSERT INTO `question` VALUES (1733399837652656130, 1466, 'b5914', 0002, '驾驶机动车在高速公路以外的道路上逆行的，将被一次记多少分？ ', 'A、9分 B、6分 C、3分 D、1分', '9分 ', '6分 ', '3分 ', '1分', 'C', '《道路交通安全违法行为记分管理办法》第十一条，机动车驾驶人有下列交通违法行为之一，一次<b>记3分</b>：<br>（四）驾驶机动车不按规定超车、让行，或者在高速公路、城市快速路<b>以外的</b>道路上<b>逆行</b>的；', '', '以外的|逆行', 0005, 0.58);
INSERT INTO `question` VALUES (1733399837711376385, 1467, 'fefd6', 0001, '驾驶机动车在人行横道上临时停车属于违法行为。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》规定，机动车禁止在人行横道线上停车，通过人行横道时应减速慢行，遇到行人需停车礼让。《道路交通安全违法行为记分管理办法》规定，驾驶机动车违反禁令标志、<b>禁止标线指示</b>的<b>违法行为</b>，一次记1分。', '', '人行横道', 0002, 0.06);
INSERT INTO `question` VALUES (1733399837778485250, 1468, 'fb552', 0001, '在道路上从事校车业务，严重超员超速的，构成危险驾驶罪，将处拘役，并处罚金。', NULL, '对', '错', '空', '空', '对', '根据《刑法》第一百三十三条之一，【<b>危险驾驶罪</b>】在道路上驾驶机动车，有下列情形之一的，<b>处拘役，并处罚金</b>：<br>（一）追逐竞驶，情节恶劣的；<br>（二）醉酒驾驶机动车的；<br><b>（三）从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶的；</b><br>（四）违反危险化学品安全管理规定运输危险化学品，危及公共安全的。<br>机动车所有人、管理人对前款第三项、第四项行为负有直接责任的，依照前款的规定处罚。<br>有前两款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', '', '危险驾驶罪', 0002, 0.04);
INSERT INTO `question` VALUES (1733399837845594113, 1469, 'cc7ee', 0001, '机动车驾驶人在一个记分周期内累积记满36分的，应当在道路交通安全法律、法规和相关知识考试合格后，按规定预约参加场地驾驶技能和道路驾驶技能考试。', NULL, '对', '错', '空', '空', '对', '《道路交通安全违法行为记分管理办法》第二十二条，<b>机动车驾驶人在一个记分周期内三次以上累积记分满12分或者累积记分满36分的</b>，应当在<b>道路交通安全法律、法规和相关知识考试合格后</b>，按照《机动车驾驶证申领和使用规定》第四十三条和第四十四条的规定<b>预约参加场地驾驶技能和道路驾驶技能考试</b>。考试不合格的，十日后预约重新考试。<br>【补充知识】<br>记满12分，重考法规；记满24分，重考法规和场地；<b>记满36分，重考法规、场地和道路</b>。', '', '记满36分|场地驾驶技能|道路驾驶技能', 0003, 0.15);
INSERT INTO `question` VALUES (1733399837900120066, 1470, '8432f', 0001, '机动车在道路上发生轻微交通事故且妨碍交通时，不需移动。', NULL, '对', '错', '空', '空', '错', '机动车之间发生道路交通事故，“车能动，人未伤，有保险，车有牌，人有证，没有严重交通违法行为”的交通事故都可以进行快处快赔，<b>发生轻微交通事故</b>后，双方驾驶员应拍照或标明车辆现场位置，并<b>将车辆移到不妨碍交通的地方</b>。', '', '轻微|不需', 0003, 0.08);
INSERT INTO `question` VALUES (1733399838000783362, 1471, 'df1a0', 0002, '在道路上发生未造成人员伤亡且无争议的轻微交通事故如何处置？ ', 'A、保护好现场再协商 B、不要移动车辆 C、疏导其他车辆绕行 D、撤离现场自行协商', '保护好现场再协商 ', '不要移动车辆 ', '疏导其他车辆绕行 ', '撤离现场自行协商', 'D', '《道路交通安全法》第七十条：<b>在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；</b>不立即撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', '', '无争议', 0003, 0.09);
INSERT INTO `question` VALUES (1733399838051115009, 1472, '2c89d', 0001, '驾驶机动车在道路上发生交通事故要立即将车移到路边。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第七十条，<b>在道路上发生交通事故</b>，车辆驾驶人应当<b>立即停车，保护现场</b>；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。', '', '立即', 0004, 0.22);
INSERT INTO `question` VALUES (1733399838114029569, 1473, 'da55f', 0001, '驾驶人在发生交通事故后因抢救伤员变动现场时要标明位置。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第七十条，在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。<b>因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 </b>', '', '抢救|标明位置', 0001, 0.02);
INSERT INTO `question` VALUES (1733399838168555522, 1474, 'c6e3e', 0001, '在道路上发生交通事故造成人身伤亡时，要立即抢救受伤人员并迅速报警。', NULL, '对', '错', '空', '空', '对', '根据《道路交通安全法》第七十条的规定：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；<b>造成人身伤亡的</b>，车辆驾驶人<b>应当立即抢救受伤人员</b>，<b>并迅速报告执勤的交通警察或者公安机关交通管理部门</b>。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。', '', '立即抢救', 0001, 0.02);
INSERT INTO `question` VALUES (1733399838248247297, 1475, '59f0a', 0002, '如图所示，A车货物掉落，导致B车与掉落货物发生碰撞，以下说法正确的是什么？ ', 'A、B车自负责任 B、A车负全部责任 C、各负一半责任 D、偶然事件，不可避免', 'B车自负责任 ', 'A车负全部责任 ', '各负一半责任 ', '偶然事件，不可避免', 'B', '如图所示，由于<b>A车没有对货物进行捆绑牢固，从而导致货物掉落，造成碰撞事故</b>，因此<b>A车负全部责任</b>。', 'https://timg.mnks.cn/20230926e86719d5.jpg', 'A车货物掉落', 0003, 0.09);
INSERT INTO `question` VALUES (1733399838298578946, 1476, '15247', 0002, '在高速公路上驾驶机动车，车辆发生故障后的处置方法，以下说法错误的是什么？ ', 'A、打开危险报警闪光灯，夜间还应开启示廓灯、后位灯 B、在车后150米以外设置安全警告标志 C、车内乘员应下车辅助将故障车辆推移到紧急停车带上 D、所有人员需离开故障车辆，在紧急停车带或护栏以外安全位置报警并等候救援', '打开危险报警闪光灯，夜间还应开启示廓灯', '后位灯 B', '在车后150米以外设置安全警告标志 C', '车内乘员应下车辅助将故障车辆推移到紧急停车带上 D', 'C', '<b>请注意，题干要求选出说法错误的选项</b>。根据《道路交通安全法》规定：机动车在高速公路上发生故障时，驾驶人应当立即开启危险报警闪光灯，将车辆移至不妨碍交通的地点，并在车后150米设置警告标志。夜间还应当同时开启示廓灯和后位灯。驾驶人和乘车人应当迅速转移到右侧路肩上或者应急车道内。 <br>如果发生故障无法正常行驶的，应当由救援车、清障车拖曳、牵引，<b>车内乘员将其推到紧急停车带上，做法错误。</b>', '', '高速|故障|错误', 0004, 0.21);
INSERT INTO `question` VALUES (1733399838344716289, 1477, '56df7', 0001, '驾驶机动车发生故障或事故不能正常行驶时，应立即打开危险报警闪光灯。', NULL, '对', '错', '空', '空', '对', '危险报警闪光灯是一种提醒其他车辆与行人注意本车发生了特殊情况的信号灯。所以，<b>当车辆发生故障或事故不能正常行驶时，应第一时间开启</b>。<br>《道路交通安全法》第五十二条 机动车在道路上发生故障，需要停车排除故障时，驾驶人应当立即开启危险报警闪光灯，将机动车移至不妨碍交通的地方停放；难以移动的，应当持续开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离，必要时迅速报警。', '', '危险报警闪光灯|故障或事故', 0002, 0.04);
INSERT INTO `question` VALUES (1733399838395047938, 1478, 'a2675', 0002, '如图所示，在这起交通事故中，以下说法正确的是什么？ ', 'A、A车负全部责任 B、B车负全部责任 C、各负一半的责任 D、B车负主要责任', 'A车负全部责任 ', 'B车负全部责任 ', '各负一半的责任 ', 'B车负主要责任', 'A', '在有障碍的路段遇对向来车时，<b>无障碍的一方先行</b>；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有<b>障碍的一方先行</b>。<br>如图所示，AB两车会车时，无障碍一方的B车享有优先通行权，A车应让B车先行，因此，A车违反了道路通行规定，负事故的全部责任。', 'https://timg.mnks.cn/20230926d41a30a3.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399838445379585, 1479, 'd6d44', 0001, '驾驶机动车碰撞建筑物、公共设施后可即行撤离现场。', NULL, '对', '错', '空', '空', '错', '《道路交通事故处理程序规定》第十四条，发生财产损失事故且有下列情形之一，车辆可以移动的，当事人应当组织车上人员疏散到路外安全地点，在确保安全的原则下，采取现场拍照或者标划事故车辆现场位置等方式固定证据，将<b>车辆移至不妨碍交通的地点后报警</b>：<br>（一）机动车无检验合格标志或者无保险标志的；<br>（二）<b>碰撞建筑物、公共设施或者其他设施的</b>。', '', '即行撤离', 0003, 0.08);
INSERT INTO `question` VALUES (1733399838495711234, 1480, '353d7', 0001, '发生交通事故造成人员受伤时，要保护现场并立即报警。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，<b>当事人应当保护现场并立即报警：</b> <br><b>（一）造成人员死亡、受伤的； </b>　　<br>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　<br>（三）机动车无号牌、无检验合格标志、无保险标志的； 　　<br>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的；　　<br>（五）碰撞建筑物、公共设施或者其他设施的；　　<br>（六）驾驶人无有效机动车驾驶证的； 　　<br>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； <br>（八）当事人不能自行移动车辆的。 　　<br>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', '', '立即报警', 0001, 0.02);
INSERT INTO `question` VALUES (1733399838541848578, 1481, 'dcbf0', 0001, '道路交通事故中，驾驶人有饮酒、醉酒嫌疑时，要保护现场并立即报警。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当<b>保护现场并立即报警</b>：　　<br>（一）造成人员死亡、受伤的； 　　<br>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　<br>（三）机动车无号牌、无检验合格标志、无保险标志的； 　　<br>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的；　　<br>（五）碰撞建筑物、公共设施或者其他设施的；　　<br>（六）驾驶人无有效机动车驾驶证的； 　　<br><b>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； </b>　　<br>（八）当事人不能自行移动车辆的。 　　<br>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', '', '保护现场', 0001, 0.01);
INSERT INTO `question` VALUES (1733399838596374529, 1482, 'fe16c', 0002, '如图所示，在这起交通事故中，以下说法正确的是什么？ ', 'A、A车负全部责任 B、B车负全部责任 C、都无责任，后果自行承担 D、各负一半责任', 'A车负全部责任 ', 'B车负全部责任 ', '都无责任，后果自行承担 ', '各负一半责任', 'B', '驾驶机动车需要掉头时，观察前、后方交通情况，确认安全后减速或停车开启左转向灯，正确选择掉头地点和时机进行掉头。掉头时不要妨碍其他车辆和行人的正常通行。 <br><b>图中B车掉头妨碍A车正常行驶，因此，B车负全部责任。</b>', 'https://timg.mnks.cn/20230926afc2b07f.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399838646706177, 1483, '89195', 0001, '如图所示，在环岛交叉路口发生的交通事故中，应由A车负全部责任。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第五十一条 机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行：<br>(一)在划有导向车道的路口，按所需行进方向驶入导向车道；<br>(二)<b>准备进入环形路口的让已在路口内的机动车先行</b>；<br>如图所示，A车准备进入环岛，B车在环岛内行驶，A车未让B车先行引发的交通事故，因此A车负事故的全部责任。', 'https://timg.mnks.cn/20230926e866cf01.jpg', 'A车负全部责任', 0002, 0.07);
INSERT INTO `question` VALUES (1733399838701232129, 1484, '88926', 0001, '驾驶机动车发生财产损失交通事故，当事人对事实及成因无争议的可先撤离现场。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》第十三条：<b>机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。</b>车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', '', '无争议', 0003, 0.13);
INSERT INTO `question` VALUES (1733399838747369473, 1485, '8fb61', 0001, '驾驶机动车发生财产损失交通事故后，当事人对事实及成因无争议移动车辆时需要对现场拍照或者标划停车位置。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。<b>车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。</b>非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', '', '无争议', 0002, 0.07);
INSERT INTO `question` VALUES (1733399838789312513, 1486, 'c1a4f', 0001, '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离造成交通堵塞的，可以对驾驶人处以200元罚款。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》第十九条：对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；<b>造成交通堵塞的</b>，对驾驶人处以200元罚款。', '', '造成交通堵塞', 0003, 0.10);
INSERT INTO `question` VALUES (1733399838835449858, 1487, 'd06cb', 0002, '机动车之间发生交通事故造成轻微财产损失，当事人对事实及成因无争议时，在确保安全的原则下，对现场拍照或标划事故车辆现场位置后，可自行撤离现场处理损害赔偿事宜，主要目的是什么？ ', 'A、双方互有损失 B、找现场证人就行了，不必报警 C、为了及时恢复交通，避免造成交通拥堵 D、事故后果很小，无需赔偿', '双方互有损失 ', '找现场证人就行了，不必报警 ', '为了及时恢复交通，避免造成交通拥堵 ', '事故后果很小，无需赔偿', 'C', '轻微交通事故，当事人将事故现场进行拍照后，<b>需及时撤离事故现场，恢复交通，以免造成交通拥堵</b>。<br>若有拒不撤离事故现场，造成交通拥堵的，会受到处罚。', '', '无争议|自行撤离现场', 0001, 0.03);
INSERT INTO `question` VALUES (1733399838889975809, 1488, 'ed719', 0001, '在道路上造成人身伤亡、事故后果非常严重的交通事故，可自行撤离现场。', NULL, '对', '错', '空', '空', '错', '《道路交通事故处理程序规定》第十三条 发生死亡事故、伤人事故的，或者发生财产损失事故且有下列情形之一的，当事人<b>应当保护现场并立即报警</b>：', '', '伤亡|自行撤离', 0002, 0.06);
INSERT INTO `question` VALUES (1733399838940307458, 1489, 'c5ea9', 0001, '车辆发生轻微剐蹭事故，双方驾驶人争执不下，坚持在原地等待警察来处理，造成路面堵塞，该行为会受到罚款的处罚。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，<b>立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。</b><br><b>对应当自行撤离现场而未撤离</b>的，交通警察应当责令当事人撤离现场；<b>造成交通堵塞的，</b><b>对驾驶人处以200元罚款</b>；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', '', '轻微|原地等待|造成|罚款', 0002, 0.05);
INSERT INTO `question` VALUES (1733399839003222017, 1490, 'e79bb', 0001, '如图所示，造成事故的责任是大客车倒车没有避让正常驶来的车辆，小客车看到前车掉头时没有停车等待。', NULL, '对', '错', '空', '空', '对', '安全倒车，倒车时，若发现有过往车辆通过，应主动停车避让。因掉头需要倒车时，应选择在不影响其他车辆和行人正常通行的地段操作。<b>如图所示交通事故</b>是由于大客车倒车未避让正常行驶的小客车，小客车看到大客车正在掉头时没有停车等待造成的。', 'https://timg.mnks.cn/20230926d4563e45.jpg', '大客车倒车', 0003, 0.10);
INSERT INTO `question` VALUES (1733399839049359361, 1491, 'e8f63', 0002, '驾驶机动车发生交通事故后当事人故意破坏、伪造现场、毁灭证据的，应当承担什么责任？ ', 'A、主要责任 B、次要责任 C、同等责任 D、全部责任', '主要责任 ', '次要责任 ', '同等责任 ', '全部责任', 'D', '《道路交通事故处理程序规定》第六十一条，当事人有下列情形之一的，<b>承担全部责任</b>：<br>（一）发生道路交通事故后逃逸的；<br><b>（二）故意破坏、伪造现场、毁灭证据的。</b>', '', '故意', 0003, 0.09);
INSERT INTO `question` VALUES (1733399839099691009, 1492, '3f9cf', 0001, '非机动车驾驶人、行人故意碰撞机动车造成交通事故的，机动车一方不承担赔偿责任。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第七十六条，交通事故的损失是由非机动车驾驶人、行人<b>故意碰撞</b>机动车造成的，机动车一方<b>不承担赔偿责任</b>。因此，题干表述正确。', '', '故意|不承担', 0004, 0.18);
INSERT INTO `question` VALUES (1733399839145828353, 1493, 'cacc0', 0001, '发生交通事故后，当事人故意破坏、伪造现场、毁灭证据的，承担全部责任。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》第六十一条，当事人有下列情形之一的，<b>承担全部责任</b>：<br>（一）发生道路交通事故后逃逸的；<br><b>（二）故意破坏、伪造现场、毁灭证据的。</b>', '', '故意|全部责任', 0001, 0.01);
INSERT INTO `question` VALUES (1733399839301017602, 1494, '5df53', 0001, '驾驶机动车在道路上发生交通事故，任何情况下都应标明现场位置后，先行撤离现场。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第七十条　在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。<b>因抢救受伤人员变动现场的，应当标明位置。</b>乘车人、过往车辆驾驶人、过往行人应当予以协助。<br>在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。<br>在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', '', '任何情况', 0005, 0.30);
INSERT INTO `question` VALUES (1733399839355543554, 1495, 'a2193', 0001, '驾驶机动车在道路上发生交通事故，当事人不能自行移动车辆的，应当保护现场并立即报警。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》第十三条，发生死亡事故、伤人事故的，或者发生财产损失事故且有下列情形之一的，当事人应当<b>保护现场并立即报警</b>：<br><b>（五）当事人不能自行移动车辆的；</b>', '', '不能自行移动|立即报警', 0002, 0.07);
INSERT INTO `question` VALUES (1733399839401680898, 1496, '74e69', 0002, '驾驶机动车发生以下交通事故，哪种情况适用自行协商解决？ ', 'A、对方饮酒的 B、对事实及成因有争议的 C、未造成人身伤亡，对事实及成因无争议的 D、造成人身伤亡的', '对方饮酒的 ', '对事实及成因有争议的 ', '未造成人身伤亡，对事实及成因无争议的 ', '造成人身伤亡的', 'C', '《道路交通安全法实施条例》第八十六条规定 机动车与机动车、机动车与非机动车在道路上发生<b>未造成人身伤亡的交通事故，当事人对事实及成因无争议的</b>，在记录交通事故的时间、地点、对方当事人的姓名和联系方式、机动车牌号、驾驶证号、保险凭证号、碰撞部位，并共同签名后，撤离现场，<b>自行协商损害赔偿事宜</b>。当事人对交通事故事实及成因有争议的，应当迅速报警。', '', '自行协商', 0001, 0.03);
INSERT INTO `question` VALUES (1733399839456206849, 1497, 'e5f45', 0002, '驾驶机动车发生交通事故，仅造成财产损失的，但是对交通事故事实及成因有争议的，应当怎么处理？ ', 'A、迅速报警 B、占道继续和对方争辩 C、找中间人帮忙解决 D、自行协商损害赔偿事宜', '迅速报警 ', '占道继续和对方争辩 ', '找中间人帮忙解决 ', '自行协商损害赔偿事宜', 'A', '《道路交通安全法实施条例》第八十六条　机动车与机动车、机动车与非机动车在道路上发生未造成人身伤亡的交通事故，当事人对事实及成因无争议的，在记录交通事故的时间、地点、对方当事人的姓名和联系方式、机动车牌号、驾驶证号、保险凭证号、碰撞部位，并共同签名后，撤离现场，自行协商损害赔偿事宜。<b>当事人对交通事故事实及成因有争议的</b>，应当<b>迅速报警</b>。', '', '有争议', 0004, 0.21);
INSERT INTO `question` VALUES (1733399839498149890, 1498, '32725', 0002, '发生交通事故时，下列哪种情况下当事人应当保护现场并立即报警？ ', 'A、未造成人员伤亡的 B、未发生财产损失事故 C、未损害公共设施及建筑物的 D、驾驶人有酒后驾驶嫌疑的', '未造成人员伤亡的 ', '未发生财产损失事故 ', '未损害公共设施及建筑物的 ', '驾驶人有酒后驾驶嫌疑的', 'D', '《道路交通事故处理程序规定》第十三条，发生死亡事故、伤人事故的，或者发生财产损失事故且有下列情形之一的，<b>当事人应当保护现场并立即报警</b>：<br>（一）驾驶人无有效机动车驾驶证或者驾驶的机动车与驾驶证载明的准驾车型不符的；<br>（二）<b>驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的；</b><br>（三）驾驶人有从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶嫌疑的；<br>（四）机动车无号牌或者使用伪造、变造的号牌的；<br>（五）当事人不能自行移动车辆的;<br>（六）一方当事人离开现场的；<br>（七）有证据证明事故是由一方故意造成的。', '', '保护现场', 0002, 0.05);
INSERT INTO `question` VALUES (1733399839540092930, 1499, 'ab719', 0001, '驾驶机动车与行人之间发生交通事故造成人身伤亡、财产损失的，机动车一方没有过错的，不承担赔偿责任。', NULL, '对', '错', '空', '空', '错', '《道路交通事故》第七十六条 机动车发生交通事故造成人身伤亡、财产损失的，由保险公司在机动车第三者责任强制保险责任限额范围内予以赔偿。超过责任限额的部分，按照下列方式承担赔偿责任：（一）机动车之间发生交通事故的，由有过错的一方承担责任；双方都有过错的，按照各自过错的比例分担责任。<b>（二）机动车与非机动车驾驶人、行人之间发生交通事故的，由机动车一方承担责任；但是，有证据证明非机动车驾驶人、行人违反道路交通安全法律、法规，机动车驾驶人已经采取必要处置措施的，减轻机动车一方的责任。</b>交通事故的损失是由非机动车驾驶人、行人故意造成的，机动车一方不承担责任。', '', '不承担|行人|人身伤亡', 0004, 0.23);
INSERT INTO `question` VALUES (1733399839590424577, 1500, 'fb1de', 0002, '发生无人员伤亡的、财产轻微损失的交通事故后，以下做法正确的是什么？ ', 'A、必须报警，等候警察处理 B、开车离开现场 C、确保安全的情况下，对现场拍照，然后将车辆移至路边等不妨碍交通的地点 D、停在现场保持不动', '必须报警，等候警察处理 ', '开车离开现场 ', '确保安全的情况下，对现场拍照，然后将车辆移至路边等不妨碍交通的地点 ', '停在现场保持不动', 'C', '《道路交通安全法实施条例》第八十六条规定 机动车与机动车、机动车与非机动车在道路上发生未造成人身伤亡的交通事故，当事人对事实及成因无争议的，<b>在记录交通事故的时间、地点、对方当事人的姓名和联系方式、机动车牌号、驾驶证号、保险凭证号、碰撞部位，并共同签名后，撤离现场</b>，自行协商损害赔偿事宜。当事人对交通事故事实及成因有争议的，应当迅速报警。', '', '做法正确', 0001, 0.03);
INSERT INTO `question` VALUES (1733399839636561922, 1501, 'e3c8f', 0001, '驾驶机动车在道路上发生交通事故造成人身伤亡的，驾驶人必须报警。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》第七十条规定：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并<b>迅速报告执勤的交通警察或者公安机关交通管理部门</b>。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。', '', '人身伤亡|必须', 0001, 0.02);
INSERT INTO `question` VALUES (1733399839707865090, 1502, 'bbcec', 0001, '两辆机动车发生轻微碰擦事故后，为保证理赔，必须等保险公司人员到场鉴定后才能撤离现场。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》中第七十条，在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当<b>先撤离现场再进行协商处理</b>。', '', '轻微碰擦|必须', 0004, 0.23);
INSERT INTO `question` VALUES (1733399839754002433, 1503, '2ee36', 0002, '遇到这种单方交通事故，应如何处理？ ', 'A、不用报警 B、报警 C、直接联系路政部门进行理赔 D、直接联系绿化部门', '不用报警 ', '报警 ', '直接联系路政部门进行理赔 ', '直接联系绿化部门', 'B', '如图所示，机动车碰撞道路防护栏，应立即报警处理。根据《道路交通安全法》道路交通事故有下列情形之一的，当事人应当保护现场并<b>立即报警</b>：5、<b>碰撞建筑物、公共设施或者其他设施的</b>；', 'https://timg.mnks.cn/20230926a33272af.jpg', '单方', 0003, 0.08);
INSERT INTO `question` VALUES (1733399839804334081, 1504, '11346', 0001, '机动车之间发生交通事故，不管是否有人员伤亡，只要双方当事人同意，都可自行协商解决。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第七十条：在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；造成人身伤亡的，应立即报警由交警认定事故中是否存在酒驾等刑事犯罪行为。', '', '都可|只要', 0003, 0.14);
INSERT INTO `question` VALUES (1733399839837888514, 1505, '164e0', 0001, '事故报警时，要向交警提供事故地点、人员伤情、车辆号牌等信息，协助交警快速定位到达现场。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法》规定：发生事故报警时，要向交警提供事故地点、人员伤情、车辆号牌等信息，协助交警快速定位到达现场。', '', '提供|协助', 0001, 0.02);
INSERT INTO `question` VALUES (1733399839896608769, 1506, 'd9a53', 0002, '机动车与机动车发生轻微剐蹭，未造成人身伤亡，当事人对事实及成因无争议，且排除驾驶人有无证驾驶、酒后驾驶、机动车无号牌或使用伪造变造号牌情况的，以下做法正确的是什么？ ', 'A、立即报警 B、保护好现场 C、撤离现场，自行协商处理 D、不得移动车辆', '立即报警 ', '保护好现场 ', '撤离现场，自行协商处理 ', '不得移动车辆', 'C', '《道路交通安全法》第七十条：在道路上发生交通事故，<b>未造成人身伤亡，当事人对事实及成因无争议</b>的，可以<b>即行撤离现场，恢复交通，自行协商处理损害赔偿事宜</b>；不立即撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', '', '未造成|无争议|做法正确|排除', 0004, 0.22);
INSERT INTO `question` VALUES (1733399839959523329, 1507, 'b49ea', 0001, '车辆发生责任明确的轻微剐蹭事故，双方驾驶人争执不下，能够自行撤离现场而未自行撤离，坚持在原地等待警察来处理，造成路面堵塞，该行为会受到罚款的处罚。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，<b>立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。</b><br><b>对应当自行撤离现场而未撤离</b>的，交通警察应当责令当事人撤离现场；<b>造成交通堵塞的，</b><b>对驾驶人处以200元罚款</b>；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', '', '责任明确|未自行撤离|罚款', 0002, 0.04);
INSERT INTO `question` VALUES (1733399840005660674, 1508, 'abc7c', 0001, '发生仅造成财产损失交通事故，且双方无争议。但其中一方损毁严重不能移动的，可以自行协商解决。', NULL, '对', '错', '空', '空', '错', '《道路交通事故处理程序规定》第十三条，发生死亡事故、伤人事故的，或者<b>发生财产损失事故且有下列情形之一</b>的，当事人应当<b>保护现场并立即报警</b>：<br>（一）驾驶人无有效机动车驾驶证或者驾驶的机动车与驾驶证载明的准驾车型不符的；<br>（二）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的；<br>（三）驾驶人有从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶嫌疑的；<br>（四）机动车无号牌或者使用伪造、变造的号牌的；<br>（五）<b>当事人不能自行移动车辆的;</b><br>（六）一方当事人离开现场的；<br>（七）有证据证明事故是由一方故意造成的。', '', '不能移动|可以自行协商', 0004, 0.26);
INSERT INTO `question` VALUES (1733399840051798017, 1509, 'b0dda', 0002, '驾驶机动车与非机动车发生剐蹭，未造成人身伤亡且对事实及成因无争议的，以下做法正确的是什么？ ', 'A、撤离现场，自行协商损害赔偿事宜 B、现场找人指证 C、立即报警 D、不得移动车辆', '撤离现场，自行协商损害赔偿事宜 ', '现场找人指证 ', '立即报警 ', '不得移动车辆', 'A', '《道路交通安全法》第七十条：在道路上发生交通事故，<b>未造成人身伤亡，当事人对事实及成因无争议</b>的，可以<b>即行撤离现场，恢复交通，自行协商处理损害赔偿事宜</b>；不立即撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', '', '未造成|无争议|做法正确', 0002, 0.07);
INSERT INTO `question` VALUES (1733399840106323969, 1510, 'd9939', 0001, '机动车发生财产损失事故且一方当事人离开现场的，应当保护现场并立即报警。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》第十三条，发生死亡事故、伤人事故的，或者<b>发生财产损失事故且</b>有下列情形之一的，当事人应当<b>保护现场并立即报警</b>：<br>（一）驾驶人无有效机动车驾驶证或者驾驶的机动车与驾驶证载明的准驾车型不符的；<br>（二）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的；<br>（三）驾驶人有从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶嫌疑的；<br>（四）机动车无号牌或者使用伪造、变造的号牌的；<br>（五）当事人不能自行移动车辆的;<br><b>（六）一方当事人离开现场的；</b><br>（七）有证据证明事故是由一方故意造成的。', '', '财产损失|离开现场|立即报警', 0001, 0.02);
INSERT INTO `question` VALUES (1733399840148267010, 1511, 'dc521', 0002, '在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，应如何处置？ ', 'A、不得移动车辆 B、保护好现场并报警 C、指挥过往车辆绕行 D、当事人应当先撤离现场再进行协商处理', '不得移动车辆 ', '保护好现场并报警 ', '指挥过往车辆绕行 ', '当事人应当先撤离现场再进行协商处理', 'D', '《道路交通安全法》第七十条：在道路上发生交通事故，<b>仅造成轻微财产损失，并且基本事实清楚</b>的，当事人应当<b>先撤离现场再进行协商处理</b>。<br>追尾、碰擦等不涉及人伤、责任明确、损失轻微的道路交通事故，当事人应当先撤离现场，再进行协商处理，以恢复交通。现场不快速撤离造成拥堵的，交警部门将依法进行处罚。', '', '轻微|事实清楚', 0002, 0.06);
INSERT INTO `question` VALUES (1733399840194404354, 1512, 'f743c', 0001, '机动车与行人之间发生交通事故造成人身伤亡、财产损失的，机动车一方没有过错的，应该由行人承担赔偿责任。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第七十六条，（二）<b>机动车与</b>非机动车驾驶人、<b>行人之间发生交通事故</b>，非机动车驾驶人、行人没有过错的，由机动车一方承担赔偿责任；有证据证明非机动车驾驶人、行人有过错的，根据过错程度适当减轻机动车一方的赔偿责任；<b>机动车一方没有过错的，承担不超过百分之十的赔偿责任。</b><br>　　交通事故的损失是由非机动车驾驶人、行人故意碰撞机动车造成的，机动车一方不承担赔偿责任。', '', '没有过错|行人承担', 0004, 0.27);
INSERT INTO `question` VALUES (1733399840236347394, 1513, 'e5427', 0001, '机动车发生人员受伤的交通事故，且驾驶人有饮酒嫌疑的，当事人应当保护现场并立即报警。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，<b>当事人应当保护现场并立即报警：</b> <br><b>（一）造成人员死亡、受伤的； </b>　　<br>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　<br>（三）机动车无号牌、无检验合格标志、无保险标志的； 　　<br>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的；　　<br>（五）碰撞建筑物、公共设施或者其他设施的；　　<br>（六）驾驶人无有效机动车驾驶证的； 　　<br><b>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的</b>； <br>（八）当事人不能自行移动车辆的。 　　<br>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', '', '人员受伤|饮酒嫌疑|报警', 0001, 0.01);
INSERT INTO `question` VALUES (1733399840286679042, 1514, 'ccea6', 0001, '机动车发生人员死亡的交通事故时，当事人应当保护现场并立即报警。', NULL, '对', '错', '空', '空', '对', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，<b>当事人应当保护现场并立即报警</b>：<br><b>（一）造成人员死亡、受伤的</b>； 　　<br>（二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　<br>（三）机动车无号牌、无检验合格标志、无保险标志的； 　　<br>（四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的；　　<br>（五）碰撞建筑物、公共设施或者其他设施的；　　<br>（六）驾驶人无有效机动车驾驶证的； 　　<br>（七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的；<br>（八）当事人不能自行移动车辆的。 　　<br>发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', '', '人员死亡|立即报警', 0001, 0.01);
INSERT INTO `question` VALUES (1733399840357982210, 1515, 'd9908', 0001, '如图所示，两车在路口发生事故，A车没有让右方道路来车先行，在无其他过错情形下，A车应负全责。', NULL, '对', '错', '空', '空', '错', '如图所示，A车直行，B车右转，<b>A车享有优先通行权</b>；当信号灯为圆形红灯亮时，B车可以右转，但不得妨碍被放行的直行车辆。所以，右转弯行驶的B车应当让直行的A车先行，该起交通事故<b>B车应负全责</b>。', 'https://timg.mnks.cn/20230927bfe89fb0.jpg', '没有让右|负全责', 0003, 0.10);
INSERT INTO `question` VALUES (1733399840399925249, 1516, 'bd446', 0001, '你驾驶的机动车在高速公路上发生故障时，你应开启危险报警闪光灯增大警示范围。', NULL, '对', '错', '空', '空', '对', '机动车在高速公路上发生故障时，应当先<b>开启危险报警灯示警</b>，然后将车辆移到不妨碍交通的位置，再将警示标志放置在车辆后方150m以外，将车内人员转移到安全的地方，最后报警等待救援。', '', '应开启|警示', 0002, 0.05);
INSERT INTO `question` VALUES (1733399840441868289, 1517, 'f72a3', 0002, '非机动车驾驶人、行人与处于静止状态的机动车发生交通事故造成损失，机动车一方无责任，关于机动车一方的赔偿责任，下面哪种说法是正确的是什么？ ', 'A、不承担赔偿责任 B、承担10%赔偿责任 C、承担60%赔偿责任 D、承担全部责任', '不承担赔偿责任 ', '承担10%赔偿责任 ', '承担60%赔偿责任 ', '承担全部责任', 'A', '非机动车驾驶人、行人与处于静止状态的机动车发生交通事故造成损失、<b>机动车一方无事故责任</b>，或者非机动车驾驶人、行人故意碰撞机动车的，机动车一方<b>不承担赔偿责任</b>。<br>《道路交通安全法》第七十六条，交通事故的损失是由非机动车驾驶人、行人故意碰撞机动车造成的，机动车一方<b>不承担赔偿责任</b>。', '', '静止状态的机动车|无责任', 0005, 0.32);
INSERT INTO `question` VALUES (1733399840479617025, 1518, 'ad249', 0002, '如图所示，A车在倒车过程中发生事故，应负什么责任？ ', 'A、次要责任 B、主要责任 C、全部责任 D、没有责任', '次要责任 ', '主要责任 ', '全部责任 ', '没有责任', 'C', '机动车倒车入库时，应当察明车后情况，确认安全后再倒车，如图所示，被撞车辆停在规定的停车位内，无违法行为；事故的原因是由A车倒车撞到后车造成的，因此A车应负全部责任。', 'https://timg.mnks.cn/20230606d9cc9d32.jpg', '倒车', 0003, 0.11);
INSERT INTO `question` VALUES (1733399840534142978, 1519, 'e3277', 0002, '如图所示，该机动车发生故障在应急车道停车，该车停车时存在的不安全行为是什么？①未开启危险报警闪光灯 ②警告标志放置不足150米③车上人员未转移到安全区域。 ', 'A、①③ B、②③ C、①②③ D、①②', '①③ ', '②③ ', '①②③ ', '①②', 'C', '机动车在高速公路上发生故障必须停车时，<b>立即开启危险报警闪光灯</b>，夜间还有开启示廓灯和后位灯，在<b>车后150米以外设置警告标志</b>，车上人员要<b>迅速转移至车右前方护栏以外的安全地带</b>，面向来车方向并迅速拨打紧急电话求援或报警。<br>因此，如图所示情形中存在的不安全行为是：1、未开启危险报警闪光灯；2、警告标志放置不足150米；3、人员未转移到护栏以外的安全地带。', 'https://timg.mnks.cn/20230926da0a29d0.jpg', '应急车道停车|不安全行为', 0003, 0.14);
INSERT INTO `question` VALUES (1733399840576086018, 1520, 'e92fb', 0002, '在高速公路以外的道路上使用三角警告牌时，应设置在来车方向多少米处？ ', 'A、50m~100m B、100m~150m C、25m~50m D、0m~25m', '50m~100m ', '100m~150m ', '25m~50m ', '0m~25m', 'A', '《道路交通安全法实施条例》第六十条，机动车<b>在道路上</b>发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并<b>在车后50米至100米处设置警告标志</b>，夜间还应当同时开启示廓灯和后位灯。<br>机动车在高速公路上发生故障时，警告标志应当设置在故障车来车方向一百五十米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', '', '以外的道路|来车方向', 0005, 0.48);
INSERT INTO `question` VALUES (1733399840794189825, 1521, 'd7944', 0002, '机动车在高速公路上发生故障时，应当如何设置警告标志？ ', 'A、本车车前 B、来车方向 C、不用设置 D、随意设置', '本车车前 ', '来车方向 ', '不用设置 ', '随意设置', 'B', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；<b>警告标志应当设置在故障车来车方向150米以外</b>，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', '', '警告标志', 0002, 0.07);
INSERT INTO `question` VALUES (1733399841037459457, 1522, 'f1da0', 0002, '机动车在道路上发生故障，需要停车排除时，驾驶人应立即开启危险报警闪光灯，还应怎样做？ ', 'A、就地停车后迅速报警 B、就地停车，以免造成机械事故 C、迅速停车，并在车前方设置警告标志 D、将机动车移至不妨碍交通的地方停放', '就地停车后迅速报警 ', '就地停车，以免造成机械事故 ', '迅速停车，并在车前方设置警告标志 ', '将机动车移至不妨碍交通的地方停放', 'D', '《道路交通安全法》第五十二条，<b>机动车在道路上发生故障，需要停车排除故障时，驾驶人应当立即开启危险报警闪光灯，将机动车移至不妨碍交通的地方停放</b>；难以移动的，应当持续开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离，必要时迅速报警。', '', '需要停车', 0004, 0.16);
INSERT INTO `question` VALUES (1733399841087791105, 1523, 'ad793', 0002, '对未按照国家规定投保机动车第三者责任强制保险的车辆，除扣留机动车外，还会受到什么处罚？ ', 'A、投保最低责任限额应缴纳保险费的5倍罚款 B、投保最低责任限额应缴纳保险费的4倍罚款 C、投保最低责任限额应缴纳保险费的3倍罚款 D、投保最低责任限额应缴纳保险费的2倍罚款', '投保最低责任限额应缴纳保险费的5倍罚款 ', '投保最低责任限额应缴纳保险费的4倍罚款 ', '投保最低责任限额应缴纳保险费的3倍罚款 ', '投保最低责任限额应缴纳保险费的2倍罚款', 'D', '《道路交通安全法》第九十八条，机动车所有人、管理人<b>未按照国家规定</b>投保机动车第三者责任强制保险的，由公安机关交通管理部门扣留车辆至依照规定投保后，并处依照规定投保最低责任限额应缴纳的<b>保险费的二倍罚款</b>。', '', '未按照|第三者责任强制保险', 0005, 0.33);
INSERT INTO `question` VALUES (1733399841125539842, 1524, 'b5700', 0002, '当事人未在道路交通事故现场报警，事后请求公安机关交通管理部门处理的，公安机关交通管理部门应当按照相关规定予以记录，并在多长时间内做出是否受理的决定？ ', 'A、3日 B、5日 C、2日 D、10日', '3日 ', '5日 ', '2日 ', '10日', 'A', '《道路交通事故处理程序规定》第十二条规定：当事人未在道路交通事故现场报警，事后请求公安机关交通管理部门处理的，公安机关交通管理部门应当按照本规定第十条的规定予以记录，并在<b>三日内</b>作出是否受理的决定。当事人应当在提出请求后10日内向公安机关交通管理部门提供交通事故证据。', '', '事后请求|应当', 0005, 0.29);
INSERT INTO `question` VALUES (1733399841163288577, 1525, 'fda87', 0002, '驾驶机动车发生交通事故，以下哪种情况不可以自行协商解决？ ', 'A、未造成人身伤亡 B、车辆可以移动 C、对事实及成因无争议 D、其中一方使用伪造、变造的车牌', '未造成人身伤亡 ', '车辆可以移动 ', '对事实及成因无争议 ', '其中一方使用伪造', 'D', '《道路交通事故处理程序规定》第十三条 发生死亡事故、伤人事故的，或者发生财产损失事故且有下列情形之一的，当事人<b>应当保护现场并立即报警</b>：<br>（一）驾驶人无有效机动车驾驶证或者驾驶的机动车与驾驶证载明的准驾车型不符的；<br>（二）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的；<br>（三）驾驶人有从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶嫌疑的；<br>（四）<b>机动车无号牌或者使用伪造、变造的号牌的</b>；<br>（五）当事人不能自行移动车辆的;<br>（六）一方当事人离开现场的；<br>（七）有证据证明事故是由一方故意造成的。', '', '不可以', 0004, 0.17);
INSERT INTO `question` VALUES (1733399841217814530, 1526, 'f13cb', 0001, '机动车发生轻微财产损失的交通事故，对应当自行撤离现场而未撤离的，交通警察有权责令当事人撤离现场。', NULL, '对', '错', '空', '空', '对', '《中华人民共和国道路交通安全法》中第七十条第二、三款规定： <br>在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 <br><b>机动车发生轻微财产损失的交通事故，对应当自行撤离现场而未撤离的，交通警察有权责令当事人撤离现场。</b>', '', '有权责令', 0002, 0.04);
INSERT INTO `question` VALUES (1733399841276534786, 1527, 'bd536', 0001, '有醉酒后驾驶行为的，机动车交通事故责任强制保险费率会上浮。', NULL, '对', '错', '空', '空', '对', '根据公安部、中国保险监督管理委员会《关于实行酒后驾驶与机动车交强险费率联系浮动制度的通知》的要求，酒后驾驶违法行为与机动车交强险费率联系浮动。饮酒后驾驶违法行为一次上浮的交强险费率控制在10%至15%之间，<b>醉酒后驾驶违法行为一次上浮的交强险费率控制在20%至30%之间，累计上浮的费率不得超过60%。</b>', '', '醉酒|上浮', 0003, 0.15);
INSERT INTO `question` VALUES (1733399841318477826, 1528, 'f9162', 0002, '行车中遇有前方发生交通事故，需要帮助时，应怎样做？ ', 'A、尽量绕道躲避 B、立即报警，停车观望 C、协助保护现场，并立即报警 D、加速通过，不予理睬', '尽量绕道躲避 ', '立即报警，停车观望 ', '协助保护现场，并立即报警 ', '加速通过，不予理睬', 'C', '助人为乐是中华民族的传统美德，也是文明驾驶的重要体现，驾车过程中如发现他人遇到困难，应积极主动伸出援助之手，为他人排忧解难。<b>遇到交通事故需要帮助时</b>，应<b>减速停车</b>，<b>协助保护事故现场，并立即报警</b>。如果伤者需要抢救，应及时拨打急救电话或送伤者去医院。', '', '交通事故|需要帮助', 0002, 0.05);
INSERT INTO `question` VALUES (1733399841389780994, 1529, '335c9', 0002, '行车中遇交通事故受伤者需要抢救时，应怎样做？ ', 'A、及时将伤者送医院抢救或拨打急救电话 B、尽量避开，少惹麻烦 C、绕过现场行驶 D、借故避开现场', '及时将伤者送医院抢救或拨打急救电话 ', '尽量避开，少惹麻烦 ', '绕过现场行驶 ', '借故避开现场', 'A', '行车中遇到交通事故需要帮助时，应减速停车，协助保护事故现场，并立即报警。<b>如果伤者需要抢救，应及时拨打急救电话或送伤者去医院</b>。', '', '交通事故|需要抢救', 0001, 0.01);
INSERT INTO `question` VALUES (1733399841427529729, 1530, 'f3ab4', 0002, '机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由哪类机动车拖曳、牵引？ ', 'A、救援车、清障车 B、小型汽车 C、中型客车 D、重型货车', '救援车', '清障车 B', '小型汽车 C', '中型客车 D', 'A', '《道路交通安全法》第六十八条： 机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当<b>由救援车、清障车拖曳、牵引</b>。', '', '故障|拖曳|牵引', 0001, 0.02);
INSERT INTO `question` VALUES (1733399841469472770, 1531, '757ad', 0002, '在下图所示的交通事故中，有关事故责任认定，正确的说法是什么？ ', 'A、B车闯红灯，所以B负全责 B、B车可以右转，但不得妨碍被放行的直行车辆，所以B车负全责 C、直行车辆不得妨碍右转车辆，所以A车负全责 D、右侧方向的车辆具有优先通行权，故A车负全责', 'B车闯红灯，所以B负全责 ', 'B车可以右转，但不得妨碍被放行的直行车辆，所以B车负全责 ', '直行车辆不得妨碍右转车辆，所以A车负全责 ', '右侧方向的车辆具有优先通行权，故A车负全责', 'B', '如图所示，A车直行，B车右转，<b>A车享有优先通行权</b>；当信号灯为圆形红灯亮时，B车可以右转，但不得妨碍被放行的直行车辆。所以，右转弯行驶的B车应当让直行的A车先行，该起交通事故<b>B车应负全责</b>。', 'https://timg.mnks.cn/20230927bfe89fb0.jpg', '责任认定', 0003, 0.12);
INSERT INTO `question` VALUES (1733399841511415809, 1532, 'a746e', 0001, '高速公路因发生事故造成堵塞时，可在右侧紧急停车带或路肩行驶。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第八十二条 机动车在高速公路上行驶，<b>不得有下列行为</b>： <br>(一)倒车、逆行、穿越中央分隔带掉头或者在车道内停车； <br>(二)在匝道、加速车道或者减速车道上超车； <br>(三)骑、轧车行道分界线或者<b>在路肩上行驶</b>； <br>(四)<b>非紧急情况时在应急车道行驶</b>或者停车；', '', '堵塞|紧急停车带|路肩行驶', 0003, 0.15);
INSERT INTO `question` VALUES (1733399841553358849, 1533, '6975e', 0002, '机动车在高速公路上发生故障或交通事故无法正常行驶时由什么车拖曳或牵引？ ', 'A、过路车 B、大客车 C、同行车 D、清障车', '过路车 ', '大客车 ', '同行车 ', '清障车', 'D', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；但是，警告标志应当设置在故障车来车方向一百五十米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。<b>机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引。</b>', '', '拖拽或牵引', 0001, 0.02);
INSERT INTO `question` VALUES (1733399841595301890, 1534, '93929', 0001, '机动车在高速公路上发生故障时，在来车方向50至100米处设置警告标志。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第六十八条：机动车在<b>高速公路</b>上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向<b>150米以外</b>，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', '', '警告标志', 0003, 0.14);
INSERT INTO `question` VALUES (1733399841662410754, 1535, 'b72f1', 0001, '车辆发生故障被牵引时，可以临时搭载乘客。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法实施条例》第六十一条 <b>牵引故障机动车应当遵守</b>下列规定：<br>(一)被牵引的机动车除驾驶人外<b>不得载人</b>，不得拖带挂车；<br>(二)被牵引的机动车宽度不得大于牵引机动车的宽度；<br>(三)使用软连接牵引装置时，牵引车与被牵引车之间的距离应当大于4米小于10米；<br>(四)对制动失效的被牵引车，应当使用硬连接牵引装置牵引；<br>(五)牵引车和被牵引车均应当开启危险报警闪光灯。', '', '被牵引|可以|搭载', 0002, 0.05);
INSERT INTO `question` VALUES (1733399841716936706, 1536, '3185e', 0002, '这辆在高速公路上临时停放的故障车，警告标志应该设置在车后多远处？ ', 'A、150米以外 B、50～150米 C、50～100米 D、50米以内', '150米以外 ', '50～150米 ', '50～100米 ', '50米以内', 'A', '《道路交通安全法》第六十八条&nbsp; 机动车在高速公路上发生故障时，<b>警告标志应当设置在故障车来车方向一百五十米以外</b>，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。&nbsp;&nbsp;', 'https://timg.mnks.cn/20230927d8d32be2.jpg', '警告标志', 0002, 0.07);
INSERT INTO `question` VALUES (1733399841788239873, 1537, 'e0e77', 0001, '机动车在高速公路上发生故障时，将车上人员迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', NULL, '对', '错', '空', '空', '对', '在高速公路上发生交通事故，应当遵守两大原则：一是确保人员安全；二是预防二次事故发生。一旦发生事故，一定要做到<b>车靠边、</b><b>人撤离</b>、<b>即报警</b>，<b>避免二次伤害</b>。<br>车靠边：车辆移动到最右边的应急车道，开启危险报警闪光灯，按规定设置警告标志。<br><b>人撤离</b>：人员迅速转移到右侧路肩、应急车道内或护栏外的安全地带。<br><b>即报警</b>：撤离后，立即拨打110或122报警。', '', '迅速转移|迅速报警', 0003, 0.14);
INSERT INTO `question` VALUES (1733399841851154434, 1538, '3e0ad', 0001, '牵引故障车时，牵引与被牵引的机动车，在行驶中都要开启危险报警闪光灯。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第六十一条 牵引故障机动车应当遵守下列规定： 　　 <br>(一)被牵引的机动车除驾驶人外不得载人，不得拖带挂车； 　　 <br>(二)被牵引的机动车宽度不得大于牵引机动车的宽度； 　　 <br>(三)使用软连接牵引装置时，牵引车与被牵引车之间的距离应当大于4米小于10米； 　　 <br>(四)对制动失效的被牵引车，应当使用硬连接牵引装置牵引； 　　 <br>(五)<b>牵引车和被牵引车均应当开启危险报警闪光灯。</b>汽车吊车和轮式专用机械车不得牵引车辆。摩托车不得牵引车辆或者被其他车辆牵引。转向或者照明、信号装置失效的故障机动车，应当使用专用清障车拖曳。&gt;&gt;&gt;因此本题正确。', '', '故障车|都要开启', 0001, 0.03);
INSERT INTO `question` VALUES (1733399841905680385, 1539, '81923', 0002, '驾驶机动车在高速公路发生故障，需要停车排除故障时，以下做法先后顺序正确的是？①放置警告标志，转移乘车人员至安全处，迅速报警②开启危险报警闪光灯③将车辆移至不妨碍交通的位置④等待救援。 ', 'A、④③①② B、①②③④ C、③②①④ D、②③①④', '④③①② ', '①②③④ ', '③②①④ ', '②③①④', 'D', '车辆在高速公路上发生故障，需要停车排除故障时，应①迅速打开危险报警闪光灯；②将车辆移至不妨碍交通的位置；③在来车方向150米外设置警告标志，同时驾乘人员都应尽快从车辆安全的一侧离开转移至护栏外避险，并立即报警；④等待救援。不要因紧急避险造成二次事故或更大的损失。【车靠边、人撤离、即报警】', '', '高速|故障', 0004, 0.24);
INSERT INTO `question` VALUES (1733399841943429121, 1540, '30f8a', 0001, '车辆发生故障无法移动时，以下做法是否正确？', NULL, '对', '错', '空', '空', '错', '危险警告标志应放置在故障车<b>车后的150米以外</b>，图中危险警告标志牌放在故障车前，因此做法错误。<br>【拓展知识】<br>机动车在高速公路上发生故障无法移动时，应当持续开启危险报警闪光灯，并<b>在故障车来车方向150米以外设置警告标志</b>等措施扩大示警距离，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 'https://timg.mnks.cn/20230926de308394.jpg', '无法移动', 0003, 0.08);
INSERT INTO `question` VALUES (1733399841989566465, 1541, '40d77', 0002, '机动车在道路上发生故障，难以移动时下列做法正确的是什么？ ', 'A、开启危险报警闪光灯 B、开启车上所有灯光 C、禁止车上人员下车 D、在车前方设置警告标志', '开启危险报警闪光灯 ', '开启车上所有灯光 ', '禁止车上人员下车 ', '在车前方设置警告标志', 'A', '《道路交通安全法》第五十二条，<b>机动车在道路上发生故障</b>，需要停车排除故障时，驾驶人应当立即开启危险报警闪光灯，将机动车移至不妨碍交通的地方停放；<b>难以移动的</b>，应当<b>持续开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离，必要时迅速报警</b>。【车靠边、人撤离、即报警】', '', '难以移动时', 0003, 0.16);
INSERT INTO `question` VALUES (1733399842023120898, 1542, '04fb8', 0002, '车辆因故障必须在高速公路停车时，应在车后方多少米以外设置故障警告标志？ ', 'A、25 B、150 C、100 D、50', '25 ', '150 ', '100 ', '50', 'B', '《道路交通安全法》第六十八条：机动车在<b>高速公路</b>上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向<b>150米以外</b>，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', '', '因故障|以外', 0002, 0.05);
INSERT INTO `question` VALUES (1733399842086035457, 1543, 'e63cd', 0002, '驾驶机动车在高速公路上行驶，发生故障必须停车检查时，可以在什么地方停车？ ', 'A、应急车道内 B、出口匝道上 C、最左侧车道 D、进口匝道上', '应急车道内 ', '出口匝道上 ', '最左侧车道 ', '进口匝道上', 'A', '机动车<b>在高速路发生故障必须停车检查</b>时，应控制好车速，观察前后及右侧交通情况，开启右转向灯，尽快驶离行车道，<b>将车停在紧急停车带或应急车道内</b>。切不可紧急停车，更不要在行车道内停车。', '', '必须停车|高速公路', 0002, 0.07);
INSERT INTO `question` VALUES (1733399842119589889, 1544, 'c7f6c', 0001, '驾驶机动车在高速公路上车辆发生故障时，为获得其他车辆的帮助，可将警告标志放置在其他车道。', NULL, '对', '错', '空', '空', '错', '机动车在高速路或城市快速路上发生故障时，警告标志应当设置在<b>故障车来车方向</b>150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。因此，题干<b>将警告标志放在其他车道的做法是错误的</b>。', '', '警告标志放置', 0003, 0.11);
INSERT INTO `question` VALUES (1733399842161532929, 1545, 'a4f81', 0001, '驾驶机动车在高速公路上车辆发生故障时，若车辆可以移动至应急车道内，只需开启危险报警闪光灯，警告标志可根据交通流情况选择是否放置。', NULL, '对', '错', '空', '空', '错', '车辆移至应急车道停车后，应<b>①立即开启危险报警闪光灯</b>（夜间还要开启示廓灯和后位灯），<b>②在车后150米以外设置警告标志，</b>③车上人员要迅速转移至车右前方护栏以外的安全地带，④面向来车方向并迅速拨打紧急电话求援或报警。', '', '只需开启', 0004, 0.22);
INSERT INTO `question` VALUES (1733399842203475970, 1546, 'bcbfb', 0002, '机动车在高速公路上发生故障时错误的做法是什么？ ', 'A、开启危险报警闪光灯 B、按规定设置警告标志 C、车上人员不能下车 D、迅速报警', '开启危险报警闪光灯 ', '按规定设置警告标志 ', '车上人员不能下车 ', '迅速报警', 'C', '《道路交通安全法》第五十二条：机动车在高速公路上发生故障必须停车时，立即<b>开启危险报警闪光灯</b>，夜间还有开启示廓灯和后位灯，在<b>车后150米以外设置警告标志</b>，<b>车上人员要迅速转移至车右前方护栏以外的安全地带</b>，面向来车方向并迅速拨打紧急电话求援或报警。【车靠边、人撤离、即报警】<br><b>综上，车上人员不能下车，做法错误</b>。', '', '错误的做法', 0003, 0.12);
INSERT INTO `question` VALUES (1733399842237030401, 1547, '07081', 0001, '车辆发生故障而无法移动时，首先应在车辆后方50-150米处放置危险警告标志，防止后车追尾。', NULL, '对', '错', '空', '空', '错', '车辆发生故障无法移动时，<b>应当首先开启</b>危险报警闪光灯。<br>根据《道路交通安全法》规定，机动车在路上发生故障时，应当采取以下措施：1、<b>驾驶人应当立即开启危险报警闪光灯</b>，将机动车移至不妨碍交通的地方停放；2、难以移动的，应当持续开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离。普通道路上应把警告标志设置在车后50米-100米处，高速公路上应把警告标志设置在故障车来车方向150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。<br><b>因此，应首先开启危险报警闪光灯，而不是首先放置警告标志做法。</b>', '', '发生故障|首先|放置', 0005, 0.37);
INSERT INTO `question` VALUES (1733399842295750658, 1548, 'eaaf6', 0001, '机动车在夜间道路上发生故障难以移动时要开启危险报警闪光灯、示廓灯、后位灯。', NULL, '对', '错', '空', '空', '对', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启<b>危险报警闪光灯</b>并在车后50米至100米处设置警告标志，<b>夜间</b>还应当同时开启<b>示廓灯和后位灯</b>。', '', '难以移动', 0002, 0.04);
INSERT INTO `question` VALUES (1733399842354470914, 1549, 'a02d4', 0001, '车辆在高速公路发生故障不能移动时，驾驶人这种尝试排除故障的做法是否正确？', NULL, '对', '错', '空', '空', '错', '车辆在高速公路发生故障不能移动时，应①<b>开启危险报警闪光灯</b>（夜间还要开启示廓灯和后位灯），②<b>在车后150米以外设置警告标志</b>，③<b>车上人员要迅速转移至车右前方护栏以外的安全地带</b>，④<b>面向来车方向并迅速拨打紧急电话求援或报警</b>。因此，如图所示，驾驶人的处理方式错误。', 'https://timg.mnks.cn/20230926e252caa6.jpg', '不能移动|是否正确', 0002, 0.04);
INSERT INTO `question` VALUES (1733399842400608258, 1550, 'bdb21', 0001, '高速公路上车辆发生故障后，开启危险报警闪光灯和摆放警告标志的作用是警告后续车辆注意避让。', NULL, '对', '错', '空', '空', '对', '高速公路上车辆发生故障后，要开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离，主要用于提醒其他车辆注意到本车发生的特殊情况，<b>起到警示作用</b>。', '', '注意避让', 0001, 0.02);
INSERT INTO `question` VALUES (1733399842455134210, 1551, 'fb5c5', 0002, '在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，应当如何处置？ ', 'A、保护现场，请保险公司定损 B、即行撤离现场，自行协商处理损害赔偿事宜 C、将车停在原地，保护好现场，等待交通警察前来处理 D、不得撤离现场', '保护现场，请保险公司定损 ', '即行撤离现场，自行协商处理损害赔偿事宜 ', '将车停在原地，保护好现场，等待交通警察前来处理 ', '不得撤离现场', 'B', '《道路交通安全法》第七十条：在道路上发生交通事故，未造成人身伤亡，当事人<b>对事实及成因无争议</b>的，可以<b>即行撤离现场，恢复交通</b>，<b>自行协商处理</b>损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。', '', '未造成|无争议', 0003, 0.13);
INSERT INTO `question` VALUES (1733399842522243073, 1552, '6a1bd', 0002, '这个仪表是何含义？ ', 'A、发动机转速表 B、行驶速度表 C、区间里程表 D、百公里油耗表', '发动机转速表 ', '行驶速度表 ', '区间里程表 ', '百公里油耗表', 'A', '<b>发动机转速表</b>：指示发动机的转速，单位为1000转/分（×1000r/min）；转速表指针所指的数字显示当前发动机转速。发动机运转时，转速表指针不能超过红色警示区。', 'https://timg.mnks.cn/20230927f54d0581.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399842576769026, 1553, '39449', 0002, '这个仪表是何含义？ ', 'A、百公里油耗表 B、速度和里程表 C、发动机转速表 D、最高时速值表', '百公里油耗表 ', '速度和里程表 ', '发动机转速表 ', '最高时速值表', 'B', '<b>速度和里程表</b>：速度表指示汽车行驶速度，单位为公里/小时（km/h），速度表指针所指的数字显示当前车辆的行驶速度。里程表累计行驶总里程数以公里（km）为单位；日里程表用于记录一天或某段区间的里程数；按回零按钮至“0”位后重新开始计数。', 'https://timg.mnks.cn/20230927cddf68bb.jpg', '', 0003, 0.16);
INSERT INTO `question` VALUES (1733399842610323458, 1554, '54e4d', 0002, '这个仪表是何含义？ ', 'A、电流表 B、压力表 C、水温表 D、燃油表', '电流表 ', '压力表 ', '水温表 ', '燃油表', 'C', '<b>水温表：</b>指示发动机冷却液的温度，单位为摄氏度（℃）。“C”表示温度低，“H”表示温度高，水温表指针所指的位置显示当前冷却液的温度。', 'https://timg.mnks.cn/20230927eee95549.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399842648072193, 1555, '2e738', 0002, '这个仪表是何含义？ ', 'A、电流表 B、压力表 C、水温表 D、燃油表', '电流表 ', '压力表 ', '水温表 ', '燃油表', 'D', '<b>燃油表</b>：指示油箱内的燃油量。“E”表示空，“F”表示满。当指针指在红色警告线以内时，提示油箱内燃油不足，需及时加油。', 'https://timg.mnks.cn/20230927ccba0f97.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399842681626625, 1556, 'af179', 0001, '仪表显示当前车速是20公里/小时。', NULL, '对', '错', '空', '空', '错', '如图所示是<b>发动机转速表</b>，指示发动机的转速，转速表指针所指的数字显示当前发动机转速，发动机运转时，转速表指针不能超过红色警示区。', 'https://timg.mnks.cn/20230927c205ff0a.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399842719375361, 1557, 'ca0da', 0001, '仪表显示当前发动机转速是6000转/分钟。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"https://tp.mnks.cn/tp_362.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>速度和里程表</b>：速度表指示<b>汽车行驶速度</b>，单位为公里/小时（<b>km/h</b>），速度表指针所指的数字显示当前车辆的行驶速度。里程表累计行驶总里程数以公里（KM）为单位；日里程表用于记录一天或某段区间的里程数；按回零按钮至“0”位后重新开始计数。<br><img src=\"https://tp.mnks.cn/tp_361.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>发动机转速表：指示发动机的转速，单位为1000转/分；转速表指针所指的数字显示当前发动机转速。发动机运转时，转速表指针不能超过红色警示区。</p>', 'https://timg.mnks.cn/20230926e8d1a245.jpg', '发动机转速', 0003, 0.12);
INSERT INTO `question` VALUES (1733399842765512706, 1558, 'ee1da', 0001, '仪表显示当前冷却液的温度是90℃。', NULL, '对', '错', '空', '空', '对', '<b>水温表</b>：指示发动机冷却液的温度，单位为摄氏度（℃）。<b>水温表指针所指的位置显示当前冷却液的温度</b>。 <br>如图所示，指针指的位置是90，表示当前冷却液的温度是90℃。', 'https://timg.mnks.cn/20230926ef1f0783.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399842803261442, 1559, 'd26b1', 0001, '仪表显示油箱内存油量已在警告线以内。', NULL, '对', '错', '空', '空', '对', '<b>燃油表</b>：指示油箱内的燃油量。当<b>指针指在红色警告线以内</b>时，提示<b>油箱内燃油不足</b>，需及时加油。', 'https://timg.mnks.cn/20230926ca91ac2d.jpg', '', 0002, 0.04);
INSERT INTO `question` VALUES (1733399842845204481, 1560, '9df0e', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、前雾灯打开 B、后雾灯打开 C、前照灯近光打开 D、前照灯远光打开', '前雾灯打开 ', '后雾灯打开 ', '前照灯近光打开 ', '前照灯远光打开', 'A', '如图所示，<b>前雾灯</b>开启。弯曲的“丰”表示光线的方向，绿“丰”在左，表示前雾灯；黄“丰”在右，表示后雾灯。', 'https://timg.mnks.cn/20230926b4b52ac8.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399842887147522, 1561, 'b96b0', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、前雾灯打开 B、后雾灯打开 C、前照灯近光打开 D、前照灯远光打开', '前雾灯打开 ', '后雾灯打开 ', '前照灯近光打开 ', '前照灯远光打开', 'B', '如图所示，<b>后雾灯</b>开启。弯曲的“丰”表示光线的方向，绿“丰”在左，表示前雾灯；黄“丰”在右，表示后雾灯。', 'https://timg.mnks.cn/20230926eb933dec.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399842933284865, 1562, '68834', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、危险报警闪光灯开启 B、前照灯开启 C、前后位置灯开启 D、前后雾灯开启', '危险报警闪光灯开启 ', '前照灯开启 ', '前后位置灯开启 ', '前后雾灯开启', 'C', '如图所示，<b>前后位置灯</b>（又称示廓灯）开启。', 'https://timg.mnks.cn/20230926c6fcd4dc.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399843012976641, 1563, '30254', 0001, '打开前雾灯开关，（如图所示）亮起。', NULL, '对', '错', '空', '空', '错', '如图所示，表示<b>后雾灯</b>开启。', 'https://timg.mnks.cn/20230926c9f22c19.jpg', '前雾灯', 0003, 0.13);
INSERT INTO `question` VALUES (1733399843063308289, 1564, 'bf7dd', 0001, '打开后雾灯开关，（如图所示）亮起。', NULL, '对', '错', '空', '空', '错', '如图所示，表示<b>前雾灯</b>开启。', 'https://timg.mnks.cn/20230926a9b04e1f.jpg', '后雾灯', 0003, 0.15);
INSERT INTO `question` VALUES (1733399843117834242, 1565, '86dea', 0001, '打开位置灯开关，（如图所示）亮起。', NULL, '对', '错', '空', '空', '对', '图中标志为<b>前后位置灯</b>（又称示廓灯）开启。该指示灯是用来显示车辆示宽灯（也叫示廓灯、位置灯）的工作状态，平时为熄灭状态，当示宽灯打开时，该指示灯随即点亮。当示宽灯关闭或者关闭示宽灯打开大灯时，该指示灯自动熄灭。', 'https://timg.mnks.cn/20230926ed5448db.jpg', '', 0003, 0.13);
INSERT INTO `question` VALUES (1733399843151388674, 1566, '3a9e8', 0002, '发动机起动后仪表板上（如图所示）亮表示什么？ ', 'A、发动机机油压力过高 B、发动机主油道堵塞 C、发动机机油压力过低 D、发动机曲轴箱漏气', '发动机机油压力过高 ', '发动机主油道堵塞 ', '发动机机油压力过低 ', '发动机曲轴箱漏气', 'C', '如图所示灯亮，表示<b>发动机机油压力过低或机油量不足。</b> <br>应对措施：停车检查机油量，若过少则进行补充添加，若不少则尽快联系救援。', 'https://timg.mnks.cn/20230926b1c93abe.jpg', '', 0004, 0.18);
INSERT INTO `question` VALUES (1733399843189137410, 1567, '94598', 0001, '机动车仪表板上（如图所示）亮表示发动机可能机油量不足。', NULL, '对', '错', '空', '空', '对', '如图所示灯亮，<b>表示发动机机油压力过低或机油量不足</b>。', 'https://timg.mnks.cn/20230926b20f58ff.jpg', '机油量不足', 0004, 0.22);
INSERT INTO `question` VALUES (1733399843226886146, 1568, '94df9', 0001, '机动车仪表板上（如图所示）亮表示发动机可能机油压力过高。', NULL, '对', '错', '空', '空', '错', '如图所示灯亮，<b>表示发动机机油压力过低或机油量不足。</b>', 'https://timg.mnks.cn/20230926e742ebd7.jpg', '机油压力过高', 0004, 0.19);
INSERT INTO `question` VALUES (1733399843268829186, 1569, 'f2e3f', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、制动踏板没回位 B、驻车制动解除 C、行车制动器失效 D、制动系统出现异常', '制动踏板没回位 ', '驻车制动解除 ', '行车制动器失效 ', '制动系统出现异常', 'D', '括号圆圈里面是“！” 表示制动系统出现异常或故障。 <br>应对措施：1，检查手刹是否完全松开；2、检查刹车油液是否足够；3、行驶时是否有拖拽感。若有故障，则应尽快行驶至修理厂进行检修。', 'https://timg.mnks.cn/20230926c7106f83.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399843306577921, 1570, 'aa06a', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、行车制动系统出现故障 B、驻车制动器处于制动状态 C、防抱死制动系统出现故障 D、驻车制动器处于解除状态', '行车制动系统出现故障 ', '驻车制动器处于制动状态 ', '防抱死制动系统出现故障 ', '驻车制动器处于解除状态', 'B', '如图所示，表示<b>驻车制动器</b>处于制动状态。', 'https://timg.mnks.cn/20230926af47d578.jpg', '', 0005, 0.40);
INSERT INTO `question` VALUES (1733399843356909569, 1571, '6e363', 0001, '机动车仪表板上（如图所示）亮，表示驻车制动器操纵杆可能没松到底。', NULL, '对', '错', '空', '空', '错', '机动车仪表板上如图所示标志灯亮，表示<b>制动系统</b>出现异常或故障。因此，题干表述错误。（<b>！</b>)——制动系统；（P）——驻车制动。', 'https://timg.mnks.cn/20230926efcbf74d.jpg', '驻车制动器', 0004, 0.21);
INSERT INTO `question` VALUES (1733399843398852610, 1572, 'c43b6', 0001, '机动车仪表板上（如图所示）亮，表示行车制动系统可能出现故障。', NULL, '对', '错', '空', '空', '对', '中间是个“<b>!</b>”的，表示制动系统出现<b>异常或故障</b>； <br>中间是个“P”的，表示驻车制动器处于制动状态。', 'https://timg.mnks.cn/20230926e3fbf2ac.jpg', '行车制动|故障', 0004, 0.17);
INSERT INTO `question` VALUES (1733399843432407041, 1573, 'f93d4', 0001, '机动车仪表板上（如图所示）亮时，不影响正常行驶。', NULL, '对', '错', '空', '空', '错', '图中指示灯亮表示：制动系统出现异常或故障。行车途中该灯亮起，表示制动系统出了问题或故障，<b>会影响</b>正常行驶。', 'https://timg.mnks.cn/20230926ac1d24fb.jpg', '不影响', 0002, 0.08);
INSERT INTO `question` VALUES (1733399843465961473, 1574, 'a29ad', 0001, '机动车仪表板上（如图所示）亮时，防抱死制动系统处于打开状态。', NULL, '对', '错', '空', '空', '错', '仪表板上如图所示指示灯亮，表示<b>防抱死制动系统出现故障</b>。', 'https://timg.mnks.cn/20230926bd010c71.jpg', '防抱死制动|打开', 0005, 0.32);
INSERT INTO `question` VALUES (1733399843503710210, 1575, 'f7109', 0001, '机动车仪表板上（如图所示）亮时，表示驻车制动器处于制动状态。', NULL, '对', '错', '空', '空', '对', '仪表板上如图所示灯亮，表示驻车制动器处于<b>制动状态</b>，即手刹没松开，但是车辆没有发生故障。', 'https://timg.mnks.cn/20230926adcfc10a.jpg', '驻车制动器|制动', 0004, 0.25);
INSERT INTO `question` VALUES (1733399843616956417, 1576, '85197', 0002, '发动机起动后仪表板上（如图所示）亮表示什么？ ', 'A、油箱内燃油已到最低液面 B、发动机供油系统出现异常 C、发动机点火系统出现故障 D、燃油泵出现异常或者故障', '油箱内燃油已到最低液面 ', '发动机供油系统出现异常 ', '发动机点火系统出现故障 ', '燃油泵出现异常或者故障', 'A', '仪表板上如图所示灯亮，表示<b>油箱内燃油已到最低液面</b>。', 'https://timg.mnks.cn/20230925a7f358c3.jpg', '', 0003, 0.12);
INSERT INTO `question` VALUES (1733399843658899458, 1577, 'd6f3f', 0001, '机动车仪表板上（如图所示）亮时，提醒发动机需要补充机油。', NULL, '对', '错', '空', '空', '错', '如图所示灯亮，表示<b>燃油不足，油箱内燃油已到最低液面</b>，应当补充燃油，不是补充发动机机油。', 'https://timg.mnks.cn/20230926fb265379.jpg', '补充机油', 0004, 0.23);
INSERT INTO `question` VALUES (1733399843696648194, 1578, '1e624', 0002, '行车中仪表板上（如图所示）亮表示什么？ ', 'A、发动机温度过低 B、发动机温度过高 C、发动机冷却系故障 D、发动机润滑系故障', '发动机温度过低 ', '发动机温度过高 ', '发动机冷却系故障 ', '发动机润滑系故障', 'B', '<b>发动机温度过高时，仪表盘上如图所示标志会亮起</b>，这个类似于温度计的图标，叫做水温指示灯。当这个图标亮起，说明可能是冷却液不足了，发动机温度自然就高了。', 'https://timg.mnks.cn/20230926ce6b7643.jpg', '', 0005, 0.31);
INSERT INTO `question` VALUES (1733399843734396930, 1579, '355c4', 0001, '机动车仪表板上（如图所示）亮时，提醒发动机冷却液可能不足。', NULL, '对', '错', '空', '空', '对', '如图所示的报警灯亮时，表示发动机温度过高，提醒可能是发动机的<b>冷却液不足</b>引起的，冷却液不足是引起发动机温度过高的原因之一。图标像体温计：发动机温度高，冷却液不足。', 'https://timg.mnks.cn/20230926fe77f290.jpg', '冷却液', 0004, 0.24);
INSERT INTO `question` VALUES (1733399843788922881, 1580, 'be5fb', 0001, '机动车仪表板上（如图所示）亮时提醒发动机需要加注机油。', NULL, '对', '错', '空', '空', '错', '当发动机的温度过高时，车辆仪表盘上的<b>水温报警灯（如图所示）</b>就会<b>亮起</b>，提醒可能是发动机的<b>冷却液不足引起的</b>，不是提醒要加注机油。', 'https://timg.mnks.cn/20230926f3111b78.jpg', '加注机油', 0003, 0.14);
INSERT INTO `question` VALUES (1733399843830865921, 1581, '029f4', 0002, '机动车仪表板上（如图所示）亮时表示什么？ ', 'A、已开启前照灯近光 B、已开启前雾灯 C、已开启前照灯远光 D、已开启后雾灯', '已开启前照灯近光 ', '已开启前雾灯 ', '已开启前照灯远光 ', '已开启后雾灯', 'C', '仪表板上如图所示灯亮，表示<b>远光灯</b>开启。', 'https://timg.mnks.cn/20230926e935f25e.jpg', '', 0004, 0.17);
INSERT INTO `question` VALUES (1733399843881197569, 1582, 'd5d6a', 0002, '机动车仪表板上（如图所示）亮时表示什么？ ', 'A、已开启前照灯远光 B、已开启前雾灯 C、已开启后雾灯 D、已开启前照灯近光', '已开启前照灯远光 ', '已开启前雾灯 ', '已开启后雾灯 ', '已开启前照灯近光', 'D', '近光光束斜向下；远光光束平直照的远。仪表板上如图所示灯亮，表示<b>近光灯</b>开启。', 'https://timg.mnks.cn/20230926e80f963d.jpg', '', 0004, 0.20);
INSERT INTO `question` VALUES (1733399843927334913, 1583, '32fb5', 0001, '开启前照灯远光时仪表板上（如图所示）亮起。', NULL, '对', '错', '空', '空', '错', '【直线远光，<b>斜线近光</b>】前照灯俗称大灯，包含远光灯和近光灯。如图所示，光线斜向地面照射，表示<b>近光灯</b>开启。所以，题干表述错误。', 'https://timg.mnks.cn/20230926f22357ea.jpg', '远光', 0004, 0.19);
INSERT INTO `question` VALUES (1733399843973472258, 1584, '46759', 0001, '开启前照灯近光时仪表板上（如图所示）亮起。', NULL, '对', '错', '空', '空', '错', '开启前照<b>远光灯</b>时，仪表板上如图所示灯亮起。', 'https://timg.mnks.cn/20230926b4018576.jpg', '近光', 0004, 0.18);
INSERT INTO `question` VALUES (1733399844027998210, 1585, '8bf77', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、没有系好安全带 B、安全带出现故障 C、已经系好安全带 D、安全带系得过松', '没有系好安全带 ', '安全带出现故障 ', '已经系好安全带 ', '安全带系得过松', 'A', '仪表板上如图所示指示灯亮，表示<b>没系安全带或安全带插头未插好</b>。', 'https://timg.mnks.cn/20230926d3c380f0.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399844074135553, 1586, '3b5ad', 0001, '机动车仪表板上（如图所示）亮时，提醒驾驶人座椅没调整好。', NULL, '对', '错', '空', '空', '错', '该指示灯亮起时，说明有驾乘人员没系安全带或安全带插头未插好，有些车型会有相应的提示音。当驾乘人员正确系好安全带后，该指示灯自动熄灭。', 'https://timg.mnks.cn/20230926aa290855.jpg', '座椅没调整好', 0003, 0.14);
INSERT INTO `question` VALUES (1733399844128661506, 1587, 'd7bdc', 0001, '机动车仪表板上（如图所示）亮时，提醒驾驶人安全带插头未插入锁扣。', NULL, '对', '错', '空', '空', '对', '仪表板上如图所示指示灯亮，表示<b>没系安全带</b>或<b>安全带插头未插好</b>。', 'https://timg.mnks.cn/20230926c1fb77b7.jpg', '安全带|未插入锁扣', 0003, 0.09);
INSERT INTO `question` VALUES (1733399844178993153, 1588, 'cc6fa', 0002, '危险报警闪光灯可用于下列什么场合？ ', 'A、在道路上跟车行驶时 B、遇到道路拥堵时 C、机动车发生故障停车时 D、引领后车行驶时', '在道路上跟车行驶时 ', '遇到道路拥堵时 ', '机动车发生故障停车时 ', '引领后车行驶时', 'C', '危险报警闪光灯也称为<b>故障停车信号灯</b>，与转向信号灯共同用一套灯具，<b>当汽车发生故障停车或遇到紧急情况需要处理时使用</b>，发出交替的闪光信号，用于警告后方车辆和行人。', '', '危险报警闪光灯', 0001, 0.03);
INSERT INTO `question` VALUES (1733399844220936194, 1589, '900d2', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、右转向指示灯闪烁 B、危险报警闪光灯闪烁 C、左转向指示灯闪烁 D、车前后位置灯闪烁', '右转向指示灯闪烁 ', '危险报警闪光灯闪烁 ', '左转向指示灯闪烁 ', '车前后位置灯闪烁', 'B', '<p><img src=\"https://tp.mnks.cn/tp_394.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>危险报警闪光灯</b>：与转向信号灯共用一套灯具，当汽车发生故障停车或遇到紧急情况需要处理时使用，发出交替的闪光信号，用于警告后方车辆和行人。<br><img src=\"https://tp.mnks.cn/tp_370.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>前后位置灯：又称示廓灯、小灯，用于夜间和特殊天气行驶时，标示汽车的宽度，提醒其他车辆注意。</p>', 'https://timg.mnks.cn/20230926c50dfdb4.jpg', '', 0001, 0.02);
INSERT INTO `question` VALUES (1733399844283850753, 1590, '16a25', 0001, '机动车发生故障时，（如图所示）闪烁。', NULL, '对', '错', '空', '空', '错', '机动车发生故障时，危险报警闪光灯<b>不会闪烁</b>，<b>需要手动按下开关键</b>才会闪烁。<br>【拓展】<br>危险报警闪光灯也称为<b>故障停车信号灯</b>，开关都是按钮式的，<b>按下起作用</b>，再按一下解除。按下危险报警闪光灯（俗称双闪）开关，前后的两侧转向灯会同时闪烁；再按一次开关，危险报警闪光灯关闭。', 'https://timg.mnks.cn/20230926fa1fc9ab.jpg', '故障', 0005, 0.35);
INSERT INTO `question` VALUES (1733399844317405185, 1591, '3f564', 0001, '开启危险报警闪光灯时，（如图所示）闪烁。', NULL, '对', '错', '空', '空', '对', '危险报警闪光灯也称为故障停车信号灯，开关都是按钮式的，<b>按下起作用</b>，再按一下解除。<b>按下危险报警闪光灯（俗称双闪）开关，前后的两侧转向灯会同时闪烁</b>；再按一次开关，危险报警闪光灯关闭。', 'https://timg.mnks.cn/20230926e458be85.jpg', '开启|闪烁', 0003, 0.14);
INSERT INTO `question` VALUES (1733399844355153921, 1592, 'c2128', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、左转向指示灯闪烁 B、右转向指示灯闪烁 C、车前后位置灯亮起 D、车前后示宽灯亮起', '左转向指示灯闪烁 ', '右转向指示灯闪烁 ', '车前后位置灯亮起 ', '车前后示宽灯亮起', 'B', '如图所示，机动车仪表板上的指示灯亮，表示<b>右转向指示灯</b>开启，<b>右转向指示灯</b>闪烁。', 'https://timg.mnks.cn/20230926c11ef10a.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399844980105217, 1593, '02f95', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、车前后位置灯亮起 B、右转向指示灯闪烁 C、左转向指示灯闪烁 D、车前后示宽灯亮起', '车前后位置灯亮起 ', '右转向指示灯闪烁 ', '左转向指示灯闪烁 ', '车前后示宽灯亮起', 'C', '如图所示，机动车仪表板上指示灯亮，表示<b>左转向指示灯</b>开启，<b>左转向指示灯</b>闪烁。', 'https://timg.mnks.cn/20230926cce25b37.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399845609250818, 1594, 'fe151', 0001, '打开左转向灯开关，（如图所示）亮起。', NULL, '对', '错', '空', '空', '错', '如图所示，亮灯的箭头向<b>右</b>，表示<b>右</b>转向灯开启。因此，题干表述错误。', 'https://timg.mnks.cn/20230926dc6bf8d2.jpg', '左转向灯', 0001, 0.03);
INSERT INTO `question` VALUES (1733399845659582466, 1595, '99874', 0001, '打开右转向灯开关，（如图所示）亮起。', NULL, '对', '错', '空', '空', '错', '如图所示，亮灯的<b>箭头向左</b>，表示<b>左转向灯</b>开启。因此，题干表述错误。', 'https://timg.mnks.cn/20230926a0e4f1ad.jpg', '右转向灯', 0002, 0.04);
INSERT INTO `question` VALUES (1733399845701525506, 1596, 'd4d11', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、充电电流过大 B、蓄电池损坏 C、电流表故障 D、充电电路故障', '充电电流过大 ', '蓄电池损坏 ', '电流表故障 ', '充电电路故障', 'D', '如图所示指示灯亮，表示<b>充电电路故障或发电机不向蓄电池充电</b>。', 'https://timg.mnks.cn/20230926d31715ef.jpg', '', 0004, 0.22);
INSERT INTO `question` VALUES (1733399845743468545, 1597, '3f2eb', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、两侧车门开启 B、行李舱开启 C、发动机舱开启 D、燃油箱盖开启', '两侧车门开启 ', '行李舱开启 ', '发动机舱开启 ', '燃油箱盖开启', 'A', '该指示灯用来显示车辆各车门状况，任意车门未关上，或者未关好，该指示灯都有点亮相应的车门指示灯，提示车主车门未关好，当车门关闭或者关好时，相应车门指示灯熄灭。', 'https://timg.mnks.cn/20230926c7d44425.jpg', '', 0001, 0.01);
INSERT INTO `question` VALUES (1733399845802188802, 1598, '9ae7e', 0002, '机动车仪表板上（如图所示）这个符号表示什么？ ', 'A、一侧车门开启 B、行李舱开启 C、发动机舱开启 D、燃油箱盖开启', '一侧车门开启 ', '行李舱开启 ', '发动机舱开启 ', '燃油箱盖开启', 'B', '车头发动机舱，车尾行李舱（俗称后备箱），机动车仪表板上出现如图所示符号，提示<b>行李舱开启</b>。', 'https://timg.mnks.cn/20230926f7e5b4da.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399845869297665, 1599, 'a9480', 0002, '机动车仪表板上（如图所示）这个符号表示什么？ ', 'A、一侧车门开启 B、行李舱开启 C、发动机舱开启 D、燃油箱盖开启', '一侧车门开启 ', '行李舱开启 ', '发动机舱开启 ', '燃油箱盖开启', 'C', '车头发动机舱，车尾行李舱，机动车仪表板上出现如图所示的符号，提示<b>发动机舱开启</b>。', 'https://timg.mnks.cn/20230926e2194164.jpg', '', 0002, 0.07);
INSERT INTO `question` VALUES (1733399845911240705, 1600, '40d08', 0002, '机动车仪表板上（如图所示）一直亮表示什么？ ', 'A、防抱死制动系统故障 B、安全气囊处于故障状态 C、安全气囊处于工作状态 D、安全带没有系好', '防抱死制动系统故障 ', '安全气囊处于故障状态 ', '安全气囊处于工作状态 ', '安全带没有系好', 'B', '圆球表示安全气囊，黄色表示处于故障状态。机动车仪表板上出现如图所示指示灯亮，表示<b>安全气囊处于故障</b>状态。', 'https://timg.mnks.cn/20230926c95c049b.jpg', '一直亮', 0004, 0.20);
INSERT INTO `question` VALUES (1733399845957378049, 1601, '2fa4c', 0002, '机动车仪表板上（如图所示）这个符号表示什么？ ', 'A、近光灯开关 B、远光灯开关 C、车灯总开关 D、后雾灯开关', '近光灯开关 ', '远光灯开关 ', '车灯总开关 ', '后雾灯开关', 'C', '图中符号表示<b>车灯总开关</b>。', 'https://timg.mnks.cn/20230926ab92d246.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399846628466689, 1602, '859b1', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、空气内循环 B、空气外循环 C、迎面吹风 D、风窗玻璃除霜', '空气内循环 ', '空气外循环 ', '迎面吹风 ', '风窗玻璃除霜', 'B', '如图所示，箭头由车外进入车内，表示<b>空气外循环</b>。 <br>\n<p><img src=\"https://tp.mnks.cn/tp_408.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>空气内循环</p>\n<p><img src=\"https://tp.mnks.cn/tp_407.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br><b>空气外循环</b></p>', 'https://timg.mnks.cn/20230926b60b1686.jpg', '', 0004, 0.25);
INSERT INTO `question` VALUES (1733399846703964161, 1603, '74b70', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、迎面出风 B、空气外循环 C、空气内循环 D、风窗玻璃除霜', '迎面出风 ', '空气外循环 ', '空气内循环 ', '风窗玻璃除霜', 'C', '箭头从车外到车内是空气外循环，箭头只在车内是空气内循环。', 'https://timg.mnks.cn/20230926b7cb71ce.jpg', '', 0003, 0.08);
INSERT INTO `question` VALUES (1733399846766878721, 1604, 'daf04', 0002, '机动车仪表板上（如图所示）这个符号表示什么？ ', 'A、冷风暖气风扇 B、空调制冷 C、空气循环 D、雪地起步模式', '冷风暖气风扇 ', '空调制冷 ', '空气循环 ', '雪地起步模式', 'A', '图中符号表示启用<b>冷风暖气风扇</b>。', 'https://timg.mnks.cn/20230926b5b55bb6.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399846817210370, 1605, 'cf855', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、空气内循环 B、地板及迎面出风 C、空气外循环 D、侧面及地板出风', '空气内循环 ', '地板及迎面出风 ', '空气外循环 ', '侧面及地板出风', 'B', '如图所示：箭头，一个指向驾驶人，一个指向地面，因此该标志表示<b>地板及迎面出风</b>。', 'https://timg.mnks.cn/20230926e7d72523.jpg', '', 0003, 0.11);
INSERT INTO `question` VALUES (1733399846863347713, 1606, 'aa924', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、空气内循环 B、侧面出风 C、空气外循环 D、迎面出风', '空气内循环 ', '侧面出风 ', '空气外循环 ', '迎面出风', 'D', '如图所示：箭头指向驾驶人，因此该标志表示<b>迎面出风</b>。', 'https://timg.mnks.cn/20230926f30f64bf.jpg', '', 0004, 0.17);
INSERT INTO `question` VALUES (1733399846917873665, 1607, 'f0083', 0002, '（如图所示）这个符号的开关控制什么装置？ ', 'A、前风窗玻璃刮水器 B、后风窗玻璃除霜 C、后风窗玻璃刮水器 D、前风窗玻璃除霜', '前风窗玻璃刮水器 ', '后风窗玻璃除霜 ', '后风窗玻璃刮水器 ', '前风窗玻璃除霜', 'A', '图中符号的开关控制<b>前风窗玻璃刮水器</b>装置。扇形图标是前风窗，四四方方的图标是后风窗。 <br>【拓展延伸】 <br><img src=\"https://tp.mnks.cn/tp_412.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>前风窗玻璃刮水器 <br><img src=\"https://tp.mnks.cn/tp_413.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>前风窗玻璃刮水器及洗涤器 <br><img src=\"https://tp.mnks.cn/tp_414.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>后风窗玻璃刮水器及洗涤器', 'https://timg.mnks.cn/20230926aa72ead4.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399846959816706, 1608, 'd9005', 0002, '（如图所示）这个符号的开关控制什么装置？ ', 'A、后风窗玻璃除霜或除雾 B、前风窗玻璃刮水器及洗涤器 C、后风窗玻璃刮水器及洗涤器 D、前风窗玻璃除霜或除雾', '后风窗玻璃除霜或除雾 ', '前风窗玻璃刮水器及洗涤器 ', '后风窗玻璃刮水器及洗涤器 ', '前风窗玻璃除霜或除雾', 'B', '图中符号的开关控制<b>前风窗玻璃刮水器及洗涤器</b>装置。扇形图标是前风窗，四四方方的图标是后风窗。 <br>\n<p><img src=\"https://tp.mnks.cn/tp_413.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>前风窗玻璃刮水器及洗涤器 <br><img src=\"https://tp.mnks.cn/tp_414.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>后风窗玻璃刮水器及洗涤器 <br><img src=\"https://tp.mnks.cn/tp_412.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>前风窗玻璃刮水器</p>', 'https://timg.mnks.cn/20230926b06221bf.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399847014342658, 1609, 'fd398', 0002, '（如图所示）这个符号的开关控制什么装置？ ', 'A、后风窗玻璃除霜或除雾 B、前风窗玻璃刮水器及洗涤器 C、后风窗玻璃刮水器及洗涤器 D、前风窗玻璃除霜或除雾', '后风窗玻璃除霜或除雾 ', '前风窗玻璃刮水器及洗涤器 ', '后风窗玻璃刮水器及洗涤器 ', '前风窗玻璃除霜或除雾', 'C', '图中符合的开关控制<b>后风窗玻璃刮水器及洗涤器</b>装置。<b>扇形</b>图标<b>是前风窗</b>，<b>四四方方</b>的图标<b>是后风窗</b>。<br>\n<p><img src=\"https://tp.mnks.cn/tp_413.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>前风窗玻璃刮水器及洗涤器<br><img src=\"https://tp.mnks.cn/tp_414.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>后风窗玻璃刮水器及洗涤器<br></p>', 'https://timg.mnks.cn/20230926db91ab7f.jpg', '', 0005, 0.31);
INSERT INTO `question` VALUES (1733399847052091393, 1610, '71a6c', 0002, '（如图所示）这个符号的开关控制什么装置？ ', 'A、儿童安全锁 B、两侧车窗玻璃 C、电动车门 D、车门锁住开锁', '儿童安全锁 ', '两侧车窗玻璃 ', '电动车门 ', '车门锁住开锁', 'D', '如图所示，车门锁住开锁开关，控制车门锁住开锁装置。', 'https://timg.mnks.cn/20230926b37e6a13.jpg', '', 0004, 0.18);
INSERT INTO `question` VALUES (1733399847094034433, 1611, 'a3dee', 0002, '（如图所示）这个符号的开关控制什么装置？ ', 'A、儿童安全锁 B、两侧车窗玻璃 C、电动车门 D、车门锁住开锁', '儿童安全锁 ', '两侧车窗玻璃 ', '电动车门 ', '车门锁住开锁', 'A', '如图所示，<b>儿童安全锁开关</b>，是为保证乘车儿童安全的一种主动安全装置，防止车辆行驶过程中儿童从车内误开门而产生的危险。在儿童安全锁锁止情况下，即使通过中控锁电动解锁，儿童安全装置仍处在锁止状态，车门只能从外部打开，车内门拉手暂时失去开门作用。', 'https://timg.mnks.cn/20230926fb68d425.jpg', '', 0001, 0.03);
INSERT INTO `question` VALUES (1733399847131783169, 1612, '73b03', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、制动液不足 B、洗涤液不足 C、冷却系故障 D、冷却液不足', '制动液不足 ', '洗涤液不足 ', '冷却系故障 ', '冷却液不足', 'D', '机动车仪表板上如图所示指示灯亮，表示<b>冷却液不足</b>，用于提醒机动车驾驶人车辆<b>冷却液不足</b>，需及时补充。', 'https://timg.mnks.cn/20230926b0ab870c.jpg', '', 0005, 0.32);
INSERT INTO `question` VALUES (1733399847177920513, 1613, 'cb0b6', 0001, '机动车仪表板上（如图所示）亮，提示发电机向蓄电池充电。', NULL, '对', '错', '空', '空', '错', '机动车仪表板上如图所示指示灯亮，表示<b>充电电路故障</b>或发电机<b>不向蓄电池充电</b>。因此，题干表述错误。', 'https://timg.mnks.cn/20230926f07fa762.jpg', '充电', 0004, 0.24);
INSERT INTO `question` VALUES (1733399847232446466, 1614, 'b69bc', 0001, '机动车仪表板上（如图所示)亮，提示两侧车门未关闭。', NULL, '对', '错', '空', '空', '对', '机动车仪表板上如图所示指示灯亮，表示车辆两<b>侧车门均未关闭</b>。', 'https://timg.mnks.cn/20230926e62f1147.jpg', '两侧', 0001, 0.02);
INSERT INTO `question` VALUES (1733399847270195201, 1615, '8d219', 0001, '机动车仪表板上（如图所示）亮，提示左侧车门未关闭。', NULL, '对', '错', '空', '空', '错', '从图而知，图标中右侧车门显示开启状态，因此该标志是提示<b>右侧车门未关闭</b>，并非提示左侧车门未关闭。题目描述错误，因此本题答案为错误。', 'https://timg.mnks.cn/20230926d0392b5e.jpg', '左侧', 0002, 0.04);
INSERT INTO `question` VALUES (1733399847333109762, 1616, '17b2f', 0001, '机动车仪表板上（如图所示）亮，提示右侧车门未关闭。', NULL, '对', '错', '空', '空', '错', '机动车仪表板上左侧车门打开的指示灯亮，提示<b>左侧车门未关闭</b>。', 'https://timg.mnks.cn/20230926f8717873.jpg', '右侧', 0002, 0.05);
INSERT INTO `question` VALUES (1733399847379247106, 1617, 'e89ce', 0001, '机动车仪表板上（如图所示）亮，提示行李舱开启。', NULL, '对', '错', '空', '空', '错', '车头发动机舱，车尾行李舱。仪表板上如图所示指示灯亮，提示<b>发动机舱</b>开启。', 'https://timg.mnks.cn/20230926abc45c10.jpg', '行李舱', 0002, 0.04);
INSERT INTO `question` VALUES (1733399847416995841, 1618, 'd10be', 0001, '机动车仪表板上（如图所示）亮，提示发动机舱开启。', NULL, '对', '错', '空', '空', '错', '车头发动机舱，车尾行李舱。图中标志提示<b>行李舱</b>开启。<br>【拓展延伸】\n<p><img src=\"https://tp.mnks.cn/tp_423.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br><b>行李舱</b>开启</p>\n<p><img src=\"https://tp.mnks.cn/tp_404.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>发动机舱开启</p>', 'https://timg.mnks.cn/20230926f5169928.jpg', '发动机舱', 0002, 0.06);
INSERT INTO `question` VALUES (1733399847467327490, 1619, '39a0c', 0001, '机动车仪表板上（如图所示）一直亮，表示安全气囊处于工作状态。', NULL, '对', '错', '空', '空', '错', '机动车仪表板上如图所示指示灯亮，表示安全气囊处于<b>故障状态，</b>不是工作状态。', 'https://timg.mnks.cn/20230926fc2a1718.jpg', '工作状态', 0004, 0.19);
INSERT INTO `question` VALUES (1733399847517659137, 1620, 'dd9b8', 0001, '机动车仪表板上（如图所示）亮表示启用地板及前风窗玻璃吹风。', NULL, '对', '错', '空', '空', '对', '扇型代表前风窗玻璃，曲线上升表示启用前风窗玻璃吹风，向下的大箭头表示启用地板吹风。因此，图中标志亮表示<b>启用地板及前风窗玻璃吹风</b>。', 'https://timg.mnks.cn/20230926c056729e.jpg', '地板及前风窗', 0003, 0.11);
INSERT INTO `question` VALUES (1733399847588962306, 1621, 'c20d4', 0001, '机动车仪表板上（如图所示）一直亮，表示发动机控制系统故障。', NULL, '对', '错', '空', '空', '对', '机动车仪表板上如图所示指示灯一直亮，表示<b>发动机控制系统发生故障</b>，发动机控制系统故障，此标志亮起。发动机故障灯点亮的原因是发动机的电子控制系统、传感器或电路出现故障，需及时去4S店检修。', 'https://timg.mnks.cn/20230926e406b595.jpg', '故障', 0003, 0.12);
INSERT INTO `question` VALUES (1733399847630905346, 1622, '78a36', 0001, '这种握转向盘的动作是正确的。', NULL, '对', '错', '空', '空', '错', '转向盘的握法：双手握在转向盘两侧盘缘，<b>左手握住9点位置，右手握住3点位置</b>，食指到小指四个手指由内向外自然地握住，拇指自然按住转向盘缘，注意不要握得太紧。转动转向盘以左手为主，右手辅助。 <br>综上，如图所示握转向盘的动作是错误的。', 'https://timg.mnks.cn/20230926d687b11d.jpg', '正确', 0001, 0.03);
INSERT INTO `question` VALUES (1733399847681236994, 1623, '551a9', 0002, '这是什么踏板？ ', 'A、加速踏板 B、离合器踏板 C、制动踏板 D、驻车制动器', '加速踏板 ', '离合器踏板 ', '制动踏板 ', '驻车制动器', 'B', '如图所示装置，从左向右依次是：<b>离合器踏板</b>、制动踏板、加速踏板。', 'https://timg.mnks.cn/20230926feddffa2.jpg', '', 0003, 0.16);
INSERT INTO `question` VALUES (1733399847727374337, 1624, '05d9c', 0002, '这是什么踏板？ ', 'A、加速踏板 B、离合器踏板 C、制动踏板 D、驻车制动器', '加速踏板 ', '离合器踏板 ', '制动踏板 ', '驻车制动器', 'C', '如图所示装置，从左向右依次是：离合器踏板、<b>制动踏板</b>、加速踏板。', 'https://timg.mnks.cn/20230926c2b185af.jpg', '', 0004, 0.26);
INSERT INTO `question` VALUES (1733399847769317377, 1625, '46c54', 0002, '这是什么踏板？ ', 'A、加速踏板 B、离合器踏板 C、制动踏板 D、驻车制动器', '加速踏板 ', '离合器踏板 ', '制动踏板 ', '驻车制动器', 'A', '如图所示装置，从左向右依次是：离合器踏板、制动踏板、<b>加速踏板</b>。', 'https://timg.mnks.cn/20230926a7de2de9.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399847802871809, 1626, '4539c', 0002, '这是什么操纵装置？ ', 'A、节气门操纵杆 B、驻车制动器操纵杆 C、变速器操纵杆 D、离合器操纵杆', '节气门操纵杆 ', '驻车制动器操纵杆 ', '变速器操纵杆 ', '离合器操纵杆', 'C', '如图所示，<b>变速器操纵杆</b>。<b>变速器操纵杆</b>内不同齿轮的啮合，可改变汽车的动力、速度和进退方向，使汽车加速、减速或倒车。设有N挡、R挡和5个（或6个）前进挡。', 'https://timg.mnks.cn/20230926ead1b24d.jpg', '', 0004, 0.24);
INSERT INTO `question` VALUES (1733399847844814849, 1627, '109f3', 0002, '这是什么操纵装置？ ', 'A、驻车制动器操纵杆 B、节气门操纵杆 C、变速器操纵杆 D、离合器操纵杆', '驻车制动器操纵杆 ', '节气门操纵杆 ', '变速器操纵杆 ', '离合器操纵杆', 'A', '如图所示，<b>驻车制动器操纵杆</b>（手刹）拉紧<b>驻车制动器操纵杆</b>起制动作用，放下操纵杆，制动解除。', 'https://timg.mnks.cn/20230926cd44ebd9.jpg', '', 0004, 0.16);
INSERT INTO `question` VALUES (1733399847882563585, 1628, 'a6bb1', 0002, '这是什么操纵装置？ ', 'A、灯光开关 B、空调开关 C、点火开关 D、雨刷开关', '灯光开关 ', '空调开关 ', '点火开关 ', '雨刷开关', 'C', '图中为<b>点火开关操纵装置</b>。点火开关4个档位的功能是：LOCK：切断电源，锁定方向盘；ACC：接通附件电源（比如收音机等附件）ON：接通除起动机外的全车全部电源；START：接通起动机电源，起动机起动。', 'https://timg.mnks.cn/20230926cfd62ab6.jpg', '', 0002, 0.08);
INSERT INTO `question` VALUES (1733399847924506626, 1629, 'c5c01', 0001, '将点火开关转到ACC位置起动机工作。', NULL, '对', '错', '空', '空', '错', '汽车的点火开关，一共有4个挡位，分别为LOCK锁止，ACC断电，ON通电，START起动。<br>点火开关转到LOCK位置，发动机关闭，拔出钥匙，转向盘会锁止； <br><b>点火开关转到ACC位置，发动机关闭，其他车用电器可正常使用； </b><br>点火开关转到ON位置，发动机工作； <br>点火开关转到START位置，起动机起动。', 'https://timg.mnks.cn/20230926bb0d81dc.jpg', 'ACC', 0004, 0.24);
INSERT INTO `question` VALUES (1733399847974838273, 1630, '1d5ed', 0001, '点火开关在ON位置，车用电器不能使用。', NULL, '对', '错', '空', '空', '错', '汽车的点火开关，一共有4个挡位，分别为LOCK锁止，ACC断电，<b>ON通电</b>，START起动。<br>点火开关转到LOCK位置，发动机关闭，拔出钥匙，转向盘会锁止； <br>点火开关转到ACC位置，发动机关闭，其他车用电器可正常使用； <br><b>点火开关转到ON位置，发动机工作； </b><br>点火开关转到START位置，起动机起动。', 'https://timg.mnks.cn/20230926f041af11.jpg', 'ON|不能使用', 0004, 0.22);
INSERT INTO `question` VALUES (1733399848029364226, 1631, 'bb359', 0001, '点火开关在LOCK位置拔出钥匙转向盘会锁住。', NULL, '对', '错', '空', '空', '对', '汽车的点火开关，一共有4个挡位，分别为<b>LOCK锁止</b>，ACC断电，ON通电，START起动。<br><b>点火开关转到LOCK位置，发动机关闭，拔出钥匙，转向盘会锁止</b>；<br>点火开关转到ACC位置，发动机关闭，其他车用电器可正常使用；<br>点火开关转到ON位置，发动机工作；<br>点火开关转到START位置，起动机起动。', 'https://timg.mnks.cn/20230926da04d2a9.jpg', 'LOCK|锁住', 0003, 0.12);
INSERT INTO `question` VALUES (1733399848071307266, 1632, '42b9c', 0001, '点火开关在START位置起动机起动。', NULL, '对', '错', '空', '空', '对', '汽车的点火开关，一共有4个挡位，分别为LOCK锁止，ACC断电，ON通电，<b>START起动</b>。<br>点火开关转到LOCK位置，发动机关闭，拔出钥匙，转向盘会锁止； <br>点火开关转到ACC位置，发动机关闭，其他车用电器可正常使用； <br>点火开关转到ON位置，发动机工作； <br><b>点火开关转到START位置，起动机起动。</b>', 'https://timg.mnks.cn/20230926f2f5f2dd.jpg', 'START|起动机', 0003, 0.10);
INSERT INTO `question` VALUES (1733399848134221825, 1633, 'c565f', 0002, '这是什么操纵装置？ ', 'A、灯光、信号组合开关 B、倒车灯开关 C、刮水器开关 D、危险报警闪光灯开关', '灯光', '信号组合开关 B', '倒车灯开关 C', '刮水器开关 D', 'A', '<b>灯光-信号组合开关</b>，是控制照明灯光和信号灯光的装置，常见的有旋转——提拉式，大多数安装在转向盘左下方转向柱上，用左手操纵。', 'https://timg.mnks.cn/20230926d8b8016c.jpg', '', 0002, 0.06);
INSERT INTO `question` VALUES (1733399848180359169, 1634, 'a28db', 0002, '提拉这个开关控制机动车哪个部位？ ', 'A、倒车灯 B、左右转向灯 C、示廓灯 D、报警闪光灯', '倒车灯 ', '左右转向灯 ', '示廓灯 ', '报警闪光灯', 'B', '<b>左右转向灯开关</b>是控制转向信号灯的操纵装置，与前照灯共用一个操纵杆。向上抬杆，开启右转向灯；向下按杆，开启左转向灯。', 'https://timg.mnks.cn/20230926ded18738.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399848230690817, 1635, '1bb95', 0002, '旋转开关这一档控制机动车哪个部位？ ', 'A、左右转向灯 B、近光灯 C、前后雾灯 D、远光灯', '左右转向灯 ', '近光灯 ', '前后雾灯 ', '远光灯', 'C', '<b>雾灯开关</b>与前照灯和转向信号灯开关组合为一体，或者装在仪表板上，<b>分为前雾灯开关和后雾灯开关，用于控制前后雾灯</b>。常见的有旋转、按钮式。向前旋转（或按下）前雾灯开关，前雾指示灯、前雾灯亮；向后旋转（或按下）后雾灯开关，后雾指示灯、后雾灯亮。', 'https://timg.mnks.cn/20230926acb8316e.jpg', '', 0004, 0.21);
INSERT INTO `question` VALUES (1733399848272633858, 1636, '1b7d2', 0001, '将转向灯开关向上提，左转向灯亮。', NULL, '对', '错', '空', '空', '错', '<b>向上提：右转向灯亮</b>；向下拉：左转向灯亮。', 'https://timg.mnks.cn/20230926ff50780e.jpg', '左转向灯', 0004, 0.22);
INSERT INTO `question` VALUES (1733399848310382593, 1637, '145d9', 0001, '将转向灯开关向下拉，右转向灯亮。', NULL, '对', '错', '空', '空', '错', '转向灯开关向<b>上提</b>：<b>右转向灯亮</b>（顺时针方向，向右打方向盘的方向） ；转向灯向<b>下拉</b>：<b>左转向灯亮</b>（逆时针方向，向左打方向盘的方向）。因此，题干表述错误。', 'https://timg.mnks.cn/20230926bae793e7.jpg', '向下拉', 0004, 0.19);
INSERT INTO `question` VALUES (1733399848348131330, 1638, '01e32', 0001, '灯光开关旋转到这个位置时，全车灯光点亮。', NULL, '对', '错', '空', '空', '错', '<p><img src=\"//wx4.sinaimg.cn/large/\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>如图所示：<b>前后位置灯（又称示廓灯）开启</b>，俗称小灯，用于夜间和特殊天气行驶时，标示汽车的宽度，提醒其他车辆注意。', 'https://timg.mnks.cn/20230926e4eef162.jpg', '全车灯光', 0005, 0.28);
INSERT INTO `question` VALUES (1733399848381685761, 1639, 'b4f0d', 0001, '灯光开关在该位置时，前雾灯点亮。', NULL, '对', '错', '空', '空', '对', '<p><img src=\"https://tp.mnks.cn/tp_365.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"></p>如图所示，红色箭头指向的<b>白色标记</b>位置，是前雾灯指示灯，因此<b>打开前雾灯开关，前雾灯点亮。</b>', 'https://timg.mnks.cn/20230926afa3c07e.jpg', '该位置|前雾灯', 0004, 0.16);
INSERT INTO `question` VALUES (1733399848465571841, 1640, 'af009', 0001, '灯光开关在该位置时，后雾灯点亮。', NULL, '对', '错', '空', '空', '对', '<p><img src=\"https://tp.mnks.cn/tp_368.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"><br>如图所示，红色箭头指向的<b>白色标记</b>位置，是<b>后雾灯指示灯</b>，打开后雾灯开关，后雾灯指示灯和后雾灯亮。因此，题干表述正确。</p>', 'https://timg.mnks.cn/20230926dc87f979.jpg', '后雾灯', 0004, 0.19);
INSERT INTO `question` VALUES (1733399848520097793, 1641, '34d96', 0002, '这是什么操纵装置？ ', 'A、除雾器开关 B、转向灯开关 C、前照灯开关 D、刮水器开关', '除雾器开关 ', '转向灯开关 ', '前照灯开关 ', '刮水器开关', 'D', '<b>刮水器开关</b>控制刮水器与洗涤器，在雨天或下雪天行驶时使用，可清除风窗玻璃上的雨雪，保证驾驶人有良好的视线。拨动开关，风窗玻璃刮水器开始工作。', 'https://timg.mnks.cn/20230926e9650bbb.jpg', '', 0004, 0.19);
INSERT INTO `question` VALUES (1733399848591400961, 1642, '25c0b', 0002, '这是什么操纵装置？ ', 'A、除雾器开关 B、转向灯开关 C、前照灯开关 D、刮水器开关', '除雾器开关 ', '转向灯开关 ', '前照灯开关 ', '刮水器开关', 'A', '<b>除雾器开关</b>控制汽车除雾器的工作，用于减少前、后风窗和车外后视镜表面上的湿气、雾气和霜，以改善视线。有的车型还设有风量调整钮、温度调整钮、送风开关、冷气开关。', 'https://timg.mnks.cn/20230926a6475670.jpg', '', 0003, 0.10);
INSERT INTO `question` VALUES (1733399848637538306, 1643, '8e392', 0002, '这个开关控制机动车哪个部位？ ', 'A、风窗玻璃除雾器 B、风窗玻璃刮水器 C、危险报警闪光灯 D、照明、信号装置', '风窗玻璃除雾器 ', '风窗玻璃刮水器 ', '危险报警闪光灯 ', '照明', 'B', '<b>风窗玻璃刮水器开关</b>一般安装在转向盘右下方转向柱上，用右手操纵。<b>上下拨动这个开关，前风窗玻璃刮水器开始工作</b>。向上拨动拨杆，刮水器单次刮水一回；向下拨动拨杆有3个挡位，分别是间歇刮水、慢速刮水、快速刮水；将杠向内拉动，清洗液喷出。', 'https://timg.mnks.cn/20230926ee99890a.jpg', '', 0003, 0.09);
INSERT INTO `question` VALUES (1733399848679481346, 1644, 'd1493', 0001, '按下这个开关，后风窗玻璃除霜器开始工作。', NULL, '对', '错', '空', '空', '错', '<b>前风窗玻璃除雾器</b>开关点亮，前风窗玻璃除雾器开始工作。弯曲的箭头表示雾气，【前扇后方】<b>扇形图标是前风窗</b>；四四方方是后风窗。', 'https://timg.mnks.cn/20230926ca1bba25.jpg', '后风窗', 0004, 0.16);
INSERT INTO `question` VALUES (1733399848713035777, 1645, 'd669f', 0001, '上下扳动这个开关前风窗玻璃刮水器开始工作。', NULL, '对', '错', '空', '空', '对', '<b>风窗玻璃刮水器开关</b>一般安装在转向盘右下方转向柱上，用右手操纵。向上拨操纵杆，将操纵杆向下拉或向上推，可选择不同的挂刷挡位；向内拉动操纵杆，将喷出清洗液。因此，题干表述正确。', 'https://timg.mnks.cn/20230926e38b99a7.jpg', '刮水器', 0002, 0.05);
INSERT INTO `question` VALUES (1733399848754978817, 1646, '58ca4', 0002, '安全头枕在发生追尾事故时，能有效保护驾驶人的什么部位？ ', 'A、腰部 B、胸部 C、颈部 D、头部', '腰部 ', '胸部 ', '颈部 ', '头部', 'C', '座椅上头枕的主要作用是汽车被追尾时，<b>有效保护驾乘人的颈椎</b>。调整头枕高度时，保持头枕中心与后脑中心平齐，才能发挥保护作用。', '', '安全头枕', 0003, 0.15);
INSERT INTO `question` VALUES (1733399848788533250, 1647, '89c4b', 0001, '安全头枕用于在发生追尾事故时保护驾驶人的头部不受伤害。', NULL, '对', '错', '空', '空', '错', '座椅上头枕的主要作用是汽车被追尾时，<b>有效保护</b>驾驶人和乘车人的<b>颈椎</b>。调整头枕高度时，保持头枕中心与后脑中心平齐，才能发挥保护作用。【安全头枕】三要素：1、正对后脑；2、支撑头部；3、保护颈椎。', '', '头部|安全头枕', 0004, 0.22);
INSERT INTO `question` VALUES (1733399848826281985, 1648, '22006', 0002, '机动车发生碰撞时座椅安全带主要作用是什么？ ', 'A、减轻驾乘人员伤害 B、保护驾乘人员腰部 C、保护驾乘人员颈部 D、保护驾乘人员胸部', '减轻驾乘人员伤害 ', '保护驾乘人员腰部 ', '保护驾乘人员颈部 ', '保护驾乘人员胸部', 'A', '<b>座椅安全带的作用</b>是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。', '', '安全带', 0004, 0.18);
INSERT INTO `question` VALUES (1733399848880807938, 1649, '7838f', 0001, '机动车发生正面碰撞时，安全气囊加上安全带的双重保护才能充分发挥作用。', NULL, '对', '错', '空', '空', '对', '汽车发生碰撞时，安全气囊迅速膨胀，在驾驶人、乘车人的证正面形成一个气垫，从而降低人体受伤害的程度。<b>安全气囊与安全带要配套使用</b>，如果不系安全带，气囊引爆时会对驾乘人员头部和颈部造成严重伤害。', '', '正面碰撞|双重保护', 0001, 0.03);
INSERT INTO `question` VALUES (1733399848939528193, 1650, '17d55', 0001, '机动车在发生碰撞时，安全带可以减轻驾乘人员伤害。', NULL, '对', '错', '空', '空', '对', '<b>座椅安全带的作用</b>是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。', '', '减轻|伤害', 0001, 0.03);
INSERT INTO `question` VALUES (1733399848981471233, 1651, '6e4fd', 0002, '机动车在紧急制动时ABS系统会起到什么作用？ ', 'A、切断动力输出 B、自动控制方向 C、减轻制动惯性 D、防止车轮抱死', '切断动力输出 ', '自动控制方向 ', '减轻制动惯性 ', '防止车轮抱死', 'D', '防抱死制动系统（ABS）的作用是保证车辆在任何路面上进行紧急制动时，<b>防止车轮抱死，保持前轮转向能力</b>，消除制动过程中的跑偏、侧滑等非稳定状态，并获得良好的制动效果.', 'https://timg.mnks.cn/20230926beac63d8.jpg', '紧急制动|ABS', 0003, 0.12);
INSERT INTO `question` VALUES (1733399849040191489, 1652, 'e4924', 0002, '防抱死制动系统（ABS）在什么情况下可以最大限度发挥制动器效能？ ', 'A、间歇制动 B、持续制动 C、缓踏制动踏板 D、紧急制动', '间歇制动 ', '持续制动 ', '缓踏制动踏板 ', '紧急制动', 'D', '防抱死制动系统（ABS）的作用是保证车辆在任何路面上<b>进行紧急制动时</b>，防止车轮抱死，保持前轮转向能力，消除制动过程中的跑偏、侧滑等非稳定状态，并<b>获得良好的制动效果</b>。', '', '最大限度发挥', 0004, 0.26);
INSERT INTO `question` VALUES (1733399849077940226, 1653, '6e438', 0001, '机动车紧急制动时，ABS系统在提供最大制动力的同时能使车前轮保持转向能力。', NULL, '对', '错', '空', '空', '对', '防抱死制动系统（ABS）的作用是保证车辆在任何路面上进行紧急制动时，<b>防止车轮抱死，保持前轮转向能力</b>，消除制动过程中的跑偏、侧滑等非稳定状态，并获得良好的制动效果。', '', '紧急制动|ABS|保持转向', 0003, 0.11);
INSERT INTO `question` VALUES (1733399849119883265, 1654, '1978a', 0001, '装有ABS系统的机动车在冰雪路面上会最大限度缩短制动距离。', NULL, '对', '错', '空', '空', '错', 'ABS系统主要是在紧急制动时，<b>防止车轮抱死</b>，保持前轮的转向能力，并获得良好的制动效果。但在<b>冰雪路面</b>上紧急制动时，它<b>无法有效的缩短制动距离</b>。因为ABS防抱死制动装置防止侧滑，就是当它发现有轮胎抱死后，会松开刹车，以后再刹再松，直到车停下，增加了制动距离。', '', 'ABS|缩短', 0005, 0.43);
INSERT INTO `question` VALUES (1733399849157632001, 1655, '92778', 0001, '驾驶有ABS系统的机动车在紧急制动的同时转向可能会发生侧滑。', NULL, '对', '错', '空', '空', '对', '安装ABS的车辆紧急制动时，可用力踏制动踏板，<b>但在紧急制动的同时转向，车辆还可能侧滑。</b>另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。', '', 'ABS|同时转向', 0003, 0.10);
INSERT INTO `question` VALUES (1733399849195380738, 1656, '1e447', 0001, '安装防抱死制动装置（ABS）的机动车紧急制动时，可用力踏制动踏板。', NULL, '对', '错', '空', '空', '对', 'ABS的作用是在车辆紧急制动时，防止车轮抱死。安装ABS的车辆<b>紧急制动时</b>，可<b>用力踏制动踏板</b>，以获得良好的制动效果。<br>提示：ABS起作用时，制动踏板会出现振动现象。', '', 'ABS|用力踏', 0005, 0.28);
INSERT INTO `question` VALUES (1733399849233129474, 1657, 'a79c0', 0001, '安装防抱死制动装置（ABS）的机动车制动时，制动距离会大大缩短，因此不必保持安全车距。', NULL, '对', '错', '空', '空', '错', '<b>不必保持安全车距，做法错误</b>。保持安全车距确保行车安全，是防止追尾事故最直接、最有效、最广泛和最根本的方法。<br><b>ABS的作用</b>是在车辆紧急制动时，防止车轮抱死。在提供最大制动力的同时能<b>使前轮保持转向能力</b>，消除制动过程中的跑偏、甩尾等不稳定状态，<b>并获得良好的制动效果</b>。', '', 'ABS|缩短', 0003, 0.11);
INSERT INTO `question` VALUES (1733399849283461122, 1658, 'b79d0', 0002, '安全气囊是一种什么装置？ ', 'A、驾驶人头颈保护系统 B、防抱死制动系统 C、电子制动力分配系统 D、辅助驾乘人员保护系统', '驾驶人头颈保护系统 ', '防抱死制动系统 ', '电子制动力分配系统 ', '辅助驾乘人员保护系统', 'D', '<b>安全气囊是一种辅助驾乘人员保护系统</b>，当汽车发生碰撞时，安全气囊迅速膨胀，在驾驶人、乘车人的正面形成一个气垫（安装有侧面安全气囊的，在驾乘人侧面也会形成气垫），<b>从而降低人体受伤害的程度。</b>', '', '', 0004, 0.24);
INSERT INTO `question` VALUES (1733399849321209857, 1659, 'c79e0', 0002, '正面安全气囊与什么配合才能充分发挥保护作用？ ', 'A、座椅安全带 B、防抱死制动系统 C、座椅安全头枕 D、安全玻璃', '座椅安全带 ', '防抱死制动系统 ', '座椅安全头枕 ', '安全玻璃', 'A', '安全气囊与<b>安全带要配套</b>使用，如果不系安全带，气囊引爆时会对驾乘人员头部和颈部造成严重伤害，这种伤害对儿童可能是致命的。', 'https://timg.mnks.cn/20230926a745544b.jpg', '', 0003, 0.15);
INSERT INTO `question` VALUES (1733399849371541506, 1660, 'c2ff4', 0002, '下列哪个指示灯亮表示车辆在使用近光灯。 ', 'A、D图 B、C图 C、B图 D、图A', 'D图 ', 'C图 ', 'B图 ', '图A', 'B', '<b>【请注意】：图片的ABCD并不是对应选项的ABCD。</b><br>A图：前后位置灯（又称示廓灯）开启；<br>B图：前后雾灯；<br><b>C图：近光灯</b>；<br>D图：手刹指示灯。<br>因此，<b>C图指示灯亮</b>表示车辆在<b>使用近光灯</b>。', 'https://timg.mnks.cn/20230926bc18fe8a.jpg', '近光灯', 0003, 0.13);
INSERT INTO `question` VALUES (1733399849421873154, 1661, 'a40d3', 0002, '下列哪个指示灯亮表示车辆在使用远光灯。 ', 'A、C图 B、D图 C、B图 D、图A', 'C图 ', 'D图 ', 'B图 ', '图A', 'B', '<b>【请注意】：图片的ABCD并不是对应选项的ABCD。</b><br>A图：驻车制动器处于制动状态；<br>B图：前后雾灯；<br>C图：车灯总开关；<br><b>D图：远光灯</b>。<br>因此，D图指示灯亮，表示车辆在<b>使用远光灯</b>。', 'https://timg.mnks.cn/20230926cb3c3b59.jpg', '远光灯', 0003, 0.12);
INSERT INTO `question` VALUES (1733399849463816194, 1662, '9c604', 0002, '湿滑路面制动过程中，发现车辆偏离方向，以下做法正确的是？ ', 'A、连续轻踩轻放制动踏板 B、用力踩制动踏板 C、不要踩制动踏板 D、任意踩制动踏板', '连续轻踩轻放制动踏板 ', '用力踩制动踏板 ', '不要踩制动踏板 ', '任意踩制动踏板', 'C', '湿滑路面，车轮的附着力随车速的增加急剧变小，当车辆发生侧滑或水滑方向偏离时，<b>不要急转向或紧急制动</b>，应松抬加速踏板，充分利用发动机制动减速。', '', '湿滑|偏离方向', 0005, 0.51);
INSERT INTO `question` VALUES (1733399849501564930, 1663, '83512', 0002, '假如你驾车行驶在颠簸路段时，以下做法正确的是什么？ ', 'A、稳住加速踏板 B、挂低挡位缓抬加速踏板 C、挂高挡位缓抬加速踏板 D、挂低挡位踏满加速踏板', '稳住加速踏板 ', '挂低挡位缓抬加速踏板 ', '挂高挡位缓抬加速踏板 ', '挂低挡位踏满加速踏板', 'B', '行车中遇颠簸路段时，应<b>挂低速挡缓抬加速踏板</b>，充分利用发动机制动减速，降低凹凸不平路面带来的冲击，缓解车身震动，从而低速平稳地通过颠簸路段。', '', '颠簸路段|做法正确', 0004, 0.18);
INSERT INTO `question` VALUES (1733399849543507970, 1664, '17d01', 0002, '行驶至这种上坡路段时，以下做法正确的是什么？ ', 'A、换低挡位，踏加速踏板 B、换低挡位，松开加速踏板 C、换高挡位，踏加速踏板 D、换高档位，松开加速踏板', '换低挡位，踏加速踏板 ', '换低挡位，松开加速踏板 ', '换高挡位，踏加速踏板 ', '换高档位，松开加速踏板', 'A', '驾驶机动车在山区上坡时，提前观察路况、坡道长度和坡度，在车速下降前减挡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火。<b>上陡坡时，要在坡底提前减挡，加速冲坡</b>。驶近坡道顶端无法看清前方路口时，要减速慢行并鸣喇叭示意，不得加速冲过坡顶，防止对向有来车和其他意外情况。', 'https://timg.mnks.cn/20230926d2ee4d2c.jpg', '上坡路', 0004, 0.21);
INSERT INTO `question` VALUES (1733399849593839618, 1665, '8c817', 0002, '长下坡禁止挂空挡，下列原因错误的是？ ', 'A、长下坡挂低速挡可以借助发动机控制车速 B、避免因刹车失灵发生危险 C、长下坡空挡滑行导致车速过高时，难以抢挂低速档控制车速 D、下坡挂空挡，油耗容易增多', '长下坡挂低速挡可以借助发动机控制车速 ', '避免因刹车失灵发生危险 ', '长下坡空挡滑行导致车速过高时，难以抢挂低速档控制车速 ', '下坡挂空挡，油耗容易增多', 'D', '请注意，题目要求选出<b>原因错误</b>的选项，因此<b>挂空挡，油耗增多说法错误</b>。因为长下坡空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很容易引发严重的交通事故。空挡滑行不但不省油，会伤车，而且还不安全。', '', '空挡|错误', 0004, 0.21);
INSERT INTO `question` VALUES (1733399849707085825, 1666, '86de7', 0002, '驾驶机动车下陡坡时，以下说法正确的是？ ', 'A、可以熄火 B、可以空挡但不准熄火 C、可以空挡 D、不准空挡或熄火', '可以熄火 ', '可以空挡但不准熄火 ', '可以空挡 ', '不准空挡或熄火', 'D', '驾驶机动车下陡坡行驶，要挂中低速档位，充分利用发动机制动来控制车速，<b>严禁空挡滑行</b>。 <br>【拓展知识-空挡滑行不省油而且危险大】 <br>空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效。转向失灵等极端情况，很容易引发严重的交通事故。', '', '下陡坡', 0002, 0.07);
INSERT INTO `question` VALUES (1733399849799360513, 1667, 'fef40', 0002, '下面哪种做法能帮助您避免被其他车辆从后方追撞？ ', 'A、在任何时候都打开转向灯 B、在转弯前提前打开相应的转向灯 C、一直打开双闪 D、转弯前鸣笛示意', '在任何时候都打开转向灯 ', '在转弯前提前打开相应的转向灯 ', '一直打开双闪 ', '转弯前鸣笛示意', 'B', '驾驶人要充分<b>借助车辆照明及信号装置向其他交通参与者发出信号，表明自己的行驶意图和行驶状态</b>，让其他车辆和行人及时地注意到自己。因此，当车辆转弯前，要<b>提前开启相应的转向灯示意</b>，可避免被其他车辆从后方追撞。', '', '避免|后方追撞', 0002, 0.08);
INSERT INTO `question` VALUES (1733399849866469377, 1668, '3fa04', 0002, '以下安全带系法正确的是？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'A', '系安全带时，先根据身高扳动安全带高度调整钮调整好高度，再用右手缓慢将安全带向下平顺拉出，使<b>安全带从肩与颈根（锁骨下）之间，通过胸部的适当位置，将搭扣插头插入插座里</b>，当听到“喀”的一声为止。然后，猛拉安全带，检查自动锁止是否安全有效。', 'https://timg.mnks.cn/20230926c2097c5e.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399849908412418, 1669, '08491', 0002, '以下哪个仪表表示发动机转速表？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'A', '看图识仪表盘，<b>图1：发动机转速表</b>；图2：速度和里程表；图3：水温表；图4：燃油表。因此，图1仪表表示发动机转速表。', 'https://timg.mnks.cn/20230926ab591681.jpg', '转速表', 0004, 0.24);
INSERT INTO `question` VALUES (1733399849950355458, 1670, '3007d', 0002, '以下哪个仪表表示速度和里程表？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'B', '图1：发动机转速表；<b>图2：速度和里程表</b>；图3：水温表；图4：燃油表。<br><b>速度和里程表</b>：速度表指示汽车行驶速度，单位为公里/小时<b>（km/h）</b>，速度表指针所指的数字显示当前车辆的行驶速度。里程表累计行驶总里程数以公里（km）为单位；', 'https://timg.mnks.cn/20230926d7424183.jpg', '速度和里程表', 0003, 0.13);
INSERT INTO `question` VALUES (1733399849988104194, 1671, '1065a', 0002, '以下哪个仪表表示水温表？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'C', '<img src=\"https://tp.mnks.cn/tp_359.jpg\" style=\"max-width: 100%; max-height: 350px; width: auto; height: auto;\"> <br>如图所示：水温表<b>（图3）</b>，指示发动机冷却液的温度，单位为摄氏度（℃）。“C”表示温度低，“H”表示温度高，水温表指针所指的位置显示当前冷却液的温度。', 'https://timg.mnks.cn/20230926f5ba8c14.jpg', '水温表', 0002, 0.06);
INSERT INTO `question` VALUES (1733399850021658625, 1672, '4ab8e', 0002, '以下哪个仪表表示燃油表？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '燃油表上有个加油站里的加油机的标志', 'https://timg.mnks.cn/20230926aca53fa1.jpg', '燃油表', 0002, 0.05);
INSERT INTO `question` VALUES (1733399850059407361, 1673, '8022a', 0002, '图中哪个报警灯亮，提示充电电路异常或故障？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '图1提示油箱内燃油已到最低液面；<br>图2提示制动系统异常或故障；<br>图3提示防抱死制动系统出现故障；<br><b>图4提示充电电路故障或发电机不向蓄电池充电</b>。有正负极＋－标志的，就表示有电。', 'https://timg.mnks.cn/20230926f94ad455.jpg', '充电电路', 0002, 0.06);
INSERT INTO `question` VALUES (1733399850097156098, 1674, '85b11', 0002, '哪个报警灯亮，提示发动机控制系统异常或故障？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'C', '图1-油量指示灯，该灯常亮，提示油量不足，油箱内燃油已到最低液面；<br>图2-气囊指示灯，该灯常亮，提示安全气囊出现故障；<br><b>图3-发动机指示灯</b>，该灯常亮，提示<b>发动机控制系统出现异常或故障</b>；<br>图4-水温指示灯，该灯常亮，提示发动机温度过高，发动机内冷却液的温度超过规定值，需要立即暂停行驶。', 'https://timg.mnks.cn/20230926ace720a9.jpg', '发动机控制系统', 0002, 0.06);
INSERT INTO `question` VALUES (1733399850151682050, 1675, '362fd', 0002, '为提示车辆和行人注意，雾天必须开启哪个灯？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '<b>雾天行车必须开启雾灯</b>。<br>图1-前风窗玻璃刮水器及洗涤器开关；<br>图2-远光灯；<br>图3-左右转向灯；<br><b>图4-前后雾灯</b>。', 'https://timg.mnks.cn/20230926b84526e1.jpg', '雾天', 0003, 0.12);
INSERT INTO `question` VALUES (1733399850197819394, 1676, '4c8fa', 0002, '车辆发生意外，要及时打开哪个灯? ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '车辆发生意外时，要及时开启<b>危险报警闪光灯</b>。<br>图1，前后位置灯（又称示廓灯）；<br>图2，前后雾灯；<br>图3，远光灯；<br><b>图4，<b>危险报警闪光灯</b>（故障停车信号灯）</b>。', 'https://timg.mnks.cn/20230926adf735a8.jpg', '', 0002, 0.05);
INSERT INTO `question` VALUES (1733399850235568130, 1677, '19daa', 0002, '以下哪个指示灯亮时，提醒驾驶人安全带插头未插入锁扣？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'A', '<b>图1-安全带指示灯，该灯亮，提醒安全带插头未插入锁扣</b>；<br>图2-水温指示灯，该灯常亮，提醒发动机温度过高，发动机内冷却液的温度超过规定值，需要立即暂停行驶；<br>图3-ABS指示灯，该灯常亮，提醒车辆ABS防抱死系统出现故障；<br>图4-油量指示灯，该灯常亮，提醒油量不足，油箱内燃油已到最低液面.', 'https://timg.mnks.cn/20230926a68ddd7d.jpg', '安全带插头', 0001, 0.02);
INSERT INTO `question` VALUES (1733399850277511169, 1678, '3c089', 0002, '以下哪个指示灯亮时，表示发动机温度过高？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'B', '图1-机油指示灯，该灯常亮，表示发动机机油压力低于规定标准，需要维修；<br><b>图2-水温指示灯，该灯常亮，表示发动机温度过高，发动机内冷却液的温度超过规定值，需要立即暂停行驶；</b><br>图3-ABS指示灯，该灯常亮，表示车辆ABS防抱死系统出现故障；<br>图4-油量指示灯，该灯常亮，表示油量不足，油箱内燃油已到最低液面。', 'https://timg.mnks.cn/20230926e96ed712.jpg', '温度过高', 0003, 0.16);
INSERT INTO `question` VALUES (1733399850323648513, 1679, '88326', 0002, '以下哪个指示灯亮时，表示油箱内燃油已到最低液面？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'D', '图1-机油指示灯，该灯常亮，表示发动机机油压力低于规定标准，需要维修；<br>图2-前雾灯，该灯亮，表示前雾灯开启；<br>图3-ABS指示灯，该灯常亮，表示车辆ABS防抱死系统出现故障；<br><b>图4-油量指示灯</b>，该灯常亮，表示<b>油量不足，油箱内燃油已到最低液面</b>；', 'https://timg.mnks.cn/20230926dd41fc37.jpg', '燃油', 0004, 0.19);
INSERT INTO `question` VALUES (1733399850365591553, 1680, '567a4', 0002, '行车中下列哪个灯亮，提示驾驶人车辆制动系统出现异常? ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'C', '图1-油量指示灯，该灯常亮，提示油量不足，油箱内燃油已到最低液面；<br>图2-水温指示灯，该灯常亮，提示发动机温度过高，发动机内冷却液的温度超过规定值，需要立即暂停行驶；<br><b>图3-手刹指示灯，该灯常亮，提示制动系统出现异常或故障</b>；<br>图4-发动机指示灯，该灯常亮，提示发动机控制系统出现异常或故障，需要维修。', 'https://timg.mnks.cn/20230926dfc89a47.jpg', '制动系统|行车中', 0004, 0.19);
INSERT INTO `question` VALUES (1733399850407534593, 1681, '2bf69', 0002, '以下哪个指示灯亮时，表示防抱死制动系统出现故障？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'C', '图1-机油指示灯，该灯常亮，表示发动机机油压力低于规定标准，需要维修；<br>图2-前雾灯，该灯亮，表示前雾灯开启；<br><b>图3-ABS指示灯</b>，该灯常亮，表示<b>车辆ABS防抱死系统出现故障</b>；<br>图4-水温指示灯，该灯常亮，表示发动机温度过高，发动机内冷却液的温度超过规定值，需要立即暂停行驶。', 'https://timg.mnks.cn/20230926d968e59d.jpg', '防抱死制动系统', 0001, 0.04);
INSERT INTO `question` VALUES (1733399850449477633, 1682, '9f173', 0002, '以下哪个指示灯亮时，表示发动机机油压力过低？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'A', '<b>图1-机油指示灯</b>，该灯常亮，表示<b>发动机机油压力低于规定标准</b>，需要维修；<br>图2-前雾灯，该灯亮，表示前雾灯开启；<br>图3-ABS指示灯，该灯常亮，表示车辆ABS防抱死系统出现故障；<br>图4-水温指示灯，该灯常亮，表示发动机温度过高，发动机内冷却液的温度超过规定值，需要立即暂停行驶。', 'https://timg.mnks.cn/20230926fcd0e3e3.jpg', '机油压力过低', 0003, 0.11);
INSERT INTO `question` VALUES (1733399850491420674, 1683, 'b2c15', 0001, '机油压力报警灯持续亮，可边行驶，边观察，等待报警灯自行熄灭。', NULL, '对', '错', '空', '空', '错', '机油压力报警灯持续亮，说明该车发动机机油压力低于规定标准，<b>需要立即停车检查，不能继续运行发动机</b>，否则有可能造成发动机因得不到良好润滑而烧瓦抱轴的严重故障。因此，题干表述错误。', '', '持续亮', 0003, 0.09);
INSERT INTO `question` VALUES (1733399850537558018, 1684, '485d5', 0001, '图中左侧白色轿车，在这种情况下为了保证安全，应适当降低车速。', NULL, '对', '错', '空', '空', '对', '如图所示，右侧限速标志指示该路段最高限速60公里/小时，而速度仪表指针指在数字60，随时都有超速风险。因此，在这种情况下为了保证安全，应<b>适当降低车速</b>。', 'https://timg.mnks.cn/20230926cf9a6dbd.jpg', '降低车速', 0002, 0.06);
INSERT INTO `question` VALUES (1733399850696941570, 1685, '91bfb', 0001, '行车中，制动报警灯亮，应试踩一下制动，只要有效可正常行车。', NULL, '对', '错', '空', '空', '错', '行车中制动报警灯亮，说明制动系统出现异常或故障，<b>不可继续行驶</b>，应尽快选择安全地点靠边停车，对故障原因进行排查，必要时开到修理厂检修。', '', '试踩|可正常', 0003, 0.11);
INSERT INTO `question` VALUES (1733399850751467521, 1686, 'fb25d', 0001, '行车中，燃油报警灯亮，应及时到附近加油站加油，以免造成车辆乘员滞留公路，发生交通事故。', NULL, '对', '错', '空', '空', '对', '行车中，<b>燃油报警灯亮，提示油箱内燃油不足</b>，需及时加油，保证车辆正常运行。', '', '燃油报警|及时', 0001, 0.02);
INSERT INTO `question` VALUES (1733399850797604866, 1687, '8fa4d', 0002, '行车中水温报警灯亮，下列可能是其原因的是？ ', 'A、缺少润滑油 B、指示灯损坏 C、缺少冷却液 D、冷却液过多', '缺少润滑油 ', '指示灯损坏 ', '缺少冷却液 ', '冷却液过多', 'C', '行车中<b>水温报警灯亮</b>，提示发动机温度过高，可能是<b>冷却液不足</b>导致的。<br>【拓展知识】<br>冷却液不足，导致发动机工作温度增高，发动机温度得不到充分冷却，在高温情况下，机油粘度增加而润滑性下降，发动机磨损加快，严重的会导致发动机拉缸。', '', '水温报警', 0002, 0.08);
INSERT INTO `question` VALUES (1733399850831159297, 1688, '7573d', 0002, '车辆因故障等原因需被牵引时，以下说法正确的是什么？ ', 'A、前后车均应打开报警灯 B、所有车辆都应让行 C、两车尽量快速行驶 D、不受交通信号限制', '前后车均应打开报警灯 ', '所有车辆都应让行 ', '两车尽量快速行驶 ', '不受交通信号限制', 'A', '《道路交通安全法实施条例》第六十一条 牵引故障机动车应当遵守下列规定：<br>(一)被牵引的机动车除驾驶人外不得载人，不得拖带挂车；<br>(二)被牵引的机动车宽度不得大于牵引机动车的宽度；<br>(三)使用软连接牵引装置时，牵引车与被牵引车之间的距离应当大于4米小于10米；<br>(四)对制动失效的被牵引车，应当使用硬连接牵引装置牵引；<br>(五)<b>牵引车和被牵引车均应当开启危险报警闪光灯。</b>', 'https://timg.mnks.cn/20230926f62ee645.jpg', '故障|被牵引', 0002, 0.04);
INSERT INTO `question` VALUES (1733399850868908034, 1689, 'f69fc', 0001, '驾驶电动汽车出行前，应检查剩余电量。', NULL, '对', '错', '空', '空', '对', '驾驶<b>电动汽车出行前，应提前检查好剩余电量，掌握好充电时间</b>，尽量保证每天使用车辆后及时充电，避免因电量过低导致车辆搁浅；充电前，养成良好的充电设备外观检查及车辆充电口检查习惯，确保充电安全。', '', '出行前|应检查', 0001, 0.01);
INSERT INTO `question` VALUES (1733399850915045377, 1690, 'e2cc5', 0002, '驾驶电动汽车，以下说法错误的是什么？ ', 'A、充电时不得在车上放置易燃易爆物品 B、出行前，应确认剩余电量 C、车辆应配备普通灭火器 D、电量不足时要及时充电', '充电时不得在车上放置易燃易爆物品 ', '出行前，应确认剩余电量 ', '车辆应配备普通灭火器 ', '电量不足时要及时充电', 'C', '请注意，题干要求<b>选出说法错误的选项</b>。出行前要查看车辆剩余电量，电量不足时及时充电，保证能够满足此次出行距离；在为电动汽车充电时要规范充电，不得在车上放置易燃易爆物品；车辆应配备<b>专用的车载灭火器</b>。', '', '说法错误', 0003, 0.16);
INSERT INTO `question` VALUES (1733399850952794113, 1691, 'e67b7', 0001, '冬季给电动汽车充电前，应提前预热电池。', NULL, '对', '错', '空', '空', '对', '冬季气温降低，电动车的电池性能会受到影响。<b>在给电动车充电之前</b>，需要<b>提前预热电池</b>，以确保电池能够正常充电。<br>【拓展知识】<br>预热电池的方法有几种，其中一种方法是使用电动车的暖风系统，将车内温度提高至一定程度，从而使电池温度升高。另一种方法是使用电池加热器，将电池加热至一定温度，以提高电池性能。', '', '冬季|应提前', 0002, 0.06);
INSERT INTO `question` VALUES (1733399850994737153, 1692, 'e88a3', 0002, '以下缩写中，表示车辆自适应巡航系统缩写的是什么？ ', 'A、EBD B、ACC C、ABS D、BSD', 'EBD ', 'ACC ', 'ABS ', 'BSD', 'B', '<b>自适应巡航控制系统的英文缩写是ACC</b>，又可称为智能巡航控制系统，它将汽车自动巡航控制系统（CCS）和车辆前向撞击报警（FCW）系统有机结合起来，自适应巡航控制不但具有自动巡航的全部功能，还可以通过车载雷达等传感器监测汽车前方的道路交通环境。', '', '自适应巡航系统', 0004, 0.22);
INSERT INTO `question` VALUES (1733399851099594754, 1693, 'bf0b3', 0002, '以下缩写中，表示车辆车道偏离预警系统缩写的是什么？ ', 'A、TSR B、TMC C、LDW D、ALC', 'TSR ', 'TMC ', 'LDW ', 'ALC', 'C', '<b>LDW车道偏离预警系统</b>是一种通过报警的方式辅助驾驶员减少汽车因车道偏离而发生交通事故的系统。<br>【拓展知识】<br>车道偏离预警系统提供智能的车道偏离预警，在无意识（驾驶人未打转向灯）偏离原车道时，能在偏离车道0.5秒之前发出警报，为驾驶人提供更多的反应时间，大大减少了因车道偏离引发的碰撞事故；该系统可提醒过度疲劳或解决长时间单调驾驶引发的注意力不集中等情况。', '', '车道偏离预警系统', 0005, 0.32);
INSERT INTO `question` VALUES (1733399851141537793, 1694, 'e6b05', 0001, '你驾车启动发动机后，应检查仪表盘，如有黄色或红色指示灯亮时，须及时排除故障。', NULL, '对', '错', '空', '空', '对', '<b>机动车启动后，仪表盘上指示灯亮，车辆可能有异常情况时，应及时停车处置。</b><br>在使用过程中仪表盘黄灯亮起，说明车辆发动机需要保养/维护；发动机故障灯亮的方式有几种：黄灯常亮，黄灯闪烁；红灯亮，红灯闪烁。如果亮的是红灯，马上去4S店检查、修理，故障很严重。如果是黄灯亮，故障可能不影响行驶，但也要尽快去检查。', '', '检查仪表盘|须及时', 0001, 0.03);
INSERT INTO `question` VALUES (1733399851183480834, 1695, 'fc3d2', 0001, '已经达到报废标准的机动车在缴纳管理费后可以上道路行驶。', NULL, '对', '错', '空', '空', '错', '《道路交通安全法》第十四条　国家实行机动车强制报废制度，根据机动车的安全技术状况和不同用途，规定不同的报废标准。<br>应当报废的机动车必须及时办理注销登记。<br><b>达到报废标准的机动车不得上道路行驶</b>。报废的大型客、货车及其他营运车辆应当在公安机关交通管理部门的监督下解体。', '', '达到报废|可以上道路', 0002, 0.05);
INSERT INTO `question` VALUES (1733399851225423873, 1696, 'f2f14', 0002, '机动车仪表板上（如图所示）亮表示什么？ ', 'A、防抱死制动系统故障 B、驻车制动器处于解除状态 C、安全气囊处于故障状态 D、行车制动系统故障', '防抱死制动系统故障 ', '驻车制动器处于解除状态 ', '安全气囊处于故障状态 ', '行车制动系统故障', 'A', '仪表板上如图所示<b>ABS防抱死</b>制动系统指示灯亮，表示<b>防抱死制动系统</b>出现故障。', 'https://timg.mnks.cn/20230926bd010c71.jpg', '', 0004, 0.18);
INSERT INTO `question` VALUES (1733399851267366913, 1697, 'bd58d', 0001, '应注意电动汽车的保养，电动汽车出现故障需要维修时，应选择专业的维修机构或者人员。', NULL, '对', '错', '空', '空', '对', '新能源汽车的内部结构与传统燃油车不同，驾驶人发现电动汽车发生故障需要维修时，不能擅自拆卸装置，<b>应当选择专业的维修人员或者机构进行维修</b>。', '', '应注意|选择专业', 0001, 0.01);
INSERT INTO `question` VALUES (1733399851305115650, 1698, 'dceb0', 0001, '为电动汽车充电应使用符合规定的充电设备，掌握适当的充电时长，规范充电。', NULL, '对', '错', '空', '空', '对', '电动汽车正确的充电方法及注意事项：<br>（1）电池每次使用放电深度越小（距离越短），电池的使用寿命就越长，平时应养成随用随充的良好习惯，使用电池经常保持足电状态。<br>（2）<b>充电时间应根据行驶里程长短有所不同</b>，里程越长，充电时间就长，反之则短，充电时间就控制在4-12小时，不得长时间充电。<br>（3）电池需长时间放置时必须先充足电量，一般每一个月补充一次。<br>（4）大电流放电对电池有一定的损害，所以在起步、上坡、负重、顶风时用脚踏加以助力。<br>（5）<b>充电时要用符合规定的充电设备、配套的充电器</b>，<b>放置有阴凉通风处、避免高温和潮湿，切勿让水进入充电器</b>，防止触电事故。', '', '符合规定|规范', 0001, 0.01);
INSERT INTO `question` VALUES (1733399851355447297, 1699, 'b7cea', 0002, '关于驾驶机动车时转向灯的使用，以下说法正确的是什么？ ', 'A、超车完毕驶回原车道时可不开转向灯 B、驶离停车地点时不必开启转向灯 C、靠边停车时应提前开启右转向灯 D、准备变更车道时可以不开转向灯', '超车完毕驶回原车道时可不开转向灯 ', '驶离停车地点时不必开启转向灯 ', '靠边停车时应提前开启右转向灯 ', '准备变更车道时可以不开转向灯', 'C', '根据《道路交通安全法实施条例》第五十七条，机动车应当按照下列规定使用转向灯：<br>1、向左转弯、向左<b>变更车道</b>、准备超车、<b>驶离停车地点</b>或者掉头时，应当<b>提前开启左转向灯</b>；<br>2、向右转弯、向右<b>变更车道</b>、<b>超车完毕驶回原车道</b>、<b>靠路边停车</b>时，应当<b>提前开启右转向灯</b>。', '', '转向灯', 0003, 0.09);
INSERT INTO `question` VALUES (1733399851414167554, 1700, 'be257', 0002, '下列避免爆胎的做法中，错误的是哪一个？ ', 'A、降低轮胎气压 B、定期检查轮胎 C、行车中轻踏制动踏板 D、行车中控制好方向', '降低轮胎气压 ', '定期检查轮胎 ', '行车中轻踏制动踏板 ', '行车中控制好方向', 'A', '<b>轮胎气压过低</b>，车辆高速行驶时会出现波浪变形、温度升高<b>极易导致爆胎</b>。避免爆胎的正确做法是：1、每次行车前检查胎压是否正常，及时清理轮胎表面及沟槽里的异物；2、胎面磨损到小三角标志或轮胎花纹里的标记线或达到保质期时，必须更换新胎；3、季节更替时不可随意变更轮胎的气压；4、要选用同一品牌同一型号的轮胎；5、行驶中，应尽量避免碾压、碰撞、剐蹭坚硬的物体及液体。', '', '错误', 0004, 0.18);
INSERT INTO `question` VALUES (1733399851502247938, 1701, 'ccc7e', 0002, '以下缩写中，表示车辆随动转向前照灯系统缩写的是什么？ ', 'A、TSR B、TMC C、LDW D、ALC', 'TSR ', 'TMC ', 'LDW ', 'ALC', 'B', '<b>车辆随动转向前照灯系统的英文缩写是AFS</b>，全称为汽车自适应前大灯系统或者智能前照灯系统，自适应前大灯系统AFS（Adaptive Front-lighting System），随动转向大灯可以根据方向盘的角度、车辆的偏转率和行驶速度不断地动态调整前照灯，以适应当前的转向角，使光的方向与车辆的当前行驶方向保持一致，为驾驶人提供全方位的安全照明，从而有效地提高了夜间驾驶的安全性。', '', '车辆随动转向前照灯系统', 0005, 0.45);
INSERT INTO `question` VALUES (1733399851560968193, 1702, 'c1cd7', 0002, '驾车过程中发现以下哪个指示灯亮时，应意识到是安全带插头未插入锁扣？ ', 'A、图1 B、图2 C、图3 D、图4', '图1 ', '图2 ', '图3 ', '图4', 'A', '<b>图1-安全带指示灯，该灯亮，提醒安全带插头未插入锁扣</b>；<br>图2-水温指示灯，该灯常亮，提醒发动机温度过高，发动机内冷却液的温度超过规定值，需要立即暂停行驶；<br>图3-ABS指示灯，该灯常亮，提醒车辆ABS防抱死系统出现故障；<br>图4-油量指示灯，该灯常亮，提醒油量不足，油箱内燃油已到最低液面.', 'https://timg.mnks.cn/20230926a68ddd7d.jpg', '安全带插头', 0001, 0.02);
INSERT INTO `question` VALUES (1733399851607105537, 1703, 'adcf1', 0001, '儿童安全座椅能够在汽车发生碰撞情况下，通过减缓对儿童的冲击力和限制儿童的身体移动来减少对他们的伤害。', NULL, '对', '错', '空', '空', '对', '儿童安全座椅系于汽车座位上（应当置于后排座位上），供儿童乘坐并且具有束缚设备，在汽车发生碰撞或突然减速的情况下，减缓对儿童的冲击力和限制儿童的身体移动来减少伤害，最大限度保障儿童的安全。题干表述正确。', '', '儿童安全座椅|减缓|限制', 0001, 0.02);
INSERT INTO `question` VALUES (1733399851657437186, 1704, 'eda53', 0002, '交通事故中急救中毒伤员，以下做法错误的是什么？ ', 'A、尽快将中毒人员移出毒区 B、脱去接触有毒空气的衣服 C、用清水清洗暴露部位 D、原地等待救援', '尽快将中毒人员移出毒区 ', '脱去接触有毒空气的衣服 ', '用清水清洗暴露部位 ', '原地等待救援', 'D', '<b>请注意，题干要求选出做法错误的选项</b>。交通事故中急救中毒伤员时，要在<b>第一时间迅速将伤员移到有新鲜空气的地方</b>，以防止继续中毒。因此，<b>原地等待做法错误。</b>', '', '中毒伤员|错误', 0004, 0.16);
INSERT INTO `question` VALUES (1733399851699380226, 1705, 'aa4f4', 0002, '在事故现场抢救伤员的基本要求是什么？ ', 'A、先治伤，后救命 B、先救命，后治伤 C、先帮助轻伤员 D、后救助重伤员', '先治伤，后救命 ', '先救命，后治伤 ', '先帮助轻伤员 ', '后救助重伤员', 'B', '在事故现场抢救伤员的基本要求是<b>先救命后治伤</b>。遇重特大事故有众多伤员需送往医院时，处于昏迷状态的伤员，首先送往医院。', '', '基本要求', 0002, 0.04);
INSERT INTO `question` VALUES (1733399851732934658, 1706, 'a5537', 0001, '遇伤者被压于车轮或货物下时，要立即拉拽伤者的肢体将其拖出。', NULL, '对', '错', '空', '空', '错', '【避免二次伤害，<b>切勿</b>生拉硬拽】受伤者被压于车轮或货物下时，对伤员生拉硬拽很可能对伤员造成二次伤害，在没有把握时，应保护好现场，等候医护人员的到来。', '', '立即拉拽', 0002, 0.08);
INSERT INTO `question` VALUES (1733399851770683393, 1707, 'c50e6', 0002, '抢救昏迷失去知觉的伤员需注意什么？ ', 'A、马上实施心肺复苏 B、使劲掐伤员的人中 C、连续拍打伤员面部 D、抢救前先检查呼吸', '马上实施心肺复苏 ', '使劲掐伤员的人中 ', '连续拍打伤员面部 ', '抢救前先检查呼吸', 'D', '抢救昏迷失去知觉的伤员应<b>先检查伤员的呼吸情况</b>，测定生命体征，确定有无呼吸、心跳，确定大动脉有无搏动，如果伤员失去生命体征，应立即进行心肺复苏。', '', '昏迷失去知觉', 0003, 0.12);
INSERT INTO `question` VALUES (1733399851808432129, 1708, 'ec83b', 0001, '搬运昏迷失去知觉的伤员要采取仰卧位。', NULL, '对', '错', '空', '空', '错', '搬运昏迷失去知觉或有窒息危险的伤员时，应采取<b>侧卧的方式</b>，头偏向一侧，防止口腔分泌物或呕吐物进入气道，引起窒息。', '', '搬运昏迷|仰卧位', 0004, 0.25);
INSERT INTO `question` VALUES (1733399851875540994, 1709, 'c7bc0', 0002, '成人心肺复苏时，胸外按压频率是多少？ ', 'A、80-100次/分 B、60-80次/分 C、100-120次/分 D、120-140次/分', '80-100次/分 ', '60-80次/分 ', '100-120次/分 ', '120-140次/分', 'C', '对伤员进行胸外心脏按压的具体操作方法是：将掌根放在胸部中央胸骨下1/2段，中指对准乳头，双掌根重叠十指紧扣肘关节伸直，垂直向下按压30次，深度至少5厘米，但不超过6厘米，每次按压应确保胸廓完全回弹，放松时掌根不离开伤员胸部，并且按压和放松的时间相对。注意：<b>成人按压次数为：每分钟至少100次，但不超过120次。</b>', '', '按压频率', 0004, 0.21);
INSERT INTO `question` VALUES (1733399851984592898, 1710, 'efc78', 0002, '抢救失血伤员时，要先采取什么措施？ ', 'A、观察 B、包扎 C、止血 D、询问', '观察 ', '包扎 ', '止血 ', '询问', 'C', '抢救失血伤员，要<b>先止血</b>。', '', '先采取', 0002, 0.05);
INSERT INTO `question` VALUES (1733399852039118850, 1711, 'a684d', 0002, '采用指压止血法为动脉出血伤员止血时，拇指压住伤口的什么位置？ ', 'A、近心端动脉 B、血管下方动脉 C、远心端动脉 D、血管中部', '近心端动脉 ', '血管下方动脉 ', '远心端动脉 ', '血管中部', 'A', '伤员较大动脉出血时，采用<b>指压止血法，用拇指压住伤口的近心端动脉</b>，阻断动脉运动，达到快速止血的目的。', '', '动脉出血', 0004, 0.19);
INSERT INTO `question` VALUES (1733399852076867586, 1712, 'eaabe', 0001, '在没有绷带急救伤员的情况下，可用毛巾、手帕、床单、长筒尼龙袜子等代替绷带包扎。', NULL, '对', '错', '空', '空', '对', '手帕、毛巾、床单、长筒尼龙袜等<b>棉质物品，相对柔软透气</b>，可以根据受伤位置的不同和创面的大小，做成简易的三角巾代替绷带进行包扎救护。', '', '代替绷带', 0001, 0.02);
INSERT INTO `question` VALUES (1733399852127199233, 1713, 'fed38', 0001, '在紧急情况下为伤员止血时，须先用压迫法止血后再根据出血情况改用其他止血法。', NULL, '对', '错', '空', '空', '对', '压迫止血法是最直接、最简单、最有效地制止外部出血的方法。在紧急情况下为伤员止血时，须先用压迫法止血后再根据出血情况改用其他止血法。', '', '先用压迫法止血', 0002, 0.04);
INSERT INTO `question` VALUES (1733399852169142274, 1714, 'd23f1', 0001, '救助失血过多出现休克的伤员要采取保暖措施。', NULL, '对', '错', '空', '空', '对', '<b>失血性休克常伴有四肢厥冷的症状</b>，所以需要采取保暖的措施保持伤员的正常体温。', '', '休克|保暖', 0002, 0.07);
INSERT INTO `question` VALUES (1733399852211085313, 1715, 'a4f5b', 0002, '救助全身燃烧伤员采取哪种应急措施？ ', 'A、用沙土覆盖火焰灭火 B、向身上喷冷水灭火 C、用灭火器进行灭火 D、帮助脱掉燃烧的衣服', '用沙土覆盖火焰灭火 ', '向身上喷冷水灭火 ', '用灭火器进行灭火 ', '帮助脱掉燃烧的衣服', 'B', '救助<b>全身燃烧</b>的伤员，向<b>全身燃烧</b>伤员身上喷冷水，<b>迅速扑灭衣服上的火焰</b>。用沙土覆盖会造成伤口感染，甚至危及生命；灭火器喷出的物质也会造成身体上的伤害；脱去正在燃烧的衣服会对施救者的自身安全造成威胁，还可能会对伤者的皮肤造成二次伤害。', '', '全身燃烧', 0004, 0.21);
INSERT INTO `question` VALUES (1733399852253028354, 1716, 'f46ad', 0001, '烧伤伤员口渴时，可喝少量的淡盐水。', NULL, '对', '错', '空', '空', '对', '<b>烧伤伤员口渴时，可喝少量的淡盐水。</b>因为烧伤是有创面的，会损失体液，而人的体液本来就含有盐分，所以喝淡盐水可迅速补充体液。', '', '烧伤|淡盐水', 0002, 0.04);
INSERT INTO `question` VALUES (1733399852294971393, 1717, 'a7c94', 0002, '抢救骨折伤员时注意什么？ ', 'A、迅速抬上担架送往医院 B、适当调整损伤时的姿势 C、用绷带对骨折部位进行包扎 D、不要移动身体骨折部位', '迅速抬上担架送往医院 ', '适当调整损伤时的姿势 ', '用绷带对骨折部位进行包扎 ', '不要移动身体骨折部位', 'D', '抢救骨折伤员时，应遵守现场急救的“<b>三不</b>”黄金法则：<br><b>1、不复位</b>，即<b>不要移动骨折部位</b>，因为移动骨折部位极易造成骨折处的二次受损，或污染的骨折端回缩造成深部感染。<br><b>2、不要盲目上药</b>，这种做法会给医院处理增加难度，建议没有医学经验或经过急救培训的人，最好不要给患者上药，以免增加处理难度。<br><b>3、不冲洗</b>，因为冲洗易将污染物带入身体深部甚至骨髓,造成伤口感染，引发骨髓炎。', '', '骨折', 0003, 0.13);
INSERT INTO `question` VALUES (1733399852328525826, 1718, 'ea81c', 0001, '移动脊柱骨折的伤员，切勿扶持伤者走动，可用软担架运送。', NULL, '对', '错', '空', '空', '错', '为了防止对脊柱损伤进行二次伤害，切勿扶持伤者走动，更不得使用软担架，一般是<b>通过硬担架进行运送</b>。<br>使用软担架移动脊柱骨折伤员可能会导致伤员腰部过度弯曲，导致脊柱骨折情况加重甚至会损伤到脊髓神经，导致伤员出现不同程度的瘫痪。', '', '脊柱骨折|软担架', 0004, 0.19);
INSERT INTO `question` VALUES (1733399852362080258, 1719, 'bf4cc', 0002, '机动车着火且火势可控时，应怎样正确使用灭火器灭火？ ', 'A、灭火器瞄准火源 B、尽量远离火源 C、灭火器瞄准火苗 D、人要站在下风处', '灭火器瞄准火源 ', '尽量远离火源 ', '灭火器瞄准火苗 ', '人要站在下风处', 'A', '灭火器要<b>瞄准火源</b>，人要站在<b>上风处</b>，借风势将灭火器泡沫吹向火源。', '', '正确使用', 0003, 0.15);
INSERT INTO `question` VALUES (1733399852399828994, 1720, 'd558d', 0002, '机动车着火时，以下做法错误的是什么？ ', 'A、车辆远离加油站 B、设置警告标志 C、向下风方向撤离 D、尽快减速停车', '车辆远离加油站 ', '设置警告标志 ', '向下风方向撤离 ', '尽快减速停车', 'C', '<b>请注意，题干要求选出做法错误的选项。</b>机动车着火时，应尽快减速停车，将车辆远离加油站、建筑物、高压电线、树木、灌木丛及车辆或其他易燃物品的空旷地带，并在车后设置警告标志，尽快组织车上人员下车<b>撤离到上风</b>安全区域。', '', '做法错误', 0003, 0.14);
INSERT INTO `question` VALUES (1733399852450160641, 1721, 'f7dcd', 0001, '驾驶电动汽车发生起火需要报警时，只需要告知起火地点，无需告知起火汽车的品牌和型号。', NULL, '对', '错', '空', '空', '错', '新能源汽车着火，在报警时<b>一定要告知起火汽车的品牌和型号</b>，让救援人员能够迅速了解该车的动力电池种类和容量，以及车辆最高电压、高压线路走向等，有助于在现场选择合适的灭火处置措施。', '', '只需|无需', 0003, 0.11);
INSERT INTO `question` VALUES (1733399852483715073, 1722, 'e5f9e', 0001, '为防止有害气体中毒伤员继续中毒，首先要将伤员转移到空气新鲜的地方。', NULL, '对', '错', '空', '空', '对', '救助有害气体中毒伤员时，要在<b>第一时间迅速</b><b>将伤员移到有新鲜空气的地方</b>，以防止继续中毒。因此，题干做法正确。', '', '首先要', 0001, 0.02);
INSERT INTO `question` VALUES (1733399852538241026, 1723, 'ce818', 0001, '对无骨端外露的骨折伤员肢体固定时，要超过伤口上下关节。', NULL, '对', '错', '空', '空', '对', '对没有骨外露的闭合性骨折伤员，用夹板或木棍、树枝等固定肢体时，一定要<b>超过伤口上、下关节</b>，妥善的固定以后再进行转运，有助于减少骨折移位，损伤周围神经血管等风险。', '', '无骨端外露', 0002, 0.07);
